    -- data
    _data = {
        [1] = {90001,"Lữ Bố","","Giao Danh Tướng Lệnh ra, ta sẽ tha cho ngươi!","Giao Danh Tướng Lệnh ra, ta sẽ tha cho ngươi!",},
        [2] = {90001,"Triệu Vân","Lữ Bố","Tên phản phúc, đừng hòng hỗn xược!","Tên phản phúc, đừng hòng hỗn xược!",},
        [3] = {90002,"Lữ Bố","","Hừ! Không biết tự lượng sức, xem Phương Thiên Họa Kích của ta đây!","Hừ! Không biết tự lượng sức, xem Phương Thiên Họa Kích của ta đây!",},
        [4] = {90002,"Điêu Thuyền","","Thiếp đến giúp phu quân!","Thiếp đến giúp phu quân!",},
        [5] = {90003,"Tôn Sách","","Lữ Bố, có dám ra đấu với ta không? Ta là Giang Đông Tiểu Bá Vương, Tôn Bá Phù!","Lữ Bố, có dám ra đấu với ta không? Ta là Giang Đông Tiểu Bá Vương, Tôn Bá Phù!",},
        [6] = {90003,"Đại Kiều","","Được kết thúc dưới tay Tôn Bá Phù, là vinh hạnh của các ngươi.","Được kết thúc dưới tay Tôn Bá Phù, là vinh hạnh của các ngươi.",},
        [7] = {90004,"Tả Từ","","Hừ, dám dòm ngó Danh Tướng Lệnh, xem tiên thuật của ta đây!","Hừ, dám dòm ngó Danh Tướng Lệnh, xem tiên thuật của ta đây!",},
        [8] = {90006,"Trương Giác","","Chỉ có quân Hoàng Cân chúng ta mới xứng sở hữu Danh Tướng Lệnh!","Chỉ có quân Hoàng Cân chúng ta mới xứng sở hữu Danh Tướng Lệnh!",},
        [9] = {90006,"Nhân vật","Trương Giác","Hừ, một lũ ô hợp, dám tự xưng chính nghĩa?","Hừ, một lũ ô hợp, dám tự xưng chính nghĩa?",},
        [10] = {91101,"Quan Vũ","","Quan Vân Trường ở đây! Trương Giác, Vu Cát, có dám đánh không!","Quan Vân Trường ở đây! Trương Giác, Vu Cát, có dám đánh không!",},
        [11] = {91101,"Trương Giác","Quan Vũ","Mọi chuyện trong thiên hạ, đều do ta quyết định! Mau giơ tay chịu trói đi!","Mọi chuyện trong thiên hạ, đều do ta quyết định! Mau giơ tay chịu trói đi!",},
        [12] = {91101,"Trương Phi","Trương Giác","Ta đây là Trương Dực Đức! Tiếp chiêu!","Ta đây là Trương Dực Đức! Tiếp chiêu!",},
        [13] = {91103,"Quan Vũ","","Yêu đạo, nạp mạng đi!","Yêu đạo, nạp mạng đi!",},
        [14] = {91104,"Trương Giác","","Ông trời ơi...","Ông trời ơi...",},
        [15] = {91104,"Nhân vật","","Đa tạ hai vị giúp đỡ!","Đa tạ hai vị giúp đỡ!",},
        [16] = {91104,"Trương Phi","Nhân vật","Tiểu anh hùng tuổi trẻ tài cao, ắt đạt thành tựu lớn! Ta sẽ cho con gái ta Trương Tinh Thái đi theo anh hùng rèn luyện, được không?","Tiểu anh hùng tuổi trẻ tài cao, ắt đạt thành tựu lớn! Ta sẽ cho con gái ta Trương Tinh Thái đi theo anh hùng rèn luyện, được không?",},
        [17] = {91104,"Quan Vũ","Nhân vật","Trên chiến trường, đao kiếm vô tình, cháu ta là Trương Tinh Thái từng theo học Hoa Đà, rất giỏi trị liệu, hãy để nó giúp anh hùng một tay!","Trên chiến trường, đao kiếm vô tình, cháu ta là Trương Tinh Thái từng theo học Hoa Đà, rất giỏi trị liệu, hãy để nó giúp anh hùng một tay!",},
        [18] = {91104,"Nhân vật","Quan Vũ","Vậy thì tốt quá, đa tạ hai vị! Ta đang định tìm người đồng hành!","Vậy thì tốt quá, đa tạ hai vị! Ta đang định tìm người đồng hành!",},
        [19] = {91105,"Tinh Thái","Nhân vật","Bái kiến chúa công! #Trương Tinh Thái# nguyện đi theo chúa công, chinh chiến sa trường!","Bái kiến chúa công! #Trương Tinh Thái# nguyện đi theo chúa công, chinh chiến sa trường!",},
        [20] = {2,"Tinh Thái","Nhân vật","Chúa công, thế lực chúng ta còn yếu, cần #chiêu mộ# thêm một người.","Chúa công, thế lực chúng ta còn yếu, cần #chiêu mộ# thêm một người.",},
        [21] = {2,"Nhân vật","","Đi đâu #chiêu mộ# đồng hành đây?","Đi đâu #chiêu mộ# đồng hành đây?",},
        [22] = {2,"Tinh Thái","","Chúa công hãy đi theo ta!","Chúa công hãy đi theo ta!",},
        [23] = {3,"Tinh Thái","Ngân Bình","Thì ra là #Quan Ngân Bình# tỉ tỉ! Chúng ta có thể cùng chiến đấu bên nhau rồi!","Thì ra là #Quan Ngân Bình# tỉ tỉ! Chúng ta có thể cùng chiến đấu bên nhau rồi!",},
        [24] = {3,"Ngân Bình","","Bái kiến chúa công! Quan Ngân Bình nguyện dùng thanh đao trong tay, cùng ngài dẹp yên thiên hạ!","Bái kiến chúa công! Quan Ngân Bình nguyện dùng thanh đao trong tay, cùng ngài dẹp yên thiên hạ!",},
        [25] = {3,"Nhân vật","","Thì ra là con gái của Quan tướng quân! Lực lượng của ta lại mạnh hơn rồi!","Thì ra là con gái của Quan tướng quân! Lực lượng của ta lại mạnh hơn rồi!",},
        [26] = {3,"Ngân Bình","","Trường đao của ta đang rất muốn trổ tài! Chúa công hãy để ta ra trận diệt địch!","Trường đao của ta đang rất muốn trổ tài! Chúa công hãy để ta ra trận diệt địch!",},
        [27] = {4,"Tinh Thái","Ngân Bình","Chúa công nếu cho tướng có duyên phận #Hợp Kích# cùng ra trận, có thể thi triển tuyệt kỹ Hợp Kích, uy lực vô biên!","Chúa công nếu cho tướng có duyên phận #Hợp Kích# cùng ra trận, có thể thi triển tuyệt kỹ Hợp Kích, uy lực vô biên!",},
        [28] = {4,"Ngân Bình","","Chúa công, ta và Tinh Thái Muội Muội có Duyên Phận #Hợp Kích# Tinh Nguyệt Ngân Thái.","Chúa công, ta và Tinh Thái Muội Muội có Duyên Phận #Hợp Kích# Tinh Nguyệt Ngân Thái.",},
        [29] = {4,"Ngân Bình","","Chúng ta mau chiến nào, ta phải dùng tuyệt chiêu #Hợp Kích# tiêu diệt kẻ địch!","Chúng ta mau chiến nào, ta phải dùng tuyệt chiêu #Hợp Kích# tiêu diệt kẻ địch!",},
        [30] = {5,"Ngân Bình","Tinh Thái","Tuyệt kỹ Hợp Kích vừa rồi thật lợi hại! Một chiêu đã tiêu diệt sạch bọn chúng!","Tuyệt kỹ Hợp Kích vừa rồi thật lợi hại! Một chiêu đã tiêu diệt sạch bọn chúng!",},
        [31] = {5,"Tinh Thái","","Đợi tỷ tỷ #tăng cấp#, chắc chắn sẽ mạnh hơn!","Đợi tỷ tỷ #tăng cấp#, chắc chắn sẽ mạnh hơn!",},
        [32] = {5,"Tinh Thái","","Chúa công, mau mở #Rương Bản Đồ#, xem bên trong có gì nào!","Chúa công, mau mở #Rương Bản Đồ#, xem bên trong có gì nào!",},
        [33] = {6,"Tinh Thái","Ngân Bình","Chúa công nhận được #Rượu Đỗ Khang# và Bạc! Rượu Đỗ Khang có thể tăng cấp cho tướng, giúp tăng lực chiến.","Chúa công nhận được #Rượu Đỗ Khang# và Bạc! Rượu Đỗ Khang có thể tăng cấp cho tướng, giúp tăng lực chiến.",},
        [34] = {6,"Ngân Bình","","Chúa công, hãy mau #tăng cấp# để tăng lực chiến cho ta! Sẽ có một ngày, ta sẽ hơn cả cha ta!","Chúa công, hãy mau #tăng cấp# để tăng lực chiến cho ta! Sẽ có một ngày, ta sẽ hơn cả cha ta!",},
        [35] = {7,"Tinh Thái","Ngân Bình","Quan Ngân Bình tỉ tỉ chưa gì đã tăng đến Lv3, thật lợi hại!","Quan Ngân Bình tỉ tỉ chưa gì đã tăng đến Lv3, thật lợi hại!",},
        [36] = {7,"Ngân Bình","","Chúa công, chúng ta tiếp tục đi thảo phạt kẻ địch nào!","Chúa công, chúng ta tiếp tục đi thảo phạt kẻ địch nào!",},
        [37] = {91301,"Viên Thiệu","","Ta là Nhữ Ân Viên Thị, các ngươi là ai?","Ta là Nhữ Ân Viên Thị, các ngươi là ai?",},
        [38] = {91301,"Nhân vật","Viên Thiệu","Nhữ Ân Viên Thị có là gì, sai tên nào đến ta xử tên đó!","Nhữ Ân Viên Thị có là gì, sai tên nào đến ta xử tên đó!",},
        [39] = {91303,"Gia Cát Lượng","","Bày mưu tính kế, quyết chiến quyết thắng.","Bày mưu tính kế, quyết chiến quyết thắng.",},
        [40] = {8,"Tinh Thái","Ngân Bình","Chúa công, vượt ải chính tuyến đạt số sao nhất định sẽ nhận được #Rương Sao#.","Chúa công, vượt ải chính tuyến đạt số sao nhất định sẽ nhận được #Rương Sao#.",},
        [41] = {9,"Tinh Thái","Ngân Bình","Chúa công nhận được #Đột Phá Đơn#! Đột Phá Đơn là vật phẩm dùng để tăng lực chiến cho tướng!","Chúa công nhận được #Đột Phá Đơn#! Đột Phá Đơn là vật phẩm dùng để tăng lực chiến cho tướng!",},
        [42] = {9,"Ngân Bình","","Võ tướng sau khi #đột phá# sẽ nhận thiên phú chiến đấu mới, thực lực tăng mạnh!","Võ tướng sau khi #đột phá# sẽ nhận thiên phú chiến đấu mới, thực lực tăng mạnh!",},
        [43] = {9,"Tinh Thái","","Chúa công, giờ chúng ta cùng #đột phá# nào!","Chúa công, giờ chúng ta cùng #đột phá# nào!",},
        [44] = {10,"Tinh Thái","Ngân Bình","Ồ, chúa công đã #đột phá +1#, thuộc tính tăng mạnh!","Ồ, chúa công đã #đột phá +1#, thuộc tính tăng mạnh!",},
        [45] = {10,"Ngân Bình","","Chúa công, sau khi #đột phá#, kỹ năng của ngài nay có thể đánh một lúc 3 người!","Chúa công, sau khi #đột phá#, kỹ năng của ngài nay có thể đánh một lúc 3 người!",},
        [46] = {10,"Tinh Thái","","Chúa công thật lợi hại! Chúng ta tiếp tục đi tìm Danh Tướng Lệnh nào!","Chúa công thật lợi hại! Chúng ta tiếp tục đi tìm Danh Tướng Lệnh nào!",},
        [47] = {106,"Tinh Thái","","Hoa Hùng chẳng là gì, chúa công anh minh thần võ! Chúa công sau khi #đột phá# thật lợi hại!","Hoa Hùng chẳng là gì, chúa công anh minh thần võ! Chúa công sau khi #đột phá# thật lợi hại!",},
        [48] = {106,"Ngân Bình","","Chúa công, chúng ta bắt đầu đi thảo phạt Đổng Trác nào!","Chúa công, chúng ta bắt đầu đi thảo phạt Đổng Trác nào!",},
        [49] = {91501,"Đổng Trác","Điêu Thuyền","Hi hi hi! Nào, mỹ nhân, cho ta thơm một cái!","Hi hi hi! Nào, mỹ nhân, cho ta thơm một cái!",},
        [50] = {91501,"Điêu Thuyền","Đổng Trác","Phu quân hãy giúp thiếp đòi lại công bằng!","Phu quân hãy giúp thiếp đòi lại công bằng!",},
        [51] = {91501,"Lữ Bố","Đổng Trác","Lão tặc Đổng Trác, nộp mạng đi!","Lão tặc Đổng Trác, nộp mạng đi!",},
        [52] = {91502,"Đổng Trác","","Thuận ta thì sống, chống ta thì chết!","Thuận ta thì sống, chống ta thì chết!",},
        [53] = {13,"Trương Giác","Nhân vật","Ta được Nam Hoa Lão Tiên truyền thụ Thái Bình Thuật, sáng lập Thái Bình Đạo, nay đã có 36 vạn đệ tử!","Ta được Nam Hoa Lão Tiên truyền thụ Thái Bình Thuật, sáng lập Thái Bình Đạo, nay đã có 36 vạn đệ tử!",},
        [54] = {13,"Trương Giác","","Gia nhập Thái Bình Đạo có thể tránh được bệnh tật, tai kiếp, bảo vệ gia đình bình an!","Gia nhập Thái Bình Đạo có thể tránh được bệnh tật, tai kiếp, bảo vệ gia đình bình an!",},
        [55] = {13,"Nhân vật","","Trương Giác, ngươi lừa gạt bá tánh, làm xằng làm bậy! Nếu không có #Danh Tướng Lệnh#, ngươi có nổi tài cán gì!","Trương Giác, ngươi lừa gạt bá tánh, làm xằng làm bậy! Nếu không có #Danh Tướng Lệnh#, ngươi có nổi tài cán gì!",},
        [56] = {13,"Trương Giác","","Hừ! Ngươi biết quá nhiều rồi, hãy nếm mùi lợi hại của quân Hoàng Cân!","Hừ! Ngươi biết quá nhiều rồi, hãy nếm mùi lợi hại của quân Hoàng Cân!",},
        [57] = {202,"Trương Lương","Tinh Thái","Các ngươi là ai, dám mạo phạm quân Hoàng Cân?","Các ngươi là ai, dám mạo phạm quân Hoàng Cân?",},
        [58] = {202,"Ngân Bình","","Hoàng Cân ức hiếp bá tánh, còn dám tự xem mình là chính đạo! Xem đại đao của ta đây!","Hoàng Cân ức hiếp bá tánh, còn dám tự xem mình là chính đạo! Xem đại đao của ta đây!",},
        [59] = {14,"Tinh Thái","Ngân Bình","Chúa công, sau khi kích hoạt #Sổ Danh Tướng#, thuộc tính của chúa công tăng mạnh, lực chiến cũng tăng theo.","Chúa công, sau khi kích hoạt #Sổ Danh Tướng#, thuộc tính của chúa công tăng mạnh, lực chiến cũng tăng theo.",},
        [60] = {14,"Ngân Bình","","Chúa công, mỗi tướng đều có #Sổ Danh Tướng# của bản thân.","Chúa công, mỗi tướng đều có #Sổ Danh Tướng# của bản thân.",},
        [61] = {14,"Tinh Thái","","Chúa công, sau này nhớ kích hoạt #Sổ Danh Tướng#!","Chúa công, sau này nhớ kích hoạt #Sổ Danh Tướng#!",},
        [62] = {203,"Trương Giác","Ngân Bình","Trời xanh đã mất, trời vàng lên thay! Đến năm Giáp Tý, thiên hạ cùng mừng! Mọi chuyện trong thiên hạ, đều do ta quyết định!","Trời xanh đã mất, trời vàng lên thay! Đến năm Giáp Tý, thiên hạ cùng mừng! Mọi chuyện trong thiên hạ, đều do ta quyết định!",},
        [63] = {203,"Ngân Bình","","Hừ, một lũ tà môn ngoại đạo!","Hừ, một lũ tà môn ngoại đạo!",},
        [64] = {203,"Trương Giác","","Ông sấm bà sét, nghe ta hiệu lệnh! Tụ thiên khí, hành thiên đạo!","Ông sấm bà sét, nghe ta hiệu lệnh! Tụ thiên khí, hành thiên đạo!",},
        [65] = {203,"Ngân Bình","","Nói nhiều quá, xem chiêu!","Nói nhiều quá, xem chiêu!",},
        [66] = {204,"Vu Cát","Tinh Thái","Trương Giác đạo hữu, bần đạo Vu Cát đến giúp ngài!","Trương Giác đạo hữu, bần đạo Vu Cát đến giúp ngài!",},
        [67] = {204,"Tinh Thái","","Bọn đạo sĩ ở đâu ra vậy? Đánh bại một Trương Giác, lại xuất hiện một Vu Cát!","Bọn đạo sĩ ở đâu ra vậy? Đánh bại một Trương Giác, lại xuất hiện một Vu Cát!",},
        [68] = {204,"Tào Tháo","","Hoàng Cân làm loạn, ai cũng có trách nhiệm diệt trừ! Ta sẽ giúp ngài!","Hoàng Cân làm loạn, ai cũng có trách nhiệm diệt trừ! Ta sẽ giúp ngài!",},
        [69] = {204,"Vu Cát","","Tào Tháo, quân Hoàng Cân chẳng qua chỉ là những bá tánh nghèo khổ, hà tất phải đuổi cùng diệt tận?","Tào Tháo, quân Hoàng Cân chẳng qua chỉ là những bá tánh nghèo khổ, hà tất phải đuổi cùng diệt tận?",},
        [70] = {204,"Vu Cát","","Tào Tháo, hãy nghe ta khuyên...","Tào Tháo, hãy nghe ta khuyên...",},
        [71] = {204,"Vu Cát","","Sau này nếu ngươi trở thành thừa tướng, hãy nhớ: Đừng ức hiếp cô nhi quả phụ, kẻo 40 năm sau lại một vòng luân hồi!","Sau này nếu ngươi trở thành thừa tướng, hãy nhớ: Đừng ức hiếp cô nhi quả phụ, kẻo 40 năm sau lại một vòng luân hồi!",},
        [72] = {204,"Tào Tháo","","Ta sẽ là Thừa Tướng? Nếu đã vậy, Vu Cát, sao ngươi lại giúp bọn giặc Hoàng Cân? Chi bằng cứ đi theo Tào Tháo ta đây!","Ta sẽ là Thừa Tướng? Nếu đã vậy, Vu Cát, sao ngươi lại giúp bọn giặc Hoàng Cân? Chi bằng cứ đi theo Tào Tháo ta đây!",},
        [73] = {204,"Vu Cát","","Ngươi đúng là cứng đầu! Ta thay trời dạy ngươi một bài học!","Ngươi đúng là cứng đầu! Ta thay trời dạy ngươi một bài học!",},
        [74] = {15,"Tinh Thái","Ngân Bình","Chúa công nhận được #trang bị#!","Chúa công nhận được #trang bị#!",},
        [75] = {15,"Ngân Bình","","Đây là Cổ Đĩnh Đao! Cổ Đĩnh Đao rất nổi tiếng, nhiều anh hùng đều sử dụng.","Đây là Cổ Đĩnh Đao! Cổ Đĩnh Đao rất nổi tiếng, nhiều anh hùng đều sử dụng.",},
        [76] = {15,"Tinh Thái","","Chúa công hãy trang bị #vũ khí#!","Chúa công hãy trang bị #vũ khí#!",},
        [77] = {16,"Tinh Thái","Ngân Bình","Chúa công, phía trước có kẻ địch khó đối phó, chúng ta cân #cường hóa# vũ khí tăng lực chiến!","Chúa công, phía trước có kẻ địch khó đối phó, chúng ta cân #cường hóa# vũ khí tăng lực chiến!",},
        [78] = {17,"Tinh Thái","Ngân Bình","Vũ khí sau khi #cường hóa# quả nhiên lợi hại! Sức tấn công gấp đôi trước đây!","Vũ khí sau khi #cường hóa# quả nhiên lợi hại! Sức tấn công gấp đôi trước đây!",},
        [79] = {17,"Tinh Thái","","Chúa công sau này nhớ phải #cường hóa# vũ khí!","Chúa công sau này nhớ phải #cường hóa# vũ khí!",},
        [80] = {17,"Ngân Bình","","Phía trước lại có kẻ địch, chúa công hãy mau thử uy lực vũ khí đã cường hóa!","Phía trước lại có kẻ địch, chúa công hãy mau thử uy lực vũ khí đã cường hóa!",},
        [81] = {18,"Tinh Thái","Ngân Bình","Thực lực của chúa công tăng mạnh, đã đến lúc mở rộng đội ngũ.","Thực lực của chúa công tăng mạnh, đã đến lúc mở rộng đội ngũ.",},
        [82] = {18,"Ngân Bình","","Chúa công, trong #Quà Hợp Kích Tướng# có một tổ hợp Tướng Hợp Kích Tím! Có họ gia nhập, chúa công như hổ thêm cánh!","Chúa công, trong #Quà Hợp Kích Tướng# có một tổ hợp Tướng Hợp Kích Tím! Có họ gia nhập, chúa công như hổ thêm cánh!",},
        [83] = {19,"Tinh Thái","Ngân Bình","Tốt quá, có Tướng Hợp Kích mới rồi! Họ có vẻ rất lợi hại!","Tốt quá, có Tướng Hợp Kích mới rồi! Họ có vẻ rất lợi hại!",},
        [84] = {19,"Tinh Thái","","Khi chúa công tăng đến #Lv9# và #Lv17# sẽ mở vị trí trận mới, họ sẽ có thể ra trận cùng chiến đấu với chúa công!","Khi chúa công tăng đến #Lv9# và #Lv17# sẽ mở vị trí trận mới, họ sẽ có thể ra trận cùng chiến đấu với chúa công!",},
        [85] = {19,"Ngân Bình","","Chúa công mau đưa Ngân Bình đi chiến đấu tăng cấp nào!","Chúa công mau đưa Ngân Bình đi chiến đấu tăng cấp nào!",},
        [86] = {20,"Tinh Thái","","Chúa công thực lực tăng nhanh, có thể đến #Đấu Trường# khiêu chiến anh hùng thiên hạ!","Chúa công thực lực tăng nhanh, có thể đến #Đấu Trường# khiêu chiến anh hùng thiên hạ!",},
        [87] = {20,"Tinh Thái","","Tinh Thái sẽ đưa ngài vào #Đấu Trường# xem thử.","Tinh Thái sẽ đưa ngài vào #Đấu Trường# xem thử.",},
        [88] = {21,"Tinh Thái","Ngân Bình","Chúa công, anh hùng trong #Đấu Trường# rất nhiều, hãy đánh bại người chơi khác để tăng hạng.","Chúa công, anh hùng trong #Đấu Trường# rất nhiều, hãy đánh bại người chơi khác để tăng hạng.",},
        [89] = {21,"Ngân Bình","","Chúa công, mỗi lần tăng #hạng#, đều được nhận nhiều phần thưởng! Hạng càng cao, thưởng càng nhiều!","Chúa công, mỗi lần tăng #hạng#, đều được nhận nhiều phần thưởng! Hạng càng cao, thưởng càng nhiều!",},
        [90] = {21,"Tinh Thái","","Chúa công, chúng ta đến Tiệm Thi Đấu nào!","Chúa công, chúng ta đến Tiệm Thi Đấu nào!",},
        [91] = {22,"Tinh Thái","Ngân Bình","Chúa công, mỗi lần chiến thắng kẻ địch ở Đấu Trường, đều nhận dược danh vọng và hạng, đạt đến hạng nhất định sẽ có thưởng thêm!","Chúa công, mỗi lần chiến thắng kẻ địch ở Đấu Trường, đều nhận dược danh vọng và hạng, đạt đến hạng nhất định sẽ có thưởng thêm!",},
        [92] = {22,"Tinh Thái","","Mỗi lần tăng hạng sẽ nhận được nhiều Nguyên Bảo! Hạng càng cao, danh vọng nhận được mỗi ngày càng nhiều.","Mỗi lần tăng hạng sẽ nhận được nhiều Nguyên Bảo! Hạng càng cao, danh vọng nhận được mỗi ngày càng nhiều.",},
        [93] = {22,"Ngân Bình","","Đi nào! Vào Đấu Trường nỗ lực tăng hạng! Ta phải giúp chúa công đoạt hạng nhất!","Đi nào! Vào Đấu Trường nỗ lực tăng hạng! Ta phải giúp chúa công đoạt hạng nhất!",},
        [94] = {23,"Tinh Thái","Ngân Bình","Chúa công, thực lực ngài tăng mạnh, có thể đi #Du Ngoạn# rồi!","Chúa công, thực lực ngài tăng mạnh, có thể đi #Du Ngoạn# rồi!",},
        [95] = {23,"Ngân Bình","","Từ xưa đến nay, nhiều loại Binh Thư, Binh Phù đã từng xuất hiện ở Thần Châu. Nếu kiên trì Du Ngoạn, thế nào cũng có ngày nhận những #bảo vật# này!","Từ xưa đến nay, nhiều loại Binh Thư, Binh Phù đã từng xuất hiện ở Thần Châu. Nếu kiên trì Du Ngoạn, thế nào cũng có ngày nhận những #bảo vật# này!",},
        [96] = {23,"Tinh Thái","","Ngoài báu vật, Du Ngoạn còn kích hoạt các loại #Kỳ Ngộ#, nhận được nhiều EXP để tăng cấp.","Ngoài báu vật, Du Ngoạn còn kích hoạt các loại #Kỳ Ngộ#, nhận được nhiều EXP để tăng cấp.",},
        [97] = {23,"Tinh Thái","","Chúa công hãy theo Tinh Thái đến #Đào Viên# du ngoạn!","Chúa công hãy theo Tinh Thái đến #Đào Viên# du ngoạn!",},
        [98] = {24,"Ngân Bình","","Chỉ cần trả lời đúng các câu hỏi của Thủy Kính Tiên Sinh, sẽ được nhận nhiều Nguyên Bảo!","Chỉ cần trả lời đúng các câu hỏi của Thủy Kính Tiên Sinh, sẽ được nhận nhiều Nguyên Bảo!",},
        [99] = {24,"Ngân Bình","","Chúa công, câu này ta biết!","Chúa công, câu này ta biết!",},
        [100] = {25,"Tinh Thái","Ngân Bình","Chúa công nhận được #bảo vật#! bảo vật gồm 2 loại, Binh Thư và Binh Phù. Binh Thư tăng tấn công, Binh Phù tăng sinh lực.","Chúa công nhận được #bảo vật#! bảo vật gồm 2 loại, Binh Thư và Binh Phù. Binh Thư tăng tấn công, Binh Phù tăng sinh lực.",},
        [101] = {25,"Ngân Bình","","Quyển Ngô Tử này là binh thư do danh tướng Ngô Khởi viết, có thể giúp chúa công tăng nhiều lực chiến!","Quyển Ngô Tử này là binh thư do danh tướng Ngô Khởi viết, có thể giúp chúa công tăng nhiều lực chiến!",},
        [102] = {25,"Tinh Thái","","Chúa công hãy theo ta! Chúng ta vào Trận Hình trang bị bảo vật nào.","Chúa công hãy theo ta! Chúng ta vào Trận Hình trang bị bảo vật nào.",},
        [103] = {26,"Tinh Thái","","Chúa công, chúng ta hãy cường hóa bảo vật!","Chúa công, chúng ta hãy cường hóa bảo vật!",},
        [104] = {26,"Tinh Thái","","Binh Thư và Binh Phù đều có thể dùng Nghiên Đài để cường hóa! Sau khi cường hóa, uy lực bảo vật sẽ tăng lên.","Binh Thư và Binh Phù đều có thể dùng Nghiên Đài để cường hóa! Sau khi cường hóa, uy lực bảo vật sẽ tăng lên.",},
        [105] = {27,"Ngân Bình","","Chúa công xem, bảo vật sau khi cường hóa, lực chiến tăng mạnh.","Chúa công xem, bảo vật sau khi cường hóa, lực chiến tăng mạnh.",},
        [106] = {27,"Ngân Bình","","Chúa công, chúng ta tiếp tục đi Du Ngoạn, sẽ nhận được nhiều bảo vật hơn!","Chúa công, chúng ta tiếp tục đi Du Ngoạn, sẽ nhận được nhiều bảo vật hơn!",},
        [107] = {28,"Tinh Thái","","Chúa công, hệ thống #Quân Hàm# đã mở!","Chúa công, hệ thống #Quân Hàm# đã mở!",},
        [108] = {28,"Tinh Thái","","Chúa công, thực lực của ngài tăng mạnh, Tinh Thái sẽ đưa ngày đi tăng cấp Quân Hàm!","Chúa công, thực lực của ngài tăng mạnh, Tinh Thái sẽ đưa ngày đi tăng cấp Quân Hàm!",},
        [109] = {29,"Tinh Thái","Ngân Bình","Chúa công đã tăng cấp Quân Hàm, giúp tăng lực chiến tướng!","Chúa công đã tăng cấp Quân Hàm, giúp tăng lực chiến tướng!",},
        [110] = {29,"Ngân Bình","","Ồ, lực chiến tăng mạnh! Người mạnh sẽ luôn mạnh!","Ồ, lực chiến tăng mạnh! Người mạnh sẽ luôn mạnh!",},
        [111] = {29,"Ngân Bình","","Chúa công, chúng ta hãy vào phó bản chính tuyến, tiếp tục chinh chiến!","Chúa công, chúng ta hãy vào phó bản chính tuyến, tiếp tục chinh chiến!",},
        [112] = {30,"Tinh Thái","","Chúa công danh chấn thiên hạ, có thể đi #Leo Tháp#, giành trang bị cực phẩm!","Chúa công danh chấn thiên hạ, có thể đi #Leo Tháp#, giành trang bị cực phẩm!",},
        [113] = {31,"Tinh Thái","Ngân Bình","Chúa công, Leo Tháp mỗi ải có 3 độ khó. Độ khó càng cao, số sao và Tinh Thiết nhận được càng nhiều!","Chúa công, Leo Tháp mỗi ải có 3 độ khó. Độ khó càng cao, số sao và Tinh Thiết nhận được càng nhiều!",},
        [114] = {31,"Ngân Bình","","Có thể dùng Tinh Thiết đổi các loại trang bị quý trong Tiệm Trang Bị! Khi đạt số sao nhất định, còn được nhận thưởng thêm!","Có thể dùng Tinh Thiết đổi các loại trang bị quý trong Tiệm Trang Bị! Khi đạt số sao nhất định, còn được nhận thưởng thêm!",},
        [115] = {31,"Tinh Thái","","Chúa công vừa nhận được nhiều Tinh Thiết, có thể đến Tiệm Trang Bị xem thử.","Chúa công vừa nhận được nhiều Tinh Thiết, có thể đến Tiệm Trang Bị xem thử.",},
        [116] = {32,"Tinh Thái","Ngân Bình","Chúa công nhận được Hàn Băng Kiếm!","Chúa công nhận được Hàn Băng Kiếm!",},
        [117] = {32,"Tinh Thái","","Chúa công muốn nhận trang bị cực phẩm, mỗi ngày nhớ đi Leo Tháp!","Chúa công muốn nhận trang bị cực phẩm, mỗi ngày nhớ đi Leo Tháp!",},
        [118] = {32,"Ngân Bình","","Chúa công, Ngân Bình muốn có nhiều trang bị tốt hơn! Nhanh lên, chúng ta đi Leo Tháp tiếp đi!","Chúa công, Ngân Bình muốn có nhiều trang bị tốt hơn! Nhanh lên, chúng ta đi Leo Tháp tiếp đi!",},
        [119] = {33,"Tinh Thái","Ngân Bình","Chúa công, #Phó Bản Ngày# đã mở! Phó Bản Ngày là một nơi tốt để rèn luyện!","Chúa công, #Phó Bản Ngày# đã mở! Phó Bản Ngày là một nơi tốt để rèn luyện!",},
        [120] = {33,"Ngân Bình","","Chúa công, đánh Phó Bản Ngày, được nhận rất nhiều tài nguyên! Ví dụ như Bạc, Đột Phá Đơn, các loại Đá Luyện..., giúp lực chiến tăng không ngừng!","Chúa công, đánh Phó Bản Ngày, được nhận rất nhiều tài nguyên! Ví dụ như Bạc, Đột Phá Đơn, các loại Đá Luyện..., giúp lực chiến tăng không ngừng!",},
        [121] = {33,"Tinh Thái","","Phó Bản Ngày hay nhỉ, chúng ta mau đi xem nào!","Phó Bản Ngày hay nhỉ, chúng ta mau đi xem nào!",},
        [122] = {34,"Tinh Thái","Ngân Bình","Chúa công ra trận diệt địch, không thể cô đơn một mình! Hệ thống #Quân Đoàn# đã mở, chúng ta cùng đi xem nào!","Chúa công ra trận diệt địch, không thể cô đơn một mình! Hệ thống #Quân Đoàn# đã mở, chúng ta cùng đi xem nào!",},
        [123] = {34,"Ngân Bình","","Chúa công! Chúa công! Gia nhập Quân Đoàn có thể mở nhiều tính năng hấp dẫn! Mau xin gia nhập ngay nào!","Chúa công! Chúa công! Gia nhập Quân Đoàn có thể mở nhiều tính năng hấp dẫn! Mau xin gia nhập ngay nào!",},
        [124] = {205,"Nhân vật","Tào Tháo","Hừm! Không ngờ hạ được Trương Giác, lại để Vu Cát chạy mất!","Hừm! Không ngờ hạ được Trương Giác, lại để Vu Cát chạy mất!",},
        [125] = {205,"Tào Tháo","","Tiểu anh hùng sau này ắt sẽ có thành tựu lớn! Mai này nếu đến Lạc Dương hãy đến gặp ta! Tào mỗ còn có việc, đi trước đây.","Tiểu anh hùng sau này ắt sẽ có thành tựu lớn! Mai này nếu đến Lạc Dương hãy đến gặp ta! Tào mỗ còn có việc, đi trước đây.",},
        [126] = {205,"Tinh Thái","Nhân vật","Chúa công, lời của Vu Cát có ẩn ý, nhưng có vẻ Tào Tháo không để trong lòng...","Chúa công, lời của Vu Cát có ẩn ý, nhưng có vẻ Tào Tháo không để trong lòng...",},
        [127] = {205,"Tinh Thái","","Chúa công, ngài nói Tào Tháo là anh hùng hay là kiêu hùng?","Chúa công, ngài nói Tào Tháo là anh hùng hay là kiêu hùng?",},
        [128] = {205,"Nhân vật","","Anh hùng thì sao, kiêu hùng thì sao! Hôm nay Tào Tháo giúp ta, sau này có cơ hội, ta cũng sẽ dốc sức giúp hắn!","Anh hùng thì sao, kiêu hùng thì sao! Hôm nay Tào Tháo giúp ta, sau này có cơ hội, ta cũng sẽ dốc sức giúp hắn!",},
        [129] = {205,"Tinh Thái","","Chúa công, đây là #Danh Tướng Lệnh# Vu Cát đánh rơi!","Chúa công, đây là #Danh Tướng Lệnh# Vu Cát đánh rơi!",},
        [130] = {205,"Nhân vật","","Lại một #Danh Tướng Lệnh#! Tốt quá!","Lại một #Danh Tướng Lệnh#! Tốt quá!",},
        [131] = {205,"Tinh Thái","","Chúa công, nghe nói Quốc Cựu Hà Tiến có #Danh Tướng Lệnh#, chúng ta đến Lạc Dương tìm Hà Tiến nào.","Chúa công, nghe nói Quốc Cựu Hà Tiến có #Danh Tướng Lệnh#, chúng ta đến Lạc Dương tìm Hà Tiến nào.",},
        [132] = {205,"Nhân vật","","Được, ta cũng đang định đến Lạc Dương du ngoạn!","Được, ta cũng đang định đến Lạc Dương du ngoạn!",},
        [133] = {301,"Tinh Thái","Nhân vật","Suỵt! Hình như có bí mật.","Suỵt! Hình như có bí mật.",},
        [134] = {301,"Trương Nhượng","Nhân vật","Hà Tiến to gan, dám mưu hại ta, ta phải ra tay trước!","Hà Tiến to gan, dám mưu hại ta? Ta phải ra tay trước!",},
        [135] = {301,"Trương Nhượng","Nhân vật","Người đâu! Giả truyền ý chỉ thái hậu, lừa Hà Tiến vào cung, diệt trừ hắn!","Người đâu! Giả truyền ý chỉ thái hậu, lừa Hà Tiến vào cung, diệt trừ hắn!",},
        [136] = {301,"Nhân vật","Tinh Thái","Nhìn dáng vẻ người này.... có vẻ là thái giám?","Nhìn dáng vẻ người này.... có vẻ là thái giám?",},
        [137] = {301,"Tinh Thái","Nhân vật","Chúa công đoán rất đúng! Người này chính là Đại Thái Giám Trương Nhượng, thủ lĩnh Thập Thường Thị.","Chúa công đoán rất đúng! Người này chính là Đại Thái Giám Trương Nhượng, thủ lĩnh Thập Thường Thị.",},
        [138] = {301,"Tinh Thái","Nhân vật","Hà Tiến là Đại Tướng Quân đương triều, anh của Hà Thái Hậu. Tào Tháo và Viên Thiệu khuyên Hà Tiến diệt thế lực hoạn quan để lấy lòng dân.","Hà Tiến là Đại Tướng Quân đương triều, anh của Hà Thái Hậu. Tào Tháo và Viên Thiệu khuyên Hà Tiến diệt thế lực hoạn quan để lấy lòng dân.",},
        [139] = {301,"Tinh Thái","Nhân vật","Nhưng không biết tại sao tin này lại bị lộ, Trương Nhượng biết được bèn ra tay mai phục trước.","Nhưng không biết tại sao tin này lại bị lộ, Trương Nhượng biết được bèn ra tay mai phục trước.",},
        [140] = {301,"Nhân vật","Tinh Thái","Thì ra là thái giám và đại thần tranh quyền!","Thì ra là thái giám và đại thần tranh quyền!",},
        [141] = {301,"Tinh Thái","Nhân vật","Chúa công, chúng ta mau tìm Hà Tiến, không thể để Trương Nhượng giành được Danh Tướng Lệnh trước chúng ta.","Chúa công, chúng ta mau tìm Hà Tiến, không thể để Trương Nhượng giành được Danh Tướng Lệnh trước chúng ta.",},
        [142] = {302,"Hà Thái Hậu","Tinh Thái","Tiện dân to gan, dám xông vào Tiêu Phòng Điện của ta! Người đâu, bắt hết bọn chúng cho ta!","Tiện dân to gan, dám xông vào Tiêu Phòng Điện của ta! Người đâu, bắt hết bọn chúng cho ta!",},
        [143] = {302,"Tinh Thái","","Đây là Thái Hậu sao? Trẻ quá! Nhưng có vẻ không xinh lắm....","Đây là Thái Hậu sao? Trẻ quá! Nhưng có vẻ không xinh lắm....",},
        [144] = {302,"Hà Thái Hậu","","Hai ả nha đầu đáng ghét, dám phỉ báng ai gia! Bắt chúng lại cho ta!","Hai ả nha đầu đáng ghét, dám phỉ báng ai gia! Bắt chúng lại cho ta!",},
        [145] = {303,"Tinh Thái","Hà Tiến","Hà Tiến, cuối cùng ta cũng tìm được ngươi, nghe nói ngươi có #Danh Tướng Lệnh#?","Hà Tiến, cuối cùng ta cũng tìm được ngươi, nghe nói ngươi có #Danh Tướng Lệnh#?",},
        [146] = {303,"Hà Tiến","","Các ngươi là ai? Sao dám xông vào hoàng cung! Bắt lấy chúng!","Các ngươi là ai? Sao dám xông vào hoàng cung! Bắt lấy chúng!",},
        [147] = {303,"Ngân Bình","","A! Lại được đánh nhau nữa!","Ồ, lại được đánh nhau!",},
        [148] = {304,"Tinh Thái","Hà Tiến","Hà Tiến, Trương Nhượng đang lập mưu làm hại ngươi, đừng vào cung!","Hà Tiến, Trương Nhượng đang lập mưu làm hại ngươi, đừng vào cung!",},
        [149] = {304,"Hà Tiến","","Hoạn quan chuyên quyền, quyết không thể tha! Hơn nữa muội muội ta còn ở hoàng cung, ta sao có thể bỏ mặc?","Hoạn quan chuyên quyền, quyết không thể tha! Hơn nữa muội muội ta còn ở hoàng cung, ta sao có thể bỏ mặc?",},
        [150] = {304,"Tinh Thái","Ngân Bình","Hà Tiến tuy kiêu căng tự phụ, nhưng rất thương muội muội, biết nguy hiểm mà vẫn vào cung!","Hà Tiến tuy kiêu căng tự phụ, nhưng rất thương muội muội, biết nguy hiểm mà vẫn vào cung!",},
        [151] = {304,"Ngân Bình","","Chúng ta mau đi tìm Trương Nhượng, dẹp yên cuộc loạn này!","Chúng ta mau đi tìm Trương Nhượng, dẹp yên cuộc loạn này!",},
        [152] = {305,"Trương Nhượng","","Ha ha, Hà Tiến, ngươi mở to mắt ra mà xem, giờ ai mới là người nắm quyền? Không phải ngươi mà là ta đây!","Ha ha, Hà Tiến, ngươi mở to mắt ra mà xem, giờ ai mới là người nắm quyền? Không phải ngươi mà là ta đây!",},
        [153] = {305,"Trương Nhượng","","Ngươi muốn đối phó ta, cho gọi Đổng Trác vào kinh. Nhưng ngươi có biết Đổng Trác là người như thế nào không?","Ngươi muốn đối phó ta, cho gọi Đổng Trác vào kinh. Nhưng ngươi có biết Đổng Trác là người như thế nào không?",},
        [154] = {305,"Trương Nhượng","","Ha ha, Đổng Trác vào kinh, loạn thế chỉ mới bắt đầu thôi!","Ha ha, Đổng Trác vào kinh, loạn thế chỉ mới bắt đầu thôi!",},
        [155] = {305,"Tinh Thái","Trương Nhượng","Đến trễ rồi! Trương Nhượng đã sát hại Hà Tiến! Thiếu Đế cũng bị Trương Nhượng uy hiếp!","Đến trễ rồi! Trương Nhượng đã sát hại Hà Tiến! Thiếu Đế cũng bị Trương Nhượng uy hiếp!",},
        [156] = {305,"Ngân Bình","","Trương Nhượng, sao ngươi dám uy hiếp Thiếu Đế!? Mau giao Thiếu Đế và Danh Tướng Lệnh cho ta, tha mạng cho ngươi!","Trương Nhượng, sao ngươi dám uy hiếp Thiếu Đế!? Mau giao Thiếu Đế và Danh Tướng Lệnh cho ta, tha mạng cho ngươi!",},
        [157] = {305,"Trương Nhượng","","Dựa vào các ngươi sao? Hãy hỏi vũ khí trong tay ta trước! Ta bắt các ngươi nếm mùi đoạn tử tuyệt tôn!","Dựa vào các ngươi sao? Hãy hỏi vũ khí trong tay ta trước! Ta bắt các ngươi nếm mùi đoạn tử tuyệt tôn!",},
        [158] = {306,"Trương Nhượng","Thiếu Đế","Thập Thường Thị thật ra cũng chỉ là những kẻ yếu ớt, nay đã thất thế, đành xin các vị hãy bảo vệ Thiếu Đế thật tốt!","Thập Thường Thị thật ra cũng chỉ là những kẻ yếu ớt, nay đã thất thế, đành xin các vị hãy bảo vệ Thiếu Đế thật tốt!",},
        [159] = {306,"Thiếu Đế","Trương Nhượng","Trương Thường Thị! Đừng!","Trương Thường Thị! Đừng!",},
        [160] = {306,"Trương Nhượng","Thiếu Đế","Số mệnh đã ban bài, mong bệ hạ bảo trọng (Trương Nhượng nhảy sông tự vẫn).","Số mệnh đã ban bài, mong bệ hạ bảo trọng (Trương Nhượng nhảy sông tự vẫn).",},
        [161] = {306,"Thiếu Đế","Tinh Thái","Dù sao cũng cảm tạ các vị đã dẹp yên phản loạn, #Danh Tướng Lệnh# này tặng cho các vị.","Dù sao cũng cảm tạ các vị đã dẹp yên phản loạn, #Danh Tướng Lệnh# này tặng cho các vị.",},
        [162] = {306,"Tinh Thái","Ngân Bình","Chúa công, Thập Thường Thị vừa dẹp yên, nay Đổng Trác lại vào kinh! Nguy to rồi!","Chúa công, Thập Thường Thị vừa dẹp yên, nay Đổng Trác lại vào kinh! Nguy to rồi!",},
        [163] = {306,"Ngân Bình","Tinh Thái","Lần trước triều đình lệnh Đổng Trác diệt Hoàng Cân, hắn dám mượn cớ đưa quân Tây Lương đến đoạt Danh Tướng Lệnh của ta, thật là quá đáng!","Lần trước triều đình lệnh Đổng Trác diệt Hoàng Cân, hắn dám mượn cớ đưa quân Tây Lương đến đoạt Danh Tướng Lệnh của ta, thật là quá đáng!",},
        [164] = {306,"Tinh Thái","Nhân vật","Chúa công, chúng ta đi tìm Tào Tháo đi. Tào Tháo thuộc dòng dõi quan lại, quen nhiều biết rộng, chắc sẽ có cách đối phó Đổng Trác.","Chúa công, chúng ta đi tìm Tào Tháo đi. Tào Tháo thuộc dòng dõi quan lại, quen nhiều biết rộng, chắc sẽ có cách đối phó Đổng Trác.",},
        [165] = {306,"Ngân Bình","Nhân vật","Không chừng trên tay Tào Tháo còn có manh mối #Danh Tướng Lệnh# khác","Không chừng trên tay Tào Tháo còn có manh mối #Danh Tướng Lệnh# khác",},
        [166] = {306,"Nhân vật","Ngân Bình","Vậy cũng tốt! Đến Lạc Dương tìm Tào Tháo nào!","Vậy cũng tốt! Đến Lạc Dương tìm Tào Tháo nào!",},
        [167] = {401,"Tào Tháo","Nhân vật","Tiểu anh hùng, ngươi đến thật đúng lúc! Đổng Trác kéo quân vào kinh đô, làm loạn triều cương, làm nhiều chuyện bạo ngược!","Tiểu anh hùng, ngươi đến thật đúng lúc! Đổng Trác kéo quân vào kinh đô, làm loạn triều cương, làm nhiều chuyện bạo ngược!",},
        [168] = {401,"Tào Tháo","Nhân vật","Đổng Trác muốn diệt Viên Thiệu, nay Viên Thiệu đã chạy đến Ký Châu. Trước khi đi, Viên Thiệu nhờ ta gửi thư này cho Vương Tư Đồ, cùng bàn cách đối phó với Đổng Trác.","Đổng Trác muốn diệt Viên Thiệu, nay Viên Thiệu đã chạy đến Ký Châu. Trước khi đi, Viên Thiệu nhờ ta gửi thư này cho Vương Tư Đồ, cùng bàn cách đối phó với Đổng Trác.",},
        [169] = {401,"Nhân vật","Tào Tháo","Cùng đi nào! Ta đến cũng vì chuyện Đổng Trác vào kinh.","Cùng đi nào! Ta đến cũng vì chuyện Đổng Trác vào kinh.",},
        [170] = {401,"Vương Doãn","Tào Tháo","(Vương Doãn sau khi xem thư khóc lớn) Đổng Trác lộng quyền, xã tắc khó giữ, lão phu thật hổ thẹn.","(Vương Doãn sau khi xem thư khóc lớn) Đổng Trác lộng quyền, xã tắc khó giữ, lão phu thật hổ thẹn.",},
        [171] = {401,"Tào Tháo","Vương Doãn","Tư Đồ đừng lo, Tào Tháo tuy bất tài, nhưng cũng bằng lòng đi diệt Đổng Trác.","Tư Đồ đừng lo, Tào Tháo tuy bất tài, nhưng cũng bằng lòng đi diệt Đổng Trác.",},
        [172] = {401,"Nhân vật","Vương Doãn","Đổng Trác hại người vô số, ai cũng muốn diệt trừ hắn!","Đổng Trác hại người vô số, ai cũng muốn diệt trừ hắn!",},
        [173] = {401,"Vương Doãn","Tào Tháo","Có các anh hùng đây, triều đình có hi vọng rồi! Đổng Trác đến từ Tây Lương, thế như hùm sói, nếu mọi người không đồng lòng, rất dễ bị hắn đánh bại.","Có các anh hùng đây, triều đình có hi vọng rồi! Đổng Trác đến từ Tây Lương, thế như hùm sói, nếu mọi người không đồng lòng, rất dễ bị hắn đánh bại.",},
        [174] = {401,"Vương Doãn","Tào Tháo","Nếu các ngươi có thể đánh bại cao thủ trong phủ ta, chứng minh thực lực của các ngươi, ta sẽ tặng cho các ngươi Thất Tinh Đao, giúp các ngươi diệt Đổng Trác!","Nếu các ngươi có thể đánh bại cao thủ trong phủ ta, chứng minh thực lực của các ngươi, ta sẽ tặng cho các ngươi Thất Tinh Đao, giúp các ngươi diệt Đổng Trác!",},
        [175] = {401,"Nhân vật","Tào Tháo","Việc này có gì khó, chẳng qua một tên thị vệ, có đáng là gì! Tào huynh cứ nghỉ ngơi, để ta!","Việc này có gì khó, chẳng qua một tên thị vệ, có đáng là gì! Tào huynh cứ nghỉ ngơi, để ta!",},
        [176] = {402,"Vương Doãn","Tào Tháo","Hai vị quả là lợi hại! Thất Tinh Đao này tặng hai vị, nếu lần này thành công, đó là phúc của thiên hạ!","Hai vị quả là lợi hại! Thất Tinh Đao này tặng hai vị, nếu lần này thành công, đó là phúc của thiên hạ!",},
        [177] = {402,"Tào Tháo","Vương Doãn","Vương Tư Đồ chỉ cần chờ nghe tin lành là được!","Vương Tư Đồ chỉ cần chờ nghe tin lành là được!",},
        [178] = {402,"Tào Tháo","Nhân vật","Tiểu anh hùng, chút nữa vào phủ Đổng Trác, hãy giả làm tùy tùng của ta, tùy cơ ứng biến. Nếu ta ho hai tiếng, đó là tín hiệu ra tay!","Tiểu anh hùng, chút nữa vào phủ Đổng Trác, hãy giả làm tùy tùng của ta, tùy cơ ứng biến. Nếu ta ho hai tiếng, đó là tín hiệu ra tay!",},
        [179] = {402,"Nhân vật","Tào Tháo","Được, Tào huynh cứ yên tâm!","Được, Tào huynh cứ yên tâm!",},
        [180] = {403,"Đổng Trác","Tào Tháo","Mạnh Đức, hôm nay đến chỗ ta có chuyện gì?","Mạnh Đức, hôm nay đến chỗ ta có chuyện gì?",},
        [181] = {403,"Tào Tháo","Đổng Trác","Tào Tháo có một bảo đao, muốn dâng cho ân tướng. Khụ khụ!","Tào Tháo có một bảo đao, muốn dâng cho ân tướng. Khụ khụ!",},
        [182] = {403,"Đổng Trác","Tào Tháo","Để lão phu xem thử! Sắc bén như vậy, quả là đao tốt!","Để lão phu xem thử! Sắc bén như vậy, quả là đao tốt!",},
        [183] = {403,"Nhân vật","Đổng Trác","Lúc này không ra tay, còn đợi đến khi nào? Lão tặc Đổng Trác, nộp mạng đi!","Lúc này không ra tay, còn đợi đến khi nào? Lão tặc Đổng Trác, nộp mạng đi!",},
        [184] = {404,"Đổng Trác","Lữ Bố","Người đâu, có Thích Khách! Con ta Phụng Tiên ở đâu? Mau đến cứu ta!","Người đâu, có Thích Khách! Con ta Phụng Tiên ở đâu? Mau đến cứu ta!",},
        [185] = {404,"Lữ Bố","Nhân vật","Kẻ nào dám đụng đến nghĩa phụ của ta?","Kẻ nào dám đụng đến nghĩa phụ của ta?",},
        [186] = {404,"Nhân vật","Đổng Trác","Hừ! Nếu Lữ Bố không đến kịp lúc, Đổng tặc hôm nay đã phải đền tội!","Hừ! Nếu Lữ Bố không đến kịp lúc, Đổng tặc hôm nay đã phải đền tội!",},
        [187] = {404,"Đổng Trác","Nhân vật","Lại là ngươi! Lần trước chính ngươi đã cướp #Danh Tướng Lệnh#! Đúng lúc lắm! Phụng Tiên, diệt sạch bọn chúng cho ta!","Lại là ngươi! Lần trước chính ngươi đã cướp #Danh Tướng Lệnh#! Đúng lúc lắm! Phụng Tiên, diệt sạch bọn chúng cho ta!",},
        [188] = {404,"Tào Tháo","Nhân vật","Tiểu anh hùng chạy mau, đừng cố nữa! Lữ Bố bản lĩnh cao cường, phủ Đổng Trác người đông thế mạnh, tạm thời lánh nạn, chạy! (Thiếu niên và Tào Tháo cưỡi ngựa ra khỏi thành, chạy về hướng Đông Nam)","Tiểu anh hùng chạy mau, đừng cố nữa! Lữ Bố bản lĩnh cao cường, phủ Đổng Trác người đông thế mạnh, tạm thời lánh nạn, chạy! (Thiếu niên và Tào Tháo cưỡi ngựa ra khỏi thành, chạy về hướng Đông Nam)",},
        [189] = {405,"Trần Cung","Tào Tháo","Ta là quan Huyện Lệnh Trung Mâu, Trần Cung, các ngươi là ai?","Ta là quan Huyện Lệnh Trung Mâu, Trần Cung, các ngươi là ai?",},
        [190] = {405,"Tào Tháo","Trần Cung","Ta là khách buôn qua đường, ta họ Hoàng Phủ.","Ta là khách buôn qua đường, ta họ Hoàng Phủ.",},
        [191] = {405,"Trần Cung","Tào Tháo","Lúc ta còn ở Lạc Dương, từng biết mặt ngươi là Tào Tháo! Đổng Thừa Tướng đối đãi với ngươi không tệ, sao ngươi dám thích sát ngài!?","Lúc ta còn ở Lạc Dương, từng biết mặt ngươi là Tào Tháo! Đổng Thừa Tướng đối đãi với ngươi không tệ, sao ngươi dám thích sát ngài!?",},
        [192] = {405,"Trần Cung","Tào Tháo","Người đâu! Nhốt hai người này vào nhà lao, ngày mai giải đi kinh sư nhận thưởng.","Người đâu! Nhốt hai người này vào nhà lao, ngày mai giải đi kinh sư nhận thưởng.",},
        [193] = {405,"Tào Tháo","Trần Cung","Kẻ tầm thường sao hiểu được hoài bão lớn! Giờ ta đã bị lật tẩy rồi, cứ đánh một trận đi!","Kẻ tầm thường sao hiểu được hoài bão lớn! Giờ ta đã bị lật tẩy rồi, cứ đánh một trận đi!",},
        [194] = {406,"Trần Cung","Tào Tháo","Đổng Trác cậy thế lộng quyền, ta nhận bổng lộc triều đình, nếu không có lòng báo quốc, thì khác gì cầm thú! Vừa rồi ta chỉ thử lòng hai vị thôi!","Đổng Trác cậy thế lộng quyền, ta nhận bổng lộc triều đình, nếu không có lòng báo quốc, thì khác gì cầm thú! Vừa rồi ta chỉ thử lòng hai vị thôi!",},
        [195] = {406,"Trần Cung","Tào Tháo","Hai vị anh hùng quả là người trung nghĩa! Trần Cung nguyện từ quan đi theo hai vị.","Hai vị anh hùng quả là người trung nghĩa! Trần Cung nguyện từ quan đi theo hai vị.",},
        [196] = {406,"Tào Tháo","Trần Cung","Đầu tiên gặp được tiểu anh hùng, nay lại được Công Đài giúp đỡ, Tào Tháo quả thật may mắn! Nào nào, hôm nay chúng ta cùng uống no say!","Đầu tiên gặp được tiểu anh hùng, nay lại được Công Đài giúp đỡ, Tào Tháo quả thật may mắn! Nào nào, hôm nay chúng ta cùng uống no say!",},
        [197] = {406,"Tào Tháo","Trần Cung","Rượu gặp tri kỷ, ngàn ly không say! Cạn! Uống rượu vui ca, đời người được mấy!","Rượu gặp tri kỷ, ngàn ly không say! Cạn! Uống rượu vui ca, đời người được mấy!",},
        [198] = {406,"Nhân vật","Tào Tháo","Tào huynh, hay chúng ta cùng tỉ thí với nhau xem sao!","Tào huynh, hay chúng ta cùng tỉ thí với nhau xem sao!",},
        [199] = {406,"Tào Tháo","Nhân vật","Được được, tiểu anh hùng có nhã hứng, Tào Tháo đồng ý chiến đấu!","Được được, tiểu anh hùng có nhã hứng, Tào Tháo đồng ý chiến đấu!",},
        [200] = {407,"Tào Tháo","Nhân vật","Ha ha ha, tiểu anh hùng thật tài giỏi, Tào Tháo xin chịu thua!","Ha ha ha, tiểu anh hùng thật tài giỏi, Tào Tháo xin chịu thua!",},
        [201] = {407,"Nhân vật","","Quá khen quá khen! Tâm nguyện của ta là trở thành tướng mạnh nhất Tam Quốc!","Quá khen quá khen! Tâm nguyện của ta là trở thành tướng mạnh nhất Tam Quốc!",},
        [202] = {407,"Tào Tháo","","Tiểu anh hùng quả có chí lớn! Ta xin tặng #Danh Tướng Lệnh# này cho tiểu anh hùng, chúc tiểu anh hùng sớm hoàn thành ước nguyện","Tiểu anh hùng quả có chí lớn! Ta xin tặng #Danh Tướng Lệnh# này cho tiểu anh hùng, chúc tiểu anh hùng sớm hoàn thành ước nguyện",},
        [203] = {407,"Nhân vật","","Đa tạ! Tào huynh, sau này huynh có dự định gì?","Đa tạ! Tào huynh, sau này huynh có dự định gì?",},
        [204] = {407,"Tào Tháo","","Ta sẽ về Trần Lưu, chiêu binh mãi mã, hiệu triệu chư hầu trong thiên hạ cùng thảo phạt Đổng Trác!","Ta sẽ về Trần Lưu, chiêu binh mãi mã, hiệu triệu chư hầu trong thiên hạ cùng thảo phạt Đổng Trác!",},
        [205] = {407,"Nhân vật","","Thảo phạt Đổng Trác nhớ gọi ta, ta nhất định sẽ lấy được thủ cấp của hắn!","Thảo phạt Đổng Trác nhớ gọi ta, ta nhất định sẽ lấy được thủ cấp của hắn!",},
        [206] = {407,"Tào Tháo","","Ha ha ha, có tiểu anh hùng giúp ta, đại sự ắt thành!","Ha ha ha, có tiểu anh hùng giúp ta, đại sự ắt thành!",},
        [207] = {501,"Tinh Thái","Nhân vật","Từ khi chúa công và Tào Tháo phát hịch thảo phạt Đổng Trác đến nay, chư hầu lần lượt khởi binh hưởng ứng, chúa công thật lợi hại!","Từ khi chúa công và Tào Tháo phát hịch thảo phạt Đổng Trác đến nay, chư hầu lần lượt khởi binh hưởng ứng, chúa công thật lợi hại!",},
        [208] = {501,"Nhân vật","","Công Tôn Toản ở U Châu cũng đã tới, lần trước đại ca nhị ca tam ca tìm đến Công Tôn Toản, không biết lần này có theo cùng không..","Công Tôn Toản ở U Châu cũng đã tới, lần trước đại ca nhị ca tam ca tìm đến Công Tôn Toản, không biết lần này có theo cùng không..",},
        [209] = {501,"Lưu Bị","","Tứ đệ, nghe nói đệ và Tào Tháo phát hịch thảo phạt Đổng Trác, nên đến hỗ trợ!","Tứ đệ, nghe nói đệ và Tào Tháo phát hịch thảo phạt Đổng Trác, nên đến hỗ trợ!",},
        [210] = {501,"Nhân vật","","Đại ca nhị ca tam ca! Các huynh quả nhiên đã đến!","Đại ca nhị ca tam ca! Các huynh quả nhiên đã đến!",},
        [211] = {501,"Viên Thuật","","Hừ, chỉ là một kẻ vô danh mà cũng dám diễu võ dương oai ở đây!","Hừ, chỉ là một kẻ vô danh mà cũng dám diễu võ dương oai ở đây!",},
        [212] = {501,"Tào Tháo","","Viên Công Lộ, không được! Vị tiểu anh hùng này rất tài giỏi! Các lộ chư hầu vì chuyện thảo phạt Đổng Trác mà đến, sao lại đánh nhau, làm lỡ việc lớn!","Viên Công Lộ, không được! Vị tiểu anh hùng này rất tài giỏi! Các lộ chư hầu vì chuyện thảo phạt Đổng Trác mà đến, sao lại đánh nhau, làm lỡ việc lớn!",},
        [213] = {501,"Nhân vật","","Chư vị không phục nhau thì hãy tỷ thí một phen!","Chư vị không phục nhau thì hãy tỷ thí một phen!",},
        [214] = {501,"Viên Thiệu","","Được! Ai vô địch sẽ là minh chủ chư hầu! Viên Thiệu ta sẽ nhường #Danh Tướng Lệnh#!","Được! Ai vô địch sẽ là minh chủ chư hầu! Viên Thiệu ta sẽ nhường #Danh Tướng Lệnh#!",},
        [215] = {501,"Nhân vật","","Hi hi, vậy thì bắt đầu thôi, #Danh Tướng Lệnh# chắc chắn thuộc về ta!","Hi hi, vậy thì bắt đầu thôi, #Danh Tướng Lệnh# chắc chắn thuộc về ta!",},
        [216] = {502,"Lưu Bị","Nhân vật","Tứ đệ, đây là Công Tôn Toản, đồng môn ngày trước của ta.","Tứ đệ, đây là Công Tôn Toản, đồng môn ngày trước của ta.",},
        [217] = {502,"Nhân vật","","Thì ra là Bạch Mã Tướng Công Tôn Toản.","Thì ra là Bạch Mã Tướng Công Tôn Toản.",},
        [218] = {502,"Công Tôn Toản","","Hay lắm, đã là đệ của Huyền Đức thì hãy tỷ thí xem sao.","Hay lắm, đã là đệ của Huyền Đức thì hãy tỷ thí xem sao.",},
        [219] = {503,"Viên Thuật","Lưu Bị","Ngươi là ai, ta là Viên Thuật, con trai trưởng của Viên gia 4 đời tam công!","Ngươi là ai, ta là Viên Thuật, con trai trưởng của Viên gia 4 đời tam công!",},
        [220] = {503,"Lưu Bị","","Ta là dòng họ Hán thất, họ Lưu, tên Bị, tự Huyền Đức.","Ta là dòng họ Hán thất, họ Lưu, tên Bị, tự Huyền Đức.",},
        [221] = {503,"Viên Thuật","","Chỉ là một kẻ bán giày cỏ mà cũng dám tự xưng dòng họ Hán thất!","Chỉ là một kẻ bán giày cỏ mà cũng dám tự xưng dòng họ Hán thất!",},
        [222] = {503,"Nhân vật","","Viên Thuật, không được vô lễ với đại ca ta!","Viên Thuật, không được vô lễ với đại ca ta!",},
        [223] = {503,"Ngân Bình","","Viên Thuật, ngươi còn không xứng xách giày cho chúa công ta đâu!","Viên Thuật, ngươi còn không xứng xách giày cho chúa công ta đâu!",},
        [224] = {503,"Tinh Thái","","Chúa công, xông lên đi, đánh cho Viên Thuật tâm phục khẩu phục rồi tính!","Chúa công, xông lên đi, đánh cho Viên Thuật tâm phục khẩu phục rồi tính!",},
        [225] = {504,"Viên Thiệu","Nhân vật","Viên Thiệu xin chịu thua! Nguyện đề cử vị tiểu anh hùng này làm minh chủ! #Danh Tướng Lệnh# này, xin minh chủ hãy nhận.","Viên Thiệu xin chịu thua! Nguyện đề cử vị tiểu anh hùng này làm minh chủ! #Danh Tướng Lệnh# này, xin minh chủ hãy nhận.",},
        [226] = {504,"Nhân vật","","#Danh Tướng Lệnh# thì ta lấy! Nhưng chức minh chủ chư hầu này thì nhường cho ngươi đấy!","#Danh Tướng Lệnh# thì ta lấy! Nhưng chức minh chủ chư hầu này thì nhường cho ngươi đấy!",},
        [227] = {504,"Lưu Bị","","Tứ đệ, không được nói bậy!","Tứ đệ, không được nói bậy!",},
        [228] = {504,"Viên Thuật","","Ha ha, chức minh chủ này cuối cùng vẫn dành cho Viên gia chúng ta!","Ha ha, chức minh chủ này cuối cùng vẫn dành cho Viên gia chúng ta!",},
        [229] = {504,"Ngân Bình","","Đâu phải Viên Thuật ngồi, đắc ý gì chứ!","Đâu phải Viên Thuật ngồi, đắc ý gì chứ!",},
        [230] = {504,"Viên Thuật","","Ngươi... (Viên Thuật tức quá nói không nên lời)","Ngươi... (Viên Thuật tức quá nói không nên lời)",},
        [231] = {504,"Tinh Thái","Nhân vật","Chúa công, bại tướng dưới tay Hoa Hùng lại đến khiêu chiến! Chúa công, nghe nói Lữ Bố cũng đang giữ ải!","Chúa công, bại tướng dưới tay Hoa Hùng lại đến khiêu chiến! Chúa công, nghe nói Lữ Bố cũng đang giữ ải!",},
        [232] = {504,"Nhân vật","","Sợ hắn làm gì! Đi, chúng ta đến Hổ Lao Quan gặp Chiến Thần Lữ Bố!","Sợ hắn làm gì! Đi, chúng ta đến Hổ Lao Quan gặp Chiến Thần Lữ Bố!",},
        [233] = {601,"Lữ Bố","Nhân vật","Ta có ngựa Xích Thố và Họa Kích, lại có #Danh Tướng Lệnh# trong tay, thiên hạ này không còn ai là đối thủ của ta!","Ta có ngựa Xích Thố và Họa Kích, lại có #Danh Tướng Lệnh# trong tay, thiên hạ này không còn ai là đối thủ của ta!",},
        [234] = {601,"Nhân vật","","Chưa chắc đâu, ta có thể đánh bại ngươi!","Chưa chắc đâu, ta có thể đánh bại ngươi!",},
        [235] = {601,"Lữ Bố","","Là ngươi! Lần trước ngươi đã giúp Đại Nhĩ Lang cướp #Danh Tướng Lệnh# của ta!","Là ngươi! Lần trước ngươi đã giúp Đại Nhĩ Lang cướp #Danh Tướng Lệnh# của ta!",},
        [236] = {601,"Nhân vật","","Đúng, là ta đây! Sợ rồi chứ! Mau giao #Danh Tướng Lệnh# ra đây!","Đúng, là ta đây! Sợ rồi chứ! Mau giao #Danh Tướng Lệnh# ra đây!",},
        [237] = {601,"Lữ Bố","","Đánh bại thủ hạ của ta đi rồi tính!","Đánh bại thủ hạ của ta đi rồi tính!",},
        [238] = {602,"Lý Thôi","Ngân Bình","Vị tướng quân trẻ tuổi này võ nghệ cao cường, Đổng tướng có con gái, muốn gả cho tướng quân, thế nào?","Vị tướng quân trẻ tuổi này võ nghệ cao cường, Đổng tướng có con gái, muốn gả cho tướng quân, thế nào?",},
        [239] = {602,"Ngân Bình","","Đừng có mơ! Đổng Trác làm chuyện trái đạo lý, chúa công ta sao lại kết thông gia với nghịch tặc!","Đừng có mơ! Đổng Trác làm chuyện trái đạo lý, chúa công ta sao lại kết thông gia với nghịch tặc!",},
        [240] = {602,"Tinh Thái","","Con gái Đổng Trác xấu xí, làm sao xứng với chúa công nhà ta!","Con gái Đổng Trác xấu xí, làm sao xứng với chúa công nhà ta!",},
        [241] = {603,"Hoa Hùng","Quan Vũ","Ta đã trảm Phan Phượng, ai dám tới chiến!","Ta đã trảm Phan Phượng, ai dám tới chiến!",},
        [242] = {603,"Quan Vũ","","Hoa Hùng, đừng ngông cuồng!","Hoa Hùng, đừng ngông cuồng!",},
        [243] = {603,"Nhân vật","","Nhị ca, Hoa Hùng này là bại tướng dưới tay ta, đâu cần huynh phải ra tay! Hãy rót rượu rồi ta sẽ đi.","Nhị ca, Hoa Hùng này là bại tướng dưới tay ta, đâu cần huynh phải ra tay! Hãy rót rượu rồi ta sẽ đi.",},
        [244] = {604,"Quan Vũ","Lưu Bị","Tứ đệ quả nhiên oai hùng!","Tứ đệ quả nhiên oai hùng!",},
        [245] = {604,"Quan Vũ","","Tứ đệ đã trảm Hoa Hùng, lúc này không vào ải bắt sống Đổng Trác thì chờ đến bao giờ!","Tứ đệ đã trảm Hoa Hùng, lúc này không vào ải bắt sống Đổng Trác thì chờ đến bao giờ!",},
        [246] = {604,"Lưu Bị","","Nhị đệ hãy bình tĩnh, Lữ Bố anh dũng vô địch, bắt Lữ Bố trước, dễ diệt Đổng Trác.","Nhị đệ hãy bình tĩnh, Lữ Bố anh dũng vô địch, bắt Lữ Bố trước, dễ diệt Đổng Trác.",},
        [247] = {604,"Lưu Bị","","Tứ đệ, chúng ta giúp đệ chiến với Lữ Bố!","Tứ đệ, chúng ta giúp đệ chiến với Lữ Bố!",},
        [248] = {604,"Lữ Bố","","Lữ Bố ở đây! Kẻ nào dám đả thương nghĩa phụ ta!","Lữ Bố ở đây! Kẻ nào dám đả thương nghĩa phụ ta!",},
        [249] = {604,"Trương Phi","","Trương Phi người nước Yên ở đây! Lữ Bố, nộp mạng đi!","Trương Phi người nước Yên ở đây! Lữ Bố, nộp mạng đi!",},
        [250] = {604,"Lữ Bố","","Ta là Chiến Thần Lữ Bố, các ngươi cùng xông lên cũng chưa chắc thắng được ta! Ta mà thua, #Danh Tướng Lệnh# này sẽ thuộc về các ngươi!","Ta là Chiến Thần Lữ Bố, các ngươi cùng xông lên cũng chưa chắc thắng được ta! Ta mà thua, #Danh Tướng Lệnh# này sẽ thuộc về các ngươi!",},
        [251] = {604,"Nhân vật","","#Danh Tướng Lệnh# ta chắc chắn sẽ giành được! Chiến đấu nào!","#Danh Tướng Lệnh# ta chắc chắn sẽ giành được! Chiến đấu nào!",},
        [252] = {605,"Lữ Bố","Trương Phi","Ta rất nhớ Điêu Thuyền, không có tâm trạng để chiến đấu! Xin đưa #Danh Tướng Lệnh# cho các ngươi! (Lữ Bố lên ngựa chạy mất.)","Ta rất nhớ Điêu Thuyền, không có tâm trạng để chiến đấu! Xin đưa #Danh Tướng Lệnh# cho các ngươi! (Lữ Bố lên ngựa chạy mất.)",},
        [253] = {605,"Trương Phi","","Đừng hòng chạy!","Đừng hòng chạy!",},
        [254] = {605,"Lưu Bị","","Tam đệ, giặc cùng chớ đuổi!","Tam đệ, giặc cùng chớ đuổi!",},
        [255] = {605,"Lưu Bị","Nhân vật","Tứ đệ, nay Hổ Lao Quan đã phá, bọn ta sẽ theo Công Tôn Toản về U Châu. Hẹn ngày gặp lại!","Tứ đệ, nay Hổ Lao Quan đã phá, bọn ta sẽ theo Công Tôn Toản về U Châu. Hẹn ngày gặp lại!",},
        [256] = {605,"Nhân vật","","Tạm biệt!","Tạm biệt!",},
        [257] = {605,"Tinh Thái","","Chúa công lại có được 1 #Danh Tướng Lệnh#! Chúa công, giờ chúng ta đi đâu?","Chúa công lại có được 1 #Danh Tướng Lệnh#! Chúa công, giờ chúng ta đi đâu?",},
        [258] = {605,"Nhân vật","","Bắt giặc phải bắt kẻ cầm đầu, chi bằng tấn công Lạc Dương, bắt Đổng Trác thì sẽ nhổ cỏ tận gốc!","Bắt giặc phải bắt kẻ cầm đầu, chi bằng tấn công Lạc Dương, bắt Đổng Trác thì sẽ nhổ cỏ tận gốc!",},
        [259] = {701,"Tào Tháo","Nhân vật","Thám tử đến báo, nay Đổng Trác lo sợ uy danh của các hộ chư hầu, hiện đã hỏa thiêu Lạc Dương, cướp thiên tử đến Trường An, các vị thấy sao?","Thám tử đến báo, nay Đổng Trác lo sợ uy danh của các hộ chư hầu, hiện đã hỏa thiêu Lạc Dương, cướp thiên tử đến Trường An, các vị thấy sao?",},
        [260] = {701,"Nhân vật","","Đổng Trác tội không thể tha, chúng ta hãy thừa thế truy kích. Tấn công Lạc Dương, bắt sống Đổng Trác, còn chờ đến khi nào!","Đổng Trác tội không thể tha, chúng ta hãy thừa thế truy kích. Tấn công Lạc Dương, bắt sống Đổng Trác, còn chờ đến khi nào!",},
        [261] = {701,"Viên Thiệu","","Binh lính mệt mỏi, Đổng Trác người đông thế mạnh, chi bằng tạm hoãn để bàn tính. Nếu nhất quyết đuổi theo ắt sẽ bại.","Binh lính mệt mỏi, Đổng Trác người đông thế mạnh, chi bằng tạm hoãn để bàn tính. Nếu nhất quyết đuổi theo ắt sẽ bại.",},
        [262] = {701,"Tào Tháo","","Ngươi không đủ tư cách bàn việc lớn với ta!","Ngươi không đủ tư cách bàn việc lớn với ta!",},
        [263] = {701,"Nhân vật","","Viên Thiệu quả nhiên không thể làm nên việc lớn!","Viên Thiệu quả nhiên không thể làm nên việc lớn!",},
        [264] = {701,"Tào Tháo","","Nay chư hầu theo Viên Thiệu, chỉ có tiểu anh hùng là hào kiệt. Được quen biết tiểu anh hùng là may mắn của Tháo.","Nay chư hầu theo Viên Thiệu, chỉ có tiểu anh hùng là hào kiệt. Được quen biết tiểu anh hùng là may mắn của Tháo.",},
        [265] = {701,"Tào Tháo","","Tôn Kiên đã dẫn quân đến Lạc Dương, tiểu anh hùng, chúng ta hãy chia ra hai hướng, ta đuổi theo Đổng Trác, ngươi giúp Tôn Kiên, thế nào?","Tôn Kiên đã dẫn quân đến Lạc Dương, tiểu anh hùng, chúng ta hãy chia ra hai hướng, ta đuổi theo Đổng Trác, ngươi giúp Tôn Kiên, thế nào?",},
        [266] = {701,"Nhân vật","","Được, xin cáo biệt, ta đến Lạc Dương giúp Tôn Kiên đây!","Được, xin cáo biệt, ta đến Lạc Dương giúp Tôn Kiên đây!",},
        [267] = {702,"Hoàng Cái","Tinh Thái","Ngươi là ai, sao lại làm loạn trong thành Lạc Dương?","Ngươi là ai, sao lại làm loạn trong thành Lạc Dương?",},
        [268] = {702,"Tinh Thái","","Chúng ta đến giúp Tôn Kiên diệt trừ tàn dư của Đổng Trác!","Chúng ta đến giúp Tôn Kiên diệt trừ tàn dư của Đổng Trác!",},
        [269] = {702,"Hoàng Cái","","Ta thấy các ngươi trẻ tuổi háo thắng, chắc là gian tế do Đổng Trác phái đến!","Ta thấy các ngươi trẻ tuổi háo thắng, chắc là gian tế do Đổng Trác phái đến!",},
        [270] = {702,"Tinh Thái","","Ngươi thật không biết đúng sai gì cả, sớm muộn gì cũng rước khổ vào thân!","Ngươi thật không biết đúng sai gì cả, sớm muộn gì cũng rước khổ vào thân!",},
        [271] = {702,"Ngân Bình","","Hừ, không biết đúng sai, hãy đánh cho hắn phục rồi tính.","Hừ, không biết đúng sai, hãy đánh cho hắn phục rồi tính.",},
        [272] = {703,"Tinh Thái","Ngân Bình","Chúa công, Tôn Kiên này vốn là bộ hạ của Viên Thuật, chúng ta hãy thử hắn xem sao!","Chúa công, Tôn Kiên này vốn là bộ hạ của Viên Thuật, chúng ta hãy thử hắn xem sao!",},
        [273] = {703,"Ngân Bình","","Tinh Thái muội muội lại nghịch ngợm nữa rồi!","Tinh Thái muội muội lại nghịch ngợm nữa rồi!",},
        [274] = {703,"Tinh Thái","","Hi hi!","Hi hi!",},
        [275] = {703,"Tinh Thái","Tôn Kiên","Tôn Kiên, thừa tướng có con gái, dung mạo xinh đẹp, muốn gả cho con tướng quân, tướng quân thấy sao?","Tôn Kiên, thừa tướng có con gái, dung mạo xinh đẹp, muốn gả cho con tướng quân, tướng quân thấy sao?",},
        [276] = {703,"Tôn Kiên","","Đổng Trác làm chuyện trái đạo lý, ta hận không diệt cửu tộc của hắn, làm gì có chuyện kết thông gia với nghịch tặc!","Đổng Trác làm chuyện trái đạo lý, ta hận không diệt cửu tộc của hắn, làm gì có chuyện kết thông gia với nghịch tặc!",},
        [277] = {704,"Nhân vật","Tôn Kiên","Tinh Thái, đừng nghịch nữa! Tôn tướng quân là chân anh hùng, là tiên phong khi thảo phạt Hoàng Cân và Đổng Trác, trung thành với Hán thất.","Tinh Thái, đừng nghịch nữa! Tôn tướng quân là chân anh hùng, là tiên phong khi thảo phạt Hoàng Cân và Đổng Trác, trung thành với Hán thất.",},
        [278] = {704,"Tôn Kiên","","Tiểu anh hùng quá khen! Tiểu anh hùng chiến công lẫy lừng, thanh danh vang xa, Tôn Kiên tự thấy không bằng, #Danh Tướng Lệnh# này, ta lấy được trong thành Lạc Dương, xin hãy nhận cho.","Tiểu anh hùng quá khen! Tiểu anh hùng chiến công lẫy lừng, thanh danh vang xa, Tôn Kiên tự thấy không bằng, #Danh Tướng Lệnh# này, ta lấy được trong thành Lạc Dương, xin hãy nhận cho.",},
        [279] = {704,"Nhân vật","","Đa tạ Tôn tướng quân! Vậy ta nhận nhé!","Đa tạ Tôn tướng quân! Vậy ta nhận nhé!",},
        [280] = {704,"Nhân vật","","Tôn tướng quân sau này định thế nào?","Tôn tướng quân sau này định thế nào?",},
        [281] = {704,"Tôn Kiên","","Ôi, chư hầu lừa gạt lẫn nhau, Tôn mỗ quyết định trở về Giang Đông.","Ôi, chư hầu lừa gạt lẫn nhau, Tôn mỗ quyết định trở về Giang Đông.",},
        [282] = {704,"Nhân vật","","Như vậy rất tốt, ta và tướng quân chí hướng giống nhau, ta sẽ theo tướng quân đến Giang Đông du ngoạn một chuyến.","Như vậy rất tốt, ta và tướng quân chí hướng giống nhau, ta sẽ theo tướng quân đến Giang Đông du ngoạn một chuyến.",},
        [283] = {704,"Tôn Kiên","","Hiện giờ Viên Thiệu vẫn là minh chủ chư hầu, ta sẽ đến chào từ biệt ông ta.","Hiện giờ Viên Thiệu vẫn là minh chủ chư hầu, ta sẽ đến chào từ biệt ông ta.",},
        [284] = {801,"Tôn Kiên","Viên Thiệu","Tôn Kiên thân mang bệnh, muốn về Phượng Tường, đến để từ biệt.","Tôn Kiên thân mang bệnh, muốn về Phượng Tường, đến để từ biệt.",},
        [285] = {801,"Viên Thiệu","","Ngọc tỷ là bảo vật triều đình, ngươi lại giấu mang đi, muốn gì đây?","Ngọc tỷ là bảo vật triều đình, ngươi lại giấu mang đi, muốn gì đây?",},
        [286] = {801,"Tôn Kiên","","Ngọc tỷ không ở chỗ ta! Tôn Kiên ta thề với trời đất, nếu giấu giếm ngọc tỷ, sau này không được yên thân!","Ngọc tỷ không ở chỗ ta! Tôn Kiên ta thề với trời đất, nếu giấu giếm ngọc tỷ, sau này không được yên thân!",},
        [287] = {801,"Viên Thiệu","Tôn Kiên","Ngươi đã thề độc như vậy, nếu không cho đi thì hóa ra ta là người hẹp hòi! (Viên Thiệu đồng ý, Tôn Kiên từ biệt.)","Ngươi đã thề độc như vậy, nếu không cho đi thì hóa ra ta là người hẹp hòi! (Viên Thiệu đồng ý, Tôn Kiên từ biệt.)",},
        [288] = {801,"Viên Thiệu","Nhân vật","Người đâu! Mau viết thư cho Kinh Châu Mục Lưu Biểu, nói ông ta chặn đường Tôn Kiên đoạt lại ngọc tỷ.","Người đâu! Mau viết thư cho Kinh Châu Mục Lưu Biểu, nói ông ta chặn đường Tôn Kiên đoạt lại ngọc tỷ.",},
        [289] = {801,"Tinh Thái","Nhân vật","Chúa công, Viên Thiệu trong ngoài bất nhất, là kẻ tiểu nhân. Chúa công, theo ngài Tôn Kiên có giấu giếm ngọc tỷ thật không?","Chúa công, Viên Thiệu trong ngoài bất nhất, là kẻ tiểu nhân. Chúa công, theo ngài Tôn Kiên có giấu giếm ngọc tỷ thật không?",},
        [290] = {801,"Nhân vật","","Dù ngọc tỷ trong tay Tôn Kiên, cũng là do ông ta có công giành được! Hơn nữa, chúng ta đến Kinh Châu du ngoạn, ngọc tỷ nằm trong tay ai thì liên quan gì tới ta!","Dù ngọc tỷ trong tay Tôn Kiên, cũng là do ông ta có công giành được! Hơn nữa, chúng ta đến Kinh Châu du ngoạn, ngọc tỷ nằm trong tay ai thì liên quan gì tới ta!",},
        [291] = {801,"Tinh Thái","","Chúa công, nghe nói Lưu Biểu cũng có #Danh Tướng Lệnh#, chi bằng nhân cơ hội này hãy đến thăm hỏi.","Chúa công, nghe nói Lưu Biểu cũng có #Danh Tướng Lệnh#, chi bằng nhân cơ hội này hãy đến thăm hỏi.",},
        [292] = {801,"Nhân vật","","Lưu Biểu có #Danh Tướng Lệnh#! Vậy chúng ta còn chờ gì nữa! Đi, đến Kinh Châu!","Lưu Biểu có #Danh Tướng Lệnh#! Vậy chúng ta còn chờ gì nữa! Đi, đến Kinh Châu!",},
        [293] = {802,"Thái Mạo","Tinh Thái","Hai tiểu mỹ nữ ở đâu đến vậy, hãy theo Thái Mạo nào!","Hai tiểu mỹ nữ ở đâu đến vậy, hãy theo Thái Mạo nào!",},
        [294] = {802,"Tinh Thái","","Ngươi là ai? Có bản lĩnh gì?","Ngươi là ai? Có bản lĩnh gì?",},
        [295] = {802,"Thái Mạo","","Ta là thổ hào trong Kinh Châu Tương Dương, gần đây muội muội ta gả cho Lưu Biểu, Thái gia ta càng thêm nổi tiếng!","Ta là thổ hào trong Kinh Châu Tương Dương, gần đây muội muội ta gả cho Lưu Biểu, Thái gia ta càng thêm nổi tiếng!",},
        [296] = {802,"Tinh Thái","","Hừ, muội muội ngươi chỉ làm tiểu thiếp cho Lưu Biểu thôi mà, làm gì mà đắc ý như vậy!","Hừ, muội muội ngươi chỉ làm tiểu thiếp cho Lưu Biểu thôi mà, làm gì mà đắc ý như vậy!",},
        [297] = {802,"Ngân Bình","","Tép riu mà cũng dám phách lối, đánh!","Tép riu mà cũng dám phách lối, đánh!",},
        [298] = {803,"Lưu Biểu","Tinh Thái","Tiểu tử kia, không ngờ ngươi đánh bại được Thái Mạo Hoàng Tổ, thực lực không thể xem thường.","Tiểu tử kia, không ngờ ngươi đánh bại được Thái Mạo Hoàng Tổ, thực lực không thể xem thường.",},
        [299] = {803,"Tinh Thái","","Chúa công, Lưu Biểu này khi trẻ từng vào Kinh Châu, bình định 9 quân Kinh Châu, rất lợi hại.","Chúa công, Lưu Biểu này khi trẻ từng vào Kinh Châu, bình định 9 quân Kinh Châu, rất lợi hại.",},
        [300] = {803,"Ngân Bình","","Vậy thì đã sao. Chúa công ta vào được thành Tương Dương rồi đó thôi, chuẩn bị bị đánh đi","Vậy thì đã sao. Chúa công ta vào được thành Tương Dương rồi đó thôi, chuẩn bị bị đánh đi",},
        [301] = {803,"Lưu Biểu","","Ai đánh ai còn chưa biết đâu, các ngươi hãy xem sự lợi hại của Lưu Cảnh Thăng ta!","Ai đánh ai còn chưa biết đâu, các ngươi hãy xem sự lợi hại của Lưu Cảnh Thăng ta!",},
        [302] = {804,"Lưu Biểu","Nhân vật","Từ xưa anh hùng xuất thiếu niên, tướng quân trẻ tuổi quả nhiên lợi hại, Lưu Cảnh Thăng xin chịu thua!","Từ xưa anh hùng xuất thiếu niên, tướng quân trẻ tuổi quả nhiên lợi hại, Lưu Cảnh Thăng xin chịu thua!",},
        [303] = {804,"Nhân vật","","Bị đánh xong liền hạ giọng nhỉ! Nhận thua rồi thì hãy giao #Danh Tướng Lệnh# ra đây!","Bị đánh xong liền hạ giọng nhỉ! Nhận thua rồi thì hãy giao #Danh Tướng Lệnh# ra đây!",},
        [304] = {804,"Lưu Biểu","","Tướng quân minh xét, ta không có #Danh Tướng Lệnh#!","Tướng quân minh xét, ta không có #Danh Tướng Lệnh#!",},
        [305] = {804,"Nhân vật","","Cái gì? Không có #Danh Tướng Lệnh#? Vậy chẳng phải uổng công ta sao...","Cái gì? Không có #Danh Tướng Lệnh#? Vậy chẳng phải uổng công ta sao...",},
        [306] = {804,"Hoàng Cái","","Tướng quân, không xong rồi! Chúa công đã trúng gian kế của Khoái Lương, bị Hoàng Tổ lừa ra khỏi thành, một mình chiến đấu, nguy hiểm vô cùng!","Tướng quân, không xong rồi! Chúa công đã trúng gian kế của Khoái Lương, bị Hoàng Tổ lừa ra khỏi thành, một mình chiến đấu, nguy hiểm vô cùng!",},
        [307] = {804,"Nhân vật","","Hoàng Cái lão tướng quân đừng nôn nóng, chúng ta sẽ đi cứu Tôn Kiên ngay!","Hoàng Cái lão tướng quân đừng nôn nóng, chúng ta sẽ đi cứu Tôn Kiên ngay!",},
        [308] = {805,"Nhân vật","Tinh Thái","Chúng ta đã chậm một bước! Tuy bắt được Hoàng Tổ, nhưng Tôn Kiên tướng quân đã hy sinh.","Chúng ta đã chậm một bước! Tuy bắt được Hoàng Tổ, nhưng Tôn Kiên tướng quân đã hy sinh.",},
        [309] = {805,"Tinh Thái","","Lời thề độc của Tôn Kiên đã ứng nghiệm, xem ra, ngọc tỷ hẳn nằm trong tay hắn...(Nói xong, Tôn Sách cưỡi ngựa đuổi đến.)","Lời thề độc của Tôn Kiên đã ứng nghiệm, xem ra, ngọc tỷ hẳn nằm trong tay hắn...(Nói xong, Tôn Sách cưỡi ngựa đuổi đến.)",},
        [310] = {805,"Tôn Sách","","Kẻ nào hại phụ thân ta? Có phải các ngươi không?","Kẻ nào hại phụ thân ta? Có phải các ngươi không?",},
        [311] = {805,"Nhân vật","","Ngươi là?","Ngươi là?",},
        [312] = {805,"Tôn Sách","","Ta là trưởng tử của Tôn Kiên, Tiểu Bá Vương Tôn Sách! Các ngươi hại cha ta, nộp mạng đi!","Ta là trưởng tử của Tôn Kiên, Tiểu Bá Vương Tôn Sách! Các ngươi hại cha ta, nộp mạng đi!",},
        [313] = {806,"Hoàng Cái","Tôn Sách","Thiếu chủ hiểu lầm rồi! Vị tướng quân trẻ tuổi này đến để cứu chúa công, là bọn người Hoàng Tổ đã hại chúa công.","Thiếu chủ hiểu lầm rồi! Vị tướng quân trẻ tuổi này đến để cứu chúa công, là bọn người Hoàng Tổ đã hại chúa công.",},
        [314] = {806,"Tôn Sách","","Cha ta mất, Sách nhất thời nóng vội, hành sự lỗ mãng, đã hiểu lầm tướng quân, mong bỏ qua cho.","Cha ta mất, Sách nhất thời nóng vội, hành sự lỗ mãng, đã hiểu lầm tướng quân, mong bỏ qua cho.",},
        [315] = {806,"Nhân vật","","Được rồi. Ta đã bắt sống Hoàng Tổ, chi bằng dùng Hoàng Tổ đổi lấy thi thể của Tôn Kiên tướng quân?","Được rồi. Ta đã bắt sống Hoàng Tổ, chi bằng dùng Hoàng Tổ đổi lấy thi thể của Tôn Kiên tướng quân?",},
        [316] = {806,"Tôn Sách","","Vậy thì xin đa tạ! Đại ơn của tướng quân, Sách không biết lấy gì báo đáp, #Danh Tướng Lệnh# này xin tặng cho tướng quân!","Vậy thì xin đa tạ! Đại ơn của tướng quân, Sách không biết lấy gì báo đáp, #Danh Tướng Lệnh# này xin tặng cho tướng quân!",},
        [317] = {806,"Nhân vật","","Được, Tôn Sách tướng quân quả nhiên thẳng thắn, chẳng hay sau này định thế nào?","Được, Tôn Sách tướng quân quả nhiên thẳng thắn, chẳng hay sau này định thế nào?",},
        [318] = {806,"Tôn Sách","","Nhiều thuộc hạ cũ của phụ thân ở dưới trướng Viên Thuật, Sách sẽ tạm thời theo Viên Thuật, đợi đủ lông đủ cánh sẽ suy tính, trả thù cho cha.","Nhiều thuộc hạ cũ của phụ thân ở dưới trướng Viên Thuật, Sách sẽ tạm thời theo Viên Thuật, đợi đủ lông đủ cánh sẽ suy tính, trả thù cho cha.",},
        [319] = {806,"Tôn Sách","","Đến ngày đó, tướng quân đến Giang Đông, Sách sẽ hết lòng hỗ trợ! (Tôn Sách đau lòng, đưa thi thể Tôn Kiên đi xa)","Đến ngày đó, tướng quân đến Giang Đông, Sách sẽ hết lòng hỗ trợ! (Tôn Sách đau lòng, đưa thi thể Tôn Kiên đi xa)",},
        [320] = {806,"Tinh Thái","","Chúa công, giờ chúng ta đi đâu?","Chúa công, giờ chúng ta đi đâu?",},
        [321] = {806,"Nhân vật","","Hôm đó Tào Tháo đi chặn Đổng Trác, nghe nói đã đến Huỳnh Dương, chúng ta cũng đến đó xem sao!","Hôm đó Tào Tháo đi chặn Đổng Trác, nghe nói đã đến Huỳnh Dương, chúng ta cũng đến đó xem sao!",},
        [322] = {901,"Nhân vật","Hạ Hầu Đôn","Hạ Hầu tướng quân, Tào Tháo đâu rồi?","Hạ Hầu tướng quân, Tào Tháo đâu rồi?",},
        [323] = {901,"Hạ Hầu Đôn","Nhân vật","Đổng tặc hỏa thiêu Lạc Dương, trốn đến Trường An, chúa công đến Huỳnh Dương diệt Đổng tặc!","Đổng tặc hỏa thiêu Lạc Dương, trốn đến Trường An, chúa công đến Huỳnh Dương diệt Đổng tặc!",},
        [324] = {901,"Nhân vật","Hạ Hầu Đôn","Hiện tình hình sao rồi?","Hiện tình hình sao rồi?",},
        [325] = {901,"Hạ Hầu Đôn","Nhân vật","Thái Thú Huỳnh Dương là Từ Vinh giúp Đổng tặc, Lữ Bố sau khi nhận tinh binh, Lý Nho lại hiến kế mai phục, đại quân của chúa công đã không chống đỡ được!","Thái Thú Huỳnh Dương là Từ Vinh giúp Đổng tặc, Lữ Bố sau khi nhận tinh binh, Lý Nho lại hiến kế mai phục, đại quân của chúa công đã không chống đỡ được!",},
        [326] = {901,"Nhân vật","Hạ Hầu Đôn","Vậy Tào Tháo sẽ gặp nguy hiểm! Ta sẽ đến ứng cứu!","Vậy Tào Tháo sẽ gặp nguy hiểm! Ta sẽ đến ứng cứu!",},
        [327] = {902,"Tinh Thái","Nhân vật","Chúa công, không xong rồi! Tào Tháo đã trúng mai phục ngoài thành Huỳnh Dương, bị đại quân của Đổng Trác đánh giáp công, rất nguy hiểm!","Chúa công, không xong rồi! Tào Tháo đã trúng mai phục ngoài thành Huỳnh Dương, bị đại quân của Đổng Trác đánh giáp công, rất nguy hiểm!",},
        [328] = {902,"Nhân vật","Tinh Thái","Tinh Thái muội muội đừng lo! Đã có ta!","Tinh Thái muội muội đừng lo! Đã có ta!",},
        [329] = {902,"Tinh Thái","Nhân vật","Chúa công, người kia hình như là Hạ Hầu Uyên, chúng ta hãy đi hỏi tình hình.","Chúa công, người kia hình như là Hạ Hầu Uyên, chúng ta hãy đi hỏi tình hình.",},
        [330] = {902,"Hạ Hầu Uyên","Nhân vật","Lại có truy binh! Chúa công hãy đi trước, Hạ Hầu Uyên sẽ chặn hậu!","Lại có truy binh! Chúa công hãy đi trước, Hạ Hầu Uyên sẽ chặn hậu!",},
        [331] = {902,"Hạ Hầu Uyên","Nhân vật","Các ngươi là ai?","Các ngươi là ai?",},
        [332] = {902,"Ngân Bình","Hạ Hầu Uyên","Tên Hạ Hầu Uyên không biết điều! Ngay cả chúa công nhà ta cũng không biết!","Tên Hạ Hầu Uyên không biết điều! Ngay cả chúa công nhà ta cũng không biết!",},
        [333] = {903,"Tinh Thái","Tào Tháo","Tào Tháo, vai người trúng tên, để ta trị thương.","Tào Tháo, vai người trúng tên, để ta trị thương.",},
        [334] = {903,"Tào Tháo","Tinh Thái","Là ai muốn hãm hại ta?","Là ai muốn hãm hại ta?",},
        [335] = {903,"Tinh Thái","Tào Tháo","Chúa công, Tào Tháo chắc là do vết thương nhiễm trùng nên thần trí không được minh mẫn.","Chúa công, Tào Tháo chắc là do vết thương nhiễm trùng nên thần trí không được minh mẫn.",},
        [336] = {903,"Ngân Bình","Tào Tháo","Tào Tháo rất đa nghi, ngay cả chúa công cũng không nhận ra.","Tào Tháo rất đa nghi, ngay cả chúa công cũng không nhận ra.",},
        [337] = {903,"Nhân vật","Tào Tháo","Tin chắc là Tào huynh chinh chiến liên tục, lại bị thương, nên không rõ địch ta, chúng ta phải đánh ngất người để trị thương.","Tin chắc là Tào huynh chinh chiến liên tục, lại bị thương, nên không rõ địch ta, chúng ta phải đánh ngất người để trị thương.",},
        [338] = {904,"Tinh Thái","Tào Tháo","Tào Tháo, người khỏe lại chưa?","Tào Tháo, người khỏe lại chưa?",},
        [339] = {904,"Tào Tháo","Tinh Thái","Đa tạ anh hùng cứu giúp! Đa tạ y thuật của Tinh Thái cô nương. Cô nương y thuật tài giỏi, không biết sư phụ là ai?","Đa tạ anh hùng cứu giúp! Đa tạ y thuật của Tinh Thái cô nương. Cô nương y thuật tài giỏi, không biết sư phụ là ai?",},
        [340] = {904,"Tinh Thái","Tào Tháo","Sư phụ của ta là Thần Y Hoa Đà, có một vị đạo nhân tên Tả Từ mang cho ta một túi thuốc, bảo ta hết lòng trợ giúp người có thiên mệnh.","Sư phụ của ta là Thần Y Hoa Đà, có một vị đạo nhân tên Tả Từ mang cho ta một túi thuốc, bảo ta hết lòng trợ giúp người có thiên mệnh.",},
        [341] = {904,"Tào Tháo","Tinh Thái","Y thuật của Tinh Thái cô nương thì ra có nguồn gốc kỳ lạ như thế. Vậy ai là người có thiên mệnh?","Y thuật của Tinh Thái cô nương thì ra có nguồn gốc kỳ lạ như thế. Vậy ai là người có thiên mệnh?",},
        [342] = {904,"Tinh Thái","Tào Tháo","Chúa công nhà ta chính là người có thiên mệnh! Người sẽ là người kết thúc loạn thế này!","Chúa công nhà ta chính là người có thiên mệnh! Người sẽ là người kết thúc loạn thế này!",},
        [343] = {904,"Tinh Thái","Tào Tháo","Chúa công, Tinh Thái sẽ theo người, bảo vệ cho người!","Chúa công, Tinh Thái sẽ theo người, bảo vệ cho người!",},
        [344] = {904,"Ngân Bình","Tinh Thái","Chúa công, còn có ta! Ta sẽ giúp người đánh nhau!","Chúa công, còn có ta! Ta sẽ giúp người đánh nhau!",},
        [345] = {904,"Nhân vật","Ngân Bình","Được! Được!","Được! Được!",},
        [346] = {904,"Tào Tháo","Nhân vật","Quá giỏi, vị tiểu anh hùng, thủ hạ của người quả thật tài giỏi.","Quá giỏi, vị tiểu anh hùng, thủ hạ của người quả thật tài giỏi.",},
        [347] = {905,"Hạ Hầu Đôn","Tào Tháo","Chúa công, Hạ Hầu Đôn dẫn theo vài kỵ binh đến, Tào Nhân Lý Điển Nhạc Tiến cũng đã đến.","Chúa công, Hạ Hầu Đôn dẫn theo vài kỵ binh đến, Tào Nhân Lý Điển Nhạc Tiến cũng đã đến.",},
        [348] = {905,"Tào Tháo","Hạ Hầu Đôn","Huỳnh Dương Thái Thú Từ Vinh mai phục ta, hôm nay đa tạ anh hùng cứu giúp, nếu không đã nguy mất.","Huỳnh Dương Thái Thú Từ Vinh mai phục ta, hôm nay đa tạ anh hùng cứu giúp, nếu không đã nguy mất.",},
        [349] = {905,"Hạ Hầu Đôn","Nhân vật","Hạ Hầu Đôn có một thỉnh cầu!","Hạ Hầu Đôn có một thỉnh cầu!",},
        [350] = {905,"Nhân vật","Hạ Hầu Đôn","Xin cứ nói.","Xin cứ nói.",},
        [351] = {905,"Hạ Hầu Đôn","Nhân vật","Nghe nói tướng quân ở Hổ Lao Quan bốn lần chiến Lữ Bố, uy chấn chư hầu, lực chiến cao cường, danh tiếng khắp thiên hạ, không biết Hạ Hầu Đôn có thể tỉ thí với tướng quân không?","Nghe nói tướng quân ở Hổ Lao Quan bốn lần chiến Lữ Bố, uy chấn chư hầu, lực chiến cao cường, danh tiếng khắp thiên hạ, không biết Hạ Hầu Đôn có thể tỉ thí với tướng quân không?",},
        [352] = {905,"Nhân vật","Hạ Hầu Đôn","Chỉ có không ngừng chiến đấu mới có thể gia tăng thực lực. Hạ Hầu tướng quân hãy ra chiêu!","Chỉ có không ngừng chiến đấu mới có thể gia tăng thực lực. Hạ Hầu tướng quân hãy ra chiêu!",},
        [353] = {906,"Hạ Hầu Đôn","Nhân vật","Tướng quân quả thật lợi, Hạ Hầu Đôn chịu thua. Danh Tướng Lệnh xin tặng cho tướng quân!","Tướng quân quả thật lợi, Hạ Hầu Đôn chịu thua. Danh Tướng Lệnh xin tặng cho tướng quân!",},
        [354] = {906,"Nhân vật","Tào Tháo","Được! Được!","Được! Được!",},
        [355] = {906,"Tào Tháo","Nhân vật","Đổng Trác lừa dối thiên tử, hãm hại bá tánh, gây nhiều tội ác, ai ai cũng căm hận!","Đổng Trác lừa dối thiên tử, hãm hại bá tánh, gây nhiều tội ác, ai ai cũng căm hận!",},
        [356] = {906,"Tào Tháo","Nhân vật","Nhưng tiếc, trong trận chiến tại Huỳnh Dương ta bị bại, nếu không ta quyết lấy thủ cấp của Đổng Trác!","Nhưng tiếc, trong trận chiến tại Huỳnh Dương ta bị bại, nếu không ta quyết lấy thủ cấp của Đổng Trác!",},
        [357] = {906,"Nhân vật","Tào Tháo","Tào huynh yên tâm, đợi ta đến Trường An sẽ diệt Đổng Trác!","Tào huynh yên tâm, đợi ta đến Trường An sẽ diệt Đổng Trác!",},
        [358] = {906,"Tào Tháo","Nhân vật","Quả là anh hùng hào kiệt! Diệt Đổng Trác nhờ vào ngươi! Ta sẽ về Nội Hân chiêu mộ binh lính, hẹn ngày tái ngộ.","Quả là anh hùng hào kiệt! Diệt Đổng Trác nhờ vào ngươi! Ta sẽ về Nội Hân chiêu mộ binh lính, hẹn ngày tái ngộ.",},
        [359] = {906,"Ngân Bình","Tinh Thái","Đổng Trác sẽ không thể sống lâu! Chúng ta hãy mau đến Trường An diệt Đổng Trác!","Đổng Trác sẽ không thể sống lâu! Chúng ta hãy mau đến Trường An diệt Đổng Trác!",},
        [360] = {906,"Tinh Thái","Nhân vật","Việc này không thể gấp! Lần trước ám sát Đổng Trác thất bại, xung quanh Đổng Trác rất đông binh lính. Chúa công, hay là chúng ta hãy vào Trường An trước, sau đó tìm Vương Doãn do thám tình hình?","Việc này không thể gấp! Lần trước ám sát Đổng Trác thất bại, xung quanh Đổng Trác rất đông binh lính. Chúa công, hay là chúng ta hãy vào Trường An trước, sau đó tìm Vương Doãn do thám tình hình?",},
        [361] = {906,"Nhân vật","Tinh Thái","Được, cứ theo lời Tinh Thái! Chúng ta đến Trường An tìm Vương Doãn!","Được, cứ theo lời Tinh Thái! Chúng ta đến Trường An tìm Vương Doãn!",},
        [362] = {1001,"Tinh Thái","Ngân Bình","Đổng Trác phóng hỏa Lạc Dương, ép bá tánh đến Trường An. Chúa công, phía trước là phủ đệ của Vương Doãn tại Trường An.","Đổng Trác phóng hỏa Lạc Dương, ép bá tánh đến Trường An. Chúa công, phía trước là phủ đệ của Vương Doãn tại Trường An.",},
        [363] = {1001,"Ngân Bình","Tinh Thái","Đó không phải là Lữ Bố sao? Hắn tìm Vương Doãn có việc gì?","Đó không phải là Lữ Bố sao? Hắn tìm Vương Doãn có việc gì?",},
        [364] = {1001,"Lữ Bố","Vương Doãn","Ta và Điêu Thuyền yêu nhau, Vương Tư Đồ hứa gả Điêu Thuyền cho ta, sao lại đưa đến bên thái sư, vậy là có ý gì?","Ta và Điêu Thuyền yêu nhau, Vương Tư Đồ hứa gả Điêu Thuyền cho ta, sao lại đưa đến bên thái sư, vậy là có ý gì?",},
        [365] = {1001,"Vương Doãn","Lữ Bố","Tướng quân đã trách nhầm lão phu! Hôm qua thái sư đến phủ nói chọn ngày tháng tốt nên đã lệnh ta đưa Điêu Thuyền qua đó cho tướng quân.","Tướng quân đã trách nhầm lão phu! Hôm qua thái sư đến phủ nói chọn ngày tháng tốt nên đã lệnh ta đưa Điêu Thuyền qua đó cho tướng quân.",},
        [366] = {1001,"Vương Doãn","Lữ Bố","Tướng quân hãy suy nghĩ, thái sư đích thân đến, lão phu làm sao cản được?","Tướng quân hãy suy nghĩ, thái sư đích thân đến, lão phu làm sao cản được?",},
        [367] = {1001,"Lữ Bố","Vương Doãn","Tư đồ đừng trách tội, là Lữ Bố hiểu lầm, để ngày sau quay về taa5 tội. (Lữ Bố lập tức về Đổng phủ)","Tư đồ đừng trách tội, là Lữ Bố hiểu lầm, để ngày sau quay về taa5 tội. (Lữ Bố lập tức về Đổng phủ)",},
        [368] = {1001,"Ngân Bình","Tinh Thái","Vừa rồi ở phủ của Vương Doãn đã chứng kiến một việc.","Vừa rồi ở phủ của Vương Doãn đã chứng kiến một việc.",},
        [369] = {1001,"Tinh Thái","Ngân Bình","Điêu Thuyền tỉ tỉ quả thật khổ, Đổng Trác háo sắc, chắc...","Điêu Thuyền tỉ tỉ quả thật khổ, Đổng Trác háo sắc, chắc...",},
        [370] = {1001,"Nhân vật","Ngân Bình","Chúng ta mau tìm Vương Doãn hỏi rõ, không thể để Điêu Thuyền chịu khổ!","Chúng ta mau tìm Vương Doãn hỏi rõ, không thể để Điêu Thuyền chịu khổ!",},
        [371] = {1002,"Nhân vật","Vương Doãn","Vương Doãn, tại sao ngươi lại đưa Điêu Thuyền qua bên Đổng Trác?","Vương Doãn, tại sao ngươi lại đưa Điêu Thuyền qua bên Đổng Trác?",},
        [372] = {1002,"Vương Doãn","Nhân vật","Bá tánh đang chịu nguy hiểm. Ác tặc Đổng Trác, muốn soán ngôi vua.","Bá tánh đang chịu nguy hiểm. Ác tặc Đổng Trác, muốn soán ngôi vua.",},
        [373] = {1002,"Vương Doãn","Nhân vật","Lão phu dùng kế mỹ nhân, để Điêu Thuyền ly gián Đổng Trác và Lữ Bố, Lữ Bố tin chắc sẽ diệt Đổng Trác.","Lão phu dùng kế mỹ nhân, để Điêu Thuyền ly gián Đổng Trác và Lữ Bố, Lữ Bố tin chắc sẽ diệt Đổng Trác.",},
        [374] = {1002,"Nhân vật","Vương Doãn","Diệt Đổng Trác, hãy để ta! Sao lại để phận gái yếu đuối phải hi sinh!","Diệt Đổng Trác, hãy để ta! Sao lại để phận gái yếu đuối phải hi sinh!",},
        [375] = {1002,"Nhân vật","","Chúng ta mau đi cứu Điêu Thuyền!","Chúng ta mau đi cứu Điêu Thuyền!",},
        [376] = {1003,"Tinh Thái","Điêu Thuyền","Điêu Thuyền tỉ tỉ, chúng tôi đến cứu tỉ, hãy mau theo chúng tôi.","Điêu Thuyền tỉ tỉ, chúng tôi đến cứu tỉ, hãy mau theo chúng tôi.",},
        [377] = {1003,"Điêu Thuyền","Tinh Thái","Tư đồ chưa từng ép ta, là do ta tự nguyện, chỉ cần diệt được Đổng Trắc, thiên hạ sẽ được bình yên!","Tư đồ chưa từng ép ta, là do ta tự nguyện, chỉ cần diệt được Đổng Trắc, thiên hạ sẽ được bình yên!",},
        [378] = {1003,"Tinh Thái","Điêu Thuyền","Điêu Thuyền tỉ tỉ quả là một cô gái kiên cường.","Điêu Thuyền tỉ tỉ quả là một cô gái kiên cường.",},
        [379] = {1003,"Tinh Thái","Điêu Thuyền","Điêu Thuyền tỉ tỉ yên tâm, chúa công nhà ta tin chắc có thể diệt Đổng Trác, cho Điêu Thuyền cô nương một cuộc sống bình an.","Điêu Thuyền tỉ tỉ yên tâm, chúa công nhà ta tin chắc có thể diệt Đổng Trác, cho Điêu Thuyền cô nương một cuộc sống bình an.",},
        [380] = {1004,"Nhân vật","Lữ Bố","Lữ Bố, hãy đi diệt Đổng Trác, Điêu Thuyền nếu đồng ý theo ngươi, ta sẽ không cản!","Lữ Bố, hãy đi diệt Đổng Trác, Điêu Thuyền nếu đồng ý theo ngươi, ta sẽ không cản!",},
        [381] = {1004,"Lữ Bố","Nhân vật","Tuy ta không thích Đổng Trác, nhưng Đổng Trác với ta là tình phụ tử, diệt hắn sẽ bị người đời dị nghị!","Tuy ta không thích Đổng Trác, nhưng Đổng Trác với ta là tình phụ tử, diệt hắn sẽ bị người đời dị nghị!",},
        [382] = {1004,"Nhân vật","Lữ Bố","Ngươi họ Lữ, hắn ta họ Đổng, sao lại là phụ tử?","Ngươi họ Lữ, hắn ta họ Đổng, sao lại là phụ tử?",},
        [383] = {1004,"Nhân vật","Lữ Bố","Hắn muốn giành Điêu Thuyền, sao có tình phụ tử với ngươi!","Hắn muốn giành Điêu Thuyền, sao có tình phụ tử với ngươi!",},
        [384] = {1004,"Lữ Bố","Nhân vật","Đúng vậy, tên Đổng Trác, ta giành thiên hạ cho hắn, vậy mà hắn dám giành Điều Thuyền với ta!","Đúng vậy, tên Đổng Trác, ta giành thiên hạ cho hắn, vậy mà hắn dám giành Điều Thuyền với ta!",},
        [385] = {1004,"Lữ Bố","Nhân vật","Hãy mau diệt Đổng Trác!","Hãy mau diệt Đổng Trác!",},
        [386] = {1004,"Nhân vật","Lữ Bố","Lữ Bố thật có khí thế! Quả đúng là anh hùng hào kiệt! Ta rất thích!","Lữ Bố thật có khí thế! Quả đúng là anh hùng hào kiệt! Ta rất thích!",},
        [387] = {1005,"Đổng Trác","Nhân vật","Ta phải mau vào cung! Thiên tử truyền gọi, nói sẽ nhường ngôi cho ta. Đổng Trác ta quả thật có thể làm hoàng đế rồi!","Ta phải mau vào cung! Thiên tử truyền gọi, nói sẽ nhường ngôi cho ta. Đổng Trác ta quả thật có thể làm hoàng đế rồi!",},
        [388] = {1005,"Nhân vật","Đổng Trác","Đổng Trác, ngươi dám ức hiếp Điêu Thuyền, hãy nộp mạng!","Đổng Trác, ngươi dám ức hiếp Điêu Thuyền, hãy nộp mạng!",},
        [389] = {1005,"Đổng Trác","Nhân vật","Lại là ngươi! Dám giả thánh chỉ lừa ta để mai phục!","Lại là ngươi! Dám giả thánh chỉ lừa ta để mai phục!",},
        [390] = {1005,"Lữ Bố","Đổng Trác","Thiên tử có thánh chỉ là diệt Đổng Trác, để cứu bá tánh!","Thiên tử có thánh chỉ là diệt Đổng Trác, để cứu bá tánh!",},
        [391] = {1006,"Đổng Trác","Lữ Bố","Ngươi dám phản ta!","Ngươi dám phản ta!",},
        [392] = {1006,"Đổng Trác","Lữ Bố","Ta không phục, ta vốn là Thái Sư Đại Hán... Ta sắp có Điêu Thuyền... Ta sắp làm hoàng thượng... (Đổng Trác bị diệt)","Ta không phục, ta vốn là Thái Sư Đại Hán... Ta sắp có Điêu Thuyền... Ta sắp làm hoàng thượng... (Đổng Trác bị diệt)",},
        [393] = {1006,"Lữ Bố","Điêu Thuyền","Thuyền Nhi, kể từ khi chia tay tại Phụng Nghi Đình, ta ngày đêm nhớ nàng!","Thuyền Nhi, kể từ khi chia tay tại Phụng Nghi Đình, ta ngày đêm nhớ nàng!",},
        [394] = {1006,"Điêu Thuyền","Lữ Bố","Thần thiếp giờ đã không xứng với người, hu hu...","Thần thiếp giờ đã không xứng với người, hu hu...",},
        [395] = {1006,"Lữ Bố","Điêu Thuyền","Thuyền Nhi của ta, để nàng chịu khổ rồi! Phụng Tiên sẽ không từ bỏ nàng!","Thuyền Nhi của ta, để nàng chịu khổ rồi! Phụng Tiên sẽ không từ bỏ nàng!",},
        [396] = {1006,"Lữ Bố","Điêu Thuyền","Sau này Phụng Tiên sẽ bảo vệ nàng, yêu thương nàng!","Sau này Phụng Tiên sẽ bảo vệ nàng, yêu thương nàng!",},
        [397] = {1006,"Nhân vật","Điêu Thuyền","Khụ! (Xin hai vị đừng bày tỏ tình cảm nữa, ta chịu không nổi rồi!)","Khụ! (Xin hai vị đừng bày tỏ tình cảm nữa, ta chịu không nổi rồi!)",},
        [398] = {1006,"Điêu Thuyền","Nhân vật","Để tướng quân cười chê! Đa tạ tướng quân cứu giúp! Tại Mi Ổ, Đổng tặc từng tặng ta 1 Danh Tướng Lệnh, mong tướng quân hãy tiếp nhận!","Để tướng quân cười chê! Đa tạ tướng quân cứu giúp! Tại Mi Ổ, Đổng tặc từng tặng ta 1 Danh Tướng Lệnh, mong tướng quân hãy tiếp nhận!",},
        [399] = {1006,"Nhân vật","Lữ Bố","Đa tạ Điêu Thuyền cô nương! Vậy ta không khách khí!","Đa tạ Điêu Thuyền cô nương! Vậy ta không khách khí!",},
        [400] = {1006,"Lữ Bố","Nhân vật","Tiểu anh hùng, từ giờ ngươi sẽ là bằng hữu của ta!","Tiểu anh hùng, từ giờ ngươi sẽ là bằng hữu của ta!",},
        [401] = {1006,"Lữ Bố","Nhân vật","Sau này khi có cần đến Lữ Phụng Tiên, chỉ cần lên tiếng, ta sẽ không từ chối.","Sau này khi có cần đến Lữ Phụng Tiên, chỉ cần lên tiếng, ta sẽ không từ chối.",},
        [402] = {1006,"Nhân vật","Lữ Bố","Ta chỉ có một yêu cầu là sau này đối xử tốt với Điêu Thuyền cô nương! Cáo từ!","Ta chỉ có một yêu cầu là sau này đối xử tốt với Điêu Thuyền cô nương! Cáo từ!",},
        [403] = {1006,"Tinh Thái","Nhân vật","Chúa công, giờ chúng ta đi đâu?","Chúa công, giờ chúng ta đi đâu?",},
        [404] = {1006,"Ngân Bình","Nhân vật","Đổng Trác sau khi bị diệt, thuộc hạ Lý Thôi Quách Tị đều đã chạy trốn đến Lương Châu.","Đổng Trác sau khi bị diệt, thuộc hạ Lý Thôi Quách Tị đều đã chạy trốn đến Lương Châu.",},
        [405] = {1006,"Nhân vật","Ngân Bình","Họ cũng là người xấu, Đổng Trác cũng vì thế mà có thể làm càn, chúng ta phải truy đuổi!","Họ cũng là người xấu, Đổng Trác cũng vì thế mà có thể làm càn, chúng ta phải truy đuổi!",},
        [406] = {1101,"Lý Thôi","Ngân Bình","Lại là ngươi! Là ngươi tự đến nộp mạng!","Lại là ngươi! Là ngươi tự đến nộp mạng!",},
        [407] = {1101,"Ngân Bình","Lý Thôi","Đừng nhiều lời! Ngươi chẳng đáng hầu hạ cho chúa công!","Đừng nhiều lời! Ngươi chẳng đáng hầu hạ cho chúa công!",},
        [408] = {1101,"Tinh Thái","Lý Thôi","Chúa công, chúng ta mau diệt hắn ta!","Chúa công, chúng ta mau diệt hắn ta!",},
        [409] = {1102,"Lý Thôi","Tinh Thái","Lại thất bại! Tiểu tử, lần sau ta quyết diệt ngươi. (Lý Thôi nhanh chóng trốn mất)","Lại thất bại! Tiểu tử, lần sau ta quyết diệt ngươi. (Lý Thôi nhanh chóng trốn mất)",},
        [410] = {1102,"Tinh Thái","Lý Thôi","Có bản lĩnh thì đừng chạy!","Có bản lĩnh thì đừng chạy!",},
        [411] = {1102,"Ngân Bình","Lý Thôi","Phía trước còn có Quách Tị, chúng ta hãy diệt Quách Tị!","Phía trước còn có Quách Tị, chúng ta hãy diệt Quách Tị!",},
        [412] = {1103,"Giả Hủ","Lý Thôi","Mọi người đừng vội, ta có một kế!","Mọi người đừng vội, ta có một kế!",},
        [413] = {1103,"Giả Hủ","Lý Thôi","Nếu thành công có thể quay lại làm việc cho triều đình, nếu không thành, chúng ta mới chạy trốn tiếp.","Nếu thành công có thể quay lại làm việc cho triều đình, nếu không thành, chúng ta mới chạy trốn tiếp.",},
        [414] = {1103,"Lý Thôi","Giả Hủ","Văn Hòa, ngươi có cách gì không?","Văn Hòa, ngươi có cách gì không?",},
        [415] = {1103,"Giả Hủ","Lý Thôi","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.",},
        [416] = {1103,"Lý Thôi","Giả Hủ","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.",},
        [417] = {1104,"Tinh Thái","Nhân vật","Chúa công nguy rồi! Lý Thôi Quách Tị sau khi chạy trốn, nghe kế của Giả Hủ, truyền tin khắp nơi nói triều đình muốn diệt hết người Tây Lương, họ tập hợp được gần 10 vạn người, phản công Trường An.","Chúa công nguy rồi! Lý Thôi Quách Tị sau khi chạy trốn, nghe kế của Giả Hủ, truyền tin khắp nơi nói triều đình muốn diệt hết người Tây Lương, họ tập hợp được gần 10 vạn người, phản công Trường An.",},
        [418] = {1104,"Tinh Thái","Nhân vật","Nghe nói Vương Doãn đã bị diệt, Lý Thôi Quách Tị còn ép Hiến Đế phong họ làm Đại Tướng Quân!","Nghe nói Vương Doãn đã bị diệt, Lý Thôi Quách Tị còn ép Hiến Đế phong họ làm Đại Tướng Quân!",},
        [419] = {1104,"Nhân vật","Tinh Thái","Sao lại như thế! Còn Lữ Bố đâu, không phải hắn ta trấn giữ Trường An sao?","Sao lại như thế! Còn Lữ Bố đâu, không phải hắn ta trấn giữ Trường An sao?",},
        [420] = {1104,"Ngân Bình","Nhân vật","Lữ Bố tuy anh dũng, nhưng binh lính quá ít, Tây Lương có hơn 10 vạn quân! Nghe nói hắn đã dẫn vài thuộc hạ chạy đến Hoài Nam nhờ Viên Thuật!","Lữ Bố tuy anh dũng, nhưng binh lính quá ít, Tây Lương có hơn 10 vạn quân! Nghe nói hắn đã dẫn vài thuộc hạ chạy đến Hoài Nam nhờ Viên Thuật!",},
        [421] = {1104,"Nhân vật","Ngân Bình","Nếu như thế, chúng ta phải nhanh chóng về Trường An!","Nếu như thế, chúng ta phải nhanh chóng về Trường An!",},
        [422] = {1105,"Tinh Thái","Giả Hủ","Giả Hủ, nếu không phải kế của ngươi, Trường An sao lại phải chịu tai kiếp này!","Giả Hủ, nếu không phải kế của ngươi, Trường An sao lại phải chịu tai kiếp này!",},
        [423] = {1105,"Giả Hủ","Tinh Thái","Ta vốn là người Lương Châu, đây chỉ là kế bảo tồn tính mạng! Nếu không phải ta, văn võ bá quan trong Trường An mất mạng càng nhiều!","Ta vốn là người Lương Châu, đây chỉ là kế bảo tồn tính mạng! Nếu không phải ta, văn võ bá quan trong Trường An mất mạng càng nhiều!",},
        [424] = {1105,"Giả Hủ","Tinh Thái","Các ngươi yên tâm, có ta nhất định sẽ khống chế Lý Thôi Quách Tị, bảo vệ bá quan và thiên tử. (Giả Hủ chạy trốn)","Các ngươi yên tâm, có ta nhất định sẽ khống chế Lý Thôi Quách Tị, bảo vệ bá quan và thiên tử. (Giả Hủ chạy trốn)",},
        [425] = {1105,"Tinh Thái","Giả Hủ","Đáng hận! Giả Hủ thật gian xảo, lại để hắn chạy thoát!","Đáng hận! Giả Hủ thật gian xảo, lại để hắn chạy thoát!",},
        [426] = {1106,"Tinh Thái","Nhân vật","Chúa công, Mã Đằng, Hàn Toại dẫn binh đến thảo phạt Lý Thôi Quách Tị!","Chúa công, Mã Đằng, Hàn Toại dẫn binh đến thảo phạt Lý Thôi Quách Tị!",},
        [427] = {1106,"Tinh Thái","Nhân vật","Đó là Mã Siêu con trai của Mã Đằng, ta nghĩ hắn đã hiểu lầm chúng ta!","Đó là Mã Siêu con trai của Mã Đằng, ta nghĩ hắn đã hiểu lầm chúng ta!",},
        [428] = {1106,"Ngân Bình","Tinh Thái","Mã Siêu gương mặt tú tuấn, mắt sáng như sao, thần hình cao to oai vệ, chỉ mới 17 tuổi, quả thật anh dũng!","Mã Siêu gương mặt tú tuấn, mắt sáng như sao, thần hình cao to oai vệ, chỉ mới 17 tuổi, quả thật anh dũng!",},
        [429] = {1106,"Tinh Thái","Ngân Bình","Ngân Bình tỉ tỉ, chẳng lẽ người động lòng?","Ngân Bình tỉ tỉ, chẳng lẽ người động lòng?",},
        [430] = {1106,"Ngân Bình","Tinh Thái","Ta chỉ thích đối thủ có thực lực mạnh mẽ!","Ta chỉ thích đối thủ có thực lực mạnh mẽ!",},
        [431] = {1106,"Mã Siêu","Nhân vật","Mã Mạnh ở đây! Phản tặc hãy mau nộp mạng!","Mã Mạnh ở đây! Phản tặc hãy mau nộp mạng!",},
        [432] = {1106,"Nhân vật","Mã Siêu","Lại một vị danh tướng. Chúng ta hãy ra gặp hắn!","Lại một vị danh tướng. Chúng ta hãy ra gặp hắn!",},
        [433] = {1106,"Tinh Thái","Mã Siêu","Mã Siêu ngươi đến thảo phạt Lý Thôi Quách Tị đúng không? Chúng ta là liên minh, chẳng lẽ ngươi không biết chúa công ta?","Mã Siêu ngươi đến thảo phạt Lý Thôi Quách Tị đúng không? Chúng ta là liên minh, chẳng lẽ ngươi không biết chúa công ta?",},
        [434] = {1106,"Ngân Bình","Mã Siêu","Đánh bại hắn ta nói tiếp!","Đánh bại hắn ta nói tiếp!",},
        [435] = {1107,"Nhân vật","Mã Siêu","Trận đấu thật sảng khoái!","Trận đấu thật sảng khoái!",},
        [436] = {1107,"Mã Siêu","Nhân vật","Thì ra người chính là vị anh hùng diệt Đổng Trác, danh chấn 18 chư hầu! Mã Siêu có thể quen biết tướng quân quả là vinh dự của Mã Siêu!","Thì ra người chính là vị anh hùng diệt Đổng Trác, danh chấn 18 chư hầu! Mã Siêu có thể quen biết tướng quân quả là vinh dự của Mã Siêu!",},
        [437] = {1107,"Nhân vật","Mã Siêu","Nhưng tiếc để Mạnh Khởi huynh đến công không rồi! Lý Thôi Quách Tị Giả Hủ đều đã bị ta đánh bại!","Nhưng tiếc để Mạnh Khởi huynh đến công không rồi! Lý Thôi Quách Tị Giả Hủ đều đã bị ta đánh bại!",},
        [438] = {1107,"Mã Siêu","Nhân vật","Ta có thể quen biết người bằng hữu như người đã là không uổng phí đời người!","Ta có thể quen biết người bằng hữu như người đã là không uổng phí đời người!",},
        [439] = {1107,"Mã Siêu","Nhân vật","Huynh đệ, đại quân ta sắp về Lương Châu! Danh Tướng Lệnh này ta tặng cho ngươi. Sau này nếu có cơ hội đến Lương Châu, Mã Mạnh sẽ đón tiếp chu đáo! (Mã Siêu cáo từ)","Huynh đệ, đại quân ta sắp về Lương Châu! Danh Tướng Lệnh này ta tặng cho ngươi. Sau này nếu có cơ hội đến Lương Châu, Mã Mạnh sẽ đón tiếp chu đáo! (Mã Siêu cáo từ)",},
        [440] = {1107,"Tinh Thái","Nhân vật","Chúa công, Trung Nguyên tạm thời không có chiến sự, tiếp theo chúng ta đi đâu?","Chúa công, Trung Nguyên tạm thời không có chiến sự, tiếp theo chúng ta đi đâu?",},
        [441] = {1107,"Nhân vật","Tinh Thái","Vùng đông bắc của U Châu Thanh Châu Ký Châu đang có chiến sự lớn! Đại ca cùng Công Tôn Toản tại U Châu, không biết hiện giờ ra sao?","Vùng đông bắc của U Châu Thanh Châu Ký Châu đang có chiến sự lớn! Đại ca cùng Công Tôn Toản tại U Châu, không biết hiện giờ ra sao?",},
        [442] = {1107,"Nhân vật","Tinh Thái","Chúng ta đến U Châu tìm Lưu Bị đại ca và cha của các ngươi!","Chúng ta đến U Châu tìm Lưu Bị đại ca và cha của các ngươi!",},
        [443] = {1107,"Tinh Thái","Nhân vật","Lúc trước cha bên cạnh, ta thường xuyên trách cha nói nhiều, giờ đã lâu không gặp, lại cảm thấy nhớ người!","Lúc trước cha bên cạnh, ta thường xuyên trách cha nói nhiều, giờ đã lâu không gặp, lại cảm thấy nhớ người!",},
        [444] = {1201,"Tinh Thái","Nhân vật","Chúa công, đường đến U Châu còn xa, chúng ta hãy nghỉ ngợi tại Ký Châu, sau đó đi tiếp U Châu, ý người sao?","Chúa công, đường đến U Châu còn xa, chúng ta hãy nghỉ ngợi tại Ký Châu, sau đó đi tiếp U Châu, ý người sao?",},
        [445] = {1201,"Ngân Bình","Nhân vật","Chúa công, Công Tôn Toản phái người mang thư đến!","Chúa công, Công Tôn Toản phái người mang thư đến!",},
        [446] = {1201,"Nhân vật","Ngân Bình","Trong thư nói, Viên Thiệu bảo Công Tôn Toản đánh Ký Châu, sau khi hạ Ký Châu sẽ chia đôi.","Trong thư nói, Viên Thiệu bảo Công Tôn Toản đánh Ký Châu, sau khi hạ Ký Châu sẽ chia đôi.",},
        [447] = {1201,"Ngân Bình","Nhân vật","Viên Thiệu thật gian xảo, hắn muốn đoạt Ký Châu nhưng lại để Công Tôn Toản xuất binh.","Viên Thiệu thật gian xảo, hắn muốn đoạt Ký Châu nhưng lại để Công Tôn Toản xuất binh.",},
        [448] = {1201,"Nhân vật","Ngân Bình","Ngân Bình nói đúng, Ký Châu Mục hiện tại là ai?","Ngân Bình nói đúng, Ký Châu Mục hiện tại là ai?",},
        [449] = {1201,"Tinh Thái","Nhân vật","Chúa công, là Hàn Phức! Công Tôn Toản đánh Ký Châu, phụ thân sẽ tham gia, chúng ta nhanh đi giúp ngài!","Chúa công, là Hàn Phức! Công Tôn Toản đánh Ký Châu, phụ thân sẽ tham gia, chúng ta nhanh đi giúp ngài!",},
        [450] = {1202,"Tinh Thái","Hàn Phức","Đến trễ rồi! Hàn Phức đã dâng Ký Châu đầu hàng Viên Thiệu!","Đến trễ rồi! Hàn Phức đã dâng Ký Châu đầu hàng Viên Thiệu!",},
        [451] = {1202,"Nhân vật","Hàn Phức","Hàn Phức, sao ngươi lại dễ dàng dâng Ký Châu đầu hàng Viên Thiệu chứ?","Hàn Phức, sao ngươi lại dễ dàng dâng Ký Châu đầu hàng Viên Thiệu chứ?",},
        [452] = {1202,"Ngân Bình","Hàn Phức","Viên Thiệu chỉ là một Thái Thú nho nhỏ, Ký Châu tài nguyên dồi dào, binh lực hùng hậu, có gì phải sợ?","Viên Thiệu chỉ là một Thái Thú nho nhỏ, Ký Châu tài nguyên dồi dào, binh lực hùng hậu, có gì phải sợ?",},
        [453] = {1202,"Ngân Bình","Hàn Phức","Hàn Phức, chẳng lẽ ngươi không biết Công Tôn Toản đánh Ký Châu là gian kế của Viên Thiệu sao?","Hàn Phức, chẳng lẽ ngươi không biết Công Tôn Toản đánh Ký Châu là gian kế của Viên Thiệu sao?",},
        [454] = {1202,"Hàn Phức","Ngân Bình","Cái gì? Kế của Viên Thiệu, ta trúng kế rồi!","Cái gì? Kế của Viên Thiệu, ta trúng kế rồi!",},
        [455] = {1202,"Ngân Bình","Hàn Phức","Ngươi yếu đuối như thế, sao triều đình lại để ngươi làm Ký Châu Mục chứ?","Ngươi yếu đuối như thế, sao triều đình lại để ngươi làm Ký Châu Mục chứ?",},
        [456] = {1202,"Ngân Bình","Hàn Phức","Tên nhút nhát, lui ra!","Tên nhút nhát, lui ra!",},
        [457] = {1202,"Tinh Thái","Hàn Phức","Tỉ tỉ Ngân Bình đang nhắc nhở ngươi, nhanh bỏ trốn thôi, Viên Thiệu sẽ không bỏ qua cho ngươi!","Tỉ tỉ Ngân Bình đang nhắc nhở ngươi, nhanh bỏ trốn thôi, Viên Thiệu sẽ không bỏ qua cho ngươi!",},
        [458] = {1202,"Ngân Bình","Hàn Phức","Ngươi nói nhiều quá!","Ngươi nói nhiều quá!",},
        [459] = {1202,"Hàn Phức","Ngân Bình","Đa tạ hai vị cô nương. (Hàn Phức bỏ chạy)","Đa tạ hai vị cô nương. (Hàn Phức bỏ chạy)",},
        [460] = {1203,"Công Tôn Việt","Nhân vật","Tướng quân dũng cảm thiện chiến, Công Tôn Việt không sánh bằng!","Tướng quân dũng cảm thiện chiến, Công Tôn Việt không sánh bằng!",},
        [461] = {1203,"Nhân vật","Công Tôn Việt","Công Tôn Toản là gì của ngươi!","Công Tôn Toản là gì của ngươi!",},
        [462] = {1203,"Công Tôn Việt","Nhân vật","Công Tôn Toản là ca ca của ta! Ca ca phái ta đi gặp Viên Thiệu đòi lại Ký Châu.","Công Tôn Toản là ca ca của ta! Ca ca phái ta đi gặp Viên Thiệu đòi lại Ký Châu.",},
        [463] = {1203,"Công Tôn Việt","Nhân vật","Ca ca ta giỏi võ, ngay cả thủ lĩnh Ô Hoàn ở Liêu Tây cũng bị thu phục!","Ca ca ta giỏi võ, ngay cả thủ lĩnh Ô Hoàn ở Liêu Tây cũng bị thu phục!",},
        [464] = {1203,"Nhân vật","Công Tôn Việt","Ha ha. (Tên này chính là đệ đệ Công Tôn Toản)","Ha ha. (Tên này chính là đệ đệ Công Tôn Toản)",},
        [465] = {1203,"Tinh Thái","Công Tôn Việt","Công Tôn Việt, ngươi thấy phụ thân Trương Phi của ta ở đâu không?","Công Tôn Việt, ngươi thấy phụ thân Trương Phi của ta ở đâu không?",},
        [466] = {1203,"Công Tôn Việt","Tinh Thái","Phụ thân ngươi đang ở Thảo Nguyên Huyện.","Phụ thân ngươi đang ở Thảo Nguyên Huyện.",},
        [467] = {1203,"Tinh Thái","Công Tôn Việt","Chúa công, nhanh đi Thảo Nguyên Huyện tìm phụ thân!","Chúa công, nhanh đi Thảo Nguyên Huyện tìm phụ thân!",},
        [468] = {1204,"Lưu Bị","Nhân vật","Tứ đệ, lâu không gặp, lực chiến lại tăng nữa rồi!","Tứ đệ, lâu không gặp, lực chiến lại tăng nữa rồi!",},
        [469] = {1204,"Nhân vật","Lưu Bị","Thì ra đại ca đang làm quốc tướng Thảo Nguyên, chúc mừng các huynh.","Thì ra đại ca đang làm quốc tướng Thảo Nguyên, chúc mừng các huynh.",},
        [470] = {1204,"Lưu Bị","Nhân vật","So với tứ đệ chúng ta còn kém xa, đệ hiện tại đang nổi tiếng lắm đấy!","So với tứ đệ chúng ta còn kém xa, đệ hiện tại đang nổi tiếng lắm đấy!",},
        [471] = {1204,"Nhân vật","Lưu Bị","Đại ca lại khiêm tốn rồi, ai mà không biết đại ca nhân đức chứ!","Đại ca lại khiêm tốn rồi, ai mà không biết đại ca nhân đức chứ!",},
        [472] = {1204,"Trương Phi","Nhân vật","Thôi thôi! Hai người đừng tự khen lẫn nhau nữa! Nhị ca đỏ mặt rồi kìa!","Thôi thôi! Hai người đừng tự khen lẫn nhau nữa! Nhị ca đỏ mặt rồi kìa!",},
        [473] = {1204,"Quan Vũ","Nhân vật","Hừ! Mặt ta lúc nào chẳng đỏ!","Hừ! Mặt ta lúc nào chẳng đỏ!",},
        [474] = {1204,"Trương Phi","Nhân vật","Tứ đệ tăng lực chiến rồi, đến đây cùng ta đánh thử nào!","Tứ đệ tăng lực chiến rồi, đến đây cùng ta đánh thử nào!",},
        [475] = {1205,"Lưu Bị","Công Tôn Toản","Công Tôn huynh đến Thảo Nguyên làm gì?","Công Tôn huynh đến Thảo Nguyên làm gì?",},
        [476] = {1205,"Công Tôn Toản","Lưu Bị","Huyền Đức, nhanh giao tứ đệ ngươi ra đây, hắn ở Ký Châu tiêu diệt đệ đệ Công Tôn Việt của ta!","Huyền Đức, nhanh giao tứ đệ ngươi ra đây, hắn ở Ký Châu tiêu diệt đệ đệ Công Tôn Việt của ta!",},
        [477] = {1205,"Nhân vật","Công Tôn Toản","Công Tôn Toản, chớ vu oan giá họa! Ta đấu với Công Tôn Việt nhưng không hạ sát thủ!","Công Tôn Toản, chớ vu oan giá họa! Ta đấu với Công Tôn Việt nhưng không hạ sát thủ!",},
        [478] = {1205,"Công Tôn Toản","Nhân vật","Một mạng đến một mạng!","Một mạng đến một mạng!",},
        [479] = {1206,"Nhân vật","Công Tôn Toản","Công Tôn Toản, ta không lấy mạng ngươi, ta nói lại lần nữa, ta không diệt Công Tôn Việt!","Công Tôn Toản, ta không lấy mạng ngươi, ta nói lại lần nữa, ta không diệt Công Tôn Việt!",},
        [480] = {1206,"Lưu Bị","Công Tôn Toản","Công Tôn huynh, chuyện này có hiểu lầm gì đó!","Công Tôn huynh, chuyện này có hiểu lầm gì đó!",},
        [481] = {1206,"Trương Phi","Công Tôn Toản","Đúng! Tứ đệ đã nói không là không!","Đúng! Tứ đệ đã nói không là không!",},
        [482] = {1206,"Lưu Bị","Công Tôn Toản","Công Tôn huynh, ai nói với ngươi là ta diệt Công Tôn Việt!","Công Tôn huynh, ai nói với ngươi là ta diệt Công Tôn Việt!",},
        [483] = {1206,"Công Tôn Toản","Lưu Bị","Viên Thiệu viết thư nói cho ta biết!","Viên Thiệu viết thư nói cho ta biết!",},
        [484] = {1206,"Lưu Bị","Công Tôn Toản","Hừ! Đây rõ ràng là Viên Thiệu dùng kế đoạt Ký Châu nhưng không muốn chia phần cho ngươi, dùng kế để mượn tay tứ đệ ta diệt ngươi!","Hừ! Đây rõ ràng là Viên Thiệu dùng kế đoạt Ký Châu nhưng không muốn chia phần cho ngươi, dùng kế để mượn tay tứ đệ ta diệt ngươi!",},
        [485] = {1206,"Trương Phi","Công Tôn Toản","Đại ca, thám tử báo lại, Viên Thiệu phái người giả mạ tứ đệ dùng loạn tiễn tiêu diệt Công Tôn Việt!","Đại ca, thám tử báo lại, Viên Thiệu phái người giả mạ tứ đệ dùng loạn tiễn tiêu diệt Công Tôn Việt!",},
        [486] = {1206,"Công Tôn Toản","Trương Phi","Viên Thiệu bỉ ổi vô liêm sỉ, ta phải tập hợp đại quân thảo phạt Viên Thiệu!","Viên Thiệu bỉ ổi vô liêm sỉ, ta phải tập hợp đại quân thảo phạt Viên Thiệu!",},
        [487] = {1206,"Công Tôn Toản","Trương Phi","Công Tôn Toản đã hiểu lầm tướng quân! Danh Tướng Lệnh này xem như chuộc lỗi!","Công Tôn Toản đã hiểu lầm tướng quân! Danh Tướng Lệnh này xem như chuộc lỗi!",},
        [488] = {1206,"Nhân vật","Công Tôn Toản","Nhớ khi xưa ta nhường chức minh chủ chư hầu cho Viên Thiệu, hắn không báo ân mà còn vu oan cho ta, hừ, ta cùng ngươi đánh Viên Thiệu!","Nhớ khi xưa ta nhường chức minh chủ chư hầu cho Viên Thiệu, hắn không báo ân mà còn vu oan cho ta, hừ, ta cùng ngươi đánh Viên Thiệu!",},
        [489] = {1301,"Công Tôn Toản","Viên Thiệu","Viên Thiệu, tên vô ơn nhà ngươi dám bán đứng ta?","Viên Thiệu, tên vô ơn nhà ngươi dám bán đứng ta?",},
        [490] = {1301,"Viên Thiệu","Công Tôn Toản","Hàn Phức bất tài, dâng Ký Châu cho ta, liên quan gì đến Công Tôn Toản nhà ngươi?","Hàn Phức bất tài, dâng Ký Châu cho ta, liên quan gì đến Công Tôn Toản nhà ngươi?",},
        [491] = {1301,"Nhân vật","Viên Thiệu","Viên Thiệu, khi xưa ta thấy ngươi là người trung nghĩa nên đề cử ngươi làm minh chủ, nay ngươi lại hãm hại ta sao!","Viên Thiệu, khi xưa ta thấy ngươi là người trung nghĩa nên đề cử ngươi làm minh chủ, nay ngươi lại hãm hại ta sao!",},
        [492] = {1301,"Nhân vật","Viên Thiệu","Đầu hàng đi, ta sẽ tha mạng cho ngươi!","Đầu hàng đi, ta sẽ tha mạng cho ngươi!",},
        [493] = {1301,"Viên Thiệu","Nhân vật","Nay ta có Ký Châu, văn thần võ tướng, lương thảo đầy đủ, sợ gì ngươi chứ!","Nay ta có Ký Châu, văn thần võ tướng, lương thảo đầy đủ, sợ gì ngươi chứ!",},
        [494] = {1301,"Viên Thiệu","Nhân vật","Đại tướng Nhan Lương Văn Xú của ta ở đâu? Tiêu diệt bọn chúng!","Đại tướng Nhan Lương Văn Xú của ta ở đâu? Tiêu diệt bọn chúng!",},
        [495] = {1301,"Nhân vật","Viên Thiệu","Hàn Phức khốn kiếp, lại nối giáo cho giặc rồi!","Hàn Phức khốn kiếp, lại nối giáo cho giặc rồi!",},
        [496] = {1301,"Nhân vật","Viên Thiệu","Nhan Lương Văn Xú là gì, đến bao nhiêu ta diệt bấy nhiêu!","Nhan Lương Văn Xú là gì, đến bao nhiêu ta diệt bấy nhiêu!",},
        [497] = {1302,"Văn Xú","Tinh Thái","Hôm qua bại dưới tay Nhan Lương, nay ta đang không vui, các ngươi muốn chiến sao!","Hôm qua bại dưới tay Nhan Lương, nay ta đang không vui, các ngươi muốn chiến sao!",},
        [498] = {1302,"Tinh Thái","Văn Xú","Văn Xú, ngươi sinh vào sáng sớm phải không?","Văn Xú, ngươi sinh vào sáng sớm phải không?",},
        [499] = {1302,"Văn Xú","Tinh Thái","Sao ngươi biết?","Sao ngươi biết?",},
        [500] = {1302,"Tinh Thái","Văn Xú","Vì buổi sáng là giờ sửu, mà ngươi thì nhìn như trâu, ha ha!","Vì buổi sáng là giờ sửu, mà ngươi thì nhìn như trâu, ha ha!",},
        [501] = {1302,"Văn Xú","Tinh Thái","Hừ, đúng vậy thì sao!","Hừ, đúng vậy thì sao!",},
        [502] = {1302,"Ngân Bình","Văn Xú","Vừa ngu vừa lì, chẳng lẽ không biết Tinh Thái muội muội đang đùa ngươi sao!","Vừa ngu vừa lì, chẳng lẽ không biết Tinh Thái muội muội đang đùa ngươi sao!",},
        [503] = {1303,"Tinh Thái","Trương Hợp","Tỉ xinh đẹp quá!","Tỉ xinh đẹp quá!",},
        [504] = {1303,"Trương Hợp","Tinh Thái","Cái gì? Trương Hợp ta đây là đấng nam nhi đấy!","Cái gì? Trương Hợp ta đây là đấng nam nhi đấy!",},
        [505] = {1303,"Tinh Thái","Trương Hợp","Ta không tin, tỉ tỉ đang đùa ta sao!","Ta không tin, tỉ tỉ đang đùa ta sao!",},
        [506] = {1303,"Tinh Thái","Trương Hợp","Tỉ tỉ có bí quyết dưỡng nhan gì không?","Tỉ tỉ có bí quyết dưỡng nhan gì không?",},
        [507] = {1303,"Trương Hợp","Tinh Thái","Hừ, tên hư hỏng, để ta dạy dỗ ngươi!","Hừ, tên hư hỏng, để ta dạy dỗ ngươi!",},
        [508] = {1304,"Viên Thiệu","Công Tôn Toản","Không ngờ Viên Thiệu có ngày thất bại! Công Tôn Toản, hoàng thượng có chỉ.","Không ngờ Viên Thiệu có ngày thất bại! Công Tôn Toản, hoàng thượng có chỉ.",},
        [509] = {1304,"Công Tôn Toản","Viên Thiệu","Không ngờ hoàng thượng phái người đến giảng hòa, không xong rồi!","Không ngờ hoàng thượng phái người đến giảng hòa, không xong rồi!",},
        [510] = {1304,"Viên Thiệu","Nhân vật","Danh Tướng Lệnh này tặng tướng quân để đền bù!","Danh Tướng Lệnh này tặng tướng quân để đền bù!",},
        [511] = {1304,"Nhân vật","Viên Thiệu","Hừ! Danh Tướng Lệnh này xem như đổi một mạng của ngươi. (Công Tôn Toản và Viên Thiệu rời đi)","Hừ! Danh Tướng Lệnh này xem như đổi một mạng của ngươi. (Công Tôn Toản và Viên Thiệu rời đi)",},
        [512] = {1304,"Tinh Thái","Nhân vật","Lúc trước ta cho rằng Công Tôn Toản là một anh hùng, nay xem ra vẫn kém xa Viên Thiệu.","Lúc trước ta cho rằng Công Tôn Toản là một anh hùng, nay xem ra vẫn kém xa Viên Thiệu.",},
        [513] = {1304,"Ngân Bình","Nhân vật","Đúng thế! Lúc trước nói hay lắm, nay triều đình phái thuyết khách đến nói vài câu đã lui binh!","Đúng thế! Lúc trước nói hay lắm, nay triều đình phái thuyết khách đến nói vài câu đã lui binh!",},
        [514] = {1304,"Tinh Thái","Nhân vật","May mà nhận được Danh Tướng Lệnh! Chúa công, giờ chúng ta đi đâu?","May mà nhận được Danh Tướng Lệnh! Chúa công, giờ chúng ta đi đâu?",},
        [515] = {1304,"Nhân vật","Tinh Thái","Giặc Hoàng Cân đang làm loạn Thanh Châu, Tào Tháo bị bao vây, chúng ta đi cứu giúp thôi!","Giặc Hoàng Cân đang làm loạn Thanh Châu, Tào Tháo bị bao vây, chúng ta đi cứu giúp thôi!",},
        [516] = {1401,"Tào Tháo","Nhân vật","May mà có anh hùng giúp đỡ ta mới đoạt được Thanh Châu!","May mà có anh hùng giúp đỡ ta mới đoạt được Thanh Châu!",},
        [517] = {1401,"Nhân vật","Tào Tháo","Mạnh Đức huynh, Hoàng Cân giải tán sẽ còn gây họa tại Thanh Châu, huynh thấy đúng chứ!","Mạnh Đức huynh, Hoàng Cân giải tán sẽ còn gây họa tại Thanh Châu, huynh thấy đúng chứ!",},
        [518] = {1401,"Nhân vật","Tào Tháo","Chi bằng Mạnh Đức huynh hãy thu nạp bọn họ, tạo phúc cho bá tánh, triều đình an tâm sẽ có ban thưởng!","Chi bằng Mạnh Đức huynh hãy thu nạp bọn họ, tạo phúc cho bá tánh, triều đình an tâm sẽ có ban thưởng!",},
        [519] = {1401,"Tào Tháo","Nhân vật","Hay! Kế của anh hùng quá hay!","Hay! Kế của anh hùng quá hay!",},
        [520] = {1401,"Nhân vật","Tào Tháo","Tuy ta không ham vinh hoa phú quý nhưng không có nghĩa là từ chối","Tuy ta không ham vinh hoa phú quý nhưng không có nghĩa là từ chối",},
        [521] = {1401,"Tinh Thái","Nhân vật","Chúa công, Tinh Thái xin bái phục!","Chúa công, Tinh Thái xin bái phục!",},
        [522] = {1401,"Tào Tháo","Nhân vật","Ta sẽ thu nạp Thanh Châu Hoàng Cân, sau này gọi là quân Thanh Châu!","Ta sẽ thu nạp Thanh Châu Hoàng Cân, sau này gọi là quân Thanh Châu!",},
        [523] = {1401,"Tinh Thái","Nhân vật","Chúa công, tin ngài ở Duyện Châu đã truyền ra ngoài, nhiều danh sĩ đến xin gặp!","Chúa công, tin ngài ở Duyện Châu đã truyền ra ngoài, nhiều danh sĩ đến xin gặp!",},
        [524] = {1401,"Nhân vật","Tinh Thái","Gì chứ, Mạnh Đức huynh cùng ta ra xem sao!","Gì chứ, Mạnh Đức huynh cùng ta ra xem sao!",},
        [525] = {1402,"Điển Vi","Ngân Bình","Nghe nói ngươi giỏi võ, Điển Vi ta đến thỉnh giáo!","Nghe nói ngươi giỏi võ, Điển Vi ta đến thỉnh giáo!",},
        [526] = {1402,"Ngân Bình","Điển Vi","Gà nhép mà gáy to quá!","Gà nhép mà gáy to quá!",},
        [527] = {1402,"Điển Vi","Ngân Bình","Tiểu cô nương đừng nói bừa, gọi chúa công ngươi ra đây!","Tiểu cô nương đừng nói bừa, gọi chúa công ngươi ra đây!",},
        [528] = {1402,"Nhân vật","Điển Vi","Tráng sĩ đừng dọa người của ta, ta và ngươi đánh cược vậy!","Tráng sĩ đừng dọa người của ta, ta và ngươi đánh cược vậy!",},
        [529] = {1402,"Nhân vật","Điển Vi","Nếu ngươi thắng thì mạng ta do ngươi quyết định, nếu thua thì phải làm một việc cho ta!","Nếu ngươi thắng thì mạng ta do ngươi quyết định, nếu thua thì phải làm một việc cho ta!",},
        [530] = {1402,"Điển Vi","Nhân vật","Có gì không dám!","Có gì không dám!",},
        [531] = {1402,"Nhân vật","Điển Vi","Được, chiến một trận nào!","Được, chiến một trận nào!",},
        [532] = {1403,"Điển Vi","Nhân vật","Anh hùng rất giỏi! Điển Vi bái phục!","Anh hùng rất giỏi! Điển Vi bái phục!",},
        [533] = {1403,"Điển Vi","Nhân vật","Ngươi muốn Điển Vi ta làm gì!","Ngươi muốn Điển Vi ta làm gì!",},
        [534] = {1403,"Nhân vật","Điển Vi","Tào Tháo là một người bậc kiêu hùng, ta muốn ngươi đi theo, ngươi có đồng ý không!","Tào Tháo là một người bậc kiêu hùng, ta muốn ngươi đi theo, ngươi có đồng ý không!",},
        [535] = {1403,"Điển Vi","Nhân vật","Được, sau này ta sẽ đi theo Tào Tháo, ý ngươi thế nào!","Được, sau này ta sẽ đi theo Tào Tháo, ý ngươi thế nào!",},
        [536] = {1403,"Tào Tháo","Điển Vi","Được!","Được!",},
        [537] = {1404,"Tuân Úc","Nhân vật","Tướng quân có tài dùng binh, Tuân Úc xin đi theo ngài, đây là Danh Tướng Lệnh, xin dâng cho tướng quân.","Tướng quân có tài dùng binh, Tuân Úc xin đi theo ngài, đây là Danh Tướng Lệnh, xin dâng cho tướng quân.",},
        [538] = {1404,"Nhân vật","Tuân Úc","Danh Tướng Lệnh ta sẽ thu, nhưng chí hướng của ta là trở thành danh tướng đệ nhất tam quốc!","Danh Tướng Lệnh ta sẽ thu, nhưng chí hướng của ta là trở thành danh tướng đệ nhất tam quốc!",},
        [539] = {1404,"Nhân vật","Tuân Úc","Ta đề cử chúa công cho ngươi, hắn còn giỏi hơn ta nhiều!","Ta đề cử chúa công cho ngươi, hắn còn giỏi hơn ta nhiều!",},
        [540] = {1404,"Tuân Úc","Nhân vật","Không biết người tướng quân nói là ai!","Không biết người tướng quân nói là ai!",},
        [541] = {1404,"Nhân vật","Tuân Úc","Đó là Tào Tháo, Tào Mạnh Đức!","Đó là Tào Tháo, Tào Mạnh Đức!",},
        [542] = {1404,"Tào Tháo","Tuân Úc","Thì ra là danh sĩ Dĩnh Xuyên Tuân Úc! Ngươi đến giúp ta thật sao!","Thì ra là danh sĩ Dĩnh Xuyên Tuân Úc! Ngươi đến giúp ta thật sao!",},
        [543] = {1404,"Tào Tháo","Tuân Úc","Đa tạ anh hùng đã giới thiệu quân sư cho ta!","Đa tạ anh hùng đã giới thiệu quân sư cho ta!",},
        [544] = {1404,"Nhân vật","Tào Tháo","Không có gì! Ta chuẩn bị rời khỏi Duyện Châu đi du ngoạn, cáo từ!","Không có gì! Ta chuẩn bị rời khỏi Duyện Châu đi du ngoạn, cáo từ!",},
        [545] = {1404,"Tinh Thái","Nhân vật","Từ đó Tào Tháo có đầy đủ văn thần võ tướng, chiếm Duyện Châu, trở thành thế lực lớn sánh ngang Viên Thiệu Viên Thuật!","Từ đó Tào Tháo có đầy đủ văn thần võ tướng, chiếm Duyện Châu, trở thành thế lực lớn sánh ngang Viên Thiệu Viên Thuật!",},
        [546] = {1404,"Ngân Bình","Nhân vật","Chúa công, Tào Tháo thế lực lớn, sau này phản lại chúng ta thì sao?","Chúa công, Tào Tháo thế lực lớn, sau này phản lại chúng ta thì sao?",},
        [547] = {1404,"Nhân vật","Ngân Bình","Đã nghi ngờ thì còn kết bạn làm gì, đã kết bạn thì nên tin tưởng lẫn nhau!","Đã nghi ngờ thì còn kết bạn làm gì, đã kết bạn thì nên tin tưởng lẫn nhau!",},
        [548] = {1404,"Ngân Bình","Nhân vật","Chúa công nói đúng, Ngân Bình đã hiểu!","Chúa công nói đúng, Ngân Bình đã hiểu!",},
        [549] = {1404,"Tinh Thái","Nhân vật","Chúa công, Từ Châu có manh mối của Danh Tướng Lệnh, chúng ta đến đó xem thử!","Chúa công, Từ Châu có manh mối của Danh Tướng Lệnh, chúng ta đến đó xem thử!",},
        [550] = {1501,"Ngân Bình","Tào Tung","Đến Từ Châu rồi! Phía trước là ai, sao mang nhiều châu báu thế!","Đến Từ Châu rồi! Phía trước là ai, sao mang nhiều châu báu thế!",},
        [551] = {1501,"Tinh Thái","Tào Tung","Từ nhỏ cha đã dạy ta rằng tiền của để lộ ra ngoài là đại kỵ. Ông ta làm vậy nhất định sẽ chuốc họa vào thân.","Từ nhỏ cha đã dạy ta rằng tiền của để lộ ra ngoài là đại kỵ. Ông ta làm vậy nhất định sẽ chuốc họa vào thân.",},
        [552] = {1501,"Tào Tung","Ngân Bình","Các ngươi đang nói gì thế? Ai dám đụng đến Tào Tung ta chứ? Cho các ngươi biết, con trai ta là Tào Tháo đấy!","Các ngươi đang nói gì thế? Ai dám đụng đến Tào Tung ta chứ? Cho các ngươi biết, con trai ta là Tào Tháo đấy!",},
        [553] = {1501,"Ngân Bình","Tào Tung","Nhìn bộ dạng kiêu căng của ông kìa! Nếu như không nhờ chúa công ta thì Tào Tháo nhà ông đã bỏ mạng ở Huỳnh Dương rồi!","Nhìn bộ dạng kiêu căng của ông kìa! Nếu như không nhờ chúa công ta thì Tào Tháo nhà ông đã bỏ mạng ở Huỳnh Dương rồi!",},
        [554] = {1501,"Tào Tung","Ngân Bình","Nói bừa! Tiện dân nơi nào mà dám làm càn ở đây!","Nói bừa! Tiện dân nơi nào mà dám làm càn ở đây!",},
        [555] = {1501,"Ngân Bình","Tào Tung","Dám nói chúa công của bọn ta là tiện dân sao, xem đây!","Dám nói chúa công của bọn ta là tiện dân sao, xem đây!",},
        [556] = {1502,"Ngân Bình","Đào Khiêm","Đào Khiêm, ngươi là Từ Châu Thái Thú, có Danh Tướng Lệnh không?","Đào Khiêm, ngươi là Từ Châu Thái Thú, có Danh Tướng Lệnh không?",},
        [557] = {1502,"Đào Khiêm","Ngân Bình","Cô nương minh giám, lão không biết Từ Châu này ở đâu có Danh Tướng Lệnh cả.","Cô nương minh giám, lão không biết Từ Châu này ở đâu có Danh Tướng Lệnh cả.",},
        [558] = {1502,"Ngân Bình","Đào Khiêm","Không có sao?","Không có sao?",},
        [559] = {1502,"Đào Khiêm","Ngân Bình","Lão từ nhỏ học đạo lý Khổng, Mạnh nên không dám nói dối đâu.","Lão từ nhỏ học đạo lý Khổng, Mạnh nên không dám nói dối đâu.",},
        [560] = {1502,"Nhân vật","Đào Khiêm","Đào Khiêm này xem ra là người tốt. Nếu không có thì chúng ta đến nơi khác xem sao.","Đào Khiêm này xem ra là người tốt. Nếu không có thì chúng ta đến nơi khác xem sao.",},
        [561] = {1503,"Tinh Thái","Nhân vật","Chúa công, nguy rồi!","Chúa công, nguy rồi!",},
        [562] = {1503,"Nhân vật","Tinh Thái","Tinh Thái, có chuyện gì từ từ nói nào!","Tinh Thái, có chuyện gì từ từ nói nào!",},
        [563] = {1503,"Tinh Thái","Nhân vật","Tào Tháo phái người đến Trần Lưu đón Tào Tung. Khi qua Từ Châu, Đào Khiêm đã tiếp đãi họ nồng hậu, còn phái người hộ tống nữa...","Tào Tháo phái người đến Trần Lưu đón Tào Tung. Khi qua Từ Châu, Đào Khiêm đã tiếp đãi họ nồng hậu, còn phái người hộ tống nữa...",},
        [564] = {1503,"Ngân Bình","Tinh Thái","Hừ, Đào Khiêm kia chắc muốn lấy lòng Tào Tháo đây mà. Tiếp đãi Tào Tung cũng chẳng phải là chuyện lớn lao gì.","Hừ, Đào Khiêm kia chắc muốn lấy lòng Tào Tháo đây mà. Tiếp đãi Tào Tung cũng chẳng phải là chuyện lớn lao gì.",},
        [565] = {1503,"Tinh Thái","Ngân Bình","Nghe ta nói hết đã! Trương Khải, người hộ tống Tào Tung là Giặc Hoàng Cân. Hắn thấy Tào Tung mang theo nhiều của cải nên đã diệt hết cả nhà Tào Tung để cướp tài sản.","Nghe ta nói hết đã! Trương Khải, người hộ tống Tào Tung là Giặc Hoàng Cân. Hắn thấy Tào Tung mang theo nhiều của cải nên đã diệt hết cả nhà Tào Tung để cướp tài sản.",},
        [566] = {1503,"Nhân vật","Tinh Thái","Đào Khiêm kia có ý tốt nhưng không ngờ lại ra nông nỗi. Lần này Tào Tháo nhất định sẽ trả thù...","Đào Khiêm kia có ý tốt nhưng không ngờ lại ra nông nỗi. Lần này Tào Tháo nhất định sẽ trả thù...",},
        [567] = {1503,"Ngân Bình","Tinh Thái","Tào Tung quả nhiên mất mạng vì tiền của. Tinh Thái, dự đoán của ngươi đều rất chính xác. Hãy cho ta biết, ngươi có bí quyết gì?","Tào Tung quả nhiên mất mạng vì tiền của. Tinh Thái, dự đoán của ngươi đều rất chính xác. Hãy cho ta biết, ngươi có bí quyết gì?",},
        [568] = {1503,"Tinh Thái","Ngân Bình","Quan Ngân Bình, đừng ngắt lời chứ!","Quan Ngân Bình, đừng ngắt lời chứ!",},
        [569] = {1503,"Tinh Thái","Nhân vật","Chúa công, tin tức truyền đến Duyện Châu, Tào Tháo phái mười mấy vạn binh mã công đánh Từ Châu, còn nói là phải diệt sạch người ở Từ Châu để báo thù nữa!","Chúa công, tin tức truyền đến Duyện Châu, Tào Tháo phái mười mấy vạn binh mã công đánh Từ Châu, còn nói là phải diệt sạch người ở Từ Châu để báo thù nữa!",},
        [570] = {1503,"Nhân vật","Tinh Thái","Bá tánh Từ Châu vô tội, chúng ta mau về Duyện Châu để ngăn Tào Tháo!","Bá tánh Từ Châu vô tội, chúng ta mau về Duyện Châu để ngăn Tào Tháo!",},
        [571] = {1504,"Trần Cung","Nhân vật","Tướng quân, đã lâu không gặp!","Tướng quân, đã lâu không gặp!",},
        [572] = {1504,"Nhân vật","Trần Cung","Trần Cung, ta đang muốn hỏi ngươi. Lúc còn ở Trung Mâu Huyện, ta thấy ngươi rất ngưỡng mộ Tào Tháo, tại sao sau này lại rời bỏ y?","Trần Cung, ta đang muốn hỏi ngươi. Lúc còn ở Trung Mâu Huyện, ta thấy ngươi rất ngưỡng mộ Tào Tháo, tại sao sau này lại rời bỏ y?",},
        [573] = {1504,"Trần Cung","Nhân vật","Là vì Lữ Bá Xa cả... Chuyện này không nhắc sẽ tốt hơn.","Là vì Lữ Bá Xa cả... Chuyện này không nhắc sẽ tốt hơn.",},
        [574] = {1504,"Trần Cung","Nhân vật","Lúc đó ta thấy Tào Tháo là người có chí lớn nên mới từ quan theo y nhưng không ngờ y là người đa nghi, lại không nhân từ nên ta mới bỏ đi, không theo y nữa.","Lúc đó ta thấy Tào Tháo là người có chí lớn nên mới từ quan theo y nhưng không ngờ y là người đa nghi, lại không nhân từ nên ta mới bỏ đi, không theo y nữa.",},
        [575] = {1504,"Trần Cung","Nhân vật","Chuyện Từ Châu lần này, ta vốn có giao tình với Đào Khiêm nên định đến Duyện Châu để khuyên ngăn Tào Tháo.","Chuyện Từ Châu lần này, ta vốn có giao tình với Đào Khiêm nên định đến Duyện Châu để khuyên ngăn Tào Tháo.",},
        [576] = {1504,"Trần Cung","Nhân vật","Chuyện nguy cấp, ta phải đi ngay đây, cáo từ.","Chuyện nguy cấp, ta phải đi ngay đây, cáo từ.",},
        [577] = {1504,"Tinh Thái","Nhân vật","Trần Cung chạy nhanh thật. Chúa công, người thấy y có thể khuyên ngăn Tào Tháo không?","Trần Cung chạy nhanh thật. Chúa công, người thấy y có thể khuyên ngăn Tào Tháo không?",},
        [578] = {1504,"Nhân vật","Tinh Thái","Khó nói lắm, e là ta phải ra mặt mới có thể giải quyết được.","Khó nói lắm, e là ta phải ra mặt mới có thể giải quyết được.",},
        [579] = {1505,"Tào Tháo","Trần Cung","Trần Cung, năm xưa ngươi rời bỏ ta, giờ còn mặt mũi đến gặp ta sao!","Trần Cung, năm xưa ngươi rời bỏ ta, giờ còn mặt mũi đến gặp ta sao!",},
        [580] = {1505,"Tào Tháo","Trần Cung","Đào Khiêm sát hại cả nhà ta, ta phải diệt y để báo thù! Ngươi nói gì cũng vô dụng thôi!","Đào Khiêm sát hại cả nhà ta, ta phải diệt y để báo thù! Ngươi nói gì cũng vô dụng thôi!",},
        [581] = {1505,"Trần Cung","Tào Tháo","Ta còn mặt mũi nào để gặp Đào Khiêm nữa chứ! (Trần Cung buồn bã rời đi)","Ta còn mặt mũi nào để gặp Đào Khiêm nữa chứ! (Trần Cung buồn bã rời đi)",},
        [582] = {1505,"Tào Tháo","Trần Cung","Thù hại cha quyết phải báo. Nếu không, Tào Tháo ta còn mặt mũi nào nữa chứ!","Thù hại cha quyết phải báo. Nếu không, Tào Tháo ta còn mặt mũi nào nữa chứ!",},
        [583] = {1505,"Tào Tháo","Trần Cung","Tào Nhân, ta phái ngươi thống lĩnh đại quân diệt sạch Từ Châu! Ta phải bắt cả Từ Châu kia an táng theo phụ thân ta!","Tào Nhân, ta phái ngươi thống lĩnh đại quân diệt sạch Từ Châu! Ta phải bắt cả Từ Châu kia an táng theo phụ thân ta!",},
        [584] = {1505,"Tào Nhân","Tào Tháo","Tào Nhân nhận lệnh!","Tào Nhân nhận lệnh!",},
        [585] = {1506,"Tào Nhân","Nhân vật","Tướng quân quả nhiên anh hùng xuất thiếu niên. Tào Nhân xin nhận thua! Hãy nhận lấy Danh Tướng Lệnh này!","Tướng quân quả nhiên anh hùng xuất thiếu niên. Tào Nhân xin nhận thua! Hãy nhận lấy Danh Tướng Lệnh này!",},
        [586] = {1506,"Nhân vật","Tào Nhân","Tào Tháo đâu? Tại sao lại tránh mặt ta?","Tào Tháo đâu? Tại sao lại tránh mặt ta?",},
        [587] = {1506,"Tào Nhân","Nhân vật","Chúa công biết người đến giảng hòa nên mới tránh mặt người và chỉ phái ta dẫn binh đi đánh thôi! (Tào Nhân lui xuống)","Chúa công biết người đến giảng hòa nên mới tránh mặt người và chỉ phái ta dẫn binh đi đánh thôi! (Tào Nhân lui xuống)",},
        [588] = {1506,"Ngân Bình","Nhân vật","Hừ, Tào Tháo thật xảo quyệt, dám tránh mặt ta!","Hừ, Tào Tháo thật xảo quyệt, dám tránh mặt ta!",},
        [589] = {1506,"Tinh Thái","Ngân Bình","Chúa công, theo lời Trần Cung nói thì Tào Tháo là người đa nghi và bất nhân thật sao?","Chúa công, theo lời Trần Cung nói thì Tào Tháo là người đa nghi và bất nhân thật sao?",},
        [590] = {1506,"Ngân Bình","Tinh Thái","Báo thù cho cha chỉ là cái cớ thôi! Ta thấy Tào Tháo muốn đoạt Từ Châu của Đào Khiêm và lấy Danh Tướng Lệnh thôi!","Báo thù cho cha chỉ là cái cớ thôi! Ta thấy Tào Tháo muốn đoạt Từ Châu của Đào Khiêm và lấy Danh Tướng Lệnh thôi!",},
        [591] = {1506,"Nhân vật","Ngân Bình","Nếu Tào Tháo không muốn gặp ta vậy thì chúng ta đến giúp Từ Châu. Dù sao thì bá tánh đều là người vô tội cả!","Nếu Tào Tháo không muốn gặp ta vậy thì chúng ta đến giúp Từ Châu. Dù sao thì bá tánh đều là người vô tội cả!",},
        [592] = {1601,"Đào Khiêm","Nhân vật","(Khóc to) Ta vốn muốn nhân dịp này có thể kết giao với Tào Tháo, không ngờ lại rước họa cho Từ Châu! Ta là tội nhân của Từ Châu!","(Khóc to) Ta vốn muốn nhân dịp này có thể kết giao với Tào Tháo, không ngờ lại rước họa cho Từ Châu! Ta là tội nhân của Từ Châu!",},
        [593] = {1601,"Nhân vật","Đào Khiêm","Đào Công đừng sợ! Ta nguyện giúp người đẩy lui quân Tào!","Đào Công đừng sợ! Ta nguyện giúp người đẩy lui quân Tào!",},
        [594] = {1601,"Đào Khiêm","Nhân vật","(Lại khóc to hơn) Anh hùng quả thật nhân nghĩa! Không ngờ thời khắc nguy khốn thế này vẫn có người chịu giúp Từ Châu!","(Lại khóc to hơn) Anh hùng quả thật nhân nghĩa! Không ngờ thời khắc nguy khốn thế này vẫn có người chịu giúp Từ Châu!",},
        [595] = {1601,"Tinh Thái","Nhân vật","Chúa công ta là một anh hùng phóng khoáng, hiệp nghĩa, là thiên hạ đệ nhất anh hùng đấy!","Chúa công ta là một anh hùng phóng khoáng, hiệp nghĩa, là thiên hạ đệ nhất anh hùng đấy!",},
        [596] = {1601,"Nhân vật","Tinh Thái","Hi hi, Tinh Thái thật biết ăn nói!","Hi hi, Tinh Thái thật biết ăn nói!",},
        [597] = {1602,"My Trúc","Đào Khiêm","Đào Công, ta có kế này!","Đào Công, ta có kế này!",},
        [598] = {1602,"Đào Khiêm","My Trúc","Mau nói xem nào!","Mau nói xem nào!",},
        [599] = {1602,"My Trúc","Đào Khiêm","Chi bằng phái người đến Bì Hải cầu viện! Thái Thú Bì Hải, Khổng Dung là con cháu đời thứ 20 của Khổng Tử, ông ấy nhất định giúp ông.","Chi bằng phái người đến Bì Hải cầu viện! Thái Thú Bì Hải, Khổng Dung là con cháu đời thứ 20 của Khổng Tử, ông ấy nhất định giúp ông.",},
        [600] = {1602,"Nhân vật","My Trúc","Khổng Dung là Thái Thú Bì Hải sao! Ta sẽ đích thân đến đó!","Khổng Dung là Thái Thú Bì Hải sao! Ta sẽ đích thân đến đó!",},
        [601] = {1602,"Nhân vật","My Trúc","Ha ha, đúng lúc có thể đến đó xem con cháu đời thứ 20 của Khổng Tử ra sao luôn!","Ha ha, đúng lúc có thể đến đó xem con cháu đời thứ 20 của Khổng Tử ra sao luôn!",},
        [602] = {1603,"Khổng Dung","Tinh Thái","Các ngươi là ai? Đến Bì Hải có việc chi?","Các ngươi là ai? Đến Bì Hải có việc chi?",},
        [603] = {1603,"Tinh Thái","Khổng Dung","Này, Khổng Dung kia, gặp chúa công ta phải khách sáo tí chứ! Chúa công nhà ta là anh hùng hào kiệt, tương lai là tướng mạnh nhất trong Tam Quốc đấy!","Này, Khổng Dung kia, gặp chúa công ta phải khách sáo tí chứ! Chúa công nhà ta là anh hùng hào kiệt, tương lai là tướng mạnh nhất trong Tam Quốc đấy!",},
        [604] = {1603,"Khổng Dung","Tinh Thái","Ta không tin! Từ xưa anh hùng xuất thiếu niên! Đừng thấy ta còn nhỏ mà khinh thường nhé!","Ta không tin! Từ xưa anh hùng xuất thiếu niên! Đừng thấy ta còn nhỏ mà khinh thường nhé!",},
        [605] = {1603,"Ngân Bình","Khổng Dung","Tiểu đệ đệ, bản lĩnh ngươi thế nào đánh với ta rồi nói nhé!","Tiểu đệ đệ, bản lĩnh ngươi thế nào đánh với ta rồi nói nhé!",},
        [606] = {1604,"Khổng Dung","Nhân vật","A, là người, vị anh hùng diệt Giặc Hoàng Cân, dương danh Hổ Lao Quan, diệt Đổng Trác đây mà! Hay quá, ta ngưỡng mộ người từ rất lâu rồi!","A, là người, vị anh hùng diệt Giặc Hoàng Cân, dương danh Hổ Lao Quan, diệt Đổng Trác đây mà! Hay quá, ta ngưỡng mộ người từ rất lâu rồi!",},
        [607] = {1604,"Nhân vật","Khổng Dung","Không dám, không dám! Tào Tháo hiện đang muốn công đánh Từ Châu. Bì Hải và Từ Châu rất gần nhau, xin hãy xuất binh cứu viện Từ Châu!","Không dám, không dám! Tào Tháo hiện đang muốn công đánh Từ Châu. Bì Hải và Từ Châu rất gần nhau, xin hãy xuất binh cứu viện Từ Châu!",},
        [608] = {1604,"Khổng Dung","Nhân vật","Anh hùng à, ta lực bất tòng tâm! Bì Hải vốn đang bị Giặc Hoàng Cân tấn công, may là có vị nghĩa sĩ võ nghệ cao cường nọ đến tương trợ nên mới tạm ngăn được bọn chúng!","Anh hùng à, ta lực bất tòng tâm! Bì Hải vốn đang bị Giặc Hoàng Cân tấn công, may là có vị nghĩa sĩ võ nghệ cao cường nọ đến tương trợ nên mới tạm ngăn được bọn chúng!",},
        [609] = {1604,"Nhân vật","Khổng Dung","Nghĩa sĩ võ nghệ cao cường sao! Là ai nhỉ, ta đang rất muốn tỉ thí với cao thủ đây!","Nghĩa sĩ võ nghệ cao cường sao! Là ai nhỉ, ta đang rất muốn tỉ thí với cao thủ đây!",},
        [610] = {1605,"Thái Sử Từ","Nhân vật","Anh hùng quả nhiên lợi hại, Thái Sử Từ xin nhận thua! Đây là Danh Tướng Lệnh, mong người nhận cho!","Anh hùng quả nhiên lợi hại, Thái Sử Từ xin nhận thua! Đây là Danh Tướng Lệnh, mong người nhận cho!",},
        [611] = {1605,"Nhân vật","Thái Sử Từ","Đa tạ! Hi hi, ta còn tưởng lần này đi sẽ không có thu hoạch gì nữa chứ.","Đa tạ! Hi hi, ta còn tưởng lần này đi sẽ không có thu hoạch gì nữa chứ.",},
        [612] = {1605,"Khổng Dung","Nhân vật","Hai vị tuy anh dũng nhưng cũng khó chống lại đại quân hùng mạnh! Nghe nói Lưu Bị là đại anh hùng, hay là mời Lưu Huyền Đức cứu viện Từ Châu! Chỉ là không biết ông ấy có đồng ý giúp không.","Hai vị tuy anh dũng nhưng cũng khó chống lại đại quân hùng mạnh! Nghe nói Lưu Bị là đại anh hùng, hay là mời Lưu Huyền Đức cứu viện Từ Châu! Chỉ là không biết ông ấy có đồng ý giúp không.",},
        [613] = {1605,"Nhân vật","Khổng Dung","Lưu Huyền Đức là đại ca kết nghĩa của ta! Đại ca tính tình nhân hậu, nhất định sẽ giúp! Để ta đi mời!","Lưu Huyền Đức là đại ca kết nghĩa của ta! Đại ca tính tình nhân hậu, nhất định sẽ giúp! Để ta đi mời!",},
        [614] = {1605,"Nhân vật","Khổng Dung","Thái Sử Từ huynh có muốn đi cùng ta không?","Thái Sử Từ huynh có muốn đi cùng ta không?",},
        [615] = {1605,"Thái Sử Từ","Nhân vật","Xin thứ lỗi, hiện còn mẹ già đang cần chăm sóc nên Thái Sử Từ không thể đi được. (Thái Sử Từ từ biệt rời khỏi)","Xin thứ lỗi, hiện còn mẹ già đang cần chăm sóc nên Thái Sử Từ không thể đi được. (Thái Sử Từ từ biệt rời khỏi)",},
        [616] = {1605,"Nhân vật","Thái Sử Từ","Vậy ta đi mời đại ca vậy. Sự việc cấp bách, chúng ta đi thôi!","Vậy ta đi mời đại ca vậy. Sự việc cấp bách, chúng ta đi thôi!",},
        [617] = {2018101,"Điêu Thuyền","","Hữu mỹ nhất nhân, thanh dương uyển hề... Là thơ trong Kinh Thi, là ai tặng vậy nhỉ, sao không để tên?","Hữu mỹ nhất nhân, thanh dương uyển hề... Là thơ trong Kinh Thi, là ai tặng vậy nhỉ, sao không để tên?",},
        [618] = {2018101,"Điêu Thuyền","","Vũ Nương nói ta xinh đẹp như tiên, lại múa hay, không biết sau này ai có phúc cưới được ta đây...","Vũ Nương nói ta xinh đẹp như tiên, lại múa hay, không biết sau này ai có phúc cưới được ta đây...",},
        [619] = {2018101,"Điêu Thuyền","","(Mong mỏi) Đương nhiên là ta muốn gả cho một anh hùng giỏi nhất thế gian rồi...","(Mong mỏi) Đương nhiên là ta muốn gả cho một anh hùng giỏi nhất thế gian rồi...",},
        [620] = {2018101,"Trương Lương","Điêu Thuyền","Ha ha ha, tiểu mỹ nhân, ta theo nàng lâu rồi đấy!","Ha ha ha, tiểu mỹ nhân, ta theo nàng lâu rồi đấy!",},
        [621] = {2018101,"Điêu Thuyền","","A? Ngươi là ai, sao lại theo ta?","A? Ngươi là ai, sao lại theo ta?",},
        [622] = {2018101,"Trương Lương","","Nàng nói xem sao ta lại theo nàng? Trời lạnh như vậy, nàng mặc mỏng manh thế, hay là để ta sưởi ấm cho nhé!","Nàng nói xem sao ta lại theo nàng? Trời lạnh như vậy, nàng mặc mỏng manh thế, hay là để ta sưởi ấm cho nhé!",},
        [623] = {2018101,"Điêu Thuyền","","A, ngươi là kẻ xấu! Ngươi đừng qua đây, ta sẽ la lên đấy!","A, ngươi là kẻ xấu! Ngươi đừng qua đây, ta sẽ la lên đấy!",},
        [624] = {2018101,"Trương Lương","","Ở đây không có ai hết, nàng có hét to thế nào cũng vô ích thôi, ha ha ha. (Trương Lương xông đến, Điêu Thuyền vô cùng kinh hãi)","Ở đây không có ai hết, nàng có hét to thế nào cũng vô ích thôi, ha ha ha. (Trương Lương xông đến, Điêu Thuyền vô cùng kinh hãi)",},
        [625] = {2018101,"Lữ Bố","","Dừng tay lại!","Dừng tay lại!",},
        [626] = {2018102,"Điêu Thuyền","Lữ Bố","Đa tạ anh hùng đã tương cứu. Tiểu nữ tên Điêu Thuyền, dám hỏi danh tính của anh hùng là gì ạ? (Điêu Thuyền hành lễ đa tạ Lữ Bố, khi cô ngẩng đầu lên, Lữ Bố nhìn cô ngơ ngẩn)","Đa tạ anh hùng đã tương cứu. Tiểu nữ tên Điêu Thuyền, dám hỏi danh tính của anh hùng là gì ạ? (Điêu Thuyền hành lễ đa tạ Lữ Bố, khi cô ngẩng đầu lên, Lữ Bố nhìn cô ngơ ngẩn)",},
        [627] = {2018102,"Lữ Bố","","Ta họ Lữ, tên Bố, tự là Phụng Tiên. Hiện Giặc Hoàng Cân đang hoành hành khắp nơi, sao cô nương lại chỉ có một mình?","Ta họ Lữ, tên Bố, tự là Phụng Tiên. Hiện Giặc Hoàng Cân đang hoành hành khắp nơi, sao cô nương lại chỉ có một mình?",},
        [628] = {2018102,"Điêu Thuyền","","Nghĩa phụ ta đang bị bệnh, nghe nói ở ngoài thành có danh y nổi tiếng tên Hoa Đà nên ta muốn mời ông ấy đến chữa bệnh cho nghĩa phụ.","Nghĩa phụ ta đang bị bệnh, nghe nói ở ngoài thành có danh y nổi tiếng tên Hoa Đà nên ta muốn mời ông ấy đến chữa bệnh cho nghĩa phụ.",},
        [629] = {2018102,"Lữ Bố","","(Trong lòng rung động) Thì ra là vậy, cô nương quả nhiên rất hiếu thuận. Dám hỏi, cha mẹ ruột của cô nương đâu, sao chỉ có nghĩa phụ thôi?","(Trong lòng rung động) Thì ra là vậy, cô nương quả nhiên rất hiếu thuận. Dám hỏi, cha mẹ ruột của cô nương đâu, sao chỉ có nghĩa phụ thôi?",},
        [630] = {2018102,"Điêu Thuyền","","Điêu Thuyền từ nhỏ đã mất đi song thân, nghĩa phụ một tay nuôi ta khôn lớn.","Điêu Thuyền từ nhỏ đã mất đi song thân, nghĩa phụ một tay nuôi ta khôn lớn.",},
        [631] = {2018102,"Lữ Bố","","Thật là có duyên. Lữ Bố ta từ nhỏ cũng mất cha mất mẹ, may là võ nghê cao cường nên đi theo phò nghĩa phụ.","Thật là có duyên. Lữ Bố ta từ nhỏ cũng mất cha mất mẹ, may là võ nghê cao cường nên đi theo phò nghĩa phụ.",},
        [632] = {2018102,"Điêu Thuyền","","A, không ngờ Lữ công tử cũng giống như Điêu Thuyền vậy...","A, không ngờ Lữ công tử cũng giống như Điêu Thuyền vậy...",},
        [633] = {2018102,"Lữ Bố","","Cô nương, đừng khách sáo, hãy gọi ta là Phụng Tiên. Thời buổi loạn lạc, nếu không chê, Phụng Tiên ta sẽ hộ tống cô nương đến mời danh y.","Cô nương, đừng khách sáo, hãy gọi ta là Phụng Tiên. Thời buổi loạn lạc, nếu không chê, Phụng Tiên ta sẽ hộ tống cô nương đến mời danh y.",},
        [634] = {2018102,"Điêu Thuyền","","Đa tạ Lữ công tử... Phụng Tiên!","Đa tạ Lữ công tử... Phụng Tiên!",},
        [635] = {2018103,"Điêu Thuyền","Lữ Bố","Phụng Tiên, chiếc khăn trên đầu chàng cũng đã cũ nên thiếp đã làm một chiếc khác, chàng xem có thích không?","Phụng Tiên, chiếc khăn trên đầu chàng cũng đã cũ nên thiếp đã làm một chiếc khác, chàng xem có thích không?",},
        [636] = {2018103,"Lữ Bố","","Rất đẹp, đa tạ Thuyền Nhi! Thuyền Nhi, tay nàng sao lại bị thương thế kia, có đau không?","Rất đẹp, đa tạ Thuyền Nhi! Thuyền Nhi, tay nàng sao lại bị thương thế kia, có đau không?",},
        [637] = {2018103,"Điêu Thuyền","","(E thẹn) Không sao đâu, vì không muốn bị phát hiện nên mới bị kim đâm trúng thôi. Phụng Tiên, khi nào chàng mới đến hỏi cưới thiếp?","(E thẹn) Không sao đâu, vì không muốn bị phát hiện nên mới bị kim đâm trúng thôi. Phụng Tiên, khi nào chàng mới đến hỏi cưới thiếp?",},
        [638] = {2018103,"Lữ Bố","","Thuyền Nhi, nghĩa phụ của nàng là Tư Đồ, ta làm sao có thể để nghĩa phụ nàng xem thường ta chứ! Nàng hãy chờ ta, sau khi ta thăng quan tiến tước làm đại tướng quân ta sẽ đến hỏi cưới nàng.","Thuyền Nhi, nghĩa phụ của nàng là Tư Đồ, ta làm sao có thể để nghĩa phụ nàng xem thường ta chứ! Nàng hãy chờ ta, sau khi ta thăng quan tiến tước làm đại tướng quân ta sẽ đến hỏi cưới nàng.",},
        [639] = {2018103,"Điêu Thuyền","","Phụng Tiên, nghĩa phụ rất thương thiếp, chỉ cần chàng đối xử tốt với thiếp thì nghĩa phụ cũng không chê chàng đâu.","Phụng Tiên, nghĩa phụ rất thương thiếp, chỉ cần chàng đối xử tốt với thiếp thì nghĩa phụ cũng không chê chàng đâu.",},
        [640] = {2018103,"Lữ Bố","","Nam tử hán đại trượng phu, ta làm sao có thể để người ta yêu thương chịu khổ được!","Nam tử hán đại trượng phu, ta làm sao có thể để người ta yêu thương chịu khổ được!",},
        [641] = {2018103,"Lữ Bố","","Thuyền Nhi, nàng hãy chờ ta. Chờ ta trở thành đại tướng quân rồi ta sẽ mang kiệu hoa đến rước nàng về!","Thuyền Nhi, nàng hãy chờ ta. Chờ ta trở thành đại tướng quân rồi ta sẽ mang kiệu hoa đến rước nàng về!",},
        [642] = {2018104,"Điêu Thuyền","Vương Doãn","Phụng Tiên nói rằng gần đây chàng được đề bạt làm Trung Lang Tướng, ta phải báo chuyện này cho nghĩa phụ biết mới được.","Phụng Tiên nói rằng gần đây chàng được đề bạt làm Trung Lang Tướng, ta phải báo chuyện này cho nghĩa phụ biết mới được.",},
        [643] = {2018104,"Vương Doãn","","Điêu Thuyền, con đến tìm ta có việc gì?","Điêu Thuyền, con đến tìm ta có việc gì?",},
        [644] = {2018104,"Điêu Thuyền","","Nghĩa phụ, con... (Vương Doãn đột nhiên quỵ xuống, Điêu Thuyền vội chạy đến đỡ ông ngồi xuống) Nghĩa phụ, người sao thế?","Nghĩa phụ, con... (Vương Doãn đột nhiên quỵ xuống, Điêu Thuyền vội chạy đến đỡ ông ngồi xuống) Nghĩa phụ, người sao thế?",},
        [645] = {2018104,"Vương Doãn","","(Mặt trắng bệch, khóc) Giang sơn Hán Thất đang lâm nguy! Tặc thần Đổng Trác muốn soán ngôi, triều đình không làm gì được, ta thật hổ thẹn mà...","(Mặt trắng bệch, khóc) Giang sơn Hán Thất đang lâm nguy! Tặc thần Đổng Trác muốn soán ngôi, triều đình không làm gì được, ta thật hổ thẹn mà...",},
        [646] = {2018104,"Điêu Thuyền","","Điêu Thuyền nguyện chia sẻ nỗi lòng của nghĩa phụ.","Điêu Thuyền nguyện chia sẻ nỗi lòng của nghĩa phụ.",},
        [647] = {2018104,"Vương Doãn","","(Đột nhiên quỳ trước Điêu Thuyền) Nào ngờ thiên hạ nhà Hán lại nằm trong tay con! Điêu Thuyền, ta có một mỹ nhân kế, chỉ có con mới diệt trừ được Đổng Trác.","(Đột nhiên quỳ trước Điêu Thuyền) Nào ngờ thiên hạ nhà Hán lại nằm trong tay con! Điêu Thuyền, ta có một mỹ nhân kế, chỉ có con mới diệt trừ được Đổng Trác.",},
        [648] = {2018104,"Điêu Thuyền","","Ơn dưỡng dục của nghĩa phụ, Điêu Thuyền dù hi sinh thân mình cũng khó báo đáp.","Ơn dưỡng dục của nghĩa phụ, Điêu Thuyền dù hi sinh thân mình cũng khó báo đáp.",},
        [649] = {2018105,"Vương Doãn","Điêu Thuyền","Đổng Trác có rất nhiều thủ hạ, người thường rất khó đến gần hắn, nhưng hắn lại là một kẻ háo sắc.","Đổng Trác có rất nhiều thủ hạ, người thường rất khó đến gần hắn, nhưng hắn lại là một kẻ háo sắc.",},
        [650] = {2018105,"Vương Doãn","","Hắn có một nghĩa tử tên Lữ Bố, rất dũng mãnh... Trước tiên ta hứa gả con cho Lữ Bố, lại hiến cho Đổng Trác, khiến hai phụ tử trở mặt, sau đó Lữ Bố sẽ diệt trừ Đổng Trác.","Hắn có một nghĩa tử tên Lữ Bố, rất dũng mãnh... Trước tiên ta hứa gả con cho Lữ Bố, lại hiến cho Đổng Trác, khiến hai phụ tử trở mặt, sau đó Lữ Bố sẽ diệt trừ Đổng Trác.",},
        [651] = {2018105,"Điêu Thuyền","","(Điêu Thuyền hoảng sợ kêu lên) A!","(Điêu Thuyền hoảng sợ kêu lên) A!",},
        [652] = {2018105,"Vương Doãn","","Điêu Thuyền, sắc mặt con trắng bệt, chẳng lẽ không muốn làm?","Điêu Thuyền, sắc mặt con trắng bệt, chẳng lẽ không muốn làm?",},
        [653] = {2018105,"Điêu Thuyền","","Không phải, nghĩa phụ đừng lo.","Không phải, nghĩa phụ đừng lo.",},
        [654] = {2018105,"Vương Doãn","","Con gái ngoan, ủy khuất cho con rồi. Nếu chuyện này bị lộ, cả nhà ta sẽ bị diệt đấy.","Con gái ngoan, ủy khuất cho con rồi. Nếu chuyện này bị lộ, cả nhà ta sẽ bị diệt đấy.",},
        [655] = {2018105,"Điêu Thuyền","","Nghĩa phụ yên tâm, Điêu Thuyền sẽ cố gắng hết sức để báo đáp công ơn nghĩa phụ!","Nghĩa phụ yên tâm, Điêu Thuyền sẽ cố gắng hết sức để báo đáp công ơn nghĩa phụ!",},
        [656] = {2018201,"Hà Tiến","Tả Từ","Đạo Sĩ, ta thấy ngươi đạo hạnh cao thâm, bói mệnh cho Hà Tiến ta xem!","Đạo Sĩ, ta thấy ngươi đạo hạnh cao thâm, bói mệnh cho Hà Tiến ta xem!",},
        [657] = {2018201,"Tả Từ","","Ta thấy ngươi giữa trán sáng sủa, mắt có thần, sau này chắc chắn làm Đại Tướng Quân!","Ta thấy ngươi giữa trán sáng sủa, mắt có thần, sau này chắc chắn làm Đại Tướng Quân!",},
        [658] = {2018201,"Hà Tiến","","Ha ha, ta là người mổ heo, còn có thể làm tướng quân?","Ha ha, ta là người mổ heo, còn có thể làm tướng quân?",},
        [659] = {2018201,"Tả Từ","","Nếu không tin sao còn đến đây xem bói. Ngươi lật lọng như vậy, sau này dù làm Đại Tướng Quân cũng không sống thọ được!","Nếu không tin sao còn đến đây xem bói. Ngươi lật lọng như vậy, sau này dù làm Đại Tướng Quân cũng không sống thọ được!",},
        [660] = {2018201,"Hà Tiến","","Đi đi đạo sĩ thúi, dám mắng ta không sống lâu được!","Đi đi đạo sĩ thúi, dám mắng ta không sống lâu được!",},
        [661] = {2018202,"Hà Thái Hậu","Hà Tiến","Từ khi cha mẹ mất đi, là ca ca đã cực khổ nuôi dưỡng cả nhà, giờ thời kỳ cực khổ đã qua, muội sắp vào cung rồi.","Từ khi cha mẹ mất đi, là ca ca đã cực khổ nuôi dưỡng cả nhà, giờ thời kỳ cực khổ đã qua, muội sắp vào cung rồi.",},
        [662] = {2018202,"Hà Tiến","","Muội vào cung rồi nhớ phải cẩn thận, nếu bị ức hiếp thì phải nói ta biết, ta sẽ làm chủ cho muội.","Muội vào cung rồi nhớ phải cẩn thận, nếu bị ức hiếp thì phải nói ta biết, ta sẽ làm chủ cho muội.",},
        [663] = {2018202,"Hà Thái Hậu","","Ca ca yên tâm, muội không dễ bị ức hiếp vậy đâu. Đợi sau này muội có làm được hoàng hậu, nhất định sẽ cho ca ca làm quan lớn, không phải chịu cực nữa.","Ca ca yên tâm, muội không dễ bị ức hiếp vậy đâu. Đợi sau này muội có làm được hoàng hậu, nhất định sẽ cho ca ca làm quan lớn, không phải chịu cực nữa.",},
        [664] = {2018203,"Hà Thái Hậu","","Ca ca, muội làm được hoàng hậu rồi! Đó là nhờ muội sinh cho hoàng đế một hoàng tử!","Ca ca, muội làm được hoàng hậu rồi! Đó là nhờ muội sinh cho hoàng đế một hoàng tử!",},
        [665] = {2018203,"Hà Tiến","Hà Thái Hậu","Tốt quá, Hà gia chúng ta cuối cùng đã đổi đời rồi!","Tốt quá, Hà gia chúng ta cuối cùng đã đổi đời rồi!",},
        [666] = {2018203,"Hà Thái Hậu","","Ca ca, nhờ có huynh lập công lớn, bắt được Mã Nguyên Nghĩa của giặc Hoàng Cân! Đại Tướng Quân thật lợi hại!","Ca ca, nhờ có huynh lập công lớn, bắt được Mã Nguyên Nghĩa của giặc Hoàng Cân! Đại Tướng Quân thật lợi hại!",},
        [667] = {2018203,"Hà Tiến","","Ha ha, ta làm Đại Tướng Quân mới có thể bảo vệ muội chứ!","Ha ha, ta làm Đại Tướng Quân mới có thể bảo vệ muội chứ!",},
        [668] = {2018203,"Hà Thái Hậu","","Ca ca, hoàng đế muốn lập con trai Lưu Hiệp của Vương Mỹ Nhân làm hoàng đế. Hu hu, huynh phải làm chủ cho muội đấy.","Ca ca, hoàng đế muốn lập con trai Lưu Hiệp của Vương Mỹ Nhân làm hoàng đế. Hu hu, huynh phải làm chủ cho muội đấy.",},
        [669] = {2018203,"Hà Tiến","","Muội muội yên tâm, ca ca nhất định sẽ không để chuyện này xảy ra!","Muội muội yên tâm, ca ca nhất định sẽ không để chuyện này xảy ra!",},
        [670] = {2018204,"Hà Tiến","Hà Thái Hậu","Muội muội, đám người Tào Tháo, Viên Thiệu đều khuyên ta tru sát Thập Thường Thị, ta phải nghĩ cách tiêu diệt Trương Nhượng!","Muội muội, đám người Tào Tháo, Viên Thiệu đều khuyên ta tru sát Thập Thường Thị, ta phải nghĩ cách tiêu diệt Trương Nhượng!",},
        [671] = {2018204,"Hà Thái Hậu","","Không được! Trương Nhượng từng cứu mạng muội, nếu không có hắn, hoàng đế sớm đã phế muội rồi!","Không được! Trương Nhượng từng cứu mạng muội, nếu không có hắn, hoàng đế sớm đã phế muội rồi!",},
        [672] = {2018204,"Hà Tiến","","Muội muội, Hà gia chúng ta muốn đứng vững thì phải diệt trừ Trương Nhượng, nếu không sẽ là mối họa sau này.","Muội muội, Hà gia chúng ta muốn đứng vững thì phải diệt trừ Trương Nhượng, nếu không sẽ là mối họa sau này.",},
        [673] = {2018204,"Hà Thái Hậu","","Ca ca, dù như thế nào huynh cũng không thể diệt trừ Trương Nhượng.","Ca ca, dù như thế nào huynh cũng không thể diệt trừ Trương Nhượng.",},
        [674] = {2018204,"Hà Tiến","","Được rồi, ta nghĩ cách khác xem!","Được rồi, ta nghĩ cách khác xem!",},
        [675] = {2018205,"Trương Nhượng","","Nếu không nhờ Tiểu Thái Giám khác báo tin, ta thật sự không biết Hà Tiến lại muốn tru sát ta.","Nếu không nhờ Tiểu Thái Giám khác báo tin, ta thật sự không biết Hà Tiến lại muốn tru sát ta.",},
        [676] = {2018205,"Trương Nhượng","","Nếu vậy đừng có trách ta. Người đâu, giả ý chỉ thái hậu lừa Hà Tiến vào cung rồi tru sát hắn!","Nếu vậy đừng có trách ta. Người đâu, giả ý chỉ thái hậu lừa Hà Tiến vào cung rồi tru sát hắn!",},
        [677] = {2018206,"Viên Thiệu","Hà Tiến","Chiếu chỉ này nhất định là mưu kế của Thập Thường Thị, đi chắc chắn gặp họa.","Chiếu chỉ này nhất định là mưu kế của Thập Thường Thị, đi chắc chắn gặp họa.",},
        [678] = {2018206,"Hà Tiến","","Thái hậu là muội muội ruột của ta, gặp họa gì chứ?","Thái hậu là muội muội ruột của ta, gặp họa gì chứ?",},
        [679] = {2018206,"Viên Thiệu","","Đại Tướng Quân nếu nhất quyết vào cung thì ta với Viên Thiệu sẽ dẫn người bảo vệ, đề phòng bất trắc.","Đại Tướng Quân nếu nhất quyết vào cung thì ta với Viên Thiệu sẽ dẫn người bảo vệ, đề phòng bất trắc.",},
        [680] = {2018206,"Hà Tiến","","Muội muội ta là thái hậu, ta là Đại Tướng Quân, ai dám hại ta! Nếu đây thật sự là âm mưu của Thập Thường Thị thì ta càng phải vào cung bảo vệ muội muội!","Muội muội ta là thái hậu, ta là Đại Tướng Quân, ai dám hại ta! Nếu đây thật sự là âm mưu của Thập Thường Thị thì ta càng phải vào cung bảo vệ muội muội!",},
        [681] = {2018301,"Hoa Hùng","Lý Thôi","Lý Thôi, tiểu tử thối, người đâu! Đi, đi uống rượu!","Lý Thôi, tiểu tử thối, người đâu! Đi, đi uống rượu!",},
        [682] = {2018301,"Lý Thôi","","Uống rượu thì dễ, nhưng tiền rượu...","Uống rượu thì dễ, nhưng tiền rượu...",},
        [683] = {2018301,"Hoa Hùng","","Nhắc tiền trước mặt ca ca là quá khách khí rồi.","Nhắc tiền trước mặt ca ca là quá khách khí rồi.",},
        [684] = {2018302,"Đổng Trác","Hoa Hùng","Chư Hầu đó đã đến bên ngoài Hổ Lao Quan, ai ra xuất chiến?","Chư Hầu đó đã đến bên ngoài Hổ Lao Quan, ai ra xuất chiến?",},
        [685] = {2018302,"Lữ Bố","","Phụ thân đừng lo, chư hầu đó tựa như cỏ ven đường, ta nguyện ra ngoài trảm thủ lĩnh của họ.","Phụ thân đừng lo, chư hầu đó tựa như cỏ ven đường, ta nguyện ra ngoài trảm thủ lĩnh của họ.",},
        [686] = {2018302,"Hoa Hùng","","Không cần Ôn Hầu đích thân ra tay. Ta lấy thủ cấp chư hầu như lấy đồ trong túi vậy!","Không cần Ôn Hầu đích thân ra tay. Ta lấy thủ cấp chư hầu như lấy đồ trong túi vậy!",},
        [687] = {2018303,"Đổng Trác","","Hoa Hùng, ngươi có công lớn, trảm Bào Trung, Tổ Mậu, đánh bại Tôn Kiên. Người đâu, phong Hoa Hùng làm đô đốc!","Hoa Hùng, ngươi có công lớn, trảm Bào Trung, Tổ Mậu, đánh bại Tôn Kiên. Người đâu, phong Hoa Hùng làm đô đốc!",},
        [688] = {2018303,"Hoa Hùng","","Đa tạ Thái Sư!","Đa tạ Thái Sư!",},
        [689] = {2018304,"Hoa Hùng","Viên Thiệu","(Dùng gậy khều vải đầu của Tôn Kiên) Một đám nhu nhược, ai dám đến chiến?","(Dùng gậy khều vải đầu của Tôn Kiên) Một đám nhu nhược, ai dám đến chiến?",},
        [690] = {2018304,"Viên Thiệu","","Tiếc là Nhan Lương, Văn Xú không ở đây, các vị, Hoa Hùng đó thật ngông cuồng, ai dám ra ứng chiến?","Tiếc là Nhan Lương, Văn Xú không ở đây, các vị, Hoa Hùng đó thật ngông cuồng, ai dám ra ứng chiến?",},
        [691] = {2018304,"Quan Vũ","","Ta nguyện ra trảm Hoa Hùng! Rượu làm ấm trước, ta đi xong về ngay.","Ta nguyện ra trảm Hoa Hùng! Rượu làm ấm trước, ta đi xong về ngay.",},
        [692] = {2018305,"Hoa Hùng","","Ta đã trảm Phan Phượng, ai dám tới chiến!","Ta đã trảm Phan Phượng, ai dám tới chiến!",},
        [693] = {2018305,"Quan Vũ","","Hoa Hùng, đừng ngông cuồng!","Hoa Hùng, đừng ngông cuồng!",},
        [694] = {2018305,"Hoa Hùng","","Sát khí nặng thật!","Sát khí nặng thật!",},
        [695] = {2018305,"Hoa Hùng","","Đại hán mặt đỏ này đến nhanh như vậy... Gặp ngươi cũng là vận mệnh... Da dày cũng không đỡ được đao...!","Đại hán mặt đỏ này đến nhanh như vậy... Gặp ngươi cũng là vận mệnh... Da dày cũng không đỡ được đao...!",},
        [696] = {2018401,"Tuân Du","Tuân Úc","Thúc phụ, không tốt rồi!","Thúc phụ, không tốt rồi!",},
        [697] = {2018401,"Tuân Úc","Tuân Du","Công Đạt, có chuyện gì vậy?","Công Đạt, có chuyện gì vậy?",},
        [698] = {2018401,"Tuân Du","Tuân Úc","Năm đó Trung Thường Tự Đường Hoành hứa gả dưỡng nữ cho thúc, giờ cô gái đó đã đến tuổi gả rồi.","Năm đó Trung Thường Tự Đường Hoành hứa gả dưỡng nữ cho thúc, giờ cô gái đó đã đến tuổi gả rồi.",},
        [699] = {2018401,"Tuân Du","Tuân Úc","Tuân thị chúng ta là thế gia đại tộc, nếu lấy con gái của hoạn quan chắc chắn sẽ bị chỉ trích? Thúc phụ, nay Đường Hoành đã mất, hay là hủy hôn?","Tuân thị chúng ta là thế gia đại tộc, nếu lấy con gái của hoạn quan chắc chắn sẽ bị chỉ trích? Thúc phụ, nay Đường Hoành đã mất, hay là hủy hôn?",},
        [700] = {2018401,"Tuân Úc","Tuân Du","Hoang đường! Hôn sự đã định thì sao hủy được, càng không thể thất tín!","Hoang đường! Hôn sự đã định thì sao hủy được, càng không thể thất tín!",},
        [701] = {2018401,"Tuân Úc","Tuân Du","Đường Hoành tuy là Trung Thường Thị, nhưng con gái của hắn là vô tội. Tuân Úc ta không thẹn với lòng, sao phải lo lắng người khác chỉ trích.","Đường Hoành tuy là Trung Thường Thị, nhưng con gái của hắn là vô tội. Tuân Úc ta không thẹn với lòng, sao phải lo lắng người khác chỉ trích.",},
        [702] = {2018401,"Tuân Du","Tuân Úc","Thúc phụ nói rất đúng.","Thúc phụ nói rất đúng.",},
        [703] = {2018402,"Tào Tháo","Tuân Úc","Nay xe ngựa thiên tử đã về Lạc Dương, các vị có ý kiến gì không?","Nay xe ngựa thiên tử đã về Lạc Dương, các vị có ý kiến gì không?",},
        [704] = {2018402,"Tuân Úc","Tào Tháo","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.",},
        [705] = {2018402,"Tuân Úc","Tào Tháo","Nay thiên tử gặp nạn, tướng quân nên nhân lúc này khởi xướng nghĩa binh để thuận theo lòng bá tánh. Nếu không thực hiện sớm, người khác sẽ nhanh chân hơn chúng ta.","Nay thiên tử gặp nạn, tướng quân nên nhân lúc này khởi xướng nghĩa binh để thuận theo lòng bá tánh. Nếu không thực hiện sớm, người khác sẽ nhanh chân hơn chúng ta.",},
        [706] = {2018402,"Tào Tháo","Tuân Úc","Ha ha, Tuân Lệnh Quân nói rất hợp ý ta. Giờ ta sẽ dẫn quân đi Lạc Dương, nghênh tiếp thiên tử dời đô đến Hứa Xương!","Ha ha, Tuân Lệnh Quân nói rất hợp ý ta. Giờ ta sẽ dẫn quân đi Lạc Dương, nghênh tiếp thiên tử dời đô đến Hứa Xương!",},
        [707] = {2018403,"Tào Tháo","Tuân Du","Hiện quân ta trấn giữ Quan Độ, quân lực yếu dần, lương thảo không đủ. Ta muốn từ bỏ Quan Độ lui về Hứa Xương, ý chư vị thế nào?","Hiện quân ta trấn giữ Quan Độ, quân lực yếu dần, lương thảo không đủ. Ta muốn từ bỏ Quan Độ lui về Hứa Xương, ý chư vị thế nào?",},
        [708] = {2018403,"Tuân Du","Tào Tháo","Sao tướng quân không viết thư hỏi thúc phụ ta?","Sao tướng quân không viết thư hỏi thúc phụ ta?",},
        [709] = {2018403,"Tào Tháo","Tuân Du","Người đâu, mau cho người mang bức thư này đến Hứa Xương.","Người đâu, mau cho người mang bức thư này đến Hứa Xương.",},
        [710] = {2018403,"Tuân Du","Tào Tháo","Tướng quân, đây là thư của thúc phụ, mời xem!","Tướng quân, đây là thư của thúc phụ, mời xem!",},
        [711] = {2018403,"Tào Tháo","Tuân Du","(Đọc thư của Tuân Úc) Minh công lấy yếu địch mạnh, nếu không thể chống đỡ sẽ tạo cơ hội cho kẻ địch, đây là cơ hội lớn quyết thắng thiên hạ.","(Đọc thư của Tuân Úc) Minh công lấy yếu địch mạnh, nếu không thể chống đỡ sẽ tạo cơ hội cho kẻ địch, đây là cơ hội lớn quyết thắng thiên hạ.",},
        [712] = {2018403,"Tào Tháo","Tuân Du","Quân Viên Thiệu tuy đông nhưng không biết dùng binh. Nay minh công vạch đất trấn thủ khiến hắn không thể tiến công, đợi khí thế của hắn mất hết ắt sẽ có biến, đó là lúc dùng kỳ binh để quyết thắng.","Quân Viên Thiệu tuy đông nhưng không biết dùng binh. Nay minh công vạch đất trấn thủ khiến hắn không thể tiến công, đợi khí thế của hắn mất hết ắt sẽ có biến, đó là lúc dùng kỳ binh để quyết thắng.",},
        [713] = {2018403,"Tào Tháo","Tuân Du","Ha ha, Tuân Lệnh Quân quả thật có tài. Chư vị nghe lệnh, trấn giữ Quan Độ bằng mọi giá, chờ đợi thời cơ!","Ha ha, Tuân Lệnh Quân quả thật có tài. Chư vị nghe lệnh, trấn giữ Quan Độ bằng mọi giá, chờ đợi thời cơ!",},
        [714] = {2018404,"Đổng Chiêu","Tuân Úc","Trước giờ chưa ai có công lớn như thừa tướng. Bôn ba hơn ba mươi năm, giúp bá tánh trừ hại, khôi phục nhà Hán. Người nên nhận vị trí Ngụy công, được ban Cửu Tích.","Trước giờ chưa ai có công lớn như thừa tướng. Bôn ba hơn ba mươi năm, giúp bá tánh trừ hại, khôi phục nhà Hán. Người nên nhận vị trí Ngụy công, được ban Cửu Tích.",},
        [715] = {2018404,"Tuân Úc","Đổng Chiêu","Không thể, thừa tướng vốn là vì việc giúp triều yên nước mà chiêu tập nghĩa binh, từ trước đến nay giữ lòng trung trinh, giữ bề khiêm tốn. Người quân tử phải lấy đức yêu người, không nên như vậy.","Không thể, thừa tướng vốn là vì việc giúp triều yên nước mà chiêu tập nghĩa binh, từ trước đến nay giữ lòng trung trinh, giữ bề khiêm tốn. Người quân tử phải lấy đức yêu người, không nên như vậy.",},
        [716] = {2018404,"Đổng Chiêu","Tuân Úc","Làm sao để một người cản trở ý muốn của nhiều người?","Làm sao để một người cản trở ý muốn của nhiều người?",},
        [717] = {2018404,"Tuân Úc","Đổng Chiêu","Hôm nay ta không muốn thấy chuyện này!","Hôm nay ta không muốn thấy chuyện này!",},
        [718] = {2018404,"Tào Tháo","Tuân Úc","...(Tháo tím mặt, trong lòng hận Tuân Úc)","...(Tháo tím mặt, trong lòng hận Tuân Úc)",},
        [719] = {2018405,"Tào Tháo","","Tuân Lệnh Quân mượn cớ bệnh ở Thọ Xuyến. Người đâu, mau mang hộp thức ăn đến Thọ Xuyến!","Tuân Lệnh Quân mượn cớ bệnh ở Thọ Xuyến. Người đâu, mau mang hộp thức ăn đến Thọ Xuyến!",},
        [720] = {2018405,"Tuân Du","","Thúc phụ, Tào Tháo sai ngươi đến đưa hộp đồ ăn, trên hộp có niêm phong của ngài ấy.","Thúc phụ, Tào Tháo sai ngươi đến đưa hộp đồ ăn, trên hộp có niêm phong của ngài ấy.",},
        [721] = {2018405,"Tuân Úc","Tuân Du","(Mở hộp thức ăn, bên trong trống rỗng) Ta đi theo Tào Tháo nhiều năm, hai bên đã quá hiểu nhau rồi.","(Mở hộp thức ăn, bên trong trống rỗng) Ta đi theo Tào Tháo nhiều năm, hai bên đã quá hiểu nhau rồi.",},
        [722] = {2018405,"Tuân Du","Tuân Úc","Thúc phụ, đây là ý gì?","Thúc phụ, đây là ý gì?",},
        [723] = {2018405,"Tuân Úc","Tuân Du","Tuân Úc nay không có bổng lộc, Tuân Úc hiểu rõ, cần chi phải làm thêm việc như vậy. (Tuân Úc đuổi hết người đi, dùng thuộc độc tự sát, thọ 49 tuổi.)","Tuân Úc nay không có bổng lộc, Tuân Úc hiểu rõ, cần chi phải làm thêm việc như vậy. (Tuân Úc đuổi hết người đi, dùng thuộc độc tự sát, thọ 49 tuổi.)",},
        [724] = {2018501,"Sa Ma Kha","Tiểu Nương Tử","Khụ khụ, mấy hôm trước bị bệnh, xin tiểu nương tử bố thí cho ta vài đồng tiền để đi khám đại phu.","Khụ khụ, mấy hôm trước bị bệnh, xin tiểu nương tử bố thí cho ta vài đồng tiền để đi khám đại phu.",},
        [725] = {2018501,"Tiểu Nương Tử","Sa Ma Kha","Bệnh dịch lần này khá nghiêm trọng, nghe nói Nước Bùa của lương sư Trương Giác có thể trị bệnh, ngươi có thể đi tìm lương sư.","Bệnh dịch lần này khá nghiêm trọng, nghe nói Nước Bùa của lương sư Trương Giác có thể trị bệnh, ngươi có thể đi tìm lương sư.",},
        [726] = {2018501,"Sa Ma Kha","Tiểu Nương Tử","Đa tạ tiểu nương tử chỉ điểm. Đại hiền lương sư, mong người hãy cứu ta!","Đa tạ tiểu nương tử chỉ điểm. Đại hiền lương sư, mong người hãy cứu ta!",},
        [727] = {2018501,"Trương Giác","Sa Ma Kha","Trương Giác ta được Nam Hoa Lão Tiên truyền thụ Thái Bình Thuật, tự sáng lập Thái Bình Đạo, đã có 36 vạn đệ tử!","Trương Giác ta được Nam Hoa Lão Tiên truyền thụ Thái Bình Thuật, tự sáng lập Thái Bình Đạo, đã có 36 vạn đệ tử!",},
        [728] = {2018501,"Trương Giác","Sa Ma Kha","Nếu các ngươi gia nhập Thái Bình Đạo của ta thì nhất định sẽ khỏi bệnh, đảm bảo bình an!","Nếu các ngươi gia nhập Thái Bình Đạo của ta thì nhất định sẽ khỏi bệnh, đảm bảo bình an!",},
        [729] = {2018501,"Sa Ma Kha","Trương Giác","Tốt quá, ta muốn gia nhập Thái Bình Đạo!","Tốt quá, ta muốn gia nhập Thái Bình Đạo!",},
        [730] = {2018502,"Trương Giác","","Lòng dân là khó đạt nhất. Nay lòng dân đã thuận, nếu không thừa thế đoạt thiên hạ thì rất tiếc. Từ nay ta sẽ gọi là Thiên Công tướng quân.","Lòng dân là khó đạt nhất. Nay lòng dân đã thuận, nếu không thừa thế đoạt thiên hạ thì rất tiếc. Từ nay ta sẽ gọi là Thiên Công tướng quân.",},
        [731] = {2018502,"Trương Bảo","Trương Giác","Đại ca, vậy còn ta?","Đại ca, vậy còn ta?",},
        [732] = {2018502,"Trương Giác","Trương Bảo","Ngươi là Địa Công tướng quân.","Ngươi là Địa Công tướng quân.",},
        [733] = {2018502,"Trương Lương","Trương Giác","Đại ca, vậy còn ta?","Đại ca, vậy còn ta?",},
        [734] = {2018502,"Trương Giác","Trương Lương","Ngươi là Nhân Công tướng quân.","Ngươi là Nhân Công tướng quân.",},
        [735] = {2018502,"Trương Giác","Trương Lương","Nay vận mệnh nhà Hán đã hết, chúng ta phải thuận theo ý trời để đem lại thái bình.","Nay vận mệnh nhà Hán đã hết, chúng ta phải thuận theo ý trời để đem lại thái bình.",},
        [736] = {2018502,"Trương Giác","Trương Lương","Ha ha! Triều Hán đã sắp diệt vong, thay vào đó sẽ là quân Hoàng Cân!","Ha ha! Triều Hán đã sắp diệt vong, thay vào đó sẽ là quân Hoàng Cân!",},
        [737] = {2018503,"Nhân vật","Trương Giác","Trương Giác, hôm nay sẽ là ngày tàn của ngươi, mau nộp mạng đi.","Trương Giác, hôm nay sẽ là ngày tàn của ngươi, mau nộp mạng đi.",},
        [738] = {2018503,"Trương Giác","Nhân vật","Ông trời ơi...","Ông trời ơi...",},
        [739] = {2018601,"Viên Thuật","Viên Thuật Thủ Hạ","Nghe nói Tôn Kiên đó có công thảo phạt Hoàng Cân, không biết nội tình như thế nào?","Nghe nói Tôn Kiên đó có công thảo phạt Hoàng Cân, không biết nội tình như thế nào?",},
        [740] = {2018601,"Viên Thuật Thủ Hạ","Viên Thuật","Tôn thị nhiều đời làm quan ở đất Ngô. Nghe nói trước khi Tôn Kiên ra đời, mộ tổ tiên xuất hiện ánh sáng lạ, mây ngũ sắc trải dài hàng dặm.","Tôn thị nhiều đời làm quan ở đất Ngô. Nghe nói trước khi Tôn Kiên ra đời, mộ tổ tiên xuất hiện ánh sáng lạ, mây ngũ sắc trải dài hàng dặm.",},
        [741] = {2018601,"Viên Thuật","Viên Thuật Thủ Hạ","Ồ, còn có chuyện lạ như vậy.","Ồ, còn có chuyện lạ như vậy.",},
        [742] = {2018601,"Viên Thuật Thủ Hạ","Viên Thuật","Bá tánh nơi đó đều nói đó là điềm báo, Tôn gia hẳn sẽ cường thịnh trở lại. Đến lúc Tôn Kiên ra đời, dung mạo quả nhiên bất phàm.","Bá tánh nơi đó đều nói đó là điềm báo, Tôn gia hẳn sẽ cường thịnh trở lại. Đến lúc Tôn Kiên ra đời, dung mạo quả nhiên bất phàm.",},
        [743] = {2018601,"Viên Thuật Thủ Hạ","Viên Thuật","Tôn Kiên lúc trẻ từng nhận chức Huyện Thừa, rất có danh vọng, sau có công tiêu diệt phản quân nên thăng chức Phượng Tường Thái Thú.","Tôn Kiên lúc trẻ từng nhận chức Huyện Thừa, rất có danh vọng, sau có công tiêu diệt phản quân nên thăng chức Phượng Tường Thái Thú.",},
        [744] = {2018601,"Viên Thuật","Viên Thuật Thủ Hạ","Dù hắn tài giỏi thế nào cũng không thể so được với Viên gia ta.","Dù hắn tài giỏi thế nào cũng không thể so được với Viên gia ta.",},
        [745] = {2018601,"Viên Thuật Thủ Hạ","Viên Thuật","Chúa công, nếu người này có bản lĩnh, hay là thu làm thuộc hạ cùng đánh Đổng Trác.","Chúa công, nếu người này có bản lĩnh, hay là thu làm thuộc hạ cùng đánh Đổng Trác.",},
        [746] = {2018601,"Viên Thuật","Viên Thuật Thủ Hạ","Cũng được, vậy sẽ đề nghị hắn làm Phá Lỗ tướng quân, kiêm lĩnh Dự Châu Thích Sử.","Cũng được, vậy sẽ đề nghị hắn làm Phá Lỗ tướng quân, kiêm lĩnh Dự Châu Thích Sử.",},
        [747] = {2018602,"Lưu Biểu","Tôn Kiên","Tôn Văn Đài, sao lại đi ngang Kinh Châu?","Tôn Văn Đài, sao lại đi ngang Kinh Châu?",},
        [748] = {2018602,"Tôn Kiên","Lưu Biểu","Chư Hầu Nhĩ Ngu đấu đá nhau, Tôn mỗ quyết định trở về Giang Đông.","Chư Hầu Nhĩ Ngu đấu đá nhau, Tôn mỗ quyết định trở về Giang Đông.",},
        [749] = {2018602,"Tôn Kiên","Lưu Biểu","Lúc trước ta cùng Viên Thiệu Viên Thuật khởi binh, mục đích là đẻ cứu giang sơn xã tắc. Nay địch đã dọn sạch nhưng nội bộ lại phân tranh, ta phải làm thế nào đây?","Lúc trước ta cùng Viên Thiệu Viên Thuật khởi binh, mục đích là đẻ cứu giang sơn xã tắc. Nay địch đã dọn sạch nhưng nội bộ lại phân tranh, ta phải làm thế nào đây?",},
        [750] = {2018602,"Lưu Biểu","Tôn Kiên","Ngươi muốn về Giang Đông thì về, nhưng Ngọc Tỷ là bảo vật triều đình, sao ngươi lại mang theo?","Ngươi muốn về Giang Đông thì về, nhưng Ngọc Tỷ là bảo vật triều đình, sao ngươi lại mang theo?",},
        [751] = {2018602,"Tôn Kiên","Lưu Biểu","(Giận dữ) Cần chiến thì chiến, liên quan gì ngươi?","(Giận dữ) Cần chiến thì chiến, liên quan gì ngươi?",},
        [752] = {2018603,"Hàn Đương","Tôn Kiên","Tướng quân, hôm nay gió lớn e rằng là điềm chẳng lành.","Tướng quân, hôm nay gió lớn e rằng là điềm chẳng lành.",},
        [753] = {2018603,"Tôn Kiên","Hàn Đương","Chiếm Tương Dương chỉ là việc tương lai; Sao lại vì chuyện này mà lui binh được!","Chiếm Tương Dương chỉ là việc tương lai; Sao lại vì chuyện này mà lui binh được!",},
        [754] = {2018603,"Khoái Lương","Tôn Kiên","Đêm nọ nhìn trời thấy Tướng Tinh rơi ở chỗ Tôn Kiên. Chúa công nên gửi thử đến Viên Thiệu, nhờ cứu trợ.","Đêm nọ nhìn trời thấy Tướng Tinh rơi ở chỗ Tôn Kiên. Chúa công nên gửi thử đến Viên Thiệu, nhờ cứu trợ.",},
        [755] = {2018603,"Lưu Biểu","Tôn Kiên","Nay Tương Dương bị vậy, ai dám phá vòng vây?","Nay Tương Dương bị vậy, ai dám phá vòng vây?",},
        [756] = {2018603,"Lữ Công","Lưu Biểu","Mỗ nguyện đi!","Mỗ nguyện đi!",},
        [757] = {2018603,"Khoái Lương","Lữ Công","Cho người 5 vạn binh mã, mang theo nhiều cung thủ để phá trận.","Cho người 5 vạn binh mã, mang theo nhiều cung thủ để phá trận.",},
        [758] = {2018603,"Khoái Lương","Lữ Công","Tôn Kiên sẽ dẫn binh đến, chia trăm người lên núi tìm đá chuẩn bị; trăm người mai phục trong rừng. Khi dụ địch vào chỗ mai phục sẽ tấn công.","Tôn Kiên sẽ dẫn binh đến, chia trăm người lên núi tìm đá chuẩn bị; trăm người mai phục trong rừng. Khi dụ địch vào chỗ mai phục sẽ tấn công.",},
        [759] = {2018603,"Lữ Công","Khoái Lương","Kế hay!","Kế hay!",},
        [760] = {2018604,"Quân Sĩ","Tôn Kiên","Tướng quân, có một toán binh mã đang xông về hướng Tiên Sơn.","Tướng quân, có một toán binh mã đang xông về hướng Tiên Sơn.",},
        [761] = {2018604,"Tôn Kiên","Quân Sĩ","Ta dẫn một toán binh mã đuổi theo!","Ta dẫn một toán binh mã đuổi theo!",},
        [762] = {2018604,"Quân Sĩ","Tôn Kiên","Tướng quân, cẩn thận có mai phục!","Tướng quân, cẩn thận có mai phục!",},
        [763] = {2018604,"Tôn Kiên","Quân Sĩ","(Không nghe khuyên tiếp tục tiến lên) Tướng địch đừng hòng chạy!","(Không nghe khuyên tiếp tục tiến lên) Tướng địch đừng hòng chạy!",},
        [764] = {2018604,"Quân Sĩ","Tôn Kiên","Tướng quân! (Đá từ trên rơi xuống, tên từ rừng bắn ra, Tôn Kiên trúng tên hi sinh, hưởng thọ 37 tuổi)","Tướng quân! (Đá từ trên rơi xuống, tên từ rừng bắn ra, Tôn Kiên trúng tên hi sinh, hưởng thọ 37 tuổi)",},
        [765] = {2018701,"Hoàng Huyện Quận Thú","Thị Thiếp","Hiện các châu quận đang đấu nhau, hãy để Hữu Tư (Quan lại chưởng hình thưởng) của Lạc Dương định đoạt.","Hiện các châu quận đang đấu nhau, hãy để Hữu Tư (Quan lại chưởng hình thưởng) của Lạc Dương định đoạt.",},
        [766] = {2018701,"Thị Thiếp","Hoàng Huyện Quận Thú","Ai có thể đưa tấu chương cho Hữu Tư trước thì người đó có lợi.","Ai có thể đưa tấu chương cho Hữu Tư trước thì người đó có lợi.",},
        [767] = {2018701,"Hoàng Huyện Quận Thú","Thị Thiếp","Nếu như vậy thì ai có thể đưa tấu chương quan trọng như vậy?","Nếu như vậy thì ai có thể đưa tấu chương quan trọng như vậy?",},
        [768] = {2018701,"Thị Thiếp","Hoàng Huyện Quận Thú","Nghe nói Thái Sử Từ có biệt danh Thần Xạ, đại nhân có thể phái hắn đi.","Nghe nói Thái Sử Từ có biệt danh Thần Xạ, đại nhân có thể phái hắn đi.",},
        [769] = {2018701,"Hoàng Huyện Quận Thú","Thị Thiếp","Được, vậy hãy phái Thái Sử Từ đến Lạc Dương đưa tấu chương! (Thái Sử Từ đi ngày đêm, đã đến Lạc Dương)","Được, vậy hãy phái Thái Sử Từ đến Lạc Dương đưa tấu chương! (Thái Sử Từ đi ngày đêm, đã đến Lạc Dương)",},
        [770] = {2018702,"Thái Sử Từ","Châu Lại","(Đợi lâu trước công môn) Ngươi cũng đến muốn được thông chương sao?","(Đợi lâu trước công môn) Ngươi cũng đến muốn được thông chương sao?",},
        [771] = {2018702,"Châu Lại","Thái Sử Từ","Đúng vậy.","Đúng vậy.",},
        [772] = {2018702,"Thái Sử Từ","Châu Lại","Tấu chương đâu? Có thể cho ta xem không? (Đợi Châu Lại lấy ra, Thái Sử Từ lấy dao hủy nó)","Tấu chương đâu? Có thể cho ta xem không? (Đợi Châu Lại lấy ra, Thái Sử Từ lấy dao hủy nó)",},
        [773] = {2018702,"Châu Lại","Thái Sử Từ","Sao ngươi lại hủy tấu chương của ta!","Sao ngươi lại hủy tấu chương của ta!",},
        [774] = {2018702,"Thái Sử Từ","Châu Lại","Nếu ngươi không lấy tấu chương ra thì ta cũng không thể hủy nó, nay chúng ta đều mang tội. Hãy cùng ta bỏ trốn may ra giữ được tính mạng.","Nếu ngươi không lấy tấu chương ra thì ta cũng không thể hủy nó, nay chúng ta đều mang tội. Hãy cùng ta bỏ trốn may ra giữ được tính mạng.",},
        [775] = {2018702,"Châu Lại","Thái Sử Từ","Được thôi.","Được thôi.",},
        [776] = {2018702,"Thái Sử Từ","Châu Lại","Vậy chúng ta hãy mau bỏ trốn. (Sau đó Thái Sử Từ về Lạc Dương thông truyền quận chương, hoàn thành sứ mạng.)","Vậy chúng ta hãy mau bỏ trốn. (Sau đó Thái Sử Từ về Lạc Dương thông truyền quận chương, hoàn thành sứ mạng.)",},
        [777] = {2018703,"Thái Sử Từ","Tôn Sách","Bây giờ không bắt Tôn Sách, thì đợi lúc nào! Ai có gan hãy theo ta!","Bây giờ không bắt Tôn Sách, thì đợi lúc nào! Ai có gan hãy theo ta!",},
        [778] = {2018703,"Tôn Sách","Thái Sử Từ","Ngươi là ai?","Ngươi là ai?",},
        [779] = {2018703,"Thái Sử Từ","Tôn Sách","Ta là Đông Lai Thái Sử Từ, đến để bắt Tôn Sách! Ai là Tôn Sách?","Ta là Đông Lai Thái Sử Từ, đến để bắt Tôn Sách! Ai là Tôn Sách?",},
        [780] = {2018703,"Tôn Sách","Thái Sử Từ","Là ta đây. Ngươi tưởng đi 2 người là ta sợ sao! Nếu sợ thì không còn là Tiêu Bá Phù nữa!","Là ta đây. Ngươi tưởng đi 2 người là ta sợ sao! Nếu sợ thì không còn là Tiêu Bá Phù nữa!",},
        [781] = {2018703,"Thái Sử Từ","Tôn Sách","Các ngươi đến thì ta không sợ! (Thái Sử Từ vung thương đoạt Tôn Sách, cả hai chiến đấu 50 hiệp chưa phân thắng bại.)","Các ngươi đến thì ta không sợ! (Thái Sử Từ vung thương đoạt Tôn Sách, cả hai chiến đấu 50 hiệp chưa phân thắng bại.)",},
        [782] = {2018801,"Tiểu Kiều","Đại Kiều","Tỷ tỷ, sau này tỷ muốn gả cho một người như thế nào?","Tỷ tỷ, sau này tỷ muốn gả cho một người như thế nào?",},
        [783] = {2018801,"Đại Kiều","Tiểu Kiều","Muội muội thật  nghịch ngợm.","Muội muội thật  nghịch ngợm.",},
        [784] = {2018801,"Tiểu Kiều","Đại Kiều","Tỷ tỷ mặt đỏ rồi. Tỷ có người trong mộng rồi đúng không?","Tỷ tỷ mặt đỏ rồi. Tỷ có người trong mộng rồi đúng không?",},
        [785] = {2018801,"Kiều Công","Tiểu Kiều","Tiểu Kiều, đừng cười tỷ tỷ nữa. Uyển Thành sắp phá thành rồi, chúng ta không nên ra ngoài kẻo có chuyện.","Tiểu Kiều, đừng cười tỷ tỷ nữa. Uyển Thành sắp phá thành rồi, chúng ta không nên ra ngoài kẻo có chuyện.",},
        [786] = {2018801,"Đại Kiều","Kiều Công","Ờ, hãy xin nghe theo lời cha.","Ờ, hãy xin nghe theo lời cha.",},
        [787] = {2018802,"Tiểu Kiều","Đại Kiều","Cả ngày trong phủ chán quá, nhân lúc cha ra ngoài ta muốn trốn ra ngoài chơi chút!","Cả ngày trong phủ chán quá, nhân lúc cha ra ngoài ta muốn trốn ra ngoài chơi chút!",},
        [788] = {2018802,"Đại Kiều","Tiểu Kiều","Muội muội nhanh theo ta về phủ, cha đang kiếm muội.","Muội muội nhanh theo ta về phủ, cha đang kiếm muội.",},
        [789] = {2018802,"Tôn Sách","Đại Kiều","Vị cô nương này thật xinh đẹp, không biết ở phủ nào để ta tiễn nàng một đoạn?","Vị cô nương này thật xinh đẹp, không biết ở phủ nào để ta tiễn nàng một đoạn?",},
        [790] = {2018802,"Đại Kiều","Tôn Sách","(Đại Kiều nhìn lén Tôn Sách, mặt đỏ lên) Không phiền công tử. Muội muội chúng ta mau về thôi!","(Đại Kiều nhìn lén Tôn Sách, mặt đỏ lên) Không phiền công tử. Muội muội chúng ta mau về thôi!",},
        [791] = {2018802,"Chu Du","Tôn Sách","Bá Phù, đừng hù người ta!","Bá Phù, đừng hù người ta!",},
        [792] = {2018802,"Đại Kiều","Tôn Sách","Muội muội chúng ta mau về phủ thôi! (Đại Kiều kéo Tiểu Kiều nhanh chóng về Kiều Phủ)","Muội muội chúng ta mau về phủ thôi! (Đại Kiều kéo Tiểu Kiều nhanh chóng về Kiều Phủ)",},
        [793] = {2018803,"Đại Kiều","","Uyển Thành bị phá, cha không còn cách nào bèn hứa gả ta cho Đại Tướng Tôn Sách......","Uyển Thành bị phá, cha không còn cách nào bèn hứa gả ta cho Đại Tướng Tôn Sách......",},
        [794] = {2018803,"Đại Kiều","","Không biết Tôn Sách đó là người thế nào, vẫn chưa gặp mặt lần nào mà phải gả cho hắn.","Không biết Tôn Sách đó là người thế nào, vẫn chưa gặp mặt lần nào mà phải gả cho hắn.",},
        [795] = {2018803,"Tôn Sách","Đại Kiều","Cô nương còn nhớ ta không? Hôm đó sau khi gặp cô nương ta không thể quên được, không biết nàng có muốn gả cho ta không?","Cô nương còn nhớ ta không? Hôm đó sau khi gặp cô nương ta không thể quên được, không biết nàng có muốn gả cho ta không?",},
        [796] = {2018803,"Tôn Sách","Đại Kiều","Cô nương yên tâm, nếu cô nương không đồng ý thì Tôn Bá Phù tuyệt đối không làm khó nàng.","Cô nương yên tâm, nếu cô nương không đồng ý thì Tôn Bá Phù tuyệt đối không làm khó nàng.",},
        [797] = {2018803,"Đại Kiều","Tôn Sách","(Mắc cỡ)......","(Mắc cỡ)......",},
        [798] = {2018803,"Tôn Sách","Đại Kiều","Nếu cô nương không muốn nói thì hãy gật đầu để đồng ý, lắc đầu để từ chối.","Nếu cô nương không muốn nói thì hãy gật đầu để đồng ý, lắc đầu để từ chối.",},
        [799] = {2018803,"Đại Kiều","Tôn Sách","(Nhẹ nhàng gật đầu) Công tử có tâm, Đại Kiều cũng nguyện đi cùng!","(Nhẹ nhàng gật đầu) Công tử có tâm, Đại Kiều cũng nguyện đi cùng!",},
        [800] = {2018901,"Trác Quận Thái Thú","Phu Nhân","Con gái ngoan, hôm nay ta đã chọn cho con một nhà tốt!","Con gái ngoan, hôm nay ta đã chọn cho con một nhà tốt!",},
        [801] = {2018901,"Phu Nhân","Trác Quận Thái Thú","Cha, chuyện đại sự của con gái sao có thể tùy tiện như vậy!","Cha, chuyện đại sự của con gái sao có thể tùy tiện như vậy!",},
        [802] = {2018901,"Trác Quận Thái Thú","Phu Nhân","Người ta chọn chắc chắn con sẽ đồng ý. Người này tên Công Tôn Toản, xuất thân Liêu Tây quý tộc Công Tôn Thị.","Người ta chọn chắc chắn con sẽ đồng ý. Người này tên Công Tôn Toản, xuất thân Liêu Tây quý tộc Công Tôn Thị.",},
        [803] = {2018901,"Trác Quận Thái Thú","Phu Nhân","Công Tôn Toản này tuấn tú tài giỏi, tương lai vô hạn.","Công Tôn Toản này tuấn tú tài giỏi, tương lai vô hạn.",},
        [804] = {2018901,"Phu Nhân","Trác Quận Thái Thú","Nếu vậy thì cha hãy làm chủ cho con!","Nếu vậy thì cha hãy làm chủ cho con!",},
        [805] = {2018901,"Trác Quận Thái Thú","Phu Nhân","Tốt, sau này con sẽ là Công Tôn phu nhân, ha ha!","Tốt, sau này con sẽ là Công Tôn phu nhân, ha ha!",},
        [806] = {2018902,"Lưu Bị","Công Tôn Toản","Công Tôn Toản, tên này đúng là có phúc!","Công Tôn Toản, tên này đúng là có phúc!",},
        [807] = {2018902,"Công Tôn Toản","Lưu Bị","Lưu Bị, ngươi là hậu duệ của Trung Sơn Tĩnh Vương, sao ta sánh được!","Lưu Bị, ngươi là hậu duệ của Trung Sơn Tĩnh Vương, sao ta sánh được!",},
        [808] = {2018902,"Lưu Bị","Công Tôn Toản","Ngươi vừa làm con rể của Trác QuậnThái Thú, nhạc phu ngươi lại cho ngươi chức văn thư, cho ngươi đến Lư Thực làm sư, tiền đồ vô hạn.","Ngươi vừa làm con rể của Trác QuậnThái Thú, nhạc phu ngươi lại cho ngươi chức văn thư, cho ngươi đến Lư Thực làm sư, tiền đồ vô hạn.",},
        [809] = {2018902,"Công Tôn Toản","Lưu Bị","Ha ha, ngươi cũng muốn bái Lư Thực vi sư mà đúng không? Tốt, sau này chúng ta sẽ chăm sóc cho nhau.","Ha ha, ngươi cũng muốn bái Lư Thực vi sư mà đúng không? Tốt, sau này chúng ta sẽ chăm sóc cho nhau.",},
        [810] = {2018903,"Công Tôn Toản","Ô Hằng Vương","Ta là Bạch Mã Tướng Công Tôn Toản! Được lệnh triều đình trấn thủ biên giới. Các ngươi hãy chịu tội đi!","Ta là Bạch Mã Tướng Công Tôn Toản! Được lệnh triều đình trấn thủ biên giới. Các ngươi hãy chịu tội đi!",},
        [811] = {2018903,"Ô Hằng Vương","Công Tôn Toản","Tướng quân, chúng ta đều phụng mệnh hành sự, sao cứ phải bạo lực, mắt nhắm mắt mở không tốt hơn sao.","Tướng quân, chúng ta đều phụng mệnh hành sự, sao cứ phải bạo lực, mắt nhắm mắt mở không tốt hơn sao.",},
        [812] = {2018903,"Công Tôn Toản","Ô Hằng Vương","Bọn tặc tử các ngươi còn dám đến thì đừng trách Công Tôn Toản ta!","Bọn tặc tử các ngươi còn dám đến thì đừng trách Công Tôn Toản ta!",},
        [813] = {2018903,"Ô Hằng Vương","Công Tôn Toản","Không ngờ bên địch lại có anh hùng hảo hán như vậy?! Được rồi, bọn ta lui!","Không ngờ bên địch lại có anh hùng hảo hán như vậy?! Được rồi, bọn ta lui!",},
        [814] = {2018903,"Ô Hằng Vương","Công Tôn Toản","Sau này thấy bọn cưỡi ngựa trắng thì giải tán ngay! Tính mạng quan trọng!","Sau này thấy bọn cưỡi ngựa trắng thì giải tán ngay! Tính mạng quan trọng!",},
        [815] = {20181001,"Phản Loạn","Giả Hủ","Hôm nay thật may, bắt được hơn 10 người phe địch! Ha ha ha!","Hôm nay thật may, bắt được hơn 10 người phe địch! Ha ha ha!",},
