        [1260] = {20183501,"Pháp Chính","Mạnh Đạt","Nghe nói Ích Châu Mục Lưu Chương, con người thật thà, giữa thời loạn thế vững giữ được đạo đức.","Nghe nói Ích Châu Mục Lưu Chương, con người thật thà, giữa thời loạn thế vững giữ được đạo đức.",},
        [1261] = {20183501,"Pháp Chính","Mạnh Đạt","Ta ngươi luôn có hoài bảo, hay là qua chỗ hắn xem tình hình sao.","Ta ngươi luôn có hoài bảo, hay là qua chỗ hắn xem tình hình sao.",},
