        [1263] = {20183502,"Pháp Chính","Lưu Bị","Các hạ mệnh thế anh tài, Lưu Chương không làm được minh chủ, Trương Tùng làm nội ứng, đoạt Ích Châu.","Các hạ mệnh thế anh tài, Lưu Chương không làm được minh chủ, Trương Tùng làm nội ứng, đoạt Ích Châu.",},
        [1264] = {20183502,"Pháp Chính","Lưu Bị","Lấy sự giàu có của Ích Châu làm gốc, dựa vào thành tựu đại nghiệp của Thiên Phủ, dễ như trở bàn tay.","Lấy sự giàu có của Ích Châu làm gốc, dựa vào thành tựu đại nghiệp của Thiên Phủ, dễ như trở bàn tay.",},
        [1265] = {20183502,"Lưu Bị","Pháp Chính","Được Hiếu Trực khẳng định, ta lập tức dẫn quân vào Thục.","Được Hiếu Trực khẳng định, ta lập tức dẫn quân vào Thục.",},
