        [1275] = {20183602,"Tôn Quyền","","Huyền Đức này năm lần bảy lượt xin ta, dùng nửa Giang Hạ đổi nửa Nam Quận của Kinh Châu, để hắn phát triển.","Huyền Đức này năm lần bảy lượt xin ta, dùng nửa Giang Hạ đổi nửa Nam Quận của Kinh Châu, để hắn phát triển.",},
        [1276] = {20183602,"Tôn Quyền","","Chuyện lỗ như thế sao ta lại đồng ý, hoang đường!","Chuyện lỗ như thế sao ta lại đồng ý, hoang đường!",},
        [1277] = {20183602,"Lỗ Túc","Tôn Quyền","Thần có cách nhìn khác, Kinh Châu này nên cho hắn mượn.","Thần có cách nhìn khác, Kinh Châu này nên cho hắn mượn.",},
        [1278] = {20183602,"Tôn Quyền","Lỗ Túc","Sao lại nói vậy?","Sao lại nói vậy?",},
        [1279] = {20183602,"Lỗ Túc","Tôn Quyền","Thứ nhất, tuy nói là mượn Kinh Châu, nhưng thật ra chỉ cần mượn Nam Quận, hai khu nhỏ của Tương Dương.","Thứ nhất, tuy nói là mượn Kinh Châu, nhưng thật ra chỉ cần mượn Nam Quận, hai khu nhỏ của Tương Dương.",},
        [1280] = {20183602,"Lỗ Túc","Tôn Quyền","Thứ hai, Lưu Bị ở ngoài Đông Ngô ta, người duy nhất dám chống lại Tào Tháo, kẻ địch của kẻ địch là bạn của ta.","Thứ hai, Lưu Bị ở ngoài Đông Ngô ta, người duy nhất dám chống lại Tào Tháo, kẻ địch của kẻ địch là bạn của ta.",},
        [1281] = {20183602,"Lỗ Túc","Tôn Quyền","Nó có giá trị chiến lược rất lớn, vượt qua giá trị của vài khu đất.","Nó có giá trị chiến lược rất lớn, vượt qua giá trị của vài khu đất.",},
