        [1285] = {20183603,"Lỗ Túc","Quan Vũ","Nếu sợ thì đã không đến. Hôm đó các ngươi binh bại, Giang Đông ta có thành ý cho các ngươi mượn đất.","Nếu sợ thì đã không đến. Hôm đó các ngươi binh bại, Giang Đông ta có thành ý cho các ngươi mượn đất.",},
        [1286] = {20183603,"Lỗ Túc","Quan Vũ","Nay các ngươi lấy được Ích Châu, lại không muốn trả lại Kinh Châu, vậy chỉ xin các ngươi trả lại 3 quận.","Nay các ngươi lấy được Ích Châu, lại không muốn trả lại Kinh Châu, vậy chỉ xin các ngươi trả lại 3 quận.",},
