        [1299] = {20183702,"Lưu Bị","Bàng Thống","Tào Tháo nghiêm khắc, ta sẽ rộng lượng, Tào Tháo bạo ngược, ta sẽ nhân từ, Tào Tháo gian xảo, ta sẽ trung thành.","Tào Tháo nghiêm khắc, ta sẽ rộng lượng, Tào Tháo bạo ngược, ta sẽ nhân từ, Tào Tháo gian xảo, ta sẽ trung thành.",},
        [1300] = {20183702,"Lưu Bị","Bàng Thống","Mọi việc đều ngược với hắn, mới có thành tựu hôm nay. Nay vì có được Ích Châu, phải thất tín thiên hạ sao?","Mọi việc đều ngược với hắn, mới có thành tựu hôm nay. Nay vì có được Ích Châu, phải thất tín thiên hạ sao?",},
        [1301] = {20183702,"Bàng Thống","Lưu Bị","Nay thiên hạ đại loạn, phải biết tùy cơ ứng biến.","Nay thiên hạ đại loạn, phải biết tùy cơ ứng biến.",},
        [1302] = {20183702,"Bàng Thống","Lưu Bị","Chỉ cần sau khi xong việc trả lại hắn một mảnh đất, thì không ai nói ngài phụ tín nghĩa nữa.","Chỉ cần sau khi xong việc trả lại hắn một mảnh đất, thì không ai nói ngài phụ tín nghĩa nữa.",},
