        [1319] = {20183901,"Tào Tháo","","Ở đại yến ta muốn nghe tiếng trống của Cổ Sử.","Ở đại yến ta muốn nghe tiếng trống của Cổ Sử.",},
        [1320] = {20183902,"Tào Tháo","Nễ Hoành","Các Cổ Sử đều thay đổi y phục chuyên dụng.","Các Cổ Sử đều thay đổi y phục chuyên dụng.",},
        [1321] = {20183902,"Tào Tháo","Nễ Hoành","Tại sao Nễ Hoành ngươi không thay y phục, thì đã bạo gan dẫn trước đi vào?","Tại sao Nễ Hoành ngươi không thay y phục, thì đã bạo gan dẫn trước đi vào?",},
        [1322] = {20183902,"Nễ Hoành","Tào Tháo","Được! Thần sẽ đi thay y phục. (Nói xong liền cởi y phục rồi đánh trống, sau đó từ từ mặc bộ y phục chuyên dụng.)","Được! Thần sẽ đi thay y phục. (Nói xong liền cởi y phục rồi đánh trống, sau đó từ từ mặc bộ y phục chuyên dụng.)",},
        [1323] = {20183902,"Tào Tháo","Nễ Hoành","Hay lắm Nễ Hoành, không ngờ lại bị ngươi trêu lại.","Hay lắm Nễ Hoành, không ngờ lại bị ngươi trêu lại.",},
        [1324] = {20183903,"Lưu Biểu","","Tào Tháo sẽ tặng Nễ Hoành cho ta.","Tào Tháo sẽ tặng Nễ Hoành cho ta.",},
        [1325] = {20183903,"Lưu Biểu","","Tên Nễ Hoành này đúng là không biết trời cao đất dày, dám trêu Lưu Biểu ta.","Tên Nễ Hoành này đúng là không biết trời cao đất dày, dám trêu Lưu Biểu ta.",},
        [1326] = {20183903,"Lưu Biểu","","Thái Thú Hoàng Tổ tính tình nóng nảy, ta sẽ tặng Nễ Hoành cho hắn.","Thái Thú Hoàng Tổ tính tình nóng nảy, ta sẽ tặng Nễ Hoành cho hắn.",},
        [1327] = {20183904,"Hoàng Tổ","","Tiệc trên thuyền tên Nễ Hoành ăn nói vô lễ, khiến ta khó xử.","Tiệc trên thuyền tên Nễ Hoành ăn nói vô lễ, khiến ta khó xử.",},
        [1328] = {20183904,"Hoàng Tổ","","Giáo huấn một chút thì to tiếng với ta.","Giáo huấn một chút thì to tiếng với ta.",},
        [1329] = {20183904,"Hoàng Tổ","","Đúng là không thể nhìn được, giải quyết tên Nễ Hoành này đi!","Đúng là không thể nhìn được, giải quyết tên Nễ Hoành này đi!",},
        [1330] = {20184001,"Viên Thiệu","","Ta chiếm Ký Châu, đoạt Thanh, Tịnh hai châu, trong trận Dịch Kinh đánh bại Công Tôn Toản, thống nhất Hà Bắc.","Ta chiếm Ký Châu, đoạt Thanh, Tịnh hai châu, trong trận Dịch Kinh đánh bại Công Tôn Toản, thống nhất Hà Bắc.",},
        [1331] = {20184001,"Viên Thiệu","","Đây đều là công lao của Khúc Nghĩa, Nhan Lương, Văn Xú, Trương Hợp, Cao Lãm.","Đây đều là công lao của Khúc Nghĩa, Nhan Lương, Văn Xú, Trương Hợp, Cao Lãm.",},
        [1332] = {20184001,"Viên Thiệu","","Nhưng Nhan Lương Văn Xú đôi Hổ Lang Huynh Đệ này!","Nhưng Nhan Lương Văn Xú đôi Hổ Lang Huynh Đệ này!",},
        [1333] = {20184001,"Viên Thiệu","","Là mãnh tướng dưới trướng của ta.","Là mãnh tướng dưới trướng của ta.",},
        [1334] = {20184002,"Tuân Du","Tào Tháo","Thừa tướng, nay binh ít. Công đến Diên Tân, nếu cho lính chặn đường sau, Thiệu sẽ ứng cứu.","Thừa tướng, nay binh ít. Công đến Diên Tân, nếu cho lính chặn đường sau, Thiệu sẽ ứng cứu.",},
        [1335] = {20184002,"Tuân Du","Tào Tháo","Sau đó khinh binh đánh bạch mã, nhân lúc lơ là, có thể bắt được Nhan Lương.","Sau đó khinh binh đánh bạch mã, nhân lúc lơ là, có thể bắt được Nhan Lương.",},
        [1336] = {20184002,"Tào Tháo","Tuân Du","Được, cứ thế kế Công Đạt","Được, cứ thế kế Công Đạt",},
        [1337] = {20184003,"Tào Tháo","Tuân Du","Chưa đến hơn mười lý, Nhan Lương đã sợ, đến nghịch chiến.","Chưa đến hơn mười lý, Nhan Lương đã sợ, đến nghịch chiến.",},
        [1338] = {20184003,"Tuân Du","Tào Tháo","Đến đúng lúc lắm, Trương Liêu, Quan Vũ làm tiên phong, chắc chắn đẩy lùi được.","Đến đúng lúc lắm, Trương Liêu, Quan Vũ làm tiên phong, chắc chắn đẩy lùi được.",},
        [1339] = {20184003,"Quan Vũ","Tuân Du","Giải Bạch Mã Chi Vi này, mình ta là đủ, để Sách Mã ta lấy thủ cấp của Nhan Lương.","Giải Bạch Mã Chi Vi này, mình ta là đủ, để Sách Mã ta lấy thủ cấp của Nhan Lương.",},
        [1340] = {20184004,"Văn Xú","Quan Vũ","Bạch Mã Chiến, là ngươi đã hại huynh đệ Nhan Lương của ta?","Bạch Mã Chiến, là ngươi đã hại huynh đệ Nhan Lương của ta?",},
        [1341] = {20184004,"Quan Vũ","Văn Xú","Là ta thì sao.","Là ta thì sao.",},
        [1342] = {20184004,"Văn Xú","Quan Vũ","Hôm nay ta sẽ báo thù cho Nhan Lương huynh!","Hôm nay ta sẽ báo thù cho Nhan Lương huynh!",},
        [1343] = {20184004,"Quan Vũ","Văn Xú","Báo thù? E rằng mạng của ngươi cũng không giữ được!","Báo thù? E rằng mạng của ngươi cũng không giữ được!",},
        [1344] = {20184101,"Nguyệt Anh","Thái Phu Nhân","Di mẫu mời ta đến phủ làm khách, không biết có dụng ý gì?","Di mẫu mời ta đến phủ làm khách, không biết có dụng ý gì?",},
        [1345] = {20184101,"Thái Phu Nhân","Nguyệt Anh","Nguyệt Anh, dì nhớ con. Con nhìn con suốt ngày lấy khăn che mặt, người ta cứ tưởng con xấu, thật ra Nguyệt Anh của ta xinh đẹp tuyệt trần.","Nguyệt Anh, dì nhớ con. Con nhìn con suốt ngày lấy khăn che mặt, người ta cứ tưởng con xấu, thật ra Nguyệt Anh của ta xinh đẹp tuyệt trần.",},
        [1346] = {20184101,"Nguyệt Anh","Thái Phu Nhân","Dì lại chọc ghẹo người ta rồi, Nguyệt Anh không thích gặp người ngoài.","Dì lại chọc ghẹo người ta rồi, Nguyệt Anh không thích gặp người ngoài.",},
        [1347] = {20184101,"Thái Phu Nhân","Nguyệt Anh","Đến xem này, cậu Thái Mạo có tặng mấy tấm Thục Cẩm, xem coi con thích cái nào?","Đến xem này, cậu Thái Mạo có tặng mấy tấm Thục Cẩm, xem coi con thích cái nào?",},
        [1348] = {20184101,"Nguyệt Anh","Thái Phu Nhân","Nguyệt Anh suốt ngày đọc sách, chỉ thích nghề mộc, e rằng sẽ lãng phí Thục Cẩm đầy giá trị.","Nguyệt Anh suốt ngày đọc sách, chỉ thích nghề mộc, e rằng sẽ lãng phí Thục Cẩm đầy giá trị.",},
        [1349] = {20184101,"Thái Phu Nhân","Nguyệt Anh","Thục Cẩm này là do cựu cựu Thái Mạo phái người đưa đến, con đừng phụ lòng ngài ấy. Hơn nữa con sắp thành thân rồi, phải mặc đồ màu sặc sỡ hơn mới phải.","Thục Cẩm này là do cựu cựu Thái Mạo phái người đưa đến, con đừng phụ lòng ngài ấy. Hơn nữa con sắp thành thân rồi, phải mặc đồ màu sặc sỡ hơn mới phải.",},
        [1350] = {20184101,"Nguyệt Anh","Thái Phu Nhân","Dì cứ thay con chọn là được.","Dì cứ thay con chọn là được.",},
        [1351] = {20184102,"Lưu Biểu","Thái Mạo","Triều đình bổ nhiệm ta làm Kinh Châu Thích Sử. Kinh Châu trộm cướp lộng hành, Thái Mạo, Thái gia là danh tộc Tương Dương, ngươi có kế gì có thể trị loạn không?","Triều đình bổ nhiệm ta làm Kinh Châu Thích Sử. Kinh Châu trộm cướp lộng hành, Thái Mạo, Thái gia là danh tộc Tương Dương, ngươi có kế gì có thể trị loạn không?",},
        [1352] = {20184102,"Thái Mạo","Lưu Biểu","Thủ lĩnh bọn trộm cướp tham lam, tàn bạo. Ta có một số người tài có thể phái đến gặp hắn cho chút lợi lộc, hắn ắt sẽ gọi mọi người đến đây. Sau đó người có thể diệt hắn rồi chiêu hàng thủ hạ của hắn.","Thủ lĩnh bọn trộm cướp tham lam, tàn bạo. Ta có một số người tài có thể phái đến gặp hắn cho chút lợi lộc, hắn ắt sẽ gọi mọi người đến đây. Sau đó người có thể diệt hắn rồi chiêu hàng thủ hạ của hắn.",},
        [1353] = {20184102,"Thái Mạo","Lưu Biểu","Khi đó, người ở Kinh Châu biết ngài nhân đức, chắc chắn sẽ kéo đến. Sau đó tập hợp binh lính, nam cứ Giang Lăng, bắc thủ Tương Dương, Kinh Châu tám quận đều sẽ được yên ổn.","Khi đó, người ở Kinh Châu biết ngài nhân đức, chắc chắn sẽ kéo đến. Sau đó tập hợp binh lính, nam cứ Giang Lăng, bắc thủ Tương Dương, Kinh Châu tám quận đều sẽ được yên ổn.",},
        [1354] = {20184102,"Lưu Biểu","Thái Mạo","Nếu việc này xong, ta sẽ bẩm báo triều đình ban thưởng cho ngươi!","Nếu việc này xong, ta sẽ bẩm báo triều đình ban thưởng cho ngươi!",},
        [1355] = {20184103,"Lưu Tông","","Cha ta đã mất, ta có người anh ở Giang Hạ, thúc phụ Huyền Đức ở Tân Dã. Các ngươi lập ta làm vua, nếu anh ta và thúc phụ khởi binh hỏi tội, ta phải làm sao?","Cha ta đã mất, ta có người anh ở Giang Hạ, thúc phụ Huyền Đức ở Tân Dã. Các ngươi lập ta làm vua, nếu anh ta và thúc phụ khởi binh hỏi tội, ta phải làm sao?",},
        [1356] = {20184103,"Lý Khuê","Lưu Tông","Hãy gửi thư báo tang đến Giang Hạ, xin đại công tử làm chủ Kinh Châu, lệnh Huyền Đức cùng quản lý, bắc có thể địch Tào Tháo, nam có thể chống lại Tôn Quyền. Đây là kế vẹn toàn.","Hãy gửi thư báo tang đến Giang Hạ, xin đại công tử làm chủ Kinh Châu, lệnh Huyền Đức cùng quản lý, bắc có thể địch Tào Tháo, nam có thể chống lại Tôn Quyền. Đây là kế vẹn toàn.",},
        [1357] = {20184103,"Thái Mạo","Lý Khuê","Ngươi là ai, sao dám nói điều trái lời chúa công!","Ngươi là ai, sao dám nói điều trái lời chúa công!",},
        [1358] = {20184103,"Lý Khuê","Thái Mạo","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!",},
        [1359] = {20184103,"Thái Mạo","Lý Khuê","Người đâu, kẻ này dám ăn nói xằng bậy, lôi ra trảm thủ! Chúa công trước khi mất đã dặn cho con thứ Lưu Tông làm chủ Kinh Châu!","Người đâu, kẻ này dám ăn nói xằng bậy, lôi ra trảm thủ! Chúa công trước khi mất đã dặn cho con thứ Lưu Tông làm chủ Kinh Châu!",},
        [1360] = {20184104,"Thái Mạo","Thái Phu Nhân","Muội muội, Lưu Bị phái ba tướng đi, một mình ở Kinh Châu, chắc chắn sẽ gặp họa.","Muội muội, Lưu Bị phái ba tướng đi, một mình ở Kinh Châu, chắc chắn sẽ gặp họa.",},
        [1361] = {20184104,"Thái Phu Nhân","Thái Mạo","Ta nghe nói người ở Kinh Châu thường qua lại với Lưu Bị, không thể không đề phòng.","Ta nghe nói người ở Kinh Châu thường qua lại với Lưu Bị, không thể không đề phòng.",},
        [1362] = {20184104,"Thái Mạo","Thái Phu Nhân","Hay là hãy diệt hắn trước rồi bẩm báo lại với chúa công.","Hay là hãy diệt hắn trước rồi bẩm báo lại với chúa công.",},
        [1363] = {20184104,"Y Tịch","Thái Mạo","Thái Mạo nghĩ kế hại người, ba nơi đông, nam, bắc ngoài thành đều có quân lính trấn giữ. Chỉ có cửa tây có thể đi được, ngài hãy mau chạy!","Thái Mạo nghĩ kế hại người, ba nơi đông, nam, bắc ngoài thành đều có quân lính trấn giữ. Chỉ có cửa tây có thể đi được, ngài hãy mau chạy!",},
        [1364] = {20184104,"Thái Mạo","Y Tịch","Sao ngài lại phải chạy trốn?","Sao ngài lại phải chạy trốn?",},
        [1365] = {20184104,"Lưu Bị","Thái Mạo","Ta và ngươi không thù không oán, sao lại hại ta? Cũng may ta có Đích Lô Mã, nếu không đã mất mạng!","Ta và ngươi không thù không oán, sao lại hại ta? Cũng may ta có Đích Lô Mã, nếu không đã mất mạng!",},
        [1366] = {20184105,"Thái Mạo","Lưu Kỳ","Chúa công, Tào Tháo dẫn đại quân hướng đến Tương Dương! Chúa công hay là dâng Kinh Tương chín quận cho Tào Tháo, Tào Tháo sẽ trọng dụng chúa công.","Chúa công, Tào Tháo dẫn đại quân hướng đến Tương Dương! Chúa công hay là dâng Kinh Tương chín quận cho Tào Tháo, Tào Tháo sẽ trọng dụng chúa công.",},
        [1367] = {20184105,"Lưu Kỳ","Thái Mạo","Ta vừa kế thừa cơ nghiệp tiên quân, sao có thể dâng cho người khác?","Ta vừa kế thừa cơ nghiệp tiên quân, sao có thể dâng cho người khác?",},
        [1368] = {20184105,"Thái Mạo","Lưu Kỳ","Nay Tào Tháo lấy danh nghĩa triều đình, chúa công từ chối sẽ không đúng lẽ. Hơn nữa chúa công mới lên ngôi, giặc trong giặc ngoài, biết phải làm sao chống chọi? Chúa công không nên do dự.","Nay Tào Tháo lấy danh nghĩa triều đình, chúa công từ chối sẽ không đúng lẽ. Hơn nữa chúa công mới lên ngôi, giặc trong giặc ngoài, biết phải làm sao chống chọi? Chúa công không nên do dự.",},
        [1369] = {20184105,"Lưu Kỳ","Thái Mạo","Vậy cứ theo lời tướng quân, dâng Kinh Châu cho Tào Tháo.","Vậy cứ theo lời tướng quân, dâng Kinh Châu cho Tào Tháo.",},
        [1370] = {20184106,"Tào Tháo","Thái Mạo","Trận chiến Tam Giang Khẩu, Đông Ngô tuy quân ít nhưng lại chiến thắng đó là do chúng ta bất cẩn!","Trận chiến Tam Giang Khẩu, Đông Ngô tuy quân ít nhưng lại chiến thắng đó là do chúng ta bất cẩn!",},
        [1371] = {20184106,"Thái Mạo","Tào Tháo","Kinh Châu Thủy Quân lâu không rèn luyện, quân Thanh, Từ lại không quen thủy chiến vì vậy mới bại trận. Nay nên lập Thủy Trại, lệnh quân Thanh, Từ ở giữa, quân Kinh Châu ở ngoài, ngày ngày luyện tập.","Kinh Châu Thủy Quân lâu không rèn luyện, quân Thanh, Từ lại không quen thủy chiến vì vậy mới bại trận. Nay nên lập Thủy Trại, lệnh quân Thanh, Từ ở giữa, quân Kinh Châu ở ngoài, ngày ngày luyện tập.",},
        [1372] = {20184106,"Tào Tháo","Thái Mạo","Ngươi là đô đốc thủy quân, cứ làm theo ý ngươi, không cần bẩm báo!","Ngươi là đô đốc thủy quân, cứ làm theo ý ngươi, không cần bẩm báo!",},
        [1373] = {20184106,"Thái Mạo","Tào Tháo","Thủy quân giao cho Thái Mạo, thừa tướng cứ yên tâm, Thái Mạo sẽ cố gắng rèn luyện, việc bắt Chu Du chỉ làm sớm muộn!","Thủy quân giao cho Thái Mạo, thừa tướng cứ yên tâm, Thái Mạo sẽ cố gắng rèn luyện, việc bắt Chu Du chỉ làm sớm muộn!",},
        [1374] = {20184107,"Tưởng Can","Tào Tháo","Thừa tướng, lần này ta đi Đông Ngô, tuy chưa thể thuyết phục Chu Du, nhưng đã thăm dò được một việc, Thái Mạo, Trương Doãn hai người đang âm thầm cấu kết với Chu Du, e rằng sẽ bất lợi cho thừa tướng!","Thừa tướng, lần này ta đi Đông Ngô, tuy chưa thể thuyết phục Chu Du, nhưng đã thăm dò được một việc, Thái Mạo, Trương Doãn hai người đang âm thầm cấu kết với Chu Du, e rằng sẽ bất lợi cho thừa tướng!",},
        [1375] = {20184107,"Tào Tháo","Tưởng Can","Thật to gan! Thái Mạo, Trương Doãn! Ta sẽ cho hai người các ngươi tiến quân.","Thật to gan! Thái Mạo, Trương Doãn! Ta sẽ cho hai người các ngươi tiến quân.",},
        [1376] = {20184107,"Thái Mạo","Tào Tháo","Thừa tướng, thủy quân vẫn chưa thuần thục, không thể khinh suất.","Thừa tướng, thủy quân vẫn chưa thuần thục, không thể khinh suất.",},
        [1377] = {20184107,"Tào Tháo","Thái Mạo","Đợi nữa, thủ cấp của ta đã phải dâng cho Chu Lang rồi! Người đâu, lôi Thái Mạo, Trương Doãn ra trảm thủ!","Đợi nữa, thủ cấp của ta đã phải dâng cho Chu Lang rồi! Người đâu, lôi Thái Mạo, Trương Doãn ra trảm thủ!",},
        [1378] = {20184107,"Thái Mạo","Tào Tháo","Thừa Tướng! Thái Mạo một lòng trung thành!","Thừa Tướng! Thái Mạo một lòng trung thành!",},
        [1379] = {20184201,"Điển Vi","Lý Vĩnh","Lý Vĩnh, ngươi diệt Lưu Thị, ta sẽ lấy mạng ngươi để báo thù.","Lý Vĩnh, ngươi diệt Lưu Thị, ta sẽ lấy mạng ngươi để báo thù.",},
        [1380] = {20184201,"Lý Vĩnh","Điển Vi","Ta là huyện lệnh Phú Xuân, trong nhà canh phòng nghiêm ngặt, sao phải sợ ngươi.","Ta là huyện lệnh Phú Xuân, trong nhà canh phòng nghiêm ngặt, sao phải sợ ngươi.",},
        [1381] = {20184201,"Điển Vi","Lý Vĩnh","Ngươi hãy đợi đấy, ta thề sẽ lấy mạng ngươi! (Sau Điển Vi mang gà và rượu đến, cải trang thành người hỏi thăm Lý Vĩnh, sau khi mở cửa, hắn xông vào diệt Lý Vĩnh, vì vậy được hào kiệt biết đến.)","Ngươi hãy đợi đấy, ta thề sẽ lấy mạng ngươi! (Sau Điển Vi mang gà và rượu đến, cải trang thành người hỏi thăm Lý Vĩnh, sau khi mở cửa, hắn xông vào diệt Lý Vĩnh, vì vậy được hào kiệt biết đến.)",},
        [1382] = {20184202,"Trương Mạc","Điển Vi","Ngươi là ai? Cờ nha môn vừa lớn vừa nặng, không ngờ ngươi một tay có thể cầm lên.","Ngươi là ai? Cờ nha môn vừa lớn vừa nặng, không ngờ ngươi một tay có thể cầm lên.",},
        [1383] = {20184202,"Điển Vi","Trương Mạc","Ta là Điển Vi.","Ta là Điển Vi.",},
        [1384] = {20184202,"Trương Mạc","Điển Vi","Thì ra ngươi chính là Điển Vi! Ta là Trần Lưu Thái Thú Trương Mạc, cùng Tào Tháo khởi nghĩa diệt trừ Đổng Trác, ta đã nghe danh ngươi từ lâu, nay đến chiêu mộ ngươi, ý ngươi thế nào?","Thì ra ngươi chính là Điển Vi! Ta là Trần Lưu Thái Thú Trương Mạc, cùng Tào Tháo khởi nghĩa diệt trừ Đổng Trác, ta đã nghe danh ngươi từ lâu, nay đến chiêu mộ ngươi, ý ngươi thế nào?",},
        [1385] = {20184202,"Điển Vi","Trương Mạc","Lão tặc Đổng Trác gây loạn triều đình Điển Vi nguyện góp sức cho triều đình!","Lão tặc Đổng Trác gây loạn triều đình Điển Vi nguyện góp sức cho triều đình!",},
        [1386] = {20184202,"Trương Mạc","Điển Vi","Tốt!","Tốt!",},
        [1387] = {20184203,"Tào Tháo","Điển Vi","Điển Vi trung thành, dũng võ! Ba quân đều khen ngợi ngươi.","Điển Vi trung thành, dũng võ! Ba quân đều khen ngợi ngươi.",},
        [1388] = {20184203,"Điển Vi","Tào Tháo","Ha ha, họ chỉ trêu đùa thôi.","Ha ha, họ chỉ trêu đùa thôi.",},
        [1389] = {20184203,"Tào Tháo","Điển Vi","Không cần phải khiêm tốn, ta biết ngươi thích uống rượu, người đâu, mau dâng rượu!","Không cần phải khiêm tốn, ta biết ngươi thích uống rượu, người đâu, mau dâng rượu!",},
        [1390] = {20184203,"Điển Vi","Tào Tháo","Đa tạ chúa công!","Đa tạ chúa công!",},
        [1391] = {20184204,"Tào Tháo","Điển Vi","Chuyện gì ở bên ngoài?","Chuyện gì ở bên ngoài?",},
        [1392] = {20184204,"Điển Vi","Tào Tháo","Chúa công, e là Trương Tú làm phản, ta đi ngăn chúng, ngài hãy mau rút lui!","Chúa công, e là Trương Tú làm phản, ta đi ngăn chúng, ngài hãy mau rút lui!",},
        [1393] = {20184204,"Tào Tháo","Điển Vi","Trương Tú tại sao đầu hàng rồi lại làm phản?","Trương Tú tại sao đầu hàng rồi lại làm phản?",},
        [1394] = {20184204,"Điển Vi","Tào Tháo","Đông người quá! Chúa công..., mau đi!","Đông người quá! Chúa công..., mau đi!",},
        [1395] = {20184301,"Đổng Trác","Lý Thôi","Lý Thôi, ngươi xưa nay dũng mãnh, lại giỏi dụng binh, có tài hùng biện. Nay Tôn Kiên quá lợi hại, quân ta nhiều lần bại trận, ngươi có kế sách gì không?","Lý Thôi, ngươi xưa nay dũng mãnh, lại giỏi dụng binh, có tài hùng biện. Nay Tôn Kiên quá lợi hại, quân ta nhiều lần bại trận, ngươi có kế sách gì không?",},
        [1396] = {20184301,"Lý Thôi","Đổng Trác","Thừa tướng, nghe nói Tôn Kiên có người con trai, tuổi trẻ tài cao, hay người gả con gái út cho con trai của Tôn Kiên. Như vậy, Tôn Kiên sẽ làm việc cho thừa tướng.","Thừa tướng, nghe nói Tôn Kiên có người con trai, tuổi trẻ tài cao, hay người gả con gái út cho con trai của Tôn Kiên. Như vậy, Tôn Kiên sẽ làm việc cho thừa tướng.",},
        [1397] = {20184301,"Đổng Trác","Lý Thôi","Được, vậy ngươi hãy đi cầu thân, nếu việc thành, ta sẽ trọng thưởng cho ngươi.","Được, vậy ngươi hãy đi cầu thân, nếu việc thành, ta sẽ trọng thưởng cho ngươi.",},
        [1398] = {20184301,"Lý Thôi","Đổng Trác","Thừa tướng hãy đợi tin tốt!","Thừa tướng hãy đợi tin tốt!",},
        [1399] = {20184302,"Lý Thôi","Giả Hủ","Đổng thái sư đã mất, bọn ta chạy trốn về Tây Lương, không chừng có cơ hội sống sót.","Đổng thái sư đã mất, bọn ta chạy trốn về Tây Lương, không chừng có cơ hội sống sót.",},
        [1400] = {20184302,"Giả Hủ","Lý Thôi","Mọi người đừng vội chay, ta có một kế, nếu thành sẽ làm việc cho triều đình, còn không thì mới chạy cũng chưa muộn.","Mọi người đừng vội chay, ta có một kế, nếu thành sẽ làm việc cho triều đình, còn không thì mới chạy cũng chưa muộn.",},
        [1401] = {20184302,"Lý Thôi","Giả Hủ","Văn Hòa, ngươi có cách gì không?","Văn Hòa, ngươi có cách gì không?",},
        [1402] = {20184302,"Giả Hủ","Lý Thôi","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.",},
        [1403] = {20184302,"Lý Thôi","Giả Hủ","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.",},
        [1404] = {20184303,"Hán Hiến Đế","Giả Hủ","Ngôi vua này của trẫm sao mà khổ thế. Lý Thôi, Quách Tị như hổ như sói, khiến cả Trường An tan hoang.","Ngôi vua này của trẫm sao mà khổ thế. Lý Thôi, Quách Tị như hổ như sói, khiến cả Trường An tan hoang.",},
        [1405] = {20184303,"Giả Hủ","Hán Hiến Đế","Bệ hạ, hay là đến Lạc Dương, rời khỏi Trường An thị phi.","Bệ hạ, hay là đến Lạc Dương, rời khỏi Trường An thị phi.",},
        [1406] = {20184303,"Hán Hiến Đế","Giả Hủ","E rằng Lý Thôi, Quách Tị sẽ không đồng ý.","E rằng Lý Thôi, Quách Tị sẽ không đồng ý.",},
        [1407] = {20184303,"Giả Hủ","Hán Hiến Đế","Bệ hạ đừng lo! Lý Thôi luôn nghe lời ta, ta có diệu kế này. Nghe nói thê tử của Quách Tị hay đố kỵ, có thể dùn kế phản gián, khiến Lý Thôi, Quách Tị tương tàn.","Bệ hạ đừng lo! Lý Thôi luôn nghe lời ta, ta có diệu kế này. Nghe nói thê tử của Quách Tị hay đố kỵ, có thể dùn kế phản gián, khiến Lý Thôi, Quách Tị tương tàn.",},
        [1408] = {20184304,"Quách Tị","Lý Thôi","Lý Thôi, ngươi mời ta dự tiệc, nhưng bỏ độc trong rượu, ngươi định hại ta sao?","Lý Thôi, ngươi mời ta dự tiệc, nhưng bỏ độc trong rượu, ngươi định hại ta sao?",},
        [1409] = {20184304,"Quách Tị","Lý Thôi","Cũng may ta mạng lớn","Cũng may ta mạng lớn",},
        [1410] = {20184304,"Lý Thôi","Quách Tị","Ngươi dám dụ dỗ thê tử của ta! Hôm nay không lấy được mạng ngươi, xem như ngươi may mắn.","Ngươi dám dụ dỗ thê tử của ta! Hôm nay không lấy được mạng ngươi, xem như ngươi may mắn.",},
        [1411] = {20184304,"Quách Tị","Lý Thôi","Ngươi tin lời phù thủy, suốt ngày nói nhảm. Ta dụ dỗ thê tử của ngươi? Thê tử ngươi vừa xấu vừa ngốc, ai thèm chứ!","Ngươi tin lời phù thủy, suốt ngày nói nhảm. Ta dụ dỗ thê tử của ngươi? Thê tử ngươi vừa xấu vừa ngốc, ai thèm chứ!",},
        [1412] = {20184304,"Lý Thôi","Quách Tị","Ngươi dám sỉ nhục ta? Đợi ta tậ hợp binh mã sẽ lấy thủ cấp của ngươi!","Ngươi dám sỉ nhục ta? Đợi ta tậ hợp binh mã sẽ lấy thủ cấp của ngươi!",},
        [1413] = {20184305,"Quách Tị","Lý Thôi","Sao? Trước đây cứ chống đối với ta, giờ lại nghĩ đến ta sao?","Sao? Trước đây cứ chống đối với ta, giờ lại nghĩ đến ta sao?",},
        [1414] = {20184305,"Lý Thôi","Quách Tị","Quách huynh, trên đời làm gì có kẻ địch vĩnh viễn, chỉ có lợi ích là quan trọng. Giờ chúng ta không đồng lòng, e rằng sẽ gặp nguy đấy.","Quách huynh, trên đời làm gì có kẻ địch vĩnh viễn, chỉ có lợi ích là quan trọng. Giờ chúng ta không đồng lòng, e rằng sẽ gặp nguy đấy.",},
        [1415] = {20184305,"Lý Thôi","Quách Tị","Dương Phụng, Đổng Thừa đã đưa Hiến Đế rời khỏi Trường An, đến chỗ Hoằng Nông. Nếu họ đến Sơn Đông, sau khi ổn định, chắc chắn sẽ kêu gọi chư hầu thảo phạt chúng ta, khi đó e rằng khó mà giữ mạng.","Dương Phụng, Đổng Thừa đã đưa Hiến Đế rời khỏi Trường An, đến chỗ Hoằng Nông. Nếu họ đến Sơn Đông, sau khi ổn định, chắc chắn sẽ kêu gọi chư hầu thảo phạt chúng ta, khi đó e rằng khó mà giữ mạng.",},
        [1416] = {20184305,"Quách Tị","Lý Thôi","Được, nay Trương Tế đóng quân ở Trường An, không thể xem thường. Hai ta thừa cơ liên kết đến Hoằng Nông diệt vua Hán, phân thiên hạ! Nhưng Lý Nhạc, Dương Phụng đông người, Lý huynh có diệu kế gì?","Được, nay Trương Tế đóng quân ở Trường An, không thể xem thường. Hai ta thừa cơ liên kết đến Hoằng Nông diệt vua Hán, phân thiên hạ! Nhưng Lý Nhạc, Dương Phụng đông người, Lý huynh có diệu kế gì?",},
        [1417] = {20184305,"Lý Thôi","Quách Tị","Ha ha! Kẻ già yếu cứ diệt, trai trẻ cường tráng thì sung quân, khi địch đến cho dân lên trước, sau lệnh quân lính bỏ lại đồ vật giữa đường để nhiễu loạn quân địch, khi đó chúng ta sẽ chiến thắng.","Ha ha! Kẻ già yếu cứ diệt, trai trẻ cường tráng thì sung quân, khi địch đến cho dân lên trước, sau lệnh quân lính bỏ lại đồ vật giữa đường để nhiễu loạn quân địch, khi đó chúng ta sẽ chiến thắng.",},
        [1418] = {20184305,"Quách Tị","Lý Thôi","Lý huynh dũng mãnh đa mưu, quả nhiên lợi hại!","Lý huynh dũng mãnh đa mưu, quả nhiên lợi hại!",},
        [1419] = {20184401,"Tiểu Kiều","Đại Kiều","Tỉ tỉ, tỉ tặng muội con thỏ của tỉ được không?","Tỉ tỉ, tỉ tặng muội con thỏ của tỉ được không?",},
        [1420] = {20184401,"Đại Kiều","Tiểu Kiều","Đây là do cha tặng cho ta, ta sao có thể tặng cho muội? Muội từ nhỏ đã thích tranh giành với ta, lần này ta sẽ không cho muội đâu.","Đây là do cha tặng cho ta, ta sao có thể tặng cho muội? Muội từ nhỏ đã thích tranh giành với ta, lần này ta sẽ không cho muội đâu.",},
        [1421] = {20184401,"Tiểu Kiều","Đại Kiều","Tỉ không cho, muội sẽ bỏ mặc tỉ.","Tỉ không cho, muội sẽ bỏ mặc tỉ.",},
        [1422] = {20184401,"Đại Kiều","Tiểu Kiều","Được thôi, vậy tỉ được yên tĩnh rồi...","Được thôi, vậy tỉ được yên tĩnh rồi...",},
        [1423] = {20184401,"Tiểu Kiều","Đại Kiều","Tỉ không cho, muội sẽ khóc cho tỉ xem...","Tỉ không cho, muội sẽ khóc cho tỉ xem...",},
        [1424] = {20184401,"Đại Kiều","Tiểu Kiều","Được rồi, tặng muội này, vừa khóc vừa cười, thật chẳng ra sao. Sau này lấy phu quân thì phải làm sao đây?","Được rồi, tặng muội này, vừa khóc vừa cười, thật chẳng ra sao. Sau này lấy phu quân thì phải làm sao đây?",},
        [1425] = {20184401,"Tiểu Kiều","Đại Kiều","(Cười tươi) Đa tạ tỉ tỉ, Tỉ là thương muội nhất! Tiểu Kiều sau này sẽ bảo vệ tỉ tỉ!","(Cười tươi) Đa tạ tỉ tỉ, Tỉ là thương muội nhất! Tiểu Kiều sau này sẽ bảo vệ tỉ tỉ!",},
        [1426] = {20184402,"Tiểu Kiều","Chu Du","Công Cẩn, từ khi tỉ phu đi khỏi, tỉ tỉ luôn cô đơn một mình, thật tội nghiệp.","Công Cẩn, từ khi tỉ phu đi khỏi, tỉ tỉ luôn cô đơn một mình, thật tội nghiệp.",},
        [1427] = {20184402,"Chu Du","Tiểu Kiều","Ngươi hãy thường xuyên cùng cô ấy trò chuyện, cũng may cô ấy vẫn còn một hảo muội muội.","Ngươi hãy thường xuyên cùng cô ấy trò chuyện, cũng may cô ấy vẫn còn một hảo muội muội.",},
        [1428] = {20184402,"Tiểu Kiều","Chu Du","Ta muốn đưa tỉ đến phủ chơi vai hôm, Công Cẩn thấy thế nào?","Ta muốn đưa tỉ đến phủ chơi vai hôm, Công Cẩn thấy thế nào?",},
        [1429] = {20184402,"Chu Du","Tiểu Kiều","Cũng được.","Cũng được.",},
        [1430] = {20184403,"Gia Cát Lượng","Tiểu Kiều","Lập song đài hai bên trái phải, có ngọc long và kim phụng. Nhị kiều ở đông nam, vui vẻ ngày đêm.","Lập song đài hai bên trái phải, có ngọc long và kim phụng. Nhị kiều ở đông nam, vui vẻ ngày đêm.",},
        [1431] = {20184403,"Tiểu Kiều","Gia Cát Lượng","Gia Cát tiên sinh đang ngâm Đồng Tước Đài Phú của Tào Thực.","Gia Cát tiên sinh đang ngâm Đồng Tước Đài Phú của Tào Thực.",},
        [1432] = {20184403,"Gia Cát Lượng","Tiểu Kiều","Đúng vậy, không ngờ Tiểu Kiều phu nhân cũn biết bài phú này.","Đúng vậy, không ngờ Tiểu Kiều phu nhân cũn biết bài phú này.",},
        [1433] = {20184403,"Tiểu Kiều","Gia Cát Lượng","Nếu Tiểu Kiều nhớ không lầm, nguyên văn phải là Nhị kiều ở đông tây, nhược trường không chi đế đông, tiên sinh làm vậy là muốn khích Chu Lang?","Nếu Tiểu Kiều nhớ không lầm, nguyên văn phải là Nhị kiều ở đông tây, nhược trường không chi đế đông, tiên sinh làm vậy là muốn khích Chu Lang?",},
        [1434] = {20184403,"Gia Cát Lượng","Tiểu Kiều","Không ngờ Tiểu Kiều phu nhân lại thông minh như vậy, Khổng Minh bái phục.","Không ngờ Tiểu Kiều phu nhân lại thông minh như vậy, Khổng Minh bái phục.",},
        [1435] = {20184403,"Tiểu Kiều","Gia Cát Lượng","Đại quân Tào Tháo nam hạ, vùng đất Kinh Châu, Giang Đông vốn có quan hệ mật thiết, nếu không liên kết, sớm muộn gì cũng sẽ bị Tào Tháo nuốt chửng.","Đại quân Tào Tháo nam hạ, vùng đất Kinh Châu, Giang Đông vốn có quan hệ mật thiết, nếu không liên kết, sớm muộn gì cũng sẽ bị Tào Tháo nuốt chửng.",},
        [1436] = {20184403,"Tiểu Kiều","Gia Cát Lượng","Tiểu Kiều tuy là phận nữ nhi, cũng biết đạo lý tình thế nguy cấp, phải cùng kháng Tào Tháo. Gia Cát tiên sinh, Tiểu Kiều sẽ khuyên Chu Lang trợ giúp cho người.","Tiểu Kiều tuy là phận nữ nhi, cũng biết đạo lý tình thế nguy cấp, phải cùng kháng Tào Tháo. Gia Cát tiên sinh, Tiểu Kiều sẽ khuyên Chu Lang trợ giúp cho người.",},
        [1437] = {20184404,"Tiểu Kiều","Chu Du","Công Cẩn sao lại buồn bã thế này?","Công Cẩn sao lại buồn bã thế này?",},
        [1438] = {20184404,"Chu Du","Tiểu Kiều","Gần đây quân vụ rất bận.","Gần đây quân vụ rất bận.",},
        [1439] = {20184404,"Tiểu Kiều","Chu Du","Công Cẩn, hay là nghỉ ngơi chút, để Tiểu Kiều múa một khúc cho ngài xem.","Công Cẩn, hay là nghỉ ngơi chút, để Tiểu Kiều múa một khúc cho ngài xem.",},
        [1440] = {20184404,"Chu Du","Tiểu Kiều","Tiểu Kiều ngày thường múa rất thướt tha, sao hôm nay lại múa bài Đại Phong Ca?","Tiểu Kiều ngày thường múa rất thướt tha, sao hôm nay lại múa bài Đại Phong Ca?",},
        [1441] = {20184404,"Tiểu Kiều","Chu Du","Đại Phong Ca do Hán Cao Tổ mà ra, năm đó Lưu Bang và Hạng Vũ quyết chiến nhiều lần bị đánh bại ở phía bắc, sau khi liên kết các thế lực khác đã phản bại thành thắng, lập nên nhà Hán.","Đại Phong Ca do Hán Cao Tổ mà ra, năm đó Lưu Bang và Hạng Vũ quyết chiến nhiều lần bị đánh bại ở phía bắc, sau khi liên kết các thế lực khác đã phản bại thành thắng, lập nên nhà Hán.",},
        [1442] = {20184404,"Chu Du","Tiểu Kiều","Ngươi muốn khuyên ta liên kết Lưu Bị kháng Thào. Ngươi yên tâm, ta đã sớm báo với chúa công, cùng Khổng Minh bàn định kế sách chống Tào Tháo.","Ngươi muốn khuyên ta liên kết Lưu Bị kháng Thào. Ngươi yên tâm, ta đã sớm báo với chúa công, cùng Khổng Minh bàn định kế sách chống Tào Tháo.",},
        [1443] = {20184404,"Tiểu Kiều","Chu Du","Chu lang quả cơ trí, Tiểu Kiều quả thật đang múa rìu qua mắt thợ!","Chu lang quả cơ trí, Tiểu Kiều quả thật đang múa rìu qua mắt thợ!",},
        [1444] = {20184501,"Tôn Sách","Vương Lãng","Ta đến anh định Triết Giang, sao ngươi lại giúp cho tặc tử?","Ta đến anh định Triết Giang, sao ngươi lại giúp cho tặc tử?",},
        [1445] = {20184501,"Vương Lãng","Tôn Sách","Ngươi đã lấy Ngô Quận, lại muốn phạm ranh giới của ta! Hôm nay cùng Nghiêm Thị báo thù!","Ngươi đã lấy Ngô Quận, lại muốn phạm ranh giới của ta! Hôm nay cùng Nghiêm Thị báo thù!",},
        [1446] = {20184501,"Tôn Sách","Vương Lãng","Lão Cảnh Hưng này khẩu xuất cuồng ngôn!","Lão Cảnh Hưng này khẩu xuất cuồng ngôn!",},
        [1447] = {20184501,"Tôn Sách","Vương Lãng","Thái Sử Từ xuất trận, thay ta lấy thủ cấp hắn.","Thái Sử Từ xuất trận, thay ta lấy thủ cấp hắn.",},
        [1448] = {20184502,"Tào Tháo","Vương Lãng","Không thể học ngươi năm xưa gặp họa ở Cối Kê","Không thể học ngươi năm xưa gặp họa ở Cối Kê",},
        [1449] = {20184502,"Vương Lãng","Tào Tháo","Ngôn hành cử chỉ phải làm cho thật quả là khó.","Ngôn hành cử chỉ phải làm cho thật quả là khó.",},
        [1450] = {20184502,"Tào Tháo","Vương Lãng","Tại sao lại nói thế?","Tại sao lại nói thế?",},
        [1451] = {20184502,"Vương Lãng","Tào Tháo","Năm xưa ta là Hán Sứ nhưng không thể an dân, nên phải giản lược tối đa.","Năm xưa ta là Hán Sứ nhưng không thể an dân, nên phải giản lược tối đa.",},
        [1452] = {20184502,"Vương Lãng","Tào Tháo","Còn ngươi thì lẽ ra phải hiếu khách, có sự độ lượng chiêu nhân tài, không ngờ lại đối xử với khách như vậy.","Còn ngươi thì lẽ ra phải hiếu khách, có sự độ lượng chiêu nhân tài, không ngờ lại đối xử với khách như vậy.",},
        [1453] = {20184502,"Vương Lãng","Tào Tháo","Đây chính là có thể chiết mà không chiết.","Đây chính là có thể chiết mà không chiết.",},
        [1454] = {20184502,"Tào Tháo","Vương Lãng","Ha ha ha, quả có tài hùng biện.","Ha ha ha, quả có tài hùng biện.",},
        [1455] = {20184502,"Tào Tháo","Vương Lãng","Không uổng công ta tiến cử ngươi làm Can Gián Đại Phu, Tham Ty Không Quân Sự.","Không uổng công ta tiến cử ngươi làm Can Gián Đại Phu, Tham Ty Không Quân Sự.",},
        [1456] = {20184503,"Vương Lãng","","Viên Thiệu, Tôn Sách, Lưu Bị, đều là bọn tặc khấu, ta mới chính là kẻ thống nhất chính thống.","Viên Thiệu, Tôn Sách, Lưu Bị, đều là bọn tặc khấu, ta mới chính là kẻ thống nhất chính thống.",},
        [1457] = {20184503,"Vương Lãng","","Ta đảm nhiệm chức vụ Ngự Sử, Tư Không, Tư Đồ, viết sách Chu Dịch, Xuân Thu, Hiếu Kinh, tổng 34 quyển.","Ta đảm nhiệm chức vụ Ngự Sử, Tư Không, Tư Đồ, viết sách Chu Dịch, Xuân Thu, Hiếu Kinh, tổng 34 quyển.",},
        [1458] = {20184503,"Vương Lãng","","Cháu gái ta Vương Nguyên Cơ gả cho con thứ Tư Mã Ý là Tư Mã Chiêu, sinh ra Tây Tấn Võ Đế Tư Mã Viêm.","Cháu gái ta Vương Nguyên Cơ gả cho con thứ Tư Mã Ý là Tư Mã Chiêu, sinh ra Tây Tấn Võ Đế Tư Mã Viêm.",},
        [1459] = {20184503,"Vương Lãng","","Ta không phải lão tặc, cũng không hèn nhát sống một đời.","Ta không phải lão tặc, cũng không hèn nhát sống một đời.",},
        [1460] = {20184601,"Tào Tháo","Trương Nhượng","Hoạn quan Trương Nhượng, ta phải thay trời trừ hại, mau nộp mạng!","Hoạn quan Trương Nhượng, ta phải thay trời trừ hại, mau nộp mạng!",},
        [1461] = {20184601,"Trương Nhượng","Tào Tháo","Người đâu! Mau đến đây! Tào Tháo đang đánh từ Đình Đường đến đây!","Người đâu! Mau đến đây! Tào Tháo đang đánh từ Đình Đường đến đây!",},
        [1462] = {20184601,"Tào Tháo","Trương Nhượng","Chạy đâu cho thoát!","Chạy đâu cho thoát!",},
        [1463] = {20184602,"Trương Nhượng","Tào Tháo","Mau bao vây lại, đánh cho ta!","Mau bao vây lại, đánh cho ta!",},
        [1464] = {20184602,"Trương Nhượng","Tào Tháo","Tào Tháo muốn nhảy tường, đừng để hắn thoát.","Tào Tháo muốn nhảy tường, đừng để hắn thoát.",},
        [1465] = {20184602,"Tào Tháo","Trương Nhượng","Lần này ngươi mạng lớn!","Lần này ngươi mạng lớn!",},
        [1466] = {20184602,"Trương Nhượng","Tào Tháo","Đuổi theo mau!","Đuổi theo mau!",},
        [1467] = {20184603,"Trương Nhượng","Hà Thái Hậu","Lúc đầu lập mưu hại Đại Tướng Quân chỉ có một mình Tái Thạc, không liên quan đến chúng thần.","Lúc đầu lập mưu hại Đại Tướng Quân chỉ có một mình Tái Thạc, không liên quan đến chúng thần.",},
        [1468] = {20184603,"Trương Nhượng","Hà Thái Hậu","Đại Tướng Quân nghe lời Viên Thiệu, muốn diệt chúng thần, xin nương nương cứu giúp.","Đại Tướng Quân nghe lời Viên Thiệu, muốn diệt chúng thần, xin nương nương cứu giúp.",},
        [1469] = {20184603,"Hà Thái Hậu","Trương Nhượng","Các ngươi đừng lo, ta sẽ giữ mạng các ngươi.","Các ngươi đừng lo, ta sẽ giữ mạng các ngươi.",},
        [1470] = {20184604,"Hà Thái Hậu","Hà Tiến","Ta và ngươi xuất thân hàn vi, nhờ Trương Nhượng mới có thể phú quý như hôm nay.","Ta và ngươi xuất thân hàn vi, nhờ Trương Nhượng mới có thể phú quý như hôm nay.",},
        [1471] = {20184604,"Hà Thái Hậu","Hà Tiến","Nay Tái Thạc bất nhân, ngươi nghe lời kẻ nào lại muốn diệt hoạn quan?","Nay Tái Thạc bất nhân, ngươi nghe lời kẻ nào lại muốn diệt hoạn quan?",},
        [1472] = {20184604,"Hà Tiến","Hà Thái Hậu","Tái Thạc muốn hại ta, phải diệt tộc. Những kẻ khác không cần phải liên lụy.","Tái Thạc muốn hại ta, phải diệt tộc. Những kẻ khác không cần phải liên lụy.",},
        [1473] = {20184604,"Viên Thiệu","Hà Tiến","Nếu không diệt cỏ tận gốc sẽ hại về sau.","Nếu không diệt cỏ tận gốc sẽ hại về sau.",},
        [1474] = {20184604,"Hà Tiến","Viên Thiệu","Ý ta đã quyết, đừng nói nữa.","Ý ta đã quyết, đừng nói nữa.",},
        [1475] = {20184605,"Đổng Thái Hậu","Trương Nhượng","Em gái Hà Tiến lúc đầu do ta tiến cử. Nay con trai bà ta lên ngôi vua, thần tử đều là tâm phúc, uy quyền quá nặng, ta phải làm sao?","Em gái Hà Tiến lúc đầu do ta tiến cử. Nay con trai bà ta lên ngôi vua, thần tử đều là tâm phúc, uy quyền quá nặng, ta phải làm sao?",},
        [1476] = {20184605,"Trương Nhượng","Đổng Thái Hậu","Nương nương có thể buông rèm thính chính, phong hoàng tử Hiệp làm vương, ban chức quan lớn cho quốc cữu, nắm lấy binh quyền, trọng dụng chúng thần.","Nương nương có thể buông rèm thính chính, phong hoàng tử Hiệp làm vương, ban chức quan lớn cho quốc cữu, nắm lấy binh quyền, trọng dụng chúng thần.",},
        [1477] = {20184605,"Đổng Thái Hậu","Trương Nhượng","Được, ta sẽ phong hoàng tử Hiệp làm Trần Lưu Vương, Đổng Trọng làm Phiêu Kỵ Tướng, Trương Nhượng cùng tham dự triều chính.","Được, ta sẽ phong hoàng tử Hiệp làm Trần Lưu Vương, Đổng Trọng làm Phiêu Kỵ Tướng, Trương Nhượng cùng tham dự triều chính.",},
        [1478] = {20184701,"Tào Tháo","Trâu Thị","Ngươi là ai, sao lại xinh đẹp thế?","Ngươi là ai, sao lại xinh đẹp thế?",},
        [1479] = {20184701,"Trâu Thị","Tào Tháo","Thiếp là nương tử Trương Tế tên Trâu Thị.","Thiếp là nương tử Trương Tế tên Trâu Thị.",},
        [1480] = {20184701,"Tào Tháo","Trâu Thị","Phu nhân có biết ta không?","Phu nhân có biết ta không?",},
        [1481] = {20184701,"Trâu Thị","Tào Tháo","Nghe danh thừa tướng đã lâu, hôm nay may mắn được gặp.","Nghe danh thừa tướng đã lâu, hôm nay may mắn được gặp.",},
        [1482] = {20184701,"Tào Tháo","Trâu Thị","Ta vì phu nhân mà nạp Trương Tú đầu hàng, giúp hạn không bị diệt tộc.","Ta vì phu nhân mà nạp Trương Tú đầu hàng, giúp hạn không bị diệt tộc.",},
        [1483] = {20184701,"Trâu Thị","Tào Tháo","Thực đa tạ ngài.","Thực đa tạ ngài.",},
        [1484] = {20184701,"Tào Tháo","Trâu Thị","Hôm nay được gặp phu nhân quả vinh hạnh. Nàng có muốn cùng ta về kinh đô, cùng hưởng phú quý?","Hôm nay được gặp phu nhân quả vinh hạnh. Nàng có muốn cùng ta về kinh đô, cùng hưởng phú quý?",},
        [1485] = {20184701,"Trâu Thị","Tào Tháo","Tạ thừa tướng ân sủng.","Tạ thừa tướng ân sủng.",},
        [1486] = {20184702,"Trâu Thị","Tào Tháo","Ở lâu trong thành sẽ bị người ngoài nghị luận.","Ở lâu trong thành sẽ bị người ngoài nghị luận.",},
        [1487] = {20184702,"Tào Tháo","Trâu Thị","Ngày mai ra ngoài thành nghỉ ngơi, cùng phu nhân vào trong trại ở.","Ngày mai ra ngoài thành nghỉ ngơi, cùng phu nhân vào trong trại ở.",},
