        [1490] = {20184702,"Điển Vi","Tào Tháo","Là thừa tướng.","Là thừa tướng.",},
        [1491] = {20184801,"Lý Điển","Nhạc Tiến","Người đối diện là Tôn Quyền, bên trái là Tống Khiêm, phải là Giả Hoa, hai bên cầm Họa Kích.","Người đối diện là Tôn Quyền, bên trái là Tống Khiêm, phải là Giả Hoa, hai bên cầm Họa Kích.",},
