        [1493] = {20184801,"Nhạc Tiến","Lý Điển","Để ta xem Tôn Quyền có tài cán gì.","Để ta xem Tôn Quyền có tài cán gì.",},
        [1494] = {20184802,"Tôn Quyền","Thái Sử Từ","Lúc nãy ta và Trương Liêu giao chiến, có tướng Tào từ đâu đến lấy thủ cấp của ta.","Lúc nãy ta và Trương Liêu giao chiến, có tướng Tào từ đâu đến lấy thủ cấp của ta.",},
        [1495] = {20184802,"Tôn Quyền","Thái Sử Từ","May có Trọng Khiêm, Gia Hoa dùng kích đỡ lại, nếu không ta đã mất mạng","May có Trọng Khiêm, Gia Hoa dùng kích đỡ lại, nếu không ta đã mất mạng",},
        [1496] = {20184802,"Tôn Quyền","Thái Sử Từ","Kẻ này cưỡi ngựa, vung đao, Tống Khiêm Giả Hoa liền bị gãy kích.","Kẻ này cưỡi ngựa, vung đao, Tống Khiêm Giả Hoa liền bị gãy kích.",},
        [1497] = {20184802,"Tôn Quyền","Thái Sử Từ","Là kẻ nào?","Là kẻ nào?",},
        [1498] = {20184802,"Thái Sử Từ","Tôn Quyền","Chắc là Nhạc Tiến-Ngũ Hổ Tướng Tào Ngụy!","Chắc là Nhạc Tiến-Ngũ Hổ Tướng Tào Ngụy!",},
        [1499] = {20184803,"Tào Tháo","","Văn Khiêm tấn công Lữ Bố tại Bộc Dương, Trương Siêu tại Ung Khâu, Kiều Nhuy ở Khổ Huyện.","Văn Khiêm tấn công Lữ Bố tại Bộc Dương, Trương Siêu tại Ung Khâu, Kiều Nhuy ở Khổ Huyện.",},
        [1500] = {20184803,"Tào Tháo","","Kiến An năm 3, theo đánh Trương Tú ở An Tòng, vây Lữ Bố ở Hạ Biện, năm sau lại đánh Khôi Cố ở Xạ Khuyển, Lưu Bị ở Tiểu Bái.","Kiến An năm 3, theo đánh Trương Tú ở An Tòng, vây Lữ Bố ở Hạ Biện, năm sau lại đánh Khôi Cố ở Xạ Khuyển, Lưu Bị ở Tiểu Bái.",},
        [1501] = {20184803,"Tào Tháo","","Kiến An năm 5, cùng Vu Cấm vượt sông đánh Hoạch Gia, đánh Viên Thiệu tại Quan Độ, trảm bộ tướng Viên Thiệu là Thuần Vu Quỳnh!","Kiến An năm 5, cùng Vu Cấm vượt sông đánh Hoạch Gia, đánh Viên Thiệu tại Quan Độ, trảm bộ tướng Viên Thiệu là Thuần Vu Quỳnh!",},
        [1502] = {20184803,"Tào Tháo","","Kiến An năm 9, đánh con trai Viên Thiệu là Viên Đàm, anh em Viên Thượng tại Lê Dương, trảm đại tướng Nghiêm Kính!","Kiến An năm 9, đánh con trai Viên Thiệu là Viên Đàm, anh em Viên Thượng tại Lê Dương, trảm đại tướng Nghiêm Kính!",},
        [1503] = {20184803,"Tào Tháo","","Văn Khiêm theo ta nam chinh bắc chiến, trải qua hơn hai mươi chiến dịch nhưng chưa bại lần nào, quả là một mãnh tướng!","Văn Khiêm theo ta nam chinh bắc chiến, trải qua hơn hai mươi chiến dịch nhưng chưa bại lần nào, quả là một mãnh tướng!",},
        [1504] = {20184901,"Đổng Trác","Lý Nho","Sao ngươi đến đây?","Sao ngươi đến đây?",},
        [1505] = {20184901,"Lý Nho","Đổng Trác","Ta đến cửa phủ, biết thái sư giận đi vào hậu viện tìm Lữ Bố. Do gấp rút mà đến, nay Lữ Bố đã chạy, xin thái sư xử tội.","Ta đến cửa phủ, biết thái sư giận đi vào hậu viện tìm Lữ Bố. Do gấp rút mà đến, nay Lữ Bố đã chạy, xin thái sư xử tội.",},
        [1506] = {20184901,"Lý Nho","Đổng Trác","Ta định vào trong khuyên giải.","Ta định vào trong khuyên giải.",},
        [1507] = {20184901,"Đổng Trác","Lý Nho","Nó là nghịch tặc, phải diệt!","Nó là nghịch tặc, phải diệt!",},
        [1508] = {20184901,"Lý Nho","Đổng Trác","Xin ngài suy xét. Sở Trang Vương đều vì mỹ nhân mà bại, còn kẻ bỏ được mỹ nhân lại được cứu thoát nạn.","Xin ngài suy xét. Sở Trang Vương đều vì mỹ nhân mà bại, còn kẻ bỏ được mỹ nhân lại được cứu thoát nạn.",},
        [1509] = {20184901,"Lý Nho","Đổng Trác","Điêu Thuyền chẳng qua là một nữ tử, Lữ Bố là mãnh tướng của thái sư. Nếu thái sư tặng Điêu Thuyền cho Lữ Bố, Lữ Bố sẽ tận trung với ngài.","Điêu Thuyền chẳng qua là một nữ tử, Lữ Bố là mãnh tướng của thái sư. Nếu thái sư tặng Điêu Thuyền cho Lữ Bố, Lữ Bố sẽ tận trung với ngài.",},
        [1510] = {20184901,"Lý Nho","Đổng Trác","Thái sư xin nghĩ kỹ.","Thái sư xin nghĩ kỹ.",},
