        [1578] = {20185402,"Trương Phi","Triệu Vân","Em gái Mã Siêu đến thăm huynh trưởng, trên đường đã cứu vị hôn thê của Trương Bao.","Em gái Mã Siêu đến thăm huynh trưởng, trên đường đã cứu vị hôn thê của Trương Bao.",},
        [1579] = {20185402,"Trương Phi","Triệu Vân","Em gái Mã Siêu xinh đẹp giỏi võ, chưa kết hôn.","Em gái Mã Siêu xinh đẹp giỏi võ, chưa kết hôn.",},
        [1580] = {20185402,"Trương Phi","Triệu Vân","Tứ đệ cũng chưa có hôn sự, hay là giới thiệu cho đệ, được không.","Tứ đệ cũng chưa có hôn sự, hay là giới thiệu cho đệ, được không.",},
        [1581] = {20185402,"Triệu Vân","Trương Phi","Không được","Không được",},
        [1582] = {20185402,"Trương Phi","Triệu Vân","Có gì mà không được chứ, Triệu Tử Long giỏi vậy sao lại ngại ngùng như thế!","Có gì mà không được chứ, Triệu Tử Long giỏi vậy sao lại ngại ngùng như thế!",},
        [1583] = {20185501,"Viên Thiệu","Thư Thụ","Giờ gian thần nhũng nhiễu, nhà ta nhờ ơn của Hán Thất nên phen này quyết phải chấn hưng nhà Hán.","Giờ gian thần nhũng nhiễu, nhà ta nhờ ơn của Hán Thất nên phen này quyết phải chấn hưng nhà Hán.",},
        [1584] = {20185501,"Viên Thiệu","Thư Thụ","Tế Hoàn Công không có Quản Trọng sẽ khó thành đại nghiệp.","Tế Hoàn Công không có Quản Trọng sẽ khó thành đại nghiệp.",},
        [1585] = {20185501,"Viên Thiệu","Thư Thụ","Giờ ta muốn cùng ngươi đồng tâm hiệp lực mang lại sự bình yên cho bá tánh, ngươi có kế sách gì phò tá ta không?","Giờ ta muốn cùng ngươi đồng tâm hiệp lực mang lại sự bình yên cho bá tánh, ngươi có kế sách gì phò tá ta không?",},
        [1586] = {20185501,"Thư Thụ","Viên Thiệu","Tướng quân chưa đến 20 tuổi đã được triều đình trọng dụng, thanh danh vang khắp chốn.","Tướng quân chưa đến 20 tuổi đã được triều đình trọng dụng, thanh danh vang khắp chốn.",},
        [1587] = {20185501,"Thư Thụ","Viên Thiệu","Đổng Trác làm phản, tướng quân trung hiếu dũng mãnh đánh dẹp khiến Đổng Trác vô cùng hoảng sợ.","Đổng Trác làm phản, tướng quân trung hiếu dũng mãnh đánh dẹp khiến Đổng Trác vô cùng hoảng sợ.",},
        [1588] = {20185501,"Thư Thụ","Viên Thiệu","Ngươi dẫn quân tiến về phía bắc Hoàng Hà, bá tánh Bột Hải sẽ cúi đầu quy thuận. Có đội quân của một quận cùng với lực lượng quân binh ở Ký Châu nhất định sẽ khống chế được vùng đó.","Ngươi dẫn quân tiến về phía bắc Hoàng Hà, bá tánh Bột Hải sẽ cúi đầu quy thuận. Có đội quân của một quận cùng với lực lượng quân binh ở Ký Châu nhất định sẽ khống chế được vùng đó.",},
        [1589] = {20185501,"Thư Thụ","Viên Thiệu","Nếu như tiến quân về hướng đông thì có thể diệt Giặc Hoàng Cân, công đánh Hắc Sơn, diệt Trương Yến. Nếu cho quân tiến về hướng Bắc thì sẽ diệt Công Tôn Toản, bình định Hung Nô.","Nếu như tiến quân về hướng đông thì có thể diệt Giặc Hoàng Cân, công đánh Hắc Sơn, diệt Trương Yến. Nếu cho quân tiến về hướng Bắc thì sẽ diệt Công Tôn Toản, bình định Hung Nô.",},
        [1590] = {20185501,"Thư Thụ","Viên Thiệu","Tiến đánh về phía bắc sông Hoàng Hà, thống nhất bốn châu, triệu tập nhân tài cùng hàng vạn binh mã về Trường An nghênh đón bệ hạ.","Tiến đánh về phía bắc sông Hoàng Hà, thống nhất bốn châu, triệu tập nhân tài cùng hàng vạn binh mã về Trường An nghênh đón bệ hạ.",},
        [1591] = {20185501,"Thư Thụ","Viên Thiệu","Khôi phục chùa miếu ở Lạc Dương, ra lệnh chinh phạt những người không phục.","Khôi phục chùa miếu ở Lạc Dương, ra lệnh chinh phạt những người không phục.",},
        [1592] = {20185501,"Thư Thụ","Viên Thiệu","Dựa vào điều kiện như thế để phân thắng bại, ai có thể ngăn cản! Vài năm sau nữa, chuyện lập đại công không có gì khó cả.","Dựa vào điều kiện như thế để phân thắng bại, ai có thể ngăn cản! Vài năm sau nữa, chuyện lập đại công không có gì khó cả.",},
        [1593] = {20185502,"Thư Thụ","Viên Thiệu","Thiên tinh cho thấy sắp có họa tặc binh.","Thiên tinh cho thấy sắp có họa tặc binh.",},
        [1594] = {20185502,"Thư Thụ","Viên Thiệu","Ô Sào là kho lương thực nên phải canh phòng cẩn mật. Phái tinh binh, mãnh tướng tuần tiêu theo đường núi nhằm tránh trúng kế Tào Tháo.","Ô Sào là kho lương thực nên phải canh phòng cẩn mật. Phái tinh binh, mãnh tướng tuần tiêu theo đường núi nhằm tránh trúng kế Tào Tháo.",},
        [1595] = {20185502,"Viên Thiệu","Thư Thụ","Kẻ có tội sao dám lớn tiếng ở đây!","Kẻ có tội sao dám lớn tiếng ở đây!",},
        [1596] = {20185502,"Viên Thiệu","Thư Thụ","Người đâu, bắt y cho ta!","Người đâu, bắt y cho ta!",},
        [1597] = {20185502,"Thư Thụ","Viên Thiệu","Quân ta đại bại là chuyện sớm muộn, không biết thi hài ta sẽ trôi dạt về đâu!","Quân ta đại bại là chuyện sớm muộn, không biết thi hài ta sẽ trôi dạt về đâu!",},
        [1598] = {20185503,"Tào Tháo","Thư Thụ","Viên Thiệu đại bại bỏ chạy.","Viên Thiệu đại bại bỏ chạy.",},
        [1599] = {20185503,"Tào Tháo","Thư Thụ","Thư Thụ, ngươi có chịu đầu hàng không?","Thư Thụ, ngươi có chịu đầu hàng không?",},
        [1600] = {20185503,"Thư Thụ","Tào Tháo","Quyết không đầu hàng!","Quyết không đầu hàng!",},
        [1601] = {20185503,"Tào Tháo","Thư Thụ","Nếu thế lực quân ta hùng mạnh, vững chắc thì thiên hạ này chẳng có gì đáng phải lo.","Nếu thế lực quân ta hùng mạnh, vững chắc thì thiên hạ này chẳng có gì đáng phải lo.",},
        [1602] = {20185601,"Tào Nhân","Tào Hồng","Giờ Đổng Trác hoành hành, bá tánh lầm than.","Giờ Đổng Trác hoành hành, bá tánh lầm than.",},
        [1603] = {20185601,"Tào Nhân","Tào Hồng","Tào Tháo dựng cờ khởi nghĩa, không ngừng chiêu binh mãi mã.","Tào Tháo dựng cờ khởi nghĩa, không ngừng chiêu binh mãi mã.",},
        [1604] = {20185601,"Tào Nhân","Tào Hồng","Tử Khiêm, chúng ta hãy theo phò tá Tào Tháo đi.","Tử Khiêm, chúng ta hãy theo phò tá Tào Tháo đi.",},
        [1605] = {20185601,"Tào Hồng","Tào Nhân","Tử Hiếu, ta cũng đang định vậy.","Tử Hiếu, ta cũng đang định vậy.",},
        [1606] = {20185602,"Tào Nhân","Mãn Sủng","Vì không nghe lời khuyên can nên khi thất bại đã trảm hai tướng và mất Tương Dương, giờ phải làm sao đây?","Vì không nghe lời khuyên can nên khi thất bại đã trảm hai tướng và mất Tương Dương, giờ phải làm sao đây?",},
        [1607] = {20185602,"Mãn Sủng","Tào Nhân","Vân Trường là một mãnh tướng đa mưu túc trí, không được khinh địch.","Vân Trường là một mãnh tướng đa mưu túc trí, không được khinh địch.",},
        [1608] = {20185602,"Tào Nhân","Mãn Sủng","Mau phái người về Trường An báo tin: Vân Trường đã phá Tương Dương và đang bao vây Phàn Thành. Mong đại tướng đến cứu viện.","Mau phái người về Trường An báo tin: Vân Trường đã phá Tương Dương và đang bao vây Phàn Thành. Mong đại tướng đến cứu viện.",},
        [1609] = {20185602,"Tào Nhân","Mãn Sủng","Quyết tử giữ thành!","Quyết tử giữ thành!",},
        [1610] = {20185603,"Tào Nhân","Mãn Sủng","Phàn Thành bị bao vây, mực nước ngày càng dâng cao, tường thành sắp vỡ.","Phàn Thành bị bao vây, mực nước ngày càng dâng cao, tường thành sắp vỡ.",},
        [1611] = {20185603,"Tào Nhân","Mãn Sủng","Thành đang lâm nguy, không thể cứu vãn, sao không mau thoát thân khi địch còn chưa tiến vào thành. Tuy mất thành nhưng vẫn còn có thể thoát thân.","Thành đang lâm nguy, không thể cứu vãn, sao không mau thoát thân khi địch còn chưa tiến vào thành. Tuy mất thành nhưng vẫn còn có thể thoát thân.",},
        [1612] = {20185603,"Mãn Sủng","Tào Nhân","Không được. Quan Công tuy chưa đến thành nhưng đã sớm phái binh tướng bao vây chờ sẵn cả rồi.","Không được. Quan Công tuy chưa đến thành nhưng đã sớm phái binh tướng bao vây chờ sẵn cả rồi.",},
        [1613] = {20185603,"Mãn Sủng","Tào Nhân","Sở dĩ y không dám tấn công thành ngay là vì sợ quân ta tấn công từ phía sau. Nếu bây giờ bỏ thành chạy về phía nam Hoàng Hà thì đó lại không phải là vùng đất cai quản của phe ta.","Sở dĩ y không dám tấn công thành ngay là vì sợ quân ta tấn công từ phía sau. Nếu bây giờ bỏ thành chạy về phía nam Hoàng Hà thì đó lại không phải là vùng đất cai quản của phe ta.",},
        [1614] = {20185603,"Tào Nhân","Mãn Sủng","Nếu không nghe theo lời Bá Ninh thì đại sự bất thành rồi.","Nếu không nghe theo lời Bá Ninh thì đại sự bất thành rồi.",},
        [1615] = {20185603,"Tào Nhân","Mãn Sủng","Ta nhận lệnh Ngụy Vương bảo vệ thành, ai dám bỏ thành thoát thân, trảm!","Ta nhận lệnh Ngụy Vương bảo vệ thành, ai dám bỏ thành thoát thân, trảm!",},
        [1616] = {20185603,"Tào Nhân","Mãn Sủng","Các tướng sĩ tử thủ Phàn Thành, ngày đêm canh gác không dám lơ là.","Các tướng sĩ tử thủ Phàn Thành, ngày đêm canh gác không dám lơ là.",},
        [1617] = {20185604,"Tào Nhân","","Quan Vũ đã trúng tên và bị thương, sau đó bại dưới tay Từ Hoảng, phải rút quân về Mạch Thành.","Quan Vũ đã trúng tên và bị thương, sau đó bại dưới tay Từ Hoảng, phải rút quân về Mạch Thành.",},
        [1618] = {20185604,"Tào Nhân","","Tiếc là ta không thể đích thân tiến đánh Mạch Thành để bắt sống Quan Vũ!","Tiếc là ta không thể đích thân tiến đánh Mạch Thành để bắt sống Quan Vũ!",},
        [1619] = {20185604,"Tào Nhân","","Ngược lại đã để cho tên Lữ Mông kia chớp được thời cơ, thật là đáng tiếc.","Ngược lại đã để cho tên Lữ Mông kia chớp được thời cơ, thật là đáng tiếc.",},
        [1620] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Trai lớn lấy vợ, gái lớn gả chồng là chuyện bình thường xưa nay. Phàm là con cái phải nghe theo lời mẫu thân.","Trai lớn lấy vợ, gái lớn gả chồng là chuyện bình thường xưa nay. Phàm là con cái phải nghe theo lời mẫu thân.",},
        [1621] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Ngươi muốn Lưu Huyền Đức làm rể ta, sao còn dám giấu ta? Đó là con gái ta mà!","Ngươi muốn Lưu Huyền Đức làm rể ta, sao còn dám giấu ta? Đó là con gái ta mà!",},
        [1622] = {20185701,"Tôn Quyền","Ngô Quốc Thái","Sao người lại biết được tin đó ạ?","Sao người lại biết được tin đó ạ?",},
        [1623] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Nếu không muốn người khác biết thì tốt nhất đừng làm. Giờ tất cả mọi người đều biết hết nhưng ngươi lại giấu ta!","Nếu không muốn người khác biết thì tốt nhất đừng làm. Giờ tất cả mọi người đều biết hết nhưng ngươi lại giấu ta!",},
        [1624] = {20185701,"Tôn Quyền","Ngô Quốc Thái","Không phải vậy đâu. Đây là kế của Chu Du, vì muốn lấy Kinh Châu nên mới sắp đặt chuyện này. Chỉ cần dụ và bắt Lưu Bị thì nhất định Lưu Bị sẽ dùng Kinh Châu để đổi, lúc đó Kinh Châu sẽ thuộc về ta.","Không phải vậy đâu. Đây là kế của Chu Du, vì muốn lấy Kinh Châu nên mới sắp đặt chuyện này. Chỉ cần dụ và bắt Lưu Bị thì nhất định Lưu Bị sẽ dùng Kinh Châu để đổi, lúc đó Kinh Châu sẽ thuộc về ta.",},
        [1625] = {20185701,"Tôn Quyền","Ngô Quốc Thái","Nếu Lưu Bị không đồng ý dùng Kinh Châu để đổi thì sẽ trảm y. Như thế kế này cũng chỉ là hữu danh vô thực thôi.","Nếu Lưu Bị không đồng ý dùng Kinh Châu để đổi thì sẽ trảm y. Như thế kế này cũng chỉ là hữu danh vô thực thôi.",},
        [1626] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Ngươi là đô đốc của sáu quận và tám mươi châu nhưng giờ lại muốn dùng con gái ta để lấy Kinh Châu sao!","Ngươi là đô đốc của sáu quận và tám mươi châu nhưng giờ lại muốn dùng con gái ta để lấy Kinh Châu sao!",},
        [1627] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Nếu diệt Lưu Bị, con gái ta lại trở thành góa phụ, sau này còn gả cho ai khác được nữa? Như vậy chẳng phải là lỡ duyên con ta sao!","Nếu diệt Lưu Bị, con gái ta lại trở thành góa phụ, sau này còn gả cho ai khác được nữa? Như vậy chẳng phải là lỡ duyên con ta sao!",},
        [1628] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Nếu dùng kế này mà có được Kinh Châu, cũng sẽ bị thiên hạ cười chê. Chuyện này phải làm sao đây!","Nếu dùng kế này mà có được Kinh Châu, cũng sẽ bị thiên hạ cười chê. Chuyện này phải làm sao đây!",},
        [1629] = {20185701,"Ngô Quốc Thái","Tôn Quyền","Ngày mai hẹn gặp ở Cam Lộ Tự: Nếu như không hợp ý ta thì các ngươi muốn làm gì cũng được, ngược lại thì ta sẽ tự động gả con gái ta cho y!","Ngày mai hẹn gặp ở Cam Lộ Tự: Nếu như không hợp ý ta thì các ngươi muốn làm gì cũng được, ngược lại thì ta sẽ tự động gả con gái ta cho y!",},
        [1630] = {20185702,"Tôn Quyền","Chu Du","Mẫu thân ta muốn gả muội muội ta cho Lưu Bị. Không ngờ biến giả thành thật. Chuyện này làm sao đây?","Mẫu thân ta muốn gả muội muội ta cho Lưu Bị. Không ngờ biến giả thành thật. Chuyện này làm sao đây?",},
        [1631] = {20185702,"Chu Du","Tôn Quyền","Nếu người đã quyết như vậy thì Du tôi cũng không dám phản đối.","Nếu người đã quyết như vậy thì Du tôi cũng không dám phản đối.",},
        [1632] = {20185702,"Chu Du","Tôn Quyền","Lưu Bị là bậc kỳ tài, lại có  Quan Vũ, Trương Phi, Triệu Tử Long và Gia Cát Lượng đi theo phò tá. Y chắc chắn không phải là kẻ dễ khuất phục.","Lưu Bị là bậc kỳ tài, lại có  Quan Vũ, Trương Phi, Triệu Tử Long và Gia Cát Lượng đi theo phò tá. Y chắc chắn không phải là kẻ dễ khuất phục.",},
        [1633] = {20185702,"Chu Du","Tôn Quyền","Ta thấy chi bằng bắt y trước, sau đó giam lỏng ở trong cung. Ngày ngày cho y uống rượu và vui chơi để làm nhụt chí y.","Ta thấy chi bằng bắt y trước, sau đó giam lỏng ở trong cung. Ngày ngày cho y uống rượu và vui chơi để làm nhụt chí y.",},
        [1634] = {20185702,"Chu Du","Tôn Quyền","Cũng có thể dùng kế ly gián y với Quan Vũ, Trương Phi và Gia Cát Lượng, sau đó cho binh tiến đánh từng tên một, như vậy đại sự ắt thành.","Cũng có thể dùng kế ly gián y với Quan Vũ, Trương Phi và Gia Cát Lượng, sau đó cho binh tiến đánh từng tên một, như vậy đại sự ắt thành.",},
        [1635] = {20185702,"Chu Du","Tôn Quyền","Nếu giờ chúng ta cứ bỏ mặc chúng không màng, e là sau này khó mà đối phó. Mong minh công suy xét.","Nếu giờ chúng ta cứ bỏ mặc chúng không màng, e là sau này khó mà đối phó. Mong minh công suy xét.",},
        [1636] = {20185702,"Tôn Quyền","Chu Du","Kế của Công Cẩn rất hợp ý ta. Lưu Bị tuy là dòng dõi Hán Thất nhưng xưa nay chưa từng hưởng vinh quang phú quý gì.","Kế của Công Cẩn rất hợp ý ta. Lưu Bị tuy là dòng dõi Hán Thất nhưng xưa nay chưa từng hưởng vinh quang phú quý gì.",},
        [1637] = {20185702,"Tôn Quyền","Chu Du","Nếu bây giờ cho y hưởng vinh quang phú quý thì chắc chắn sẽ có thể ly gián y với Khổng Minh, Quan Vũ và Trương Phi.","Nếu bây giờ cho y hưởng vinh quang phú quý thì chắc chắn sẽ có thể ly gián y với Khổng Minh, Quan Vũ và Trương Phi.",},
        [1638] = {20185702,"Tôn Quyền","Chu Du","Khiến họ ganh ghét nhau, sau đó chúng ta sẽ cho quân đoạt lấy Kinh Châu.","Khiến họ ganh ghét nhau, sau đó chúng ta sẽ cho quân đoạt lấy Kinh Châu.",},
        [1639] = {20185703,"Thượng Hương","Lưu Bị","Nam tử hán đại trượng phu có gì mà buồn phiền vậy?","Nam tử hán đại trượng phu có gì mà buồn phiền vậy?",},
        [1640] = {20185703,"Lưu Bị","Thượng Hương","Ta tha hương nơi xứ sở quê người, không thể chăm sóc song thân, bái tế tổ tiên, quả thật là đại nghịch bất hiếu.","Ta tha hương nơi xứ sở quê người, không thể chăm sóc song thân, bái tế tổ tiên, quả thật là đại nghịch bất hiếu.",},
        [1641] = {20185703,"Lưu Bị","Thượng Hương","Năm mới lại gần kề, ta càng buồn phiền hơn.","Năm mới lại gần kề, ta càng buồn phiền hơn.",},
        [1642] = {20185703,"Thượng Hương","Lưu Bị","Người đừng giấu ta! Lúc nãy nghe Triệu Tử Long nói là Kinh Châu lâm nguy nên người muốn quay về chứ gì.","Người đừng giấu ta! Lúc nãy nghe Triệu Tử Long nói là Kinh Châu lâm nguy nên người muốn quay về chứ gì.",},
        [1643] = {20185703,"Lưu Bị","Thượng Hương","Nếu phu nhân đã biết thì ta cũng không giấu gì. Nếu ta không đi, lỡ như Kinh Châu có gì thì ta sẽ bị thiên hạ cười chê.","Nếu phu nhân đã biết thì ta cũng không giấu gì. Nếu ta không đi, lỡ như Kinh Châu có gì thì ta sẽ bị thiên hạ cười chê.",},
        [1644] = {20185703,"Lưu Bị","Thượng Hương","Ta muốn đi nhưng lại không nỡ để phu nhân ở lại đây nên mới buồn phiền đến thế.","Ta muốn đi nhưng lại không nỡ để phu nhân ở lại đây nên mới buồn phiền đến thế.",},
        [1645] = {20185703,"Thượng Hương","Lưu Bị","Thiếp đã gả cho người, người đi đâu thiếp theo đấy.","Thiếp đã gả cho người, người đi đâu thiếp theo đấy.",},
        [1646] = {20185703,"Lưu Bị","Thượng Hương","Tâm ý phu nhân tuy là như vậy, nhưng Quốc Thái và Ngô Hầu an sao chịu để phu nhân đi? Nếu phu nhân thương xót Lưu Bị thì tạm thời từ biệt đi.","Tâm ý phu nhân tuy là như vậy, nhưng Quốc Thái và Ngô Hầu an sao chịu để phu nhân đi? Nếu phu nhân thương xót Lưu Bị thì tạm thời từ biệt đi.",},
        [1647] = {20185703,"Thượng Hương","Lưu Bị","Người đừng buồn nữa. Thiếp sẽ đến nói với mẫu thân, mẫu thân nhất định sẽ cho chúng ta về Kinh Châu.","Người đừng buồn nữa. Thiếp sẽ đến nói với mẫu thân, mẫu thân nhất định sẽ cho chúng ta về Kinh Châu.",},
        [1648] = {20185703,"Lưu Bị","Thượng Hương","Quốc Thái đồng ý nhưng quần thần sẽ ngăn cản thôi.","Quốc Thái đồng ý nhưng quần thần sẽ ngăn cản thôi.",},
        [1649] = {20185703,"Thượng Hương","Lưu Bị","Hay là chúng ta mượn cớ đi bái tế tổ tiên sau đó âm thầm trốn đi, người thấy sao?","Hay là chúng ta mượn cớ đi bái tế tổ tiên sau đó âm thầm trốn đi, người thấy sao?",},
        [1650] = {20185703,"Lưu Bị","Thượng Hương","Vậy phải hết sức cẩn thận, đừng cho ai biết.","Vậy phải hết sức cẩn thận, đừng cho ai biết.",},
        [1651] = {20185704,"Triệu Vân","Thượng Hương","Sao tẩu phu nhân lại vội vàng trở về Đông Ngô vậy?","Sao tẩu phu nhân lại vội vàng trở về Đông Ngô vậy?",},
        [1652] = {20185704,"Thượng Hương","Triệu Vân","Vừa có thư nhà gửi đến nói là mẫu thân bệnh nặng nên ta phải về thăm ngay.","Vừa có thư nhà gửi đến nói là mẫu thân bệnh nặng nên ta phải về thăm ngay.",},
        [1653] = {20185704,"Triệu Vân","Thượng Hương","Về nhà thăm mẹ, sao tẩu phu nhân lại dẫn theo A Đẩu?","Về nhà thăm mẹ, sao tẩu phu nhân lại dẫn theo A Đẩu?",},
        [1654] = {20185704,"Trương Phi","Thượng Hương","Mong tẩu phu nhân đừng làm khó chúng tôi. Người hãy về thăm mẫu thân một mình và để A Đẩu lại đây.","Mong tẩu phu nhân đừng làm khó chúng tôi. Người hãy về thăm mẫu thân một mình và để A Đẩu lại đây.",},
        [1655] = {20185801,"Lưu Hồng","Lư Thực","Trương Giác đang dấy binh khởi nghĩa ở Ký Châu, hiện đã có tám châu ủng hộ, triều đình và bá tánh đang vô cùng hoang mang.","Trương Giác đang dấy binh khởi nghĩa ở Ký Châu, hiện đã có tám châu ủng hộ, triều đình và bá tánh đang vô cùng hoang mang.",},
        [1656] = {20185801,"Lưu Hồng","Lư Thực","Nghe nói Tử Can là người văn võ song toàn nên lệnh cho Tử Can làm Trung Lang Tướng.","Nghe nói Tử Can là người văn võ song toàn nên lệnh cho Tử Can làm Trung Lang Tướng.",},
        [1657] = {20185801,"Lưu Hồng","Lư Thực","Thống lĩnh Bắc Quân Ngũ Hiệu Tướng Sĩ đến Ký Châu dẹp Giặc Hoàng Cân.","Thống lĩnh Bắc Quân Ngũ Hiệu Tướng Sĩ đến Ký Châu dẹp Giặc Hoàng Cân.",},
        [1658] = {20185801,"Lư Thực","Lưu Hồng","Tử Can tuân lệnh!","Tử Can tuân lệnh!",},
        [1659] = {20185802,"Hà Tiến","Lư Thực","Đổng Trác dâng sớ nói sở dĩ thiên hạ đại loạn là do bọn người Trương Lương nhũng nhiễu.","Đổng Trác dâng sớ nói sở dĩ thiên hạ đại loạn là do bọn người Trương Lương nhũng nhiễu.",},
        [1660] = {20185802,"Hà Tiến","Lư Thực","Đổng Trác muốn dẫn binh vào Lạc Dương diệt Trương Nhượng trả lại sự yên bình cho bá tánh!","Đổng Trác muốn dẫn binh vào Lạc Dương diệt Trương Nhượng trả lại sự yên bình cho bá tánh!",},
        [1661] = {20185802,"Lư Thực","Hà Tiến","Đổng Trác là người lòng dạ nham hiểm. Một khi để y đạt được ý đồ thì đại họa khó tránh.","Đổng Trác là người lòng dạ nham hiểm. Một khi để y đạt được ý đồ thì đại họa khó tránh.",},
        [1662] = {20185802,"Lư Thực","Hà Tiến","Chi bằng hãy ngăn cản Đổng Trác để tránh đại họa.","Chi bằng hãy ngăn cản Đổng Trác để tránh đại họa.",},
        [1663] = {20185802,"Hà Tiến","Lư Thực","Ngươi tính tình đa nghi nên sẽ không thể mưu cầu đại sự.","Ngươi tính tình đa nghi nên sẽ không thể mưu cầu đại sự.",},
        [1664] = {20185901,"Tôn Sách","Hoa Đà","Sơn tặc tấn công Tuyên Thành, Chu Thái xả thân cứu Tôn Quyền.","Sơn tặc tấn công Tuyên Thành, Chu Thái xả thân cứu Tôn Quyền.",},
        [1665] = {20185901,"Tôn Sách","Hoa Đà","Trên đường chạy thoát, Chu Thái trọng thương, khó bảo toàn tính mạng.","Trên đường chạy thoát, Chu Thái trọng thương, khó bảo toàn tính mạng.",},
        [1666] = {20185901,"Tôn Sách","Hoa Đà","Cầu xin Nguyên Hóa cứu giúp.","Cầu xin Nguyên Hóa cứu giúp.",},
        [1667] = {20185901,"Hoa Đà","Tôn Sách","Cứu người là thiên chức của các thầy thuốc, xin phép cho ta xem bệnh.","Cứu người là thiên chức của các thầy thuốc, xin phép cho ta xem bệnh.",},
        [1668] = {20185901,"Hoa Đà","Tôn Sách","Bệnh tình cũng không phức tạp đâu, sẽ cứu được thôi.","Bệnh tình cũng không phức tạp đâu, sẽ cứu được thôi.",},
        [1669] = {20185902,"Tôn Quyền","Chu Thái","Chu Thái anh dũng đánh lui quân Tào Tháo, ta phong cho làm Bình Lỗ Tướng.","Chu Thái anh dũng đánh lui quân Tào Tháo, ta phong cho làm Bình Lỗ Tướng.",},
        [1670] = {20185902,"Tôn Quyền","Chu Thái","Trọng Mưu mời người một chén rượu.","Trọng Mưu mời người một chén rượu.",},
        [1671] = {20185902,"Chu Thái","Tôn Quyền","Đa tạ Trọng Mưu.","Đa tạ Trọng Mưu.",},
        [1672] = {20185902,"Tôn Quyền","Chu Thái","Uống xong chén rượu này, xin người hãy cởi bỏ xiêm y.","Uống xong chén rượu này, xin người hãy cởi bỏ xiêm y.",},
        [1673] = {20185902,"Chu Thái","Tôn Quyền","Cởi bỏ xiêm y sao?","Cởi bỏ xiêm y sao?",},
        [1674] = {20185902,"Tôn Quyền","Chu Thái","Nghe nói trong quân có người không phục Ấu Bình, hôm nay ta sẽ cho mọi người biết.","Nghe nói trong quân có người không phục Ấu Bình, hôm nay ta sẽ cho mọi người biết.",},
        [1675] = {20185902,"Tôn Quyền","Chu Thái","Các vết thương trên người Ấu Bình có lai lịch như thế nào!","Các vết thương trên người Ấu Bình có lai lịch như thế nào!",},
        [1676] = {20185902,"Tôn Quyền","Chu Thái","Làm sao mà ta không xem y như là cốt nhục của ta và ủy thác việc quân cho y chứ!","Làm sao mà ta không xem y như là cốt nhục của ta và ủy thác việc quân cho y chứ!",},
        [1677] = {20186001,"Tào Tháo","Tả Từ","Hôm nay quan khách tề tựu nơi đây, sơn hào hải vị có đủ.","Hôm nay quan khách tề tựu nơi đây, sơn hào hải vị có đủ.",},
        [1678] = {20186001,"Tào Tháo","Tả Từ","Chỉ thiếu món cá sạo ở Tùng Giang bên Ngô Quốc thôi.","Chỉ thiếu món cá sạo ở Tùng Giang bên Ngô Quốc thôi.",},
        [1679] = {20186001,"Tả Từ","Tào Tháo","Chuyện này rất dễ.","Chuyện này rất dễ.",},
        [1680] = {20186001,"Tả Từ","Tào Tháo","Hãy cho ta một chiếc chậu đồng, đổ đầy nước vào đó. Ta chỉ cần cho mồi vào cần câu và để vào trong chậu là được. (Một lúc sau liền câu được một con cá sạo.)","Hãy cho ta một chiếc chậu đồng, đổ đầy nước vào đó. Ta chỉ cần cho mồi vào cần câu và để vào trong chậu là được. (Một lúc sau liền câu được một con cá sạo.)",},
        [1681] = {20186001,"Tào Tháo","Tả Từ","Một con sao đủ cho các quan khách ăn, hai con mới đủ.","Một con sao đủ cho các quan khách ăn, hai con mới đủ.",},
        [1682] = {20186001,"Tả Từ","Tào Tháo","Chuyện này cũng có khó gì đâu. (Lại cho cần câu vào chậu, một lúc sau lại câu được một con nữa.)","Chuyện này cũng có khó gì đâu. (Lại cho cần câu vào chậu, một lúc sau lại câu được một con nữa.)",},
        [1683] = {20186001,"Tào Tháo","Tả Từ","Nguyên Phóng tiên sinh đạo thuật quả nhiên tinh thông.","Nguyên Phóng tiên sinh đạo thuật quả nhiên tinh thông.",},
        [1684] = {20186002,"Tả Từ","Lưu Biểu","Ta có ít quà muốn khao đội quân của người.","Ta có ít quà muốn khao đội quân của người.",},
        [1685] = {20186002,"Lưu Biểu","Tả Từ","Quân ta một vạn, quan khách một ngàn, có mình ngươi liệu có khao nổi không?","Quân ta một vạn, quan khách một ngàn, có mình ngươi liệu có khao nổi không?",},
        [1686] = {20186002,"Tả Từ","Lưu Biểu","Ta có một đấu rượu và một dây thịt khô.","Ta có một đấu rượu và một dây thịt khô.",},
        [1687] = {20186002,"Lưu Biểu","Tả Từ","Một đấu rượu và một dây thịt khô sao đủ dùng?","Một đấu rượu và một dây thịt khô sao đủ dùng?",},
        [1688] = {20186002,"Tả Từ","Lưu Biểu","Mỗi quân binh sẽ uống 3 chén rượu và ăn một miếng thịt khô. (Cứ theo đầu người mà chia.)","Mỗi quân binh sẽ uống 3 chén rượu và ăn một miếng thịt khô. (Cứ theo đầu người mà chia.)",},
        [1689] = {20186002,"Lưu Biểu","Tả Từ","Ngươi làm sao mà rượu trong vò không vơi đi chút nào, thịt cũng ăn không hết vậy?","Ngươi làm sao mà rượu trong vò không vơi đi chút nào, thịt cũng ăn không hết vậy?",},
        [1690] = {20186002,"Tả Từ","Lưu Biểu","Ha ha ha.","Ha ha ha.",},
        [1691] = {20186003,"Tôn Sách","Tả Từ","Tả Từ lại giở yêu thuật rồi!","Tả Từ lại giở yêu thuật rồi!",},
        [1692] = {20186003,"Tôn Sách","Tả Từ","Ta có thể trảm Vu Cát thì cũng có thể trảm ngươi!","Ta có thể trảm Vu Cát thì cũng có thể trảm ngươi!",},
        [1693] = {20186003,"Tả Từ","Tôn Sách","Ha ha ha. Ta sẽ đi chầm chậm thôi.","Ha ha ha. Ta sẽ đi chầm chậm thôi.",},
        [1694] = {20186003,"Tả Từ","Tôn Sách","Ngươi có thể đuổi kịp ta thì cứ trảm ta đi.","Ngươi có thể đuổi kịp ta thì cứ trảm ta đi.",},
        [1695] = {20186003,"Tôn Sách","Tả Từ","Ngươi đã dùng yêu đạo gì mà ta không thể đuổi kịp ngươi thế kia!","Ngươi đã dùng yêu đạo gì mà ta không thể đuổi kịp ngươi thế kia!",},
        [1696] = {20186101,"Viên Thiệu","Thẩm Phối","Đã đến lúc chọn người kế thừa, Viên Đàm hiếu sát, Viên Hi nhu nhược, Viên Thượng tuấn tú hơn, phải làm sao đây?","Đã đến lúc chọn người kế thừa, Viên Đàm hiếu sát, Viên Hi nhu nhược, Viên Thượng tuấn tú hơn, phải làm sao đây?",},
        [1697] = {20186101,"Thẩm Phối","Viên Thiệu","Tướng quân sao không để Viên Đàm làm con thừa tự của vong huynh? Sẽ không ai nói tướng quân phế con trưởng lập con út.","Tướng quân sao không để Viên Đàm làm con thừa tự của vong huynh? Sẽ không ai nói tướng quân phế con trưởng lập con út.",},
        [1698] = {20186101,"Viên Thiệu","Thẩm Phối","Nàng nói rất phải. Vậy thì phong Viên Đàm làm Đô Đốc, để hắn đi canh giữ Thanh Châu.","Nàng nói rất phải. Vậy thì phong Viên Đàm làm Đô Đốc, để hắn đi canh giữ Thanh Châu.",},
        [1699] = {20186102,"Viên Đàm","Thẩm Phối","Ta là con cả của phụ thân, con thừa tự dòng chính, các ngươi đã lén nói gì với phụ thân?","Ta là con cả của phụ thân, con thừa tự dòng chính, các ngươi đã lén nói gì với phụ thân?",},
        [1700] = {20186102,"Thẩm Phối","Viên Đàm","Tướng quân trước khi đi đã chính miệng nói với ta, muốn Viên Thượng kế thừa đại nghiệp.","Tướng quân trước khi đi đã chính miệng nói với ta, muốn Viên Thượng kế thừa đại nghiệp.",},
        [1701] = {20186102,"Viên Đàm","Thẩm Phối","Hừ! Đừng hòng lừa ta, rõ ràng là ngươi chia rẽ quan hệ cha con!","Hừ! Đừng hòng lừa ta, rõ ràng là ngươi chia rẽ quan hệ cha con!",},
        [1702] = {20186102,"Thẩm Phối","Viên Đàm","Ngươi không được tướng quân thích, trách ta gì chứ?","Ngươi không được tướng quân thích, trách ta gì chứ?",},
        [1703] = {20186102,"Viên Đàm","Thẩm Phối","Lẽ nào lại như vậy!","Lẽ nào lại như vậy!",},
        [1704] = {20186103,"Viên Đàm","Viên Thượng","Giáp phe ta không tốt, nên lần trước bị Tào Tháo đánh bại, hiện Tào quân đã lui, không còn lòng dạ chiến đấu.","Giáp phe ta không tốt, nên lần trước bị Tào Tháo đánh bại, hiện Tào quân đã lui, không còn lòng dạ chiến đấu.",},
        [1705] = {20186103,"Viên Đàm","Viên Thượng","Chúng ta nên tấn công bất ngờ, ta cần tăng Giáp và Lính.","Chúng ta nên tấn công bất ngờ, ta cần tăng Giáp và Lính.",},
        [1706] = {20186103,"Viên Thượng","Viên Đàm","Không được, ta không có, không cho được.","Không được, ta không có, không cho được.",},
        [1707] = {20186103,"Viên Đàm","Viên Thượng","Ngươi đừng quá đáng! Có tin là ta liên minh với Tào Tháo đánh ngươi không!","Ngươi đừng quá đáng! Có tin là ta liên minh với Tào Tháo đánh ngươi không!",},
        [1708] = {20186201,"Tào Thực","Trương Nhượng","Phiên nhược kinh hồng, uyển nhược du long, vinh diệu thu cúc, hoa mậu xuân tùng.","Phiên nhược kinh hồng, uyển nhược du long, vinh diệu thu cúc, hoa mậu xuân tùng.",},
        [1709] = {20186201,"Tào Thực","Tào Tháo","Phảng phất hề nhương khinh vân chi tế nguyệt, phiêu diêu hề nhược lưu phong chi hồi tuyết.","Phảng phất hề nhương khinh vân chi tế nguyệt, phiêu diêu hề nhược lưu phong chi hồi tuyết.",},
        [1710] = {20186201,"Chân Cơ","Trương Nhượng","A, đang nói ta đó sao?","A, đang nói ta đó sao?",},
        [1711] = {20186202,"Tào Phi","Chân Cơ","Đừng lo, ta là con cả Tào thừa tướng, xin bảo vệ gia đình nàng.","Đừng lo, ta là con cả Tào thừa tướng, xin bảo vệ gia đình nàng.",},
        [1712] = {20186202,"Chân Cơ","Tào Phi","Thiếp Chân thị, xin được nâng khăn sửa túi, hầu hạ thế tử.","Thiếp Chân thị, xin được nâng khăn sửa túi, hầu hạ thế tử.",},
        [1713] = {20186203,"Chân Cơ","","Chúng khẩu thước hoàng kim, sử quân sinh biệt ly. Niệm quân khứ ngã thời, độc sầu thường khổ bi.","Chúng khẩu thước hoàng kim, sử quân sinh biệt ly. Niệm quân khứ ngã thời, độc sầu thường khổ bi.",},
        [1714] = {20186203,"Chân Cơ","","Yêu nhau không hiểu nhau. Nếu có kiếp sau, ta không muốn gặp lại chàng nữa.","Yêu nhau không hiểu nhau. Nếu có kiếp sau, ta không muốn gặp lại chàng nữa.",},
        [1715] = {20186301,"Gia Cát Cẩn","Gia Cát Lượng","Lần này tới, một là chúc mừng Huyền Đức chiếm được đất Thục.","Lần này tới, một là chúc mừng Huyền Đức chiếm được đất Thục.",},
        [1716] = {20186301,"Gia Cát Lượng","Gia Cát Cẩn","Khách sáo, khách sáo","Khách sáo, khách sáo",},
        [1717] = {20186301,"Gia Cát Cẩn","Gia Cát Lượng","Hai là muốn hỏi, khi nào thì trả Kinh Châu?","Hai là muốn hỏi, khi nào thì trả Kinh Châu?",},
        [1718] = {20186301,"Gia Cát Lượng","Gia Cát Cẩn","Nói hay lắm, nói hay lắm","Nói hay lắm, nói hay lắm",},
        [1719] = {20186302,"Tôn Quyền","Gia Cát Khác","Trên cổ con lừa sao có treo bảng có chữ Gia Cát Tử Du?","Trên cổ con lừa sao có treo bảng có chữ Gia Cát Tử Du?",},
        [1720] = {20186302,"Gia Cát Khác","Tôn Quyền","Có lẽ thiếu đi hai chữ, để ta thêm lại lên.","Có lẽ thiếu đi hai chữ, để ta thêm lại lên.",},
        [1721] = {20186302,"Tôn Quyền","Gia Cát Khác","Ngươi viết gì thế? Lừa của... Gia Cát Tử Du, hay lắm.","Ngươi viết gì thế? Lừa của... Gia Cát Tử Du, hay lắm.",},
        [1722] = {20186302,"Gia Cát Khác","Tôn Quyền","Con lừa này là của ta! Ta phải dẫn đi!","Con lừa này là của ta! Ta phải dẫn đi!",},
        [1723] = {20186303,"Tôn Quyền","Gia Cát Cẩn","Phong Tử Du Tướng Soái, Tả Đô Hộ, quản lý Dự Châu, thế nào?","Phong Tử Du Tướng Soái, Tả Đô Hộ, quản lý Dự Châu, thế nào?",},
        [1724] = {20186303,"Gia Cát Cẩn","Tôn Quyền","Dự Châu hiện đang ở thuộc Ngụy, không trong tay của chúng ta.","Dự Châu hiện đang ở thuộc Ngụy, không trong tay của chúng ta.",},
        [1725] = {20186303,"Tôn Quyền","Gia Cát Cẩn","Vì vậy Tử Du hãy cố gắng giành lấy địa bàn này cho ta!","Vì vậy Tử Du hãy cố gắng giành lấy địa bàn này cho ta!",},
        [1726] = {20186401,"Hoàng Thừa Ngạn","Nguyệt Anh","Con gái ta Anh Nguyệt còn có tên A Kiều, rất thông minh, không biết con trai nhà ai có phúc cưới được con gái ta.","Con gái ta Anh Nguyệt còn có tên A Kiều, rất thông minh, không biết con trai nhà ai có phúc cưới được con gái ta.",},
        [1727] = {20186401,"Nguyệt Anh","Hoàng Thừa Ngạn","Nghe nói Gia Cát Khổng Minh học thức hơn người, ta rất ngưỡng mộ, mong phụ thân hãy giúp mai mối.","Nghe nói Gia Cát Khổng Minh học thức hơn người, ta rất ngưỡng mộ, mong phụ thân hãy giúp mai mối.",},
        [1728] = {20186401,"Nguyệt Anh","Hoàng Thừa Ngạn","Nhưng mong phụ thân hãy nói với hắn con xấu xí, xem phản ứng của hắn thế nào, như vậy chúng ta sẽ biết được nhân phẩm của hắn.","Nhưng mong phụ thân hãy nói với hắn con xấu xí, xem phản ứng của hắn thế nào, như vậy chúng ta sẽ biết được nhân phẩm của hắn.",},
        [1729] = {20186401,"Hoàng Thừa Ngạn","Nguyệt Anh","Kế hay, Khổng Minh nếu là người háo sắc, chúng ta sẽ không tiến hành hôn sự này.","Kế hay, Khổng Minh nếu là người háo sắc, chúng ta sẽ không tiến hành hôn sự này.",},
        [1730] = {20186402,"Gia Cát Lượng","Nguyệt Anh","Nguyệt Anh phát minh cơ quan Mộc Cẩu, Mộc Hổ, Mộc Nhân, quả thật khiến ta khâm phục.","Nguyệt Anh phát minh cơ quan Mộc Cẩu, Mộc Hổ, Mộc Nhân, quả thật khiến ta khâm phục.",},
        [1731] = {20186402,"Nguyệt Anh","Gia Cát Lượng","Ta có một câu đố, khi chúng ta thành thân, ta không ngồi kiệu, không cưỡi ngựa, không ngồi thuyền thì ngươi có cách gì.","Ta có một câu đố, khi chúng ta thành thân, ta không ngồi kiệu, không cưỡi ngựa, không ngồi thuyền thì ngươi có cách gì.",},
        [1732] = {20186402,"Gia Cát Lượng","Nguyệt Anh","Đợi ta suy nghĩ.","Đợi ta suy nghĩ.",},
        [1733] = {20186403,"Gia Cát Lượng","Nguyệt Anh","Cảm tạ Anh Nguyệt đã đưa ra câu đố khó, nhờ vậy ta phát minh ra Vận Chuyển, giải quyết vấn đề vận chuyển vật tư.","Cảm tạ Anh Nguyệt đã đưa ra câu đố khó, nhờ vậy ta phát minh ra Vận Chuyển, giải quyết vấn đề vận chuyển vật tư.",},
        [1734] = {20186403,"Nguyệt Anh","Gia Cát Lượng","Quá khen, có thể giúp được tiên sinh, ta cũng rất vui.","Quá khen, có thể giúp được tiên sinh, ta cũng rất vui.",},
        [1735] = {20186403,"Nguyệt Anh","Gia Cát Lượng","Tặng tiên sinh một chiếc quạt lông, giúp tiên sinh công thành, trị quốc an dân.","Tặng tiên sinh một chiếc quạt lông, giúp tiên sinh công thành, trị quốc an dân.",},
        [1736] = {20186501,"Phan Chương","","Phía trước Trương Liêu đột kích doanh trại quân ta, chúng ta không đánh lại mà bỏ chạy, sao lại thế này?","Phía trước Trương Liêu đột kích doanh trại quân ta, chúng ta không đánh lại mà bỏ chạy, sao lại thế này?",},
        [1737] = {20186501,"Phan Chương","","Hai lính bỏ chạy đã bị ta diệt, những ai không muốn mất mạng hãy theo ta!","Hai lính bỏ chạy đã bị ta diệt, những ai không muốn mất mạng hãy theo ta!",},
        [1738] = {20186501,"Tôn Quyền","Phan Chương","Phan Chương dũng mãnh, ta phong ngươi làm Thiên Tướng, đóng quân tại Bán Châu.","Phan Chương dũng mãnh, ta phong ngươi làm Thiên Tướng, đóng quân tại Bán Châu.",},
        [1739] = {20186502,"Quan Vũ","Phan Chương","Ngươi là ai? Sao dám cản đường ta, mau tránh ra, ta sẽ tha mạng cho ngươi.","Ngươi là ai? Sao dám cản đường ta, mau tránh ra, ta sẽ tha mạng cho ngươi.",},
        [1740] = {20186502,"Phan Chương","Quan Vũ","Quả nhiên là Quan Vân Trường, ngươi quá xem thường thực lực của Đông Ngô ta rồi.","Quả nhiên là Quan Vân Trường, ngươi quá xem thường thực lực của Đông Ngô ta rồi.",},
        [1741] = {20186502,"Quan Vũ","Phan Chương","Nếu không phải vì ta vừa chiến đấu xong, người ngựa đều đã mệt thì ngươi làm sao có tư cách nói chuyện với ta?","Nếu không phải vì ta vừa chiến đấu xong, người ngựa đều đã mệt thì ngươi làm sao có tư cách nói chuyện với ta?",},
        [1742] = {20186502,"Phan Chương","Quan Vũ","Không nói nhiều, hãy thử lợi hại bẫy của bọn ta!","Không nói nhiều, hãy thử lợi hại bẫy của bọn ta!",},
        [1743] = {20186601,"Tôn Sách","Tôn Quyền","Tạo thiên hạ ngươi không bằng ta, giữ đại nghiệp ta không bằng ngươi.","Tạo thiên hạ ngươi không bằng ta, giữ đại nghiệp ta không bằng ngươi.",},
        [1744] = {20186601,"Tôn Quyền","Tôn Sách","Có huynh trưởng ở đây, sao lại lo sợ không thành công? Ta nhất định sẽ giúp huynh trưởng.","Có huynh trưởng ở đây, sao lại lo sợ không thành công? Ta nhất định sẽ giúp huynh trưởng.",},
        [1745] = {20186601,"Tôn Sách","Tôn Quyền","Ngươi từ nhỏ thông minh, sau này các quan viên đều là thuộc hạ của ngươi.","Ngươi từ nhỏ thông minh, sau này các quan viên đều là thuộc hạ của ngươi.",},
        [1746] = {20186602,"Trương Chiêu","Gia Cát Lượng","Chủ nhân ta cảm thấy việc Tôn Lưu hợp sức cùng nhau chống Tào là rất tốt.","Chủ nhân ta cảm thấy việc Tôn Lưu hợp sức cùng nhau chống Tào là rất tốt.",},
        [1747] = {20186602,"Trương Chiêu","Gia Cát Lượng","Không biết tiên sinh có thể ở lại Giang Đông, cùng nhau tạo nghiệp lớn?","Không biết tiên sinh có thể ở lại Giang Đông, cùng nhau tạo nghiệp lớn?",},
        [1748] = {20186602,"Gia Cát Lượng","Trương Chiêu","Tôn tướng quân trọng dụng nhưng không thể khiến Gia Cát Lượng phát huy hết tài năng.","Tôn tướng quân trọng dụng nhưng không thể khiến Gia Cát Lượng phát huy hết tài năng.",},
        [1749] = {20186603,"Gia Cát Lượng","Tôn Quyền","Nếu quân lực của quý quốc đủ để chống lại quân Tào thì nên sớm cắt đứt giao hữu với quân Tào. Nếu không thể chống lại quân Tào, thì nên sớm đầu hàng.","Nếu quân lực của quý quốc đủ để chống lại quân Tào thì nên sớm cắt đứt giao hữu với quân Tào. Nếu không thể chống lại quân Tào, thì nên sớm đầu hàng.",},
        [1750] = {20186603,"Tôn Quyền","Gia Cát Lượng","Lưu Bị sao không đầu hàng Tào Tháo?","Lưu Bị sao không đầu hàng Tào Tháo?",},
        [1751] = {20186603,"Gia Cát Lượng","Tôn Quyền","Chủ của ta Lưu Bị là hậu duệ của Hán Thất, nhiều người khâm phục nên đã đầu quân, bọn ta sao có thể đầu hàng?","Chủ của ta Lưu Bị là hậu duệ của Hán Thất, nhiều người khâm phục nên đã đầu quân, bọn ta sao có thể đầu hàng?",},
        [1752] = {20186603,"Tôn Quyền","Gia Cát Lượng","Ta có lãnh thổ Giang Đông rộng lớn lại có hơn 10 vạn tinh binh, sao có thể mặc các ngươi muốn làm gì thì làm?","Ta có lãnh thổ Giang Đông rộng lớn lại có hơn 10 vạn tinh binh, sao có thể mặc các ngươi muốn làm gì thì làm?",},
        [1753] = {20186701,"Văn Xú","Nhan Lương","Bọn chúng luôn nhắc đến tên hai người bọn ta, nhưng không ai biết hai người bọn ta ai lợi hại hơn.","Bọn chúng luôn nhắc đến tên hai người bọn ta, nhưng không ai biết hai người bọn ta ai lợi hại hơn.",},
        [1754] = {20186701,"Nhan Lương","Văn Xú","Ta từng đánh bại Từ Hoảng!","Ta từng đánh bại Từ Hoảng!",},
        [1755] = {20186701,"Văn Xú","Nhan Lương","Ta từng đánh bại Trương Liêu! Ta từng đánh hòa với Triệu Vân!","Ta từng đánh bại Trương Liêu! Ta từng đánh hòa với Triệu Vân!",},
        [1756] = {20186701,"Nhan Lương","Văn Xú","Chi bằng hai huynh đệ hãy cùng nhau tỉ thí!","Chi bằng hai huynh đệ hãy cùng nhau tỉ thí!",},
        [1757] = {20186702,"Thư Thụ","Viên Thiệu","Chúa công, Nhan Lương thiện chiến nhưng nóng tính, không thể để hắn đơn độc tác chiến.","Chúa công, Nhan Lương thiện chiến nhưng nóng tính, không thể để hắn đơn độc tác chiến.",},
        [1758] = {20186702,"Viên Thiệu","Thư Thụ","Các ngươi thì biết gì? Nhan Lương là đại tướng, thực lực hơn hẳn các ngươi.","Các ngươi thì biết gì? Nhan Lương là đại tướng, thực lực hơn hẳn các ngươi.",},
        [1759] = {20186703,"Lưu Bị","Nhan Lương","Nhị đệ Quan Vân Trường, thân hình cao lớn, mặc chiến bào xanh, dùng đại đao Thanh Long. Nếu có gặp xin hãy nhắn lại.","Nhị đệ Quan Vân Trường, thân hình cao lớn, mặc chiến bào xanh, dùng đại đao Thanh Long. Nếu có gặp xin hãy nhắn lại.",},
        [1760] = {20186703,"Nhan Lương","Lưu Bị","Sao? Nếu vậy, gặp được hắn nhất định phải thử xem sao.","Sao? Nếu vậy, gặp được hắn nhất định phải thử xem sao.",},
        [1761] = {20186801,"Triệu Ngang","Vương Dị","Mã Siêu tăng cường tấn công, hiện trong thành thiếu lương thực, tình thế nguy cấp, Thứ Sử Vi Khang định đầu hàng Mã Siêu. Ta khuyên hắn không nên đầu hàng nhưng hắn không chịu nghe.","Mã Siêu tăng cường tấn công, hiện trong thành thiếu lương thực, tình thế nguy cấp, Thứ Sử Vi Khang định đầu hàng Mã Siêu. Ta khuyên hắn không nên đầu hàng nhưng hắn không chịu nghe.",},
        [1762] = {20186801,"Vương Dị","Triệu Ngang","Ai nói cứu viện Trung Nguyên nhất định không đến kịp? Chúng ta phải khích lệ tướng sĩ đồng lòng quyết hi sinh báo quốc, không thể đầu hàng phản tặc.","Ai nói cứu viện Trung Nguyên nhất định không đến kịp? Chúng ta phải khích lệ tướng sĩ đồng lòng quyết hi sinh báo quốc, không thể đầu hàng phản tặc.",},
        [1763] = {20186802,"Triệu Ngang","Vương Dị","Mã Siêu chiếm địa bàn của chúng ta, còn diệt Vi Khang, mối thù này phải trả!","Mã Siêu chiếm địa bàn của chúng ta, còn diệt Vi Khang, mối thù này phải trả!",},
        [1764] = {20186802,"Triệu Ngang","Vương Dị","Hiện Triệu Nguyệt đang bị bắt làm con tin trong tay hắn, nếu chúng ta gây chuyện chắc chắn hắn sẽ hạ thủ với Triệu Nguyệt, phải làm sao đây?","Hiện Triệu Nguyệt đang bị bắt làm con tin trong tay hắn, nếu chúng ta gây chuyện chắc chắn hắn sẽ hạ thủ với Triệu Nguyệt, phải làm sao đây?",},
        [1765] = {20186802,"Vương Dị","Triệu Ngang","Trung nghĩa là gốc con người, chúng ta nhất phải báo thù, dù có mất mạng cũng không từ.","Trung nghĩa là gốc con người, chúng ta nhất phải báo thù, dù có mất mạng cũng không từ.",},
        [1766] = {20186802,"Triệu Ngang","Vương Dị","Thê tử quả là nữ anh hùng!","Thê tử quả là nữ anh hùng!",},
        [1767] = {20186901,"Quan Định","Lưu Bị","Đây là con trai thứ hai của ta Quan Bình, từ nhỏ luyện võ, hiện muốn theo Quan tướng quân, không biết ý người như thế nào?","Đây là con trai thứ hai của ta Quan Bình, từ nhỏ luyện võ, hiện muốn theo Quan tướng quân, không biết ý người như thế nào?",},
        [1768] = {20186901,"Lưu Bị","Quan Định","Năm nay bao nhiêu tuổi?","Năm nay bao nhiêu tuổi?",},
        [1769] = {20186901,"Quan Định","Lưu Bị","18 tuổi.","18 tuổi.",},
        [1770] = {20186901,"Lưu Bị","Quan Định","Đệ đệ ta chưa có con, nay nhận Hiền Lang làm con, thế nào?","Đệ đệ ta chưa có con, nay nhận Hiền Lang làm con, thế nào?",},
        [1771] = {20186901,"Quan Định","Quan Bình","Quan Bình, mau đến bái kiến phụ thân và đại bá.","Quan Bình, mau đến bái kiến phụ thân và đại bá.",},
        [1772] = {20186902,"Quan Vũ","Quan Bình","Con hãy nghe cho rõ đây, chỉ sức mạnh và dũng khí là chưa đủ, phải dùng trí tuệ và mưu lược mới có thể thành đại sự.","Con hãy nghe cho rõ đây, chỉ sức mạnh và dũng khí là chưa đủ, phải dùng trí tuệ và mưu lược mới có thể thành đại sự.",},
        [1773] = {20186902,"Quan Bình","Quan Vũ","Phụ thân nói đúng, vì thế phụ thân đừng manh động diệt sứ giả Gia Cát Cẩn nước Ngô phái đến!","Phụ thân nói đúng, vì thế phụ thân đừng manh động diệt sứ giả Gia Cát Cẩn nước Ngô phái đến!",},
        [1774] = {20186902,"Quan Vũ","Quan Bình","Nể tình Khổng Minh, ta sẽ không diệt hắn ta.","Nể tình Khổng Minh, ta sẽ không diệt hắn ta.",},
        [1775] = {20187001,"Gia Cát Lượng","Mạnh Hoạch","Tiên đế đối xử không tệ với ngươi, tại sao làm phản?","Tiên đế đối xử không tệ với ngươi, tại sao làm phản?",},
        [1776] = {20187001,"Mạnh Hoạch","Gia Cát Lượng","Vùng đất Lưỡng Xuyên, bọn ta nhiều đời sinh sống tại đây, các người xâm phạm lãnh thổ của bọn ta, còn nói bọn ta tạo phản?","Vùng đất Lưỡng Xuyên, bọn ta nhiều đời sinh sống tại đây, các người xâm phạm lãnh thổ của bọn ta, còn nói bọn ta tạo phản?",},
        [1777] = {20187001,"Gia Cát Lượng","Mạnh Hoạch","Nhưng giờ ngươi đã bị ta bắt, ngươi có phục hay không?","Nhưng giờ ngươi đã bị ta bắt, ngươi có phục hay không?",},
        [1778] = {20187001,"Mạnh Hoạch","Gia Cát Lượng","Đường núi hiểm trở, ta bất cẩn trúng kế, sao có thể phục?","Đường núi hiểm trở, ta bất cẩn trúng kế, sao có thể phục?",},
        [1779] = {20187001,"Gia Cát Lượng","Mạnh Hoạch","Ngươi không phục, ta thả ngươi đi, thế nào?","Ngươi không phục, ta thả ngươi đi, thế nào?",},
        [1780] = {20187001,"Mạnh Hoạch","Gia Cát Lượng","Ngươi thả ta đi, ta chỉnh đốn quân đội, quay lại quyết đấu, nếu có thể bắt được ta, như thế ta mới phục.","Ngươi thả ta đi, ta chỉnh đốn quân đội, quay lại quyết đấu, nếu có thể bắt được ta, như thế ta mới phục.",},
        [1781] = {20187002,"Mạnh Hoạch","Gia Cát Lượng","Gia Cát Lượng, sao ngươi bắt phu nhân ta?","Gia Cát Lượng, sao ngươi bắt phu nhân ta?",},
        [1782] = {20187002,"Gia Cát Lượng","Mạnh Hoạch","Đừng tức giận, ta chỉ muốn dùng phu nhân để đổi hai vị tướng Trương Nghi và Mã Trung.","Đừng tức giận, ta chỉ muốn dùng phu nhân để đổi hai vị tướng Trương Nghi và Mã Trung.",},
        [1783] = {20187002,"Mạnh Hoạch","Gia Cát Lượng","Hừ, được, ta sẽ đổi với ngươi!","Hừ, được, ta sẽ đổi với ngươi!",},
        [1784] = {20187002,"Chúc Dung","Mạnh Hoạch","Bản thân đã bị bắt sáu lần, giờ còn hại ta bị bắt theo!","Bản thân đã bị bắt sáu lần, giờ còn hại ta bị bắt theo!",},
        [1785] = {20187003,"Gia Cát Lượng","Mạnh Hoạch","Ngươi đi tập hợp binh lính, chúng ta quyết đấu lại.","Ngươi đi tập hợp binh lính, chúng ta quyết đấu lại.",},
        [1786] = {20187003,"Mạnh Hoạch","Gia Cát Lượng","Hu hu hu, bắt bảy lần, thả bảy lần, xưa nay chưa từng có chuyện như vậy. Ta tuy là người vùng xa nhưng cũng biết lễ nghi liêm sỉ, đừng nghĩ rằng ta không biết gì cả.","Hu hu hu, bắt bảy lần, thả bảy lần, xưa nay chưa từng có chuyện như vậy. Ta tuy là người vùng xa nhưng cũng biết lễ nghi liêm sỉ, đừng nghĩ rằng ta không biết gì cả.",},
        [1787] = {20187003,"Chúc Dung","Mạnh Hoạch","Đừng khóc nữa!","Đừng khóc nữa!",},
        [1788] = {20187003,"Gia Cát Lượng","Mạnh Hoạch","Giờ ngươi phục chưa?","Giờ ngươi phục chưa?",},
        [1789] = {20187003,"Mạnh Hoạch","Gia Cát Lượng","Con cháu ta sẽ ghi nhớ ơn này. Ta sao có thể không phục chứ...","Con cháu ta sẽ ghi nhớ ơn này. Ta sao có thể không phục chứ...",},
        [1790] = {20187101,"Tào Tháo","Hán Hiến Đế","Đổng Trác tuy phò trợ bệ hạ lên ngôi, nhưng lại phạm thượng làm nhiều điều xấu, Đổng Trác tuy đã mất, nhưng ta sẽ tiếp tục phò trợ bệ hạ.","Đổng Trác tuy phò trợ bệ hạ lên ngôi, nhưng lại phạm thượng làm nhiều điều xấu, Đổng Trác tuy đã mất, nhưng ta sẽ tiếp tục phò trợ bệ hạ.",},
        [1791] = {20187101,"Tào Tháo","Hán Hiến Đế","Ta cho con gái gả cho người làm hoàng hậu, thế nào?","Ta cho con gái gả cho người làm hoàng hậu, thế nào?",},
        [1792] = {20187101,"Hán Hiến Đế","Tào Tháo","Cứ theo ý của thừa tướng.","Cứ theo ý của thừa tướng.",},
        [1793] = {20187102,"Hán Hiến Đế","Lưu Bị","Khanh là ai?","Khanh là ai?",},
        [1794] = {20187102,"Lưu Bị","Hán Hiến Đế","Thần là hậu duệ của Trung Sơn Tĩnh Vương, cháu cố của Hiếu Cảnh Hoàng Đế, cháu Lưu Hùng, con Lưu Hoằng.","Thần là hậu duệ của Trung Sơn Tĩnh Vương, cháu cố của Hiếu Cảnh Hoàng Đế, cháu Lưu Hùng, con Lưu Hoằng.",},
        [1795] = {20187102,"Hán Hiến Đế","Lưu Bị","Theo tông tộc thế phổ, Huyền Đức là hoàng thúc của trẫm.","Theo tông tộc thế phổ, Huyền Đức là hoàng thúc của trẫm.",},
        [1796] = {20187102,"Hán Hiến Đế","Lưu Bị","Tào Tháo thao túng triều đình, quốc sự trẫm đều không thể làm chủ, nay được hoàng thúc anh dũng, trẫm được cứu rồi!","Tào Tháo thao túng triều đình, quốc sự trẫm đều không thể làm chủ, nay được hoàng thúc anh dũng, trẫm được cứu rồi!",},
        [1797] = {20187103,"Hán Hiến Đế","","Trẫm từ khi lên ngôi, gian hùng nổi dậy, trước là Đổng Trác, sau là Tào Tháo lộng quyền.","Trẫm từ khi lên ngôi, gian hùng nổi dậy, trước là Đổng Trác, sau là Tào Tháo lộng quyền.",},
        [1798] = {20187103,"Hán Hiến Đế","","Chẳng lẽ không một ai có thể cứu quốc?","Chẳng lẽ không một ai có thể cứu quốc?",},
        [1799] = {20187103,"Hán Hiến Đế","","Trẫm đã viết huyết thư, cất giữ mang cho Đổng Thừa, hy vọng hắn ta có thể triệu tập nghĩa sĩ, để diệt trừ phản loạn!","Trẫm đã viết huyết thư, cất giữ mang cho Đổng Thừa, hy vọng hắn ta có thể triệu tập nghĩa sĩ, để diệt trừ phản loạn!",},
        [1800] = {20187201,"Hàn Đương","Tôn Kiên","Gió lớn thổi gãy cột cờ, là điềm không may, chúng ta hãy mau về!","Gió lớn thổi gãy cột cờ, là điềm không may, chúng ta hãy mau về!",},
        [1801] = {20187201,"Tôn Kiên","Hàn Đương","Ta liên tục chiến thắng sắp chiếm được Tương Dương, sao lại vì cột cờ bị thổi gãy mà quay về?","Ta liên tục chiến thắng sắp chiếm được Tương Dương, sao lại vì cột cờ bị thổi gãy mà quay về?",},
        [1802] = {20187201,"Hàn Đương","Tôn Kiên","Nhưng là điềm không may, cẩn thận vẫn tốt hơn!","Nhưng là điềm không may, cẩn thận vẫn tốt hơn!",},
        [1803] = {20187202,"Tôn Sách","Hàn Đương","Giang Đông được như hôm nay, đều nhờ công lao của Hàn Đương!","Giang Đông được như hôm nay, đều nhờ công lao của Hàn Đương!",},
        [1804] = {20187202,"Hàn Đương","Tôn Sách","Ban đầu ta theo Tôn Kiên đi chinh phạt, giờ đã cố gắng hết sức, giúp chúa công tạo dựng đại nghiệp nước Ngô!","Ban đầu ta theo Tôn Kiên đi chinh phạt, giờ đã cố gắng hết sức, giúp chúa công tạo dựng đại nghiệp nước Ngô!",},
        [1805] = {20187203,"Hàn Đương","","Có người lấy ta so sánh với Chu Thái và Lữ Mông, xem ai lợi hại đúng không?","Có người lấy ta so sánh với Chu Thái và Lữ Mông, xem ai lợi hại đúng không?",},
        [1806] = {20187203,"Hàn Đương","","Luận võ công chiến tích, Chu Thái lợi hại hơn, luận mưu lược thì Lữ Mông giỏi hơn.","Luận võ công chiến tích, Chu Thái lợi hại hơn, luận mưu lược thì Lữ Mông giỏi hơn.",},
        [1807] = {20187203,"Hàn Đương","","Luận năng lực, đương nhiên ta lợi hại!","Luận năng lực, đương nhiên ta lợi hại!",},
        [1808] = {20187301,"Tào Phi","Xuân Hoa","Ta ban Bách Linh Quân gả cho Tư Mã Ý, mong các ngươi đừng phụ lòng tốt của ta.","Ta ban Bách Linh Quân gả cho Tư Mã Ý, mong các ngươi đừng phụ lòng tốt của ta.",},
        [1809] = {20187301,"Xuân Hoa","Tư Mã Ý","Rõ ràng là Tào Phi phái đến giám sát chúng ta, sao có thể chấp nhận?","Rõ ràng là Tào Phi phái đến giám sát chúng ta, sao có thể chấp nhận?",},
        [1810] = {20187301,"Tư Mã Ý","Xuân Hoa","Phu nhân nói đúng, ta không chấp nhận, ta sẽ kháng chỉ, để bệ hạ ban tội tử.","Phu nhân nói đúng, ta không chấp nhận, ta sẽ kháng chỉ, để bệ hạ ban tội tử.",},
        [1811] = {20187301,"Xuân Hoa","Tư Mã Ý","Thật đáng ghét! Được rồi, ta chịu thua, cứ để cô ta đến đây!","Thật đáng ghét! Được rồi, ta chịu thua, cứ để cô ta đến đây!",},
        [1812] = {20187302,"Tư Mã Ý","Xuân Hoa","Phu nhân giúp ta sinh ba trai một gái, quả thật vất vả cho phu nhân.","Phu nhân giúp ta sinh ba trai một gái, quả thật vất vả cho phu nhân.",},
        [1813] = {20187302,"Xuân Hoa","Tư Mã Ý","Dù sao ngươi cũng đối xử không tốt với ta, có các con hiếu thuận với ta là được.","Dù sao ngươi cũng đối xử không tốt với ta, có các con hiếu thuận với ta là được.",},
        [1814] = {20187302,"Tư Mã Ý","Xuân Hoa","Phu nhân nói gì vậy, phu nhân là nữ trung hào kiệt, ta sao dám đối xử không tốt.","Phu nhân nói gì vậy, phu nhân là nữ trung hào kiệt, ta sao dám đối xử không tốt.",},
        [1815] = {20187302,"Xuân Hoa","Tư Mã Ý","Không dám là tốt!","Không dám là tốt!",},
        [1816] = {20187303,"Tư Mã Ý","Xuân Hoa","Phu nhân đừng giận mà bỏ cơm, không tốt cho sức khỏe. Ta xin lỗi phu nhân, mong phu nhân thứ lỗi cho ta.","Phu nhân đừng giận mà bỏ cơm, không tốt cho sức khỏe. Ta xin lỗi phu nhân, mong phu nhân thứ lỗi cho ta.",},
        [1817] = {20187303,"Xuân Hoa","Tư Mã Ý","Được, vậy ta ăn xong rồi giận sau.","Được, vậy ta ăn xong rồi giận sau.",},
        [1818] = {20187303,"Tư Mã Ý","Xuân Hoa","Thật ra ta không sợ vợ, chỉ là sợ hai đứa con đói bụng.","Thật ra ta không sợ vợ, chỉ là sợ hai đứa con đói bụng.",},
        [1819] = {20187401,"Cam Ninh","Lữ Mông","Tôn Giảo luôn gây chuyện với ta, ta bỏ mặc hắn ta, đi theo ngươi.","Tôn Giảo luôn gây chuyện với ta, ta bỏ mặc hắn ta, đi theo ngươi.",},
        [1820] = {20187401,"Lữ Mông","Cam Ninh","Không nên như thế, ngươi hãy nhường một bước, dẫu sao hắn cũng là em họ của chúa công.","Không nên như thế, ngươi hãy nhường một bước, dẫu sao hắn cũng là em họ của chúa công.",},
        [1821] = {20187401,"Cam Ninh","Lữ Mông","Ta sẽ ra sức báo đáp quận chúa, nhưng không đồng nghĩa sẽ làm chuyện sai trái.","Ta sẽ ra sức báo đáp quận chúa, nhưng không đồng nghĩa sẽ làm chuyện sai trái.",},
        [1822] = {20187401,"Lữ Mông","Cam Ninh","Ngươi vẫn cứng đầu như thế.","Ngươi vẫn cứng đầu như thế.",},
        [1823] = {20187402,"Tôn Quyền","Lữ Mông","Giờ ngươi đang nắm quyền lớn, phải học tập nhiều.","Giờ ngươi đang nắm quyền lớn, phải học tập nhiều.",},
        [1824] = {20187402,"Lữ Mông","Tôn Quyền","Sự vụ bận rộn, quả thật không có thời gian.","Sự vụ bận rộn, quả thật không có thời gian.",},
        [1825] = {20187402,"Tôn Quyền","Lữ Mông","Ta đâu phải nói ngươi nghiên cứu chuyên sâu, chỉ cần đọc sách hiểu về lịch sử thôi.","Ta đâu phải nói ngươi nghiên cứu chuyên sâu, chỉ cần đọc sách hiểu về lịch sử thôi.",},
        [1826] = {20187402,"Tôn Quyền","Lữ Mông","Ngươi nói bận, ngươi bận hơn ta sao? Ta thường đọc sách, thấy rất có ích.","Ngươi nói bận, ngươi bận hơn ta sao? Ta thường đọc sách, thấy rất có ích.",},
        [1827] = {20187402,"Lữ Mông","Tôn Quyền","Được, ta sẽ cố gắng học!","Được, ta sẽ cố gắng học!",},
        [1828] = {20187403,"Lữ Mông","Lỗ Túc","Người mang trọng trách, muốn tìm chiến lược nào để phòng chuyện bất trắc?","Người mang trọng trách, muốn tìm chiến lược nào để phòng chuyện bất trắc?",},
        [1829] = {20187403,"Lỗ Túc","Lữ Mông","Vậy... Tùy cơ ứng biến vậy.","Vậy... Tùy cơ ứng biến vậy.",},
        [1830] = {20187403,"Lữ Mông","Lỗ Túc","Giờ Tôn Lưu đã liên minh, Quan Vũ vẫn là hổ tướng cần đề phòng, sao có thể không nghĩ kế?","Giờ Tôn Lưu đã liên minh, Quan Vũ vẫn là hổ tướng cần đề phòng, sao có thể không nghĩ kế?",},
        [1831] = {20187403,"Lỗ Túc","Lữ Mông","Lữ Tử Minh, ta không ngờ ngươi tài giỏi đến thế. Ngươi đã không còn là Lữ Mông rồi!","Lữ Tử Minh, ta không ngờ ngươi tài giỏi đến thế. Ngươi đã không còn là Lữ Mông rồi!",},
        [1832] = {20187403,"Lữ Mông","Lỗ Túc","Nhiều ngày không gặp, ắt có tiến bộ, huynh trưởng biết chuyện này quá trễ rồi!","Nhiều ngày không gặp, ắt có tiến bộ, huynh trưởng biết chuyện này quá trễ rồi!",},
        [1833] = {20187501,"Mã Siêu","Bàng Đức","Ở đây có nhiều ngựa tốt để chọn, vì sao Bàng Đức lại chọn ngựa vàng vừa lùn vừa xấu?","Ở đây có nhiều ngựa tốt để chọn, vì sao Bàng Đức lại chọn ngựa vàng vừa lùn vừa xấu?",},
        [1834] = {20187501,"Bàng Đức","Mã Siêu","Tuy vẻ ngoài hơi xấu, nhưng xét về tốc độ, không con ngựa nào bằng nó!","Tuy vẻ ngoài hơi xấu, nhưng xét về tốc độ, không con ngựa nào bằng nó!",},
        [1835] = {20187501,"Mã Siêu","Bàng Đức","Bàng Đức đúng là có mắt nhìn ngựa.","Bàng Đức đúng là có mắt nhìn ngựa.",},
        [1836] = {20187502,"Tào Tháo","Giả Hủ","Bàng Đức quân địch là dũng tướng Tây Lương, chắc chắn có thể giúp ta hoàn thành đại sự!","Bàng Đức quân địch là dũng tướng Tây Lương, chắc chắn có thể giúp ta hoàn thành đại sự!",},
        [1837] = {20187502,"Giả Hủ","Tào Tháo","Ta có cách này.","Ta có cách này.",},
        [1838] = {20187502,"Tào Tháo","Bàng Đức","Bàng Đức Tướng Quân, ta cởi trói cho ngươi. Trương Lỗ nói nếu thua trận sẽ diệt ngươi, và ngươi đang bị ta bắt.","Bàng Đức Tướng Quân, ta cởi trói cho ngươi. Trương Lỗ nói nếu thua trận sẽ diệt ngươi, và ngươi đang bị ta bắt.",},
        [1839] = {20187502,"Tào Tháo","Bàng Đức","Chi bằng quy thuận ta, giúp ta hoàn thành đại nghiệp?","Chi bằng quy thuận ta, giúp ta hoàn thành đại nghiệp?",},
        [1840] = {20187502,"Bàng Đức","Tào Tháo","Chúng bất nhân trước, thì đừng trách ta! Ta đầu hàng! Nghe Tào Công sai khiến.","Chúng bất nhân trước, thì đừng trách ta! Ta đầu hàng! Nghe Tào Công sai khiến.",},
        [1841] = {20187503,"Quan Vũ","Bàng Đức","Ta muốn mời ngươi làm tướng quân, mau quy phục bọn ta.","Ta muốn mời ngươi làm tướng quân, mau quy phục bọn ta.",},
        [1842] = {20187503,"Bàng Đức","Quan Vũ","Ngụy Vương hùng binh trăm vạn, uy chấn thiên hạ. Lưu Bị đâu phải đối thủ của chúng ta!","Ngụy Vương hùng binh trăm vạn, uy chấn thiên hạ. Lưu Bị đâu phải đối thủ của chúng ta!",},
        [1843] = {20187503,"Bàng Đức","Quan Vũ","Ta thà làm ma cũng không làm tướng của tặc nhân!","Ta thà làm ma cũng không làm tướng của tặc nhân!",},
        [1844] = {20187601,"Tinh Thái","Ngân Bình","Ngân Bình tỷ tỷ, nghe nói cha ta đã đặt tên cho tỷ!","Ngân Bình tỷ tỷ, nghe nói cha ta đã đặt tên cho tỷ!",},
        [1845] = {20187601,"Ngân Bình","Tinh Thái","Đúng vậy, Tam thúc đã tặng ta châu báu, nghe nói lấy từ Tử Kim Quán của Lữ Bố.","Đúng vậy, Tam thúc đã tặng ta châu báu, nghe nói lấy từ Tử Kim Quán của Lữ Bố.",},
        [1846] = {20187601,"Tinh Thái","Ngân Bình","Nghe nói có thể mang lại may mắn! Cha đâu có tặng ta, thiên vị.","Nghe nói có thể mang lại may mắn! Cha đâu có tặng ta, thiên vị.",},
        [1847] = {20187602,"Tôn Quyền","Gia Cát Cẩn","Nghe nói Quan Vân Trường có một cô con gái, hay Tử Du thay Tôn Đăng cầu thân, thể hiện thành ý Tôn Lưu Liên Minh?","Nghe nói Quan Vân Trường có một cô con gái, hay Tử Du thay Tôn Đăng cầu thân, thể hiện thành ý Tôn Lưu Liên Minh?",},
        [1848] = {20187602,"Gia Cát Cẩn","Tôn Quyền","Nhưng Quan tướng quân không đồng ý... Chúng ta cùng Tào chiếm Kinh, chúa công thấy sao?","Nhưng Quan tướng quân không đồng ý... Chúng ta cùng Tào chiếm Kinh, chúa công thấy sao?",},
        [1849] = {20187603,"Gia Cát Cẩn","Quan Vũ","Chúa công ta có ý muốn thế tử Tôn Đăng lấy con gái tướng quân, tướng quân nghĩ sao?","Chúa công ta có ý muốn thế tử Tôn Đăng lấy con gái tướng quân, tướng quân nghĩ sao?",},
        [1850] = {20187603,"Quan Vũ","Gia Cát Cẩn","Hổ nữ xứng với khuyển tử sao?","Hổ nữ xứng với khuyển tử sao?",},
        [1851] = {20187701,"Bào Tam Nương","Quan Sách","Đa tạ đã giúp ta đánh ác bá.","Đa tạ đã giúp ta đánh ác bá.",},
        [1852] = {20187701,"Quan Sách","Bào Tam Nương","Cô nương khách sáo rồi.","Cô nương khách sáo rồi.",},
        [1853] = {20187701,"Bào Tam Nương","Quan Sách","Tiểu nữ nguyện dùng thân thể báo đáp ơn cứu mạng.","Tiểu nữ nguyện dùng thân thể báo đáp ơn cứu mạng.",},
        [1854] = {20187702,"Quan Sách","Quan Vũ","Cha, sau bao năm con đã tìm được cha!","Cha, sau bao năm con đã tìm được cha!",},
        [1855] = {20187702,"Quan Vũ","Quan Sách","Phiêu bạt nhiều năm như thế, con vất vả rồi!","Phiêu bạt nhiều năm như thế, con vất vả rồi!",},
        [1856] = {20187702,"Quan Sách","Quan Vũ","Con nguyện theo cha góp sức cho Thục Hán!","Con nguyện theo cha góp sức cho Thục Hán!",},
        [1857] = {20187703,"Quan Sách","Gia Cát Lượng","Con nguyện thay cha giúp Thục Hán dẹp yên thiên hạ.","Con nguyện thay cha giúp Thục Hán dẹp yên thiên hạ.",},
        [1858] = {20187703,"Quan Sách","Gia Cát Lượng","Thừa tướng, phu thê ta nguyện theo người diệt Dị Tộc, thủ Nam Trung.","Thừa tướng, phu thê ta nguyện theo người diệt Dị Tộc, thủ Nam Trung.",},
        [1859] = {20187703,"Gia Cát Lượng","Quan Sách","Hổ phụ sinh hổ tử, Thục Hán có hai cha con người giúp đỡ quả thật may mắn.","Hổ phụ sinh hổ tử, Thục Hán có hai cha con người giúp đỡ quả thật may mắn.",},
        [1860] = {20187801,"Lục Tốn","Tôn Quyền","Giờ anh hùng các phe tranh đấu, muốn thắng kẻ địch, chúng ta cần nhiều ngựa hơn.","Giờ anh hùng các phe tranh đấu, muốn thắng kẻ địch, chúng ta cần nhiều ngựa hơn.",},
        [1861] = {20187801,"Lục Tốn","Lục Tốn","Nội bộ đang có sơn tặc làm loạn, chúng ta nên mở rộng quân đội, chọn lính tinh nhuệ.","Nội bộ đang có sơn tặc làm loạn, chúng ta nên mở rộng quân đội, chọn lính tinh nhuệ.",},
        [1862] = {20187801,"Tôn Quyền","Lục Tốn","Ta rất thích ngươi, quyết định gả cháu gái cho ngươi.","Ta rất thích ngươi, quyết định gả cháu gái cho ngươi.",},
        [1863] = {20187802,"Lục Tốn","Lữ Mông","Nếu Quan Vũ hay tin người bị bệnh, chắc chắn sẽ lơ là. Chúng ta có thể bàn bạc kỹ.","Nếu Quan Vũ hay tin người bị bệnh, chắc chắn sẽ lơ là. Chúng ta có thể bàn bạc kỹ.",},
        [1864] = {20187802,"Lữ Mông","Lục Tốn","Quan Vũ dũng mãnh ai cũng biết, ngươi đừng chống đối hắn nữa.","Quan Vũ dũng mãnh ai cũng biết, ngươi đừng chống đối hắn nữa.",},
        [1865] = {20187802,"Tôn Quyền","Lữ Mông","Nên chọn một người để thay ngươi rồi, ngươi đã chọn được người chưa?","Nên chọn một người để thay ngươi rồi, ngươi đã chọn được người chưa?",},
        [1866] = {20187802,"Lữ Mông","Lục Tốn","Lục Tốn có thể gánh vác trọng trách. Hắn vẫn chưa có danh tiếng, Quan Vũ sẽ không kiêng nể hắn, hắn là lựa chọn tốt nhất.","Lục Tốn có thể gánh vác trọng trách. Hắn vẫn chưa có danh tiếng, Quan Vũ sẽ không kiêng nể hắn, hắn là lựa chọn tốt nhất.",},
        [1867] = {20187802,"Lữ Mông","Lục Tốn","Nếu muốn dùng hắn trước tiên phải giữ kín, sau đó chờ cơ hội xuất kích chắc chắn sẽ thành công.","Nếu muốn dùng hắn trước tiên phải giữ kín, sau đó chờ cơ hội xuất kích chắc chắn sẽ thành công.",},
        [1868] = {20187901,"Ngô Quốc Thái","Tôn Quyền","Ta không nhớ Lưu Hoàng Thúc, mai hẹn ở Cam Lộ Tự, nếu hợp ta sẽ gả con gái cho hắn, nếu không sẽ mặc các ngươi xử lý hắn.","Ta không nhớ Lưu Hoàng Thúc, mai hẹn ở Cam Lộ Tự, nếu hợp ta sẽ gả con gái cho hắn, nếu không sẽ mặc các ngươi xử lý hắn.",},
        [1869] = {20187901,"Tôn Quyền","Ngô Quốc Thái","Ta đã mai phục Đao Phủ Thủ, xem thái độ của người.","Ta đã mai phục Đao Phủ Thủ, xem thái độ của người.",},
        [1870] = {20187902,"Ngô Quốc Thái","Thượng Hương","Ừ... Gia thế và tính cách cũng tốt, ta thấy hôn sự này khá ổn.","Ừ... Gia thế và tính cách cũng tốt, ta thấy hôn sự này khá ổn.",},
        [1871] = {20187902,"Thượng Hương","Ngô Quốc Thái","Để người làm chủ.","Để người làm chủ.",},
        [1872] = {20187902,"Ngô Quốc Thái","Thượng Hương","Hy vọng Huyền Đức sẽ đối xử tốt với con gái ta.","Hy vọng Huyền Đức sẽ đối xử tốt với con gái ta.",},
        [1873] = {20188001,"Lữ Bố","Trương Liêu","Văn Viễn! Ngươi là thuộc hạ của ta, ta bị Tào Tháo bắt, ngươi mau sai người đến cứu ta!","Văn Viễn! Ngươi là thuộc hạ của ta, ta bị Tào Tháo bắt, ngươi mau sai người đến cứu ta!",},
        [1874] = {20188001,"Tào Tháo","Trương Liêu","Văn Viễn Tướng Quân theo Lữ Bố quả thật mai một tài năng, sau này theo ta đi, ngươi sẽ là mãnh tướng không thể thiếu.","Văn Viễn Tướng Quân theo Lữ Bố quả thật mai một tài năng, sau này theo ta đi, ngươi sẽ là mãnh tướng không thể thiếu.",},
        [1875] = {20188001,"Trương Liêu","Tào Tháo","Được, Trương Liêu nguyện theo Tào Công lập đại nghiệp.","Được, Trương Liêu nguyện theo Tào Công lập đại nghiệp.",},
        [1876] = {20188002,"Quan Vũ","Trương Liêu","Đừng nói nữa, ta không đầu hàng đâu!","Đừng nói nữa, ta không đầu hàng đâu!",},
        [1877] = {20188002,"Trương Liêu","Quan Vũ","Vân Trường huynh đầu hàng đi, có ta không cần sợ, khi có tin của Lưu Bị, huynh muốn đi cứ đi.","Vân Trường huynh đầu hàng đi, có ta không cần sợ, khi có tin của Lưu Bị, huynh muốn đi cứ đi.",},
        [1878] = {20188002,"Quan Vũ","Trương Liêu","Thật không?","Thật không?",},
        [1879] = {20188002,"Trương Liêu","Quan Vũ","Tin ta, yên tâm đi.","Tin ta, yên tâm đi.",},
        [1880] = {20188003,"Trương Liêu","","Chiến thắng lần này đều do quân ta chuẩn bị tốt, cộng thêm sự dũng cảm của các tướng sĩ.","Chiến thắng lần này đều do quân ta chuẩn bị tốt, cộng thêm sự dũng cảm của các tướng sĩ.",},
        [1881] = {20188003,"Tôn Quyền","Trương Liêu","Trương Liêu đúng là mãnh tướng, quả thật đã khinh địch.","Trương Liêu đúng là mãnh tướng, quả thật đã khinh địch.",},
        [1882] = {20188003,"Tôn Quyền","Trương Liêu","Sau chiến dịch này, Trương Liêu đã nổi danh khắp Giang Đông.","Sau chiến dịch này, Trương Liêu đã nổi danh khắp Giang Đông.",},
        [1883] = {20188101,"Tào Thực","Dương Tu","Vốn là vương hầu, vẫn mong phụng sự bá tánh, tạo lập sự nghiệp vĩnh hằng.","Vốn là vương hầu, vẫn mong phụng sự bá tánh, tạo lập sự nghiệp vĩnh hằng.",},
        [1884] = {20188101,"Tào Thực","Dương Tu","Nếu không để lại chiến tích, chẳng lẽ dùng từ phú làm quân tử?","Nếu không để lại chiến tích, chẳng lẽ dùng từ phú làm quân tử?",},
        [1885] = {20188101,"Tào Thực","Dương Tu","Dù chí hướng không thành, ta sẽ thu thập sử sách, nhận biết được mất, tạo ra học thuyết.","Dù chí hướng không thành, ta sẽ thu thập sử sách, nhận biết được mất, tạo ra học thuyết.",},
        [1886] = {20188101,"Tào Thực","Dương Tu","Ta sẽ truyền cho người cùng chí hướng, nếu không phải tình bạn lâu năm, sao có thể đưa ra lời bình luận đó.","Ta sẽ truyền cho người cùng chí hướng, nếu không phải tình bạn lâu năm, sao có thể đưa ra lời bình luận đó.",},
        [1887] = {20188101,"Dương Tu","Tào Thực","Tạ Tử Kiến thưởng thức, không thành công sẽ thành nhân.","Tạ Tử Kiến thưởng thức, không thành công sẽ thành nhân.",},
        [1888] = {20188102,"Tư Mã Ý","Dương Tu","Thừa tướng thử thách Tào Phi và Tào Thực, lệnh hai người rời Nghiệp Thành, lại báo với lính gác chặn cổng.","Thừa tướng thử thách Tào Phi và Tào Thực, lệnh hai người rời Nghiệp Thành, lại báo với lính gác chặn cổng.",},
        [1889] = {20188102,"Tư Mã Ý","Dương Tu","Có phải ngươi xúi giục Tào Thực, nghe lệnh vua xuất thành, trảm sĩ binh cản hắn.","Có phải ngươi xúi giục Tào Thực, nghe lệnh vua xuất thành, trảm sĩ binh cản hắn.",},
        [1890] = {20188102,"Dương Tu","Tư Mã Ý","Vâng!","Vâng!",},
        [1891] = {20188102,"Tư Mã Ý","Dương Tu","Đức Tổ, đừng lãng phí tài năng nữa.","Đức Tổ, đừng lãng phí tài năng nữa.",},
        [1892] = {20188102,"Tư Mã Ý","Dương Tu","Nếu thừa tướng biết ngươi bày mưu, vậy ngươi hại Tào Thực rồi.","Nếu thừa tướng biết ngươi bày mưu, vậy ngươi hại Tào Thực rồi.",},
        [1893] = {20188103,"Tào Tháo","Hạ Hầu Đôn","Nguyên Nhượng, vì sao quân sĩ đều chuẩn bị hành trang?","Nguyên Nhượng, vì sao quân sĩ đều chuẩn bị hành trang?",},
        [1894] = {20188103,"Hạ Hầu Đôn","Tào Tháo","Chủ Bạ Dương Đức Tổ nói đại vương có ý trở về.","Chủ Bạ Dương Đức Tổ nói đại vương có ý trở về.",},
        [1895] = {20188103,"Tào Tháo","Hạ Hầu Đôn","Dương Đức Tổ sao lại nói thế?","Dương Đức Tổ sao lại nói thế?",},
        [1896] = {20188103,"Hạ Hầu Đôn","Tào Tháo","Chắc Nguỵ Vương mấy bữa nay cũng về thôi. Kê cân nghĩa là gân gà, ăn không có vị mà bỏ thì tiếc. ","Chắc Nguỵ Vương mấy bữa nay cũng về thôi. Kê cân nghĩa là gân gà, ăn không có vị mà bỏ thì tiếc. ",},
        [1897] = {20188103,"Hạ Hầu Đôn","Tào Tháo","Nay tiến đánh không được, mà lui thì sợ người cười, ở mãi cũng vô ích, chi bằng về cho sớm còn hơn.","Nay tiến đánh không được, mà lui thì sợ người cười, ở mãi cũng vô ích, chi bằng về cho sớm còn hơn.",},
        [1898] = {20188103,"Tào Tháo","Hạ Hầu Đôn","Dương Tu tự cho mình giỏi, dám làm rối lòng quân!","Dương Tu tự cho mình giỏi, dám làm rối lòng quân!",},
        [1899] = {20188103,"Tào Tháo","Hạ Hầu Đôn","Đao Phủ Thủ đưa hắn ra xử trảm, thủ cấp treo ngoài nha môn.","Đao Phủ Thủ đưa hắn ra xử trảm, thủ cấp treo ngoài nha môn.",},
        [1900] = {20188201,"Tư Mã Ý","Tư Mã Chiêu","Tử Thượng, Tử Nguyên, qua đây.","Tử Thượng, Tử Nguyên, qua đây.",},
        [1901] = {20188201,"Tư Mã Chiêu","Tư Mã Ý","Có mặt.","Có mặt.",},
        [1902] = {20188201,"Tư Mã Ý","Tư Mã Chiêu","Ta làm quan nhiều năm, nhiều người nghi ta định soán ngôi, ta rất lo sợ.","Ta làm quan nhiều năm, nhiều người nghi ta định soán ngôi, ta rất lo sợ.",},
