        [2071] = {20189802,"Lưu Biểu","Lưu Bị","Ta bệnh nặng không sống được lâu nữa, gửi gắm hiền đệ.","Ta bệnh nặng không sống được lâu nữa, gửi gắm hiền đệ.",},
        [2072] = {20189802,"Lưu Biểu","Lưu Bị","Con ta bất tài, không thể kế thừa sự nghiệp, sau khi ta mất, hiền đệ có thể dẫn dắt Kinh Châu.","Con ta bất tài, không thể kế thừa sự nghiệp, sau khi ta mất, hiền đệ có thể dẫn dắt Kinh Châu.",},
        [2073] = {20189802,"Lưu Bị","Lưu Biểu","Ta sẽ giúp đỡ hết sức, đâu dám có ý gì khác.","Ta sẽ giúp đỡ hết sức, đâu dám có ý gì khác.",},
        [2074] = {20189901,"Tôn Quyền","Đinh Phụng","Ta thay đổi người kế thừa liên tục, khiến cho cục thế triều đình bất ổn.","Ta thay đổi người kế thừa liên tục, khiến cho cục thế triều đình bất ổn.",},
        [2075] = {20189901,"Tôn Quyền","Đinh Phụng","Ta đã lớn tuổi, quyết định giao Đông Ngô cho Tôn Lượng.","Ta đã lớn tuổi, quyết định giao Đông Ngô cho Tôn Lượng.",},
        [2076] = {20189901,"Tôn Quyền","Đinh Phụng","Đinh Phụng, ngươi phải phò tá Tôn Lượng tìm cách bảo vệ Đông Ngô.","Đinh Phụng, ngươi phải phò tá Tôn Lượng tìm cách bảo vệ Đông Ngô.",},
        [2077] = {20189901,"Đinh Phụng","Tôn Quyền","Thần tuân lệnh.","Thần tuân lệnh.",},
        [2078] = {20189902,"Đinh Phụng","","Tôn Lượng vừa nhận ngôi vua, Ngụy Gia Cát Đản, Hồ Tuân liền tấn công Đông Hưng.","Tôn Lượng vừa nhận ngôi vua, Ngụy Gia Cát Đản, Hồ Tuân liền tấn công Đông Hưng.",},
        [2079] = {20189902,"Đinh Phụng","","Nghĩ cách bảo vệ Đông Ngô, Đông Hưng Chiến chắc chắn phải thắng.","Nghĩ cách bảo vệ Đông Ngô, Đông Hưng Chiến chắc chắn phải thắng.",},
        [2080] = {20189902,"Đinh Phụng","","Đến lúc lập công nhận thưởng rồi!","Đến lúc lập công nhận thưởng rồi!",},
        [2081] = {20189902,"Đinh Phụng","","Binh tướng mau cùng ta dùng binh khí ngắn diệt địch.","Binh tướng mau cùng ta dùng binh khí ngắn diệt địch.",},
        [2082] = {201810001,"Dương Cổ","Lục Kháng","Đây là thuốc ta mới pha chế, nghe nói ngươi bệnh, ta tặng cho ngươi.","Đây là thuốc ta mới pha chế, nghe nói ngươi bệnh, ta tặng cho ngươi.",},
        [2083] = {201810001,"Tướng Đông Ngô","Lục Kháng","E có điều khả nghi, tướng quân đừng dùng.","E có điều khả nghi, tướng quân đừng dùng.",},
        [2084] = {201810001,"Lục Kháng","Tướng Đông Ngô","Dương Cổ sao có thể dùng độc hại người!","Dương Cổ sao có thể dùng độc hại người!",},
        [2085] = {201810001,"Lục Kháng","Tướng Đông Ngô","Họ dùng đạo đức, ta dùng bạo lực, vậy sẽ không cần đấu mà bị chinh phục.","Họ dùng đạo đức, ta dùng bạo lực, vậy sẽ không cần đấu mà bị chinh phục.",},
        [2086] = {201810002,"Lục Kháng","","Lập quốc, kế thừa gia nghiệp không cần tiểu nhân, không dùng kẻ xấu,  ","Lập quốc, kế thừa gia nghiệp không cần tiểu nhân, không dùng kẻ xấu,  ",},
        [2087] = {201810002,"Lục Kháng","","Ngụy mượn triều đình trị quyền, chỉnh đốn xã hội là điều không thể thực hiện.","Ngụy mượn triều đình trị quyền, chỉnh đốn xã hội là điều không thể thực hiện.",},
        [2088] = {201810002,"Lục Kháng","","Tuy quan sứ tài năng xuất chúng không nhiều, họ đều là hậu duệ của vương thất, từ nhỏ đã bị đạo đức giáo hóa ảnh hưởng.","Tuy quan sứ tài năng xuất chúng không nhiều, họ đều là hậu duệ của vương thất, từ nhỏ đã bị đạo đức giáo hóa ảnh hưởng.",},
        [2089] = {201810002,"Lục Kháng","","Có lẽ những người nghèo khó mới có tư chất đáng để tin dùng, có thể căn cứ tài cán của họ để bổ nhiệm quan chức.","Có lẽ những người nghèo khó mới có tư chất đáng để tin dùng, có thể căn cứ tài cán của họ để bổ nhiệm quan chức.",},
        [2090] = {201810002,"Lục Kháng","","Sau đó xã hội mới được chỉnh đốn, triều đình trong sạch.","Sau đó xã hội mới được chỉnh đốn, triều đình trong sạch.",},
        [2091] = {1701,"Nhân vật","Ngân Bình","Lần này mang thư đến cho đại ca không chừng có thể gặp Triệu Tử Long tướng quân! Nghe nói Triệu Tử Long có quan hệ rất tốt với đại ca.","Lần này mang thư đến cho đại ca không chừng có thể gặp Triệu Tử Long tướng quân! Nghe nói Triệu Tử Long có quan hệ rất tốt với đại ca.",},
        [2092] = {1701,"Ngân Bình","","Thật sao? Có thể gặp được Triệu Tử Long, thật cảm động quá đi!","Thật sao? Có thể gặp được Triệu Tử Long, thật cảm động quá đi!",},
        [2093] = {1701,"Tinh Thái","","Ngân Bình tỷ tỷ, tỷ lại thế nữa rồi!","Ngân Bình tỷ tỷ, tỷ lại thế nữa rồi!",},
        [2094] = {1701,"Ngân Bình","","Nhất kỵ yên trần loạn thế trung, nhất thân thị đảm thùy tranh phong!","Nhất kỵ yên trần loạn thế trung, nhất thân thị đảm thùy tranh phong!",},
        [2095] = {1701,"Ngân Bình","","Triệu Tử Long tướng quân còn trẻ, lại khôi ngô, dũng mãnh, không đáng để ta ngưỡng mộ sao?","Triệu Tử Long tướng quân còn trẻ, lại khôi ngô, dũng mãnh, không đáng để ta ngưỡng mộ sao?",},
        [2096] = {1701,"Tinh Thái","","Được, đáng kính trọng! Nhưng trong lòng ta, chúa công mới là người đáng kính trọng nhất!","Được, đáng kính trọng! Nhưng trong lòng ta, chúa công mới là người đáng kính trọng nhất!",},
        [2097] = {1701,"Nhân vật","","Ha ha! Tinh Thái lại quá khen rồi! Yên tâm, ta sẽ không để ngươi thất vọng đâu!","Ha ha! Tinh Thái lại quá khen rồi! Yên tâm, ta sẽ không để ngươi thất vọng đâu!",},
        [2098] = {1702,"Nhân vật","Điền Khải","Điền Khải, ngươi hẳn là biết chuyện Tào Tháo đánh Từ Châu, Bì Hải Khổng Dung đã đồng ý cứu viện Từ Châu, còn ngươi thì sao?","Điền Khải, ngươi hẳn là biết chuyện Tào Tháo đánh Từ Châu, Bì Hải Khổng Dung đã đồng ý cứu viện Từ Châu, còn ngươi thì sao?",},
        [2099] = {1702,"Điền Khải","","Từ Châu và Thanh Châu gắn bó với nhau, theo lý phải giúp, nhưng hiện giờ Thanh Châu đang khai chiến với Viên Thiệu, nếu phân tán binh lực e là không ổn...","Từ Châu và Thanh Châu gắn bó với nhau, theo lý phải giúp, nhưng hiện giờ Thanh Châu đang khai chiến với Viên Thiệu, nếu phân tán binh lực e là không ổn...",},
        [2100] = {1702,"Điền Khải","","Nhưng Tào Tháo đó đánh xong Từ Châu, không lâu sau chắc chắn sẽ đánh đến Thanh Châu... Phải làm sao đây...","Nhưng Tào Tháo đó đánh xong Từ Châu, không lâu sau chắc chắn sẽ đánh đến Thanh Châu... Phải làm sao đây...",},
        [2101] = {1702,"Nhân vật","","Ta trước giờ luôn ghét người không quyết đoán, ngươi cứ nói đến hay không đến!","Ta trước giờ luôn ghét người không quyết đoán, ngươi cứ nói đến hay không đến!",},
        [2102] = {1702,"Điền Khải","","Tiểu anh hùng đừng gấp, Tào Tháo đó hung hãn hơn Viên Thiệu, Điền Khải sẽ chi viện Từ Châu!","Tiểu anh hùng đừng gấp, Tào Tháo đó hung hãn hơn Viên Thiệu, Điền Khải sẽ chi viện Từ Châu!",},
        [2103] = {1702,"Nhân vật","","Tốt, vậy mới sảng khoái!","Tốt, vậy mới sảng khoái!",},
        [2104] = {1703,"Nhân vật","Lưu Bị","Đại ca, Tào Tháo muốn đánh Từ Châu, chúng ta vốn giúp Đào Khiêm đi mời Khổng Dung xuất binh, nào ngờ Bì Hải bị Hoàng Cân bao vây, Khổng Dung đó bèn đưa thư nhờ đại ca dẫn binh đi viện trợ Từ Châu.","Đại ca, Tào Tháo muốn đánh Từ Châu, chúng ta vốn giúp Đào Khiêm đi mời Khổng Dung xuất binh, nào ngờ Bì Hải bị Hoàng Cân bao vây, Khổng Dung đó bèn đưa thư nhờ đại ca dẫn binh đi viện trợ Từ Châu.",},
        [2105] = {1703,"Lưu Bị","","Bì Hải Khổng Dung lại biết Lưu Bị ta! Nhưng chúng ta không đủ binh lực.","Bì Hải Khổng Dung lại biết Lưu Bị ta! Nhưng chúng ta không đủ binh lực.",},
        [2106] = {1703,"Nhân vật","","Đại ca đừng lo, chúng ta đi tìm Công Tôn Toản mượn ba ngàn binh mã, mượn luôn cả Triệu Tử Long tướng quân, được không?","Đại ca đừng lo, chúng ta đi tìm Công Tôn Toản mượn ba ngàn binh mã, mượn luôn cả Triệu Tử Long tướng quân, được không?",},
        [2107] = {1703,"Lưu Bị","","Tứ đệ nghĩ thật chu đáo, chúng ta lập tức đi tìm Công Tôn Toản.","Tứ đệ nghĩ thật chu đáo, chúng ta lập tức đi tìm Công Tôn Toản.",},
        [2108] = {1704,"Công Tôn Toản","Lưu Bị","Tào Tháo không có thù với ngươi, sao phải ra sức thay người khác?","Tào Tháo không có thù với ngươi, sao phải ra sức thay người khác?",},
        [2109] = {1704,"Lưu Bị","","Lưu Bị đã đáp ứng Khổng Dung, không dám nuốt lời.","Lưu Bị đã đáp ứng Khổng Dung, không dám nuốt lời.",},
        [2110] = {1704,"Công Tôn Toản","","Huyền Đức làm vậy sẽ tự tạo thêm kẻ địch đấy! Được rồi, ta đồng ý cho mượn ba ngàn tinh binh, còn Triệu Tử Long, ngươi phải vượt quả khảo nghiệm của hắn!","Huyền Đức làm vậy sẽ tự tạo thêm kẻ địch đấy! Được rồi, ta đồng ý cho mượn ba ngàn tinh binh, còn Triệu Tử Long, ngươi phải vượt quả khảo nghiệm của hắn!",},
        [2111] = {1704,"Nhân vật","","Đại ca, khảo nghiệm của Tử Long tướng quân cứ để ta lo! Lâu rồi không đánh nhau với cao thủ, đang ngứa tay đây!","Đại ca, khảo nghiệm của Tử Long tướng quân cứ để ta lo! Lâu rồi không đánh nhau với cao thủ, đang ngứa tay đây!",},
        [2112] = {1705,"Triệu Vân","Nhân vật","Lợi hại, tiểu anh hùng võ nghệ thật cao cường!","Lợi hại, tiểu anh hùng võ nghệ thật cao cường!",},
        [2113] = {1705,"Nhân vật","","Quá khen! Triệu Tử Long tướng quân có nguyện ý cùng đại ca ta đi cứu viện Từ Châu?","Quá khen! Triệu Tử Long tướng quân có nguyện ý cùng đại ca ta đi cứu viện Từ Châu?",},
        [2114] = {1705,"Triệu Vân","","Đương nhiên! Ngươi đã vượt qua khảo nghiệm, ta sao có thể nuốt lời!","Đương nhiên! Ngươi đã vượt qua khảo nghiệm, ta sao có thể nuốt lời!",},
        [2115] = {1705,"Triệu Vân","","Hơn nữa, ta với Huyền Đức vốn không phải người ngoài. Ngươi là tứ đệ của Huyền Đức, Danh Tướng Lệnh xem như quà gặp mặt vậy! Ta đi chuẩn bị quân mã đây!","Hơn nữa, ta với Huyền Đức vốn không phải người ngoài. Ngươi là tứ đệ của Huyền Đức, Danh Tướng Lệnh xem như quà gặp mặt vậy! Ta đi chuẩn bị quân mã đây!",},
        [2116] = {1705,"Nhân vật","","Tốt quá! Có Điền Khải, Khổng Dung trợ giúp, lại có Triệu Tử Long và binh mã của đại ca, chắc chắn sẽ đánh lui được Tào Tháo!","Tốt quá! Có Điền Khải, Khổng Dung trợ giúp, lại có Triệu Tử Long và binh mã của đại ca, chắc chắn sẽ đánh lui được Tào Tháo!",},
        [2117] = {1705,"Tinh Thái","","Chúa công thật lợi hại! Trên đời này không có việc gì làm khó được chúa công cả!","Chúa công thật lợi hại! Trên đời này không có việc gì làm khó được chúa công cả!",},
        [2118] = {1705,"Nhân vật","","Ha ha! Tinh Thái, Ngân Bình, chúng ta mau trở về Từ Châu!","Ha ha! Tinh Thái, Ngân Bình, chúng ta mau trở về Từ Châu!",},
        [2119] = {1801,"Tào Tháo","Lữ Bố","Lữ Bố, Tào Tháo ta với ngươi không thù oán, sao lại nhân lúc ta xuất binh đi Từ Châu mà đoạt hai thành Duyện Châu, Bộc Dương?","Lữ Bố, Tào Tháo ta với ngươi không thù oán, sao lại nhân lúc ta xuất binh đi Từ Châu mà đoạt hai thành Duyện Châu, Bộc Dương?",},
        [2120] = {1801,"Lữ Bố","","Thành trì nhà Hán ai cũng có phần. Trần Cung và Trương Mạc nguyện ý nghênh đón ta làm Duyện Châu Mục, đây là bản lĩnh của ta!","Thành trì nhà Hán ai cũng có phần. Trần Cung và Trương Mạc nguyện ý nghênh đón ta làm Duyện Châu Mục, đây là bản lĩnh của ta!",},
        [2121] = {1801,"Tào Tháo","","Thật ngông cuồng! Đợi ta đánh vào thành Bộc Dương xong nhất định sẽ lấy thủ cấp của ngươi!","Thật ngông cuồng! Đợi ta đánh vào thành Bộc Dương xong nhất định sẽ lấy thủ cấp của ngươi!",},
        [2122] = {1801,"Lữ Bố","","Chiến Thần Lữ Bố ta sao phải sợ ngươi chứ! Nếu ngươi dám vào Bộc Dương thì đừng mong trở ra nữa!","Chiến Thần Lữ Bố ta sao phải sợ ngươi chứ! Nếu ngươi dám vào Bộc Dương thì đừng mong trở ra nữa!",},
        [2123] = {1802,"Tinh Thái","Nhân vật","Chúa công, Tào Tháo và Lữ Bố đánh nhau rồi! Tào Tháo vừa phái người đưa thư đến, hắn nới sẽ rút quân khỏi Từ Châu, mời chúng ta mau đi Bộc Dương cứu viện.","Chúa công, Tào Tháo và Lữ Bố đánh nhau rồi! Tào Tháo vừa phái người đưa thư đến, hắn nới sẽ rút quân khỏi Từ Châu, mời chúng ta mau đi Bộc Dương cứu viện.",},
        [2124] = {1802,"Nhân vật","","Từ khi nào Lữ Bố có nhiều binh mã như vậy, đánh chiếm được hai thành của Tào Tháo!","Từ khi nào Lữ Bố có nhiều binh mã như vậy, đánh chiếm được hai thành của Tào Tháo!",},
        [2125] = {1802,"Tinh Thái","","Sau khi Lý Thôi Quách Tị gây loạn Trường An, Lữ Bố vốn đi nhờ vả Viên Thuật, nào ngờ Viên Thuật hẹp hòi,, Lữ Bố thì không chịu dưới trướng người khác.","Sau khi Lý Thôi Quách Tị gây loạn Trường An, Lữ Bố vốn đi nhờ vả Viên Thuật, nào ngờ Viên Thuật hẹp hòi,, Lữ Bố thì không chịu dưới trướng người khác.",},
        [2126] = {1802,"Tinh Thái","","Sau đó Tào Tháo đi đánh Từ Châu, Trần Lưu, Trương Mạc và Trần Cung bèn đón Lữ Bố qua đó làm Duyện Châu Mục.","Sau đó Tào Tháo đi đánh Từ Châu, Trần Lưu, Trương Mạc và Trần Cung bèn đón Lữ Bố qua đó làm Duyện Châu Mục.",},
        [2127] = {1802,"Nhân vật","","Lúc đầu ta khuyên Tào Tháo rút khỏi Từ Châu, hắn tránh không gặp ta, nay lại đến nhờ vả! Thôi được, ta cứu hắn một lần nữa vậy!","Lúc đầu ta khuyên Tào Tháo rút khỏi Từ Châu, hắn tránh không gặp ta, nay lại đến nhờ vả! Thôi được, ta cứu hắn một lần nữa vậy!",},
        [2128] = {1803,"Trương Mạc","Nhân vật","Ai đến Bộc Dương gây sự? Các ngươi có phải là gian tế của Tào Tháo?","Ai đến Bộc Dương gây sự? Các ngươi có phải là gian tế của Tào Tháo?",},
        [2129] = {1803,"Tinh Thái","","Chúa công, Trương Mạc này nổi tiếng chính trực, là một trong Bát Trù, giao chiến với hắn phải cẩn thận.","Chúa công, Trương Mạc này nổi tiếng chính trực, là một trong Bát Trù, giao chiến với hắn phải cẩn thận.",},
        [2130] = {1803,"Nhân vật","","Bát Trù? Chẳng lẽ hắn là một đầu bếp làm thức ăn rất ngon?","Bát Trù? Chẳng lẽ hắn là một đầu bếp làm thức ăn rất ngon?",},
        [2131] = {1803,"Tinh Thái","","Ha ha, chúa công hiểu lầm rồi, Bát Trù này là tám người Trương Mạc, Độ Thượng... Ý là dùng tài phú giúp đỡ người khác.","Ha ha, chúa công hiểu lầm rồi, Bát Trù này là tám người Trương Mạc, Độ Thượng... Ý là dùng tài phú giúp đỡ người khác.",},
        [2132] = {1803,"Tinh Thái","","Trương Mạc lúc trẻ nổi danh hiệp nghĩa, không tiếc tán gia bại sản để giúp đỡ người khác. Tào Tháo, Viên Thiệu đều từng là bằng hữu của Trương Mạc.","Trương Mạc lúc trẻ nổi danh hiệp nghĩa, không tiếc tán gia bại sản để giúp đỡ người khác. Tào Tháo, Viên Thiệu đều từng là bằng hữu của Trương Mạc.",},
        [2133] = {1803,"Tinh Thái","","Trương Mạc ngày càng nổi danh, sau đó được triều đình ban cho chức quan Trần Lưu Thái Thú.","Trương Mạc ngày càng nổi danh, sau đó được triều đình ban cho chức quan Trần Lưu Thái Thú.",},
        [2134] = {1803,"Trương Mạc","","Hừ! Đừng thấy ta cầm dao bếp mà nghĩ ta là đầu bếp! Đây là tuyệt thế bảo đao đấy!","Hừ! Đừng thấy ta cầm dao bếp mà nghĩ ta là đầu bếp! Đây là tuyệt thế bảo đao đấy!",},
        [2135] = {1803,"Nhân vật","","Trương Mạc, xem ra ngươi cũng có bản lĩnh. Nhưng chẳng đáng là gì khi ở trước mặt ta cả!","Trương Mạc, xem ra ngươi cũng có bản lĩnh. Nhưng chẳng đáng là gì khi ở trước mặt ta cả!",},
        [2136] = {1804,"Trương Mạc","Nhân vật","Tướng quân quả thật tài giỏi! Ta nguyện ý tặng bảo đao này cho tướng quân!","Tướng quân quả thật tài giỏi! Ta nguyện ý tặng bảo đao này cho tướng quân!",},
        [2137] = {1804,"Nhân vật","","Thôi vậy, ngươi cứ giữ lại bảo đao để dùng đi!","Thôi vậy, ngươi cứ giữ lại bảo đao để dùng đi!",},
        [2138] = {1804,"Trương Mạc","","Không ngờ Trương Mạc ta cũng có ngày bị người khác ghét bỏ!","Không ngờ Trương Mạc ta cũng có ngày bị người khác ghét bỏ!",},
        [2139] = {1805,"Vu Cấm","Tào Tháo","Chúa công còn ở ngoài thành, cho chúng ta vào thành trước đi!","Chúa công còn ở ngoài thành, cho chúng ta vào thành trước đi!",},
        [2140] = {1805,"Tào Tháo","Vu Cấm","Ta không tự đi, ai dám đi trước!","Ta không tự đi, ai dám đi trước!",},
        [2141] = {1805,"Nhân vật","Tinh Thái","Tào Tháo đâu? Lúc nãy còn nghe ông ta nói chuyện với người khác mà!","Tào Tháo đâu? Lúc nãy còn nghe ông ta nói chuyện với người khác mà!",},
        [2142] = {1805,"Tinh Thái","","Chúa công, Tào Tháo đó tin lầm gian kế của Trần Cung và Lữ Bố, nay đã dẫn binh xông vào thành rồi!","Chúa công, Tào Tháo đó tin lầm gian kế của Trần Cung và Lữ Bố, nay đã dẫn binh xông vào thành rồi!",},
        [2143] = {1805,"Tinh Thái","","Bên trong khói lửa ngập trời, không biết Tào Tháo sao rồi! Lúc nãy thấy Nhạc Tiến, Lý Điển, Điển Vi đều đang tìm Tào Tháo!","Bên trong khói lửa ngập trời, không biết Tào Tháo sao rồi! Lúc nãy thấy Nhạc Tiến, Lý Điển, Điển Vi đều đang tìm Tào Tháo!",},
        [2144] = {1805,"Nhân vật","","Chúng ta đánh bại Lữ Bố, mau đi cứu người.","Chúng ta đánh bại Lữ Bố, mau đi cứu người.",},
        [2145] = {1806,"Lữ Bố","Nhân vật","Ha ha, trận đánh này thật sảng khoái! Tiểu anh hùng, nhờ ngươi mà ta với Điêu Thuyền mới có ngày hôm nay! Nể mặt ngươi, hôm nay ta sẽ tha cho Tào Tháo!","Ha ha, trận đánh này thật sảng khoái! Tiểu anh hùng, nhờ ngươi mà ta với Điêu Thuyền mới có ngày hôm nay! Nể mặt ngươi, hôm nay ta sẽ tha cho Tào Tháo!",},
        [2146] = {1806,"Nhân vật","Lữ Bố","Lữ Ôn Hầu quả nhiên sảng khoái!","Lữ Ôn Hầu quả nhiên sảng khoái!",},
        [2147] = {1806,"Tào Tháo","Nhân vật","Lúc nãy cây cột cổng thành đổ xuống trung tay ta, giờ cánh tay đã bị phỏng!","Lúc nãy cây cột cổng thành đổ xuống trung tay ta, giờ cánh tay đã bị phỏng!",},
        [2148] = {1806,"Tào Tháo","","Nếu không nhờ tiểu anh hùng đánh bại Lữ Bố, hôm nay quả thật sẽ gặp nguy! Danh Tướng Lệnh này xin tặng cho các hạ!","Nếu không nhờ tiểu anh hùng đánh bại Lữ Bố, hôm nay quả thật sẽ gặp nguy! Danh Tướng Lệnh này xin tặng cho các hạ!",},
        [2149] = {1806,"Nhân vật","","Mong Tào huynh hãy giữ lời rút quân khỏi Từ Châu!","Mong Tào huynh hãy giữ lời rút quân khỏi Từ Châu!",},
        [2150] = {1806,"Tào Tháo","","Tiểu anh hùng yên tâm, ta đã sớm rút quân đi rồi, lần trước tránh mặt không gặp, mong tiểu anh hùng tha thứ.","Tiểu anh hùng yên tâm, ta đã sớm rút quân đi rồi, lần trước tránh mặt không gặp, mong tiểu anh hùng tha thứ.",},
        [2151] = {1806,"Nhân vật","","Được, nếu Tào huynh đã giữa lời thì ta cũng không tính toán nhiều nữa. Giờ ta phải về Từ Châu tìm đại ca, xin cáo từ.","Được, nếu Tào huynh đã giữa lời thì ta cũng không tính toán nhiều nữa. Giờ ta phải về Từ Châu tìm đại ca, xin cáo từ.",},
        [2152] = {1806,"Tào Tháo","","Cáo từ! Lần này trúng kế của bọn thất phu, sớm muộn ta cũng phải đoạt lại thành Bộc Dương!","Cáo từ! Lần này trúng kế của bọn thất phu, sớm muộn ta cũng phải đoạt lại thành Bộc Dương!",},
        [2153] = {1901,"Đào Khiêm","Lưu Bị","Ngươi đâu, mau mang Đại Ấn của Từ Châu ra đây!","Ngươi đâu, mau mang Đại Ấn của Từ Châu ra đây!",},
        [2154] = {1901,"Lưu Bị","","Đào công đây là có ý gì?","Đào công đây là có ý gì?",},
        [2155] = {1901,"Đào Khiêm","","Huyền Đức, ngươi dang vẻ hiên ngang, có đức có tài, lại hán dòng dõi nhà Hán, ta nguyện nhường Từ Châu cho ngươi!","Huyền Đức, ngươi dang vẻ hiên ngang, có đức có tài, lại hán dòng dõi nhà Hán, ta nguyện nhường Từ Châu cho ngươi!",},
        [2156] = {1901,"Lưu Bị","","Lưu Bị tuy là dòng họ nhà Hán, nhưng chưa có công lớn nên không dám nhận lệnh. Sao Đào công không nhường cho Viên Thiệu?","Lưu Bị tuy là dòng họ nhà Hán, nhưng chưa có công lớn nên không dám nhận lệnh. Sao Đào công không nhường cho Viên Thiệu?",},
        [2157] = {1901,"Đào Khiêm","","Viên Thiệu kém cỏi, làm sao so được với ngươi!","Viên Thiệu kém cỏi, làm sao so được với ngươi!",},
        [2158] = {1901,"Lưu Bị","","Lần này ta dẫn binh đến cứu Từ Châu là vì đại nghĩa, không hề có tư lợi. Lưu Bị quả thật không dám nhận ủy thác của Đào công.","Lần này ta dẫn binh đến cứu Từ Châu là vì đại nghĩa, không hề có tư lợi. Lưu Bị quả thật không dám nhận ủy thác của Đào công.",},
        [2159] = {1902,"Đào Khiêm","Nhân vật","Tiểu anh hùng tuổi trẻ tài cao, lòng có chí lớn. Đào Khiêm muốn tiểu anh hùng giúp một việc.","Tiểu anh hùng tuổi trẻ tài cao, lòng có chí lớn. Đào Khiêm muốn tiểu anh hùng giúp một việc.",},
        [2160] = {1902,"Nhân vật","","Đào công cứ nói.","Đào công cứ nói.",},
        [2161] = {1902,"Đào Khiêm","","Nay thiên hạ đại loạn, lão phu đã già, con trai thì bất tài, ta muốn nhường chức Từ Châu Thái Thú cho Lưu Bị, nào ngờ Lưu Bị từ chối, mong tiểu anh hùng giúp khuyên nhủ Lưu Bị.","Nay thiên hạ đại loạn, lão phu đã già, con trai thì bất tài, ta muốn nhường chức Từ Châu Thái Thú cho Lưu Bị, nào ngờ Lưu Bị từ chối, mong tiểu anh hùng giúp khuyên nhủ Lưu Bị.",},
        [2162] = {1902,"Nhân vật","","Kỳ lạ, sao người không truyền lại cho con trai!","Kỳ lạ, sao người không truyền lại cho con trai!",},
        [2163] = {1902,"Đào Khiêm","","Tiểu anh hùng có điều không biết, hai đứa con ta thật sự không đủ tài năng đảm nhận, biết phải làm sao chứ? (Nói xong liền khóc)","Tiểu anh hùng có điều không biết, hai đứa con ta thật sự không đủ tài năng đảm nhận, biết phải làm sao chứ? (Nói xong liền khóc)",},
        [2164] = {1902,"Nhân vật","","Đào công đừng gấp, ta sẽ đi khuyên đại ca ngay!","Đào công đừng gấp, ta sẽ đi khuyên đại ca ngay!",},
        [2165] = {1903,"Quan Vũ","Nhân vật","Tứ đệ, đại ca biết ngươi đến khuyên nên bảo ta và Dực Đức cản ngươi lại.","Tứ đệ, đại ca biết ngươi đến khuyên nên bảo ta và Dực Đức cản ngươi lại.",},
        [2166] = {1903,"Nhân vật","","Nhị ca, đắc tội rồi! Vừa hay nhân cơ hội này chúng ta tỉ thí xem.","Nhị ca, đắc tội rồi! Vừa hay nhân cơ hội này chúng ta tỉ thí xem.",},
        [2167] = {1904,"Tinh Thái","Trương Phi","Gia gia, người thật hồ đồ, ngăn cản chúa công ta làm gì chứ?","Gia gia, người thật hồ đồ, ngăn cản chúa công ta làm gì chứ?",},
        [2168] = {1904,"Trương Phi","","Nữ nhi ngoan, đây là quân lệnh, đừng trách gia gia.","Nữ nhi ngoan, đây là quân lệnh, đừng trách gia gia.",},
        [2169] = {1904,"Tinh Thái","","Vậy đến lúc chiến đấu, người không được làm chúa công ta bị thương.","Vậy đến lúc chiến đấu, người không được làm chúa công ta bị thương.",},
        [2170] = {1904,"Trương Phi","","Được rồi, lời của ngươi ta đều nghe theo hết!","Được rồi, lời của ngươi ta đều nghe theo hết!",},
        [2171] = {1905,"Nhân vật","Lưu Bị","Đại ca, Từ Châu là vùng đất tốt, Tào Tháo ở phía bắc và Viên Thuật ở phái nam đều muốn đoạt được. Nếu người không tiếp nhận Từ Châu, e là bá tánh Từ Châu sẽ gặp nguy.","Đại ca, Từ Châu là vùng đất tốt, Tào Tháo ở phía bắc và Viên Thuật ở phái nam đều muốn đoạt được. Nếu người không tiếp nhận Từ Châu, e là bá tánh Từ Châu sẽ gặp nguy.",},
        [2172] = {1905,"Lưu Bị","","Tứ đệ nói rất đúng, là đại ca quá cổ hủ rồi. Nhưng một mình đại ca làm sao đảm nhận trách nhiệm lớn này!","Tứ đệ nói rất đúng, là đại ca quá cổ hủ rồi. Nhưng một mình đại ca làm sao đảm nhận trách nhiệm lớn này!",},
        [2173] = {1905,"Nhân vật","","Tồn Càn, My Trúc, Trần Đăng sẽ hỗ trợ người, còn có huynh đệ chúng ta nữa. Đại ca đừng chối từ nữa!","Tồn Càn, My Trúc, Trần Đăng sẽ hỗ trợ người, còn có huynh đệ chúng ta nữa. Đại ca đừng chối từ nữa!",},
        [2174] = {1905,"Lưu Bị","","Được, ta sẽ nghe lời tứ đệ!","Được, ta sẽ nghe lời tứ đệ!",},
        [2175] = {1905,"Lưu Bị","","Tứ đệ, đây là Danh Tướng Lệnh do Đào công tặng cho ta, đa tạ ngươi khiến Tào Tháo lui binh, giải nguy cho Từ Châu!","Tứ đệ, đây là Danh Tướng Lệnh do Đào công tặng cho ta, đa tạ ngươi khiến Tào Tháo lui binh, giải nguy cho Từ Châu!",},
        [2176] = {1905,"Nhân vật","","Đa tạ đại ca! Tứ đệ xin nhận lấy vậy!","Đa tạ đại ca! Tứ đệ xin nhận lấy vậy!",},
        [2177] = {2001,"Nhân vật","","Đại ca đã ổn định ở Từ Châu, ta đã từ biệt đại ca đến Trường An, muốn xem thử Trường An giờ như thế nào.","Đại ca đã ổn định ở Từ Châu, ta đã từ biệt đại ca đến Trường An, muốn xem thử Trường An giờ như thế nào.",},
        [2178] = {2001,"Nhân vật","Tinh Thái","A! Lần trước đến Trường An, nơi đây còn rất phồn hoa, sao giờ lại tan hoang suy tàn như vậy! Tinh Thái, ngươi biết chuyện gì xảy ra không?","A! Lần trước đến Trường An, nơi đây còn rất phồn hoa, sao giờ lại tan hoang suy tàn như vậy! Tinh Thái, ngươi biết chuyện gì xảy ra không?",},
        [2179] = {2001,"Tinh Thái","","Chúa công, sau khi Lý Thôi, Quách Tị chiếm Trường An bèn uy hiếp thiên tử, nắm giữ triều chính, nhưng hai người này lại nghi kỵ nhau và thường xảy ra tranh đấu nên Trường An mới trở nên như vậy.","Chúa công, sau khi Lý Thôi, Quách Tị chiếm Trường An bèn uy hiếp thiên tử, nắm giữ triều chính, nhưng hai người này lại nghi kỵ nhau và thường xảy ra tranh đấu nên Trường An mới trở nên như vậy.",},
        [2180] = {2001,"Ngân Bình","","Lúc trước Giả Hủ đó không phải nói là sẽ kềm chế Lý Thôi, Quách Tị, bảo vệ bá quan và thiên tử sao?","Lúc trước Giả Hủ đó không phải nói là sẽ kềm chế Lý Thôi, Quách Tị, bảo vệ bá quan và thiên tử sao?",},
        [2181] = {2001,"Nhân vật","","Đi, chúng ta đi tiêu diệt Lý Thôi, Quách Tị, tìm Giả Hủ và cứu thiên tử!","Đi, chúng ta đi tiêu diệt Lý Thôi, Quách Tị, tìm Giả Hủ và cứu thiên tử!",},
        [2182] = {2002,"Hán Hiến Đế","Nhân vật","Tiểu anh hùng đã hiểu lầm Giả Hủ. Nếu không nhờ Giả Hủ đứng giữa hòa giải, trẫm đã sớm mất mạng rồi.","Tiểu anh hùng đã hiểu lầm Giả Hủ. Nếu không nhờ Giả Hủ đứng giữa hòa giải, trẫm đã sớm mất mạng rồi.",},
        [2183] = {2002,"Nhân vật","","Thì ra là vậy. Giả Hủ, nể mặt hoàng đế nên ta tha cho ngươi.","Thì ra là vậy. Giả Hủ, nể mặt hoàng đế nên ta tha cho ngươi.",},
        [2184] = {2002,"Giả Hủ","","Bệ hạ, Lý Thôi, Quách Tị đó đã có ý muốn diệt trừ ta, ta phải rời khỏi Trường An. (Giả Hủ rời đi)","Bệ hạ, Lý Thôi, Quách Tị đó đã có ý muốn diệt trừ ta, ta phải rời khỏi Trường An. (Giả Hủ rời đi)",},
        [2185] = {2002,"Hán Hiến Đế","","Giả Hủ đã đi rồi, từ giờ ai sẽ bảo vệ trẫm? Hoàng đế như trẫm thật là bi thảm.","Giả Hủ đã đi rồi, từ giờ ai sẽ bảo vệ trẫm? Hoàng đế như trẫm thật là bi thảm.",},
        [2186] = {2002,"Nhân vật","","Bệ hạ, hay là đến Lạc Dương, rời khỏi Trường An thị phi.","Bệ hạ, hay là đến Lạc Dương, rời khỏi Trường An thị phi.",},
        [2187] = {2002,"Hán Hiến Đế","","E rằng Lý Thôi, Quách Tị sẽ không đồng ý.","E rằng Lý Thôi, Quách Tị sẽ không đồng ý.",},
        [2188] = {2002,"Nhân vật","","Bệ hạ đừng lo! Quách Tị đã bị ta đánh đuổi, giờ ta đi tìm Lý Thôi! Hắn buộc phải đồng ý thôi!","Bệ hạ đừng lo! Quách Tị đã bị ta đánh đuổi, giờ ta đi tìm Lý Thôi! Hắn buộc phải đồng ý thôi!",},
        [2189] = {2003,"Ngân Bình","Dương Phụng","Dương Phụng, ngươi thân là thần tử nhà Hán sao lại tiếp tay cho giặc?","Dương Phụng, ngươi thân là thần tử nhà Hán sao lại tiếp tay cho giặc?",},
        [2190] = {2003,"Ngân Bình","","Lý Thôi tham lam nhưng vô mưu, không thưởng cho quân tướng, chỉ thưởng cho nữ vu, ngươi theo hắn có lợi gì chứ.","Lý Thôi tham lam nhưng vô mưu, không thưởng cho quân tướng, chỉ thưởng cho nữ vu, ngươi theo hắn có lợi gì chứ.",},
        [2191] = {2003,"Dương Phụng","","Đúng vậy! Ta theo hắn vào sinh ra tử, chẳng lẽ không bằng nữ vu sao? Giờ ta sẽ triệu tập binh mã phản lại Lý Thôi.","Đúng vậy! Ta theo hắn vào sinh ra tử, chẳng lẽ không bằng nữ vu sao? Giờ ta sẽ triệu tập binh mã phản lại Lý Thôi.",},
        [2192] = {2003,"Ngân Bình","Nhân vật","Có được quân mã của Dương Phụng, chúa công có thể yên tâm được rồi.","Có được quân mã của Dương Phụng, chúa công có thể yên tâm được rồi.",},
        [2193] = {2003,"Nhân vật","","Ta thấy Dương Phụng chẳng ra sao cả, có điều đại tướng cầm rìu của hắn có vẻ rất giỏi, người này là ai?","Ta thấy Dương Phụng chẳng ra sao cả, có điều đại tướng cầm rìu của hắn có vẻ rất giỏi, người này là ai?",},
        [2194] = {2003,"Ngân Bình","","Chúa công, người này là người ở Hãn Đông Dương Huyện, tên Từ Minh Hoảng, tự Công Minh.","Chúa công, người này là người ở Hãn Đông Dương Huyện, tên Từ Minh Hoảng, tự Công Minh.",},
        [2195] = {2003,"Nhân vật","","Thì ra là danh tướng Từ Hoảng, theo Dương Phụng thật đáng tiếc.","Thì ra là danh tướng Từ Hoảng, theo Dương Phụng thật đáng tiếc.",},
        [2196] = {2004,"Hán Hiến Đế","Đổng Thừa","Hoàng đế như ta thật chẳng ra làm sao, trước có Đổng Trác lộng quyền hỏa thiêu Lạc Dương, ép ta đến Trường An, sau khi Đổng Trác mất, lại có Lý Thôi Quách Tị loạn Trường An...","Hoàng đế như ta thật chẳng ra làm sao, trước có Đổng Trác lộng quyền hỏa thiêu Lạc Dương, ép ta đến Trường An, sau khi Đổng Trác mất, lại có Lý Thôi Quách Tị loạn Trường An...",},
        [2197] = {2004,"Hán Hiến Đế","","Trải qua bao gian nguy mới về đến Lạc Dương, lại gặp Hàn Tiêm lộng quyền và nạn đói, ta và các đại thần không ăn no được bữa nào!","Trải qua bao gian nguy mới về đến Lạc Dương, lại gặp Hàn Tiêm lộng quyền và nạn đói, ta và các đại thần không ăn no được bữa nào!",},
        [2198] = {2004,"Đổng Thừa","","Bệ hạ, thần có kế này, có thể mật báo Duyện Châu Mục Tào Tháo cứu giúp, Tào Tháo quân đông lương thực nhiều, bệ hạ sau này không phải lo.","Bệ hạ, thần có kế này, có thể mật báo Duyện Châu Mục Tào Tháo cứu giúp, Tào Tháo quân đông lương thực nhiều, bệ hạ sau này không phải lo.",},
        [2199] = {2004,"Nhân vật","","Chuyện này có gì mà khó, Tào Tháo là chỗ quen biết cũ của ta, ta sẽ đi báo ào Tháo.","Chuyện này có gì mà khó, Tào Tháo là chỗ quen biết cũ của ta, ta sẽ đi báo ào Tháo.",},
        [2200] = {2004,"Đổng Thừa","","Chỉ e Dương Phụng đó không bằng lòng, đại tướng Từ Hoảng của hắn rất khó đối phó.","Chỉ e Dương Phụng đó không bằng lòng, đại tướng Từ Hoảng của hắn rất khó đối phó.",},
        [2201] = {2004,"Nhân vật","","Dương Phụng Hàn Tiêm thì không đáng nói, Từ Hoảng là bậc lương tướng, ta sẽ đi gặp ông ta.","Dương Phụng Hàn Tiêm thì không đáng nói, Từ Hoảng là bậc lương tướng, ta sẽ đi gặp ông ta.",},
        [2202] = {2005,"Nhân vật","Từ Hoảng","Tài năng của ngươi thật là hiếm có, sao lại theo Dương, Hàn?","Tài năng của ngươi thật là hiếm có, sao lại theo Dương, Hàn?",},
        [2203] = {2005,"Từ Hoảng","","Ta biết Dương Phụng, Hàn Tiềm không phải người lập nghiệp lớn, chỉ là theo đã lâu, không nỡ bỏ đi.","Ta biết Dương Phụng, Hàn Tiềm không phải người lập nghiệp lớn, chỉ là theo đã lâu, không nỡ bỏ đi.",},
        [2204] = {2005,"Nhân vật","","Chim khôn chọn cây mà đậu, hiền thần chọn chủ mà thờ, Duyện Châu Mục Tào Tháo là anh hùng đương thế, trọng hiền sĩ, ngươi có thể đến nương nhờ ông ta.","Chim khôn chọn cây mà đậu, hiền thần chọn chủ mà thờ, Duyện Châu Mục Tào Tháo là anh hùng đương thế, trọng hiền sĩ, ngươi có thể đến nương nhờ ông ta.",},
        [2205] = {2005,"Từ Hoảng","","Đa tạ tiểu anh hùng, xin tặng Danh Tướng Lệnh này làm quà đáp tạ. (Từ Hoảng cáo từ)","Đa tạ tiểu anh hùng, xin tặng Danh Tướng Lệnh này làm quà đáp tạ. (Từ Hoảng cáo từ)",},
        [2206] = {2005,"Nhân vật","Ngân Bình","Mấy năm nay thiên tai liên miên, hoàng đế tuy về Lạc Dương, nhưng không có nơi nào phụng dưỡng, lại không có quân lính bảo vệ, chắc chắn không phải kế lâu dài.","Mấy năm nay thiên tai liên miên, hoàng đế tuy về Lạc Dương, nhưng không có nơi nào phụng dưỡng, lại không có quân lính bảo vệ, chắc chắn không phải kế lâu dài.",},
        [2207] = {2005,"Ngân Bình","","Làm hoàng đế như vậy thật khổ, chỉ có chúa công là sướng, tiêu dao tự tại, không ai dám bắt nạt.","Làm hoàng đế như vậy thật khổ, chỉ có chúa công là sướng, tiêu dao tự tại, không ai dám bắt nạt.",},
        [2208] = {2005,"Nhân vật","","Ha ha, ai bảo ông ta là Hán Hiến Đế làm chi! Ta thì khác! Ta tuy không phải hoàng đế, nhưng ta mạnh nhất! Đây là thế giới dùng sức mạnh để nói chuyện!","Ha ha, ai bảo ông ta là Hán Hiến Đế làm chi! Ta thì khác! Ta tuy không phải hoàng đế, nhưng ta mạnh nhất! Đây là thế giới dùng sức mạnh để nói chuyện!",},
        [2209] = {2005,"Ngân Bình","","Chúa công, tiếp theo chúng ta đi đâu đây?","Chúa công, tiếp theo chúng ta đi đâu đây?",},
        [2210] = {2005,"Nhân vật","","Hi hi, đi, ta dẫn các ngươi xem cái này hay lắm.","Hi hi, đi, ta dẫn các ngươi xem cái này hay lắm.",},
        [2211] = {2101,"Tinh Thái","Nhân vật","Chúa công, chúng ta đang đi đâu vậy?","Chúa công, chúng ta đang đi đâu vậy?",},
        [2212] = {2101,"Nhân vật","","Viên Thiệu và Tào Tháo là hai thế lực lớn nhất hiện nay, đi, chúng ta đi xem xem làm sao đưa hoàng đế về Lạc Dương.","Viên Thiệu và Tào Tháo là hai thế lực lớn nhất hiện nay, đi, chúng ta đi xem xem làm sao đưa hoàng đế về Lạc Dương.",},
        [2213] = {2101,"Tinh Thái","","Chúa công, dường như ngài đã có dự tính trước, có phải đã đoán được suy nghĩ của Viên Thiệu và Tào Tháo không?","Chúa công, dường như ngài đã có dự tính trước, có phải đã đoán được suy nghĩ của Viên Thiệu và Tào Tháo không?",},
        [2214] = {2101,"Nhân vật","","Bảo mật!","Bảo mật!",},
        [2215] = {2101,"Ngân Bình","","Ha ha, chúa công, chúng ta chuyện vui chắc là không sao, đi!","Ha ha, chúa công, chúng ta chuyện vui chắc là không sao, đi!",},
        [2216] = {2102,"Thư Thụ","Viên Thiệu","Tướng quân dùng trung nghĩa cứu giúp thiên hạ. Nay hoàng thượng không chốn nương thân, tướng quân nên sớm nghênh đại giá, lập đô ở Nghiệp Thành.","Tướng quân dùng trung nghĩa cứu giúp thiên hạ. Nay hoàng thượng không chốn nương thân, tướng quân nên sớm nghênh đại giá, lập đô ở Nghiệp Thành.",},
        [2217] = {2102,"Thư Thụ","","Khống chế thiên tử để lệnh cho chư hầu, tập hợp binh mã để dẹp kẻ không thần phục. Lúc đó, còn ai chống cự được!","Khống chế thiên tử để lệnh cho chư hầu, tập hợp binh mã để dẹp kẻ không thần phục. Lúc đó, còn ai chống cự được!",},
        [2218] = {2102,"Viên Thiệu","","Quách Đồ, ngươi thấy thế nào?","Quách Đồ, ngươi thấy thế nào?",},
        [2219] = {2102,"Quách Đồ","","Hán thất suy vi đã lâu, muốn chấn hưng thì không phải dễ!","Hán thất suy vi đã lâu, muốn chấn hưng thì không phải dễ!",},
        [2220] = {2102,"Quách Đồ","","Nếu đón thiên tử, thì việc gì cũng phải dâng biểu thỉnh thị. Phục tùng mệnh lệnh sẽ mất đi quyền lực, không phục tùng thì mang tội kháng lệnh, đây không phải là cách hay.","Nếu đón thiên tử, thì việc gì cũng phải dâng biểu thỉnh thị. Phục tùng mệnh lệnh sẽ mất đi quyền lực, không phục tùng thì mang tội kháng lệnh, đây không phải là cách hay.",},
        [2221] = {2102,"Viên Thiệu","","Lúc đầu Đổng Trác tàn sát Lạc Dương để hại ta, Viên Thiệu ta đơn thương độc mã, lập nghiệp từ Bột Hải, hiệu triệu anh hùng thiên hạ thảo phạt Đổng Trác, thống lĩnh chư hầu.","Lúc đầu Đổng Trác tàn sát Lạc Dương để hại ta, Viên Thiệu ta đơn thương độc mã, lập nghiệp từ Bột Hải, hiệu triệu anh hùng thiên hạ thảo phạt Đổng Trác, thống lĩnh chư hầu.",},
        [2222] = {2102,"Viên Thiệu","","Nay ta đã làm chủ Ký Châu, tiêu diệt Hắc Sơn Hoàng Cân, thống nhất Hà Bắc nằm trong tầm tay, cớ sao phải đón thiên tử để bị sai khiến.","Nay ta đã làm chủ Ký Châu, tiêu diệt Hắc Sơn Hoàng Cân, thống nhất Hà Bắc nằm trong tầm tay, cớ sao phải đón thiên tử để bị sai khiến.",},
        [2223] = {2102,"Thư Thụ","","Tướng quân, thắng nằm ở chỗ không bỏ lỡ thời cơ, nếu chúng ta không ra tay trước, nhất định sẽ có người làm trước!","Tướng quân, thắng nằm ở chỗ không bỏ lỡ thời cơ, nếu chúng ta không ra tay trước, nhất định sẽ có người làm trước!",},
        [2224] = {2102,"Viên Thiệu","","Thư Thụ không cần nhiều lời, ý ta đã quyết!","Thư Thụ không cần nhiều lời, ý ta đã quyết!",},
        [2225] = {2103,"Tuân Úc","Tào Tháo","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.",},
        [2226] = {2103,"Tuân Úc","","Nay thiên tử gặp nạn, tướng quân phụng thiên tử để theo nguyện vọng của dân chúng, sách lược rất hay.","Nay thiên tử gặp nạn, tướng quân phụng thiên tử để theo nguyện vọng của dân chúng, sách lược rất hay.",},
        [2227] = {2103,"Đổng Chiêu","","Nay chư tướng ở Lạc Dương nhiều ý kiến, không nhất thiết phải phục tùng, muốn làm nên chuyện lớn, không những phải đón thiên tử, phải dời đô Hứa Xương mới là thượng sách!","Nay chư tướng ở Lạc Dương nhiều ý kiến, không nhất thiết phải phục tùng, muốn làm nên chuyện lớn, không những phải đón thiên tử, phải dời đô Hứa Xương mới là thượng sách!",},
        [2228] = {2103,"Tào Tháo","","Theo ý của ta, chuyện không nên chậm trễ, ta sẽ đi bẩm báo thiên tử để đến Hứa Đô.","Theo ý của ta, chuyện không nên chậm trễ, ta sẽ đi bẩm báo thiên tử để đến Hứa Đô.",},
        [2229] = {2104,"Tào Tháo","Hán Hiến Đế","Thần luôn một lòng muốn báo quốc. Nay Giác, Tị tội ác tày trời; thần có hơn 20 vạn tinh binh, dùng để diệt giặc. Bệ hạ hãy giữ gìn long thể, lấy xã tắc làm trọng!","Thần luôn một lòng muốn báo quốc. Nay Giác, Tị tội ác tày trời; thần có hơn 20 vạn tinh binh, dùng để diệt giặc. Bệ hạ hãy giữ gìn long thể, lấy xã tắc làm trọng!",},
        [2230] = {2104,"Hán Hiến Đế","","Vất vả cho ái khanh quá!","Vất vả cho ái khanh quá!",},
        [2231] = {2104,"Tào Tháo","","Bệ hạ, Đông Đô hoang phế đã lâu, không thể sửa chữa, khó vận chuyển lương thực. Hứa Đô thành quách cung thất, tiền của lương thực dồi dào. Thần dám xin dời đến Hứa Đô, mong bệ hạ chấp thuận.","Bệ hạ, Đông Đô hoang phế đã lâu, không thể sửa chữa, khó vận chuyển lương thực. Hứa Đô thành quách cung thất, tiền của lương thực dồi dào. Thần dám xin dời đến Hứa Đô, mong bệ hạ chấp thuận.",},
        [2232] = {2104,"Hán Hiến Đế","","Cuộc sống khó khăn, nay dời đô nữa e sẽ chê trách.","Cuộc sống khó khăn, nay dời đô nữa e sẽ chê trách.",},
        [2233] = {2104,"Tào Tháo","","Bệ hạ, chuyện không nên chậm trễ, xin bệ hạ mau dời đến Hứa Đô!","Bệ hạ, chuyện không nên chậm trễ, xin bệ hạ mau dời đến Hứa Đô!",},
        [2234] = {2104,"Hán Hiến Đế","","Vậy, sẽ theo ý khanh, dời đô đến Hứa Xương!","Vậy, sẽ theo ý khanh, dời đô đến Hứa Xương!",},
        [2235] = {2105,"Nhân vật","Ngân Bình","Ha ha, quả nhiên không ngoài dự đoán của ta, Tào Tháo mới là anh hùng dám khống chế thiên tử!","Ha ha, quả nhiên không ngoài dự đoán của ta, Tào Tháo mới là anh hùng dám khống chế thiên tử!",},
        [2236] = {2105,"Ngân Bình","","Viên Thiệu hồ đồ, không nghe lời của Thư Thụ.","Viên Thiệu hồ đồ, không nghe lời của Thư Thụ.",},
        [2237] = {2105,"Tinh Thái","","Chúa công, Tào Tháo khống chế thiên tử để lệnh cho chư hầu, từ đó đại quyền thuộc về Tào thị, thiên hạ Hán gia đã hữu danh vô thực, đến lúc đó Hiến Đế e rằng...","Chúa công, Tào Tháo khống chế thiên tử để lệnh cho chư hầu, từ đó đại quyền thuộc về Tào thị, thiên hạ Hán gia đã hữu danh vô thực, đến lúc đó Hiến Đế e rằng...",},
        [2238] = {2105,"Nhân vật","","Tinh Thái lo xa quá! Cục diện hiện giờ, Tào Tháo muốn thời cuộc ổn định, chắc sẽ không hại Hiến Đế, Hiến Đế kiếp này sống đến già cũng không có vấn đề gì.","Tinh Thái lo xa quá! Cục diện hiện giờ, Tào Tháo muốn thời cuộc ổn định, chắc sẽ không hại Hiến Đế, Hiến Đế kiếp này sống đến già cũng không có vấn đề gì.",},
        [2239] = {2105,"Tinh Thái","","Chúa công nói rất có lý.","Chúa công nói rất có lý.",},
        [2240] = {2105,"Nhân vật","","Hiến Đế phong ta làm Dương Uy Tướng, ta cũng hộ tống ông ấy về Hứa Đô! Đi, đi Hứa Xương!","Hiến Đế phong ta làm Dương Uy Tướng, ta cũng hộ tống ông ấy về Hứa Đô! Đi, đi Hứa Xương!",},
        [2241] = {2106,"Tào Tháo","Tuân Úc","Từ sau khi Chí Tài mất, chẳng có ai giúp ta tính việc. Vùng Nhữ, Dĩnh có nhiều kẻ sĩ giỏi, ai có thể kế nối được?","Từ sau khi Chí Tài mất, chẳng có ai giúp ta tính việc. Vùng Nhữ, Dĩnh có nhiều kẻ sĩ giỏi, ai có thể kế nối được?",},
        [2242] = {2106,"Tuân Úc","","Dĩnh Xuyên Quách Gia mưu lược, là kẻ sĩ giỏi.","Dĩnh Xuyên Quách Gia mưu lược, là kẻ sĩ giỏi.",},
        [2243] = {2106,"Nhân vật","","Quách Gia tiếng tăm lừng lẫy! Tào huynh, để đệ đi gặp hắn trước đã!","Quách Gia tiếng tăm lừng lẫy! Tào huynh, để đệ đi gặp hắn trước đã!",},
        [2244] = {2107,"Tuân Úc","Quách Gia","Phụng Hiếu, đây là tiểu anh hùng ta từng nhắc đến, hắn đã dẹp loạn Thập Thường Thị, Phụng Nghi Đình trảm sát Đổng Trác, đánh bại Lý Thôi Quách Tị, hộ tống thiên tử trở về Lạc Dương.","Phụng Hiếu, đây là tiểu anh hùng ta từng nhắc đến, hắn đã dẹp loạn Thập Thường Thị, Phụng Nghi Đình trảm sát Đổng Trác, đánh bại Lý Thôi Quách Tị, hộ tống thiên tử trở về Lạc Dương.",},
        [2245] = {2107,"Tuân Úc","","Thiên tử đã thông báo trước thiên hạ, phong hắn là Dương Uy Tướng. Hắn lại không thích công danh lợi lộc, chỉ nhận chức, không chịu nhận quân.","Thiên tử đã thông báo trước thiên hạ, phong hắn là Dương Uy Tướng. Hắn lại không thích công danh lợi lộc, chỉ nhận chức, không chịu nhận quân.",},
        [2246] = {2107,"Quách Gia","Nhân vật","Tiểu anh hùng là Dương Uy Tướng! Ngưỡng mộ đã lâu.","Tiểu anh hùng là Dương Uy Tướng! Ngưỡng mộ đã lâu.",},
        [2247] = {2107,"Quách Gia","","Tướng quân võ lược, tấm lòng độ lượng, thật là hào kiệt đương thế , thật khâm phục! Phụng Hiếu xin tặng Danh Tướng Lệnh này cho tướng quân!","Tướng quân võ lược, tấm lòng độ lượng, thật là hào kiệt đương thế , thật khâm phục! Phụng Hiếu xin tặng Danh Tướng Lệnh này cho tướng quân!",},
        [2248] = {2107,"Nhân vật","","Đa tạ, quá khen quá khen, ha ha ha!","Đa tạ, quá khen quá khen, ha ha ha!",},
        [2249] = {35,"Ngân Bình","Tinh Thái","Tinh Thái, ta biết rất nhiều bí mật của tam quốc, ngươi muốn biết không?","Tinh Thái, ta biết rất nhiều bí mật của tam quốc, ngươi muốn biết không?",},
        [2250] = {35,"Tinh Thái","","Muốn! Mau nói ta biết đi, có những bí mật gì?","Muốn! Mau nói ta biết đi, có những bí mật gì?",},
        [2251] = {35,"Ngân Bình","","Như là, Tào Tháo khi trẻ từng cùng Viên Thiệu đi cướp tân nương, Từ Thứ khi trẻ thích luyện kiếm hành hiệp trượng nghĩa, Viên Thuật là cha của tất cả mọi người...","Như là, Tào Tháo khi trẻ từng cùng Viên Thiệu đi cướp tân nương, Từ Thứ khi trẻ thích luyện kiếm hành hiệp trượng nghĩa, Viên Thuật là cha của tất cả mọi người...",},
        [2252] = {35,"Tinh Thái","","Ha ha, thì ra các danh tướng đểu có câu chuyện hay như vậy, đi đâu để xem được những câu chuyện này?","Ha ha, thì ra các danh tướng đểu có câu chuyện hay như vậy, đi đâu để xem được những câu chuyện này?",},
        [2253] = {35,"Ngân Bình","","Trong phó bản Danh Tướng! Khiêu chiến phó bản Danh Tướng, có thể kết bạn nhiều danh tướng hơn, tìm hiểu câu chuyện đằng sau danh tướng.","Trong phó bản Danh Tướng! Khiêu chiến phó bản Danh Tướng, có thể kết bạn nhiều danh tướng hơn, tìm hiểu câu chuyện đằng sau danh tướng.",},
        [2254] = {35,"Tinh Thái","","Chúa công, vậy chúng ta mau đi khiêu chiến phó bản Danh Tướng đầu tiên đi.","Chúa công, vậy chúng ta mau đi khiêu chiến phó bản Danh Tướng đầu tiên đi.",},
        [2255] = {36,"Ngân Bình","Tinh Thái","Chúa công, lần đầu vượt ải phó bản Danh Tướng, được nhận nhiều Nguyên Bảo thưởng.","Chúa công, lần đầu vượt ải phó bản Danh Tướng, được nhận nhiều Nguyên Bảo thưởng.",},
        [2256] = {36,"Tinh Thái","","Khiêu chiến phó bản Danh Tướng còn được nhận Tướng Hồn, Tướng Hồn có thể đến Tiệm Võ Tướng đổi Tướng phẩm chất cao!","Khiêu chiến phó bản Danh Tướng còn được nhận Tướng Hồn, Tướng Hồn có thể đến Tiệm Võ Tướng đổi Tướng phẩm chất cao!",},
        [2257] = {36,"Ngân Bình","","Chúa công nhớ mỗi ngày phải đến khiêu chiến phó bản Danh Tướng nhé!","Chúa công nhớ mỗi ngày phải đến khiêu chiến phó bản Danh Tướng nhé!",},
        [2258] = {37,"Nhân vật","Tinh Thái","Tinh Thái, ta lấy được nhiều Tướng Lam và Tướng Lục, phẩm chất thấp quá hình như không làm gì được!","Tinh Thái, ta lấy được nhiều Tướng Lam và Tướng Lục, phẩm chất thấp quá hình như không làm gì được!",},
        [2259] = {37,"Tinh Thái","","Chúa công, Tướng Lục trở lên có thể #thu hồi#, thu hồi được nhận tài nguyên rất có ích!","Chúa công, Tướng Lục trở lên có thể #thu hồi#, thu hồi được nhận tài nguyên rất có ích!",},
        [2260] = {37,"Tinh Thái","","Bây giờ Tinh Thái sẽ nói ngài biết cách #thu hồi# Tướng.","Bây giờ Tinh Thái sẽ nói ngài biết cách #thu hồi# Tướng.",},
        [2261] = {38,"Tinh Thái","Nhân vật","Chúa công nhìn thấy rồi chứ, Tướng Hồn nhận khi thu hồi Tướng có thể đến Tiệm Võ Tướng mua Tướng và tài nguyên quý khác.","Chúa công nhìn thấy rồi chứ, Tướng Hồn nhận khi thu hồi Tướng có thể đến Tiệm Võ Tướng mua Tướng và tài nguyên quý khác.",},
        [2262] = {38,"Nhân vật","","Thấy rồi! Tướng Hồn có thể mua Tướng, Đột Phá Đơn và EXP Tướng. Ha ha, ta muốn thu hồi toàn bộ Tướng Lam và Tướng Lục để đổi Tướng Hồn!","Thấy rồi! Tướng Hồn có thể mua Tướng, Đột Phá Đơn và EXP Tướng. Ha ha, ta muốn thu hồi toàn bộ Tướng Lam và Tướng Lục để đổi Tướng Hồn!",},
        [2263] = {38,"Tinh Thái","","Chúa công, Tướng Tím và Tướng Cam đã dưỡng thành nếu không cần nữa cũng có thể thu hồi, đồng thời hoàn trả toàn bộ tài nguyên dưỡng thành!","Chúa công, Tướng Tím và Tướng Cam đã dưỡng thành nếu không cần nữa cũng có thể thu hồi, đồng thời hoàn trả toàn bộ tài nguyên dưỡng thành!",},
        [2264] = {38,"Nhân vật","","Ồ, hay vậy sao?! #Hoàn trả toàn bộ# tài nguyên dưỡng thành!","Ồ, hay vậy sao?! #Hoàn trả toàn bộ# tài nguyên dưỡng thành!",},
        [2265] = {38,"Tinh Thái","","Phải! Ngoài Tướng ra, trang bị, Thần Binh để có thể thu hồi, chúa công sau này nhớ phải tới thu hồi.","Phải! Ngoài Tướng ra, trang bị, Thần Binh để có thể thu hồi, chúa công sau này nhớ phải tới thu hồi.",},
        [2266] = {38,"Nhân vật","","Cảm ơn Tinh Thái, ta sẽ nhớ!","Cảm ơn Tinh Thái, ta sẽ nhớ!",},
        [2267] = {39,"Ngân Bình","Nhân vật","Chúa công, ải phía tây thường có #Dị Tộc Xâm Lược#, chúng thường nhân lúc chúng ta chiến đấu và càn quét ở chiến tuyến để đến xâm nhập.","Chúa công, ải phía tây thường có #Dị Tộc Xâm Lược#, chúng thường nhân lúc chúng ta chiến đấu và càn quét ở chiến tuyến để đến xâm nhập.",},
        [2268] = {39,"Nhân vật","","Đáng ghét, bọn Dị Tộc này thật gian xảo!","Đáng ghét, bọn Dị Tộc này thật gian xảo!",},
        [2269] = {39,"Ngân Bình","","Chúa công đừng lo, đánh Dị Tộc là cơ hội chiến đấu tốt! Vì đánh Dị Tộc có thể nhận tài nguyên Thần Binh.","Chúa công đừng lo, đánh Dị Tộc là cơ hội chiến đấu tốt! Vì đánh Dị Tộc có thể nhận tài nguyên Thần Binh.",},
        [2270] = {39,"Nhân vật","","Thần Binh? Thần Binh là gì? Nghe có vẻ rất lợi hại.","Thần Binh? Thần Binh là gì? Nghe có vẻ rất lợi hại.",},
        [2271] = {39,"Ngân Bình","","Mỗi Tướng đều có #Thần Binh riêng#, như Thần Binh của Quan Vũ cha ta là Thanh Long Đao, Thần Binh của Đại Kiều tỷ tỷ là Liên Hoa Tản.","Mỗi Tướng đều có #Thần Binh riêng#, như Thần Binh của Quan Vũ cha ta là Thanh Long Đao, Thần Binh của Đại Kiều tỷ tỷ là Liên Hoa Tản.",},
        [2272] = {39,"Ngân Bình","","Chúa công, đánh thêm vài lần Dị Tộc là có thể đến Tiệm Thần Binh mua Thần Binh.","Chúa công, đánh thêm vài lần Dị Tộc là có thể đến Tiệm Thần Binh mua Thần Binh.",},
        [2273] = {39,"Nhân vật","","Ta rất nóng lòng muốn nhận Thần Binh! Đi, đi đánh Dị Tộc!","Ta rất nóng lòng muốn nhận Thần Binh! Đi, đi đánh Dị Tộc!",},
        [2274] = {40,"Tinh Thái","Nhân vật","Ồ, chúc mừng chúa công tăng đến Lv58! Lv58 có thể đeo #Cẩm Nang# rồi!","Ồ, chúc mừng chúa công tăng đến Lv58! Lv58 có thể đeo #Cẩm Nang# rồi!",},
        [2275] = {40,"Nhân vật","","#Cẩm Nang#? Cẩm Nang có tác dụng gì?","#Cẩm Nang#? Cẩm Nang có tác dụng gì?",},
        [2276] = {40,"Tinh Thái","","Cẩm Nang có thể cường hóa đặc tính của Tướng, như định vị Ngân Bình là sát thương, đeo Cẩm Nang có thể tăng tấn công; định vị Điêu Thuyền là giảm Nộ, đeo Cẩm Nang có thể giảm Nộ.","Cẩm Nang có thể cường hóa đặc tính của Tướng, như định vị Ngân Bình là sát thương, đeo Cẩm Nang có thể tăng tấn công; định vị Điêu Thuyền là giảm Nộ, đeo Cẩm Nang có thể giảm Nộ.",},
        [2277] = {40,"Tinh Thái","","Nói cho dễ hiểu, Cẩm Nang cung cấp nhiều đặc tính hơn để kết hợp và chọn, đeo Cẩm Nang có thể giúp Tướng lợi hại hơn.","Nói cho dễ hiểu, Cẩm Nang cung cấp nhiều đặc tính hơn để kết hợp và chọn, đeo Cẩm Nang có thể giúp Tướng lợi hại hơn.",},
        [2278] = {40,"Nhân vật","","Nghe có vẻ Cẩm Nang rất lợi hại.","Nghe có vẻ Cẩm Nang rất lợi hại.",},
        [2279] = {40,"Tinh Thái","","Phải! Chúa công, để Tinh Thái đeo cho ngài 1 Cẩm Nang thử nhé.","Phải! Chúa công, để Tinh Thái đeo cho ngài 1 Cẩm Nang thử nhé.",},
        [2280] = {41,"Tinh Thái","Nhân vật","Lợi hại thật! Sau khi đeo Cẩm Nang Ngân Bình, tấn công của chúa công tăng 1000 điểm!","Lợi hại thật! Sau khi đeo Cẩm Nang Ngân Bình, tấn công của chúa công tăng 1000 điểm!",},
        [2281] = {41,"Nhân vật","","Đây không chỉ là Cẩm Nang Tướng Tím, Cẩm Nang Tướng Cam chẳng phải lợi hại hơn sao?","Đây không chỉ là Cẩm Nang Tướng Tím, Cẩm Nang Tướng Cam chẳng phải lợi hại hơn sao?",},
        [2282] = {41,"Tinh Thái","","Chúa công thật thông minh! Tư chất của Cẩm Nang càng cao, đặc tính càng lợi hại.","Chúa công thật thông minh! Tư chất của Cẩm Nang càng cao, đặc tính càng lợi hại.",},
        [2283] = {41,"Nhân vật","","Cẩm Nang lợi hại như vậy, có thể nhận Cẩm Nang ở đâu?","Cẩm Nang lợi hại như vậy, có thể nhận Cẩm Nang ở đâu?",},
        [2284] = {41,"Tinh Thái","","Chúa công, Cẩm Nang giống với Ấn Quân Hàm, nhận qua đấu giá! Hoạt động BOSS, Thí Luyện Quân Đoàn, Vấn Đáp đều có xác suất nhận Cẩm Nang.","Chúa công, Cẩm Nang giống với Ấn Quân Hàm, nhận qua đấu giá! Hoạt động BOSS, Thí Luyện Quân Đoàn, Vấn Đáp đều có xác suất nhận Cẩm Nang.",},
        [2285] = {41,"Nhân vật","","Ồ, chỉ cần thường xuyên tham gia hoạt động Quân Đoàn và đấu giá là nhận được Cẩm Nang.","Ồ, chỉ cần thường xuyên tham gia hoạt động Quân Đoàn và đấu giá là nhận được Cẩm Nang.",},
        [2286] = {41,"Tinh Thái","","Phải rồi, chúa công có thể đến Cẩm Nang Đồ Giám xem xem, nghiên cứu cách kết hợp Cẩm Nang.","Phải rồi, chúa công có thể đến Cẩm Nang Đồ Giám xem xem, nghiên cứu cách kết hợp Cẩm Nang.",},
        [2287] = {41,"Nhân vật","","Biết rồi, cảm ơn Tinh Thái!","Biết rồi, cảm ơn Tinh Thái!",},
        [2288] = {41,"Tinh Thái","","Chúa công khách sáo quá! Tinh Thái muốn được bảo vệ chúa công mãi mãi!","Chúa công khách sáo quá! Tinh Thái muốn được bảo vệ chúa công mãi mãi!",},
        [2289] = {42,"Ngân Bình","Nhân vật","Chúa công đã Lv70, thực lực uy chấn thiên hạ, lực chiến cao, ngay cả Thần Thú cũng muốn sát cánh cùng ngài chiến đấu!","Chúa công đã Lv70, thực lực uy chấn thiên hạ, lực chiến cao, ngay cả Thần Thú cũng muốn sát cánh cùng ngài chiến đấu!",},
        [2290] = {42,"Nhân vật","","Thần Thú? Là Thanh Long, Bạch Hổ, Chu Tước, Huyền Vũ sao?","Thần Thú? Là Thanh Long, Bạch Hổ, Chu Tước, Huyền Vũ sao?",},
        [2291] = {42,"Ngân Bình","","Phải, ở Thần Châu, từ xưa lưu truyền các truyền thuyết về Thần Thú, chỉ người có mệnh trời và cường giả thật sự mới thấy được bóng họ, Quan Vũ cha ta từng được Thanh Long che chở.","Phải, ở Thần Châu, từ xưa lưu truyền các truyền thuyết về Thần Thú, chỉ người có mệnh trời và cường giả thật sự mới thấy được bóng họ, Quan Vũ cha ta từng được Thanh Long che chở.",},
        [2292] = {42,"Nhân vật","","Chả trách nhị ca lợi hại như vậy, thì ra là được Thần Thú che chở! Ta cũng muốn có Thần Thú!","Chả trách nhị ca lợi hại như vậy, thì ra là được Thần Thú che chở! Ta cũng muốn có Thần Thú!",},
        [2293] = {42,"Ngân Bình","","Chúa công đừng lo, chúng tôi đã chuẩn bị cho ngài Thần Thú thứ nhất, bây giờ hãy để Ngân Bình đưa ngài đi xem.","Chúa công đừng lo, chúng tôi đã chuẩn bị cho ngài Thần Thú thứ nhất, bây giờ hãy để Ngân Bình đưa ngài đi xem.",},
        [2294] = {43,"Ngân Bình","Nhân vật","Chúa công, đây là Thần Thú đầu tiên của ngài!","Chúa công, đây là Thần Thú đầu tiên của ngài!",},
        [2295] = {43,"Nhân vật","","Gấu Trúc Lớn xem ra rất giỏi, Gấu Trúc Nhỏ dễ thương!","Gấu Trúc Lớn xem ra rất giỏi, Gấu Trúc Nhỏ dễ thương!",},
        [2296] = {43,"Ngân Bình","","Chúa công, Thần Thú ở vị trí thứ 7 khi chiến đấu, nghĩa là có thêm 1 Tướng lợi hại.","Chúa công, Thần Thú ở vị trí thứ 7 khi chiến đấu, nghĩa là có thêm 1 Tướng lợi hại.",},
        [2297] = {43,"Nhân vật","","Ngân Bình, Gấu Trúc chỉ là tư chất Lam, nếu muốn nhận Thần Thú lợi hại hơn thì phải đi đâu nhận?","Ngân Bình, Gấu Trúc chỉ là tư chất Lam, nếu muốn nhận Thần Thú lợi hại hơn thì phải đi đâu nhận?",},
        [2298] = {43,"Ngân Bình","","Chúa công có thể đến Tiệm Thần Thú dùng #Thú Hồn# đổi Thần Thú.","Chúa công có thể đến Tiệm Thần Thú dùng #Thú Hồn# đổi Thần Thú.",},
        [2299] = {43,"Nhân vật","","#Thú Hồn# đó làm sao nhận được?","#Thú Hồn# đó làm sao nhận được?",},
        [2300] = {43,"Ngân Bình","","Gia viên Thần Thú có thể tạo ra Thú Hồn, hãy để Ngân Bình đưa ngài đi xem.","Gia viên Thần Thú có thể tạo ra Thú Hồn, hãy để Ngân Bình đưa ngài đi xem.",},
        [2301] = {44,"Ngân Bình","Nhân vật","Chúa công, nhìn xem, đây là gia viên Thần Thú.","Chúa công, nhìn xem, đây là gia viên Thần Thú.",},
        [2302] = {44,"Ngân Bình","","Trong gia viên Thần Thú có 1 Cây Thần, cây này hút linh khí trời đất, ngưng tụ thành Thú Hồn thu hút Thần Thú. Cây Thần cấp càng cao, Thú Hồn càng nhiều.","Trong gia viên Thần Thú có 1 Cây Thần, cây này hút linh khí trời đất, ngưng tụ thành Thú Hồn thu hút Thần Thú. Cây Thần cấp càng cao, Thú Hồn càng nhiều.",},
        [2303] = {44,"Nhân vật","","Ta hiểu rồi! Tăng cấp Cây Thần có thể nhận nhiều Thú Hồn! Có Thú Hồn rồi có thể đến Tiệm Thần Thú đổi Thần Thú cao cấp hơn.","Ta hiểu rồi! Tăng cấp Cây Thần có thể nhận nhiều Thú Hồn! Có Thú Hồn rồi có thể đến Tiệm Thần Thú đổi Thần Thú cao cấp hơn.",},
        [2304] = {44,"Ngân Bình","","Chúa công thật lợi hại, nhanh chóng hiểu ra, chúa công hãy nhớ tăng cấp cho Cây Thần!","Chúa công thật lợi hại, nhanh chóng hiểu ra, chúa công hãy nhớ tăng cấp cho Cây Thần!",},
        [2305] = {44,"Nhân vật","","Ta muốn Thần Thú tốt nhất trên thế giới!","Ta muốn Thần Thú tốt nhất trên thế giới!",},
        [2306] = {44,"Ngân Bình","","Chúa công cố gắng sẽ sớm trở thành vấn đỉnh Tam Quốc!","Chúa công cố gắng sẽ sớm trở thành vấn đỉnh Tam Quốc!",},
        [2307] = {2201,"Tào Tháo","Tuân Úc","Lưu Bị đóng quân ở Từ Châu. Lữ Bố bị thất bại, trốn tại Tiểu Bái, nếu hai người họ cùng nhau tấn công, quả là vấn đề lớn, các vị có kế nào hay để đối phó?","Lưu Bị đóng quân ở Từ Châu. Lữ Bố bị thất bại, trốn tại Tiểu Bái, nếu hai người họ cùng nhau tấn công, quả là vấn đề lớn, các vị có kế nào hay để đối phó?",},
        [2308] = {2201,"Tuân Úc","","Hiện Hứa Đô đã ổn định, không cần xuất binh. Ta có một kế, có thể khiến Lưu Bị diệt Lữ Bố.","Hiện Hứa Đô đã ổn định, không cần xuất binh. Ta có một kế, có thể khiến Lưu Bị diệt Lữ Bố.",},
        [2309] = {2201,"Tào Tháo","","Thám tử hồi báo, kế này đã bị Lưu Bị hóa giải, Lưu Bị chưa diệt Lữ Bố.","Thám tử hồi báo, kế này đã bị Lưu Bị hóa giải, Lưu Bị chưa diệt Lữ Bố.",},
        [2310] = {2201,"Tuân Úc","","Chúa công hãy bảo Lưu Bị tấn công Viên Thuật, sau đó sai báo tin cho Viên Thuật nói Lưu Bị muốn giành Nam Quận, Viên Thuật sẽ tức giận mà tấn công. Hai bên tấn công lẫn nhau, Lữ Bố sẽ thay lòng.","Chúa công hãy bảo Lưu Bị tấn công Viên Thuật, sau đó sai báo tin cho Viên Thuật nói Lưu Bị muốn giành Nam Quận, Viên Thuật sẽ tức giận mà tấn công. Hai bên tấn công lẫn nhau, Lữ Bố sẽ thay lòng.",},
        [2311] = {2201,"Tào Tháo","","Kế hay! Hãy làm theo ý của người.","Kế hay! Hãy làm theo ý của người.",},
        [2312] = {2201,"Nhân vật","","Nguy rồi, Tào Tháo muốn tấn công đại ca! Chúng ta phải mau tìm đại ca, nếu không sẽ trúng kế của Tào Tháo!","Nguy rồi, Tào Tháo muốn tấn công đại ca! Chúng ta phải mau tìm đại ca, nếu không sẽ trúng kế của Tào Tháo!",},
        [2313] = {2202,"Viên Thuật","Lưu Bị","Lưu Bị chỉ là hạng dân thường, hiện đã chiếm Từ Châu. Ta đã định thảo phạt hắn ta, không ngờ hắn ta lại dám tấn công ta trước!","Lưu Bị chỉ là hạng dân thường, hiện đã chiếm Từ Châu. Ta đã định thảo phạt hắn ta, không ngờ hắn ta lại dám tấn công ta trước!",},
        [2314] = {2202,"Lưu Bị","","Ta phụng lệnh thiên tử, thảo phạt kẻ tạo phản. Ngươi dám chống lại, tội đáng bị diệt!","Ta phụng lệnh thiên tử, thảo phạt kẻ tạo phản. Ngươi dám chống lại, tội đáng bị diệt!",},
        [2315] = {2202,"Nhân vật","","Đại ca, Viên Thuật rất ngông cuồng, để ta cho hắn biết tay!","Đại ca, Viên Thuật rất ngông cuồng, để ta cho hắn biết tay!",},
        [2316] = {2203,"Nhân vật","","Đại ca, đừng đánh Viên Thuật, đó là kế Tào Tháo.","Đại ca, đừng đánh Viên Thuật, đó là kế Tào Tháo.",},
        [2317] = {2203,"Lưu Bị","","Tuy là kế, nhưng không thể làm trái. Nhị đệ tam đệ, hai người ai có thể giữ thành?","Tuy là kế, nhưng không thể làm trái. Nhị đệ tam đệ, hai người ai có thể giữ thành?",},
        [2318] = {2203,"Trương Phi","","Tiểu đệ nguyện giữ thành.","Tiểu đệ nguyện giữ thành.",},
        [2319] = {2203,"Lưu Bị","","Ngươi không thể giữ được thành. Vì sau khi uống rượu ngươi sẽ đánh binh sĩ, làm việc không phân nặng nhẹ, ta không yên tâm.","Ngươi không thể giữ được thành. Vì sau khi uống rượu ngươi sẽ đánh binh sĩ, làm việc không phân nặng nhẹ, ta không yên tâm.",},
        [2320] = {2203,"Trương Phi","","Đệ sẽ không uống rượu, không đánh binh sĩ, mọi việc đều nghe khuyên can.","Đệ sẽ không uống rượu, không đánh binh sĩ, mọi việc đều nghe khuyên can.",},
        [2321] = {2203,"Nhân vật","","Tam ca hãy cố gắng giữ thành, ta giúp nhị ca đại ca đánh Viên Thuật.","Tam ca hãy cố gắng giữ thành, ta giúp nhị ca đại ca đánh Viên Thuật.",},
        [2322] = {2204,"Nhân vật","Lưu Bị","Lữ Bố thật đáng hận, dám nhân lúc chúng ta xuất binh, mà giành Từ Châu.","Lữ Bố thật đáng hận, dám nhân lúc chúng ta xuất binh, mà giành Từ Châu.",},
        [2323] = {2204,"Lưu Bị","","Tứ đệ đừng tức giận! Lữ Bố đã đưa hai vị phu nhân và gia quyến về, còn tặng nhiều lương thực. Việc này đúng là do tam đệ sai sót, ta và Phụng Tiên đã bàn bạc, hắn từ bỏ Từ Châu, ta chiếm Tiểu Bái.","Tứ đệ đừng tức giận! Lữ Bố đã đưa hai vị phu nhân và gia quyến về, còn tặng nhiều lương thực. Việc này đúng là do tam đệ sai sót, ta và Phụng Tiên đã bàn bạc, hắn từ bỏ Từ Châu, ta chiếm Tiểu Bái.",},
        [2324] = {2204,"Lưu Bị","","Nếu ta và Lữ Bố đánh nhau sẽ trúng kế Tào Tháo, để Tào Tháo và Viên Thuật cùng nhau hưởng lợi.","Nếu ta và Lữ Bố đánh nhau sẽ trúng kế Tào Tháo, để Tào Tháo và Viên Thuật cùng nhau hưởng lợi.",},
        [2325] = {2204,"Nhân vật","","Tuy là vậy, nhưng ta vẫn phải tìm Lữ Bố, để lấy lại công bằng cho huynh!","Tuy là vậy, nhưng ta vẫn phải tìm Lữ Bố, để lấy lại công bằng cho huynh!",},
        [2326] = {2205,"Kỷ Linh","Nhân vật","Lưu Bị đang ở đâu? Chúa công ta Viên Thuật đã điều động 10 vạn binh mã đến bắt Lưu Bị!","Lưu Bị đang ở đâu? Chúa công ta Viên Thuật đã điều động 10 vạn binh mã đến bắt Lưu Bị!",},
        [2327] = {2205,"Nhân vật","","Kỷ Linh, đừng ngông cuồng! Ngươi chỉ là thủ hại bại tướng của ta! Đại ca ta vốn là công thần diệt Hoàng Cân, ngươi có gì ghê gớm, dám tấn công đại ca ta?","Kỷ Linh, đừng ngông cuồng! Ngươi chỉ là thủ hại bại tướng của ta! Đại ca ta vốn là công thần diệt Hoàng Cân, ngươi có gì ghê gớm, dám tấn công đại ca ta?",},
        [2328] = {2205,"Lữ Bố","","Tiểu anh hùng, hãy nghe ta nói!","Tiểu anh hùng, hãy nghe ta nói!",},
        [2329] = {2205,"Nhân vật","","Ngươi giành Từ Châu của đại ca ta, ta sẽ cho ngươi biết tay!","Ngươi giành Từ Châu của đại ca ta, ta sẽ cho ngươi biết tay!",},
        [2330] = {2206,"Lữ Bố","Nhân vật","Tính khí của tiểu anh hùng ta rất thích, nhưng ta khuyên hai ngươi đừng đánh nhau.","Tính khí của tiểu anh hùng ta rất thích, nhưng ta khuyên hai ngươi đừng đánh nhau.",},
        [2331] = {2206,"Lữ Bố","","Nếu ta có thể bắn một mũi tên trúng vào cây kích, thì hai ngươi hãy rút binh. Nếu không trúng, hãy chuẩn bị chiến đấu.","Nếu ta có thể bắn một mũi tên trúng vào cây kích, thì hai ngươi hãy rút binh. Nếu không trúng, hãy chuẩn bị chiến đấu.",},
        [2332] = {2206,"Lữ Bố","","Xem đây! (Lữ Bố lấy cung tên bắn một phát trúng ngay Họa Kích) Ha ha, ông trời cũng có ý bảo các ngươi rút binh.","Xem đây! (Lữ Bố lấy cung tên bắn một phát trúng ngay Họa Kích) Ha ha, ông trời cũng có ý bảo các ngươi rút binh.",},
        [2333] = {2206,"Nhân vật","","Quả nhiên là Lữ Bố bách phát bách trúng, việc này kết thúc tại đây. Kỷ Linh, nếu ngươi đến xâm phạm, ta quyết không tha.","Quả nhiên là Lữ Bố bách phát bách trúng, việc này kết thúc tại đây. Kỷ Linh, nếu ngươi đến xâm phạm, ta quyết không tha.",},
        [2334] = {2206,"Kỷ Linh","","Không dám! Ta sẽ rút binh!","Không dám! Ta sẽ rút binh!",},
        [2335] = {2206,"Lữ Bố","","Huyền Đức, Danh Tướng Lệnh tặng cho ngươi, Phụng Tiên sẽ hoàn trả Từ Châu cho ngươi!","Huyền Đức, Danh Tướng Lệnh tặng cho ngươi, Phụng Tiên sẽ hoàn trả Từ Châu cho ngươi!",},
        [2336] = {2206,"Lưu Bị","","Không được. Lưu Bị nhường cho Phụng Tiên đã lâu, đóng quân tại Tiểu Bái, cùng nhau phối hợp chống lại ngoại xâm.","Không được. Lưu Bị nhường cho Phụng Tiên đã lâu, đóng quân tại Tiểu Bái, cùng nhau phối hợp chống lại ngoại xâm.",},
        [2337] = {2206,"Lưu Bị","","Tứ đệ, Danh Tướng Lệnh hãy nhận lấy.","Tứ đệ, Danh Tướng Lệnh hãy nhận lấy.",},
        [2338] = {2206,"Nhân vật","","Đa tạ đại ca, sau này có ta, Viên Thuật sẽ không dám đến xâm phạm!","Đa tạ đại ca, sau này có ta, Viên Thuật sẽ không dám đến xâm phạm!",},
        [2339] = {2301,"Kỷ Linh","Viên Thuật","Chúa công, Lữ Bố bắn tên ép ta rút quân.","Chúa công, Lữ Bố bắn tên ép ta rút quân.",},
        [2340] = {2301,"Viên Thuật","","Ta đã tặng Lữ Bố rất nhiều lương thực, hắn ta đã nhận mà không giúp, còn dám bênh Lưu Bị!","Ta đã tặng Lữ Bố rất nhiều lương thực, hắn ta đã nhận mà không giúp, còn dám bênh Lưu Bị!",},
        [2341] = {2301,"Viên Thuật","","Người đâu, ta sẽ cho binh tấn công Lưu Bị, tiện thể diệt Lữ Bố.","Người đâu, ta sẽ cho binh tấn công Lưu Bị, tiện thể diệt Lữ Bố.",},
        [2342] = {2301,"Kỷ Linh","","Chúa công không thể làm thế. Lữ Bố dũng mãnh hơn người, đang ở vùng Từ Châu, nếu Lữ Bố cùng Lưu Bị liên thủ sẽ rất nguy hiểm","Chúa công không thể làm thế. Lữ Bố dũng mãnh hơn người, đang ở vùng Từ Châu, nếu Lữ Bố cùng Lưu Bị liên thủ sẽ rất nguy hiểm",},
        [2343] = {2301,"Kỷ Linh","","Ta nghe nói Lữ Bố có một đứa con gái. Chúa công đã có con trai, hãy cầu thân với Lữ Bố.","Ta nghe nói Lữ Bố có một đứa con gái. Chúa công đã có con trai, hãy cầu thân với Lữ Bố.",},
        [2344] = {2301,"Viên Thuật","","Kế hay, sau này ta và Lữ Bố trở thành người nhà, xưng đế thiên hạ chỉ là chuyện sớm muộn.","Kế hay, sau này ta và Lữ Bố trở thành người nhà, xưng đế thiên hạ chỉ là chuyện sớm muộn.",},
        [2345] = {2302,"Lữ Bố","Nghiêm Thị","Viên Thuật phái người đến cầu thân, các ngươi thấy sao?","Viên Thuật phái người đến cầu thân, các ngươi thấy sao?",},
        [2346] = {2302,"Nghiêm Thị","","Viên Công ở vùng Hoài Nam, nhiều lương thực và binh mã, sớm muộn sẽ thành hoàng đế, ngài chỉ có một người con trai, con gái chúng ta lấy hắn, sau này sẽ trở thành hoàng hậu.","Viên Công ở vùng Hoài Nam, nhiều lương thực và binh mã, sớm muộn sẽ thành hoàng đế, ngài chỉ có một người con trai, con gái chúng ta lấy hắn, sau này sẽ trở thành hoàng hậu.",},
        [2347] = {2302,"Trần Cung","","Hiện chư hầu trong thiên hạ đều tranh giành nhau, phía bắc chỉ có người và Viên Công, các chư hầu chắc chắn sẽ đố kỵ. Nếu đưa Linh Thư đến Thọ Xuyến, rồi sau chọn ngày lành thành thân.","Hiện chư hầu trong thiên hạ đều tranh giành nhau, phía bắc chỉ có người và Viên Công, các chư hầu chắc chắn sẽ đố kỵ. Nếu đưa Linh Thư đến Thọ Xuyến, rồi sau chọn ngày lành thành thân.",},
        [2348] = {2302,"Lữ Bố","","Nếu vậy hãy để Lữ Linh Thư gả qua. Người đâu, hãy viết thư trả lời Viên Thuật, Lữ Bố ta đồng ý gả con gái cho hắn ta.","Nếu vậy hãy để Lữ Linh Thư gả qua. Người đâu, hãy viết thư trả lời Viên Thuật, Lữ Bố ta đồng ý gả con gái cho hắn ta.",},
        [2349] = {2303,"Trần Khuê","Lữ Bố","Ta đến chia buồn.","Ta đến chia buồn.",},
        [2350] = {2303,"Lữ Bố","","Sao lại nói thế?","Sao lại nói thế?",},
        [2351] = {2303,"Trần Khuê","","Viên Thuật có ý xưng đế, đợi hắn ta xưng đế, sẽ trở thành phản tặc trong thiên hạ. Nếu giờ người kết thân với phản tặc, lúc đó thiên hạ sẽ không có chỗ cho người dung thân.","Viên Thuật có ý xưng đế, đợi hắn ta xưng đế, sẽ trở thành phản tặc trong thiên hạ. Nếu giờ người kết thân với phản tặc, lúc đó thiên hạ sẽ không có chỗ cho người dung thân.",},
        [2352] = {2303,"Nhân vật","","Cuối cùng cũng đến kịp! Lữ Bố, nghe nói ngươi định gả con gái cho con trai của Viên Thuật đúng không, tuyệt đối đừng làm thế!","Cuối cùng cũng đến kịp! Lữ Bố, nghe nói ngươi định gả con gái cho con trai của Viên Thuật đúng không, tuyệt đối đừng làm thế!",},
        [2353] = {2303,"Lữ Bố","","Tiểu anh hùng sao lại nói thế?","Tiểu anh hùng sao lại nói thế?",},
        [2354] = {2303,"Nhân vật","","Lúc trước người từng nhờ Viên Thuật, nhưng hắn luôn bài xích người, người quên rồi sao? Tiểu nhân như Viên Thuật, tuyệt đối không thể đến gần, nếu không sẽ mang họa.","Lúc trước người từng nhờ Viên Thuật, nhưng hắn luôn bài xích người, người quên rồi sao? Tiểu nhân như Viên Thuật, tuyệt đối không thể đến gần, nếu không sẽ mang họa.",},
        [2355] = {2303,"Lữ Bố","","Ta tin người! Trần Cung đã hại ta! Người đâu, mau đuổi theo Linh Thư!","Ta tin người! Trần Cung đã hại ta! Người đâu, mau đuổi theo Linh Thư!",},
        [2356] = {2303,"Nhân vật","","Việc này không thể chậm trễ, hãy để ta!","Việc này không thể chậm trễ, hãy để ta!",},
        [2357] = {2304,"Điêu Thuyền","Nhân vật","Phụng Tiên, tiểu anh hùng đã đuổi kịp Linh Thư!","Phụng Tiên, tiểu anh hùng đã đuổi kịp Linh Thư!",},
        [2358] = {2304,"Nhân vật","","Việc Điêu Thuyền cô nương nhờ, tại hạ chắc chắn sẽ cố gắng!","Việc Điêu Thuyền cô nương nhờ, tại hạ chắc chắn sẽ cố gắng!",},
        [2359] = {2304,"Điêu Thuyền","","Phụng Tiên, thiếp cho rằng, việc hôn nhân tuy là do ý phụ mẫu, nhưng ý nguyện của Linh Thư cũng không thể bỏ qua. Nếu không sẽ hại đời của con.","Phụng Tiên, thiếp cho rằng, việc hôn nhân tuy là do ý phụ mẫu, nhưng ý nguyện của Linh Thư cũng không thể bỏ qua. Nếu không sẽ hại đời của con.",},
        [2360] = {2304,"Lữ Bố","","Lời của Thuyền Nhi rất đúng!","Lời của Thuyền Nhi rất đúng!",},
        [2361] = {2304,"Nhân vật","","Lữ Bố, ngươi hiện là hào kiệt một phương, không cần dùng con gái để làm việc này.","Lữ Bố, ngươi hiện là hào kiệt một phương, không cần dùng con gái để làm việc này.",},
        [2362] = {2304,"Lữ Bố","","Nói đúng! Lữ Bố ta hổ bộ Giang Hoài, không hề sợ ai! Con gái của Lữ Bố đương nhiên muốn gả cho ai thì gả, phải tôn trọng ý của Linh Thư.","Nói đúng! Lữ Bố ta hổ bộ Giang Hoài, không hề sợ ai! Con gái của Lữ Bố đương nhiên muốn gả cho ai thì gả, phải tôn trọng ý của Linh Thư.",},
        [2363] = {2305,"Lữ Bố","Nhân vật","Tiểu anh hùng Danh Tướng Lệnh này hãy nhận lấy, nếu không nhờ người thì ta đã gặp nguy!","Tiểu anh hùng Danh Tướng Lệnh này hãy nhận lấy, nếu không nhờ người thì ta đã gặp nguy!",},
        [2364] = {2305,"Nhân vật","","Đa tạ!","Đa tạ!",},
        [2365] = {2305,"Lữ Linh Thư ","","Cha, con không muốn lấy con trai của Viên Thuật, con muốn gả cho một đại anh hùng.","Cha, con không muốn lấy con trai của Viên Thuật, con muốn gả cho một đại anh hùng.",},
        [2366] = {2305,"Lữ Bố","","Linh Thư, sau này cha không ép con gả người con không muốn, vậy con muốn lấy vị anh hùng nào?","Linh Thư, sau này cha không ép con gả người con không muốn, vậy con muốn lấy vị anh hùng nào?",},
        [2367] = {2305,"Lữ Linh Thư ","","Con nghe nói thiên tử mới phong một vị Dương Uy Tướng, trẻ tuổi tài cao, võ công cao cường, con muốn gả cho người đó!","Con nghe nói thiên tử mới phong một vị Dương Uy Tướng, trẻ tuổi tài cao, võ công cao cường, con muốn gả cho người đó!",},
        [2368] = {2305,"Nhân vật","","Ngươi muốn gả cho ta? (Như vậy Điêu Thuyền trở thành mẹ vợ của ta, không được!)","Ngươi muốn gả cho ta? (Như vậy Điêu Thuyền trở thành mẹ vợ của ta, không được!)",},
        [2369] = {2305,"Nhân vật","","Đa tạ ý tốt của cô nương, ta còn trẻ, chưa muốn thành thân, cáo từ!","Đa tạ ý tốt của cô nương, ta còn trẻ, chưa muốn thành thân, cáo từ!",},
        [2370] = {2305,"Lữ Linh Thư ","","Dương Uy Tướng, đừng chạy, cho dù đi đến đâu, ta cũng sẽ gả cho người!","Dương Uy Tướng, đừng chạy, cho dù đi đến đâu, ta cũng sẽ gả cho người!",},
        [2371] = {2401,"Lữ Bố","Lưu Bị","Huyền Đức, ta bắn tên cứu ngươi, tại sao ngươi lại giả dạng sơn tặc, giành lấy 150 con ngựa tốt của ta?","Huyền Đức, ta bắn tên cứu ngươi, tại sao ngươi lại giả dạng sơn tặc, giành lấy 150 con ngựa tốt của ta?",},
        [2372] = {2401,"Lưu Bị","","Lưu Bị thiếu ngựa, sai người đi mua, không dám lấy ngựa của huynh.","Lưu Bị thiếu ngựa, sai người đi mua, không dám lấy ngựa của huynh.",},
        [2373] = {2401,"Trương Phi","","Lữ Bố, là ta lấy ngựa của ngươi, đừng vu khống cho đại ca ta.","Lữ Bố, là ta lấy ngựa của ngươi, đừng vu khống cho đại ca ta.",},
        [2374] = {2401,"Lữ Bố","","Ngươi dám xem thường ta?","Ngươi dám xem thường ta?",},
        [2375] = {2401,"Trương Phi","","Ta giành ngựa nên ngươi tức giận, vậy ngươi giành Từ Châu của đại ca ta thì sao?","Ta giành ngựa nên ngươi tức giận, vậy ngươi giành Từ Châu của đại ca ta thì sao?",},
        [2376] = {2401,"Lữ Bố","","Hừ, vậy xem ai bản lĩnh hơn. Ta sẽ đưa quân đến tự lấy lại ngựa!","Hừ, vậy xem ai bản lĩnh hơn. Ta sẽ đưa quân đến tự lấy lại ngựa!",},
        [2377] = {2402,"Ngân Bình","Nhân vật","Chúa công, chúa công, không xong rồi! Lữ Bố dẫn binh đến sắp tấn công Tiểu Bái! Lữ Bố binh lính nhiều gấp vài lần cha ta, lần này phải làm sao đây?","Chúa công, chúa công, không xong rồi! Lữ Bố dẫn binh đến sắp tấn công Tiểu Bái! Lữ Bố binh lính nhiều gấp vài lần cha ta, lần này phải làm sao đây?",},
        [2378] = {2402,"Tinh Thái","","Chắc do cha ta làm hỏng việc! Tính cha nóng nẩy lại thích uống rượu gây chuyện, làm sao đây?","Chắc do cha ta làm hỏng việc! Tính cha nóng nẩy lại thích uống rượu gây chuyện, làm sao đây?",},
        [2379] = {2402,"Nhân vật","","Tinh Thái, Ngân Bình, đừng lo, ta sẽ đi giải nguy!","Tinh Thái, Ngân Bình, đừng lo, ta sẽ đi giải nguy!",},
        [2380] = {2402,"Nhân vật","","Lữ huynh, nếu ta giúp người tìm lại ngựa, người sẽ rút binh, nếu không được thì mới tấn công có được không?","Lữ huynh, nếu ta giúp người tìm lại ngựa, người sẽ rút binh, nếu không được thì mới tấn công có được không?",},
        [2381] = {2402,"Lữ Bố","","Nể tình tiểu anh hùng, quyết định như vậy.","Nể tình tiểu anh hùng, quyết định như vậy.",},
        [2382] = {2403,"Quan Vũ","Nhân vật","Tứ đệ, đệ sao lại giúp người ngoài?","Tứ đệ, đệ sao lại giúp người ngoài?",},
        [2383] = {2403,"Nhân vật","","Nhị ca đừng vội trách ta, ta có đạo lý riêng!","Nhị ca đừng vội trách ta, ta có đạo lý riêng!",},
        [2384] = {2404,"Trương Phi","Nhân vật","Tứ đệ, ngươi dám giúp người ngoài thì đừng trách ta!","Tứ đệ, ngươi dám giúp người ngoài thì đừng trách ta!",},
        [2385] = {2404,"Nhân vật","","Tam ca, hãy nghe ta nói hết!","Tam ca, hãy nghe ta nói hết!",},
        [2386] = {2404,"Trương Phi","","Không cần nói nhiều! Hãy tiếp chiêu của ta!","Không cần nói nhiều! Hãy tiếp chiêu của ta!",},
        [2387] = {2405,"Nhân vật","Trương Phi","Tam ca, huynh hiểu lầm rồi. Ta đến giúp huynh và đại ca, nhị ca, đây chỉ là kế hoãn binh.","Tam ca, huynh hiểu lầm rồi. Ta đến giúp huynh và đại ca, nhị ca, đây chỉ là kế hoãn binh.",},
        [2388] = {2405,"Nhân vật","","Bên cạnh Lữ Bố có Trần Cung, Trần Cung luôn muốn gây bất lợi cho đại ca, nhân lúc Lữ Bố chuẩn bị binh lính cần thời gian, ta sẽ bảo vệ mọi ngươi ra khỏi thành, Tiểu Bái không cần cũng được!","Bên cạnh Lữ Bố có Trần Cung, Trần Cung luôn muốn gây bất lợi cho đại ca, nhân lúc Lữ Bố chuẩn bị binh lính cần thời gian, ta sẽ bảo vệ mọi ngươi ra khỏi thành, Tiểu Bái không cần cũng được!",},
        [2389] = {2405,"Trương Phi","","Thì ra thế, tam ca là người thô lỗ, xin thứ tội.","Thì ra thế, tam ca là người thô lỗ, xin thứ tội.",},
        [2390] = {2405,"Tinh Thái","","Cha, người cứ thích uống rượu, uống rượu xong lại gây chuyện, suýt chút đã hại mọi người mất mạng, sau này tuyệt đối không được uống rượu.","Cha, người cứ thích uống rượu, uống rượu xong lại gây chuyện, suýt chút đã hại mọi người mất mạng, sau này tuyệt đối không được uống rượu.",},
        [2391] = {2405,"Nhân vật","","Hãy bỏ thành đi Hứa Đô, đầu hàng Tào Tháo.","Hãy bỏ thành đi Hứa Đô, đầu hàng Tào Tháo.",},
        [2392] = {2405,"Nhân vật","","Tào Tháo hận Lữ Bố. Lữ Bố trước đây từng tấn công Duyện Châu, suýt chút đã diệt Tào Tháo.","Tào Tháo hận Lữ Bố. Lữ Bố trước đây từng tấn công Duyện Châu, suýt chút đã diệt Tào Tháo.",},
        [2393] = {2405,"Lưu Bị","","Lời của tứ đệ có lý.","Lời của tứ đệ có lý.",},
        [2394] = {2405,"Trương Phi","","Tứ đệ, là ta hiểu lầm đệ, đây là Danh Tướng Lệnh, hãy nhận lấy.","Tứ đệ, là ta hiểu lầm đệ, đây là Danh Tướng Lệnh, hãy nhận lấy.",},
        [2395] = {2501,"Nhân vật","Tinh Thái","Đại ca đã ổn định tại Hứa Đô. Tinh Thái, Ngân Bình, đã lâu chúng ta chưa ra ngoài du ngoạn, có muốn đi không?","Đại ca đã ổn định tại Hứa Đô. Tinh Thái, Ngân Bình, đã lâu chúng ta chưa ra ngoài du ngoạn, có muốn đi không?",},
        [2396] = {2501,"Tinh Thái","","Dạo này vì giúp cha ta, quả thật vất vả cho chúa công.","Dạo này vì giúp cha ta, quả thật vất vả cho chúa công.",},
        [2397] = {2501,"Ngân Bình","","Chúa công, chúng ta đi đâu đây? Ở Trung Nguyên đã lâu, hay là đến giang đông xem sao.","Chúa công, chúng ta đi đâu đây? Ở Trung Nguyên đã lâu, hay là đến giang đông xem sao.",},
        [2398] = {2501,"Nhân vật","","Kể từ khi Tôn Kiên qua đời, Tôn Sách theo Viên Thuật, rút về Giang Nam, chúng ta hãy đến thăm, xem có thể giúp được gì không.","Kể từ khi Tôn Kiên qua đời, Tôn Sách theo Viên Thuật, rút về Giang Nam, chúng ta hãy đến thăm, xem có thể giúp được gì không.",},
        [2399] = {2501,"Ngân Bình","","Được, Tiểu Bá Vương, vừa đẹp trai vừa dũng mãnh, chúng ta đi tìm người.","Được, Tiểu Bá Vương, vừa đẹp trai vừa dũng mãnh, chúng ta đi tìm người.",},
        [2400] = {2502,"Nhân vật","Tôn Sách","Tôn huynh, đã lâu không gặp, sao có vẻ không vui?","Tôn huynh, đã lâu không gặp, sao có vẻ không vui?",},
        [2401] = {2502,"Tôn Sách","","Viên Thuật không giữ chữ tín, hứa phong ta làm Thái Thú Cửu Giang, Thái Thú Lư Giang nhưng lần nào cũng nuốt lời. Thật khiến người thất vọng.","Viên Thuật không giữ chữ tín, hứa phong ta làm Thái Thú Cửu Giang, Thái Thú Lư Giang nhưng lần nào cũng nuốt lời. Thật khiến người thất vọng.",},
        [2402] = {2502,"Tôn Sách","","Phụ thân Tôn Kiên ta là bậc anh hùng, nhưng ta lại làm kẻ ăn nhờ ở đậu, không xứng kế thừa chí hướng của phụ thân.","Phụ thân Tôn Kiên ta là bậc anh hùng, nhưng ta lại làm kẻ ăn nhờ ở đậu, không xứng kế thừa chí hướng của phụ thân.",},
        [2403] = {2502,"Nhân vật","","Tôn Sách, đừng tin lời Viên Thuật, hãy triệu tập thuộc hạ cũ của cha huynh thừa cơ đoạt Giang Đông, dựng cơ nghiệp.","Tôn Sách, đừng tin lời Viên Thuật, hãy triệu tập thuộc hạ cũ của cha huynh thừa cơ đoạt Giang Đông, dựng cơ nghiệp.",},
        [2404] = {2502,"Tôn Sách","","Chỉ e Viên Thuật không dễ buông tha ta!","Chỉ e Viên Thuật không dễ buông tha ta!",},
        [2405] = {2502,"Nhân vật","","Ngươi hãy giao ngọc tỉ cho Viên Thuật, hắn sẽ đồng ý.","Ngươi hãy giao ngọc tỉ cho Viên Thuật, hắn sẽ đồng ý.",},
        [2406] = {2502,"Tôn Sách","","Được! Cậu ta Thái Thú Đan Dương Ngô Cảnh bị Thích Sử Dương Châu Lưu Dao ép bức, đây là cơ hội tốt.","Được! Cậu ta Thái Thú Đan Dương Ngô Cảnh bị Thích Sử Dương Châu Lưu Dao ép bức, đây là cơ hội tốt.",},
        [2407] = {2502,"Nhân vật","","Nếu hắn không đồng ý thì đánh đến đồng ý mới thôi.","Nếu hắn không đồng ý thì đánh đến đồng ý mới thôi.",},
        [2408] = {2503,"Viên Thuật","Nhân vật","Viên Thuật ta là cha thiên hạ, các ngươi không được làm thế.","Viên Thuật ta là cha thiên hạ, các ngươi không được làm thế.",},
        [2409] = {2503,"Ngân Bình","","Bại tướng của chúa công mà lớn lối quá, đỡ nè!","Bại tướng của chúa công mà lớn lối quá, đỡ nè!",},
        [2410] = {2503,"Nhân vật","","Ta sẽ đánh cho ngươi khóc mới thôi!","Ta sẽ đánh cho ngươi khóc mới thôi!",},
        [2411] = {2504,"Tôn Sách","Nhân vật","Lần này thuận buồm xuôi gió về lại Giang Đông, thật cảm ơn tiểu đệ!","Lần này thuận buồm xuôi gió về lại Giang Đông, thật cảm ơn tiểu đệ!",},
        [2412] = {2504,"Nhân vật","","Huynh đệ với nhau, cảm ơn gì chứ!","Huynh đệ với nhau, cảm ơn gì chứ!",},
        [2413] = {2504,"Nhân vật","","Một cây làm chẳng nên non, ba cây chụm lại nên hòn núi cao, huynh nên chiêu mộ nhân tài Giang Đông.","Một cây làm chẳng nên non, ba cây chụm lại nên hòn núi cao, huynh nên chiêu mộ nhân tài Giang Đông.",},
        [2414] = {2504,"Nhân vật","","Trương Chiêu là thủ lĩnh bốn thế gia lớn của Ngô Quận, chỉ cần thu phục ông ta là có thể thu phục các thế lực còn lại.","Trương Chiêu là thủ lĩnh bốn thế gia lớn của Ngô Quận, chỉ cần thu phục ông ta là có thể thu phục các thế lực còn lại.",},
        [2415] = {2505,"Ngân Bình","Nhân vật","Chúa công nhìn xem, ai mà phong độ thế kia?","Chúa công nhìn xem, ai mà phong độ thế kia?",},
        [2416] = {2505,"Nhân vật","","Người này anh dũng bất phàm, Tôn huynh biết là ai chứ?","Người này anh dũng bất phàm, Tôn huynh biết là ai chứ?",},
        [2417] = {2505,"Tôn Sách","","Đó là người quen của ta Chu Du, Chu Công Cẩn. Khi phụ thân ta thảo phạt Đổng Trác, lúc đi qua Kinh Châu đã từng ở nhờ tại nhà Chu Du.","Đó là người quen của ta Chu Du, Chu Công Cẩn. Khi phụ thân ta thảo phạt Đổng Trác, lúc đi qua Kinh Châu đã từng ở nhờ tại nhà Chu Du.",},
        [2418] = {2505,"Nhân vật","","À, thì ra là danh tướng Chu Du!","À, thì ra là danh tướng Chu Du!",},
        [2419] = {2506,"Tôn Sách","Chu Du","Công Cẩn, đây là vị anh hùng ta thường hay nhắc đến, lần này ta thoát khỏi tay Viên Thuật, thuận lợi xuôi nam là nhờ có hắn.","Công Cẩn, đây là vị anh hùng ta thường hay nhắc đến, lần này ta thoát khỏi tay Viên Thuật, thuận lợi xuôi nam là nhờ có hắn.",},
        [2420] = {2506,"Chu Du","","Thì ra là Dương Uy Tướng, ngưỡng mộ đã lâu.","Thì ra là Dương Uy Tướng, ngưỡng mộ đã lâu.",},
        [2421] = {2506,"Tôn Sách","","Chúng ta vừa gặp đã như thân quen, hay là kết bái làm huynh đệ nhé.","Chúng ta vừa gặp đã như thân quen, hay là kết bái làm huynh đệ nhé.",},
        [2422] = {2506,"Nhân vật","","Ta đã kết bái cùng Lưu Bị, Quan Vũ, Trương Phi. Hay ta làm chứng cho hai vị kết bái vậy.","Ta đã kết bái cùng Lưu Bị, Quan Vũ, Trương Phi. Hay ta làm chứng cho hai vị kết bái vậy.",},
        [2423] = {2506,"Tôn Sách","","Tôn Sách ta cùng Chu Du hôm nay kết bái huynh đệ, từ nay đối xử chân thành, hoạn nạn có nhau. Kế thừa ý chí phụ thân, cùng Công Cẩn đoạt thiên hạ.","Tôn Sách ta cùng Chu Du hôm nay kết bái huynh đệ, từ nay đối xử chân thành, hoạn nạn có nhau. Kế thừa ý chí phụ thân, cùng Công Cẩn đoạt thiên hạ.",},
        [2424] = {2506,"Chu Du","","Chu Du ta cùng Tôn Sách hôm nay kết bái huynh đệ, từ nay đối xử chân thành, hoạn nạn có nhau. Chu Du nguyện cả đời phò tá Bá Phù đoạt thiên hạ.","Chu Du ta cùng Tôn Sách hôm nay kết bái huynh đệ, từ nay đối xử chân thành, hoạn nạn có nhau. Chu Du nguyện cả đời phò tá Bá Phù đoạt thiên hạ.",},
        [2425] = {2506,"Tôn Sách","","Danh Tướng Lệnh này tặng cho tiểu huynh đệ, xem như quà làm chứng cho hai chúng ta!","Danh Tướng Lệnh này tặng cho tiểu huynh đệ, xem như quà làm chứng cho hai chúng ta!",},
        [2426] = {2601,"Nhân vật","Tôn Sách","Tôn huynh, chúng ta bắt đầu từ đâu đây?","Tôn huynh, chúng ta bắt đầu từ đâu đây?",},
        [2427] = {2601,"Tôn Sách","","Chúng ta đánh Thích Sử Dương Châu Lưu Dao đang đóng quân tại Khúc A trước.","Chúng ta đánh Thích Sử Dương Châu Lưu Dao đang đóng quân tại Khúc A trước.",},
        [2428] = {2601,"Nhân vật","","Đã là Thích Sử Dương Châu, sao lại đóng quân tại Khúc A chứ!","Đã là Thích Sử Dương Châu, sao lại đóng quân tại Khúc A chứ!",},
        [2429] = {2601,"Tôn Sách","","Triều đình bổ nhiệm Lưu Dao làm Thích Sử Dương Châu, đóng quân Thọ Xuyến, nhưng nay Thọ Xuyến đã bị Viên Thuật chiếm đóng.","Triều đình bổ nhiệm Lưu Dao làm Thích Sử Dương Châu, đóng quân Thọ Xuyến, nhưng nay Thọ Xuyến đã bị Viên Thuật chiếm đóng.",},
        [2430] = {2601,"Tôn Sách","","Cậu và đường huynh của ta, Thái Thú Đan Dương Ngô Cảnh và Tôn Bôn từng giúp Lưu Dao chiếm đóng Khúc A, nhưng hắn lại vong ân phụ nghĩa, đến khi vững chân rồi lại tìm cách diệt họ.","Cậu và đường huynh của ta, Thái Thú Đan Dương Ngô Cảnh và Tôn Bôn từng giúp Lưu Dao chiếm đóng Khúc A, nhưng hắn lại vong ân phụ nghĩa, đến khi vững chân rồi lại tìm cách diệt họ.",},
        [2431] = {2601,"Nhân vật","","Hừm, để ta giúp Tôn huynh xử lý hắn.","Hừm, để ta giúp Tôn huynh xử lý hắn.",},
        [2432] = {2602,"Nhân vật","Tinh Thái","Thích Sử Dương Châu Lưu Dao này chỉ có vậy thôi sao.","Thích Sử Dương Châu Lưu Dao này chỉ có vậy thôi sao.",},
        [2433] = {2602,"Tinh Thái","","Chúa công anh minh thần võ, chỉ là một tên tôm tép Lưu Dao sao xứng là đối thủ!","Chúa công anh minh thần võ, chỉ là một tên tôm tép Lưu Dao sao xứng là đối thủ!",},
        [2434] = {2602,"Nhân vật","","Thuộc hạ của Lưu Dao rất lợi hại, hình như tên Thái Sử Từ, hắn không phải người Khổng Dung nhắc đến trong thư sao, sao lại đầu quân Lưu Dao!","Thuộc hạ của Lưu Dao rất lợi hại, hình như tên Thái Sử Từ, hắn không phải người Khổng Dung nhắc đến trong thư sao, sao lại đầu quân Lưu Dao!",},
        [2435] = {2602,"Tinh Thái","","Đúng là hắn, Thái Sử Từ và Lưu Dao là người cùng quê nên mới ra tay giúp đỡ.","Đúng là hắn, Thái Sử Từ và Lưu Dao là người cùng quê nên mới ra tay giúp đỡ.",},
        [2436] = {2602,"Nhân vật","","Thì ra là thế, nếu thu phục được Thái Sử Từ thì hay quá, đó là một viên tướng giỏi.","Thì ra là thế, nếu thu phục được Thái Sử Từ thì hay quá, đó là một viên tướng giỏi.",},
        [2437] = {2603,"Nghiêm Bạch Hổ","Nhân vật","Ngươi là ai? Ta chính là Ngô Đức Vương Nghiêm Bạch Hổ, sợ chưa nào!","Ngươi là ai? Ta chính là Ngô Đức Vương Nghiêm Bạch Hổ, sợ chưa nào!",},
        [2438] = {2603,"Nhân vật","","Bạch Hổ là cái thứ gì, ai dám cản đường ta đều đánh hết!","Bạch Hổ là cái thứ gì, ai dám cản đường ta đều đánh hết!",},
        [2439] = {2603,"Nghiêm Bạch Hổ","","Nhóc con chém gió to quá!","Nhóc con chém gió to quá!",},
        [2440] = {2603,"Tinh Thái","","Nghiêm Bạch Hổ xuất thân sơn tặc, chiếm Ngô Quận tự xưng vương, chúa công phải cẩn thận!","Nghiêm Bạch Hổ xuất thân sơn tặc, chiếm Ngô Quận tự xưng vương, chúa công phải cẩn thận!",},
        [2441] = {2603,"Nhân vật","","Tên giặc cỏ mà cũng chiếm đất xưng vương, ta phải đánh bầm dập mới thôi! Đánh đi!","Tên giặc cỏ mà cũng chiếm đất xưng vương, ta phải đánh bầm dập mới thôi! Đánh đi!",},
        [2442] = {2604,"Vương Lãng","Tôn Sách","Nhóc con Tôn Sách, ta đã thất bại, muốn xử thế nào tùy ngươi, Vương Lãng ta sẽ không làm thuộc hạ của ngươi đâu!","Nhóc con Tôn Sách, ta đã thất bại, muốn xử thế nào tùy ngươi, Vương Lãng ta sẽ không làm thuộc hạ của ngươi đâu!",},
        [2443] = {2604,"Tôn Sách","","Vương Lãng, nếu ngươi không muốn theo ta thì thôi vậy! Tào Tháo đã thượng tấu gọi ngươi về Hứa Xương, ta sai người tiễn ngươi đi Hứa Xương.","Vương Lãng, nếu ngươi không muốn theo ta thì thôi vậy! Tào Tháo đã thượng tấu gọi ngươi về Hứa Xương, ta sai người tiễn ngươi đi Hứa Xương.",},
        [2444] = {2604,"Nhân vật","","Vương Lãng không biết tốt xấu, Tôn huynh còn đối xử như thế, thật khiến người bội phục!","Vương Lãng không biết tốt xấu, Tôn huynh còn đối xử như thế, thật khiến người bội phục!",},
        [2445] = {2604,"Tôn Sách","","Ha ha ha! Đâu có..","Ha ha ha! Đâu có..",},
        [2446] = {2605,"Nhân vật","Tôn Sách","Chỉ còn Thái Sử Từ thôi, Tôn huynh, ta giúp ngươi nghênh chiến hắn!","Chỉ còn Thái Sử Từ thôi, Tôn huynh, ta giúp ngươi nghênh chiến hắn!",},
        [2447] = {2605,"Tôn Sách","","Lần trước ta và Thái Sử Từ chưa phân thắng bại, lần này sao lại để đệ ra trận chứ!","Lần trước ta và Thái Sử Từ chưa phân thắng bại, lần này sao lại để đệ ra trận chứ!",},
        [2448] = {2605,"Nhân vật","","Vậy chúng ta thử sức xem, ai thắng sẽ đấu với Thái Sử Từ.","Vậy chúng ta thử sức xem, ai thắng sẽ đấu với Thái Sử Từ.",},
        [2449] = {2605,"Tôn Sách","","Được, vậy đánh đi!","Được, vậy đánh đi!",},
        [2450] = {2606,"Thái Sử Từ","Nhân vật","Dương Uy Tướng thật lợi hại, tại hạ bội phục, Danh Tướng Lệnh này cho ngươi.","Dương Uy Tướng thật lợi hại, tại hạ bội phục, Danh Tướng Lệnh này cho ngươi.",},
        [2451] = {2606,"Nhân vật","","Cảm ơn!","Cảm ơn!",},
        [2452] = {2606,"Tôn Sách","","Lưu Dao ngu ngốc không biết dùng người nên mới thất bại. Tử Nghĩa hãy theo ta lập cơ nghiệp.","Lưu Dao ngu ngốc không biết dùng người nên mới thất bại. Tử Nghĩa hãy theo ta lập cơ nghiệp.",},
        [2453] = {2606,"Thái Sử Từ","","Quân Lưu vừa phá, quân tâm rối loạn, hãy để ta đi xử lý, trợ giúp chúa công.","Quân Lưu vừa phá, quân tâm rối loạn, hãy để ta đi xử lý, trợ giúp chúa công.",},
        [2454] = {2606,"Tôn Sách","","Được, ngày mai gặp lại. (Thái Sử Từ đáp ứng rời đi)","Được, ngày mai gặp lại. (Thái Sử Từ đáp ứng rời đi)",},
        [2455] = {2606,"Nhân vật","","Tôn huynh không sợ Thái Sử Từ bỏ đi sao.","Tôn huynh không sợ Thái Sử Từ bỏ đi sao.",},
        [2456] = {2606,"Tôn Sách","","Tử Nghĩa trọng chữ tín, sẽ không gạt ta.","Tử Nghĩa trọng chữ tín, sẽ không gạt ta.",},
        [2457] = {2606,"Nhân vật","","Không giấu huynh, Tào Tháo nghe danh Thái Sử Từ, gửi thư chiêu hàng, không ngờ lại bị Tôn huynh đoạt trước.","Không giấu huynh, Tào Tháo nghe danh Thái Sử Từ, gửi thư chiêu hàng, không ngờ lại bị Tôn huynh đoạt trước.",},
        [2458] = {2606,"Tôn Sách","","Tôn Sách ta dùng người sẽ không nghi kỵ, Thái Sử Từ sẽ là một viên đại tướng của Giang Đông.","Tôn Sách ta dùng người sẽ không nghi kỵ, Thái Sử Từ sẽ là một viên đại tướng của Giang Đông.",},
        [2459] = {2701,"Ngân Bình","Hoa Đà","Tên phía trước đang làm gì thế, luyện công sao?","Tên phía trước đang làm gì thế, luyện công sao?",},
        [2460] = {2701,"Hoa Đà","","(Vừa luyện vừa nói thầm) Hổ Phác, Lộc Bôn, Hùng Vận, Điểu Phi...","(Vừa luyện vừa nói thầm) Hổ Phác, Lộc Bôn, Hùng Vận, Điểu Phi...",},
        [2461] = {2701,"Tinh Thái","","A, đó là sư phụ ta!","A, đó là sư phụ ta!",},
        [2462] = {2701,"Hoa Đà","","Ha ha! Tinh Thái đó sao, vi sư đi khắp nơi trị bệnh, nay vừa khéo lại đến Giang Đông!","Ha ha! Tinh Thái đó sao, vi sư đi khắp nơi trị bệnh, nay vừa khéo lại đến Giang Đông!",},
        [2463] = {2701,"Tinh Thái","","Sư phụ, đây là chúa công của ta! Chúa công, đây là sư phụ ta, Hoa Đà!","Sư phụ, đây là chúa công của ta! Chúa công, đây là sư phụ ta, Hoa Đà!",},
        [2464] = {2701,"Hoa Đà","","Dương Uy Tướng Quân! Đúng là anh hùng thiếu niên! (Không thẹn là người mang thiên mệnh mà Tả Từ nói đến)","Dương Uy Tướng Quân! Đúng là anh hùng thiếu niên! (Không thẹn là người mang thiên mệnh mà Tả Từ nói đến)",},
        [2465] = {2701,"Nhân vật","","Hân hạnh! Thì ra là danh y Hoa Đà!","Hân hạnh! Thì ra là danh y Hoa Đà!",},
        [2466] = {2701,"Hoa Đà","","Tinh Thái, bộ Ngũ Cầm Hí này hãy nhận thay chúa công của con, vi sư có việc phải đến Cối Kê.","Tinh Thái, bộ Ngũ Cầm Hí này hãy nhận thay chúa công của con, vi sư có việc phải đến Cối Kê.",},
        [2467] = {2701,"Ngân Bình","","Tinh Thái, sư phụ muội trẻ thật đấy! Ơ...sao đi mất rồi!","Tinh Thái, sư phụ muội trẻ thật đấy! Ơ...sao đi mất rồi!",},
        [2468] = {2701,"Tinh Thái","","Sư phụ vốn là như thế! Chúa công và Ngân Bình tỉ chớ trách!","Sư phụ vốn là như thế! Chúa công và Ngân Bình tỉ chớ trách!",},
        [2469] = {2702,"Tinh Thái","Nhân vật","Sao Tuyên Thành bị sơn tặc bao vây rồi!","Sao Tuyên Thành bị sơn tặc bao vây rồi!",},
        [2470] = {2702,"Nhân vật","","Tuyên Thành do em trai Tôn Sách là Tôn Quyền trấn thủ, chúng ta mau cứu Tôn Quyền!","Tuyên Thành do em trai Tôn Sách là Tôn Quyền trấn thủ, chúng ta mau cứu Tôn Quyền!",},
        [2471] = {2702,"Ngân Bình","","Ha ha! Có cơ hội ra tay rồi!","Ha ha! Có cơ hội ra tay rồi!",},
        [2472] = {2703,"Chu Thái","Nhân vật","Cảm ơn! Mong các vị bảo vệ Tôn Quyền tướng quân! (Nói xong, Chu Thái té xỉu)","Cảm ơn! Mong các vị bảo vệ Tôn Quyền tướng quân! (Nói xong, Chu Thái té xỉu)",},
        [2473] = {2703,"Tôn Quyền","","Chu Thái tướng quân! Hắn vì cứu ta mới bị trọng thương!","Chu Thái tướng quân! Hắn vì cứu ta mới bị trọng thương!",},
        [2474] = {2703,"Nhân vật","","Chu Thái thật là người trung nghĩa, Tinh Thái, nhanh cứu người!","Chu Thái thật là người trung nghĩa, Tinh Thái, nhanh cứu người!",},
        [2475] = {2703,"Tinh Thái","","Chu Thái tướng quân bị thương quá nặng, muội chỉ có thể bảo vệ tính mạng, nếu muốn cứu chữa phải mời sư phụ Hoa Đà giúp mới được!","Chu Thái tướng quân bị thương quá nặng, muội chỉ có thể bảo vệ tính mạng, nếu muốn cứu chữa phải mời sư phụ Hoa Đà giúp mới được!",},
        [2476] = {2703,"Tôn Quyền","","Đa tạ cô nương! Xin hỏi lệnh sư Hoa Đà ở đâu?","Đa tạ cô nương! Xin hỏi lệnh sư Hoa Đà ở đâu?",},
        [2477] = {2703,"Tinh Thái","","Trước khi vào thành ta có gặp người, người bảo đến Cối Khê!","Trước khi vào thành ta có gặp người, người bảo đến Cối Khê!",},
        [2478] = {2703,"Tôn Quyền","","Nhanh phái người đi Cối Kê mời Thần Y Hoa Đà đến cứu Chu Thái!","Nhanh phái người đi Cối Kê mời Thần Y Hoa Đà đến cứu Chu Thái!",},
        [2479] = {2704,"Hoa Đà","Nhân vật","Dương Uy Tướng giỏi quá! Danh Tướng Lệnh này tặng tướng quân!","Dương Uy Tướng giỏi quá! Danh Tướng Lệnh này tặng tướng quân!",},
        [2480] = {2704,"Nhân vật","","Trước được Hoa Thần Y tặng Ngũ Cầm Hí, nay lại được Danh Tướng Lệnh, thật cảm ơn!","Trước được Hoa Thần Y tặng Ngũ Cầm Hí, nay lại được Danh Tướng Lệnh, thật cảm ơn!",},
        [2481] = {2704,"Hoa Đà","","Tướng quân là người kết thúc thời buổi loạn lạc này, mong ngài hãy rèn luyện Ngũ Cầm Hí, giữ gìn sức khỏe!","Tướng quân là người kết thúc thời buổi loạn lạc này, mong ngài hãy rèn luyện Ngũ Cầm Hí, giữ gìn sức khỏe!",},
        [2482] = {2704,"Nhân vật","","Xin hỏi, sao trông Hoa Thần Y trẻ vậy, là do luyện Ngũ Cầm Hí sao?","Xin hỏi, sao trông Hoa Thần Y trẻ vậy, là do luyện Ngũ Cầm Hí sao?",},
        [2483] = {2704,"Hoa Đà","","Đúng thế!","Đúng thế!",},
        [2484] = {2704,"Nhân vật","","Ngài yên tâm, ta sẽ luyện ngày luyện đêm! Tinh Thái, Ngân Bình, hai muội cùng ta luyện ào, sau này ta sẽ tìm phu quân cho các muội!","Ngài yên tâm, ta sẽ luyện ngày luyện đêm! Tinh Thái, Ngân Bình, hai muội cùng ta luyện ào, sau này ta sẽ tìm phu quân cho các muội!",},
        [2485] = {2704,"Tinh Thái","","Ai thèm, muội sẽ theo chúa công suốt đời!","Ai thèm, muội sẽ theo chúa công suốt đời!",},
        [2486] = {2704,"Ngân Bình","","Muội cũng vậy!","Muội cũng vậy!",},
        [2487] = {2704,"Nhân vật","","He he! Được thôi! Nghe nói giặc Hoàng Cân xuất hiện tại Trần Lưu, chúng ta đi thôi!","He he! Được thôi! Nghe nói giặc Hoàng Cân xuất hiện tại Trần Lưu, chúng ta đi thôi!",},
        [2488] = {2801,"Nhân vật","Tinh Thái","Trần Lưu vốn là nơi phồn hoa, sao nay chỉ toàn đói rách thế này!","Trần Lưu vốn là nơi phồn hoa, sao nay chỉ toàn đói rách thế này!",},
        [2489] = {2801,"Tinh Thái","Nhân vật","Chúa công, hai năm nay nơi đây gặp nạn châu chấu, mùa màng mất sạch, lương thực khan hiếm, thê thảm lắm.","Chúa công, hai năm nay nơi đây gặp nạn châu chấu, mùa màng mất sạch, lương thực khan hiếm, thê thảm lắm.",},
        [2490] = {2801,"Nhân vật","Tinh Thái","Đây là phạm vi thế lực Tào Tháo, chúng ta đi gặp Tào Tháo xin ít lương thực cứu tế bá tánh.","Đây là phạm vi thế lực Tào Tháo, chúng ta đi gặp Tào Tháo xin ít lương thực cứu tế bá tánh.",},
        [2491] = {2802,"Nhân vật","Tào Tháo","Tào huynh, ta một đường từ Giang Đông đến Trần Lưu, thấy cảnh dân chúng lầm than, sao huynh không cứu giúp?","Tào huynh, ta một đường từ Giang Đông đến Trần Lưu, thấy cảnh dân chúng lầm than, sao huynh không cứu giúp?",},
        [2492] = {2802,"Tào Tháo","Nhân vật","Tiểu huynh đệ không biết đó thôi, gần đây nạn châu chấu hoành hành, trong quân ta không có nhiều lương thực, huống chi vừa giao chiến với Lữ Bố, ta đang lo lắm đây?","Tiểu huynh đệ không biết đó thôi, gần đây nạn châu chấu hoành hành, trong quân ta không có nhiều lương thực, huống chi vừa giao chiến với Lữ Bố, ta đang lo lắm đây?",},
        [2493] = {2802,"Tuân Úc","Nhân vật","Hai vị, tàn dư Hoàng Cân là Hà Nghi, Hoàng Thiệu, cướp bóc châu quận, có nhiều vàng lụa, lương thực,","Hai vị, tàn dư Hoàng Cân là Hà Nghi, Hoàng Thiệu, cướp bóc châu quận, có nhiều vàng lụa, lương thực,",},
        [2494] = {2802,"Tuân Úc","Nhân vật","Bọn giặc này rất dễ phá; phá xong lấy lương thực của chúng để nuôi ba quân, triều đình vui, trăm họ mừng, là việc thuận theo trời.","Bọn giặc này rất dễ phá; phá xong lấy lương thực của chúng để nuôi ba quân, triều đình vui, trăm họ mừng, là việc thuận theo trời.",},
        [2495] = {2802,"Nhân vật","Tuân Úc","Ý hay lắm, Tào huynh, diệt Hoàng Cân, ta làm tiên phong, ta chờ tin huynh thắng trận quay về.","Ý hay lắm, Tào huynh, diệt Hoàng Cân, ta làm tiên phong, ta chờ tin huynh thắng trận quay về.",},
        [2496] = {2803,"Hoàng Thiệu","Ngân Bình","Ta là Hoàng Thiệu! Ngươi là ai?","Ta là Hoàng Thiệu! Ngươi là ai?",},
        [2497] = {2803,"Ngân Bình","Hoàng Thiệu","Chúa công ta là Dương Uy Tướng được đương kim thiên tử sắc phong, bọn giặc Hoàng Cân, còn không bó tay chịu trói!","Chúa công ta là Dương Uy Tướng được đương kim thiên tử sắc phong, bọn giặc Hoàng Cân, còn không bó tay chịu trói!",},
        [2498] = {2803,"Hoàng Thiệu","Ngân Bình","Dương Uy Tướng gì, chưa nghe bao giờ! Mặc kệ ngươi là ai, dám xông vào Hoàng Cân Thiên Quân ta, hôm nay ta sẽ đánh cho các ngươi thua thê thảm!","Dương Uy Tướng gì, chưa nghe bao giờ! Mặc kệ ngươi là ai, dám xông vào Hoàng Cân Thiên Quân ta, hôm nay ta sẽ đánh cho các ngươi thua thê thảm!",},
        [2499] = {2804,"Hoàng Thiệu","Ngân Bình","Tiểu nhân có mắt mà không biết Dương Uy Tướng, tướng quân tha mạng!","Tiểu nhân có mắt mà không biết Dương Uy Tướng, tướng quân tha mạng!",},
        [2500] = {2804,"Ngân Bình","Hoàng Thiệu","Bây giờ lại xin chúa công ta tha mạng! Hừ!","Bây giờ lại xin chúa công ta tha mạng! Hừ!",},
        [2501] = {2804,"Nhân vật","Hoàng Thiệu","Được, không lấy mạng ngươi cũng được, hãy giao vàng bạc lương thực trong trại ra đây thì ta sẽ tha cho.","Được, không lấy mạng ngươi cũng được, hãy giao vàng bạc lương thực trong trại ra đây thì ta sẽ tha cho.",},
        [2502] = {2804,"Hoàng Thiệu","Nhân vật","Không giấu gì tướng quân, trong trại chúng tôi có một đại hán, tướng quân muốn lấy lương thực vàng bạc thì phải thắng hắn.","Không giấu gì tướng quân, trong trại chúng tôi có một đại hán, tướng quân muốn lấy lương thực vàng bạc thì phải thắng hắn.",},
        [2503] = {2804,"Ngân Bình","Hoàng Thiệu","Kẻ nào to gan dám cướp đồ của chúa công ta. Đi, đi xem xem!","Kẻ nào to gan dám cướp đồ của chúa công ta. Đi, đi xem xem!",},
        [2504] = {2805,"Ngân Bình","Nhân vật","Chúa công nhìn đi, gã béo này đã cướp đồ của chúng ta.","Chúa công nhìn đi, gã béo này đã cướp đồ của chúng ta.",},
        [2505] = {2805,"Nhân vật","Ngân Bình","Vị tráng sĩ này cao to vạm vỡ, trông rất quen.","Vị tráng sĩ này cao to vạm vỡ, trông rất quen.",},
        [2506] = {2805,"Ngân Bình","Nhân vật","Chúa công thật quá khen, hắn béo thế kia mà. Này tên béo, mau giao Giặc Hoàng Cân ra đây!","Chúa công thật quá khen, hắn béo thế kia mà. Này tên béo, mau giao Giặc Hoàng Cân ra đây!",},
        [2507] = {2805,"Hứa Chử","Nhân vật","Hừ hừ, các ngươi thắng được Lưu Tinh Chùy 180 cân trong tay ta đi đã!","Hừ hừ, các ngươi thắng được Lưu Tinh Chùy 180 cân trong tay ta đi đã!",},
        [2508] = {2805,"Nhân vật","Hứa Chử","Ngân Bình, ngươi đứng sau ta, đừng để Lưu Tinh Chùy của hắn làm bị thương!","Ngân Bình, ngươi đứng sau ta, đừng để Lưu Tinh Chùy của hắn làm bị thương!",},
        [2509] = {2806,"Hứa Chử","Nhân vật","Hứa Chử thua tâm phục khẩu phục, Danh Tướng Lệnh này xin trao cho tướng quân! Hứa Chử xin được theo tướng quân!","Hứa Chử thua tâm phục khẩu phục, Danh Tướng Lệnh này xin trao cho tướng quân! Hứa Chử xin được theo tướng quân!",},
        [2510] = {2806,"Nhân vật","Hứa Chử","Ta thường đi khắp nơi, chi bằng ngươi hãy theo Tào Tháo.","Ta thường đi khắp nơi, chi bằng ngươi hãy theo Tào Tháo.",},
        [2511] = {2806,"Nhân vật","Tào Tháo","Tào huynh, ta tiến cử cho huynh 1 đại tướng, đây là Hứa Chử, võ nghệ ngang với Điển Vi.","Tào huynh, ta tiến cử cho huynh 1 đại tướng, đây là Hứa Chử, võ nghệ ngang với Điển Vi.",},
        [2512] = {2806,"Tào Tháo","Nhân vật","Ha ha, Tháo may mắn có được 2 hổ vệ, tả Điển Vi, hữu Hứa Chử, e là trên đời này không còn ai hại được ta!","Ha ha, Tháo may mắn có được 2 hổ vệ, tả Điển Vi, hữu Hứa Chử, e là trên đời này không còn ai hại được ta!",},
        [2513] = {2901,"Tào Tháo","Nhân vật","Tiểu anh hùng, Trương Tú dùng Giả Hủ làm mưu sĩ, liên minh với Lưu Biểu, đóng quân ở Uyển Thành, ngươi thấy sao về chuyện này?","Tiểu anh hùng, Trương Tú dùng Giả Hủ làm mưu sĩ, liên minh với Lưu Biểu, đóng quân ở Uyển Thành, ngươi thấy sao về chuyện này?",},
        [2514] = {2901,"Nhân vật","Tào Tháo","Tào huynh, ta thấy hãy phái người khuyên hàng Trương Tú. Ta từng tiếp xúc Giả Hủ, rất mưu lược, lại còn biết thức thời. Ta tới giúp Tào huynh khuyên hàng, không mất sức mà vẫn lấy được Uyển Thành.","Tào huynh, ta thấy hãy phái người khuyên hàng Trương Tú. Ta từng tiếp xúc Giả Hủ, rất mưu lược, lại còn biết thức thời. Ta tới giúp Tào huynh khuyên hàng, không mất sức mà vẫn lấy được Uyển Thành.",},
        [2515] = {2901,"Tào Tháo","Nhân vật","Vậy thì tốt quá, xin đa tạ tiểu anh hùng!","Vậy thì tốt quá, xin đa tạ tiểu anh hùng!",},
        [2516] = {2902,"Tào Tháo","Trâu Thị","Phu nhân tên họ là gì?","Phu nhân tên họ là gì?",},
        [2517] = {2902,"Trâu Thị","Tào Tháo","Thiếp là nương tử Trương Tế tên Trâu Thị.","Thiếp là nương tử Trương Tế tên Trâu Thị.",},
        [2518] = {2902,"Tào Tháo","Trâu Thị","Phu nhân có biết ta không?","Phu nhân có biết ta không?",},
        [2519] = {2902,"Trâu Thị","Tào Tháo","Nghe danh thừa tướng đã lâu, hôm nay may mắn được gặp.","Nghe danh thừa tướng đã lâu, hôm nay may mắn được gặp.",},
        [2520] = {2902,"Tào Tháo","Trâu Thị","Ta vì phu nhân mà nạp Trương Tú đầu hàng, giúp hạn không bị diệt tộc.","Ta vì phu nhân mà nạp Trương Tú đầu hàng, giúp hạn không bị diệt tộc.",},
        [2521] = {2902,"Tào Tháo","Trâu Thị","Hôm nay được gặp phu nhân quả vinh hạnh. Nàng có muốn cùng ta về kinh đô, cùng hưởng phú quý?","Hôm nay được gặp phu nhân quả vinh hạnh. Nàng có muốn cùng ta về kinh đô, cùng hưởng phú quý?",},
        [2522] = {2902,"Trâu Thị","Tào Tháo","Thiếp nguyện theo thừa tướng.","Thiếp nguyện theo thừa tướng.",},
        [2523] = {2903,"Trương Tú","Giả Hủ","Tháo lại sỉ nhục thím ta! Phải trảm Tào Tháo thì mới rửa được nỗi hận trong lòng ta!","Tháo lại sỉ nhục thím ta! Phải trảm Tào Tháo thì mới rửa được nỗi hận trong lòng ta!",},
        [2524] = {2903,"Giả Hủ","Trương Tú","Chuyện này không được tiết lộ. Ngày mai chờ Tháo ra trướng bàn việc, ngươi có thể nói là binh lính đầu hàng có nhiều người bỏ trốn, xin đưa đồn vào trung quân.","Chuyện này không được tiết lộ. Ngày mai chờ Tháo ra trướng bàn việc, ngươi có thể nói là binh lính đầu hàng có nhiều người bỏ trốn, xin đưa đồn vào trung quân.",},
        [2525] = {2903,"Trương Tú","Giả Hủ","Nhưng Điển Vi, thủ hạ của Tào Tháo rất dũng mãnh, phải ứng phó sao đây?","Nhưng Điển Vi, thủ hạ của Tào Tháo rất dũng mãnh, phải ứng phó sao đây?",},
        [2526] = {2903,"Giả Hủ","Trương Tú","Ngày mai hãy mời hắn uống rượu đến say mới về. Lúc đó cho người lẻn vào trướng, trộm kích của hắn.","Ngày mai hãy mời hắn uống rượu đến say mới về. Lúc đó cho người lẻn vào trướng, trộm kích của hắn.",},
        [2527] = {2903,"Trương Tú","Giả Hủ","Đúng là diệu kế! Ta phải diệt Tào Tháo","Đúng là diệu kế! Ta phải diệt Tào Tháo",},
        [2528] = {2904,"Nhân vật","Ngân Bình","Lạ thật, trong thành sao hỗn loạn như vậy? Trương Tú chẳng phải đã đầu hàng Tào Tháo rồi sao?","Lạ thật, trong thành sao hỗn loạn như vậy? Trương Tú chẳng phải đã đầu hàng Tào Tháo rồi sao?",},
        [2529] = {2904,"Ngân Bình","Nhân vật","Chúa công, không hay rồi, Trương Tú vốn dĩ đã đầu hàng, Tào Tháo sau khi vào Uyển Thành đã vui thú với thím của Trương Tú là Trâu Thị, Trương Tú biết chuyện đã lên kế hoạch đối phó với Tào Tháo.","Chúa công, không hay rồi, Trương Tú vốn dĩ đã đầu hàng, Tào Tháo sau khi vào Uyển Thành đã vui thú với thím của Trương Tú là Trâu Thị, Trương Tú biết chuyện đã lên kế hoạch đối phó với Tào Tháo.",},
        [2530] = {2904,"Nhân vật","Ngân Bình","Vậy chúng ta mau đi cứu Tào Tháo.","Vậy chúng ta mau đi cứu Tào Tháo.",},
        [2531] = {2905,"Tinh Thái","Nhân vật","Chúng ta đã đến chậm một bước, Điển Vi vì bảo vệ Tào Tháo mà bị trọng thương.","Chúng ta đã đến chậm một bước, Điển Vi vì bảo vệ Tào Tháo mà bị trọng thương.",},
        [2532] = {2905,"Nhân vật","Tinh Thái","Tinh Thái, ngươi mau xem giúp Điển Vi.","Tinh Thái, ngươi mau xem giúp Điển Vi.",},
        [2533] = {2905,"Tinh Thái","Nhân vật","Chúa công, Tinh Thái không cứu được, Điển tướng quân bị thương quá nặng!","Chúa công, Tinh Thái không cứu được, Điển tướng quân bị thương quá nặng!",},
        [2534] = {2905,"Điển Vi","Nhân vật","Ta chặn cổng trại, thừa tướng cưỡi ngựa chạy từ sau trại, nhưng truy binh quá nhiều, rất nguy hiểm.","Ta chặn cổng trại, thừa tướng cưỡi ngựa chạy từ sau trại, nhưng truy binh quá nhiều, rất nguy hiểm.",},
        [2535] = {2905,"Điển Vi","Nhân vật","Dương Uy Tướng, ngươi và thừa tướng xưa nay thân thiết! Ngươi hãy mau đến cứu thừa tướng!","Dương Uy Tướng, ngươi và thừa tướng xưa nay thân thiết! Ngươi hãy mau đến cứu thừa tướng!",},
        [2536] = {2905,"Nhân vật","Điển Vi","Điển tướng quân yên tâm, ta sẽ cứu Tào Tháo!","Điển tướng quân yên tâm, ta sẽ cứu Tào Tháo!",},
        [2537] = {2905,"Điển Vi","Nhân vật","Chúa công, chạy mau... (Điển Vi thì thào, rồi lìa đời)","Chúa công, chạy mau... (Điển Vi thì thào, rồi lìa đời)",},
        [2538] = {3001,"Tinh Thái","Nhân vật","Chúa công, chúng ta đuổi theo mà sao không thấy Tào Tháo, không lẽ ông ta không đi đường này?","Chúa công, chúng ta đuổi theo mà sao không thấy Tào Tháo, không lẽ ông ta không đi đường này?",},
        [2539] = {3001,"Nhân vật","Tinh Thái","Có lẽ Tào Tháo cưỡi ngựa chạy lung tung, không biết chạy về bên nào. Chớ lo, người phía trước hình như là Vu Cấm, thủ hạ của Tào Tháo.","Có lẽ Tào Tháo cưỡi ngựa chạy lung tung, không biết chạy về bên nào. Chớ lo, người phía trước hình như là Vu Cấm, thủ hạ của Tào Tháo.",},
        [2540] = {3001,"Vu Cấm","Nhân vật","Dương Uy Tướng, nghe nói Uyển Thành lại loạn, ngươi có thấy tướng quân không?","Dương Uy Tướng, nghe nói Uyển Thành lại loạn, ngươi có thấy tướng quân không?",},
        [2541] = {3001,"Nhân vật","Vu Cấm","Ta vừa ra khỏi Uyển Thành, thì Trương Tú làm phản, Điển Vi tướng quân đã tử trận, thừa tướng không biết chạy đi đâu rồi.","Ta vừa ra khỏi Uyển Thành, thì Trương Tú làm phản, Điển Vi tướng quân đã tử trận, thừa tướng không biết chạy đi đâu rồi.",},
        [2542] = {3001,"Vu Cấm","Nhân vật","Lại có chuyện này sao! Ta sẽ về Duyện Châu điều quân đến cứu viện!","Lại có chuyện này sao! Ta sẽ về Duyện Châu điều quân đến cứu viện!",},
        [2543] = {3001,"Nhân vật","Vu Cấm","Trương Tú người đông thế mạnh, như vậy thật tốt, chúng ta hãy chia ra hai hướng, ta tiếp tục tìm thừa tướng!","Trương Tú người đông thế mạnh, như vậy thật tốt, chúng ta hãy chia ra hai hướng, ta tiếp tục tìm thừa tướng!",},
        [2544] = {3002,"Tào Ngang","Tào Tháo","Phụ thân, ngựa của phụ thân đã trúng 3 tên, nay mắt ngựa lại trúng 1 tên, không thể chạy được nữa. Phụ thân, hãy mau cưỡi ngựa của con đi đi!","Phụ thân, ngựa của phụ thân đã trúng 3 tên, nay mắt ngựa lại trúng 1 tên, không thể chạy được nữa. Phụ thân, hãy mau cưỡi ngựa của con đi đi!",},
        [2545] = {3002,"Tào Tháo","Tào Ngang","Ta cưỡi ngựa của con, còn con làm thế nào?","Ta cưỡi ngựa của con, còn con làm thế nào?",},
        [2546] = {3002,"Tào Ngang","Tào Tháo","Phụ thân cứ yên tâm, con võ nghệ cao cường, sẽ ở lại chặn hậu! (Nói xong vỗ mông ngựa, ngựa chở Tào Tháo phóng đi, phía sau tên bắn như mưa, Tào Ngang ra sức chiến đấu)","Phụ thân cứ yên tâm, con võ nghệ cao cường, sẽ ở lại chặn hậu! (Nói xong vỗ mông ngựa, ngựa chở Tào Tháo phóng đi, phía sau tên bắn như mưa, Tào Ngang ra sức chiến đấu)",},
        [2547] = {3003,"Đinh Phu Nhân","Tào Tháo","(Đinh phu nhân nghe nói Tào Ngang tử trận, tức giận về quê nhà) Tào Tháo, ngài đã hại con trai ta, giờ đây ta không còn gì để lưu luyến nữa!","(Đinh phu nhân nghe nói Tào Ngang tử trận, tức giận về quê nhà) Tào Tháo, ngài đã hại con trai ta, giờ đây ta không còn gì để lưu luyến nữa!",},
        [2548] = {3003,"Tào Tháo","Đinh Phu Nhân","Phu nhân, chúng ta cùng ngồi xe về nhà, được không?","Phu nhân, chúng ta cùng ngồi xe về nhà, được không?",},
        [2549] = {3003,"Đinh Phu Nhân","Tào Tháo","Không!","Không!",},
        [2550] = {3003,"Tào Tháo","Đinh Phu Nhân","Phu nhân, về với ta được không?","Phu nhân, về với ta được không?",},
        [2551] = {3003,"Đinh Phu Nhân","Tào Tháo","Không!","Không!",},
        [2552] = {3003,"Tào Tháo","Đinh Phu Nhân","Cả đời ta làm chuyện gì cũng không thấy hối hận. Nếu qua đời mà vẫn còn linh hồn, nếu Tào Ngang hỏi ta mẫu thân nó ở đâu thì ta trả lời sao đây!","Cả đời ta làm chuyện gì cũng không thấy hối hận. Nếu qua đời mà vẫn còn linh hồn, nếu Tào Ngang hỏi ta mẫu thân nó ở đâu thì ta trả lời sao đây!",},
        [2553] = {3004,"Hạ Hầu Đôn","Nhân vật","Dương Uy Tướng, ta kính trọng ngươi và thừa tướng, nhưng sao ngươi lại tiêu diệt quân Thanh Châu của ta?","Dương Uy Tướng, ta kính trọng ngươi và thừa tướng, nhưng sao ngươi lại tiêu diệt quân Thanh Châu của ta?",},
        [2554] = {3004,"Nhân vật","Hạ Hầu Đôn","Hạ Hầu tướng quân, lại dám hỏi lại ta sao? Quân Thanh Châu của ngươi lúc đầu là Giặc Hoàng Cân mà ta thu phục cho thừa tướng.","Hạ Hầu tướng quân, lại dám hỏi lại ta sao? Quân Thanh Châu của ngươi lúc đầu là Giặc Hoàng Cân mà ta thu phục cho thừa tướng.",},
        [2555] = {3004,"Nhân vật","Hạ Hầu Đôn","Ta từ Uyển Thành đến ứng cứu thừa tướng, dọc đường lại thấy quân Thanh Châu cướp bóc nhà dân, nên ta diệt! Sao, không phục à?","Ta từ Uyển Thành đến ứng cứu thừa tướng, dọc đường lại thấy quân Thanh Châu cướp bóc nhà dân, nên ta diệt! Sao, không phục à?",},
        [2556] = {3004,"Hạ Hầu Đôn","Nhân vật","Hạ Hầu Đôn lỗ mãng! Có chuyện thế này là tại Hạ Hầu Đôn trị quân không nghiêm!","Hạ Hầu Đôn lỗ mãng! Có chuyện thế này là tại Hạ Hầu Đôn trị quân không nghiêm!",},
        [2557] = {3005,"Tào Tháo","Vu Cấm","Vu Cấm, quân Thanh Châu nói ngươi thừa chuyện ở Uyển Thành mà làm phản, ngươi có biết tội không!","Vu Cấm, quân Thanh Châu nói ngươi thừa chuyện ở Uyển Thành mà làm phản, ngươi có biết tội không!",},
        [2558] = {3005,"Nhân vật","Tào Tháo","Tào huynh, chuyện này oan cho đệ quá!","Tào huynh, chuyện này oan cho đệ quá!",},
        [2559] = {3005,"Tào Tháo","Nhân vật","Hả?","Hả?",},
        [2560] = {3005,"Nhân vật","Tào Tháo","Quân Thanh Châu đó vốn là giặc Hoàng Cân, chắc huynh còn nhớ. Trong số họ có vài người thừa lúc hỗn loạn cướp bóc nhà dân, ta và Vu Cấm tướng quân nhìn thấy, đã dạy cho chúng một bài học.","Quân Thanh Châu đó vốn là giặc Hoàng Cân, chắc huynh còn nhớ. Trong số họ có vài người thừa lúc hỗn loạn cướp bóc nhà dân, ta và Vu Cấm tướng quân nhìn thấy, đã dạy cho chúng một bài học.",},
        [2561] = {3005,"Tào Tháo","Vu Cấm","Thì ra là vậy. Vu Cấm, suýt nữa đã trách lầm ngươi, sao ngươi không báo cho ta biết sớm?","Thì ra là vậy. Vu Cấm, suýt nữa đã trách lầm ngươi, sao ngươi không báo cho ta biết sớm?",},
        [2562] = {3005,"Vu Cấm","Tào Tháo","Lúc đó tình hình khẩn cấp, kẻ địch đuổi theo phía sau, ta chống cự giặc, Tào công là người sáng suốt, chắc sẽ không tin vào lời đồn nhảm.","Lúc đó tình hình khẩn cấp, kẻ địch đuổi theo phía sau, ta chống cự giặc, Tào công là người sáng suốt, chắc sẽ không tin vào lời đồn nhảm.",},
        [2563] = {3005,"Tào Tháo","Nhân vật","Tướng quân trong lúc bối rối, thế mà nghiêm được binh, bền được lũy, mặc người gièm chê, làm cho chuyển bại thành thắng, dù danh tướng đời xưa, vị tất đã có ai hơn!","Tướng quân trong lúc bối rối, thế mà nghiêm được binh, bền được lũy, mặc người gièm chê, làm cho chuyển bại thành thắng, dù danh tướng đời xưa, vị tất đã có ai hơn!",},
        [2564] = {3005,"Nhân vật","Tào Tháo","Tào huynh anh minh!","Tào huynh anh minh!",},
        [2565] = {3005,"Tào Tháo","Nhân vật","Trận Uyển Thành, ta mất con trưởng và cháu yêu, cũng không thương là mấy, chỉ thương khóc Điển Vi mà thôi!","Trận Uyển Thành, ta mất con trưởng và cháu yêu, cũng không thương là mấy, chỉ thương khóc Điển Vi mà thôi!",},
        [2566] = {3101,"Viên Thuật","Các Tượng","Xưa Hán Cao Tổ chỉ là một đình trưởng ở Tứ Thượng, thế mà lấy được thiên hạ; Đã qua 400 năm, khí số đã hết, khắp nơi loạn tứ tung như vạc sôi.","Xưa Hán Cao Tổ chỉ là một đình trưởng ở Tứ Thượng, thế mà lấy được thiên hạ; Đã qua 400 năm, khí số đã hết, khắp nơi loạn tứ tung như vạc sôi.",},
        [2567] = {3101,"Viên Thuật","Các Tượng","Nhà ta 4 đời làm đến tam công, trăm họ ai cũng trông ngóng; ta muốn ứng vận trời, thuận lòng người, lên ngôi cửu ngũ, các người nghĩ thế nào?","Nhà ta 4 đời làm đến tam công, trăm họ ai cũng trông ngóng; ta muốn ứng vận trời, thuận lòng người, lên ngôi cửu ngũ, các người nghĩ thế nào?",},
        [2568] = {3101,"Các Tượng","Viên Thuật","Nay minh công gia thế tuy rằng quý, nhưng chưa được thịnh bằng nhà Chu; Nhà Hán tuy rằng suy, nhưng chưa đến nỗi tàn bạo như vua Trụ nhà Ân. Việc ấy quyết không nền làm.","Nay minh công gia thế tuy rằng quý, nhưng chưa được thịnh bằng nhà Chu; Nhà Hán tuy rằng suy, nhưng chưa đến nỗi tàn bạo như vua Trụ nhà Ân. Việc ấy quyết không nền làm.",},
        [2569] = {3101,"Viên Thuật","Các Tượng","Ta họ Viên do từ họ Trần mà ra, thuộc về hành Thổ, lấy Thổ kế vào Hòa chính ứng vận trời. Có câu sấm: Thay nhà Hán ấy là chỗ cao ở giữa đường. Tên tự ta là Công Lộ, thế là chính ứng câu sấm ấy.","Ta họ Viên do từ họ Trần mà ra, thuộc về hành Thổ, lấy Thổ kế vào Hòa chính ứng vận trời. Có câu sấm: Thay nhà Hán ấy là chỗ cao ở giữa đường. Tên tự ta là Công Lộ, thế là chính ứng câu sấm ấy.",},
        [2570] = {3101,"Viên Thuật","Các Tượng","Lại có Ngọc Tỉ truyền quốc, nếu ta không làm vua, thì hóa ra trái đạo trời đất. Ý ta đã quyết, ai còn can nữa ta trảm!","Lại có Ngọc Tỉ truyền quốc, nếu ta không làm vua, thì hóa ra trái đạo trời đất. Ý ta đã quyết, ai còn can nữa ta trảm!",},
        [2571] = {3102,"Tinh Thái","Nhân vật","Chúa công, nghe nói Viên Thuật to gan làm càn, muốn xưng đế ở Thọ Xuyến!","Chúa công, nghe nói Viên Thuật to gan làm càn, muốn xưng đế ở Thọ Xuyến!",},
        [2572] = {3102,"Ngân Bình","Nhân vật","Viên Thuật xưa nay lòng dạ bất chính, lại muốn xưng đế! Chúa công nhà ta là Dương Uy Tướng được thiên tử Đại Hán phong cho!","Viên Thuật xưa nay lòng dạ bất chính, lại muốn xưng đế! Chúa công nhà ta là Dương Uy Tướng được thiên tử Đại Hán phong cho!",},
        [2573] = {3102,"Nhân vật","Ngân Bình","Viên Thuật mà trở thành hoàng đế, ta sẽ trèo lên thành Lạc Dương 3 vòng! Hừ!","Viên Thuật mà trở thành hoàng đế, ta sẽ trèo lên thành Lạc Dương 3 vòng! Hừ!",},
        [2574] = {3102,"Tinh Thái","Nhân vật","Chúa công, giờ trời đang lạnh, ngài trèo lên thành Lạc Dương sẽ bị cóng đấy!","Chúa công, giờ trời đang lạnh, ngài trèo lên thành Lạc Dương sẽ bị cóng đấy!",},
        [2575] = {3102,"Ngân Bình","Nhân vật","Tinh Thái, câu đùa của ngươi nhạt quá!","Tinh Thái, câu đùa của ngươi nhạt quá!",},
        [2576] = {3102,"Nhân vật","Ngân Bình","(Lúng túng cười) Đi đi đi, tìm người cùng đánh Viên Thuật!","(Lúng túng cười) Đi đi đi, tìm người cùng đánh Viên Thuật!",},
        [2577] = {3103,"Nhân vật","Viên Thuật","Viên Thuật, ngươi ngông cuồng tự xưng đế, bị chư hầu xem thường, ta đã liên minh với Tào Tháo, Lưu Bị và Lữ Bố, 3 đại quân đang tiến về Thọ Xuyến, ngươi sẽ thành mục tiêu công kích!","Viên Thuật, ngươi ngông cuồng tự xưng đế, bị chư hầu xem thường, ta đã liên minh với Tào Tháo, Lưu Bị và Lữ Bố, 3 đại quân đang tiến về Thọ Xuyến, ngươi sẽ thành mục tiêu công kích!",},
        [2578] = {3103,"Viên Thuật","Nhân vật","Ngọc Tỉ trong tay, thiên hạ của ta! Ai dám ngăn ta, ta sẽ diệt hắn!","Ngọc Tỉ trong tay, thiên hạ của ta! Ai dám ngăn ta, ta sẽ diệt hắn!",},
        [2579] = {3104,"Ngân Bình","Nhân vật","Viên Thuật này thật buồn cười! Chúa công nhìn hắn đi, đánh không thắng liền bỏ chạy! Thế là có được 1 Danh Tướng Lệnh!","Viên Thuật này thật buồn cười! Chúa công nhìn hắn đi, đánh không thắng liền bỏ chạy! Thế là có được 1 Danh Tướng Lệnh!",},
        [2580] = {3104,"Nhân vật","Ngân Bình","Viên Thuật tuy bị chúng ta đánh bại, nhưng phạm vi thế lực của hắn rất lớn, chúng ta vẫn phải nghĩ cách.","Viên Thuật tuy bị chúng ta đánh bại, nhưng phạm vi thế lực của hắn rất lớn, chúng ta vẫn phải nghĩ cách.",},
        [2581] = {3104,"Ngân Bình","Nhân vật","Viên Thuật chiếm Hoa Dương, Nhữ Ân và Giang Hoài, địa bàn lớn, người đông, lương thực dồi dào, phải nghĩ cách làm suy yếu thế lực của hắn.","Viên Thuật chiếm Hoa Dương, Nhữ Ân và Giang Hoài, địa bàn lớn, người đông, lương thực dồi dào, phải nghĩ cách làm suy yếu thế lực của hắn.",},
        [2582] = {3104,"Nhân vật","Ngân Bình","Thế lực của Viên Thuật, ai khác thì không nói, vùng Giang Đông này thì ta có thể tiêu diệt hắn!","Thế lực của Viên Thuật, ai khác thì không nói, vùng Giang Đông này thì ta có thể tiêu diệt hắn!",},
        [2583] = {3104,"Ngân Bình","Nhân vật","Lúc đầu Tôn Kiên tìm được Ngọc Tỉ từ Lạc Dương, đem đến Giang Đông, sau bị Viên Thuật cướp, Tôn Sách rất hận Viên Thuật!","Lúc đầu Tôn Kiên tìm được Ngọc Tỉ từ Lạc Dương, đem đến Giang Đông, sau bị Viên Thuật cướp, Tôn Sách rất hận Viên Thuật!",},
        [2584] = {3104,"Nhân vật","Ngân Bình","Đi, đến Giang Đông tìm Tôn Sách ôn chuyện cũ!","Đi, đến Giang Đông tìm Tôn Sách ôn chuyện cũ!",},
        [2585] = {3201,"Tôn Sách","Nhân vật","Tiểu anh hùng hiếm khi tới Giang Đông ta, ngươi đã giúp ta rất nhiều lần, ta phải tiếp đã đàng hoàng! Người đâu, gọi rượu ngon món ngon!","Tiểu anh hùng hiếm khi tới Giang Đông ta, ngươi đã giúp ta rất nhiều lần, ta phải tiếp đã đàng hoàng! Người đâu, gọi rượu ngon món ngon!",},
        [2586] = {3201,"Nhân vật","Tôn Sách","Tôn huynh khách sáo, ha ha! Vậy phải đem rượu quý của huynh ra đây, đừng tiếc!","Tôn huynh khách sáo, ha ha! Vậy phải đem rượu quý của huynh ra đây, đừng tiếc!",},
        [2587] = {3201,"Tôn Sách","Nhân vật","Nói hay lắm, nào, ta kính đệ 1 ly!","Nói hay lắm, nào, ta kính đệ 1 ly!",},
        [2588] = {3201,"Nhân vật","Tôn Sách","(Ngà ngà say) Tôn huynh, đệ có một chuyện khuyên nhủ!","(Ngà ngà say) Tôn huynh, đệ có một chuyện khuyên nhủ!",},
        [2589] = {3201,"Tôn Sách","Nhân vật","Hai ta không phải người ngoài, có việc gì cứ nói!","Hai ta không phải người ngoài, có việc gì cứ nói!",},
        [2590] = {3201,"Nhân vật","Tôn Sách","Viên Thuật tiếm quyền thiên tử, bị cô lập, ắt sẽ trở thành mục tiêu công kích, ngươi nên đoạn tuyệt với ông ta thì hơn.","Viên Thuật tiếm quyền thiên tử, bị cô lập, ắt sẽ trở thành mục tiêu công kích, ngươi nên đoạn tuyệt với ông ta thì hơn.",},
        [2591] = {3201,"Tôn Sách","Nhân vật","Ha ha, đúng là anh hùng có khác. Viên Thuật đã có ý xưng đế, ta từng viết thư can gián, ai ngờ ông ta không nghe. Nghe ngươi nói chuyện này hôm nay, ta càng muốn đoạn tuyệt với ông ta!","Ha ha, đúng là anh hùng có khác. Viên Thuật đã có ý xưng đế, ta từng viết thư can gián, ai ngờ ông ta không nghe. Nghe ngươi nói chuyện này hôm nay, ta càng muốn đoạn tuyệt với ông ta!",},
        [2592] = {3201,"Nhân vật","Tôn Sách","Ha ha, Tôn huynh rất có trình độ!","Ha ha, Tôn huynh rất có trình độ!",},
        [2593] = {3201,"Tôn Sách","Nhân vật","Gần đây ta định tấn công Lư Giang, thống nhất 6 quận Giang Đông, tiểu anh hùng có muốn giúp ta không?","Gần đây ta định tấn công Lư Giang, thống nhất 6 quận Giang Đông, tiểu anh hùng có muốn giúp ta không?",},
        [2594] = {3201,"Nhân vật","Tôn Sách","Xin sẵn lòng!","Xin sẵn lòng!",},
        [2595] = {3201,"Tôn Sách","Nhân vật","Ha ha! Tiểu anh hùng quả nhiên hào khí ngút trời!","Ha ha! Tiểu anh hùng quả nhiên hào khí ngút trời!",},
        [2596] = {3202,"Tôn Sách","Nhân vật","Thái thú Lư Giang Lưu Huân chạy đến Hứa Xương nương nhờ Tào Tháo.","Thái thú Lư Giang Lưu Huân chạy đến Hứa Xương nương nhờ Tào Tháo.",},
        [2597] = {3202,"Nhân vật","Tôn Sách","Hoàng Tổ cũng bị chúng ta đánh bại, nhưng tiếc chưa báo thù được cho Tôn huynh!","Hoàng Tổ cũng bị chúng ta đánh bại, nhưng tiếc chưa báo thù được cho Tôn huynh!",},
        [2598] = {3202,"Tôn Sách","Nhân vật","Đừng vội, đợi ta thống nhất Giang Đông, ta sớm muộn cũng sẽ tiêu diệt hắn ta, để tế vong hồn của cha ta!","Đừng vội, đợi ta thống nhất Giang Đông, ta sớm muộn cũng sẽ tiêu diệt hắn ta, để tế vong hồn của cha ta!",},
        [2599] = {3202,"Nhân vật","Tôn Sách","Chỉ còn lại Dự Chương, hãy xông lên!","Chỉ còn lại Dự Chương, hãy xông lên!",},
        [2600] = {3203,"Nhân vật","Tôn Sách","Tôn huynh hôm nay có vẻ rất vui!","Tôn huynh hôm nay có vẻ rất vui!",},
        [2601] = {3203,"Tôn Sách","Nhân vật","Ha ha, Ngu Phiên khuyên hàng thành công, Dự Chương không cần tốn một binh một tốt đã có thể chiếm được, đương nhiên ta vui!","Ha ha, Ngu Phiên khuyên hàng thành công, Dự Chương không cần tốn một binh một tốt đã có thể chiếm được, đương nhiên ta vui!",},
        [2602] = {3203,"Nhân vật","Tôn Sách","Dưới trướng Tôn huynh quả thật nhiều người đa tài, vị thiếu niên phía trước là ai? Nhìn dáng vẻ chắc là nhân vật quan trọng.","Dưới trướng Tôn huynh quả thật nhiều người đa tài, vị thiếu niên phía trước là ai? Nhìn dáng vẻ chắc là nhân vật quan trọng.",},
        [2603] = {3203,"Tôn Sách","Nhân vật","Chúng ta hãy cùng đi xem thử nào.","Chúng ta hãy cùng đi xem thử nào.",},
        [2604] = {3204,"Tôn Sách","Lục Tốn","Ta là Tôn Sách, ngươi là ai?","Ta là Tôn Sách, ngươi là ai?",},
        [2605] = {3204,"Lục Tốn","Tôn Sách","Bẩm tướng quân, tiểu sinh Lục Tốn, tự Bá Ngô, vốn là người Ngô Quận.","Bẩm tướng quân, tiểu sinh Lục Tốn, tự Bá Ngô, vốn là người Ngô Quận.",},
        [2606] = {3204,"Tôn Sách","Lục Tốn","Ta biết Ngô Quận có tứ đại gia tộc, Lục, Cố, Chu, Trương. Ngươi là người của gia tộc Lục?","Ta biết Ngô Quận có tứ đại gia tộc, Lục, Cố, Chu, Trương. Ngươi là người của gia tộc Lục?",},
        [2607] = {3204,"Lục Tốn","Tôn Sách","Đúng vậy.","Đúng vậy.",},
        [2608] = {3204,"Tôn Sách","Lục Tốn","Rất tốt, quả là phong thái của người từ gia tộc lớn, ta tin chắc sau này sẽ có thành quả lớn, ngươi đã có gia thất chưa?","Rất tốt, quả là phong thái của người từ gia tộc lớn, ta tin chắc sau này sẽ có thành quả lớn, ngươi đã có gia thất chưa?",},
        [2609] = {3204,"Lục Tốn","Tôn Sách","Vẫn chưa.","Vẫn chưa.",},
        [2610] = {3204,"Tôn Sách","Lục Tốn","Ha ha, tốt, vậy ta sẽ gả con gái lớn của ta cho ngươi.","Ha ha, tốt, vậy ta sẽ gả con gái lớn của ta cho ngươi.",},
        [2611] = {3205,"Tôn Sách","Nhân vật","Tiểu anh hùng, con rể mới của ta thế nào?","Tiểu anh hùng, con rể mới của ta thế nào?",},
        [2612] = {3205,"Nhân vật","Tôn Sách","Xuất thân từ gia tộc lớn, lại có học thức, tương lai sẽ rộng mở.","Xuất thân từ gia tộc lớn, lại có học thức, tương lai sẽ rộng mở.",},
        [2613] = {3205,"Tôn Sách","Nhân vật","Ha ha, ta cũng cảm thấy vậy, hôm nay rất vui, chúng ta hãy ra ngoài săn bắn!","Ha ha, ta cũng cảm thấy vậy, hôm nay rất vui, chúng ta hãy ra ngoài săn bắn!",},
        [2614] = {3205,"Nhân vật","Tôn Sách","Tôn huynh, hãy mang thêm tùy tùng.","Tôn huynh, hãy mang thêm tùy tùng.",},
        [2615] = {3205,"Tôn Sách","Nhân vật","Ta là Tiểu Bá Vương Tôn Sách! Hiện ta đã bình định Lục Quận, Giang Đông đều là của ta! Ta biết ngươi thích Danh Tướng Lệnh, nên đã sai người tìm kiếm mang tặng cho ngươi!","Ta là Tiểu Bá Vương Tôn Sách! Hiện ta đã bình định Lục Quận, Giang Đông đều là của ta! Ta biết ngươi thích Danh Tướng Lệnh, nên đã sai người tìm kiếm mang tặng cho ngươi!",},
        [2616] = {3205,"Nhân vật","Tôn Sách","Tôn huynh quả nhiên khí khái hơn người, đa tạ!","Tôn huynh quả nhiên khí khái hơn người, đa tạ!",},
        [2617] = {3301,"Tôn Sách","Nhân vật","Tiểu anh hùng, có thể giúp ta một việc nữa không?","Tiểu anh hùng, có thể giúp ta một việc nữa không?",},
        [2618] = {3301,"Nhân vật","Tôn Sách","Tôn huynh là vì hai người con gái Đại Kiều, Tiểu Kiều của Kiều Công mà đến?","Tôn huynh là vì hai người con gái Đại Kiều, Tiểu Kiều của Kiều Công mà đến?",},
        [2619] = {3301,"Tôn Sách","Nhân vật","Tiểu anh hùng quả là thần cơ diệu toán.","Tiểu anh hùng quả là thần cơ diệu toán.",},
        [2620] = {3301,"Nhân vật","Tôn Sách","Tinh Thái và Tiểu Kiều vốn quen biết, cô ấy đã nói việc này với ta!","Tinh Thái và Tiểu Kiều vốn quen biết, cô ấy đã nói việc này với ta!",},
        [2621] = {3301,"Tôn Sách","Nhân vật","Thì ra là thế, Tôn mỗ thành tâm nhờ ngươi giúp đỡ, ta và Công Cẩn đã từng gặp hai tỉ muội họ, ta đã thích họ.","Thì ra là thế, Tôn mỗ thành tâm nhờ ngươi giúp đỡ, ta và Công Cẩn đã từng gặp hai tỉ muội họ, ta đã thích họ.",},
        [2622] = {3301,"Tôn Sách","Nhân vật","Nhưng Kiều Công vì việc ta tấn công Uyển Thành không đồng ý gả họ cho ta. Giờ ngươi là Dương Uy Tướng do thiên tử phong, ngươi hãy giúp ta cầu thân, chắc chắn ôn ấy sẽ không từ chối.","Nhưng Kiều Công vì việc ta tấn công Uyển Thành không đồng ý gả họ cho ta. Giờ ngươi là Dương Uy Tướng do thiên tử phong, ngươi hãy giúp ta cầu thân, chắc chắn ôn ấy sẽ không từ chối.",},
        [2623] = {3301,"Nhân vật","Tôn Sách","Để ta thử xem sao!","Để ta thử xem sao!",},
        [2624] = {3302,"Nhân vật","Tinh Thái","Tinh Thái sao lại là cô?","Tinh Thái sao lại là cô?",},
        [2625] = {3302,"Tinh Thái","Nhân vật","Chúa công, Kiều Công nói người thông qua tỉ thí của hai chị em Tiểu Kiều, thì ông ấy sẽ đồng ý!","Chúa công, Kiều Công nói người thông qua tỉ thí của hai chị em Tiểu Kiều, thì ông ấy sẽ đồng ý!",},
        [2626] = {3302,"Nhân vật","Tinh Thái","Việc này đơn giản, không cần nhiều lời, ta sẽ dễ dàng vượt qua tỉ thí!","Việc này đơn giản, không cần nhiều lời, ta sẽ dễ dàng vượt qua tỉ thí!",},
        [2627] = {3302,"Tinh Thái","Nhân vật","Chúa công lợi hại, hãy bắt đầu nghênh chiến!","Chúa công lợi hại, hãy bắt đầu nghênh chiến!",},
        [2628] = {3303,"Ngân Bình","Thượng Hương","Thượng Hương tỉ tỉ, võ nghệ người cao cường, tương lai nhất định sẽ lấy một vị đại anh hùng!","Thượng Hương tỉ tỉ, võ nghệ người cao cường, tương lai nhất định sẽ lấy một vị đại anh hùng!",},
        [2629] = {3303,"Thượng Hương","Ngân Bình","Ta không muốn lấy chồng, ta muốn ra trận diệt địch!","Ta không muốn lấy chồng, ta muốn ra trận diệt địch!",},
        [2630] = {3303,"Ngân Bình","Thượng Hương","Tỉ tỉ, lấy đại anh hùng thì mới có cơ hội giúp phu quân ra chiến trường! Nếu lấy một người bình thường thì chỉ có thể giúp phu quân sinh con đẻ cái!","Tỉ tỉ, lấy đại anh hùng thì mới có cơ hội giúp phu quân ra chiến trường! Nếu lấy một người bình thường thì chỉ có thể giúp phu quân sinh con đẻ cái!",},
        [2631] = {3303,"Thượng Hương","Ngân Bình","Ngươi nói cũng có lý, để ta tìm mẹ ta, ta phải lấy một vị đại anh hùng!","Ngươi nói cũng có lý, để ta tìm mẹ ta, ta phải lấy một vị đại anh hùng!",},
        [2632] = {3304,"Tinh Thái","Đại Kiều","Đại Kiều tỉ tỉ, người rất đẹp, quả là quốc sắc thiên hương!","Đại Kiều tỉ tỉ, người rất đẹp, quả là quốc sắc thiên hương!",},
        [2633] = {3304,"Đại Kiều","Tinh Thái","Tinh Thái muội muội, muội cũng rất dễ thương.","Tinh Thái muội muội, muội cũng rất dễ thương.",},
        [2634] = {3304,"Ngân Bình","Tinh Thái","Quan Ngân Bình mới là đệ nhất thiên hạ!","Quan Ngân Bình mới là đệ nhất thiên hạ!",},
        [2635] = {3304,"Tinh Thái","Ngân Bình","...Quan Ngân Bình!","...Quan Ngân Bình!",},
        [2636] = {3304,"Ngân Bình","Đại Kiều","Đừng đùa nữa! Đại Kiều tỉ tỉ, chúa công bảo ta hỏi tỉ có đồng ý lấy Tôn Sách không?","Đừng đùa nữa! Đại Kiều tỉ tỉ, chúa công bảo ta hỏi tỉ có đồng ý lấy Tôn Sách không?",},
        [2637] = {3304,"Đại Kiều","Tinh Thái","Ta đồng ý lấy Tôn lang!","Ta đồng ý lấy Tôn lang!",},
        [2638] = {3304,"Ngân Bình","Đại Kiều","Ha ha, vậy thì tốt, quả là trai tài gái sắc!","Ha ha, vậy thì tốt, quả là trai tài gái sắc!",},
        [2639] = {3305,"Tiểu Kiều","Nhân vật","Dương Uy Tướng, ta cuối cùng có thể gặp được người! Xin tha thứ cho sự lỗ mãng của Tiểu Kiều!","Dương Uy Tướng, ta cuối cùng có thể gặp được người! Xin tha thứ cho sự lỗ mãng của Tiểu Kiều!",},
        [2640] = {3305,"Nhân vật","Tiểu Kiều","Sao lại nói vậy?","Sao lại nói vậy?",},
        [2641] = {3305,"Tiểu Kiều","Nhân vật","Thật ra ta và Công Cẩn sớm đã quen biết nhau, Thỏ Trắng của ta cũng là do Công Cẩn tặng! Để chị và Tôn Lang gặp nhau cũng là ý của ta! Yêu cầu tỉ thí là do ý của ta!","Thật ra ta và Công Cẩn sớm đã quen biết nhau, Thỏ Trắng của ta cũng là do Công Cẩn tặng! Để chị và Tôn Lang gặp nhau cũng là ý của ta! Yêu cầu tỉ thí là do ý của ta!",},
        [2642] = {3305,"Ngân Bình","Tiểu Kiều","Tiểu Kiều muội muội quả thật nghịch ngợm!","Tiểu Kiều muội muội quả thật nghịch ngợm!",},
        [2643] = {3305,"Tiểu Kiều","Ngân Bình","Ta yêu cầu tỉ thí là vì muốn gặp Dương Uy Tướng!","Ta yêu cầu tỉ thí là vì muốn gặp Dương Uy Tướng!",},
        [2644] = {3305,"Tiểu Kiều","Nhân vật","Dương Uy Tướng, Tiểu Kiều tuy ở Giang Đông, nhưng sớm đã nghe danh người.","Dương Uy Tướng, Tiểu Kiều tuy ở Giang Đông, nhưng sớm đã nghe danh người.",},
        [2645] = {3305,"Tiểu Kiều","Nhân vật","Hôm nay, được thấy phong thái của tướng quân quả danh bất hư truyền! Công Cẩn nói người thích Danh Tướng Lệnh, ta đây có 1 cái, tặng cho tướng quân!","Hôm nay, được thấy phong thái của tướng quân quả danh bất hư truyền! Công Cẩn nói người thích Danh Tướng Lệnh, ta đây có 1 cái, tặng cho tướng quân!",},
        [2646] = {3305,"Nhân vật","Tiểu Kiều","Ha ha, đa tạ! Nếu hai tỉ muội họ đều đồng ý, vậy thì hãy sớm tiến hành hôn lễ!","Ha ha, đa tạ! Nếu hai tỉ muội họ đều đồng ý, vậy thì hãy sớm tiến hành hôn lễ!",},
        [2647] = {3401,"Chu Du","Nhân vật","Dương Uy Tướng, Công Cẩn thật đa tạ ngươi, nếu không có ngươi, e rằng hôn sự này khó thành.","Dương Uy Tướng, Công Cẩn thật đa tạ ngươi, nếu không có ngươi, e rằng hôn sự này khó thành.",},
        [2648] = {3401,"Nhân vật","Chu Du","Từ xưa Giang Đông nổi tiếng nhiều mỹ nhân, Đại Tiểu Kiều quốc sắc thiên hương, người và Bá Phù đều có được mỹ nhân, quả thật đáng mừng.","Từ xưa Giang Đông nổi tiếng nhiều mỹ nhân, Đại Tiểu Kiều quốc sắc thiên hương, người và Bá Phù đều có được mỹ nhân, quả thật đáng mừng.",},
        [2649] = {3401,"Chu Du","Nhân vật","Dương Uy Tướng quá khen...","Dương Uy Tướng quá khen...",},
        [2650] = {3401,"Nhân vật","Chu Du","Mau mang rượu cho Công Cẩn! Ta xin cạn chén trước!","Mau mang rượu cho Công Cẩn! Ta xin cạn chén trước!",},
        [2651] = {3401,"Chu Du","Nhân vật","Tướng quân uống nhiều rồi!","Tướng quân uống nhiều rồi!",},
        [2652] = {3401,"Nhân vật","Chu Du","Không, ta không say, vị kia có phải là Thái Sử Từ tướng quân không? Ta muốn tỉ thí với hắn!","Không, ta không say, vị kia có phải là Thái Sử Từ tướng quân không? Ta muốn tỉ thí với hắn!",},
        [2653] = {3402,"Ngân Bình","Thái Sử Từ","Thái Sử Từ tướng quân, chúa công ta uống say nên muốn tỉ thí với ngài!","Thái Sử Từ tướng quân, chúa công ta uống say nên muốn tỉ thí với ngài!",},
        [2654] = {3402,"Thái Sử Từ","Ngân Bình","Ngân Bình cô nương yên tâm, ta sẽ không đả thương chúa công đâu!","Ngân Bình cô nương yên tâm, ta sẽ không đả thương chúa công đâu!",},
        [2655] = {3402,"Ngân Bình","Thái Sử Từ","Ta chỉ sợ chúa công vốn lợi hại, sau khi uống rượu sẽ càng mạnh! Ta lo ngươi bị đả thương nên nhắc nhở!","Ta chỉ sợ chúa công vốn lợi hại, sau khi uống rượu sẽ càng mạnh! Ta lo ngươi bị đả thương nên nhắc nhở!",},
        [2656] = {3402,"Thái Sử Từ","Ngân Bình","...Đa tạ cô nương nhắc nhở.","...Đa tạ cô nương nhắc nhở.",},
        [2657] = {3403,"Tinh Thái","Nhân vật","Chúa công, Tinh Thái đã chuẩn bị canh giải rượu! Hãy mau uống.","Chúa công, Tinh Thái đã chuẩn bị canh giải rượu! Hãy mau uống.",},
        [2658] = {3403,"Nhân vật","Tinh Thái","Đa tạ Tinh Thái, ta đã đỡ nhiều! Hôm qua uống hơi nhiều.","Đa tạ Tinh Thái, ta đã đỡ nhiều! Hôm qua uống hơi nhiều.",},
        [2659] = {3403,"Tinh Thái","Nhân vật","Chúa công, bên ngoài có vài vị anh hùng đến xin gặp, họ đợi đã lâu.","Chúa công, bên ngoài có vài vị anh hùng đến xin gặp, họ đợi đã lâu.",},
        [2660] = {3403,"Nhân vật","Tinh Thái","Là ai vậy?","Là ai vậy?",},
        [2661] = {3403,"Tinh Thái","Nhân vật","Đây là thiệp xin gặp của họ, xin chúa công xem qua!","Đây là thiệp xin gặp của họ, xin chúa công xem qua!",},
        [2662] = {3403,"Nhân vật","Tinh Thái","Thì ra là Lỗ Túc, Lữ Mông, báo cho Chu Du, chúng ta cùng nhau đến gặp họ.","Thì ra là Lỗ Túc, Lữ Mông, báo cho Chu Du, chúng ta cùng nhau đến gặp họ.",},
        [2663] = {3404,"Lỗ Túc","Nhân vật","Lỗ Túc bái kiến tướng quân!","Lỗ Túc bái kiến tướng quân!",},
        [2664] = {3404,"Nhân vật","Lỗ Túc","Tử Kính huynh, đã lâu không gặp! Ta nghe Công Cẩn nhắc qua, người là chỗ quen biết cũ với ngài ấy.","Tử Kính huynh, đã lâu không gặp! Ta nghe Công Cẩn nhắc qua, người là chỗ quen biết cũ với ngài ấy.",},
        [2665] = {3404,"Chu Du","Lỗ Túc","Lúc xưa khi ta còn là Sào Trưởng, nghe đồn Tử Kính huynh hay giúp đỡ người khác nên đến nhờ huynh. Tử Kính huynh đã cho ta cả kho lương thực.","Lúc xưa khi ta còn là Sào Trưởng, nghe đồn Tử Kính huynh hay giúp đỡ người khác nên đến nhờ huynh. Tử Kính huynh đã cho ta cả kho lương thực.",},
        [2666] = {3404,"Nhân vật","Lỗ Túc","Tử Kính huynh quả là hào phóng! Ai ai đều đồn huynh là vị thần tài sống, quả thật không sai!","Tử Kính huynh quả là hào phóng! Ai ai đều đồn huynh là vị thần tài sống, quả thật không sai!",},
        [2667] = {3404,"Lỗ Túc","Nhân vật","Tướng quân sau này nếu có khó khăn có thể tìm ta, Tử Kính chắc chắn sẽ không từ chối!","Tướng quân sau này nếu có khó khăn có thể tìm ta, Tử Kính chắc chắn sẽ không từ chối!",},
        [2668] = {3405,"Lữ Mông","Nhân vật","Lữ Mông bái kiến tướng quân!","Lữ Mông bái kiến tướng quân!",},
        [2669] = {3405,"Nhân vật","Lữ Mông","Tử Minh tuổi trẻ tài cao, quả là anh hùng xuất thiếu niên!","Tử Minh tuổi trẻ tài cao, quả là anh hùng xuất thiếu niên!",},
        [2670] = {3405,"Lữ Mông","Nhân vật","Tướng quân quá khen!","Tướng quân quá khen!",},
        [2671] = {3405,"Nhân vật","Lữ Mông","Sao người lại mang theo mỏ neo và thẻ tre?","Sao người lại mang theo mỏ neo và thẻ tre?",},
        [2672] = {3405,"Lữ Mông","Nhân vật","Mỏ neo là vũ khí do ta chế tạo, thẻ tre là binh thư, Tử Kính huynh khuyên ta nên đọc nhiều binh thư để sau này báo quốc.","Mỏ neo là vũ khí do ta chế tạo, thẻ tre là binh thư, Tử Kính huynh khuyên ta nên đọc nhiều binh thư để sau này báo quốc.",},
        [2673] = {3405,"Nhân vật","Lữ Mông","Được! Tương lai của Tử Minh chắc chắn sẽ rộng mở, Giang Đông quả nhiên nhiều nhân tài!","Được! Tương lai của Tử Minh chắc chắn sẽ rộng mở, Giang Đông quả nhiên nhiều nhân tài!",},
        [2674] = {3406,"Chu Du","Nhân vật","Dương Uy Tướng, hãy xem!","Dương Uy Tướng, hãy xem!",},
        [2675] = {3406,"Nhân vật","Chu Du","Đây là thư Tào Tháo viết cho người!","Đây là thư Tào Tháo viết cho người!",},
        [2676] = {3406,"Chu Du","Nhân vật","Tào Tháo dám khuyên ta đầu quân cho hắn ta.","Tào Tháo dám khuyên ta đầu quân cho hắn ta.",},
        [2677] = {3406,"Nhân vật","Chu Du","Công Cẩn văn thao võ lược, vốn là anh hùng đương đại, Tào Tháo muốn chiêu mộ cũng là việc bình thường.","Công Cẩn văn thao võ lược, vốn là anh hùng đương đại, Tào Tháo muốn chiêu mộ cũng là việc bình thường.",},
        [2678] = {3406,"Chu Du","Nhân vật","Tuy nói thế, nhưng ta và Bá Phù vốn có giao tình! Tào Tháo vốn có dã tâm, e rằng sau này chắc chắn phải chiến đấu với hắn.","Tuy nói thế, nhưng ta và Bá Phù vốn có giao tình! Tào Tháo vốn có dã tâm, e rằng sau này chắc chắn phải chiến đấu với hắn.",},
        [2679] = {3406,"Nhân vật","Chu Du","Công Cẩn có thể yên tâm! Viên Thiệu đang chiếm Ký Châu, Viên Thuật chiếm Hoài Nam, lại có thêm Lữ Bố, Tào Tháo đang gặp địch ba hướng, khó lòng chống lại, hơn nữa Giang Đông còn cách một con sông lớn!","Công Cẩn có thể yên tâm! Viên Thiệu đang chiếm Ký Châu, Viên Thuật chiếm Hoài Nam, lại có thêm Lữ Bố, Tào Tháo đang gặp địch ba hướng, khó lòng chống lại, hơn nữa Giang Đông còn cách một con sông lớn!",},
        [2680] = {3406,"Chu Du","Nhân vật","Tướng quân lần này đến Giang Đông, đã giúp đỡ rất nhiều, Danh Tướng Lệnh, xin tặng cho tướng quân.","Tướng quân lần này đến Giang Đông, đã giúp đỡ rất nhiều, Danh Tướng Lệnh, xin tặng cho tướng quân.",},
        [2681] = {3501,"Hạ Hầu Uyên","Tào Tháo","Thừa tướng muốn bình loạn thiên hạ, cần phải chiêu mộ Trương Tú, sau đó ổn định Hà Bắc.","Thừa tướng muốn bình loạn thiên hạ, cần phải chiêu mộ Trương Tú, sau đó ổn định Hà Bắc.",},
        [2682] = {3501,"Tào Tháo","Hạ Hầu Uyên","Trương Tú, là ai?","Trương Tú, là ai?",},
        [2683] = {3501,"Nhân vật","Tào Tháo","Bẩm thừa tướng, Trương Tú vốn là con trai của Phiêu Kỵ Tướng Trương Tế, nắm giữ Danh Tướng Lệnh, đóng quân tại Uyển Thành.","Bẩm thừa tướng, Trương Tú vốn là con trai của Phiêu Kỵ Tướng Trương Tế, nắm giữ Danh Tướng Lệnh, đóng quân tại Uyển Thành.",},
        [2684] = {3501,"Nhân vật","Tào Tháo","Danh hiệu Bắc Địa Thương Vương, dùng Hổ Thủ Kim Thương, Bách Điễu Triều Phụng Thương uy chấn thiên hạ.","Danh hiệu Bắc Địa Thương Vương, dùng Hổ Thủ Kim Thương, Bách Điễu Triều Phụng Thương uy chấn thiên hạ.",},
        [2685] = {3501,"Nhân vật","Tào Tháo","Người này vốn là anh hùng đương đại, nếu có thể thu phục sẽ có tác dụng lớn.","Người này vốn là anh hùng đương đại, nếu có thể thu phục sẽ có tác dụng lớn.",},
        [2686] = {3501,"Tào Tháo","Nhân vật","Theo ý của ngươi là ta nên lập tức tấn công phía nam.","Theo ý của ngươi là ta nên lập tức tấn công phía nam.",},
        [2687] = {3502,"Tào Tháo","Vu Cấm","Vu Cấm, ba quân đã tập trung xong chưa?","Vu Cấm, ba quân đã tập trung xong chưa?",},
        [2688] = {3502,"Vu Cấm","Tào Tháo","Bẩm thừa tướng, đã tập trung xong có thể xuất chinh.","Bẩm thừa tướng, đã tập trung xong có thể xuất chinh.",},
        [2689] = {3502,"Tào Tháo","Vu Cấm","Truyền lệnh xuống, lập tức xuất phát.","Truyền lệnh xuống, lập tức xuất phát.",},
        [2690] = {3503,"Tào Tháo","Quách Gia","Phụng Hiếu, dọc đường lúa mạch đã chín, sao không thấy nông dân thu hoạch?","Phụng Hiếu, dọc đường lúa mạch đã chín, sao không thấy nông dân thu hoạch?",},
        [2691] = {3503,"Quách Gia","Tào Tháo","Bẩm thừa tướng, có thể do quân đội ta đi ngang, nên dân chúng lo sợ.","Bẩm thừa tướng, có thể do quân đội ta đi ngang, nên dân chúng lo sợ.",},
        [2692] = {3503,"Tào Tháo","Quách Gia","Phụng Hiếu, hãy mau thông báo cho phụ lão trong thôn và các quan viên xung quanh.","Phụng Hiếu, hãy mau thông báo cho phụ lão trong thôn và các quan viên xung quanh.",},
        [2693] = {3503,"Tào Tháo","Quách Gia","Ta thừa lệnh thiên tử xuất chinh thảo phạt loạn tặc, vì dân trừ hại.","Ta thừa lệnh thiên tử xuất chinh thảo phạt loạn tặc, vì dân trừ hại.",},
        [2694] = {3503,"Tào Tháo","Quách Gia","Hiện lúa mạch đã chín, các binh sĩ khi đi ngang không được giẫm lên, nếu không sẽ bị xử lý theo quân pháp.","Hiện lúa mạch đã chín, các binh sĩ khi đi ngang không được giẫm lên, nếu không sẽ bị xử lý theo quân pháp.",},
        [2695] = {3503,"Tào Tháo","Quách Gia","Quân pháp rất nghiêm, bá tánh không cần phải lo sợ.","Quân pháp rất nghiêm, bá tánh không cần phải lo sợ.",},
        [2696] = {3503,"Quách Gia","Tào Tháo","Đúng là hành động nhân nghĩa.","Đúng là hành động nhân nghĩa.",},
        [2697] = {3504,"Tào Tháo","Nhân vật","Vừa nãy có một chim gáy bay ra trong ruộng, khiến ngựa hoảng sợ giẫm lên ruộng, ta phải bị xử lý theo quân pháp.","Vừa nãy có một chim gáy bay ra trong ruộng, khiến ngựa hoảng sợ giẫm lên ruộng, ta phải bị xử lý theo quân pháp.",},
        [2698] = {3504,"Nhân vật","Tào Tháo","Sao có thể trị tội thừa tướng?","Sao có thể trị tội thừa tướng?",},
        [2699] = {3504,"Tào Tháo","Nhân vật","Luật là do ta đặt ra, ta sai không phạt thì sao phục chúng?","Luật là do ta đặt ra, ta sai không phạt thì sao phục chúng?",},
        [2700] = {3504,"Quách Gia","Tào Tháo","Trong Xuân Thu có viết: Pháp bất gia vu tôn. Thừa tướng thống lĩnh đại quân, sao có thể trị tội?","Trong Xuân Thu có viết: Pháp bất gia vu tôn. Thừa tướng thống lĩnh đại quân, sao có thể trị tội?",},
        [2701] = {3504,"Tào Tháo","Quách Gia","Pháp bất gia vu tôn, ta chỉ được miễn tội tử.","Pháp bất gia vu tôn, ta chỉ được miễn tội tử.",},
        [2702] = {3504,"Tào Tháo","Quách Gia","Truyền cho ba quân, hôm nay thừa tướng giẫm vào ruộng, hiện sẽ dùng hình phạt cắt tóc thay thế.","Truyền cho ba quân, hôm nay thừa tướng giẫm vào ruộng, hiện sẽ dùng hình phạt cắt tóc thay thế.",},
        [2703] = {3601,"Tào Tháo","Tuân Du","Nếu ta tấn công Lữ Bố, không lo Viên Thiệu tấn công, chỉ sợ Lưu Biểu, Trương Tú liên kết với nhau.","Nếu ta tấn công Lữ Bố, không lo Viên Thiệu tấn công, chỉ sợ Lưu Biểu, Trương Tú liên kết với nhau.",},
        [2704] = {3601,"Tuân Du","Tào Tháo","Lữ Bố nếu liên kết với Viên Thuật, lúc đó sẽ rất nguy hiểm.","Lữ Bố nếu liên kết với Viên Thuật, lúc đó sẽ rất nguy hiểm.",},
        [2705] = {3601,"Quách Gia","Tào Tháo","Nhân lúc chưa xảy ra, chúng ta phải nhanh chóng tiêu diệt.","Nhân lúc chưa xảy ra, chúng ta phải nhanh chóng tiêu diệt.",},
