        [2709] = {3602,"Cao Thuận","Lữ Bố","Phụng Tiên, Tào Tháo muốn phái lính cứu Lưu Bị tấn công ngươi.","Phụng Tiên, Tào Tháo muốn phái lính cứu Lưu Bị tấn công ngươi.",},
        [2710] = {3602,"Cao Thuận","Lữ Bố","Đã lệnh Hạ Hầu Uyên, Lữ Kiền, Lý Điển thống lĩnh 5 vạn quân đi trước.","Đã lệnh Hạ Hầu Uyên, Lữ Kiền, Lý Điển thống lĩnh 5 vạn quân đi trước.",},
