        [2730] = {3702,"Điêu Thuyền","Lữ Bố","Phụng Tiên, uống từ từ thôi.","Phụng Tiên, uống từ từ thôi.",},
        [2731] = {3702,"Trần Cung","Lữ Bố","Hiện đang lúc dầu sôi lửa bỏng, không thể chìm đắm trong rượu được.","Hiện đang lúc dầu sôi lửa bỏng, không thể chìm đắm trong rượu được.",},
