        [2908] = {4504,"Trần Đăng","Quan Vũ","Vân Trường huynh, Tào Tháo lệnh Xa Trụ ám sát Lưu Bị.","Vân Trường huynh, Tào Tháo lệnh Xa Trụ ám sát Lưu Bị.",},
        [2909] = {4504,"Trần Đăng","Quan Vũ","Chúng định chờ Huyền Đức huynh trở về và sẽ ra tay ở Ung Thành.","Chúng định chờ Huyền Đức huynh trở về và sẽ ra tay ở Ung Thành.",},
        [2910] = {4504,"Quan Vũ","Trần Đăng","Vậy thì tương kế tựu kế, ta sẽ giả dạng quân Tào đến Từ Châu trước để dụ Xa Trụ ra nghênh chiến và diệt y.","Vậy thì tương kế tựu kế, ta sẽ giả dạng quân Tào đến Từ Châu trước để dụ Xa Trụ ra nghênh chiến và diệt y.",},
        [2911] = {4504,"Trần Đăng","Quan Vũ","Được, nếu Xa Trụ xuất thành, ta sẽ cho lính bắn tên tấn công.","Được, nếu Xa Trụ xuất thành, ta sẽ cho lính bắn tên tấn công.",},
        [2912] = {4505,"Quan Vũ","Lưu Bị","Trần Đăng vừa báo là Xa Trụ phụng lệnh Tào Tháo đến mưu hại đại ca.","Trần Đăng vừa báo là Xa Trụ phụng lệnh Tào Tháo đến mưu hại đại ca.",},
        [2913] = {4505,"Quan Vũ","Lưu Bị","Đệ và Trần Đăng đã tương kế tựu kế để ra tay trước với y.","Đệ và Trần Đăng đã tương kế tựu kế để ra tay trước với y.",},
        [2914] = {4505,"Lưu Bị","Quan Vũ","Nếu Tào Tháo đến đây thì phải làm sao?","Nếu Tào Tháo đến đây thì phải làm sao?",},
        [2915] = {4505,"Quan Vũ","Lưu Bị","Đệ và Trương Phi sẽ nghênh đón.","Đệ và Trương Phi sẽ nghênh đón.",},
        [2916] = {4505,"Lưu Bị","Quan Vũ","Nếu chúng ta diệt tâm phúc của Tào Tháo thì y sẽ bỏ qua dễ dàng vậy sao?","Nếu chúng ta diệt tâm phúc của Tào Tháo thì y sẽ bỏ qua dễ dàng vậy sao?",},
        [2917] = {4505,"Trần Đăng","Lưu Bị","Huyền Đức Công đừng lo, chỉ vào Từ Châu là được. Ta có kế này có thể giúp Huyền Đức Công đánh lui quân Tào Tháo.","Huyền Đức Công đừng lo, chỉ vào Từ Châu là được. Ta có kế này có thể giúp Huyền Đức Công đánh lui quân Tào Tháo.",},
        [2918] = {4601,"Trần Đăng","Lưu Bị","Viên Thiệu hiện đang chiếm đóng Ký Châu, Thanh Châu, U Châu và Tính Châu, quân binh hùng mạnh, Tào Tháo đang rất e dè y.","Viên Thiệu hiện đang chiếm đóng Ký Châu, Thanh Châu, U Châu và Tính Châu, quân binh hùng mạnh, Tào Tháo đang rất e dè y.",},
        [2919] = {4601,"Trần Đăng","Lưu Bị","Vậy tại sao không gửi thư cầu cứu Viên Thiệu?","Vậy tại sao không gửi thư cầu cứu Viên Thiệu?",},
        [2920] = {4601,"Lưu Bị","Trần Đăng","Trước giờ Viên Thiệu và ta không qua lại với nhau, ta lại diệt đệ đệ của y, y sao chịu giúp ta chứ?","Trước giờ Viên Thiệu và ta không qua lại với nhau, ta lại diệt đệ đệ của y, y sao chịu giúp ta chứ?",},
        [2921] = {4601,"Trần Đăng","Lưu Bị","Có một người có mối quan hệ rất tốt với Viên Thiệu, nếu nhờ người đó ra mặt thì chắc chắn Viên Thiệu sẽ đồng ý giúp.","Có một người có mối quan hệ rất tốt với Viên Thiệu, nếu nhờ người đó ra mặt thì chắc chắn Viên Thiệu sẽ đồng ý giúp.",},
        [2922] = {4601,"Lưu Bị","Trần Đăng","Người đó là ai?","Người đó là ai?",},
        [2923] = {4601,"Trần Đăng","Lưu Bị","Đó là người mà Huyền Đức Công luôn kính trọng, người quên rồi sao?","Đó là người mà Huyền Đức Công luôn kính trọng, người quên rồi sao?",},
        [2924] = {4601,"Lưu Bị","Trần Đăng","Là Trịnh Khang Thành tiên sinh sao?","Là Trịnh Khang Thành tiên sinh sao?",},
        [2925] = {4601,"Trần Đăng","Lưu Bị","Đúng vậy.","Đúng vậy.",},
        [2926] = {4602,"Viên Thiệu","","Trịnh Thượng Thư gửi thư đến lệnh cho ta xuất binh giúp Lưu Bị đánh lui quân Tào. Ta phải làm sao đây?","Trịnh Thượng Thư gửi thư đến lệnh cho ta xuất binh giúp Lưu Bị đánh lui quân Tào. Ta phải làm sao đây?",},
        [2927] = {4602,"Thư Thụ","Viên Thiệu","Minh công trước nay dùng mạnh khắc yếu, diệt Hán tặc để chấn hưng vương thất: Nên khởi binh.","Minh công trước nay dùng mạnh khắc yếu, diệt Hán tặc để chấn hưng vương thất: Nên khởi binh.",},
        [2928] = {4602,"Viên Thiệu","Thư Thụ","Lời của ngươi rất hợp ý ta.","Lời của ngươi rất hợp ý ta.",},
        [2929] = {4602,"Viên Thiệu","Thư Thụ","Lệnh cho Điền Phong, Tuân Thầm, Hứa Du là mưu sĩ, Nhan Lương, Văn Xú là tướng quân, dẫn theo 30 vạn binh tiến về Lê Dương.","Lệnh cho Điền Phong, Tuân Thầm, Hứa Du là mưu sĩ, Nhan Lương, Văn Xú là tướng quân, dẫn theo 30 vạn binh tiến về Lê Dương.",},
        [2930] = {4602,"Viên Thiệu","Thư Thụ","Lệnh cho Trần Lâm làm hịch thảo phạt Tào Tháo, công bố tội trạng của Tào Tháo cho mọi người biết.","Lệnh cho Trần Lâm làm hịch thảo phạt Tào Tháo, công bố tội trạng của Tào Tháo cho mọi người biết.",},
        [2931] = {4603,"Khổng Dung","Tào Tháo","Tào Tháo, tội trạng tố cáo người đang truyền khắp Hứa Đô.","Tào Tháo, tội trạng tố cáo người đang truyền khắp Hứa Đô.",},
        [2932] = {4603,"Tào Tháo","Khổng Dung","Ta đã biết chuyện này rồi. Ta vừa thấy hơi nhức đầu, thấy cáo trạng đó xong đột nhiên lại hết đau.","Ta đã biết chuyện này rồi. Ta vừa thấy hơi nhức đầu, thấy cáo trạng đó xong đột nhiên lại hết đau.",},
        [2933] = {4603,"Khổng Dung","Tào Tháo","Hết đau đầu thì tốt rồi nhưng Viên Thiệu binh hùng tướng mạnh, chúng ta chỉ nên hòa với y, không nên đánh.","Hết đau đầu thì tốt rồi nhưng Viên Thiệu binh hùng tướng mạnh, chúng ta chỉ nên hòa với y, không nên đánh.",},
        [2934] = {4603,"Tuân Úc","Khổng Dung","Viên Thiệu là kẻ vô dụng, tại sao phải nghị hòa chứ?","Viên Thiệu là kẻ vô dụng, tại sao phải nghị hòa chứ?",},
        [2935] = {4603,"Khổng Dung","Tuân Úc","Viên Thiệu thế mạnh, thuộc hạ dưới trướng y như Hứa Du, Quách Đồ, Thẩm Phối đều là những mưu sĩ tài giỏi.","Viên Thiệu thế mạnh, thuộc hạ dưới trướng y như Hứa Du, Quách Đồ, Thẩm Phối đều là những mưu sĩ tài giỏi.",},
        [2936] = {4603,"Khổng Dung","Tuân Úc","Điền Phong, Thư Thụ đều là trung thần. Nhan Lương, Văn Xú vô cùng dũng mãnh. Cao Lãm, Trương Hợp, Thuần Vu Quỳnh đều là danh tướng nổi tiếng.","Điền Phong, Thư Thụ đều là trung thần. Nhan Lương, Văn Xú vô cùng dũng mãnh. Cao Lãm, Trương Hợp, Thuần Vu Quỳnh đều là danh tướng nổi tiếng.",},
        [2937] = {4603,"Khổng Dung","Tuân Úc","Thế thì làm sao y là người vô dụng được.","Thế thì làm sao y là người vô dụng được.",},
        [2938] = {4603,"Tuân Úc","Khổng Dung","Viên Thiệu tuy quân binh đông nhưng lại không kỷ luật, Điền Phong là người không được Viên Thiệu tin tưởng, Hứa Du thì tham lam còn Thẩm Phối thì vô mưu, nên có cũng như không.","Viên Thiệu tuy quân binh đông nhưng lại không kỷ luật, Điền Phong là người không được Viên Thiệu tin tưởng, Hứa Du thì tham lam còn Thẩm Phối thì vô mưu, nên có cũng như không.",},
        [2939] = {4603,"Tuân Úc","Khổng Dung","Người đông nhưng không hòa hợp, sớm muộn cũng sẽ có chuyện. Nhan Lương, Văn Xú cũng chỉ là bọn thất phu, muốn bắt rất dễ.","Người đông nhưng không hòa hợp, sớm muộn cũng sẽ có chuyện. Nhan Lương, Văn Xú cũng chỉ là bọn thất phu, muốn bắt rất dễ.",},
        [2940] = {4603,"Tuân Úc","Khổng Dung","Còn lại đều là những kẻ không ra gì, nên không có gì đáng lo cả.","Còn lại đều là những kẻ không ra gì, nên không có gì đáng lo cả.",},
        [2941] = {4603,"Tào Tháo","Tuân Úc","Mong là mọi việc sẽ như lời ngươi nói.","Mong là mọi việc sẽ như lời ngươi nói.",},
        [2942] = {4603,"Tào Tháo","Tuân Úc","Lệnh cho Lưu Đại, Vương Trung dẫn theo năm vạn quân đến Từ Châu tiến đánh Lưu Bị.","Lệnh cho Lưu Đại, Vương Trung dẫn theo năm vạn quân đến Từ Châu tiến đánh Lưu Bị.",},
        [2943] = {4604,"Lưu Bị","Trần Đăng","Viên Bản Sơ tuy cho binh đồn trú ở Lê Dương nhưng vì các mưu thần bất hòa nên vẫn chưa có động tĩnh gì.","Viên Bản Sơ tuy cho binh đồn trú ở Lê Dương nhưng vì các mưu thần bất hòa nên vẫn chưa có động tĩnh gì.",},
        [2944] = {4604,"Lưu Bị","Trần Đăng","Tào Tháo không biết đang ở đâu. Nghe nói trong Lê Dương Thành không thấy cờ của quân Tào, nhưng sao ở đây lại có cờ hiệu của y?","Tào Tháo không biết đang ở đâu. Nghe nói trong Lê Dương Thành không thấy cờ của quân Tào, nhưng sao ở đây lại có cờ hiệu của y?",},
        [2945] = {4604,"Trần Đăng","Lưu Bị","Tào Tháo quỷ kế đa đoan, y rất xem trọng Hà Bắc nên nhất định sẽ không rời khỏi nơi đó. Cờ hiệu của Tào Tháo ở đây nhưng y chắc chắn không có mặt ở đây đâu.","Tào Tháo quỷ kế đa đoan, y rất xem trọng Hà Bắc nên nhất định sẽ không rời khỏi nơi đó. Cờ hiệu của Tào Tháo ở đây nhưng y chắc chắn không có mặt ở đây đâu.",},
        [2946] = {4604,"Lưu Bị","Trần Đăng","Có ai có thể đi do thám tin tức không?","Có ai có thể đi do thám tin tức không?",},
        [2947] = {4604,"Quan Vũ","Lưu Bị","Tiểu đệ nguyện nhận lệnh do thám.","Tiểu đệ nguyện nhận lệnh do thám.",},
        [2948] = {4604,"Nhân vật","Lưu Bị","Đệ cũng muốn đi theo giúp Vân Trường một tay.","Đệ cũng muốn đi theo giúp Vân Trường một tay.",},
        [2949] = {4605,"Vương Trung","Quan Vũ","Thừa tướng ở đây, sao còn không hàng?","Thừa tướng ở đây, sao còn không hàng?",},
        [2950] = {4605,"Quan Vũ","Vương Trung","Hãy mời Thừa Tướng xuất trận, ta có lời muốn nói.","Hãy mời Thừa Tướng xuất trận, ta có lời muốn nói.",},
        [2951] = {4605,"Vương Trung","Quan Vũ","Thừa Tướng tại sao phải gặp ngươi chứ!","Thừa Tướng tại sao phải gặp ngươi chứ!",},
        [2952] = {4605,"Quan Vũ","Vương Trung","Thừa Tướng không có ở đây, các ngươi đừng giả vờ nữa.","Thừa Tướng không có ở đây, các ngươi đừng giả vờ nữa.",},
        [2953] = {4605,"Nhân vật","Vương Trung","Vậy để bọn ta bắt ngươi trước rồi tính.","Vậy để bọn ta bắt ngươi trước rồi tính.",},
        [2954] = {4606,"Nhân vật","Lưu Bị","Vương Trung đã bị bắt!","Vương Trung đã bị bắt!",},
        [2955] = {4606,"Lưu Bị","Nhân vật","Tốt quá!","Tốt quá!",},
        [2956] = {4606,"Trương Phi","Lưu Bị","Nhị ca bắt Vương Trung, để đệ đi bắt tên Lưu Đại kia!","Nhị ca bắt Vương Trung, để đệ đi bắt tên Lưu Đại kia!",},
        [2957] = {4606,"Lưu Bị","Trương Phi","Lưu Đại từng là Duyện Châu Thích Sử, lại là chư hầu khi thảo phạt Đổng Trác ở Hổ Lao Quan, giờ làm tiền phong, đệ đừng khinh địch.","Lưu Đại từng là Duyện Châu Thích Sử, lại là chư hầu khi thảo phạt Đổng Trác ở Hổ Lao Quan, giờ làm tiền phong, đệ đừng khinh địch.",},
        [2958] = {4606,"Trương Phi","Lưu Bị","Chỉ vậy thôi thì có gì đáng sợ chứ! Đệ cũng sẽ như nhị ca vậy, nhất định sẽ bắt y về đây.","Chỉ vậy thôi thì có gì đáng sợ chứ! Đệ cũng sẽ như nhị ca vậy, nhất định sẽ bắt y về đây.",},
        [2959] = {4606,"Lưu Bị","Trương Phi","Chỉ sợ là y xảy ra chuyện gì thì đại sự của ta sẽ khó thành.","Chỉ sợ là y xảy ra chuyện gì thì đại sự của ta sẽ khó thành.",},
        [2960] = {4606,"Nhân vật","Lưu Bị","Đại ca đừng lo, đệ sẽ đi cùng Trương Phi.","Đại ca đừng lo, đệ sẽ đi cùng Trương Phi.",},
        [2961] = {4606,"Lưu Bị","Nhân vật","Nếu Dương Uy Tướng đi cùng vậy ta yên tâm rồi.","Nếu Dương Uy Tướng đi cùng vậy ta yên tâm rồi.",},
        [2962] = {4607,"Trương Phi","","Tên Lưu Đại kia cũng chỉ vậy thôi sao, ta sẽ bắt được hắn dễ dàng thôi!","Tên Lưu Đại kia cũng chỉ vậy thôi sao, ta sẽ bắt được hắn dễ dàng thôi!",},
        [2963] = {4607,"Trương Phi","","Ha ha ha.","Ha ha ha.",},
        [2964] = {4608,"Trương Phi","Lưu Bị","Đại ca, Vương Trung và Lưu Đại đều đã bị bắt về rồi đây!","Đại ca, Vương Trung và Lưu Đại đều đã bị bắt về rồi đây!",},
        [2965] = {4608,"Lưu Đại","Lưu Bị","Hừ! Muốn làm gì tùy ý các ngươi!","Hừ! Muốn làm gì tùy ý các ngươi!",},
        [2966] = {4608,"Lưu Bị","Lưu Đại","Đắc tội rồi, ta sẽ cởi trói cho hai người.","Đắc tội rồi, ta sẽ cởi trói cho hai người.",},
        [2967] = {4608,"Lưu Bị","Lưu Đại","Vì Xa Trụ muốn hãm hại ta nên ta đành phải diệt y. Thừa Tướng hiểu lầm là ta tạo phản nên mới lệnh hai vị đến đây hỏi tội ta.","Vì Xa Trụ muốn hãm hại ta nên ta đành phải diệt y. Thừa Tướng hiểu lầm là ta tạo phản nên mới lệnh hai vị đến đây hỏi tội ta.",},
        [2968] = {4608,"Lưu Bị","Lưu Đại","Lưu Bị tôi mang ơn Thừa Tướng thì sao lại làm phản chứ? Nhị vị tướng quân về Hứa Đô, nếu có thể nói giúp dùm ta một lời, vậy là ta đã vui lắm rồi.","Lưu Bị tôi mang ơn Thừa Tướng thì sao lại làm phản chứ? Nhị vị tướng quân về Hứa Đô, nếu có thể nói giúp dùm ta một lời, vậy là ta đã vui lắm rồi.",},
        [2969] = {4608,"Vương Trung","Lưu Bị","Đa tạ đã tha tội.","Đa tạ đã tha tội.",},
        [2970] = {4608,"Lưu Đại","Lưu Bị","Đa tạ đã tha tội. Bọn ta sẽ về bẩm lại với Thừa Tướng.","Đa tạ đã tha tội. Bọn ta sẽ về bẩm lại với Thừa Tướng.",},
        [2971] = {4608,"Lưu Bị","Lưu Đại","Ta sẽ chuẩn bị ngựa và trả lại binh mã cho nhị vị tướng quân.","Ta sẽ chuẩn bị ngựa và trả lại binh mã cho nhị vị tướng quân.",},
        [2972] = {4609,"Trương Phi","Lưu Đại","Đứng lại!","Đứng lại!",},
        [2973] = {4609,"Trương Phi","Lưu Đại","Đại ca ta sao lại thả bọn ngươi kia chứ?","Đại ca ta sao lại thả bọn ngươi kia chứ?",},
        [2974] = {4609,"Lưu Đại","Trương Phi","Ngươi muốn gì?","Ngươi muốn gì?",},
        [2975] = {4609,"Quan Vũ","Trương Phi","Không được vô lễ!","Không được vô lễ!",},
        [2976] = {4609,"Quan Vũ","Trương Phi","Huynh trưởng đã thả họ đi, sao đệ không nghe theo?","Huynh trưởng đã thả họ đi, sao đệ không nghe theo?",},
        [2977] = {4609,"Trương Phi","Quan Vũ","Hôm nay thả chúng về lần sau chúng lại đến.","Hôm nay thả chúng về lần sau chúng lại đến.",},
        [2978] = {4609,"Quan Vũ","Trương Phi","Chờ chúng đến thì trảm luôn cũng không muộn.","Chờ chúng đến thì trảm luôn cũng không muộn.",},
        [2979] = {4609,"Vương Trung","Trương Phi","Nếu Thừa Tướng tru di tam tộc ta, lúc đó ta sẽ không đến nữa. Mong tướng quân khoan hồng.","Nếu Thừa Tướng tru di tam tộc ta, lúc đó ta sẽ không đến nữa. Mong tướng quân khoan hồng.",},
        [2980] = {4609,"Trương Phi","Vương Trung","Nếu Tào Tháo đến đây, ta sẽ đánh cho y không còn manh giáp nào luôn! Hôm nay xem như ta độ lượng để lại hai thủ cấp của bọn ngươi đấy.","Nếu Tào Tháo đến đây, ta sẽ đánh cho y không còn manh giáp nào luôn! Hôm nay xem như ta độ lượng để lại hai thủ cấp của bọn ngươi đấy.",},
        [2981] = {4609,"Lưu Đại","Vương Trung","Vương Trung, chúng ta mau đi thôi. Nếu không y lại đổi ý.","Vương Trung, chúng ta mau đi thôi. Nếu không y lại đổi ý.",},
        [2982] = {4610,"Tào Tháo","","Vương Trung, Lưu Đại, các ngươi còn mặt mũi quay về sao! Trảm chúng cho ta.","Vương Trung, Lưu Đại, các ngươi còn mặt mũi quay về sao! Trảm chúng cho ta.",},
        [2983] = {4610,"Lưu Đại","Tào Tháo","Thừa Tướng khai ân.","Thừa Tướng khai ân.",},
        [2984] = {4610,"Vương Trung","Tào Tháo","Thừa Tướng khai ân.","Thừa Tướng khai ân.",},
        [2985] = {4610,"Khổng Dung","Tào Tháo","Hai người họ vốn không phải là đối thủ của Lưu Bị, nếu trảm họ e là lòng quân sẽ hoang mang.","Hai người họ vốn không phải là đối thủ của Lưu Bị, nếu trảm họ e là lòng quân sẽ hoang mang.",},
        [2986] = {4610,"Tào Tháo","Khổng Dung","Tội tử có thể tha nhưng vẫn phải bị phạt.","Tội tử có thể tha nhưng vẫn phải bị phạt.",},
        [2987] = {4610,"Tào Tháo","Khổng Dung","Tước hết bổng lộc của bọn chúng và lôi ra ngoài cho ta!","Tước hết bổng lộc của bọn chúng và lôi ra ngoài cho ta!",},
        [2988] = {4610,"Lưu Đại","Tào Tháo","Đa tạ Khổng Dung, đa tạ Thừa Tướng đã khai ân!","Đa tạ Khổng Dung, đa tạ Thừa Tướng đã khai ân!",},
        [2989] = {4610,"Vương Trung","Tào Tháo","Đa tạ Thừa Tướng khai ân!","Đa tạ Thừa Tướng khai ân!",},
        [2990] = {4701,"Lưu Bị","Nhân vật","Lúc trước, Tào Tháo vì Y Đai Chiếu nên trảm Đổng Thừa, Vương Tử Phục, Chủng Tập, Ngô Thạc...","Lúc trước, Tào Tháo vì Y Đai Chiếu nên trảm Đổng Thừa, Vương Tử Phục, Chủng Tập, Ngô Thạc...",},
        [2991] = {4701,"Lưu Bị","Nhân vật","Ngay cả Đổng Phi đang mang thai năm tháng cũng không tha.","Ngay cả Đổng Phi đang mang thai năm tháng cũng không tha.",},
        [2992] = {4701,"Lưu Bị","Nhân vật","Ta có liên quan đến Y Đai Chiếu, chỉ sợ là Tào Tháo sẽ lại khởi binh tiến đánh.","Ta có liên quan đến Y Đai Chiếu, chỉ sợ là Tào Tháo sẽ lại khởi binh tiến đánh.",},
        [2993] = {4701,"Nhân vật","Lưu Bị","Đừng lo, ta sẽ lập tức đến Ký Châu và mời Viên Thiệu trợ giúp.","Đừng lo, ta sẽ lập tức đến Ký Châu và mời Viên Thiệu trợ giúp.",},
        [2994] = {4702,"Nhân vật","Viên Thiệu","Bản Sơ sao lại tiều tụy thế kia?","Bản Sơ sao lại tiều tụy thế kia?",},
        [2995] = {4702,"Viên Thiệu","Nhân vật","Ta không muốn sống nữa!","Ta không muốn sống nữa!",},
        [2996] = {4702,"Nhân vật","Viên Thiệu","Chúa công sao lại nói vậy?","Chúa công sao lại nói vậy?",},
        [2997] = {4702,"Viên Thiệu","Nhân vật","Ta có năm đứa con nhưng chỉ có con út là hợp ý ta. Giờ nó bệnh nặng, ta còn tâm trạng nào để lo chuyện khác nữa chứ?","Ta có năm đứa con nhưng chỉ có con út là hợp ý ta. Giờ nó bệnh nặng, ta còn tâm trạng nào để lo chuyện khác nữa chứ?",},
        [2998] = {4702,"Nhân vật","Viên Thiệu","Giờ Tào Tháo đang đông chinh thảo phạt Lưu Huyền Đức, Hứa Xương không ai cai quản, nếu chúng ta nhân dịp này dấy binh tiến công thì sẽ toàn thắng.","Giờ Tào Tháo đang đông chinh thảo phạt Lưu Huyền Đức, Hứa Xương không ai cai quản, nếu chúng ta nhân dịp này dấy binh tiến công thì sẽ toàn thắng.",},
        [2999] = {4702,"Nhân vật","Viên Thiệu","Đây quả nhiên là cơ hội hiếm có.","Đây quả nhiên là cơ hội hiếm có.",},
        [3000] = {4702,"Viên Thiệu","Nhân vật","Nhưng ta vẫn sợ là sẽ không thuận lợi.","Nhưng ta vẫn sợ là sẽ không thuận lợi.",},
        [3001] = {4702,"Nhân vật","Viên Thiệu","Hãy lấy chuyện thiên hạ làm trọng.","Hãy lấy chuyện thiên hạ làm trọng.",},
        [3002] = {4703,"Nhân vật","Lưu Bị","Bản Sơ ta tuy không yên tâm lắm nhưng sẽ nhận lời giúp.","Bản Sơ ta tuy không yên tâm lắm nhưng sẽ nhận lời giúp.",},
        [3003] = {4703,"Nhân vật","Lưu Bị","Tào Tháo đông chinh thảo phạt chúng ta, Viên Thiệu có thể nhân cơ hội tập kích Hứa Xương, quả là kế vây Ngụy cứu Triệu.","Tào Tháo đông chinh thảo phạt chúng ta, Viên Thiệu có thể nhân cơ hội tập kích Hứa Xương, quả là kế vây Ngụy cứu Triệu.",},
        [3004] = {4703,"Nhân vật","Lưu Bị","Viên Thiệu còn nói sau này có chuyện gì cần đều có thể đến nhờ y.","Viên Thiệu còn nói sau này có chuyện gì cần đều có thể đến nhờ y.",},
        [3005] = {4703,"Lưu Bị","Nhân vật","Được.","Được.",},
        [3006] = {4801,"Trương Tú","Giả Hủ","Hiện tại Viên Thiệu mạnh, Tào Tháo yếu. Nếu hủy thư chiêu hàng của Viên Thiệu sau này y đến ta phải làm sao?","Hiện tại Viên Thiệu mạnh, Tào Tháo yếu. Nếu hủy thư chiêu hàng của Viên Thiệu sau này y đến ta phải làm sao?",},
        [3007] = {4801,"Giả Hủ","Trương Tú","Hay là theo Tào Tháo.","Hay là theo Tào Tháo.",},
        [3008] = {4801,"Trương Tú","Giả Hủ","Ta có thù với Tào Tháo, y làm sao chấp nhận ta?","Ta có thù với Tào Tháo, y làm sao chấp nhận ta?",},
        [3009] = {4801,"Giả Hủ","Trương Tú","Có 3 lý do để Tào Công chấp nhận người: Một là người có thể phò tá Tào Công thành thiên tử, chinh phạt thiên hạ.","Có 3 lý do để Tào Công chấp nhận người: Một là người có thể phò tá Tào Công thành thiên tử, chinh phạt thiên hạ.",},
        [3010] = {4801,"Giả Hủ","Trương Tú","Hai là, Tào Công hiện yếu thế hơn so với Viên Thiệu nên chắc chắn sẽ trọng dụng người.","Hai là, Tào Công hiện yếu thế hơn so với Viên Thiệu nên chắc chắn sẽ trọng dụng người.",},
        [3011] = {4801,"Giả Hủ","Trương Tú","Ba là Tào Công đang muốn dùng đức để thu phục lòng người, nên chắc chắn Tào Công sẽ không làm gì người đâu.","Ba là Tào Công đang muốn dùng đức để thu phục lòng người, nên chắc chắn Tào Công sẽ không làm gì người đâu.",},
        [3012] = {4802,"Trương Tú","Tào Tháo","Ta và Giả Hủ lần này đến Hứa Đô là muốn đầu hàng.","Ta và Giả Hủ lần này đến Hứa Đô là muốn đầu hàng.",},
        [3013] = {4802,"Giả Hủ","Tào Tháo","Mong Thừa Tướng không câu nệ chuyện quá khứ.","Mong Thừa Tướng không câu nệ chuyện quá khứ.",},
        [3014] = {4802,"Tào Tháo","Giả Hủ","Chuyện có đáng gì đâu, đừng để trong lòng.","Chuyện có đáng gì đâu, đừng để trong lòng.",},
        [3015] = {4802,"Tào Tháo","Giả Hủ","Ta phong cho Trương Tú là Dương Võ Tướng, Giả Hủ làm Chấp Kim Ngô Sứ.","Ta phong cho Trương Tú là Dương Võ Tướng, Giả Hủ làm Chấp Kim Ngô Sứ.",},
        [3016] = {4901,"Tôn Càn","Lưu Bị","Viên Thiệu đang nghênh chiến với Tào Tháo nên chưa thể xuất binh cứu Tiểu Bái.","Viên Thiệu đang nghênh chiến với Tào Tháo nên chưa thể xuất binh cứu Tiểu Bái.",},
        [3017] = {4901,"Lưu Bị","Tôn Càn","Phải làm sao đây?","Phải làm sao đây?",},
        [3018] = {4901,"Trương Phi","Lưu Bị","Huynh trưởng đừng lo. Quân Tào từ xa đến chắc chắn sẽ gặp nhiều khó khăn. Nhân lúc chúng vừa đến, chúng ta hãy tập kích chúng trước.","Huynh trưởng đừng lo. Quân Tào từ xa đến chắc chắn sẽ gặp nhiều khó khăn. Nhân lúc chúng vừa đến, chúng ta hãy tập kích chúng trước.",},
        [3019] = {4901,"Lưu Bị","Trương Phi","Đệ quả nhiên đa mưu túc trí, kế này rất hay.","Đệ quả nhiên đa mưu túc trí, kế này rất hay.",},
        [3020] = {4902,"Tuân Úc","Tào Tháo","Gió từ đâu đến mà lớn vậy? Cờ hiệu bị gãy là cờ màu gì? (Gió lớn thổi đến làm gãy cờ hiệu.)","Gió từ đâu đến mà lớn vậy? Cờ hiệu bị gãy là cờ màu gì? (Gió lớn thổi đến làm gãy cờ hiệu.)",},
        [3021] = {4902,"Tào Tháo","Tuân Úc","Là gió đông nam, cờ gãy là cờ màu xanh và đỏ.","Là gió đông nam, cờ gãy là cờ màu xanh và đỏ.",},
        [3022] = {4902,"Tuân Úc","Tào Tháo","Nguy rồi, tối nay Lưu Bị sẽ đến tập kích doanh trại.","Nguy rồi, tối nay Lưu Bị sẽ đến tập kích doanh trại.",},
        [3023] = {4903,"Trương Phi","","Tại sao tập kích Tào doanh lại dễ dàng như vậy, trong doanh trại binh mã lại không có mấy người.","Tại sao tập kích Tào doanh lại dễ dàng như vậy, trong doanh trại binh mã lại không có mấy người.",},
        [3024] = {4903,"Trương Phi","","Nguy rồi, trúng kế rồi.","Nguy rồi, trúng kế rồi.",},
        [3025] = {4903,"Từ Hoảng","Trương Phi","Chạy đâu cho thoát, ngươi bị bao vây rồi!","Chạy đâu cho thoát, ngươi bị bao vây rồi!",},
        [3026] = {4903,"Nhân vật","Trương Phi","Dực Đức, hãy theo ta phá vòng vây!","Dực Đức, hãy theo ta phá vòng vây!",},
        [3027] = {4903,"Trương Phi","Nhân vật","Dương Uy Tướng, đa tạ ơn cứu mạng!","Dương Uy Tướng, đa tạ ơn cứu mạng!",},
        [3028] = {4904,"Trương Phi","Nhân vật","Khó khăn lắm mới phá được vòng vây, mau về Tiểu Bái thôi!","Khó khăn lắm mới phá được vòng vây, mau về Tiểu Bái thôi!",},
        [3029] = {4904,"Nhân vật","Trương Phi","Không được, giờ Tiểu Bái cũng rất nguy hiểm, Dực Đức hãy đến Mang Đãng Sơn lánh mặt trước.","Không được, giờ Tiểu Bái cũng rất nguy hiểm, Dực Đức hãy đến Mang Đãng Sơn lánh mặt trước.",},
        [3030] = {4904,"Trương Phi","Nhân vật","Vậy Huyền Đức thì sao?","Vậy Huyền Đức thì sao?",},
        [3031] = {4904,"Nhân vật","Trương Phi","Đừng lo, ta sẽ đến cứu, yên tâm đi.","Đừng lo, ta sẽ đến cứu, yên tâm đi.",},
        [3032] = {4905,"Lưu Bị","","Nguy rồi, Tiểu Bái Thành lửa bốc lên, đành phải bỏ lại Tiểu Bái thôi.","Nguy rồi, Tiểu Bái Thành lửa bốc lên, đành phải bỏ lại Tiểu Bái thôi.",},
        [3033] = {4905,"Lưu Bị","","Đến Từ Châu, Hạ Phì thôi.","Đến Từ Châu, Hạ Phì thôi.",},
        [3034] = {4905,"Hạ Hầu Uyên","Lưu Bị","Muốn đến Từ Châu, Hạ Phì sao? Hãy hỏi cung tên của ta trước!","Muốn đến Từ Châu, Hạ Phì sao? Hãy hỏi cung tên của ta trước!",},
        [3035] = {4905,"Nhân vật","Hạ Hầu Uyên","Huyền Đức mau chạy đi, đệ sẽ chặn Hạ Hầu Uyên.","Huyền Đức mau chạy đi, đệ sẽ chặn Hạ Hầu Uyên.",},
        [3036] = {4906,"Lưu Bị","Nhân vật","Dực Đức giờ vẫn chưa về, phải làm sao đây?","Dực Đức giờ vẫn chưa về, phải làm sao đây?",},
        [3037] = {4906,"Nhân vật","Lưu Bị","Huyền Đức yên tâm, đệ đã đến cứu Dực Đức và bảo nó đến Mang Đãng Sơn lánh mặt rồi.","Huyền Đức yên tâm, đệ đã đến cứu Dực Đức và bảo nó đến Mang Đãng Sơn lánh mặt rồi.",},
        [3038] = {4906,"Nhân vật","Lưu Bị","Ngươi có dự tính gì.","Ngươi có dự tính gì.",},
        [3039] = {4906,"Lưu Bị","Nhân vật","Viên Thiệu có nói, nếu không như ý có thể đến nương nhờ.","Viên Thiệu có nói, nếu không như ý có thể đến nương nhờ.",},
        [3040] = {4906,"Lưu Bị","Nhân vật","Nay nên tìm nơi nương tựa rồi tính kế khác.","Nay nên tìm nơi nương tựa rồi tính kế khác.",},
        [3041] = {4906,"Nhân vật","Lưu Bị","Được, ngươi đi đi, ta đi tìm Vân Trường huynh đã thất lạc.","Được, ngươi đi đi, ta đi tìm Vân Trường huynh đã thất lạc.",},
        [3042] = {5001,"Tào Tháo","","Nhan Lương quả thật là dũng tướng!","Nhan Lương quả thật là dũng tướng!",},
        [3043] = {5001,"Tào Tháo","","Chỉ vài hiệp đã trảm hai đại tướng phe ta.","Chỉ vài hiệp đã trảm hai đại tướng phe ta.",},
        [3044] = {5001,"Tào Tháo","","Giờ phải làm sao đây?","Giờ phải làm sao đây?",},
        [3045] = {5001,"Trình Dục","Tào Tháo","Ta đề cử một người có thể địch lại Nhan Lương.","Ta đề cử một người có thể địch lại Nhan Lương.",},
        [3046] = {5001,"Tào Tháo","Trình Dục","Ai?","Ai?",},
        [3047] = {5001,"Trình Dục","Tào Tháo","Vân Trường.","Vân Trường.",},
        [3048] = {5001,"Tào Tháo","Trình Dục","Ta sợ hắn lập công rồi sẽ rời đi.","Ta sợ hắn lập công rồi sẽ rời đi.",},
        [3049] = {5001,"Trình Dục","Tào Tháo","Lưu Bị nếu còn sống hẳn sẽ nương nhờ Viên Thiệu. Nếu Vân Trường diệt lính của Viên Thiệu, Viên Thiệu chắc chắn sẽ diệt trừ Lưu Bị. Mất đi Lưu Bị, Vân Trường có thể đi đâu?","Lưu Bị nếu còn sống hẳn sẽ nương nhờ Viên Thiệu. Nếu Vân Trường diệt lính của Viên Thiệu, Viên Thiệu chắc chắn sẽ diệt trừ Lưu Bị. Mất đi Lưu Bị, Vân Trường có thể đi đâu?",},
        [3050] = {5001,"Tào Tháo","Trình Dục","Ta lập tức phái người đi mời.","Ta lập tức phái người đi mời.",},
        [3051] = {5002,"Tào Tháo","Quan Vũ","Nhan Lương trảm liên tiếp hai tướng, cực kỳ dũng mãnh, nay đến mời Vân Trường giúp đỡ.","Nhan Lương trảm liên tiếp hai tướng, cực kỳ dũng mãnh, nay đến mời Vân Trường giúp đỡ.",},
        [3052] = {5002,"Quan Vũ","Tào Tháo","Để ta xem thử.","Để ta xem thử.",},
        [3053] = {5002,"Tào Tháo","Quan Vũ","Quân mã Hàn Bắc nghiêm chỉnh uy vũ, thật là hùng tráng nhỉ!","Quân mã Hàn Bắc nghiêm chỉnh uy vũ, thật là hùng tráng nhỉ!",},
        [3054] = {5002,"Quan Vũ","Tào Tháo","Dương Uy tướng quân thấy thế nào?","Dương Uy tướng quân thấy thế nào?",},
        [3055] = {5002,"Nhân vật","Quan Vũ","Theo ta thấy chỉ là hữu danh vô thực!","Theo ta thấy chỉ là hữu danh vô thực!",},
        [3056] = {5002,"Tào Tháo","Quan Vũ","Người mặc áo gấm giáp vàng, cầm đao cưỡi ngựa chính là Nhan Lương đấy.","Người mặc áo gấm giáp vàng, cầm đao cưỡi ngựa chính là Nhan Lương đấy.",},
        [3057] = {5002,"Quan Vũ","Tào Tháo","Ta thấy Nhan Lương không đáng là gì cả.","Ta thấy Nhan Lương không đáng là gì cả.",},
        [3058] = {5002,"Tào Tháo","Quan Vũ","Không thể xem thường.","Không thể xem thường.",},
        [3059] = {5002,"Quan Vũ","Tào Tháo","Ta tuy bất tài, nhưng nguyện đi lấy thủ cấp của hắn ta hiến cho thừa tướng.","Ta tuy bất tài, nhưng nguyện đi lấy thủ cấp của hắn ta hiến cho thừa tướng.",},
        [3060] = {5002,"Trương Liêu","Quan Vũ","Trong quân không nói đùa, các ngươi đừng xem thường.","Trong quân không nói đùa, các ngươi đừng xem thường.",},
        [3061] = {5002,"Nhân vật","Trương Liêu","Ta với Vân Trường huynh đi chút rồi về!","Ta với Vân Trường huynh đi chút rồi về!",},
        [3062] = {5003,"Quan Vũ","Tào Tháo","Đây là thủ cấp Nhan Lương, hãy tiếp lấy! (Lập tức hiến thủ cấp của Nhan Lương cho Tào Tháo.)","Đây là thủ cấp Nhan Lương, hãy tiếp lấy! (Lập tức hiến thủ cấp của Nhan Lương cho Tào Tháo.)",},
        [3063] = {5003,"Tào Tháo","Quan Vũ","Tướng quân quả thật dũng mãnh!","Tướng quân quả thật dũng mãnh!",},
        [3064] = {5003,"Quan Vũ","Tào Tháo","Lấy thủ cấp tướng giữa trăm vạn quân như lấy đồ trong túi.","Lấy thủ cấp tướng giữa trăm vạn quân như lấy đồ trong túi.",},
        [3065] = {5004,"Viên Thiệu","","Ai trảm đại tướng Nhan Lương của ta?","Ai trảm đại tướng Nhan Lương của ta?",},
        [3066] = {5004,"Thư Thụ","Viên Thiệu","Mặt đỏ râu dài dùng đại đao, hẳn là Quan Vân Trường, đệ đệ của Lưu Huyền Đức.","Mặt đỏ râu dài dùng đại đao, hẳn là Quan Vân Trường, đệ đệ của Lưu Huyền Đức.",},
        [3067] = {5004,"Viên Thiệu","Thư Thụ","Nghĩa đệ ngươi trảm tướng ta, còn lưu ngươi lại làm gì!","Nghĩa đệ ngươi trảm tướng ta, còn lưu ngươi lại làm gì!",},
        [3068] = {5004,"Lưu Bị","Viên Thiệu","Minh công chỉ nghe lời từ một phía sao?","Minh công chỉ nghe lời từ một phía sao?",},
        [3069] = {5004,"Lưu Bị","Viên Thiệu","Từ lúc thất tán ở Từ Châu, nhị đệ Vân Trường còn chưa biết sinh tử.","Từ lúc thất tán ở Từ Châu, nhị đệ Vân Trường còn chưa biết sinh tử.",},
        [3070] = {5004,"Lưu Bị","Viên Thiệu","Thiên hạ không ít người có dung mạo giống nhau, sao minh công không điều tra xem?","Thiên hạ không ít người có dung mạo giống nhau, sao minh công không điều tra xem?",},
        [3071] = {5004,"Viên Thiệu","Lưu Bị","Ta nghe lời ngươi, xem chút đã diệt nhầm người tốt.","Ta nghe lời ngươi, xem chút đã diệt nhầm người tốt.",},
        [3072] = {5005,"Văn Xú","","Nhan Lương và ta như huynh đệ, nay bị giặc Tào sát hại, ta phải rửa hận.","Nhan Lương và ta như huynh đệ, nay bị giặc Tào sát hại, ta phải rửa hận.",},
        [3073] = {5005,"Viên Thiệu","Văn Xú","Được, ta cho ngươi đem mười vạn quân vượt qua Hoàng Hà truy sát quân Tào!","Được, ta cho ngươi đem mười vạn quân vượt qua Hoàng Hà truy sát quân Tào!",},
        [3074] = {5005,"Thư Thụ","Viên Thiệu","Không được. Nay nên đóng quân ở Diên Tân rồi chia binh sang Quan Độ mới là thượng sách. Nếu đem tất cả binh sang sông, nếu có điều gì thì sao rút về kịp?","Không được. Nay nên đóng quân ở Diên Tân rồi chia binh sang Quan Độ mới là thượng sách. Nếu đem tất cả binh sang sông, nếu có điều gì thì sao rút về kịp?",},
        [3075] = {5005,"Viên Thiệu","Thư Thụ","Đều do các ngươi làm trì hoãn lòng quân để lỡ việc lớn. Chưa nghe qua câu binh quý thần tốc sao?","Đều do các ngươi làm trì hoãn lòng quân để lỡ việc lớn. Chưa nghe qua câu binh quý thần tốc sao?",},
        [3076] = {5005,"Thư Thụ","Viên Thiệu","Người trên kiêu căng, kẻ dưới tham công, nước sông chảy cuồn cuộn, ta cũng đành liều chăng! (Nói xong đẩy cửa đi ra.)","Người trên kiêu căng, kẻ dưới tham công, nước sông chảy cuồn cuộn, ta cũng đành liều chăng! (Nói xong đẩy cửa đi ra.)",},
        [3077] = {5005,"Lưu Bị","Viên Thiệu","Lưu Bị đội ơn lớn, chưa lấy gì báo đáp, nay muốn đi cùng với Văn tướng quân","Lưu Bị đội ơn lớn, chưa lấy gì báo đáp, nay muốn đi cùng với Văn tướng quân",},
        [3078] = {5005,"Lưu Bị","Viên Thiệu","Một là để báo đáp minh công, hai là thăm dò tin tức Vân Trường.","Một là để báo đáp minh công, hai là thăm dò tin tức Vân Trường.",},
        [3079] = {5005,"Viên Thiệu","Lưu Bị","Được, Văn Xú dẫn bảy vạn quân đi trước, Huyền Đức dẫn ba vạn quân theo sau.","Được, Văn Xú dẫn bảy vạn quân đi trước, Huyền Đức dẫn ba vạn quân theo sau.",},
        [3080] = {5006,"Tào Tháo","","Văn Xú là một danh tướng, ai có thể bắt được?","Văn Xú là một danh tướng, ai có thể bắt được?",},
        [3081] = {5006,"Nhân vật","Tào Tháo","Đợi ta với Vân Trường đi xong rồi về ngay!","Đợi ta với Vân Trường đi xong rồi về ngay!",},
        [3082] = {5006,"Quan Vũ","Nhân vật","Dương Uy tướng quân, chúng ta đi!","Dương Uy tướng quân, chúng ta đi!",},
        [3083] = {5007,"Quan Vũ","","Đây là thủ cấp Văn Xú, hãy tiếp lấy! (Lập tức hiến thủ cấp của Văn Xú cho Tào Tháo.)","Đây là thủ cấp Văn Xú, hãy tiếp lấy! (Lập tức hiến thủ cấp của Văn Xú cho Tào Tháo.)",},
        [3084] = {5007,"Tào Tháo","Quan Vũ","Trảm liên tiếp hai đại tướng của Viên Thiệu, Vân Trường với Dương Uy Tướng quả thật là anh hùng đương thời.","Trảm liên tiếp hai đại tướng của Viên Thiệu, Vân Trường với Dương Uy Tướng quả thật là anh hùng đương thời.",},
        [3085] = {5008,"Viên Thiệu","Lưu Bị","Lần này lại là Quan Vũ tiêu diệt Văn Xú, sao ngươi dám làm như vậy!","Lần này lại là Quan Vũ tiêu diệt Văn Xú, sao ngươi dám làm như vậy!",},
        [3086] = {5008,"Lưu Bị","Viên Thiệu","Ta có tội gì?","Ta có tội gì?",},
        [3087] = {5008,"Viên Thiệu","Lưu Bị","Ngươi sai nghĩa đệ trảm tiếp một đại tướng của ta, sao không có tội?","Ngươi sai nghĩa đệ trảm tiếp một đại tướng của ta, sao không có tội?",},
        [3088] = {5008,"Lưu Bị","Viên Thiệu","Hãy nghe ta giải thích: Tào Tháo biết Bị ở chỗ minh công, sợ Bị trợ giúp minh công nên sai Vân Trường tru sát hai tướng.","Hãy nghe ta giải thích: Tào Tháo biết Bị ở chỗ minh công, sợ Bị trợ giúp minh công nên sai Vân Trường tru sát hai tướng.",},
        [3089] = {5008,"Lưu Bị","Viên Thiệu","Minh công biết chắc chắn sẽ giận dữ, Tháo muốn mượn tay minh công diệt trừ ta. Mong minh công nghĩ lại.","Minh công biết chắc chắn sẽ giận dữ, Tháo muốn mượn tay minh công diệt trừ ta. Mong minh công nghĩ lại.",},
        [3090] = {5008,"Viên Thiệu","Lưu Bị","Huyền Đức nói đúng lắm! Thiếu chút là các ngươi làm ta hại người hiền rồi.","Huyền Đức nói đúng lắm! Thiếu chút là các ngươi làm ta hại người hiền rồi.",},
        [3091] = {5008,"Lưu Bị","Viên Thiệu","Minh công khoan hồng, Bị không biết lấy gì báo đáp. Nay muốn sai một người tâm phúc đem mật thư đưa cho Vân Trường","Minh công khoan hồng, Bị không biết lấy gì báo đáp. Nay muốn sai một người tâm phúc đem mật thư đưa cho Vân Trường",},
        [3092] = {5008,"Lưu Bị","Viên Thiệu","Vân Trường biết tin ta ở đây sẽ lập tức đến giúp minh công cùng đánh Tào Tháo, báo thù cho Nhan Lương, Văn Sú, minh công nghĩ thế nào?","Vân Trường biết tin ta ở đây sẽ lập tức đến giúp minh công cùng đánh Tào Tháo, báo thù cho Nhan Lương, Văn Sú, minh công nghĩ thế nào?",},
        [3093] = {5008,"Viên Thiệu","Lưu Bị","Ta được Vân Trường tức là được một viên tướng giỏi gấp mười lần Nhan Lương, Văn Sú.","Ta được Vân Trường tức là được một viên tướng giỏi gấp mười lần Nhan Lương, Văn Sú.",},
        [3094] = {5008,"Lưu Bị","Viên Thiệu","Ta lập tức đi viết thư và sai ngươi đưa đi.","Ta lập tức đi viết thư và sai ngươi đưa đi.",},
        [3095] = {5101,"Trương Liêu","Quan Vũ","Nghe nói huynh nghe được tin Huyền Đức nên đến chúc mừng.","Nghe nói huynh nghe được tin Huyền Đức nên đến chúc mừng.",},
        [3096] = {5101,"Quan Vũ","Trương Liêu","Chúa công tuy ở đó nhưng chưa được gặp, có gì đáng mừng!","Chúa công tuy ở đó nhưng chưa được gặp, có gì đáng mừng!",},
        [3097] = {5101,"Trương Liêu","Quan Vũ","Huynh với Huyền Đức, so giữa đệ với huynh, bên nào thân hơn?","Huynh với Huyền Đức, so giữa đệ với huynh, bên nào thân hơn?",},
        [3098] = {5101,"Quan Vũ","Trương Liêu","Ta với huynh chỉ là bằng hữu. Ta với Huyền Đức là bằng hữu và huynh đệ, cũng là chủ thần, sao so sánh được?","Ta với huynh chỉ là bằng hữu. Ta với Huyền Đức là bằng hữu và huynh đệ, cũng là chủ thần, sao so sánh được?",},
        [3099] = {5101,"Trương Liêu","Quan Vũ","Nay Huyền Đức ở Hàn Bắc, huynh có đi đến đó không?","Nay Huyền Đức ở Hàn Bắc, huynh có đi đến đó không?",},
        [3100] = {5101,"Quan Vũ","Trương Liêu","Lời nói ngày trước, sao làm trái được! Nhờ Văn Viễn nói hộ cho ta với thừa tướng.","Lời nói ngày trước, sao làm trái được! Nhờ Văn Viễn nói hộ cho ta với thừa tướng.",},
        [3101] = {5102,"Tào Tháo","Quan Vũ","Vân Trường đi có vội quá không?","Vân Trường đi có vội quá không?",},
        [3102] = {5102,"Quan Vũ","Tào Tháo","Quan mỗ từng thưa với thừa tướng trước, nay chủ cũ ở Hà Bắc, tất nhiên phải vội đi.","Quan mỗ từng thưa với thừa tướng trước, nay chủ cũ ở Hà Bắc, tất nhiên phải vội đi.",},
        [3103] = {5102,"Quan Vũ","Tào Tháo","Đã mấy lần đến phủ nhưng không được gặp, cho nên đã viết bức thư cáo từ, gói vàng bạc, ấn nộp lại thừa tướng. Xin thừa tướng đừng quên lời hứa ngày trước.","Đã mấy lần đến phủ nhưng không được gặp, cho nên đã viết bức thư cáo từ, gói vàng bạc, ấn nộp lại thừa tướng. Xin thừa tướng đừng quên lời hứa ngày trước.",},
        [3104] = {5102,"Tào Tháo","Quan Vũ","Ta sao có thể làm trái lời hứa, ta chỉ sợ tướng quân đi đường thiếu thốn, nên đem ít đồ hành trang đến đưa tiễn. (Nói xong bưng mâm vàng đến.)","Ta sao có thể làm trái lời hứa, ta chỉ sợ tướng quân đi đường thiếu thốn, nên đem ít đồ hành trang đến đưa tiễn. (Nói xong bưng mâm vàng đến.)",},
        [3105] = {5102,"Quan Vũ","Tào Tháo","Ta nhiều lần được thừa tướng ban thưởng, vẫn còn đủ dùng. Xin để lại số vàng này thưởng cho tướng sĩ.","Ta nhiều lần được thừa tướng ban thưởng, vẫn còn đủ dùng. Xin để lại số vàng này thưởng cho tướng sĩ.",},
        [3106] = {5102,"Tào Tháo","Quan Vũ","Món quà nhỏ thưởng công lớn, sao phải từ chối chứ?","Món quà nhỏ thưởng công lớn, sao phải từ chối chứ?",},
        [3107] = {5102,"Quan Vũ","Tào Tháo","Chút công lao nhỏ có đáng là gì.","Chút công lao nhỏ có đáng là gì.",},
        [3108] = {5102,"Tào Tháo","Quan Vũ","Vân Trường là nghĩa sĩ, tiếc là ta kém phúc không lưu lại được. Chiếc cẩm bào xem như chút lòng thành.","Vân Trường là nghĩa sĩ, tiếc là ta kém phúc không lưu lại được. Chiếc cẩm bào xem như chút lòng thành.",},
        [3109] = {5102,"Quan Vũ","Tào Tháo","Đội ơn thừa tướng ban áo, sau này sẽ gặp lại.","Đội ơn thừa tướng ban áo, sau này sẽ gặp lại.",},
        [3110] = {5103,"Khổng Tú","Quan Vũ","Tướng quân đi đâu?","Tướng quân đi đâu?",},
        [3111] = {5103,"Quan Vũ","Khổng Tú","Ta vừa cáo từ thừa tướng, sang Hàn Bắc tìm huynh.","Ta vừa cáo từ thừa tướng, sang Hàn Bắc tìm huynh.",},
        [3112] = {5103,"Khổng Tú","Quan Vũ","Hàn Bắc Viên Thiệu đối đầu với thừa tướng. Tướng quân qua đó chắc là có công văn của thừa tướng?","Hàn Bắc Viên Thiệu đối đầu với thừa tướng. Tướng quân qua đó chắc là có công văn của thừa tướng?",},
        [3113] = {5103,"Quan Vũ","Khổng Tú","Vì đi đường vội vã nên chưa kịp xin công văn.","Vì đi đường vội vã nên chưa kịp xin công văn.",},
        [3114] = {5103,"Khổng Tú","Quan Vũ","Không có bằng chứng, đợi ta bẩm báo Thừa Tướng mới có thể thả.","Không có bằng chứng, đợi ta bẩm báo Thừa Tướng mới có thể thả.",},
        [3115] = {5103,"Quan Vũ","Khổng Tú","Đợi bẩm báo sẽ làm lỡ mất hành trình.","Đợi bẩm báo sẽ làm lỡ mất hành trình.",},
        [3116] = {5103,"Khổng Tú","Quan Vũ","Phép tắc bắt buộc, phải như thế thôi.","Phép tắc bắt buộc, phải như thế thôi.",},
        [3117] = {5103,"Quan Vũ","Khổng Tú","Ngươi không cho ta qua cửa quan phải không?","Ngươi không cho ta qua cửa quan phải không?",},
        [3118] = {5103,"Khổng Tú","Quan Vũ","Muốn đi thì phải để lại già trẻ làm con tin.","Muốn đi thì phải để lại già trẻ làm con tin.",},
        [3119] = {5103,"Quan Vũ","Khổng Tú","Thật ngông cuồng, xem đao.","Thật ngông cuồng, xem đao.",},
        [3120] = {5104,"Hàn Phúc","Quan Vũ","Người đến là ai?","Người đến là ai?",},
        [3121] = {5104,"Quan Vũ","Hàn Phúc","Ta là Hán Thọ Đình Hầu Quan Vũ, xin mượn đường đi qua.","Ta là Hán Thọ Đình Hầu Quan Vũ, xin mượn đường đi qua.",},
        [3122] = {5104,"Hàn Phúc","Quan Vũ","Có công văn của thừa tướng không?","Có công văn của thừa tướng không?",},
        [3123] = {5104,"Quan Vũ","Hàn Phúc","Ta bận việc chưa kịp xin lấy.","Ta bận việc chưa kịp xin lấy.",},
        [3124] = {5104,"Hàn Phúc","Quan Vũ","Ta phụng mệnh thừa tướng trấn thủ nơi này để khám xét kẻ gian đi lại. Nếu không có công văn tức là trốn chạy.","Ta phụng mệnh thừa tướng trấn thủ nơi này để khám xét kẻ gian đi lại. Nếu không có công văn tức là trốn chạy.",},
        [3125] = {5104,"Quan Vũ","Hàn Phúc","Khổng Tú vừa bị ta diệt, ngươi cũng không muốn sống sao?","Khổng Tú vừa bị ta diệt, ngươi cũng không muốn sống sao?",},
        [3126] = {5104,"Quan Vũ","Hàn Phúc","Xem đao!","Xem đao!",},
        [3127] = {5105,"Biện Hỉ","Quan Vũ","Tướng quân vang danh thiên hạ, ai cũng kính ngưỡng! Nay trở về với hoàng thúc là sự trung nghĩa!","Tướng quân vang danh thiên hạ, ai cũng kính ngưỡng! Nay trở về với hoàng thúc là sự trung nghĩa!",},
        [3128] = {5105,"Quan Vũ","Biện Hỉ","Nhưng ta vừa trảm sát Khổng Tú, Hàn Phúc!","Nhưng ta vừa trảm sát Khổng Tú, Hàn Phúc!",},
        [3129] = {5105,"Biện Hỉ","Quan Vũ","Tướng quân làm đúng, ta sẽ trình bày uẩn khúc với thừa tướng.","Tướng quân làm đúng, ta sẽ trình bày uẩn khúc với thừa tướng.",},
        [3130] = {5105,"Quan Vũ","Biện Hỉ","Ha ha, xem như ngươi biết điều.","Ha ha, xem như ngươi biết điều.",},
        [3131] = {5105,"Biện Hỉ","Quan Vũ","Vân Trường có muốn vào Trấn Quốc Tự uống trà, trong tự có một tăng nhân cùng quê hương với Vân Trường.","Vân Trường có muốn vào Trấn Quốc Tự uống trà, trong tự có một tăng nhân cùng quê hương với Vân Trường.",},
        [3132] = {5105,"Quan Vũ","Biện Hỉ","Rất tốt.","Rất tốt.",},
        [3133] = {5106,"Quan Vũ","Biện Hỉ","Biện quân mời quan mỗ là có ý tốt hay ý xấu?","Biện quân mời quan mỗ là có ý tốt hay ý xấu?",},
        [3134] = {5106,"Biện Hỉ","Quan Vũ","Đương nhiên là ý tốt.","Đương nhiên là ý tốt.",},
        [3135] = {5106,"Quan Vũ","Biện Hỉ","Ta nghĩ ngươi là người tốt, ai ngờ ngươi dám làm vậy!","Ta nghĩ ngươi là người tốt, ai ngờ ngươi dám làm vậy!",},
        [3136] = {5106,"Quan Vũ","Biện Hỉ","Ta đã thấy đám Đao Phủ Thủ đang ẩn thân rồi. (Nói xong giận dữ trảm Đao Phủ Thủ và Biện Hỉ.)","Ta đã thấy đám Đao Phủ Thủ đang ẩn thân rồi. (Nói xong giận dữ trảm Đao Phủ Thủ và Biện Hỉ.)",},
        [3137] = {5107,"Vương Thực","Quan Vũ","Tướng quân đi đường vất vả, hai phu nhân trên xe mỏi mệt, xin mời vào thành tạm nghỉ một đêm, ngày mai lên đường tiếp.","Tướng quân đi đường vất vả, hai phu nhân trên xe mỏi mệt, xin mời vào thành tạm nghỉ một đêm, ngày mai lên đường tiếp.",},
        [3138] = {5107,"Quan Vũ","Vương Thực","Rất tốt, xin mời đi trước, ta với nhị tẩu theo sau.","Rất tốt, xin mời đi trước, ta với nhị tẩu theo sau.",},
        [3139] = {5107,"Nhân vật","Quan Vũ","Tuyệt đối không thể!","Tuyệt đối không thể!",},
        [3140] = {5107,"Nhân vật","Quan Vũ","Vương Thực có lòng muốn hại tướng quân.","Vương Thực có lòng muốn hại tướng quân.",},
        [3141] = {5107,"Quan Vũ","Nhân vật","Là thế nào?","Là thế nào?",},
        [3142] = {5107,"Nhân vật","Quan Vũ","Ta thấy xung quanh quán trọ rải đầy vật dễ cháy, chắc chắn hắn ta muốn đốt lửa thiêu quán trọ.","Ta thấy xung quanh quán trọ rải đầy vật dễ cháy, chắc chắn hắn ta muốn đốt lửa thiêu quán trọ.",},
        [3143] = {5107,"Quan Vũ","Nhân vật","Dương Uy Tướng hãy hộ tống nhị tẩu ra thành trước, ta đi diệt tên Vương Thực đó.","Dương Uy Tướng hãy hộ tống nhị tẩu ra thành trước, ta đi diệt tên Vương Thực đó.",},
        [3144] = {5108,"Tần Kỳ","Quan Vũ","Người đến là ai?","Người đến là ai?",},
        [3145] = {5108,"Quan Vũ","Tần Kỳ","Hán Thọ Đình Hầu Quan Vũ.","Hán Thọ Đình Hầu Quan Vũ.",},
        [3146] = {5108,"Tần Kỳ","Quan Vũ","Nay muốn đi đâu?","Nay muốn đi đâu?",},
        [3147] = {5108,"Quan Vũ","Tần Kỳ","Muốn đến Hàn Bắc tìm huynh trưởng Lưu Huyền Đức, xin cho đi qua bến đò.","Muốn đến Hàn Bắc tìm huynh trưởng Lưu Huyền Đức, xin cho đi qua bến đò.",},
        [3148] = {5108,"Tần Kỳ","Quan Vũ","Công văn của thừa tường đâu?","Công văn của thừa tường đâu?",},
        [3149] = {5108,"Quan Vũ","Tần Kỳ","Ta không chịu điều khiển của thừa tướng, có công văn gì chứ!","Ta không chịu điều khiển của thừa tướng, có công văn gì chứ!",},
        [3150] = {5108,"Tần Kỳ","Quan Vũ","Ta phụng lệnh Hà Hầu tướng quân giữ cửa ải này. Ngươi dù có cánh cũng không bay qua được!","Ta phụng lệnh Hà Hầu tướng quân giữ cửa ải này. Ngươi dù có cánh cũng không bay qua được!",},
        [3151] = {5108,"Quan Vũ","Tần Kỳ","Ngươi có biết việc ta trảm tướng chặn đường không?","Ngươi có biết việc ta trảm tướng chặn đường không?",},
        [3152] = {5108,"Tần Kỳ","Quan Vũ","Ngươi chỉ diệt được hạ tướng vô danh, dám diệt ta sao?","Ngươi chỉ diệt được hạ tướng vô danh, dám diệt ta sao?",},
        [3153] = {5108,"Quan Vũ","Tần Kỳ","Ngươi so được với Nhan Lương, Văn Xú không?","Ngươi so được với Nhan Lương, Văn Xú không?",},
        [3154] = {5108,"Tần Kỳ","Quan Vũ","Lại dám xem thường ta, dẫn ngựa ra chiến!","Lại dám xem thường ta, dẫn ngựa ra chiến!",},
        [3155] = {5109,"Hạ Hầu Đôn","Quan Vũ","Quan mỗ đừng chạy!","Quan mỗ đừng chạy!",},
        [3156] = {5109,"Quan Vũ","Hạ Hầu Đôn","Ngươi đuổi đến đây làm mất sự độ lượng của thừa tướng.","Ngươi đuổi đến đây làm mất sự độ lượng của thừa tướng.",},
        [3157] = {5109,"Hạ Hầu Đôn","Quan Vũ","Thừa tướng không có công văn truyền báo. Ngươi dọc đường trảm tướng, rất là vô lễ. Ta quyết bắt ngươi giải về để thừa tướng xét xử!","Thừa tướng không có công văn truyền báo. Ngươi dọc đường trảm tướng, rất là vô lễ. Ta quyết bắt ngươi giải về để thừa tướng xét xử!",},
        [3158] = {5110,"Trương Liêu","","Vân Trường, Nguyên Nhượng đừng đánh nhau nữa!","Vân Trường, Nguyên Nhượng đừng đánh nhau nữa!",},
        [3159] = {5110,"Trương Liêu","","Phụng chỉ thừa tướng: Vì biết Vân Trường vượt ải trảm tướng, sợ Vân Trường đi đường bị ngăn trở, nên sai ta đi truyền lời cho các ải cứ để cho Vân Trường đi.","Phụng chỉ thừa tướng: Vì biết Vân Trường vượt ải trảm tướng, sợ Vân Trường đi đường bị ngăn trở, nên sai ta đi truyền lời cho các ải cứ để cho Vân Trường đi.",},
        [3160] = {5110,"Hạ Hầu Đôn","Trương Liêu","Tần Kỳ là cháu của Thái Dương, hẳn gửi Tần Kỳ ở chỗ ta, nay bị Quan mỗ sát hại, sao hắn chịu để yên?","Tần Kỳ là cháu của Thái Dương, hẳn gửi Tần Kỳ ở chỗ ta, nay bị Quan mỗ sát hại, sao hắn chịu để yên?",},
        [3161] = {5110,"Trương Liêu","Hạ Hầu Đôn","Ta gặp Sái tướng quân sẽ có cách hòa giải. Thừa tướng khoan dung để cho Vân Trường đi, ngươi không nên trái lệnh.","Ta gặp Sái tướng quân sẽ có cách hòa giải. Thừa tướng khoan dung để cho Vân Trường đi, ngươi không nên trái lệnh.",},
        [3162] = {5110,"Hạ Hầu Đôn","Trương Liêu","Hừ, nếu thừa tướng đã nói vậy, ta biết làm sao được!","Hừ, nếu thừa tướng đã nói vậy, ta biết làm sao được!",},
        [3163] = {5110,"Trương Liêu","Hạ Hầu Đôn","Giờ Vân Trường muốn đi đâu?","Giờ Vân Trường muốn đi đâu?",},
        [3164] = {5110,"Quan Vũ","Trương Liêu","Nghe nói huynh trưởng không ở chỗ Viên Thiệu nữa nên định đi tìm khắp thiên hạ.","Nghe nói huynh trưởng không ở chỗ Viên Thiệu nữa nên định đi tìm khắp thiên hạ.",},
        [3165] = {5110,"Trương Liêu","Quan Vũ","Nếu chưa biết Huyền Đức ở đâu, hãy trở về gặp thừa tướng đã, được không?","Nếu chưa biết Huyền Đức ở đâu, hãy trở về gặp thừa tướng đã, được không?",},
        [3166] = {5110,"Quan Vũ","Trương Liêu","Không thể như vậy được! Văn Viễn về gặp thừa tướng hãy tạ tội thay ta.","Không thể như vậy được! Văn Viễn về gặp thừa tướng hãy tạ tội thay ta.",},
        [3167] = {5201,"Chu Thương","Quan Vũ","Đây hẳn là Quan tướng quân, Chu Thương xin bái kiến tướng quân.","Đây hẳn là Quan tướng quân, Chu Thương xin bái kiến tướng quân.",},
        [3168] = {5201,"Quan Vũ","Chu Thương","Tráng sĩ từng gặp Quan mỗ ở đâu?","Tráng sĩ từng gặp Quan mỗ ở đâu?",},
        [3169] = {5201,"Chu Thương","Quan Vũ","Lúc theo Hoàng Cân Trương Bảo từng thấy qua ngài, tiếc rằng mình trót theo giặc, không được theo hầu.","Lúc theo Hoàng Cân Trương Bảo từng thấy qua ngài, tiếc rằng mình trót theo giặc, không được theo hầu.",},
        [3170] = {5201,"Chu Thương","Quan Vũ","Nay may được gặp, mong tướng quân đừng ruồng bỏ, cho ta làm lính bộ, sớm tối cầm roi theo sau ngựa!","Nay may được gặp, mong tướng quân đừng ruồng bỏ, cho ta làm lính bộ, sớm tối cầm roi theo sau ngựa!",},
        [3171] = {5201,"Quan Vũ","Chu Thương","Ngươi đi theo ta, thuộc hạ ngươi làm thế nào?","Ngươi đi theo ta, thuộc hạ ngươi làm thế nào?",},
        [3172] = {5201,"Chu Thương","Quan Vũ","Ai muốn theo thì theo, nếu không thì tuỳ ý.","Ai muốn theo thì theo, nếu không thì tuỳ ý.",},
        [3173] = {5201,"Chu Thương","Quan Vũ","Ta chỉ xin một mình đi bộ theo tướng quân, dù đường xa vạn dặm cũng không ngại.","Ta chỉ xin một mình đi bộ theo tướng quân, dù đường xa vạn dặm cũng không ngại.",},
        [3174] = {5202,"Liêu Hóa","Quan Vũ","Quan tướng quân xin dừng lại!","Quan tướng quân xin dừng lại!",},
        [3175] = {5202,"Quan Vũ","Liêu Hóa","Ngươi là ai?","Ngươi là ai?",},
        [3176] = {5202,"Liêu Hóa","Quan Vũ","Ta họ Liêu tên Hoá, tự Nguyên Kiệm, vốn người Tương Dương, vì thời loạn mà lưu lạc giang hồ, tụ tập được hơn năm trăm người, cướp bóc kiếm ăn.","Ta họ Liêu tên Hoá, tự Nguyên Kiệm, vốn người Tương Dương, vì thời loạn mà lưu lạc giang hồ, tụ tập được hơn năm trăm người, cướp bóc kiếm ăn.",},
        [3177] = {5202,"Liêu Hóa","Quan Vũ","Vừa rồi có đồng bạn là Đỗ Viễn, xuống núi tuần tra, cướp nhầm hai vị phu nhân đem lên núi.","Vừa rồi có đồng bạn là Đỗ Viễn, xuống núi tuần tra, cướp nhầm hai vị phu nhân đem lên núi.",},
        [3178] = {5202,"Liêu Hóa","Quan Vũ","Ta hỏi người theo hầu mới biết là phu nhân của Lưu hoàng thúc, lại nghe có tướng quân đi hộ tống, ta muốn đưa ngay hai phu nhân xuống núi.","Ta hỏi người theo hầu mới biết là phu nhân của Lưu hoàng thúc, lại nghe có tướng quân đi hộ tống, ta muốn đưa ngay hai phu nhân xuống núi.",},
        [3179] = {5202,"Liêu Hóa","Quan Vũ","Đỗ Viễn nói năng càn rỡ nên bị ta trảm sát, đem thủ cấp nộp tướng quân để xin chịu tội.","Đỗ Viễn nói năng càn rỡ nên bị ta trảm sát, đem thủ cấp nộp tướng quân để xin chịu tội.",},
        [3180] = {5202,"Quan Vũ","Liêu Hóa","Hai vị phu nhân đang ở đâu?","Hai vị phu nhân đang ở đâu?",},
        [3181] = {5202,"Liêu Hóa","Quan Vũ","Đang ở trong núi.","Đang ở trong núi.",},
        [3182] = {5202,"Quan Vũ","Liêu Hóa","Mau dẫn ta đi!","Mau dẫn ta đi!",},
        [3183] = {5203,"Quan Định","Lưu Bị","Quan công ngủ lại ở Quan Định Trang ta, thật là vinh hạnh!","Quan công ngủ lại ở Quan Định Trang ta, thật là vinh hạnh!",},
        [3184] = {5203,"Quan Định","Lưu Bị","Ta có hai con trai: Trưởng tử Quan Ninh, học văn. Con thứ Quan Bình, học võ.","Ta có hai con trai: Trưởng tử Quan Ninh, học văn. Con thứ Quan Bình, học võ.",},
        [3185] = {5203,"Quan Định","Lưu Bị","Nay có ý muốn để con thứ theo Quan tướng quân, không biết có được không?","Nay có ý muốn để con thứ theo Quan tướng quân, không biết có được không?",},
        [3186] = {5203,"Lưu Bị","Quan Định","Bao nhiêu tuổi?","Bao nhiêu tuổi?",},
        [3187] = {5203,"Quan Định","Lưu Bị","Mười tám tuổi.","Mười tám tuổi.",},
        [3188] = {5203,"Lưu Bị","Quan Định","Đệ đệ ta chưa có con, nay nhận Hiền Lang làm con, thế nào?","Đệ đệ ta chưa có con, nay nhận Hiền Lang làm con, thế nào?",},
        [3189] = {5203,"Quan Định","Lưu Bị","Tốt lắm, tốt lắm!","Tốt lắm, tốt lắm!",},
        [3190] = {5203,"Nhân vật","Lưu Bị","Chúc mừng Vân Trường nhận được con cháu!","Chúc mừng Vân Trường nhận được con cháu!",},
        [3191] = {5203,"Quan Vũ","Nhân vật","Ha ha ha.","Ha ha ha.",},
        [3192] = {5301,"Quan Vũ","Trương Phi","Hiền đệ sao lại hò hét múa xà mâu chạy lại đâm ta.","Hiền đệ sao lại hò hét múa xà mâu chạy lại đâm ta.",},
        [3193] = {5301,"Quan Vũ","Trương Phi","Chẳng lẽ đã quên kết nghĩa vườn đào?","Chẳng lẽ đã quên kết nghĩa vườn đào?",},
        [3194] = {5301,"Trương Phi","Quan Vũ","Ngươi đã bội nghĩa còn mặt mũi nào đến gặp ta nữa?","Ngươi đã bội nghĩa còn mặt mũi nào đến gặp ta nữa?",},
        [3195] = {5301,"Quan Vũ","Trương Phi","Ta làm sao mà bội nghĩa?","Ta làm sao mà bội nghĩa?",},
        [3196] = {5301,"Trương Phi","Quan Vũ","Ngươi bỏ huynh trưởng, đầu hàng Tào Tháo, nay lại đến lừa ta, hôm nay ta sẽ liều mạng với ngươi!","Ngươi bỏ huynh trưởng, đầu hàng Tào Tháo, nay lại đến lừa ta, hôm nay ta sẽ liều mạng với ngươi!",},
        [3197] = {5301,"Nhân vật","Trương Phi","Thì ra ngươi không biết! Đừng trách Vân Trường huynh.","Thì ra ngươi không biết! Đừng trách Vân Trường huynh.",},
        [3198] = {5301,"Nhân vật","Trương Phi","Vân Trường vượt ngũ quan trảm lục tướng, đi ngàn dặm mới đến được Cổ Thành!","Vân Trường vượt ngũ quan trảm lục tướng, đi ngàn dặm mới đến được Cổ Thành!",},
        [3199] = {5301,"Trương Phi","Nhân vật","Là đệ trách nhầm nhị ca rồi!","Là đệ trách nhầm nhị ca rồi!",},
        [3200] = {5302,"Thái Dương","Quan Vũ","Ngươi hại cháu ta Tần Kỳ, thì ra trốn ở đây!","Ngươi hại cháu ta Tần Kỳ, thì ra trốn ở đây!",},
        [3201] = {5302,"Thái Dương","Quan Vũ","Ta phụng mệnh thừa tướng, đến bắt ngươi!","Ta phụng mệnh thừa tướng, đến bắt ngươi!",},
        [3202] = {5302,"Quan Vũ","Thái Dương","Đệ đánh trống cho ta, trước 3 tiếng trống sẽ hạ được hắn.","Đệ đánh trống cho ta, trước 3 tiếng trống sẽ hạ được hắn.",},
        [3203] = {5302,"Trương Phi","Quan Vũ","Được!","Được!",},
        [3204] = {5303,"Chu Thương","Lưu Bị","Trước khi ta về Ngọa Ngưu Sơn, có một tướng cưỡi ngựa đến, chỉ một hiệp đã hạ thủ Bùi Nguyên Thiệu!","Trước khi ta về Ngọa Ngưu Sơn, có một tướng cưỡi ngựa đến, chỉ một hiệp đã hạ thủ Bùi Nguyên Thiệu!",},
        [3205] = {5303,"Chu Thương","Lưu Bị","Kêu gọi đầu hàng, chiếm lấy sơn trại.","Kêu gọi đầu hàng, chiếm lấy sơn trại.",},
        [3206] = {5303,"Chu Thương","Lưu Bị","Ta cùng tướng đó giao chiến, bị hắn thắng liên tục, người bị thương 3 chỗ, nên đến báo.","Ta cùng tướng đó giao chiến, bị hắn thắng liên tục, người bị thương 3 chỗ, nên đến báo.",},
