        [3212] = {5304,"Triệu Vân","Lưu Bị","Vân tự biệt sứ quân, không ngờ Công Tôn Toản không nghe lời khuyên, dẫn đến tự diệt.","Vân tự biệt sứ quân, không ngờ Công Tôn Toản không nghe lời khuyên, dẫn đến tự diệt.",},
        [3213] = {5304,"Triệu Vân","Lưu Bị","Viên Thiệu nhiều lần chiêu Vân, Vân nghĩ Thiệu Diệc không phải là người dùng người, nên không đến.","Viên Thiệu nhiều lần chiêu Vân, Vân nghĩ Thiệu Diệc không phải là người dùng người, nên không đến.",},
        [3214] = {5304,"Triệu Vân","Lưu Bị","Sau đó muốn đến Từ Châu đầu sứ quân, lại nghe nói Từ Châu thất thủ, Vân Trường đã quy Tào Tháo, sứ quân thì ở chỗ Viên Thiệu.","Sau đó muốn đến Từ Châu đầu sứ quân, lại nghe nói Từ Châu thất thủ, Vân Trường đã quy Tào Tháo, sứ quân thì ở chỗ Viên Thiệu.",},
        [3215] = {5304,"Triệu Vân","Lưu Bị","Vân nhiều lần đến nương nhờ, chỉ e Viên Thiệu trách phạt.","Vân nhiều lần đến nương nhờ, chỉ e Viên Thiệu trách phạt.",},
