        [3218] = {5304,"Lưu Bị","Triệu Vân","Ta mới gặp Tử Long, thì đã không nỡ rời khỏi. Đúng là may mắn!","Ta mới gặp Tử Long, thì đã không nỡ rời khỏi. Đúng là may mắn!",},
        [3219] = {5304,"Triệu Vân","Lưu Bị","Vân phiêu bạc khắp nơi chưa gặp được ai như sứ quân. Nay được gặp, mong xóa bỏ quá khứ.","Vân phiêu bạc khắp nơi chưa gặp được ai như sứ quân. Nay được gặp, mong xóa bỏ quá khứ.",},
