        [3285] = {5503,"Tôn Sách","Nhân vật","Ngươi không thấy sao? Tên Vu Cát yêu đạo đang ngồi trong lều của ta!","Ngươi không thấy sao? Tên Vu Cát yêu đạo đang ngồi trong lều của ta!",},
        [3286] = {5503,"Nhân vật","Tôn Sách","Vu Cát nào ở đây, mấy ngày trước chẳng phải ta đã diệt rồi sao?","Vu Cát nào ở đây, mấy ngày trước chẳng phải ta đã diệt rồi sao?",},
        [3287] = {5503,"Nhân vật","Tôn Sách","Có thể do dạo này ngươi quá mệt mỏi, thêm việc vết thương bị Hứa Cống Gia Khách đánh lén chưa lành, nên xuất hiện ảo giác.","Có thể do dạo này ngươi quá mệt mỏi, thêm việc vết thương bị Hứa Cống Gia Khách đánh lén chưa lành, nên xuất hiện ảo giác.",},
        [3288] = {5503,"Nhân vật","Tôn Sách","Không sao đâu!","Không sao đâu!",},
        [3289] = {5503,"Tôn Sách","Nhân vật","Sao ta lại tiều tụy như thế! (Nói xong lấy gương tự soi.)","Sao ta lại tiều tụy như thế! (Nói xong lấy gương tự soi.)",},
        [3290] = {5503,"Tôn Sách","Nhân vật","Mau xem! Tên Vu Cát đang trong gương của ta này.","Mau xem! Tên Vu Cát đang trong gương của ta này.",},
        [3291] = {5503,"Tôn Sách","Nhân vật","A a a a a a! (Tôn Sách la lớn rồi ngất xỉu.)","A a a a a a! (Tôn Sách la lớn rồi ngất xỉu.)",},
        [3292] = {5503,"Nhân vật","Tôn Sách","Bá Phù ngươi không sao chứ? Bá Phù ngươi tỉnh dậy đi!","Bá Phù ngươi không sao chứ? Bá Phù ngươi tỉnh dậy đi!",},
        [3293] = {5504,"Tôn Sách","Tôn Quyền","Thiên hạ đại loạn, kiên giữ tam giang, chắc chắn thành công.","Thiên hạ đại loạn, kiên giữ tam giang, chắc chắn thành công.",},
        [3294] = {5504,"Tôn Sách","Tôn Quyền","Nếu lấy quân Giang Đông lập kế giữ hai trận, tranh gianh thiên hạ, khanh không bằng ta.","Nếu lấy quân Giang Đông lập kế giữ hai trận, tranh gianh thiên hạ, khanh không bằng ta.",},
        [3295] = {5504,"Tôn Sách","Tôn Quyền","Khiến mọi người cố sức bảo vệ Giang Đông, ta không bằng khanh!","Khiến mọi người cố sức bảo vệ Giang Đông, ta không bằng khanh!",},
        [3296] = {5504,"Tôn Quyền","Tôn Sách","Ta còn trẻ, không làm chuyện lớn thì làm gì?","Ta còn trẻ, không làm chuyện lớn thì làm gì?",},
        [3297] = {5504,"Tôn Sách","Tôn Quyền","Đệ có thể đảm đương chuyện lớn. Nếu chuyện nội bộ không quyết có thể hỏi Trương Chiêu.","Đệ có thể đảm đương chuyện lớn. Nếu chuyện nội bộ không quyết có thể hỏi Trương Chiêu.",},
        [3298] = {5504,"Tôn Sách","Tôn Quyền","Chuyện ngoài không quyết có thể hỏi Chu Du.","Chuyện ngoài không quyết có thể hỏi Chu Du.",},
        [3299] = {5504,"Tôn Sách","Tôn Quyền","Dương Uy Tướng ở đâu?","Dương Uy Tướng ở đâu?",},
        [3300] = {5504,"Nhân vật","Tôn Sách","Ta ở đây.","Ta ở đây.",},
        [3301] = {5504,"Tôn Sách","Nhân vật","Bá Phù được các anh hùng giúp đỡ, sau này nếu đệ của ta gặp nạn, chắc lại phải làm phiền các vị anh hùng.","Bá Phù được các anh hùng giúp đỡ, sau này nếu đệ của ta gặp nạn, chắc lại phải làm phiền các vị anh hùng.",},
        [3302] = {5504,"Nhân vật","Tôn Sách","Nếu gặp khó khăn, việc không phạm thiên lý, thì ta sẽ giúp đỡ hết mình.","Nếu gặp khó khăn, việc không phạm thiên lý, thì ta sẽ giúp đỡ hết mình.",},
        [3303] = {5504,"Tôn Sách","Nhân vật","Lại phiền Dương Uy Tướng, chuyển đến Chu Lang, hãy cố gắng phò trợ đệ của ta. (Nhắm mắt xuôi tay.)","Lại phiền Dương Uy Tướng, chuyển đến Chu Lang, hãy cố gắng phò trợ đệ của ta. (Nhắm mắt xuôi tay.)",},
        [3304] = {5601,"Tôn Quyền","Chu Du","Nay kế thừa sự nghiệp, phải giữ thế nào đây?","Nay kế thừa sự nghiệp, phải giữ thế nào đây?",},
        [3305] = {5601,"Chu Du","Tôn Quyền","Hiện giờ phải tìm người cao minh phò trợ, sau có thể định Giang Đông.","Hiện giờ phải tìm người cao minh phò trợ, sau có thể định Giang Đông.",},
        [3306] = {5601,"Tôn Quyền","Chu Du","Tiên huynh di ngôn: Chuyện nội nhờ Tử Bố, chuyện ngoại nhờ Công Cấn.","Tiên huynh di ngôn: Chuyện nội nhờ Tử Bố, chuyện ngoại nhờ Công Cấn.",},
        [3307] = {5601,"Chu Du","Tôn Quyền","Tử Bố là người hiền đạt, có thể nhận trọng trách. Du bất tài không thể đảm nhận, nhưng có thể tiến cử một người.","Tử Bố là người hiền đạt, có thể nhận trọng trách. Du bất tài không thể đảm nhận, nhưng có thể tiến cử một người.",},
        [3308] = {5601,"Tôn Quyền","Chu Du","Ai?","Ai?",},
        [3309] = {5601,"Chu Du","Tôn Quyền","Họ Lỗ, tên Túc, tự Tử Kính, người Lâm Chuẩn Đông Châu.","Họ Lỗ, tên Túc, tự Tử Kính, người Lâm Chuẩn Đông Châu.",},
        [3310] = {5601,"Chu Du","Tôn Quyền","Người này tấm lòng rộng lượng, đầy mưu kế. Thường hay tế bần.","Người này tấm lòng rộng lượng, đầy mưu kế. Thường hay tế bần.",},
        [3311] = {5601,"Chu Du","Tôn Quyền","Khi Du ở Sào Trường, do thiếu lương thực nên đã đến chỗ Lỗ Túc xin giúp đỡ.","Khi Du ở Sào Trường, do thiếu lương thực nên đã đến chỗ Lỗ Túc xin giúp đỡ.",},
        [3312] = {5601,"Chu Du","Tôn Quyền","Túc liền tặng ngay một khuân lương thực. Bình thường thích cưỡi ngựa bắn cung, làm nhạc.","Túc liền tặng ngay một khuân lương thực. Bình thường thích cưỡi ngựa bắn cung, làm nhạc.",},
        [3313] = {5601,"Chu Du","Tôn Quyền","Bạn của hắn là Lưu Tử Dương muốn gặp ở Sào Hồ, nhưng Túc vẫn đang lương lự. Nay có thể triệu vào.","Bạn của hắn là Lưu Tử Dương muốn gặp ở Sào Hồ, nhưng Túc vẫn đang lương lự. Nay có thể triệu vào.",},
        [3314] = {5601,"Tôn Quyền","Chu Du","Hay quá, cứ theo lời của Tử Bố.","Hay quá, cứ theo lời của Tử Bố.",},
        [3315] = {5602,"Chu Du","Lỗ Túc","Tôn Quyền khá ngưỡng mộ ngươi.","Tôn Quyền khá ngưỡng mộ ngươi.",},
        [3316] = {5602,"Lỗ Túc","Chu Du","Lưu Tử Dương hẹn mỗ đến Sào Hồ, mỗ đành đến theo.","Lưu Tử Dương hẹn mỗ đến Sào Hồ, mỗ đành đến theo.",},
        [3317] = {5602,"Chu Du","Lỗ Túc","Tích Mã Viện nói với Quang Võ Vân: Lúc này không phải chỉ quân chọn thần, thần cũng chọn quân.","Tích Mã Viện nói với Quang Võ Vân: Lúc này không phải chỉ quân chọn thần, thần cũng chọn quân.",},
        [3318] = {5602,"Chu Du","Lỗ Túc","Nay Tôn tướng quân ta đã tài giỏi vô cùng. Hãy cùng ta đến Đông Ngô.","Nay Tôn tướng quân ta đã tài giỏi vô cùng. Hãy cùng ta đến Đông Ngô.",},
        [3319] = {5602,"Lỗ Túc","Chu Du","Ta cùng ngươi đi gặp Tôn Quyền.","Ta cùng ngươi đi gặp Tôn Quyền.",},
        [3320] = {5603,"Tôn Quyền","Lỗ Túc","Nay ta kế thừa sự nghiệp của cha huynh, muốn giữ lấy nó, chuyện văn thư, quân tướng có gì chỉ dạy?","Nay ta kế thừa sự nghiệp của cha huynh, muốn giữ lấy nó, chuyện văn thư, quân tướng có gì chỉ dạy?",},
        [3321] = {5603,"Lỗ Túc","Tôn Quyền","Lúc trước Hạng Vũ là hiểm họa. Nay Tào Tháo cũng như Hạng Vũ, tướng quân làm sao bình định?","Lúc trước Hạng Vũ là hiểm họa. Nay Tào Tháo cũng như Hạng Vũ, tướng quân làm sao bình định?",},
        [3322] = {5603,"Lỗ Túc","Tôn Quyền","Túc nghĩ Hán thất khó lòng phục hưng, Tào Tháo cũng khó diệt. Nay chỉ còn cách ở lại Giang Đông xem tình hình.","Túc nghĩ Hán thất khó lòng phục hưng, Tào Tháo cũng khó diệt. Nay chỉ còn cách ở lại Giang Đông xem tình hình.",},
        [3323] = {5603,"Lỗ Túc","Tôn Quyền","Nay phương Bắc diệt Hoàng Tổ, tiến phạt ưu Biểu, chiếm giữ Trường Giang.","Nay phương Bắc diệt Hoàng Tổ, tiến phạt ưu Biểu, chiếm giữ Trường Giang.",},
        [3324] = {5603,"Lỗ Túc","Tôn Quyền","Sau đó lập hiệu Đế Hoàng, sau đó mưu đồ thiên hạ.","Sau đó lập hiệu Đế Hoàng, sau đó mưu đồ thiên hạ.",},
        [3325] = {5603,"Tôn Quyền","Lỗ Túc","Ha ha, nói có lý.","Ha ha, nói có lý.",},
        [3326] = {5604,"Lỗ Túc","Tôn Quyền","Ta có một người có thể tiến cử cho Tôn Quyền ngươi.","Ta có một người có thể tiến cử cho Tôn Quyền ngươi.",},
        [3327] = {5604,"Tôn Quyền","Lỗ Túc","Người đó là ai?","Người đó là ai?",},
        [3328] = {5604,"Lỗ Túc","Tôn Quyền","Người này học rộng hiểu nhiều, hiếu thảo cha mẹ. Họ Gia Cát, tên Cấn, tự Tử Du, người Lang Nha Hoa Dương.","Người này học rộng hiểu nhiều, hiếu thảo cha mẹ. Họ Gia Cát, tên Cấn, tự Tử Du, người Lang Nha Hoa Dương.",},
        [3329] = {5604,"Tôn Quyền","Lỗ Túc","Mau tiến cử cho ta.","Mau tiến cử cho ta.",},
        [3330] = {5605,"Tôn Quyền","Gia Cát Cẩn","Chuyện hôm nay Tử Du thấy thế nào?","Chuyện hôm nay Tử Du thấy thế nào?",},
        [3331] = {5605,"Gia Cát Cẩn","Tôn Quyền","Đừng theo Viên Thiệu, hãy theo Tào Tháo, sau đó tính kế","Đừng theo Viên Thiệu, hãy theo Tào Tháo, sau đó tính kế",},
        [3332] = {5605,"Tôn Quyền","Gia Cát Cẩn","Nói có lý.","Nói có lý.",},
        [3333] = {5606,"Nhân vật","Tôn Quyền","Ta du ngoạn Giang Đông, cũng có một người tiến cử cho ngươi.","Ta du ngoạn Giang Đông, cũng có một người tiến cử cho ngươi.",},
        [3334] = {5606,"Tôn Quyền","Nhân vật","Người đó là ai?","Người đó là ai?",},
        [3335] = {5606,"Nhân vật","Tôn Quyền","Người này họ Cổ, tên Ung, tự Nguyên Thán, là đệ tử của Trung Lương Thái Un; Người này nghiêm túc, không uống rượu.","Người này họ Cổ, tên Ung, tự Nguyên Thán, là đệ tử của Trung Lương Thái Un; Người này nghiêm túc, không uống rượu.",},
        [3336] = {5606,"Tôn Quyền","Nhân vật","Có thể chiêu hắn làm Thừa, lo chuyện Thái Thú.","Có thể chiêu hắn làm Thừa, lo chuyện Thái Thú.",},
        [3337] = {5607,"Trần Chấn","Viên Thiệu","Tôn Sách đã mất, Tôn Quyền kế nghiệp, được lòng dân.","Tôn Sách đã mất, Tôn Quyền kế nghiệp, được lòng dân.",},
        [3338] = {5607,"Trần Chấn","Viên Thiệu","Tào Tháo phong làm tướng quân, kết thành ngoại ứng.","Tào Tháo phong làm tướng quân, kết thành ngoại ứng.",},
        [3339] = {5607,"Viên Thiệu","Trần Chấn","Gì!","Gì!",},
        [3340] = {5607,"Viên Thiệu","Trần Chấn","Binh mã Ký, Thanh, U hợp lại hơn 70 vạn, công đánh Hứa Xương.","Binh mã Ký, Thanh, U hợp lại hơn 70 vạn, công đánh Hứa Xương.",},
        [3341] = {5701,"Thư Thụ","Viên Thiệu","Quân ta tuy đông, nhưng không dũng mãnh bằng quân địch, quân địch tuy tinh, nhưng lương thảo không được như ta.","Quân ta tuy đông, nhưng không dũng mãnh bằng quân địch, quân địch tuy tinh, nhưng lương thảo không được như ta.",},
        [3342] = {5701,"Thư Thụ","Viên Thiệu","Quân địch không lương thảo, tốc chiến sẽ có lợi, quân ta có lương thảo nên cố gắng trì hoãn cố thủ.","Quân địch không lương thảo, tốc chiến sẽ có lợi, quân ta có lương thảo nên cố gắng trì hoãn cố thủ.",},
        [3343] = {5701,"Thư Thụ","Viên Thiệu","Nếu kéo dài nhiều ngày, quân địch không chiến tự bại.","Nếu kéo dài nhiều ngày, quân địch không chiến tự bại.",},
        [3344] = {5701,"Viên Thiệu","Thư Thụ","Điền Phong làm lây động lòng quân, ta về sẽ trảm hán. Ngươi sao còn dám như vậy?","Điền Phong làm lây động lòng quân, ta về sẽ trảm hán. Ngươi sao còn dám như vậy?",},
        [3345] = {5701,"Viên Thiệu","Thư Thụ","Hãy giam Thư Thụ, đợi ta phá Tào rồi sẽ trị tội chung với Điền Phong!","Hãy giam Thư Thụ, đợi ta phá Tào rồi sẽ trị tội chung với Điền Phong!",},
        [3346] = {5702,"Tào Tháo","Viên Thiệu","Ta tiến cử ngươi làm Đại Tướng Quân, tại sao ngươi lại mưu phản?","Ta tiến cử ngươi làm Đại Tướng Quân, tại sao ngươi lại mưu phản?",},
        [3347] = {5702,"Viên Thiệu","Tào Tháo","Ngươi thực chất chỉ là tặc tử! Tội ác tày trời, lại còn dám vu khống người khác mưu phản?","Ngươi thực chất chỉ là tặc tử! Tội ác tày trời, lại còn dám vu khống người khác mưu phản?",},
        [3348] = {5702,"Tào Tháo","Viên Thiệu","Ta phụng chỉ đến thảo phạt!","Ta phụng chỉ đến thảo phạt!",},
        [3349] = {5702,"Viên Thiệu","Tào Tháo","Ta phụng Y Đai Chiếu thảo phạt tặc tử!","Ta phụng Y Đai Chiếu thảo phạt tặc tử!",},
        [3350] = {5702,"Tào Tháo","Viên Thiệu","Trương Liêu xuất chiến!","Trương Liêu xuất chiến!",},
        [3351] = {5702,"Viên Thiệu","Tào Tháo","Trương Hợp nghênh chiến!","Trương Hợp nghênh chiến!",},
        [3352] = {5703,"Viên Thiệu","","Quân Tào đại bại, rút đến Quan Độ.","Quân Tào đại bại, rút đến Quan Độ.",},
        [3353] = {5703,"Viên Thiệu","","Lệnh ba quân áp sát Quan Độ đóng quân.","Lệnh ba quân áp sát Quan Độ đóng quân.",},
        [3354] = {5703,"Thẩm Phối","Viên Thiệu","Nay có thể dùng 10 vạn binh trấn giữ Quan Độ, ở trước tại Tào Tháo đấp đất, lệnh lính phóng tên.","Nay có thể dùng 10 vạn binh trấn giữ Quan Độ, ở trước tại Tào Tháo đấp đất, lệnh lính phóng tên.",},
        [3355] = {5703,"Thẩm Phối","Viên Thiệu","Nếu Tào Tháo bỏ đi, ta sẽ được nơi này, Hứa Xương rồi cũng sẽ bị phá.","Nếu Tào Tháo bỏ đi, ta sẽ được nơi này, Hứa Xương rồi cũng sẽ bị phá.",},
        [3356] = {5703,"Viên Thiệu","Thẩm Phối","Chính Nam rất có lý.","Chính Nam rất có lý.",},
        [3357] = {5704,"Tào Tháo","","Quân Viên đắp đất, định đột phá ra ngoài nhưng lại bị cung nỏ thủ của Thẩm Phối ngăn cản, không thể tiến lên.","Quân Viên đắp đất, định đột phá ra ngoài nhưng lại bị cung nỏ thủ của Thẩm Phối ngăn cản, không thể tiến lên.",},
        [3358] = {5704,"Tào Tháo","","Phải làm sao đây?","Phải làm sao đây?",},
        [3359] = {5704,"Nhân vật","Tào Tháo","Mạnh Đức không phải lo!","Mạnh Đức không phải lo!",},
        [3360] = {5704,"Nhân vật","Tào Tháo","Ta từ Giang Đông đến, giúp người đối phó với Viên Thiệu!","Ta từ Giang Đông đến, giúp người đối phó với Viên Thiệu!",},
        [3361] = {5704,"Tào Tháo","Nhân vật","Tốt lắm, nhờ cả vào Dương Uy Tướng.","Tốt lắm, nhờ cả vào Dương Uy Tướng.",},
        [3362] = {5705,"Tào Tháo","Nhân vật","Trấn giữ Quan Độ, từ tháng 8 đến tháng 9, quân lực dần yếu, lương thảo không đủ.","Trấn giữ Quan Độ, từ tháng 8 đến tháng 9, quân lực dần yếu, lương thảo không đủ.",},
        [3363] = {5705,"Tào Tháo","Nhân vật","Ta định bỏ Quan Độ rút về Hứa Xương.","Ta định bỏ Quan Độ rút về Hứa Xương.",},
        [3364] = {5705,"Nhân vật","Tào Tháo","Ta nghĩ không nên lui về Hứa Xương, hãy hỏi ý kiến của Tuân Úc.","Ta nghĩ không nên lui về Hứa Xương, hãy hỏi ý kiến của Tuân Úc.",},
        [3365] = {5705,"Tào Tháo","Nhân vật","Ngươi thấy thế nào?","Ngươi thấy thế nào?",},
        [3366] = {5705,"Tuân Úc","Tào Tháo","Viên Thiệu tập hợp binh lính ở Quan Độ, muốn quyết một trận thắng thua với ngài. Ngài phải lấy ít địch nhiều.","Viên Thiệu tập hợp binh lính ở Quan Độ, muốn quyết một trận thắng thua với ngài. Ngài phải lấy ít địch nhiều.",},
        [3367] = {5705,"Tuân Úc","Tào Tháo","Nếu không thể chế ngự, đây sẽ là mối nguy cho thiên hạ.","Nếu không thể chế ngự, đây sẽ là mối nguy cho thiên hạ.",},
        [3368] = {5705,"Tuân Úc","Tào Tháo","Quân Thiệu tuy đông nhưng không có thực lực; chúa công thần võ, không có gì là không thể.","Quân Thiệu tuy đông nhưng không có thực lực; chúa công thần võ, không có gì là không thể.",},
        [3369] = {5705,"Tuân Úc","Tào Tháo","Chúa công cố thủ, khiến quân Thiệu không thể tấn công, tình hình kéo dài ắt có biến, khi đó sẽ là cơ hội tốt nhất.","Chúa công cố thủ, khiến quân Thiệu không thể tấn công, tình hình kéo dài ắt có biến, khi đó sẽ là cơ hội tốt nhất.",},
        [3370] = {5705,"Tào Tháo","Tuân Úc","Ta lập tức lệnh tướng sĩ tử thủ!","Ta lập tức lệnh tướng sĩ tử thủ!",},
        [3371] = {5801,"Tào Tháo","Tuân Du","Theo tin báo, quân Thiệu đã lui khoảng 30 dặm, đại tướng Hàn Mãnh sớm tối vận chuyển lương thảo đến tiếp tế.","Theo tin báo, quân Thiệu đã lui khoảng 30 dặm, đại tướng Hàn Mãnh sớm tối vận chuyển lương thảo đến tiếp tế.",},
        [3372] = {5801,"Tuân Du","Tào Tháo","Hàn Mãnh chỉ là kẻ hữu dũng vô mưu. Nếu phái người mang theo vài ngàn khinh kỵ tập kích giữa đường để cắt đứt lương thảo, quân Thiệu ắt sẽ loạn.","Hàn Mãnh chỉ là kẻ hữu dũng vô mưu. Nếu phái người mang theo vài ngàn khinh kỵ tập kích giữa đường để cắt đứt lương thảo, quân Thiệu ắt sẽ loạn.",},
        [3373] = {5801,"Tào Tháo","Tuân Du","Ai nguyện đi?","Ai nguyện đi?",},
        [3374] = {5801,"Nhân vật","Tào Tháo","Ta nguyện đến đó!","Ta nguyện đến đó!",},
        [3375] = {5801,"Tào Tháo","Nhân vật","Được!","Được!",},
        [3376] = {5802,"Viên Thiệu","","Lương thảo bị cướp, phải làm sao đây?","Lương thảo bị cướp, phải làm sao đây?",},
        [3377] = {5802,"Thẩm Phối","Viên Thiệu","Hành quân quan trọng là lương thảo, không thể không đề phòng. Ô Sào là nơi trữ lương, phải cho trọng binh canh giữ.","Hành quân quan trọng là lương thảo, không thể không đề phòng. Ô Sào là nơi trữ lương, phải cho trọng binh canh giữ.",},
        [3378] = {5802,"Viên Thiệu","Thẩm Phối","Ta đã có kế sách. Ngươi hãy về Nghiệp Đô giám sát lương thảo, không được có sơ suất.","Ta đã có kế sách. Ngươi hãy về Nghiệp Đô giám sát lương thảo, không được có sơ suất.",},
        [3379] = {5802,"Viên Thiệu","Thẩm Phối","Đại tướng Thuần Vu Quỳnh, bộ lĩnh đốc tướng Khôi Nguyên Tiến, Hàn Cử Tử, Lữ Uy Hoàng, Triệu Duệ mang hai vạn binh mã trẫn giữ Ô Sào.","Đại tướng Thuần Vu Quỳnh, bộ lĩnh đốc tướng Khôi Nguyên Tiến, Hàn Cử Tử, Lữ Uy Hoàng, Triệu Duệ mang hai vạn binh mã trẫn giữ Ô Sào.",},
        [3380] = {5803,"Hứa Du","Viên Thiệu","Chúng ta nhận được thư của Tào Tháo.","Chúng ta nhận được thư của Tào Tháo.",},
        [3381] = {5803,"Hứa Du","Viên Thiệu","Tào Tháo đóng quân ở Quan Độ đã lâu, Hứa Xương chắc chắn đang trống không. Nếu phái một nhóm quân tập kích Hứa Xương, chắc chắn có thể đoạt Hứa Xương và bắt Tào Tháo.","Tào Tháo đóng quân ở Quan Độ đã lâu, Hứa Xương chắc chắn đang trống không. Nếu phái một nhóm quân tập kích Hứa Xương, chắc chắn có thể đoạt Hứa Xương và bắt Tào Tháo.",},
        [3382] = {5803,"Hứa Du","Viên Thiệu","Nay Tào Tháo lương thảo đã hết, đây là cơ hội tốt, chia hai đường tấn công.","Nay Tào Tháo lương thảo đã hết, đây là cơ hội tốt, chia hai đường tấn công.",},
        [3383] = {5803,"Viên Thiệu","Hứa Du","Tào Tháo xảo quyệt, thư này chắc là kế dụ địch.","Tào Tháo xảo quyệt, thư này chắc là kế dụ địch.",},
        [3384] = {5803,"Hứa Du","Viên Thiệu","Nay không đoạt, mai này sẽ bị phản công.","Nay không đoạt, mai này sẽ bị phản công.",},
        [3385] = {5803,"Viên Thiệu","Hứa Du","Ngươi ở Ký Châu thường xuyên cướp bóc của bá tánh, đặt ra sưu cao thuế nặng, tiền bạc đều vào túi riêng.","Ngươi ở Ký Châu thường xuyên cướp bóc của bá tánh, đặt ra sưu cao thuế nặng, tiền bạc đều vào túi riêng.",},
        [3386] = {5803,"Viên Thiệu","Hứa Du","Ngươi còn mặt mũi hiến kế trước mặt ta sao?","Ngươi còn mặt mũi hiến kế trước mặt ta sao?",},
        [3387] = {5803,"Viên Thiệu","Hứa Du","Ngươi và Tào Tháo có giao tình, chắc nay lại được hắn hối lộ làm gian tế cho hắn!","Ngươi và Tào Tháo có giao tình, chắc nay lại được hắn hối lộ làm gian tế cho hắn!",},
        [3388] = {5803,"Viên Thiệu","Hứa Du","Vốn định trảm thủ, nhưng nương tình tạm để lại thủ cấp cho ngươi! Hay mau lui đi!","Vốn định trảm thủ, nhưng nương tình tạm để lại thủ cấp cho ngươi! Hay mau lui đi!",},
        [3389] = {5803,"Hứa Du","Viên Thiệu","Lời thật mất lòng, ngươi không đủ tư cách bàn việc lớn với ta!","Lời thật mất lòng, ngươi không đủ tư cách bàn việc lớn với ta!",},
        [3390] = {5804,"Tào Tháo","Hứa Du","Đã khuya rồi sao người lại đến đây?","Đã khuya rồi sao người lại đến đây?",},
        [3391] = {5804,"Hứa Du","Tào Tháo","Ta ở bên Viên Thiệu, nói gì hắn cũng không nghe. Ta đến đây mong người thu nhận.","Ta ở bên Viên Thiệu, nói gì hắn cũng không nghe. Ta đến đây mong người thu nhận.",},
        [3392] = {5804,"Tào Tháo","Hứa Du","Tử Viễn đến đây, việc ta ắt thành! Mong người hãy hiến kế phá Thiệu.","Tử Viễn đến đây, việc ta ắt thành! Mong người hãy hiến kế phá Thiệu.",},
        [3393] = {5804,"Hứa Du","Tào Tháo","Lương thảo của ngài đủ dùng trong bao lâu?","Lương thảo của ngài đủ dùng trong bao lâu?",},
        [3394] = {5804,"Tào Tháo","Hứa Du","Một năm.","Một năm.",},
        [3395] = {5804,"Hứa Du","Tào Tháo","Ta có thành ý đến giúp mà ngài lại che giấu, ta cũng không biết phải làm sao!","Ta có thành ý đến giúp mà ngài lại che giấu, ta cũng không biết phải làm sao!",},
        [3396] = {5804,"Hứa Du","Tào Tháo","Đừng giấu ta! Lương thảo đã hết rồi đúng không?","Đừng giấu ta! Lương thảo đã hết rồi đúng không?",},
        [3397] = {5804,"Tào Tháo","Hứa Du","Đúng là không thể qua mắt được Tử Viễn, mong người hãy nghĩ tình xưa mà hiến kế.","Đúng là không thể qua mắt được Tử Viễn, mong người hãy nghĩ tình xưa mà hiến kế.",},
        [3398] = {5804,"Hứa Du","Tào Tháo","Ta có một kế, trong vòng ba ngày, quân lính Viên Thiệu không chiến tự bại. Chúa công có muốn nghe không?","Ta có một kế, trong vòng ba ngày, quân lính Viên Thiệu không chiến tự bại. Chúa công có muốn nghe không?",},
        [3399] = {5804,"Tào Tháo","Hứa Du","Xin hãy nói.","Xin hãy nói.",},
        [3400] = {5804,"Hứa Du","Tào Tháo","Lương thảo của Viên Thiệu đều ở Ô Sào, hắn để Thuần Vu Quỳnh trấn giữ, nhưng Quỳnh lại rất thích uống rượu.","Lương thảo của Viên Thiệu đều ở Ô Sào, hắn để Thuần Vu Quỳnh trấn giữ, nhưng Quỳnh lại rất thích uống rượu.",},
        [3401] = {5804,"Hứa Du","Tào Tháo","Ngài có thể chọn tinh binh giả làm lính Viên Thiệu đến đó bảo vệ lương thảo, rồi thừa cơ phóng hỏa, trong vòng ba ngày quân Thiệu ắt sẽ đại loạn.","Ngài có thể chọn tinh binh giả làm lính Viên Thiệu đến đó bảo vệ lương thảo, rồi thừa cơ phóng hỏa, trong vòng ba ngày quân Thiệu ắt sẽ đại loạn.",},
        [3402] = {5805,"Trương Liêu","Tào Tháo","Nơi Viên Thiệu để lương thảo? Thừa tướng đừng vội tin, e rằng Hứa Du đang nói dối.","Nơi Viên Thiệu để lương thảo? Thừa tướng đừng vội tin, e rằng Hứa Du đang nói dối.",},
        [3403] = {5805,"Tào Tháo","Trương Liêu","Không đâu, Hứa Du đến đây, trời diệt Viên Thiệu.","Không đâu, Hứa Du đến đây, trời diệt Viên Thiệu.",},
        [3404] = {5805,"Tào Tháo","Trương Liêu","Nay lương thảo quân ta đã hết, khó mà kéo dài. Nếu không dùng kế của Hứa Du, cũng chỉ biết ngồi chờ.","Nay lương thảo quân ta đã hết, khó mà kéo dài. Nếu không dùng kế của Hứa Du, cũng chỉ biết ngồi chờ.",},
        [3405] = {5805,"Tào Tháo","Trương Liêu","Nếu sợ có âm mưu thì có thể giữ ta lại ở đây. Việc cướp lương là diệu kế, mong chúa công đừng do dự.","Nếu sợ có âm mưu thì có thể giữ ta lại ở đây. Việc cướp lương là diệu kế, mong chúa công đừng do dự.",},
        [3406] = {5805,"Trương Liêu","Tào Tháo","Cũng phải đề phòng Viên Thiệu thừa cơ tập kích.","Cũng phải đề phòng Viên Thiệu thừa cơ tập kích.",},
        [3407] = {5805,"Tào Tháo","Trương Liêu","Ta đã trù tính cả rồi.","Ta đã trù tính cả rồi.",},
        [3408] = {5805,"Tào Tháo","Trương Liêu","Tuân Du, Giả Hủ, Tào Hồng cùng Hứa Du trấn giữ ở trại.","Tuân Du, Giả Hủ, Tào Hồng cùng Hứa Du trấn giữ ở trại.",},
        [3409] = {5805,"Tào Tháo","Trương Liêu","Hạ Hầu Đôn, Hạ Hầu Uyên dẫn quân mai phục ở bên trái, Tào Nhân, Lý Điển dẫn quân mai phục bên phải.","Hạ Hầu Đôn, Hạ Hầu Uyên dẫn quân mai phục ở bên trái, Tào Nhân, Lý Điển dẫn quân mai phục bên phải.",},
        [3410] = {5805,"Tào Tháo","Trương Liêu","Trương Liêu, Hứa Chử ở trước, Từ Hoảng, Vu Cấm ở sau, ta và Dương Uy Tướng ở giữa: tổng cộng năm ngàn binh mã.","Trương Liêu, Hứa Chử ở trước, Từ Hoảng, Vu Cấm ở sau, ta và Dương Uy Tướng ở giữa: tổng cộng năm ngàn binh mã.",},
        [3411] = {5805,"Tào Tháo","Trương Liêu","Dựng cờ của quân Viên, lúc hoàng hôn sẽ tiến về Ô Sào.","Dựng cờ của quân Viên, lúc hoàng hôn sẽ tiến về Ô Sào.",},
        [3412] = {5806,"Viên Thiệu","","Tại sao lại ồn ào vậy?","Tại sao lại ồn ào vậy?",},
        [3413] = {5806,"Trương Hợp","Viên Thiệu","Tào Tháo tập kích, Ô Sào đang cháy!","Tào Tháo tập kích, Ô Sào đang cháy!",},
        [3414] = {5806,"Viên Thiệu","Trương Hợp","Gì!","Gì!",},
        [3415] = {5901,"Trương Hợp","","Ta nguyện cùng Cao Lãm đi cứu Ô Sào.","Ta nguyện cùng Cao Lãm đi cứu Ô Sào.",},
        [3416] = {5901,"Quách Đồ","Trương Hợp","Không được! Quân Tào cướp lương, Tào Tháo chắc chắn sẽ đích thân đến đó. Tháo đã đi, vậy trong doanh trại chắc chắn trống không.","Không được! Quân Tào cướp lương, Tào Tháo chắc chắn sẽ đích thân đến đó. Tháo đã đi, vậy trong doanh trại chắc chắn trống không.",},
        [3417] = {5901,"Quách Đồ","Trương Hợp","Chúng ta có thể tấn công doanh trại Tào Tháo trước, Tào tháo nghe tin chắc chắn sẽ trở về.","Chúng ta có thể tấn công doanh trại Tào Tháo trước, Tào tháo nghe tin chắc chắn sẽ trở về.",},
        [3418] = {5901,"Trương Hợp","Quách Đồ","Không được! Tào Tháo đa mưu, nếu đã ra ngoài chắc chắn đã cho người canh giữ cẩn mật.","Không được! Tào Tháo đa mưu, nếu đã ra ngoài chắc chắn đã cho người canh giữ cẩn mật.",},
        [3419] = {5901,"Trương Hợp","Quách Đồ","Nếu tấn công Tào Tháo mà không thành công ắt sẽ gây họa lớn.","Nếu tấn công Tào Tháo mà không thành công ắt sẽ gây họa lớn.",},
        [3420] = {5901,"Quách Đồ","Trương Hợp","Tào Tháo chỉ lo cướp lương, sao có thể cho quân phòng thủ được!","Tào Tháo chỉ lo cướp lương, sao có thể cho quân phòng thủ được!",},
        [3421] = {5901,"Viên Thiệu","Quách Đồ","Hai vị đừng tranh cãi nữa, Trương Hợp, Cao Lãm đưa 5000 quân đến Quan Độ tấn công Tào doanh, Tưởng Kỳ đưa 1 vạn quân đi cứu Ô Sào.","Hai vị đừng tranh cãi nữa, Trương Hợp, Cao Lãm đưa 5000 quân đến Quan Độ tấn công Tào doanh, Tưởng Kỳ đưa 1 vạn quân đi cứu Ô Sào.",},
        [3422] = {5902,"Viên Thiệu","Quách Đồ","Sao lại để mất Ô Sào? Đã điều tra kỹ chưa?","Sao lại để mất Ô Sào? Đã điều tra kỹ chưa?",},
        [3423] = {5902,"Quách Đồ","Viên Thiệu","Thuần Vu Quỳnh say rượu nên không thể kháng địch.","Thuần Vu Quỳnh say rượu nên không thể kháng địch.",},
        [3424] = {5902,"Viên Thiệu","Quách Đồ","Trương Hợp, Cao Lãm đi tập kích Tào doanh sao vẫn chưa về?","Trương Hợp, Cao Lãm đi tập kích Tào doanh sao vẫn chưa về?",},
        [3425] = {5902,"Quách Đồ","Viên Thiệu","Trương Hợp, Cao Lãm thấy ngài thua trận, trong lòng vui mừng. (Quách Đồ nói.)","Trương Hợp, Cao Lãm thấy ngài thua trận, trong lòng vui mừng. (Quách Đồ nói.)",},
        [3426] = {5902,"Viên Thiệu","Quách Đồ","Sao lại nói thế?","Sao lại nói thế?",},
        [3427] = {5902,"Quách Đồ","Viên Thiệu","Hai người họ sớm có ý hàng Tào, nay sai đi tập kích Tào doanh, cố tình để thua để ta hao binh tổn tướng.","Hai người họ sớm có ý hàng Tào, nay sai đi tập kích Tào doanh, cố tình để thua để ta hao binh tổn tướng.",},
        [3428] = {5902,"Viên Thiệu","Quách Đồ","Sao? Mau gọi Trương Hợp, Cao Lãm về đây!","Sao? Mau gọi Trương Hợp, Cao Lãm về đây!",},
        [3429] = {5903,"Cao Lãm","Trương Hợp","Quách Đồ có âm mưu xấu xa.","Quách Đồ có âm mưu xấu xa.",},
        [3430] = {5903,"Cao Lãm","Trương Hợp","Viên Thiệu nghe lời xàm tấu chắc chắc sẽ bị Tào Tháo bắt; sao chúng ta phải khoanh tay ngồi chờ? Hay là đầu hàng Tào Tháo?","Viên Thiệu nghe lời xàm tấu chắc chắc sẽ bị Tào Tháo bắt; sao chúng ta phải khoanh tay ngồi chờ? Hay là đầu hàng Tào Tháo?",},
        [3431] = {5903,"Trương Hợp","Cao Lãm","Ta cũng nghĩ vậy.","Ta cũng nghĩ vậy.",},
        [3432] = {5904,"Hạ Hầu Đôn","Tào Tháo","Trương Hợp, Cao Lãm hai người đến hàng, không biết thật giả.","Trương Hợp, Cao Lãm hai người đến hàng, không biết thật giả.",},
        [3433] = {5904,"Tào Tháo","Hạ Hầu Đôn","Ta đối xử tốt với họ, dù thế nào thì họ cũng sẽ theo ta.","Ta đối xử tốt với họ, dù thế nào thì họ cũng sẽ theo ta.",},
        [3434] = {5904,"Tào Tháo","Hạ Hầu Đôn","Mau mở cửa cho hai người vào.","Mau mở cửa cho hai người vào.",},
        [3435] = {5905,"Trương Hợp","Tào Tháo","Viên Thiệu ngu muội, bọn ta nguyện theo thừa tướng.","Viên Thiệu ngu muội, bọn ta nguyện theo thừa tướng.",},
        [3436] = {5905,"Tào Tháo","Trương Hợp","Nếu Viên Thiệu chịu nghe lời hai vị tướng quân, chưa chắc sẽ bại.","Nếu Viên Thiệu chịu nghe lời hai vị tướng quân, chưa chắc sẽ bại.",},
        [3437] = {5905,"Tào Tháo","Trương Hợp","Nay hai vị tướng quân đến đây, như Vi Tử đến Ân, Hán Tín về Hán.","Nay hai vị tướng quân đến đây, như Vi Tử đến Ân, Hán Tín về Hán.",},
