        [3439] = {6001,"Nhân vật","Tào Tháo","Trận chiến Quan Độ, Viên Thiệu không phục!","Trận chiến Quan Độ, Viên Thiệu không phục!",},
        [3440] = {6001,"Nhân vật","Tào Tháo","Tập hợp binh lính Tứ Châu, được khoảng hai ba chục vạn, đến Thương Đình đóng quân.","Tập hợp binh lính Tứ Châu, được khoảng hai ba chục vạn, đến Thương Đình đóng quân.",},
        [3441] = {6001,"Tào Tháo","Nhân vật","Mau lệnh ba quân đến Thương Đình.","Mau lệnh ba quân đến Thương Đình.",},
        [3442] = {6002,"Tào Tháo","","Viên Thiệu quân đông, tướng giỏi ngựa khỏe, Viên Thượng chiến ba lượt đã thắng Sử Hoán.","Viên Thiệu quân đông, tướng giỏi ngựa khỏe, Viên Thượng chiến ba lượt đã thắng Sử Hoán.",},
        [3443] = {6002,"Tào Tháo","","Các vị có kế gì phá Thiệu?","Các vị có kế gì phá Thiệu?",},
        [3444] = {6002,"Trình Dục","Tào Tháo","Ta có một kế có thể phá Viên Thiệu, gọi là Thập Diện Mai Phục.","Ta có một kế có thể phá Viên Thiệu, gọi là Thập Diện Mai Phục.",},
        [3445] = {6002,"Tào Tháo","Trình Dục","Thế nào là thập diện mai phục?","Thế nào là thập diện mai phục?",},
        [3446] = {6002,"Trình Dục","Tào Tháo","Hãy lui binh đến gần sông, mai phục 10 đội quân.","Hãy lui binh đến gần sông, mai phục 10 đội quân.",},
        [3447] = {6002,"Trình Dục","Tào Tháo","Bên trái đội quân một Hạ Hầu Đôn, đội quân hai Trương Liêu, đội quân ba Lý Điển, đội quân bốn Nhạc Tiến, đội quân năm Hạ Hầu Uyên.","Bên trái đội quân một Hạ Hầu Đôn, đội quân hai Trương Liêu, đội quân ba Lý Điển, đội quân bốn Nhạc Tiến, đội quân năm Hạ Hầu Uyên.",},
        [3448] = {6002,"Trình Dục","Tào Tháo","Bên phải đội quân một Tào Hồng, đội quân hai Trương Hợp, đội quân ba Từ Hoảng, đội quân bốn Vu Cấm, đội quân năm Cao Lãm.","Bên phải đội quân một Tào Hồng, đội quân hai Trương Hợp, đội quân ba Từ Hoảng, đội quân bốn Vu Cấm, đội quân năm Cao Lãm.",},
        [3449] = {6002,"Trình Dục","Tào Tháo","Ngày mai, Hứa Chử và Dương Uy Tướng làm tiên phong trung quân, giả đi cướp trại, dụ Viên Thiệu đuổi đến sông.","Ngày mai, Hứa Chử và Dương Uy Tướng làm tiên phong trung quân, giả đi cướp trại, dụ Viên Thiệu đuổi đến sông.",},
        [3450] = {6002,"Trình Dục","Tào Tháo","Trung quân quay đầu lại tấn công, phục binh phối hợp tấn công, Viên Thiệu chắc bại.","Trung quân quay đầu lại tấn công, phục binh phối hợp tấn công, Viên Thiệu chắc bại.",},
        [3451] = {6002,"Nhân vật","Trình Dục","Kế thập diện mai phục của Trọng Đức quả nhiên rất hay!","Kế thập diện mai phục của Trọng Đức quả nhiên rất hay!",},
        [3452] = {6002,"Tào Tháo","Nhân vật","Được, cứ làm theo kế này.","Được, cứ làm theo kế này.",},
        [3453] = {6101,"Tào Tháo","Trình Dục","Thương Đình Chiến thắng lợi, nghe lời Trọng Đức, nuôi quân vài năm, khi đó mới phạt Thiệu.","Thương Đình Chiến thắng lợi, nghe lời Trọng Đức, nuôi quân vài năm, khi đó mới phạt Thiệu.",},
        [3454] = {6101,"Tào Tháo","Trình Dục","Hạ Hầu Đôn, Mãn Sủng trấn giữ Nhữ Ân đề phòng Lưu Biểu. Tào Nhân, Tuân Úc trấn giữ Hứa Đô, ta đích thân thống lĩnh đại quân ở lại Quan Độ.","Hạ Hầu Đôn, Mãn Sủng trấn giữ Nhữ Ân đề phòng Lưu Biểu. Tào Nhân, Tuân Úc trấn giữ Hứa Đô, ta đích thân thống lĩnh đại quân ở lại Quan Độ.",},
        [3455] = {6101,"Trình Dục","Tào Tháo","Trước phá Viên Thiệu, sau đoạt Kinh Tương, nam bắc đều có thu hoạch.","Trước phá Viên Thiệu, sau đoạt Kinh Tương, nam bắc đều có thu hoạch.",},
        [3456] = {6102,"Viên Thiệu","Thẩm Phối","Đã mấy năm rồi, ta muốn tấn công Hứa Đô.","Đã mấy năm rồi, ta muốn tấn công Hứa Đô.",},
        [3457] = {6102,"Thẩm Phối","Viên Thiệu","Thất bại ở Quan Độ, Thương Đình, lòng quân chưa ổn, giơ nên đào kênh đắp lũy, nuôi dưỡng binh lực.","Thất bại ở Quan Độ, Thương Đình, lòng quân chưa ổn, giơ nên đào kênh đắp lũy, nuôi dưỡng binh lực.",},
        [3458] = {6102,"Quách Đồ","Viên Thiệu","Báo! Tào Tháo tiến quân Quan Độ, tấn công Ký Châu.","Báo! Tào Tháo tiến quân Quan Độ, tấn công Ký Châu.",},
        [3459] = {6102,"Viên Thiệu","Quách Đồ","Nếu đợi kẻ địch đến thì đã trễ. Ta phải thống lĩnh đại quân đi nghênh chiến.","Nếu đợi kẻ địch đến thì đã trễ. Ta phải thống lĩnh đại quân đi nghênh chiến.",},
        [3460] = {6102,"Viên Thượng","Viên Thiệu","Cha vẫn còn chưa khỏe, không thể viễn chinh. Con nguyện đưa quân nghênh địch.","Cha vẫn còn chưa khỏe, không thể viễn chinh. Con nguyện đưa quân nghênh địch.",},
        [3461] = {6102,"Viên Thiệu","Viên Thượng","Được!","Được!",},
        [3462] = {6102,"Viên Thiệu","Viên Thượng","Mau cho người đến Thanh Châu gọi Viên Đàm, U Châu gọi Viên Hi, Tịnh Châu gọi Cao Can, bốn đường cùng phá Tào Tháo.","Mau cho người đến Thanh Châu gọi Viên Đàm, U Châu gọi Viên Hi, Tịnh Châu gọi Cao Can, bốn đường cùng phá Tào Tháo.",},
