        [3464] = {6103,"Viên Thượng","","Ta dễ dàng trảm Sử Hoán, các ngươi ai dám ra nghênh chiến?","Ta dễ dàng trảm Sử Hoán, các ngươi ai dám ra nghênh chiến?",},
