        [3478] = {6201,"Xuân Hoa","Tư Mã Ý","Tào Tháo nghe nói ngươi tài giỏi hơn người nên phái người gọi ngươi đến phủ nhậm chức.","Tào Tháo nghe nói ngươi tài giỏi hơn người nên phái người gọi ngươi đến phủ nhậm chức.",},
