        [3510] = {6401,"Tào Tháo","Quách Gia","Theo binh đến Ký Châu, nhưng Viên Đàm và Viên Thượng vào thành cố thủ, Viên Hi và Cao Can rời thành 30 dặm đóng trại, tấn công liên tiếp không được.","Theo binh đến Ký Châu, nhưng Viên Đàm và Viên Thượng vào thành cố thủ, Viên Hi và Cao Can rời thành 30 dặm đóng trại, tấn công liên tiếp không được.",},
        [3511] = {6401,"Tào Tháo","Quách Gia","Phải làm sao đây?","Phải làm sao đây?",},
        [3512] = {6401,"Quách Gia","Tào Tháo","Viên thị phế trưởng lập ấu, giữa huynh đệ thì quyền lực ngang nhau, tự lập bè cánh, lúc nguy cấp thì giúp đỡ nhau, lúc yên ổn thì tranh đấu nhau.","Viên thị phế trưởng lập ấu, giữa huynh đệ thì quyền lực ngang nhau, tự lập bè cánh, lúc nguy cấp thì giúp đỡ nhau, lúc yên ổn thì tranh đấu nhau.",},
        [3513] = {6401,"Quách Gia","Tào Tháo","Hay là dẫn binh tiến về Kinh Châu, chinh phạt Lưu Biểu, sau này huynh đệ Viên Thị có biến, lúc đó tấn công họ, có thể đạt mục đích.","Hay là dẫn binh tiến về Kinh Châu, chinh phạt Lưu Biểu, sau này huynh đệ Viên Thị có biến, lúc đó tấn công họ, có thể đạt mục đích.",},
        [3514] = {6401,"Tào Tháo","Quách Gia","Phụng Hiếu nói phải!","Phụng Hiếu nói phải!",},
        [3515] = {6401,"Tào Tháo","Quách Gia","Lệnh Giả Hủ làm Thái Thú, thủ Lê Dương, Tào Hồng dẫn binh thủ Quan Độ, dẫn đại quân tiến về Kinh Châu.","Lệnh Giả Hủ làm Thái Thú, thủ Lê Dương, Tào Hồng dẫn binh thủ Quan Độ, dẫn đại quân tiến về Kinh Châu.",},
        [3516] = {6402,"Viên Đàm","Quách Đồ","Ta là con cả lại không được kế thừa nghiệp cha; con của mẹ kế lại được ban chức tước, thực sự không cam tâm.","Ta là con cả lại không được kế thừa nghiệp cha; con của mẹ kế lại được ban chức tước, thực sự không cam tâm.",},
        [3517] = {6402,"Quách Đồ","Viên Đàm","Chúa công có thể đóng quân ngoài thành, chỉ mời Hiển Phủ, Thẩm Phối uống rượu, Đao Phủ Thủ mai phục tiêu diệt, đại sự được định.","Chúa công có thể đóng quân ngoài thành, chỉ mời Hiển Phủ, Thẩm Phối uống rượu, Đao Phủ Thủ mai phục tiêu diệt, đại sự được định.",},
        [3518] = {6403,"Thẩm Phối","Viên Thượng","Viên Đàm mời gặp riêng ngươi riêng, chắc chắn là kế của Quách Đồ. Chúa công nếu đi chắc chắn trúng gian kế, hay là thừa thế tấn công","Viên Đàm mời gặp riêng ngươi riêng, chắc chắn là kế của Quách Đồ. Chúa công nếu đi chắc chắn trúng gian kế, hay là thừa thế tấn công",},
        [3519] = {6403,"Viên Thượng","Thẩm Phối","Ta dẫn binh đến ngay! (Viên Thượng dẫn năm vạn binh ra thành.)","Ta dẫn binh đến ngay! (Viên Thượng dẫn năm vạn binh ra thành.)",},
        [3520] = {6403,"Viên Đàm","Viên Thượng","Vì đâu mà ngươi dẫn binh đến?","Vì đâu mà ngươi dẫn binh đến?",},
        [3521] = {6403,"Viên Thượng","Viên Đàm","Viên Thiệu lập ta làm con kế vị, có phải ngươi muốn tạo phản?","Viên Thiệu lập ta làm con kế vị, có phải ngươi muốn tạo phản?",},
        [3522] = {6403,"Viên Đàm","Viên Thượng","Ngươi hạ độc phụ thân chiếm đoạt tước vị, nay lại đến sát hại anh ruột!","Ngươi hạ độc phụ thân chiếm đoạt tước vị, nay lại đến sát hại anh ruột!",},
        [3523] = {6404,"Quách Gia","Tào Tháo","Viên Đàm và Lữ Khoáng, Lữ Tường đến đầu hàng.","Viên Đàm và Lữ Khoáng, Lữ Tường đến đầu hàng.",},
        [3524] = {6404,"Tào Tháo","Quách Gia","Ta đã liệu trước rồi.","Ta đã liệu trước rồi.",},
        [3525] = {6404,"Tào Tháo","Quách Gia","Nhưng Viên Đàm khắc 2 Ấn Tướng Quân, tặng cho Nhị Lữ, lệnh làm nội ứng, muốn ngày sau tạo phản.","Nhưng Viên Đàm khắc 2 Ấn Tướng Quân, tặng cho Nhị Lữ, lệnh làm nội ứng, muốn ngày sau tạo phản.",},
        [3526] = {6404,"Tào Tháo","Quách Gia","Nào ngờ Nhị Lữ bẩm báo sự tình với ta, chờ ta phá Viên Thượng xong chắc chắn sẽ trảm Viên Đàm.","Nào ngờ Nhị Lữ bẩm báo sự tình với ta, chờ ta phá Viên Thượng xong chắc chắn sẽ trảm Viên Đàm.",},
        [3527] = {6404,"Quách Gia","Tào Tháo","Thừa tướng anh minh","Thừa tướng anh minh",},
        [3528] = {6405,"Tào Tháo","","Ta theo kế của Phùng Lễ, lệnh 300 tráng sĩ đào đường hầm vào thành.","Ta theo kế của Phùng Lễ, lệnh 300 tráng sĩ đào đường hầm vào thành.",},
        [3529] = {6405,"Tào Tháo","","Không ngờ sự tình bại lộ, tinh binh Thẩm Phối vận chuyển đá đến tập kích ở miệng cống, Phùng Lễ và 300 tráng sĩ đều đã hy sinh.","Không ngờ sự tình bại lộ, tinh binh Thẩm Phối vận chuyển đá đến tập kích ở miệng cống, Phùng Lễ và 300 tráng sĩ đều đã hy sinh.",},
        [3530] = {6405,"Tào Tháo","","Còn có kế nào không?","Còn có kế nào không?",},
        [3531] = {6405,"Hứa Du","Tào Tháo","Ta có một kế, sao không dùng nước Chương Hà để dìm chúng?","Ta có một kế, sao không dùng nước Chương Hà để dìm chúng?",},
        [3532] = {6405,"Tào Tháo","Hứa Du","Xem ra khả thi, vậy làm theo kế của Tử Viễn","Xem ra khả thi, vậy làm theo kế của Tử Viễn",},
        [3533] = {6406,"Viên Thượng","Thẩm Phối","Dẫn quân đào rãnh ở ngoại thành!","Dẫn quân đào rãnh ở ngoại thành!",},
        [3534] = {6406,"Thẩm Phối","Viên Thượng","Chờ ta xem nào.","Chờ ta xem nào.",},
        [3535] = {6406,"Thẩm Phối","Viên Thượng","Làm vậy để lấy nước Chương Hà tràn vào thành. Rãnh sâu mới có thể tràn, nông như vậy thì có tác dụng gì!","Làm vậy để lấy nước Chương Hà tràn vào thành. Rãnh sâu mới có thể tràn, nông như vậy thì có tác dụng gì!",},
        [3536] = {6407,"Hứa Du","Tào Tháo","Thẩm Phối sơ ý, quân ta trong đêm đã phái thêm lượng binh sĩ gấp 10 lần cùng đào, đợi đến trời sáng sẽ đào sâu hai trượng.","Thẩm Phối sơ ý, quân ta trong đêm đã phái thêm lượng binh sĩ gấp 10 lần cùng đào, đợi đến trời sáng sẽ đào sâu hai trượng.",},
        [3537] = {6407,"Hứa Du","Tào Tháo","Dẫn nước Chương Hà vào, trong thành nước ngập lại thiếu lương thực, quân sĩ sẽ chết đói.","Dẫn nước Chương Hà vào, trong thành nước ngập lại thiếu lương thực, quân sĩ sẽ chết đói.",},
        [3538] = {6407,"Tào Tháo","Hứa Du","Tốt! Tướng địch Thẩm Vinh chịu đầu hàng, mở cổng Tây cho binh ta vào.","Tốt! Tướng địch Thẩm Vinh chịu đầu hàng, mở cổng Tây cho binh ta vào.",},
        [3539] = {6407,"Tào Tháo","Hứa Du","Ra lệnh toàn quân, tấn công Ký Châu!","Ra lệnh toàn quân, tấn công Ký Châu!",},
        [3540] = {6408,"Tào Tháo","Thẩm Phối","Khanh trung thành với Viên thị, không thể không làm vậy. Nay chịu đầu hàng ta không?","Khanh trung thành với Viên thị, không thể không làm vậy. Nay chịu đầu hàng ta không?",},
        [3541] = {6408,"Thẩm Phối","Tào Tháo","Không hàng!","Không hàng!",},
        [3542] = {6408,"Thẩm Phối","Tào Tháo","Ta là người của Viên thị, không như lũ tặc a dua nịnh hót các ngươi! Muốn chém cứ chém!","Ta là người của Viên thị, không như lũ tặc a dua nịnh hót các ngươi! Muốn chém cứ chém!",},
        [3543] = {6408,"Tào Tháo","Thẩm Phối","Cổ hủ, trảm!","Cổ hủ, trảm!",},
        [3544] = {6501,"Tào Phi","Lưu Phu Nhân","Hai người phụ nữ đang khóc là ai? (Tào Phi vào Viên Phủ thấy hai người phụ nữ khóc bèn hỏi.)","Hai người phụ nữ đang khóc là ai? (Tào Phi vào Viên Phủ thấy hai người phụ nữ khóc bèn hỏi.)",},
        [3545] = {6501,"Lưu Phu Nhân","Tào Phi","Thiếp là Lưu Thị, thê tử Viên Tướng Quân.","Thiếp là Lưu Thị, thê tử Viên Tướng Quân.",},
        [3546] = {6501,"Tào Phi","Lưu Phu Nhân","Người phụ nữ này là ai?","Người phụ nữ này là ai?",},
        [3547] = {6501,"Lưu Phu Nhân","Tào Phi","Đây là Chân Thị, thê tử của con thứ Viên Hi. Do Viên Hi rời trấn, Chân Thị không muốn đi xa nên ở lại.","Đây là Chân Thị, thê tử của con thứ Viên Hi. Do Viên Hi rời trấn, Chân Thị không muốn đi xa nên ở lại.",},
        [3548] = {6501,"Tào Phi","Lưu Phu Nhân","Ta là con trai Tào Thừa Tướng, sẽ bảo vệ gia đình ngươi, đừng lo lắng.","Ta là con trai Tào Thừa Tướng, sẽ bảo vệ gia đình ngươi, đừng lo lắng.",},
        [3549] = {6502,"Hứa Du","Tào Tháo","Không có ta, sao ngươi có thể vào cổng này?","Không có ta, sao ngươi có thể vào cổng này?",},
        [3550] = {6502,"Tào Tháo","Hứa Du","Ha ha ha ha, nào, cùng vào Cổng Thành.","Ha ha ha ha, nào, cùng vào Cổng Thành.",},
        [3551] = {6502,"Tào Tháo","Hứa Du","Chúng ta đến Thiệu Phủ trước.","Chúng ta đến Thiệu Phủ trước.",},
        [3552] = {6502,"Tào Tháo","Hứa Du","Ai từng vào cổng này?","Ai từng vào cổng này?",},
        [3553] = {6502,"Từ Hoảng","Tào Tháo","Thế tử ở trong đó.","Thế tử ở trong đó.",},
        [3554] = {6502,"Tào Tháo","Từ Hoảng","Gọi hắn ra đây! Sao có thể tự ý vào trong!","Gọi hắn ra đây! Sao có thể tự ý vào trong!",},
        [3555] = {6502,"Tào Phi","","Chân Thị dung mạo như hoa, sắc đẹp khuynh quốc, ta muốn có nàng!","Chân Thị dung mạo như hoa, sắc đẹp khuynh quốc, ta muốn có nàng!",},
        [3556] = {6502,"Lưu Phu Nhân","Tào Tháo","Ngoài thế tử ra không ai bảo toàn được gia đình thiếp, hãy để Chân Thị làm thê tử thế tử.","Ngoài thế tử ra không ai bảo toàn được gia đình thiếp, hãy để Chân Thị làm thê tử thế tử.",},
        [3557] = {6502,"Tào Tháo","Lưu Phu Nhân","Được! Ta cho phép.","Được! Ta cho phép.",},
        [3558] = {6503,"Tào Tháo","","Ngày nào ta và Bản Sơ kết giao huynh đệ, nay y đã qua đời, ta không thể không rơi lệ!","Ngày nào ta và Bản Sơ kết giao huynh đệ, nay y đã qua đời, ta không thể không rơi lệ!",},
        [3559] = {6503,"Tào Tháo","","Ban lụa vàng lương thực cho Lưu Thị, nay cư dân Hà Bắc chịu khổ vì chiến tranh, miễn thuế năm nay.","Ban lụa vàng lương thực cho Lưu Thị, nay cư dân Hà Bắc chịu khổ vì chiến tranh, miễn thuế năm nay.",},
        [3560] = {6504,"Hứa Du","Hứa Chử","Các ngươi không có ta, sao có thể ra vào cổng này?","Các ngươi không có ta, sao có thể ra vào cổng này?",},
        [3561] = {6504,"Hứa Chử","Hứa Du","Chúng ta trải qua trận chiến sinh tử đoạt được thành trì, sao ngươi dám khoác lác!","Chúng ta trải qua trận chiến sinh tử đoạt được thành trì, sao ngươi dám khoác lác!",},
        [3562] = {6504,"Hứa Du","Hứa Chử","Các ngươi đều là thất phu, không đáng quan tâm!","Các ngươi đều là thất phu, không đáng quan tâm!",},
        [3563] = {6504,"Hứa Chử","Hứa Du","Vô lễ, xem ra ngươi chán sống rồi!","Vô lễ, xem ra ngươi chán sống rồi!",},
        [3564] = {6505,"Hứa Chử","Tào Tháo","Hứa Du vô lễ, ta đã trừng trị hắn. (Trình thủ cấp Hứa Du.)","Hứa Du vô lễ, ta đã trừng trị hắn. (Trình thủ cấp Hứa Du.)",},
        [3565] = {6505,"Tào Tháo","Hứa Chử","Tử Viễn và ta vốn quen biết, sao người dám xử quyết hắn!","Tử Viễn và ta vốn quen biết, sao người dám xử quyết hắn!",},
        [3566] = {6505,"Hứa Chử","Tào Tháo","Kẻ ngông cuồng vô lễ, ta thấy quá chướng mắt nên đã trừng trị hắn!","Kẻ ngông cuồng vô lễ, ta thấy quá chướng mắt nên đã trừng trị hắn!",},
        [3567] = {6505,"Tào Tháo","Hứa Chử","Lỗ mãng lỗ mãng! Ta lệnh ngươi đích thân an táng long trọng Hứa Du.","Lỗ mãng lỗ mãng! Ta lệnh ngươi đích thân an táng long trọng Hứa Du.",},
        [3568] = {6601,"Tào Tháo","","Đã định Ký Châu, Viên Đàm, Viên Thượng, Viên Hi có tin tức gì không?","Đã định Ký Châu, Viên Đàm, Viên Thượng, Viên Hi có tin tức gì không?",},
        [3569] = {6601,"Nhân vật","Tào Tháo","Không có lòng chiến đấu, trốn đến U Châu tìm Viên Hi.","Không có lòng chiến đấu, trốn đến U Châu tìm Viên Hi.",},
        [3570] = {6601,"Nhân vật","Tào Tháo","Viên Đàm dẫn binh cướp Cam Ninh, An Bình, Hà Giản... muốn lấy lại Ký Châu.","Viên Đàm dẫn binh cướp Cam Ninh, An Bình, Hà Giản... muốn lấy lại Ký Châu.",},
        [3571] = {6601,"Tào Tháo","Nhân vật","Lệnh đại quân chinh phạt!","Lệnh đại quân chinh phạt!",},
        [3572] = {6602,"Nhân vật","Tào Tháo","Viên Đàm viết thư cầu Lưu Biểu tăng viện trợ, Lưu Biểu không có ý ra binh, tự biết không địch lại chúng ta, chạy đi giữ Nam Bì.","Viên Đàm viết thư cầu Lưu Biểu tăng viện trợ, Lưu Biểu không có ý ra binh, tự biết không địch lại chúng ta, chạy đi giữ Nam Bì.",},
        [3573] = {6602,"Tào Tháo","Nhân vật","Đuổi!","Đuổi!",},
        [3574] = {6603,"Nhân vật","Tào Tháo","Ta và Tào Hồng Phấn Uy đột trận, thì gặp Viên Đàm, vung đao loạn trảm, Đàm đã mất mạng trong chiến loạn,","Ta và Tào Hồng Phấn Uy đột trận, thì gặp Viên Đàm, vung đao loạn trảm, Đàm đã mất mạng trong chiến loạn,",},
        [3575] = {6603,"Nhân vật","Tào Tháo","Quách Đồ thấy đại loạn vội vào thành, ta nhặt lấy cung tiễn, bắn hạ thành hào, người ngựa đều gục.","Quách Đồ thấy đại loạn vội vào thành, ta nhặt lấy cung tiễn, bắn hạ thành hào, người ngựa đều gục.",},
        [3576] = {6603,"Tào Tháo","Nhân vật","Hay! Dương Uy Tướng thần võ!","Hay! Dương Uy Tướng thần võ!",},
        [3577] = {6603,"Tào Tháo","Nhân vật","Viên Đàm đã hạ, tiếp tục chinh phạt Viên Thượng Viên Hi!","Viên Đàm đã hạ, tiếp tục chinh phạt Viên Thượng Viên Hi!",},
        [3578] = {6604,"Tào Tháo","Quách Gia","Nay Viên Thượng đã theo Viên Hi, phải dùng kế sách gì?","Nay Viên Thượng đã theo Viên Hi, phải dùng kế sách gì?",},
        [3579] = {6604,"Quách Gia","Tào Tháo","Có thể khiến Viên Thị đầu hàng tướng Tiêu Xúc, Trương Nam sẽ tự tấn công.","Có thể khiến Viên Thị đầu hàng tướng Tiêu Xúc, Trương Nam sẽ tự tấn công.",},
        [3580] = {6604,"Tào Tháo","Quách Gia","Tiêu Xúc, Trương Nam, Lữ Khoáng, Lữ Tường, Mã Diên, lần lượt dẫn theo bộ binh, chia làm ba ngã tấn công U Châu.","Tiêu Xúc, Trương Nam, Lữ Khoáng, Lữ Tường, Mã Diên, lần lượt dẫn theo bộ binh, chia làm ba ngã tấn công U Châu.",},
        [3581] = {6604,"Tào Tháo","Quách Gia","Lý Điển, Nhạc Tiến tụ họp với Trương Yến, đánh Tịnh Châu, tấn công Cao Can.","Lý Điển, Nhạc Tiến tụ họp với Trương Yến, đánh Tịnh Châu, tấn công Cao Can.",},
        [3582] = {6605,"Tào Hồng","Tào Tháo","Cao Can không còn đường chạy, đành phải tìm đến Lưu Biểu, đi đến Thương Lạc, bị Đô Úy Vương Diễm hạ sát.","Cao Can không còn đường chạy, đành phải tìm đến Lưu Biểu, đi đến Thương Lạc, bị Đô Úy Vương Diễm hạ sát.",},
        [3583] = {6605,"Nhân vật","Tào Tháo","Viên Hi, Viên Thượng bại binh vong tướng, thế lực cùng tận, chạy đến sa mạc.","Viên Hi, Viên Thượng bại binh vong tướng, thế lực cùng tận, chạy đến sa mạc.",},
        [3584] = {6605,"Nhân vật","Tào Tháo","Ta dẫn binh tây kích, Lưu Bị, Lưu Biểu thừa cơ tấn công Hứa Đô, ta cứu ứng không kịp, gây họa không ít, hãy rút quân đừng vào.","Ta dẫn binh tây kích, Lưu Bị, Lưu Biểu thừa cơ tấn công Hứa Đô, ta cứu ứng không kịp, gây họa không ít, hãy rút quân đừng vào.",},
        [3585] = {6605,"Quách Gia","Tào Tháo","Dương Uy Tướng nói sai rồi. Chúa công tuy uy chấn thiên hạ, người ở sa mạc xa xôi tách biệt, chắc chắn không phòng bị.","Dương Uy Tướng nói sai rồi. Chúa công tuy uy chấn thiên hạ, người ở sa mạc xa xôi tách biệt, chắc chắn không phòng bị.",},
        [3586] = {6605,"Quách Gia","Tào Tháo","Nhân lúc không phòng bị, tấn công bất ngờ chắc chắn phá được. Viên Thiệu và Ô Hoàn có ơn, huynh đệ Thượng và Hi còn sống sót, không thể không diệt trừ.","Nhân lúc không phòng bị, tấn công bất ngờ chắc chắn phá được. Viên Thiệu và Ô Hoàn có ơn, huynh đệ Thượng và Hi còn sống sót, không thể không diệt trừ.",},
        [3587] = {6605,"Quách Gia","Tào Tháo","Lưu Biểu tự biết tài cán không đủ để quản Lưu Bị, việc quan trọng sợ không thể kiểm soát, việc nhẹ nhàng lại không thích hợp Lưu Bị làm.","Lưu Biểu tự biết tài cán không đủ để quản Lưu Bị, việc quan trọng sợ không thể kiểm soát, việc nhẹ nhàng lại không thích hợp Lưu Bị làm.",},
        [3588] = {6605,"Quách Gia","Tào Tháo","Tuy viễn chinh hư quốc, người cũng đừng lo lắng.","Tuy viễn chinh hư quốc, người cũng đừng lo lắng.",},
        [3589] = {6605,"Tào Tháo","Quách Gia","Phụng Hiếu nói rất phải. Lệnh tam quân lớn nhỏ, ngàn xe tiến lên.","Phụng Hiếu nói rất phải. Lệnh tam quân lớn nhỏ, ngàn xe tiến lên.",},
        [3590] = {6606,"Tào Tháo","Quách Gia","Vì ta muốn bình định sa mạc, khiến người đi xa vất vả bị nhiễm bệnh, ta thật áy náy! (Thấy Quách Gia không quen nơi lạ, bệnh nằm trên xe.)","Vì ta muốn bình định sa mạc, khiến người đi xa vất vả bị nhiễm bệnh, ta thật áy náy! (Thấy Quách Gia không quen nơi lạ, bệnh nằm trên xe.)",},
        [3591] = {6606,"Quách Gia","Tào Tháo","Cảm kích đại ơn Thừa Tướng, dù có mất mạng cũng không đủ báo đáp người.","Cảm kích đại ơn Thừa Tướng, dù có mất mạng cũng không đủ báo đáp người.",},
        [3592] = {6606,"Tào Tháo","Quách Gia","Ta thấy Bắc Địa hiểm trở, định rút quân về, thế nào?","Ta thấy Bắc Địa hiểm trở, định rút quân về, thế nào?",},
        [3593] = {6606,"Quách Gia","Tào Tháo","Thần tốc. Thiên lý tấn công, quân nhu nhiều nhưng khó xu lợi, hay là dẫn binh đi gấp, lợi dụng lúc hắn không phòng bị.","Thần tốc. Thiên lý tấn công, quân nhu nhiều nhưng khó xu lợi, hay là dẫn binh đi gấp, lợi dụng lúc hắn không phòng bị.",},
        [3594] = {6606,"Tào Tháo","Quách Gia","Ngươi ở Dị Châu dưỡng bệnh, ta tự nhờ đạo quan dẫn đường.","Ngươi ở Dị Châu dưỡng bệnh, ta tự nhờ đạo quan dẫn đường.",},
        [3595] = {6607,"Tào Tháo","","Phụng Hiếu qua đời, là trời muốn diệt ta rồi! (Chờ khi Tào Tháo đến Dị Châu, Quách Gia đã qua đời.)","Phụng Hiếu qua đời, là trời muốn diệt ta rồi! (Chờ khi Tào Tháo đến Dị Châu, Quách Gia đã qua đời.)",},
        [3596] = {6607,"Tào Tháo","","Tuổi tác chư quân đều ngang với ta, chỉ có Phụng Hiếu nhỏ tuổi nhất, định nhờ vả hậu sự. Nào ngờ đoản mệnh, ruột gan ta đau như cắt!","Tuổi tác chư quân đều ngang với ta, chỉ có Phụng Hiếu nhỏ tuổi nhất, định nhờ vả hậu sự. Nào ngờ đoản mệnh, ruột gan ta đau như cắt!",},
        [3597] = {6607,"Nhân vật","Tào Tháo","Trước khi Quách Công mất đã tự tay viết thư này, trong thư nói chuyện Liêu Đông đã định.","Trước khi Quách Công mất đã tự tay viết thư này, trong thư nói chuyện Liêu Đông đã định.",},
        [3598] = {6607,"Hạ Hầu Đôn","Nhân vật","Trong thư có phải viết là mau chinh phạt Liêu Đông?","Trong thư có phải viết là mau chinh phạt Liêu Đông?",},
        [3599] = {6607,"Nhân vật","Hạ Hầu Đôn","Không phải, vài ngày sau, Công Tôn Khang sẽ đem thủ cấp hai người họ Viên đến.","Không phải, vài ngày sau, Công Tôn Khang sẽ đem thủ cấp hai người họ Viên đến.",},
        [3600] = {6608,"Viên Thượng","Viên Hi","Quân binh Liêu Đông hàng vạn, đủ để giao đấu với Tào Tháo.","Quân binh Liêu Đông hàng vạn, đủ để giao đấu với Tào Tháo.",},
        [3601] = {6608,"Viên Thượng","Viên Hi","Nay tạm ở đây, sau này diệt Công Tôn Khang đoạt đất của hắn, dưỡng sức lực kháng Trung Nguyên, có thể khôi phục Hà Bắc.","Nay tạm ở đây, sau này diệt Công Tôn Khang đoạt đất của hắn, dưỡng sức lực kháng Trung Nguyên, có thể khôi phục Hà Bắc.",},
        [3602] = {6608,"Viên Hi","Viên Thượng","Ta cũng nghĩ như vậy!","Ta cũng nghĩ như vậy!",},
        [3603] = {6609,"Hạ Hầu Đôn","Tào Tháo","Liêu Đông Công Tôn Khang ở Quán Dịch trảm Viên Hi, Viên Thượng.","Liêu Đông Công Tôn Khang ở Quán Dịch trảm Viên Hi, Viên Thượng.",},
        [3604] = {6609,"Tào Tháo","Hạ Hầu Đôn","Không ngoài dự đoán của Phụng Hiếu!","Không ngoài dự đoán của Phụng Hiếu!",},
        [3605] = {6609,"Hạ Hầu Đôn","Tào Tháo","Thế nào là không ngoài dự đoán của Phụng Hiếu?","Thế nào là không ngoài dự đoán của Phụng Hiếu?",},
        [3606] = {6609,"Nhân vật","Hạ Hầu Đôn","Trong thư nói như sau.","Trong thư nói như sau.",},
        [3607] = {6609,"Nhân vật","Hạ Hầu Đôn","Nghe nói Viên Hi, Viên Thượng đến Liêu Đông, người không thể thêm binh.","Nghe nói Viên Hi, Viên Thượng đến Liêu Đông, người không thể thêm binh.",},
        [3608] = {6609,"Nhân vật","Hạ Hầu Đôn","Công Tôn Khang lo sợ Viên thị chiếm đoạt, Nhị Viên đến chắc chắn nghi ngờ. Nếu dùng binh tấn công, chắc chắn hợp sức nghênh địch, không thể gấp được.","Công Tôn Khang lo sợ Viên thị chiếm đoạt, Nhị Viên đến chắc chắn nghi ngờ. Nếu dùng binh tấn công, chắc chắn hợp sức nghênh địch, không thể gấp được.",},
        [3609] = {6609,"Nhân vật","Hạ Hầu Đôn","Nếu hoãn lại, Công Tôn Khang, Viên Thị chắc chắn tự đấu đá, ngư ông đắc lợi.","Nếu hoãn lại, Công Tôn Khang, Viên Thị chắc chắn tự đấu đá, ngư ông đắc lợi.",},
        [3610] = {6609,"Tào Tháo","Nhân vật","Trời sinh Quách Phụng Hiếu, hào kiệt anh dũng: Trong bụng chứa kinh sử, trong ngực giấu binh giáp!","Trời sinh Quách Phụng Hiếu, hào kiệt anh dũng: Trong bụng chứa kinh sử, trong ngực giấu binh giáp!",},
        [3611] = {6609,"Tào Tháo","Nhân vật","Đem linh cửu Quách Gia đến Hứa Đô mai táng long trọng!","Đem linh cửu Quách Gia đến Hứa Đô mai táng long trọng!",},
        [3612] = {6701,"Trình Dục","Tào Tháo","Phương Bắc đã định, nay trả Hứa Đô, có thể lập kế sách Giang Nam.","Phương Bắc đã định, nay trả Hứa Đô, có thể lập kế sách Giang Nam.",},
        [3613] = {6701,"Tào Tháo","Trình Dục","Ta có chí này đã từ lâu. Lời của chư quân rất hợp ý ta.","Ta có chí này đã từ lâu. Lời của chư quân rất hợp ý ta.",},
        [3614] = {6701,"Tào Tháo","Trình Dục","Phía Nam vượng khí sáng chói, e là chưa thể đoạt.","Phía Nam vượng khí sáng chói, e là chưa thể đoạt.",},
        [3615] = {6701,"Tuân Du","Tào Tháo","Với sự uy vũ của Thừa Tướng, sao có thể không phục!","Với sự uy vũ của Thừa Tướng, sao có thể không phục!",},
        [3616] = {6701,"Nhân vật","Tào Tháo","Nhìn kìa, phía trước có một tia sáng vàng từ dưới đất rọi lên.","Nhìn kìa, phía trước có một tia sáng vàng từ dưới đất rọi lên.",},
        [3617] = {6701,"Tuân Du","Tào Tháo","Chắc chắn có bảo vật bên dưới!","Chắc chắn có bảo vật bên dưới!",},
        [3618] = {6701,"Tào Tháo","Tuân Du","Đào theo ánh sáng!","Đào theo ánh sáng!",},
        [3619] = {6702,"Tào Tháo","Tuân Du","Đây là điềm gì? (Mọi người đào được một Đồng Tước ở nơi có ánh sáng vàng.)","Đây là điềm gì? (Mọi người đào được một Đồng Tước ở nơi có ánh sáng vàng.)",},
        [3620] = {6702,"Tuân Du","Tào Tháo","Khi xưa Thuấn mẫu mơ thấy Ngọc Tước bay vào lòng rồi hạ sinh Thuấn. Nay được Đồng Tước, là điềm lành.","Khi xưa Thuấn mẫu mơ thấy Ngọc Tước bay vào lòng rồi hạ sinh Thuấn. Nay được Đồng Tước, là điềm lành.",},
        [3621] = {6702,"Tào Tháo","Tuân Du","Dựng cao đài ăn mừng!","Dựng cao đài ăn mừng!",},
        [3622] = {6702,"Tào Thực","Tào Tháo","Nếu xây tầng đài, phải xây 3 tòa: Chính giữa cao nhất, tên Đồng Tước; một tòa bên trái, tên Ngọc Long; một tòa bên phải, tên Kim Phượng.","Nếu xây tầng đài, phải xây 3 tòa: Chính giữa cao nhất, tên Đồng Tước; một tòa bên trái, tên Ngọc Long; một tòa bên phải, tên Kim Phượng.",},
        [3623] = {6702,"Tào Thực","Tào Tháo","Xây hai cầu ngang không trung, hết sức hùng tráng.","Xây hai cầu ngang không trung, hết sức hùng tráng.",},
        [3624] = {6702,"Tào Tháo","Tào Thực","Con ta nói rất đúng. Một mai xây xong đài, chắc chắn sẽ rất thích thú.","Con ta nói rất đúng. Một mai xây xong đài, chắc chắn sẽ rất thích thú.",},
        [3625] = {6703,"Tào Tháo","","Ta chiếm được binh của Viên Thiệu, tổng cộng năm sáu chục vạn, khải hoàn về Hứa Đô.","Ta chiếm được binh của Viên Thiệu, tổng cộng năm sáu chục vạn, khải hoàn về Hứa Đô.",},
        [3626] = {6703,"Tào Tháo","","Muốn nam chinh Lưu Biểu, chư quân thấy thế nào?","Muốn nam chinh Lưu Biểu, chư quân thấy thế nào?",},
        [3627] = {6703,"Nhân vật","Tào Tháo","Đại quân vừa bắc chinh trở về, chưa thể hành động. Hãy chờ nửa năm, nghỉ ngơi dưỡng sức, có thể hạ Lưu Biểu, Tôn Quyền dễ dàng.","Đại quân vừa bắc chinh trở về, chưa thể hành động. Hãy chờ nửa năm, nghỉ ngơi dưỡng sức, có thể hạ Lưu Biểu, Tôn Quyền dễ dàng.",},
        [3628] = {6703,"Tào Tháo","Nhân vật","Dương Uy Tướng nói có lý, phân binh đồn điền, sau này điều dùng.","Dương Uy Tướng nói có lý, phân binh đồn điền, sau này điều dùng.",},
        [3629] = {6801,"Tào Tháo","","Gần đây ta thường nhớ đến Sái Trung Lang.","Gần đây ta thường nhớ đến Sái Trung Lang.",},
        [3630] = {6801,"Tào Tháo","","Và cũng nhớ đến Văn Cơ, người con gái học cao hiểu rộng, tinh thông âm luật của ông.","Và cũng nhớ đến Văn Cơ, người con gái học cao hiểu rộng, tinh thông âm luật của ông.",},
        [3631] = {6801,"Tào Tháo","","Nhiều năm không gặp, không biết Văn Cơ hiện giờ ở đâu?","Nhiều năm không gặp, không biết Văn Cơ hiện giờ ở đâu?",},
        [3632] = {6801,"Hạ Hầu Đôn","Tào Tháo","Thần sẽ giúp Thừa Tướng thăm dò tung tích cô ta.","Thần sẽ giúp Thừa Tướng thăm dò tung tích cô ta.",},
        [3633] = {6802,"Hạ Hầu Đôn","Tào Tháo","Đã thăm dò được tung tích Văn Cơ.","Đã thăm dò được tung tích Văn Cơ.",},
        [3634] = {6802,"Tào Tháo","Hạ Hầu Đôn","Mau nói ta nghe.","Mau nói ta nghe.",},
        [3635] = {6802,"Hạ Hầu Đôn","Tào Tháo","Năm xưa Sái Trung Lang Thái Ung bị Đổng Trác liên lụy bị trị tội, quân phiệt hỗn chiến.","Năm xưa Sái Trung Lang Thái Ung bị Đổng Trác liên lụy bị trị tội, quân phiệt hỗn chiến.",},
        [3636] = {6802,"Hạ Hầu Đôn","Tào Tháo","Binh lính Khương Hồ thừa cơ cướp đoạt, bắt cóc.","Binh lính Khương Hồ thừa cơ cướp đoạt, bắt cóc.",},
        [3637] = {6802,"Hạ Hầu Đôn","Tào Tháo","Thái Văn Cơ cùng rất nhiều phụ nữ khác bị bắt đến Hung Nô, sau đó gả cho Tả Hiền Vương.","Thái Văn Cơ cùng rất nhiều phụ nữ khác bị bắt đến Hung Nô, sau đó gả cho Tả Hiền Vương.",},
        [3638] = {6802,"Hạ Hầu Đôn","Tào Tháo","May mắn được sủng ái và sinh hai người con.","May mắn được sủng ái và sinh hai người con.",},
        [3639] = {6802,"Tào Tháo","Hạ Hầu Đôn","Cuối cùng cũng biết tung tích của cô ta, còn sống là tốt còn sống là tốt.","Cuối cùng cũng biết tung tích của cô ta, còn sống là tốt còn sống là tốt.",},
        [3640] = {6803,"Tào Tháo","","Năm xưa Thái Văn Cơ bị Hung Nô bắt, gả cho Tả Hiền Vương, ở ngoài biên giới 12 năm.","Năm xưa Thái Văn Cơ bị Hung Nô bắt, gả cho Tả Hiền Vương, ở ngoài biên giới 12 năm.",},
        [3641] = {6803,"Tào Tháo","","Ta muốn rước nàng về Hứa Đô, các ngươi có cách gì không?","Ta muốn rước nàng về Hứa Đô, các ngươi có cách gì không?",},
        [3642] = {6803,"Nhân vật","Tào Tháo","Năm xưa Viên đảng Cao Can tương cầu hắn không cứu giúp, chúng ta cũng không phạm đến đất của hắn, không có thù oán.","Năm xưa Viên đảng Cao Can tương cầu hắn không cứu giúp, chúng ta cũng không phạm đến đất của hắn, không có thù oán.",},
        [3643] = {6803,"Nhân vật","Tào Tháo","Ranh giới Thiền Vu đất đai cằn cỗi, sao không dùng ngàn lượng vàng, bạch ngọc trao đổi, ký kết liên minh.","Ranh giới Thiền Vu đất đai cằn cỗi, sao không dùng ngàn lượng vàng, bạch ngọc trao đổi, ký kết liên minh.",},
        [3644] = {6803,"Nhân vật","Tào Tháo","Lại có thể đổi lại Văn Cơ.","Lại có thể đổi lại Văn Cơ.",},
        [3645] = {6803,"Tào Tháo","Nhân vật","Lời của Dương Uy Tướng khả thi, không biết ngươi có đồng ý đến Thiền Vu hoàn thành trọng trách này.","Lời của Dương Uy Tướng khả thi, không biết ngươi có đồng ý đến Thiền Vu hoàn thành trọng trách này.",},
        [3646] = {6803,"Nhân vật","Tào Tháo","Ta sẽ đi.","Ta sẽ đi.",},
