        [3728] = {7108,"Lưu Bị","Lưu Biểu","Nay Tào Tháo đưa quân bắc chinh, Hứa Xương không người, nếu đưa quân ở Kinh Tương đến tập kích ắt sẽ thành đại sự.","Nay Tào Tháo đưa quân bắc chinh, Hứa Xương không người, nếu đưa quân ở Kinh Tương đến tập kích ắt sẽ thành đại sự.",},
        [3729] = {7108,"Lưu Biểu","Lưu Bị","Ta có Cửu Quận đã đủ rồi, còn mong gì hơn!","Ta có Cửu Quận đã đủ rồi, còn mong gì hơn!",},
        [3730] = {7108,"Lưu Biểu","Lưu Bị","Ta nghe nói hiền đệ ở Hứa Xương, cùng Tào Tháo thanh mai nấu rượu, cùng luận anh hùng.","Ta nghe nói hiền đệ ở Hứa Xương, cùng Tào Tháo thanh mai nấu rượu, cùng luận anh hùng.",},
        [3731] = {7108,"Lưu Biểu","Lưu Bị","Hiền đệ là danh sĩ thiên hạ, nhưng giờ anh hùng thiên hạ, chỉ có Tào Tháo và chúa công!","Hiền đệ là danh sĩ thiên hạ, nhưng giờ anh hùng thiên hạ, chỉ có Tào Tháo và chúa công!",},
        [3732] = {7108,"Lưu Biểu","Lưu Bị","Với quyền lực của Tào Tháo, e không thua gì đệ.","Với quyền lực của Tào Tháo, e không thua gì đệ.",},
        [3733] = {7108,"Lưu Bị","Lưu Biểu","Nếu ta có được nền tảng vững chắc thì sợ gì không có được thiên hạ.","Nếu ta có được nền tảng vững chắc thì sợ gì không có được thiên hạ.",},
        [3734] = {7109,"Thái Phu Nhân","Lưu Biểu","Hôm qua ngươi và Bị trò chuyện.","Hôm qua ngươi và Bị trò chuyện.",},
        [3735] = {7109,"Thái Phu Nhân","Lưu Biểu","Ta ở phía sau có nghe lời Lưu Bị nói, hắn có ý muốn thôn tính Kinh Châu, nay nếu không trừ khử, sau sẽ là hậu hoạn.","Ta ở phía sau có nghe lời Lưu Bị nói, hắn có ý muốn thôn tính Kinh Châu, nay nếu không trừ khử, sau sẽ là hậu hoạn.",},
        [3736] = {7109,"Lưu Biểu","Thái Phu Nhân","Huyền Đức nhân nghĩa, sao thể diệt được.","Huyền Đức nhân nghĩa, sao thể diệt được.",},
        [3737] = {7110,"Thái Phu Nhân","Thái Mạo","Ta nhiều lần khuyên Biểu diệt Lưu Bị, nhưng hắn lại không nghe.","Ta nhiều lần khuyên Biểu diệt Lưu Bị, nhưng hắn lại không nghe.",},
        [3738] = {7110,"Thái Phu Nhân","Thái Mạo","Ngươi hãy ra tay diệt y trước, ta sẽ bẩm báo chúa công sau.","Ngươi hãy ra tay diệt y trước, ta sẽ bẩm báo chúa công sau.",},
        [3739] = {7110,"Thái Mạo","Thái Phu Nhân","Được, ta sẽ đi ngay.","Được, ta sẽ đi ngay.",},
        [3740] = {7111,"Y Tịch","Lưu Bị","Thái Mạo định hại ngài, hãy mau chạy trốn.","Thái Mạo định hại ngài, hãy mau chạy trốn.",},
        [3741] = {7111,"Lưu Bị","Y Tịch","Chưa nói lời cáo biệt, làm sao đi được?","Chưa nói lời cáo biệt, làm sao đi được?",},
        [3742] = {7111,"Y Tịch","Lưu Bị","Ngài còn day dưa sẽ bị Thái Mạo diệt.","Ngài còn day dưa sẽ bị Thái Mạo diệt.",},
        [3743] = {7111,"Lưu Bị","Y Tịch","Đa tạ người, ta sẽ đi ngay.","Đa tạ người, ta sẽ đi ngay.",},
        [3744] = {7112,"Thái Mạo","Lưu Biểu","Lưu Bị có ý tạo phản, đã đề thơ trên vách đá rồi bỏ đi.","Lưu Bị có ý tạo phản, đã đề thơ trên vách đá rồi bỏ đi.",},
        [3745] = {7112,"Lưu Biểu","Thái Mạo","Bài thơ có nội dung gì?","Bài thơ có nội dung gì?",},
        [3746] = {7112,"Thái Mạo","Lưu Biểu","Số niên đồ thủ khốn, không đối cựu sơn xuyên. Long khởi trì trung vật, thừa lôi dục thượng thiên.","Số niên đồ thủ khốn, không đối cựu sơn xuyên. Long khởi trì trung vật, thừa lôi dục thượng thiên.",},
        [3747] = {7112,"Lưu Biểu","Thái Mạo","Ta và Huyền Đức bên nhau đã lâu, chưa từng thấy hắn làm thơ, đây chắc chắn là kế ly gián.","Ta và Huyền Đức bên nhau đã lâu, chưa từng thấy hắn làm thơ, đây chắc chắn là kế ly gián.",},
        [3748] = {7113,"Thái Mạo","Lưu Biểu","Gần đây được mùa, tập hợp mọi người ở Tương Dương để động viên, mong chúa công đi cùng.","Gần đây được mùa, tập hợp mọi người ở Tương Dương để động viên, mong chúa công đi cùng.",},
        [3749] = {7113,"Lưu Biểu","Thái Mạo","Ta gần đây không khỏe, không thể đi được, có thể lệnh con trai ta đi thay.","Ta gần đây không khỏe, không thể đi được, có thể lệnh con trai ta đi thay.",},
        [3750] = {7113,"Thái Mạo","Lưu Biểu","Công tử vẫn còn nhỏ, e rằng sẽ thất lễ.","Công tử vẫn còn nhỏ, e rằng sẽ thất lễ.",},
        [3751] = {7113,"Lưu Biểu","Thái Mạo","Có thể đến Tân Dã mời Huyền Đức đi thay.","Có thể đến Tân Dã mời Huyền Đức đi thay.",},
        [3752] = {7113,"Thái Mạo","Lưu Biểu","Được. (Trong lòng mừng thầm vì Lưu Biểu trúng kế, bèn cho người mời Huyền Đức đến Tương Dương.)","Được. (Trong lòng mừng thầm vì Lưu Biểu trúng kế, bèn cho người mời Huyền Đức đến Tương Dương.)",},
        [3753] = {7114,"Y Tịch","Lưu Bị","Thái Mạo vờ mời ngài đến Tương Dương, thật ra là nghĩ cách hại ngài.","Thái Mạo vờ mời ngài đến Tương Dương, thật ra là nghĩ cách hại ngài.",},
        [3754] = {7114,"Y Tịch","Lưu Bị","Đông nam bắt ngoài thành đều có quân canh giữ, chỉ còn hướng tây, ngài hãy mau chạy!","Đông nam bắt ngoài thành đều có quân canh giữ, chỉ còn hướng tây, ngài hãy mau chạy!",},
        [3755] = {7114,"Lưu Bị","Y Tịch","Ta sẽ cưỡi ngựa Đích Lô đi từ cửa tây.","Ta sẽ cưỡi ngựa Đích Lô đi từ cửa tây.",},
        [3756] = {7115,"Thái Mạo","Lưu Bị","Sao ngài lại phải chạy trốn?","Sao ngài lại phải chạy trốn?",},
        [3757] = {7115,"Lưu Bị","Thái Mạo","Ta và ngươi không thù không oán, sao ngươi lại định hại ta?","Ta và ngươi không thù không oán, sao ngươi lại định hại ta?",},
        [3758] = {7115,"Lưu Bị","Thái Mạo","Đích Lô! Đi nào! (Đích Lô nhảy qua suối qua bờ tây.)","Đích Lô! Đi nào! (Đích Lô nhảy qua suối qua bờ tây.)",},
        [3759] = {7201,"Mục Đồng","Lưu Bị","Tướng quân chính là Lưu Huyền Đức?","Tướng quân chính là Lưu Huyền Đức?",},
        [3760] = {7201,"Lưu Bị","Mục Đồng","Ngươi chỉ là tiểu đồng ở vùng quê hẻo lánh, sao lại biết họ tên ta?","Ngươi chỉ là tiểu đồng ở vùng quê hẻo lánh, sao lại biết họ tên ta?",},
        [3761] = {7201,"Mục Đồng","Lưu Bị","Ta von không biết, do thường ở bên sư phụ, nghe khách đến trò chuyện thường nhắc có người tên Lưu Huyền Đức, thân cao bảy thước rưỡi, tay dài đến gối, là vị anh hùng có tiếng.","Ta von không biết, do thường ở bên sư phụ, nghe khách đến trò chuyện thường nhắc có người tên Lưu Huyền Đức, thân cao bảy thước rưỡi, tay dài đến gối, là vị anh hùng có tiếng.",},
        [3762] = {7201,"Mục Đồng","Lưu Bị","Nhìn dáng vẻ tướng quân, nghĩ chắc là người đó.","Nhìn dáng vẻ tướng quân, nghĩ chắc là người đó.",},
        [3763] = {7201,"Lưu Bị","Mục Đồng","Sư phụ của ngươi là ai?","Sư phụ của ngươi là ai?",},
        [3764] = {7201,"Mục Đồng","Lưu Bị","Sư phụ họ Tư Mã, tên Huy, tự Đức Tháo, người Dĩnh Xuyên, đạo hiệu Thủy Kính Tiên Sinh.","Sư phụ họ Tư Mã, tên Huy, tự Đức Tháo, người Dĩnh Xuyên, đạo hiệu Thủy Kính Tiên Sinh.",},
        [3765] = {7201,"Lưu Bị","Mục Đồng","Sư phụ ngươi đang ở đâu?","Sư phụ ngươi đang ở đâu?",},
        [3766] = {7201,"Mục Đồng","Lưu Bị","Trang viên ở khu rừng phía trước.","Trang viên ở khu rừng phía trước.",},
        [3767] = {7201,"Lưu Bị","Mục Đồng","Ta chính là Lưu Huyền Đức, ngươi hãy đưa ta đến gặp sư phụ ngươi.","Ta chính là Lưu Huyền Đức, ngươi hãy đưa ta đến gặp sư phụ ngươi.",},
        [3768] = {7202,"Tư Mã Huy","Lưu Bị","Minh công tại sao đến đây?","Minh công tại sao đến đây?",},
        [3769] = {7202,"Lưu Bị","Tư Mã Huy","Tình cờ đi ngang, nghe tiểu đồng nói nên đến thăm!","Tình cờ đi ngang, nghe tiểu đồng nói nên đến thăm!",},
        [3770] = {7202,"Tư Mã Huy","Lưu Bị","Ngài không cần phải giấu, nhìn sắc mặt ngài, ta đã biết ngài chạy nạn đến đây.","Ngài không cần phải giấu, nhìn sắc mặt ngài, ta đã biết ngài chạy nạn đến đây.",},
        [3771] = {7202,"Lưu Bị","Tư Mã Huy","Huyền Đức hổ thẹn.","Huyền Đức hổ thẹn.",},
        [3772] = {7202,"Tư Mã Huy","Lưu Bị","Ta nghe danh ngài đã lâu, nay sao lại ra nông nỗi này?","Ta nghe danh ngài đã lâu, nay sao lại ra nông nỗi này?",},
        [3773] = {7202,"Lưu Bị","Tư Mã Huy","Số mệnh long đong, nên mới như vậy.","Số mệnh long đong, nên mới như vậy.",},
        [3774] = {7202,"Tư Mã Huy","Lưu Bị","Không đúng, chỉ do bên cạnh chưa có người tài.","Không đúng, chỉ do bên cạnh chưa có người tài.",},
        [3775] = {7202,"Lưu Bị","Tư Mã Huy","Ta tuy bất tài, nhưng văn có Tôn Càn, My Trúc, Giản Ung, võ có Quan, Trương, Triệu Vân, đều là người tài giỏi.","Ta tuy bất tài, nhưng văn có Tôn Càn, My Trúc, Giản Ung, võ có Quan, Trương, Triệu Vân, đều là người tài giỏi.",},
        [3776] = {7202,"Tư Mã Huy","Lưu Bị","Quan, Trương, Triệu Vân, có thể địch lại vạn người, nhưng tiếc là chưa thể phát huy hết mình, Tôn Càn, My Trúc chỉ là những thư sinh bình thường, chẳng thể gọi là người tài!","Quan, Trương, Triệu Vân, có thể địch lại vạn người, nhưng tiếc là chưa thể phát huy hết mình, Tôn Càn, My Trúc chỉ là những thư sinh bình thường, chẳng thể gọi là người tài!",},
        [3777] = {7202,"Lưu Bị","Tư Mã Huy","Ta cũng từng đến khắp nơi tìm kiếm người tài, nhưng chưa có duyên gặp!","Ta cũng từng đến khắp nơi tìm kiếm người tài, nhưng chưa có duyên gặp!",},
        [3778] = {7202,"Tư Mã Huy","Lưu Bị","Sao lại như vậy?","Sao lại như vậy?",},
        [3779] = {7202,"Lưu Bị","Tư Mã Huy","Ta đây ngu muội, xin người chỉ giáo.","Ta đây ngu muội, xin người chỉ giáo.",},
        [3780] = {7202,"Tư Mã Huy","Lưu Bị","Có nghe qua lời đồng dao ở Kinh Tương chưa? Rằng: Năm thứ 8 và 9 suy yếu, năm 13 vẫn chưa có con nối dõi.","Có nghe qua lời đồng dao ở Kinh Tương chưa? Rằng: Năm thứ 8 và 9 suy yếu, năm 13 vẫn chưa có con nối dõi.",},
        [3781] = {7202,"Tư Mã Huy","Lưu Bị","Số trời đã định, rồng bay lên trời. Bài đồng dao xuất hiện vào những năm đầu Kiến An: Kiến An năm thứ 8, thê tử của Lưu Cảnh Thăng qua đời, gia đình lục đục, đó là thời điểm bắt đầu suy vong.","Số trời đã định, rồng bay lên trời. Bài đồng dao xuất hiện vào những năm đầu Kiến An: Kiến An năm thứ 8, thê tử của Lưu Cảnh Thăng qua đời, gia đình lục đục, đó là thời điểm bắt đầu suy vong.",},
        [3782] = {7202,"Tư Mã Huy","Lưu Bị","Khi Lưu Cảnh Thăng sắp mất, chỉ còn lại vài vị quan văn võ, ông vẫn chưa có con cháu nối dõi. Số trời đã định, rồng bay lên trời, điều này ứng với tướng quân.","Khi Lưu Cảnh Thăng sắp mất, chỉ còn lại vài vị quan văn võ, ông vẫn chưa có con cháu nối dõi. Số trời đã định, rồng bay lên trời, điều này ứng với tướng quân.",},
        [3783] = {7202,"Lưu Bị","Tư Mã Huy","Ta đây nào dám nhận!","Ta đây nào dám nhận!",},
        [3784] = {7202,"Tư Mã Huy","Lưu Bị","Kỳ tài thiên hạ đêu ở đây, ngài hãy đến tìm.","Kỳ tài thiên hạ đêu ở đây, ngài hãy đến tìm.",},
        [3785] = {7202,"Lưu Bị","Tư Mã Huy","Kỳ tài? Ở đâu? Là ai?","Kỳ tài? Ở đâu? Là ai?",},
        [3786] = {7202,"Tư Mã Huy","Lưu Bị","Trời đã tối, tướng quân hãy tạm ở lại đây một đêm, ngày mai ta tiếp tục trò chuyện.","Trời đã tối, tướng quân hãy tạm ở lại đây một đêm, ngày mai ta tiếp tục trò chuyện.",},
        [3787] = {7203,"Tư Mã Huy","Từ Thứ","Nguyên Trực sao đêm khuya lại đến đây?","Nguyên Trực sao đêm khuya lại đến đây?",},
        [3788] = {7203,"Từ Thứ","Tư Mã Huy","Nghe người đời nhiều về Lưu Cảnh Thăng nên có đến phò trợ ông ấy. Nhưng sau khi gặp gỡ mới biết cũng chỉ là hư danh. Vì vậy ta đã để lại thư cáo biệt mà đến nơi đây.","Nghe người đời nhiều về Lưu Cảnh Thăng nên có đến phò trợ ông ấy. Nhưng sau khi gặp gỡ mới biết cũng chỉ là hư danh. Vì vậy ta đã để lại thư cáo biệt mà đến nơi đây.",},
        [3789] = {7203,"Tư Mã Huy","Từ Thứ","Ngài là người tài phải biết chọn người thích hợp mà phò trợ, sao lại phò trợ cho Cảnh Thăng?","Ngài là người tài phải biết chọn người thích hợp mà phò trợ, sao lại phò trợ cho Cảnh Thăng?",},
        [3790] = {7203,"Từ Thứ","Tư Mã Huy","Tiên sinh nói đúng.","Tiên sinh nói đúng.",},
        [3791] = {7204,"Lưu Bị","Tư Mã Huy","Người đêm qua đến đây là ai?","Người đêm qua đến đây là ai?",},
        [3792] = {7204,"Tư Mã Huy","Lưu Bị","Là bằng hữu của ta.","Là bằng hữu của ta.",},
        [3793] = {7204,"Lưu Bị","Tư Mã Huy","Có thể cho ta gặp người đó không.","Có thể cho ta gặp người đó không.",},
        [3794] = {7204,"Tư Mã Huy","Lưu Bị","Người này muốn tìm minh chủ, đã đi tìm kiếm.","Người này muốn tìm minh chủ, đã đi tìm kiếm.",},
        [3795] = {7204,"Lưu Bị","Tư Mã Huy","Người bằng hữu đó tên gì?","Người bằng hữu đó tên gì?",},
        [3796] = {7204,"Tư Mã Huy","Lưu Bị","Hay lắm! (Cười không nói.)","Hay lắm! (Cười không nói.)",},
        [3797] = {7204,"Lưu Bị","Tư Mã Huy","Phục Long, Phượng Sồ, là người thế nào?","Phục Long, Phượng Sồ, là người thế nào?",},
        [3798] = {7204,"Tư Mã Huy","Lưu Bị","Hay lắm! (Cười không nói.)","Hay lắm! (Cười không nói.)",},
        [3799] = {7204,"Lưu Bị","Tư Mã Huy","Thủy Kính Tiên Sinh có đồng ý tương trợ cùng bảo vệ Hán Thất không?","Thủy Kính Tiên Sinh có đồng ý tương trợ cùng bảo vệ Hán Thất không?",},
        [3800] = {7204,"Tư Mã Huy","Lưu Bị","Ta chỉ là người nhàn rỗi, không có tài cán gì.","Ta chỉ là người nhàn rỗi, không có tài cán gì.",},
        [3801] = {7204,"Tư Mã Huy","Lưu Bị","Sẽ có người tài giỏi hơn ta đến tương trợ cho ngài.","Sẽ có người tài giỏi hơn ta đến tương trợ cho ngài.",},
        [3802] = {7205,"Mục Đồng","Lưu Bị","Có một vị tướng quân dẫn theo vài trăm người đến trang.","Có một vị tướng quân dẫn theo vài trăm người đến trang.",},
        [3803] = {7205,"Lưu Bị","Mục Đồng","Là ai?","Là ai?",},
        [3804] = {7205,"Triệu Vân","Lưu Bị","Là Tử Long ta đây.","Là Tử Long ta đây.",},
        [3805] = {7205,"Triệu Vân","Lưu Bị","Đêm khuya trở về, không thấy chúa công nên vội chạy đi tìm.","Đêm khuya trở về, không thấy chúa công nên vội chạy đi tìm.",},
        [3806] = {7205,"Triệu Vân","Lưu Bị","Chúa công hãy mau về huyện. E rằng có kẻ đến tấn công.","Chúa công hãy mau về huyện. E rằng có kẻ đến tấn công.",},
        [3807] = {7205,"Lưu Bị","Triệu Vân","Được, chúng ta mau về Tân Dã.","Được, chúng ta mau về Tân Dã.",},
        [3808] = {7206,"Từ Thứ","Lưu Bị","Giang sơn nguy cấp, khó lòng vực dậy. Trong núi có hiền sĩ muốn phò trợ minh chủ, minh chủ cầu hiền tài nhưng chưa gặp.","Giang sơn nguy cấp, khó lòng vực dậy. Trong núi có hiền sĩ muốn phò trợ minh chủ, minh chủ cầu hiền tài nhưng chưa gặp.",},
        [3809] = {7206,"Lưu Bị","Từ Thứ","Người này chính là Phục Long, Phượng Sồ mà Thủy Kính nhắc đến?","Người này chính là Phục Long, Phượng Sồ mà Thủy Kính nhắc đến?",},
        [3810] = {7206,"Từ Thứ","Lưu Bị","Ta là người Dĩnh Thượng, tên Đơn Phúc. Nghe tin ngài chiêu hiền đãi sĩ nên đến đây, cố tình hát vang giữa chợ để mong ngài nghe thấy.","Ta là người Dĩnh Thượng, tên Đơn Phúc. Nghe tin ngài chiêu hiền đãi sĩ nên đến đây, cố tình hát vang giữa chợ để mong ngài nghe thấy.",},
        [3811] = {7206,"Từ Thứ","Lưu Bị","Có thể cho ta nhìn lại con ngựa ngài đang cưỡi không.","Có thể cho ta nhìn lại con ngựa ngài đang cưỡi không.",},
        [3812] = {7206,"Từ Thứ","Lưu Bị","Đây là ngựa Đích Lô mà? Tuy là thiên lý mã nhưng phản chủ, đừng nên cưỡi.","Đây là ngựa Đích Lô mà? Tuy là thiên lý mã nhưng phản chủ, đừng nên cưỡi.",},
        [3813] = {7206,"Lưu Bị","Từ Thứ","Ngựa này giúp ta vượt Đàn Khê, cứu mạng ta.","Ngựa này giúp ta vượt Đàn Khê, cứu mạng ta.",},
        [3814] = {7206,"Từ Thứ","Lưu Bị","Đó là cứu chủ, nhưng chắc chắn sẽ hại một chủ, ta có một cách để hóa giải.","Đó là cứu chủ, nhưng chắc chắn sẽ hại một chủ, ta có một cách để hóa giải.",},
        [3815] = {7206,"Lưu Bị","Từ Thứ","Xin nghe.","Xin nghe.",},
        [3816] = {7206,"Từ Thứ","Lưu Bị","Ngài nếu có kẻ thù nào hãy tặng ngựa cho người đó, để nó hại kẻ kia xong rồi mới cưỡi sẽ an toàn.","Ngài nếu có kẻ thù nào hãy tặng ngựa cho người đó, để nó hại kẻ kia xong rồi mới cưỡi sẽ an toàn.",},
        [3817] = {7206,"Lưu Bị","Từ Thứ","Ngươi lại đi bảo ta làm chuyện lợi mình hại người, Lưu Bị này không thể làm.","Ngươi lại đi bảo ta làm chuyện lợi mình hại người, Lưu Bị này không thể làm.",},
        [3818] = {7206,"Từ Thứ","Lưu Bị","Vẫn nghe danh người nhân đức, quả danh bất hư truyền.","Vẫn nghe danh người nhân đức, quả danh bất hư truyền.",},
        [3819] = {7206,"Lưu Bị","Từ Thứ","Nhân đức của ta đâu bằng ai, ngài quá khen.","Nhân đức của ta đâu bằng ai, ngài quá khen.",},
        [3820] = {7206,"Từ Thứ","Lưu Bị","Ta từ Dĩnh Thượng đến đều nghe ca tụng Lưu hoàng thúc đến đâu dân ấm no đến đó.","Ta từ Dĩnh Thượng đến đều nghe ca tụng Lưu hoàng thúc đến đâu dân ấm no đến đó.",},
        [3821] = {7206,"Lưu Bị","Từ Thứ","Huyền Đức xin nhận ngài làm quân sư, điều động nhân mã của ta.","Huyền Đức xin nhận ngài làm quân sư, điều động nhân mã của ta.",},
        [3822] = {7301,"Từ Thứ","Lưu Bị","Tào Nhân đóng quân Phàn Thành, chắc chắn sẽ đưa đại quân tiến đánh.","Tào Nhân đóng quân Phàn Thành, chắc chắn sẽ đưa đại quân tiến đánh.",},
        [3823] = {7301,"Lưu Bị","Từ Thứ","Nên làm thế nào?","Nên làm thế nào?",},
        [3824] = {7301,"Từ Thứ","Lưu Bị","Nếu hắn đưa quân đến đây, Phàn Thành sẽ trống và dễ chiếm lấy.","Nếu hắn đưa quân đến đây, Phàn Thành sẽ trống và dễ chiếm lấy.",},
        [3825] = {7301,"Lưu Bị","Từ Thứ","Nên làm thế nào?","Nên làm thế nào?",},
        [3826] = {7301,"Từ Thứ","Lưu Bị","Huyền Đức hãy ghé tai sát đây.","Huyền Đức hãy ghé tai sát đây.",},
        [3827] = {7302,"Lưu Bị","Từ Thứ","Quân Tào tấn công, bố trận.","Quân Tào tấn công, bố trận.",},
        [3828] = {7302,"Lưu Bị","Từ Thứ","Có biết thế trận này không?","Có biết thế trận này không?",},
        [3829] = {7302,"Từ Thứ","Lưu Bị","Đây là Bát Môn Kim Tỏa Trận. Bát Môn là: Hưu, Sinh, Thương, Đỗ, Cảnh, Tử, Kinh, Khai.","Đây là Bát Môn Kim Tỏa Trận. Bát Môn là: Hưu, Sinh, Thương, Đỗ, Cảnh, Tử, Kinh, Khai.",},
        [3830] = {7302,"Từ Thứ","Lưu Bị","Nếu vào từ cửa Sinh, Cảnh, Khai sẽ tốt, nếu vào từ Thương, Kinh, Hưu sẽ bị thương, từ Đỗ, Tử sẽ vong mạng.","Nếu vào từ cửa Sinh, Cảnh, Khai sẽ tốt, nếu vào từ Thương, Kinh, Hưu sẽ bị thương, từ Đỗ, Tử sẽ vong mạng.",},
        [3831] = {7302,"Từ Thứ","Lưu Bị","Bát Môn trận tuy chỉnh tề nhưng thiếu trung tâm. Nuế đánh từ cửa sinh phía đông nam đi ra từ cửa Cảnh ở chính tây thì thế trận ắt loạn.","Bát Môn trận tuy chỉnh tề nhưng thiếu trung tâm. Nuế đánh từ cửa sinh phía đông nam đi ra từ cửa Cảnh ở chính tây thì thế trận ắt loạn.",},
        [3832] = {7302,"Lưu Bị","Từ Thứ","Quân sĩ giữ vững góc trận, Dương Uy Tướng mang 500 quân vào từ đông nam, ra từ phía tây.","Quân sĩ giữ vững góc trận, Dương Uy Tướng mang 500 quân vào từ đông nam, ra từ phía tây.",},
        [3833] = {7302,"Nhân vật","Lưu Bị","Được, ta sẽ lãnh binh phá Bát Môn Kim Tỏa Trận.","Được, ta sẽ lãnh binh phá Bát Môn Kim Tỏa Trận.",},
        [3834] = {7303,"Tào Nhân","Lý Điển","Quân Lưu Bị chắc có người tài, trận của ta đã bị phá.","Quân Lưu Bị chắc có người tài, trận của ta đã bị phá.",},
        [3835] = {7303,"Lý Điển","Tào Nhân","Ta ở đây nhưng lại lo lắng Phàn Thành","Ta ở đây nhưng lại lo lắng Phàn Thành",},
        [3836] = {7303,"Tào Nhân","Lý Điển","Hôm nay đi cướp trại! Nếu thắng sẽ tính tiếp, nếu thu thì lui quân Phàn Thành.","Hôm nay đi cướp trại! Nếu thắng sẽ tính tiếp, nếu thu thì lui quân Phàn Thành.",},
        [3837] = {7303,"Lý Điển","Tào Nhân","Nhưng Lưu Bị ắt có chuẩn bị trước.","Nhưng Lưu Bị ắt có chuẩn bị trước.",},
        [3838] = {7303,"Tào Nhân","Lý Điển","Đa nghi vậy làm sao dụng binh","Đa nghi vậy làm sao dụng binh",},
        [3839] = {7304,"Từ Thứ","Lưu Bị","Tào Nhân đêm nay sẽ đến cướp trại","Tào Nhân đêm nay sẽ đến cướp trại",},
        [3840] = {7304,"Lưu Bị","Từ Thứ","Sao ngươi lại biết?","Sao ngươi lại biết?",},
        [3841] = {7304,"Từ Thứ","Lưu Bị","Ta đã tính hết rồi.","Ta đã tính hết rồi.",},
        [3842] = {7304,"Từ Thứ","Lưu Bị","Vân Trường đến Phàn Thành đợi lệnh, chờ Tào Nhân xuất thành lập tức chiếm ngay.","Vân Trường đến Phàn Thành đợi lệnh, chờ Tào Nhân xuất thành lập tức chiếm ngay.",},
        [3843] = {7304,"Từ Thứ","Lưu Bị","Chúng ta để trống tại, Triệu Vân và Dương Uy Tướng mai phục bên ngoài, kỳ tập đại quân Tào Nhân ép chúng đến bên sông.","Chúng ta để trống tại, Triệu Vân và Dương Uy Tướng mai phục bên ngoài, kỳ tập đại quân Tào Nhân ép chúng đến bên sông.",},
        [3844] = {7304,"Từ Thứ","Lưu Bị","Trương Phi mai phục bên sông, hợp sức ép hắn về Phàn Thành, Vân Trường ở Phàn Thành cũng ra quân để bao vây quân Tào Nhân.","Trương Phi mai phục bên sông, hợp sức ép hắn về Phàn Thành, Vân Trường ở Phàn Thành cũng ra quân để bao vây quân Tào Nhân.",},
        [3845] = {7304,"Lưu Bị","Từ Thứ","Diệu kế!","Diệu kế!",},
        [3846] = {7305,"Tào Nhân","","Nguy, Lưu Bị đã có chuẩn bị trước, đánh không được lại bị mất Phàn Thành","Nguy, Lưu Bị đã có chuẩn bị trước, đánh không được lại bị mất Phàn Thành",},
        [3847] = {7305,"Tào Nhân","","Mau về Hứa Đô.","Mau về Hứa Đô.",},
        [3848] = {7306,"Tào Nhân","Tào Tháo","Xin thừa tướng trị tội hao binh tổn tướng.","Xin thừa tướng trị tội hao binh tổn tướng.",},
        [3849] = {7306,"Tào Tháo","Tào Nhân","Thắng bại là chuyện thường tình. Không biết kẻ nào đã bày mưu cho Lưu Bị?","Thắng bại là chuyện thường tình. Không biết kẻ nào đã bày mưu cho Lưu Bị?",},
        [3850] = {7306,"Tào Nhân","Tào Tháo","Đơn Phúc","Đơn Phúc",},
        [3851] = {7306,"Tào Tháo","Tào Nhân","Đơn Phúc là kẻ nào?","Đơn Phúc là kẻ nào?",},
        [3852] = {7306,"Trình Dục","Tào Tháo","Kẻ nào học kiếm từ nhỏ, thường báo thù giúp kẻ khác, bôi mặt xõa tóc mà đi, bị sứ bắt được.","Kẻ nào học kiếm từ nhỏ, thường báo thù giúp kẻ khác, bôi mặt xõa tóc mà đi, bị sứ bắt được.",},
        [3853] = {7306,"Trình Dục","Tào Tháo","Hỏi họ tên thì không trả lời, liền cột trên xe để diễu hành thị chúng, đã được đồng bọn cứu thoát.","Hỏi họ tên thì không trả lời, liền cột trên xe để diễu hành thị chúng, đã được đồng bọn cứu thoát.",},
        [3854] = {7306,"Trình Dục","Tào Tháo","Sau hắn thay đổi danh tính, học hỏi danh sư, thường đàm đạo với Tư Mã Huy. Hắn là người Dĩnh Thượng, tên Từ Thứ, tự Nguyên Trực, Đơn Phúc chỉ là biệt danh.","Sau hắn thay đổi danh tính, học hỏi danh sư, thường đàm đạo với Tư Mã Huy. Hắn là người Dĩnh Thượng, tên Từ Thứ, tự Nguyên Trực, Đơn Phúc chỉ là biệt danh.",},
        [3855] = {7306,"Tào Tháo","Trình Dục","Tài năng của Từ Thứ ra sao?","Tài năng của Từ Thứ ra sao?",},
        [3856] = {7306,"Trình Dục","Tào Tháo","Vô cùng tài năng.","Vô cùng tài năng.",},
        [3857] = {7306,"Tào Tháo","Trình Dục","Nay hiền sĩ đã theo Lưu Bị rồi sao? Phải làm sao?","Nay hiền sĩ đã theo Lưu Bị rồi sao? Phải làm sao?",},
        [3858] = {7306,"Trình Dục","Tào Tháo","Thừa tướng muốn chiêu mộ Từ Thứ không khó.","Thừa tướng muốn chiêu mộ Từ Thứ không khó.",},
        [3859] = {7306,"Tào Tháo","Trình Dục","Làm sao chiêu mộ.","Làm sao chiêu mộ.",},
        [3860] = {7306,"Trình Dục","Tào Tháo","Từ Thứ chí hiếu. Chỉ còn mẹ già ở một mình.","Từ Thứ chí hiếu. Chỉ còn mẹ già ở một mình.",},
        [3861] = {7306,"Trình Dục","Tào Tháo","Thừa tướng bảo người đón mẹ hắn đến Hứa Xương, bảo mẹ hắn viết thư gọi về, chắc chắn Từ Thứ sẽ đến.","Thừa tướng bảo người đón mẹ hắn đến Hứa Xương, bảo mẹ hắn viết thư gọi về, chắc chắn Từ Thứ sẽ đến.",},
        [3862] = {7307,"Từ Thứ","Lưu Bị","Ta là Từ Thứ, do chạy nạn nên đổi tên thành Đơn Phúc. Sau được Lưu Cảnh Thăng chiêu nạp, sau khi gặp mới biết hắn là kẻ vô dụng, chỉ tỏ vẻ ta đây.","Ta là Từ Thứ, do chạy nạn nên đổi tên thành Đơn Phúc. Sau được Lưu Cảnh Thăng chiêu nạp, sau khi gặp mới biết hắn là kẻ vô dụng, chỉ tỏ vẻ ta đây.",},
        [3863] = {7307,"Từ Thứ","Lưu Bị","Buổi tối đó đến chỗ Tư Mã Thủy Kính thì bị trách Thứ không biết chọn chủ mà phụng sự, nghe nói Lưu Dự Châu ở đây, sao không đi theo?","Buổi tối đó đến chỗ Tư Mã Thủy Kính thì bị trách Thứ không biết chọn chủ mà phụng sự, nghe nói Lưu Dự Châu ở đây, sao không đi theo?",},
        [3864] = {7307,"Từ Thứ","Lưu Bị","Thứ vờ làm kẻ điên cuồng hát giữa phố chợ, may gặp chúa công nên được trọng dụng.","Thứ vờ làm kẻ điên cuồng hát giữa phố chợ, may gặp chúa công nên được trọng dụng.",},
        [3865] = {7307,"Từ Thứ","Lưu Bị","Nay mẹ già bị Tào Tháo đưa đến Hứa Xương. Mẹ đã viết thư đến, Thứ buộc phải về.","Nay mẹ già bị Tào Tháo đưa đến Hứa Xương. Mẹ đã viết thư đến, Thứ buộc phải về.",},
        [3866] = {7307,"Từ Thứ","Lưu Bị","Luôn muốn góp sức cho chúa công, nhưng đạo hiếu vẫn còn, không thể tận lực. Nay phải từ biệt, mong sẽ gặp lại.","Luôn muốn góp sức cho chúa công, nhưng đạo hiếu vẫn còn, không thể tận lực. Nay phải từ biệt, mong sẽ gặp lại.",},
        [3867] = {7307,"Lưu Bị","Từ Thứ","Hiếu với mẹ là lẽ thường. Hãy đến gặp mẹ trước, sau này chúng ta còn có dịp gặp nhau.","Hiếu với mẹ là lẽ thường. Hãy đến gặp mẹ trước, sau này chúng ta còn có dịp gặp nhau.",},
        [3868] = {7308,"Tôn Càn","Lưu Bị","Nguyên Trực là kỳ tài, lại ở Tân Dã biết hết quân tình của ta. Nay đầu quân Tào Tháo, e quân ta gặp nguy.","Nguyên Trực là kỳ tài, lại ở Tân Dã biết hết quân tình của ta. Nay đầu quân Tào Tháo, e quân ta gặp nguy.",},
        [3869] = {7308,"Tôn Càn","Lưu Bị","Chúa công nên giữ lại. Tháo thấy Nguyên Trực không đến sẽ trảm mẹ, Nguyên Trực chắc chắn vì mẹ báo thù kháng Tào.","Chúa công nên giữ lại. Tháo thấy Nguyên Trực không đến sẽ trảm mẹ, Nguyên Trực chắc chắn vì mẹ báo thù kháng Tào.",},
        [3870] = {7308,"Lưu Bị","Tôn Càn","Không được. Như vậy là bất nhân, bất nghĩa. Ta tuyệt không làm việc đó.","Không được. Như vậy là bất nhân, bất nghĩa. Ta tuyệt không làm việc đó.",},
        [3871] = {7309,"Lưu Bị","Từ Thứ","Lưu Bị không có duyên với tiên sinh. Mong tiên sinh sau này sẽ công thành doanh toại.","Lưu Bị không có duyên với tiên sinh. Mong tiên sinh sau này sẽ công thành doanh toại.",},
        [3872] = {7309,"Từ Thứ","Lưu Bị","Ta được ngài trọng dụng nay phải từ biệt đều vì mẹ già. Nếu Tào Tháo ép buộc, cả đời ta cũng sẽ không đưa ra kế sách nào.","Ta được ngài trọng dụng nay phải từ biệt đều vì mẹ già. Nếu Tào Tháo ép buộc, cả đời ta cũng sẽ không đưa ra kế sách nào.",},
        [3873] = {7309,"Lưu Bị","Từ Thứ","Tiên sinh đi rồi, Lưu Bị cũng không biết phải làm sao.","Tiên sinh đi rồi, Lưu Bị cũng không biết phải làm sao.",},
        [3874] = {7309,"Từ Thứ","Lưu Bị","Nếu ta ở lại mà suốt ngày lo lắng cho mẹ gì thì cũng không giúp ích gì được cho ngài.","Nếu ta ở lại mà suốt ngày lo lắng cho mẹ gì thì cũng không giúp ích gì được cho ngài.",},
        [3875] = {7309,"Từ Thứ","Lưu Bị","Ngài hãy tìm thêm hiền tài giúp ngài thành nghiệp lớn, đừng nản lòng.","Ngài hãy tìm thêm hiền tài giúp ngài thành nghiệp lớn, đừng nản lòng.",},
        [3876] = {7309,"Lưu Bị","Từ Thứ","Hiền tài đâu ai bằng tiên sinh.","Hiền tài đâu ai bằng tiên sinh.",},
        [3877] = {7309,"Từ Thứ","Lưu Bị","Ta chỉ là kẻ tầm thường.","Ta chỉ là kẻ tầm thường.",},
        [3878] = {7309,"Lưu Bị","Từ Thứ","Tiên sinh đi rồi, không biết khi nào gặp lại.","Tiên sinh đi rồi, không biết khi nào gặp lại.",},
        [3879] = {7309,"Từ Thứ","Lưu Bị","Ngài không cần tiễn, Thứ xin từ biệt!","Ngài không cần tiễn, Thứ xin từ biệt!",},
        [3880] = {7309,"Lưu Bị","Từ Thứ","Bảo trọng!","Bảo trọng!",},
        [3881] = {7310,"Lưu Bị","Từ Thứ","Nguyên Trực sao lại trở về?","Nguyên Trực sao lại trở về?",},
        [3882] = {7310,"Từ Thứ","Lưu Bị","Ta quên mất báo với ngài là ngoài thành Tương Dương 20 dặm có một kỳ nhân. Ngài có muốn chiêu nạp?","Ta quên mất báo với ngài là ngoài thành Tương Dương 20 dặm có một kỳ nhân. Ngài có muốn chiêu nạp?",},
        [3883] = {7310,"Lưu Bị","Từ Thứ","Nguyên Trực hãy dẫn đến đây giúp ta.","Nguyên Trực hãy dẫn đến đây giúp ta.",},
        [3884] = {7310,"Từ Thứ","Lưu Bị","Người này phải đích thân ngài đến đó. Nếu có được người đó tựa như Cao Tổ có được Trương Lương.","Người này phải đích thân ngài đến đó. Nếu có được người đó tựa như Cao Tổ có được Trương Lương.",},
        [3885] = {7310,"Lưu Bị","Từ Thứ","Người này có tài đức gì?","Người này có tài đức gì?",},
        [3886] = {7310,"Từ Thứ","Lưu Bị","Người này tài cao hơn cả Quản Trọng, Nhạc Nghị.","Người này tài cao hơn cả Quản Trọng, Nhạc Nghị.",},
        [3887] = {7310,"Từ Thứ","Lưu Bị","Có tài tung hoành, một mình lấy được cả thiên hạ.","Có tài tung hoành, một mình lấy được cả thiên hạ.",},
        [3888] = {7310,"Lưu Bị","Từ Thứ","Xin hỏi họ tên người đó.","Xin hỏi họ tên người đó.",},
        [3889] = {7310,"Từ Thứ","Lưu Bị","Người Lang Nha Dương Quận, tên Gia Cát Lượng, tự Khổng Minh, là hậu nhân Gia Cát Phong.","Người Lang Nha Dương Quận, tên Gia Cát Lượng, tự Khổng Minh, là hậu nhân Gia Cát Phong.",},
        [3890] = {7310,"Từ Thứ","Lưu Bị","Nơi ở là Ngọa Long Cang, nên gọi là Ngọa Long Tiên Sinh.","Nơi ở là Ngọa Long Cang, nên gọi là Ngọa Long Tiên Sinh.",},
        [3891] = {7310,"Từ Thứ","Lưu Bị","Là kỳ tài tuyệt đỉnh, ngài nên đến đó mời ngay. Nếu có được người này sẽ có được thiên hạ!","Là kỳ tài tuyệt đỉnh, ngài nên đến đó mời ngay. Nếu có được người này sẽ có được thiên hạ!",},
        [3892] = {7310,"Lưu Bị","Từ Thứ","Thủy Kính Tiên Sinh từng nói: Phục Long, Phượng Sồ, có được một người có thể an thiên hạ. Chẳng lẽ người đó chính là Phục Long, Phượng Sồ?","Thủy Kính Tiên Sinh từng nói: Phục Long, Phượng Sồ, có được một người có thể an thiên hạ. Chẳng lẽ người đó chính là Phục Long, Phượng Sồ?",},
        [3893] = {7310,"Từ Thứ","Lưu Bị","Phượng Sồ là Tương Dương Bàng Thống. Phục Long là Gia Cát Khổng Minh.","Phượng Sồ là Tương Dương Bàng Thống. Phục Long là Gia Cát Khổng Minh.",},
        [3894] = {7310,"Lưu Bị","Từ Thứ","May có tiên sinh nói cho biết! Lưu Bị đúng là không có tầm nhìn.","May có tiên sinh nói cho biết! Lưu Bị đúng là không có tầm nhìn.",},
        [3895] = {7311,"Từ Thứ","Gia Cát Lượng","Ta vốn muốn phụng sự cho ngài, nay do mẹ viết thư nên phải đi.","Ta vốn muốn phụng sự cho ngài, nay do mẹ viết thư nên phải đi.",},
        [3896] = {7311,"Từ Thứ","Gia Cát Lượng","Nay tiến cử Khổng Minh cho Huyền Đức. Ngài ấy sẽ đến ngay, mong tiên sinh hết lòng phò trợ.","Nay tiến cử Khổng Minh cho Huyền Đức. Ngài ấy sẽ đến ngay, mong tiên sinh hết lòng phò trợ.",},
        [3897] = {7311,"Gia Cát Lượng","Từ Thứ","Ngươi xem ta như vật thay thế sao.","Ngươi xem ta như vật thay thế sao.",},
        [3898] = {7311,"Từ Thứ","Gia Cát Lượng","Ta thật xấu hổ.","Ta thật xấu hổ.",},
        [3899] = {7312,"Lưu Bị","Gia Cát Lượng","Sớm nghe danh tiên sinh.","Sớm nghe danh tiên sinh.",},
        [3900] = {7312,"Lưu Bị","Gia Cát Lượng","Hai lần trước đến đều không được gặp, nay để thư lại văn kỷ, không biết có được vào không?","Hai lần trước đến đều không được gặp, nay để thư lại văn kỷ, không biết có được vào không?",},
        [3901] = {7312,"Gia Cát Lượng","Lưu Bị","Kẻ nhàn nơi Hoa Dương, khiến tướng quân về không mấy lần, thật xấu hổ.","Kẻ nhàn nơi Hoa Dương, khiến tướng quân về không mấy lần, thật xấu hổ.",},
        [3902] = {7312,"Gia Cát Lượng","Lưu Bị","Ta thấy ý trong sách đã đủ biết tiên sinh lo nước lo dân, chỉ tiếc là Lượng đây bất tài, có điều cần hỏi.","Ta thấy ý trong sách đã đủ biết tiên sinh lo nước lo dân, chỉ tiếc là Lượng đây bất tài, có điều cần hỏi.",},
        [3903] = {7312,"Lưu Bị","Gia Cát Lượng","Lời của Tư Mã Đức Tháo, lời của Từ Thứ, ngài nghĩ thế nào? Mong tiên sinh chỉ giáo.","Lời của Tư Mã Đức Tháo, lời của Từ Thứ, ngài nghĩ thế nào? Mong tiên sinh chỉ giáo.",},
        [3904] = {7312,"Gia Cát Lượng","Lưu Bị","Đức Tháo, Nguyên Trực đều là nhân tài. Lượng chỉ là kẻ tầm thường, nào dám bàn luận? Sao tướng quân lại cần đến kẻ tầm thường này?","Đức Tháo, Nguyên Trực đều là nhân tài. Lượng chỉ là kẻ tầm thường, nào dám bàn luận? Sao tướng quân lại cần đến kẻ tầm thường này?",},
        [3905] = {7312,"Lưu Bị","Gia Cát Lượng","Xin tiên sinh hãy vì bá tánh mà xuống núi phò trợ. Lưu Bị xin được chỉ giáo.","Xin tiên sinh hãy vì bá tánh mà xuống núi phò trợ. Lưu Bị xin được chỉ giáo.",},
        [3906] = {7312,"Gia Cát Lượng","Lưu Bị","Nghe nói tướng quân có chí lớn.","Nghe nói tướng quân có chí lớn.",},
        [3907] = {7312,"Lưu Bị","Gia Cát Lượng","Hán thất gian thần lộng quyền, Lưu Bị muốn dùng đại nghĩa cứu thiên hạ. Mong tiên sinh mở mang tầm nhìn để giúp bá tánh thiên hạ.","Hán thất gian thần lộng quyền, Lưu Bị muốn dùng đại nghĩa cứu thiên hạ. Mong tiên sinh mở mang tầm nhìn để giúp bá tánh thiên hạ.",},
        [3908] = {7312,"Gia Cát Lượng","Lưu Bị","Đây là bản đồ 54 châu. Tướng quân muốn thành bá nghiệp, phía bắc để Tào Tháo chiếm thiên thời, phía nam để Tôn Quyền chiếm địa lợi, tướng quân sẽ chiếm nhân hòa.","Đây là bản đồ 54 châu. Tướng quân muốn thành bá nghiệp, phía bắc để Tào Tháo chiếm thiên thời, phía nam để Tôn Quyền chiếm địa lợi, tướng quân sẽ chiếm nhân hòa.",},
        [3909] = {7312,"Gia Cát Lượng","Lưu Bị","Lấy Kinh Châu làm nhà, đoạt Tây Xuyên làm cơ nghiệp, mưu lấy cả thiên hạ.","Lấy Kinh Châu làm nhà, đoạt Tây Xuyên làm cơ nghiệp, mưu lấy cả thiên hạ.",},
        [3910] = {7312,"Lưu Bị","Gia Cát Lượng","Tiên sinh nói rất đúng, giúp Bị khai sáng. Nhưng Kinh Châu Lưu Biểu, Ích Châu Lưu Chương đều là con cháu hán thất, sao ta nỡ chiếm đoạt?","Tiên sinh nói rất đúng, giúp Bị khai sáng. Nhưng Kinh Châu Lưu Biểu, Ích Châu Lưu Chương đều là con cháu hán thất, sao ta nỡ chiếm đoạt?",},
        [3911] = {7312,"Gia Cát Lượng","Lưu Bị","Lượng đã xem thiên tượng, Lưu Biểu sắp mất mạng, Lưu Chương không phải kẻ làm nên việc lớn, sớm muộn gì cũng thuộc về tướng quân.","Lượng đã xem thiên tượng, Lưu Biểu sắp mất mạng, Lưu Chương không phải kẻ làm nên việc lớn, sớm muộn gì cũng thuộc về tướng quân.",},
        [3912] = {7312,"Lưu Bị","Gia Cát Lượng","Lưu Bị xin tiên sinh xuất sơn phò trợ. Lưu Bị xin nghe theo lời tiên sinh.","Lưu Bị xin tiên sinh xuất sơn phò trợ. Lưu Bị xin nghe theo lời tiên sinh.",},
        [3913] = {7312,"Gia Cát Lượng","Lưu Bị","Lượng đã quen cuộc sống dân dã, không dám phụng lệnh.","Lượng đã quen cuộc sống dân dã, không dám phụng lệnh.",},
        [3914] = {7312,"Lưu Bị","Gia Cát Lượng","Tiên sinh không đi, bá tánh làm sao?","Tiên sinh không đi, bá tánh làm sao?",},
        [3915] = {7312,"Gia Cát Lượng","Lưu Bị","Nếu tướng quân không chê bai, nguyện cống hiến sức lực cho ngài.","Nếu tướng quân không chê bai, nguyện cống hiến sức lực cho ngài.",},
        [3916] = {7401,"Lữ Mông","Tôn Quyền","Có thuộc hạ Hoàng Tổ đến đầu hàng.","Có thuộc hạ Hoàng Tổ đến đầu hàng.",},
        [3917] = {7401,"Tôn Quyền","Lữ Mông","Kẻ nào? Mau báo tên!","Kẻ nào? Mau báo tên!",},
        [3918] = {7401,"Lữ Mông","Tôn Quyền","Cam Ninh, người Ba Đô Lâm Giang, thông hiểu sách sử, có sức mạnh, thích hành hiệp.","Cam Ninh, người Ba Đô Lâm Giang, thông hiểu sách sử, có sức mạnh, thích hành hiệp.",},
        [3919] = {7401,"Lữ Mông","Tôn Quyền","Người này lưng đeo chuông đồng, lại thường dùng gấm Tây Xuyên làm buồm nên được gọi là Cẩm Phàm Tặc.","Người này lưng đeo chuông đồng, lại thường dùng gấm Tây Xuyên làm buồm nên được gọi là Cẩm Phàm Tặc.",},
        [3920] = {7401,"Lữ Mông","Tôn Quyền","Nhờ cải cách đúng đắn, Lưu Biểu được dân ủng hộ.","Nhờ cải cách đúng đắn, Lưu Biểu được dân ủng hộ.",},
        [3921] = {7401,"Lữ Mông","Tôn Quyền","Thấy Biểu khó thành sự, muốn hàng Đông Ngô, bị Hoàng Tổ giữ tại Hạ Khẩu. Trước Đông Ngô đánh Tổ, có Cam Ninh cứu về Hạ Khẩu.","Thấy Biểu khó thành sự, muốn hàng Đông Ngô, bị Hoàng Tổ giữ tại Hạ Khẩu. Trước Đông Ngô đánh Tổ, có Cam Ninh cứu về Hạ Khẩu.",},
        [3922] = {7401,"Lữ Mông","Tôn Quyền","Tô Phi nhiều lần tiến cử cho Hoàng Tổ, nhưng Tổ coi khinh không trọng dụng, khiến Ninh ôm hận.","Tô Phi nhiều lần tiến cử cho Hoàng Tổ, nhưng Tổ coi khinh không trọng dụng, khiến Ninh ôm hận.",},
        [3923] = {7401,"Lữ Mông","Tôn Quyền","Ta sao có thể hận chúa công? Ninh dẫn người qua sông, đến gặp chúa công, xin chỉ lệnh.","Ta sao có thể hận chúa công? Ninh dẫn người qua sông, đến gặp chúa công, xin chỉ lệnh.",},
        [3924] = {7401,"Tôn Quyền","Lữ Mông","Dương Uy Tướng đã từng cứu Tôn Sách huynh ta, việc này ngươi thấy sao?","Dương Uy Tướng đã từng cứu Tôn Sách huynh ta, việc này ngươi thấy sao?",},
        [3925] = {7401,"Nhân vật","Tôn Quyền","Ta nghĩ có Hưng Bá ắt diệt được Hoàng Tổ.","Ta nghĩ có Hưng Bá ắt diệt được Hoàng Tổ.",},
        [3926] = {7401,"Tôn Quyền","Nhân vật","Mau đưa hắn tới gặp.","Mau đưa hắn tới gặp.",},
        [3927] = {7402,"Tôn Quyền","Cam Ninh","Xin chỉ cho ta kế diệt Hoàng Tổ.","Xin chỉ cho ta kế diệt Hoàng Tổ.",},
        [3928] = {7402,"Cam Ninh","Tôn Quyền","Nay nhà Hán lâm nguy, Tào Tháo muốn chiếm ngôi. Lưu Biểu tầm nhìn hạn hẹp, không thể nối nghiệp, minh công hãy sớm dự liệu.","Nay nhà Hán lâm nguy, Tào Tháo muốn chiếm ngôi. Lưu Biểu tầm nhìn hạn hẹp, không thể nối nghiệp, minh công hãy sớm dự liệu.",},
        [3929] = {7402,"Cam Ninh","Tôn Quyền","Tấn công Hoàng Tổ trước. Tổ nay đã già, lại tham lợi lộc, lòng người oán hận.","Tấn công Hoàng Tổ trước. Tổ nay đã già, lại tham lợi lộc, lòng người oán hận.",},
        [3930] = {7402,"Cam Ninh","Tôn Quyền","Minh công tiến công ắt phá được Tổ quân, rồi từ Sở Quan chiếm Ba, Thục, bá nghiệp đã định.","Minh công tiến công ắt phá được Tổ quân, rồi từ Sở Quan chiếm Ba, Thục, bá nghiệp đã định.",},
        [3931] = {7402,"Tôn Quyền","Cam Ninh","Hay lắm!","Hay lắm!",},
        [3932] = {7402,"Tôn Quyền","Cam Ninh","Lệnh Chu Du làm đô đốc, Lữ Mông và Dương Uy Tướng tiên phong, Đổng Tập và Cam Ninh làm phó tướng.","Lệnh Chu Du làm đô đốc, Lữ Mông và Dương Uy Tướng tiên phong, Đổng Tập và Cam Ninh làm phó tướng.",},
        [3933] = {7402,"Tôn Quyền","Cam Ninh","Ta dẫn 10 vạn quân diệt Hoàng Tổ.","Ta dẫn 10 vạn quân diệt Hoàng Tổ.",},
        [3934] = {7403,"Hoàng Tổ","","Nghe tin, Tôn Quyền đã dẫn 10 vạn quân đến.","Nghe tin, Tôn Quyền đã dẫn 10 vạn quân đến.",},
        [3935] = {7403,"Hoàng Tổ","","Lệnh Tô Phi làm đại tướng, Trần Tựu, Đặng Long tiên phong, khởi binh nghênh chiến.","Lệnh Tô Phi làm đại tướng, Trần Tựu, Đặng Long tiên phong, khởi binh nghênh chiến.",},
        [3936] = {7404,"Nhân vật","Tôn Quyền","Tô Phi, Trần Tựu, Đặng Long đều đã bị ta diệt.","Tô Phi, Trần Tựu, Đặng Long đều đã bị ta diệt.",},
        [3937] = {7404,"Nhân vật","Tôn Quyền","Hoàng Tổ bại trận, bỏ Giang Hạ chạy về Kinh Châu.","Hoàng Tổ bại trận, bỏ Giang Hạ chạy về Kinh Châu.",},
        [3938] = {7404,"Tôn Quyền","Nhân vật","Đuổi!","Đuổi!",},
        [3939] = {7405,"Hoàng Tổ","Cam Ninh","Ta chưa từng bạc đãi ngươi, sao lại ép buộc?","Ta chưa từng bạc đãi ngươi, sao lại ép buộc?",},
        [3940] = {7405,"Cam Ninh","Hoàng Tổ","Ta ở Giang Hạ, lập nhiều công lớn, ngươi lại coi ta như kẻ cướp, việc này nói sao đây!","Ta ở Giang Hạ, lập nhiều công lớn, ngươi lại coi ta như kẻ cướp, việc này nói sao đây!",},
        [3941] = {7405,"Nhân vật","Hoàng Tổ","Không sợ gì hết, diệt!","Không sợ gì hết, diệt!",},
        [3942] = {7406,"Tôn Quyền","","Hoàng Tổ đã vong mạng, đem thủ cấp của hắn về Giang Đông thờ cha hắn.","Hoàng Tổ đã vong mạng, đem thủ cấp của hắn về Giang Đông thờ cha hắn.",},
        [3943] = {7406,"Tôn Quyền","","Ban quân, phong Cam Ninh làm đô úy.","Ban quân, phong Cam Ninh làm đô úy.",},
        [3944] = {7406,"Trương Chiêu","Tôn Quyền","Cô thành khó thủ, chi bằng về Giang Đông, Lưu Biểu biết ta diệt Hoàng Tổ sẽ báo thù.","Cô thành khó thủ, chi bằng về Giang Đông, Lưu Biểu biết ta diệt Hoàng Tổ sẽ báo thù.",},
        [3945] = {7406,"Trương Chiêu","Tôn Quyền","Ta tính kế này ắt thắng Lưu Biểu, chiếm thế thượng phong đoạt Kinh Tương.","Ta tính kế này ắt thắng Lưu Biểu, chiếm thế thượng phong đoạt Kinh Tương.",},
        [3946] = {7406,"Tôn Quyền","Trương Chiêu","Được, bỏ Giang Hạ, về Giang Đông.","Được, bỏ Giang Hạ, về Giang Đông.",},
        [3947] = {7501,"Lưu Bị","Gia Cát Lượng","Lưu Biểu mời ta đến Kinh Châu nghị sự.","Lưu Biểu mời ta đến Kinh Châu nghị sự.",},
        [3948] = {7501,"Gia Cát Lượng","Lưu Bị","Chính là kế báo thù chúa công. Ta đi cùng chúa công, tùy cơ ứng biến.","Chính là kế báo thù chúa công. Ta đi cùng chúa công, tùy cơ ứng biến.",},
        [3949] = {7501,"Lưu Bị","Gia Cát Lượng","Gặp Cảnh Thăng nên đối đáp sao?","Gặp Cảnh Thăng nên đối đáp sao?",},
        [3950] = {7501,"Gia Cát Lượng","Lưu Bị","Tạ ơn chuyện Tương Dương trước. Nếu hắn lệnh cho người chinh phạt Giang Đông thì đừng chấp thuận, nhưng nói về Tân Dã, hãy sắp xếp quân mã.","Tạ ơn chuyện Tương Dương trước. Nếu hắn lệnh cho người chinh phạt Giang Đông thì đừng chấp thuận, nhưng nói về Tân Dã, hãy sắp xếp quân mã.",},
        [3951] = {7502,"Lưu Biểu","Lưu Bị","Ta đã biết chuyện hiền đệ bị hại. Lúc đó muốn diệt Thái Mạo, vì chúng dân đang lâm nguy mà tạm tha. Hiền đệ chớ trách tội.","Ta đã biết chuyện hiền đệ bị hại. Lúc đó muốn diệt Thái Mạo, vì chúng dân đang lâm nguy mà tạm tha. Hiền đệ chớ trách tội.",},
        [3952] = {7502,"Lưu Bị","Lưu Biểu","Chuyện không phạm Thái tướng quân.","Chuyện không phạm Thái tướng quân.",},
        [3953] = {7502,"Lưu Biểu","Lưu Bị","Nay Giang Hạ thất thủ, Hoàng Tổ gặp nạn, mong hiền đệ hiến kế báo thù.","Nay Giang Hạ thất thủ, Hoàng Tổ gặp nạn, mong hiền đệ hiến kế báo thù.",},
        [3954] = {7502,"Lưu Bị","Lưu Biểu","Hoàng Tổ không biết dùng người mới gặp nạn. Nếu Hưng binh nam chinh, Tào binh bắc tiến, phải làm sao?","Hoàng Tổ không biết dùng người mới gặp nạn. Nếu Hưng binh nam chinh, Tào binh bắc tiến, phải làm sao?",},
        [3955] = {7502,"Lưu Biểu","Lưu Bị","Ta nay ốm yếu, hiền đệ hãy giúp ta quản lý. Sau này ta qua đời, đệ sẽ là chủ Kinh Châu.","Ta nay ốm yếu, hiền đệ hãy giúp ta quản lý. Sau này ta qua đời, đệ sẽ là chủ Kinh Châu.",},
        [3956] = {7502,"Lưu Bị","Lưu Biểu","Ta sao dám nhận trọng trách này.","Ta sao dám nhận trọng trách này.",},
        [3957] = {7502,"Lưu Bị","Lưu Biểu","Xin cáo lui trước.","Xin cáo lui trước.",},
        [3958] = {7503,"Gia Cát Lượng","Lưu Bị","Vì sao Cảnh Thăng muốn giao Kinh Châu cho ngài?","Vì sao Cảnh Thăng muốn giao Kinh Châu cho ngài?",},
