        [3981] = {7507,"Nhân vật","Gia Cát Lượng","Ta nghe Khổng Minh huynh đang tìm thê tử.","Ta nghe Khổng Minh huynh đang tìm thê tử.",},
        [3982] = {7507,"Nhân vật","Gia Cát Lượng","Ta du ngoạn biết một nữ nhân tài hoa hơn người, ngươi có thể gặp.","Ta du ngoạn biết một nữ nhân tài hoa hơn người, ngươi có thể gặp.",},
