        [3984] = {7507,"Nhân vật","Gia Cát Lượng","Con gái Hoàng Thừa Ngạn, Nguyệt Anh.","Con gái Hoàng Thừa Ngạn, Nguyệt Anh.",},
        [3985] = {7507,"Gia Cát Lượng","Nhân vật","Ta nghe nói tướng mạo xấu xí, tóc vàng da đen, sao lại giới thiệu cho ta?","Ta nghe nói tướng mạo xấu xí, tóc vàng da đen, sao lại giới thiệu cho ta?",},
        [3986] = {7507,"Nhân vật","Gia Cát Lượng","Trăm nghe không bằng một thấy, hãy đích thân đi xem.","Trăm nghe không bằng một thấy, hãy đích thân đi xem.",},
        [3987] = {7508,"Gia Cát Lượng","Nguyệt Anh","Thế nhân gọi thiên kim của Hoàng Thừa Ngạn là A Sửu.","Thế nhân gọi thiên kim của Hoàng Thừa Ngạn là A Sửu.",},
        [3988] = {7508,"Gia Cát Lượng","Nguyệt Anh","Nghe nói, ngươi thông minh xinh đẹp, tài hoa hơn người, võ nghệ cao cường.","Nghe nói, ngươi thông minh xinh đẹp, tài hoa hơn người, võ nghệ cao cường.",},
