        [3990] = {7508,"Nguyệt Anh","Gia Cát Lượng","Tiên sinh quá khen.","Tiên sinh quá khen.",},
        [3991] = {7508,"Nguyệt Anh","Gia Cát Lượng","Ta tặng tiên sinh cây vũ phiến.","Ta tặng tiên sinh cây vũ phiến.",},
        [3992] = {7508,"Gia Cát Lượng","Nguyệt Anh","Sao lại tặng ta vũ phiến?","Sao lại tặng ta vũ phiến?",},
        [3993] = {7508,"Nguyệt Anh","Gia Cát Lượng","Gia Cát tiên sinh cùng gia phụ luận thiên hạ đại sự, tâm mang đại kế, khí vũ hiên ngang.","Gia Cát tiên sinh cùng gia phụ luận thiên hạ đại sự, tâm mang đại kế, khí vũ hiên ngang.",},
        [3994] = {7508,"Nguyệt Anh","Gia Cát Lượng","Nhưng mỗi lần nhắc đến Tào Tháo, Tôn Quyền thì ngài chau mày lo lắng, chiếc quạt này là dùng để che mặt.","Nhưng mỗi lần nhắc đến Tào Tháo, Tôn Quyền thì ngài chau mày lo lắng, chiếc quạt này là dùng để che mặt.",},
        [3995] = {7508,"Gia Cát Lượng","Nguyệt Anh","Ta biết, đại trượng phu phải giữ tâm thái bình thản, không thể để tình cảm làm dao động mà để người khác phát hiện, sẽ bị khinh thường, hỏng việc lớn.","Ta biết, đại trượng phu phải giữ tâm thái bình thản, không thể để tình cảm làm dao động mà để người khác phát hiện, sẽ bị khinh thường, hỏng việc lớn.",},
        [3996] = {7508,"Gia Cát Lượng","Nguyệt Anh","Từ nay ta sẽ giữ quạt này bên mình!","Từ nay ta sẽ giữ quạt này bên mình!",},
        [3997] = {7509,"Nhân vật","Gia Cát Lượng","Nguyệt Anh hợp mắt Khổng Minh chứ?","Nguyệt Anh hợp mắt Khổng Minh chứ?",},
        [3998] = {7509,"Gia Cát Lượng","Nhân vật","Thông minh xinh đẹp, tài hoa hơn người, ta rất thích!","Thông minh xinh đẹp, tài hoa hơn người, ta rất thích!",},
        [3999] = {7509,"Nhân vật","Gia Cát Lượng","Khổng Minh huynh thật khó giấu cảm xúc, ha ha.","Khổng Minh huynh thật khó giấu cảm xúc, ha ha.",},
        [4000] = {7509,"Gia Cát Lượng","Nhân vật","Ta đi mang lễ cho cha mẹ!","Ta đi mang lễ cho cha mẹ!",},
        [4001] = {7601,"Hạ Hầu Đôn","Tào Tháo","Nghe nói Lưu Bị ở Tân Dã ngày ngày luyện binh, hãy dự liệu trước.","Nghe nói Lưu Bị ở Tân Dã ngày ngày luyện binh, hãy dự liệu trước.",},
        [4002] = {7601,"Tào Tháo","Hạ Hầu Đôn","Lệnh ngươi làm đô đốc, Vu Cấm, Lý Điển, Hạ Hầu Lan, Hàn Hạo làm phó tướng, dẫn quân nhắm vào Tân Dã.","Lệnh ngươi làm đô đốc, Vu Cấm, Lý Điển, Hạ Hầu Lan, Hàn Hạo làm phó tướng, dẫn quân nhắm vào Tân Dã.",},
        [4003] = {7601,"Tuân Úc","Tào Tháo","Lưu Bị có Gia Cát Lượng là quân sư, không thể khinh địch.","Lưu Bị có Gia Cát Lượng là quân sư, không thể khinh địch.",},
        [4004] = {7601,"Hạ Hầu Đôn","Tuân Úc","Bắt Lưu Bị dễ như trở tay.","Bắt Lưu Bị dễ như trở tay.",},
        [4005] = {7601,"Từ Thứ","Hạ Hầu Đôn","Tướng quân chớ coi thường Lưu Bị, hắn có Gia Cát Lượng, như hổ thêm cánh.","Tướng quân chớ coi thường Lưu Bị, hắn có Gia Cát Lượng, như hổ thêm cánh.",},
        [4006] = {7601,"Tào Tháo","Từ Thứ","Gia Cát Lượng là ai?","Gia Cát Lượng là ai?",},
        [4007] = {7601,"Từ Thứ","Tào Tháo","Tự Khổng Minh, biệt hiệu Ngọa Long Tiên Sinh, là kỳ tài, mưu tính xuất quỷ nhập thần.","Tự Khổng Minh, biệt hiệu Ngọa Long Tiên Sinh, là kỳ tài, mưu tính xuất quỷ nhập thần.",},
        [4008] = {7601,"Tào Tháo","Từ Thứ","So với ngài thì sao?","So với ngài thì sao?",},
        [4009] = {7601,"Từ Thứ","Tào Tháo","Ta không dám so với Gia Cát Lượng.","Ta không dám so với Gia Cát Lượng.",},
        [4010] = {7601,"Hạ Hầu Đôn","Tào Tháo","Ta thấy Gia Cát Lượng không có gì đáng sợ! Nếu không thể bắt sống Lưu Bị, nguyện lấy mạng này hiến thừa tướng.","Ta thấy Gia Cát Lượng không có gì đáng sợ! Nếu không thể bắt sống Lưu Bị, nguyện lấy mạng này hiến thừa tướng.",},
        [4011] = {7601,"Tào Tháo","Hạ Hầu Đôn","Mong sớm nghe tin thắng trận.","Mong sớm nghe tin thắng trận.",},
        [4012] = {7602,"Lưu Bị","Trương Phi","Tào Tháo phái Hạ Hầu Đôn dẫn 10 vạn binh đến Tân Dã. Phải làm sao?","Tào Tháo phái Hạ Hầu Đôn dẫn 10 vạn binh đến Tân Dã. Phải làm sao?",},
        [4013] = {7602,"Trương Phi","Lưu Bị","Phái Khổng Minh lên nghênh chiến.","Phái Khổng Minh lên nghênh chiến.",},
        [4014] = {7602,"Lưu Bị","Trương Phi","Khổng Minh anh tuệ, nhị đệ dũng tu, làm sao hòa hợp?","Khổng Minh anh tuệ, nhị đệ dũng tu, làm sao hòa hợp?",},
        [4015] = {7602,"Trương Phi","Lưu Bị","Hừ! Vân Trường, chúng ta đi.","Hừ! Vân Trường, chúng ta đi.",},
        [4016] = {7603,"Lưu Bị","Gia Cát Lượng","Khổng Minh, Hạ Hầu Đôn dẫn 10 vạn quân đến, ta phải làm gì?","Khổng Minh, Hạ Hầu Đôn dẫn 10 vạn quân đến, ta phải làm gì?",},
        [4017] = {7603,"Gia Cát Lượng","Lưu Bị","E là Quan, Trương không nghe lệnh ta, chúa công muốn ta xuất binh hãy ban kiếm ấn.","E là Quan, Trương không nghe lệnh ta, chúa công muốn ta xuất binh hãy ban kiếm ấn.",},
        [4018] = {7603,"Lưu Bị","Gia Cát Lượng","Ta sẽ giao kiếm ấn cho ngươi.","Ta sẽ giao kiếm ấn cho ngươi.",},
        [4019] = {7604,"Lưu Bị","Trương Phi","Ta đã giao kiếm ấn, các ngươi phải nghe lệnh Khổng Minh.","Ta đã giao kiếm ấn, các ngươi phải nghe lệnh Khổng Minh.",},
        [4020] = {7604,"Trương Phi","Lưu Bị","Thử xem hắn sắp xếp thế nào.","Thử xem hắn sắp xếp thế nào.",},
        [4021] = {7604,"Gia Cát Lượng","Trương Phi","Lợi dụng núi rừng Bác Vọng, ta có thể mai phục.","Lợi dụng núi rừng Bác Vọng, ta có thể mai phục.",},
        [4022] = {7604,"Gia Cát Lượng","Trương Phi","Vân Trường dẫn 1000 quân mai phục Dự Sơ, chờ địch nghỉ ngơi, khi thấy đằng nam nổi lửa thì xông vào đốt lương thảo địch ở phía sau.","Vân Trường dẫn 1000 quân mai phục Dự Sơ, chờ địch nghỉ ngơi, khi thấy đằng nam nổi lửa thì xông vào đốt lương thảo địch ở phía sau.",},
        [4023] = {7604,"Gia Cát Lượng","Trương Phi","Dực Đức dẫn 1000 quân mai phục sau An Lâm, khi thấy đằng nam nổi lửa thì đốt lương thảo ở Bác Vọng Thành.","Dực Đức dẫn 1000 quân mai phục sau An Lâm, khi thấy đằng nam nổi lửa thì đốt lương thảo ở Bác Vọng Thành.",},
        [4024] = {7604,"Gia Cát Lượng","Trương Phi","Quan Bình, Lưu Phong dẫn 500 quân, chuẩn bị lửa chờ ở hai bên gò Bác Vọng, khi quân vừa đến thì châm lửa.","Quan Bình, Lưu Phong dẫn 500 quân, chuẩn bị lửa chờ ở hai bên gò Bác Vọng, khi quân vừa đến thì châm lửa.",},
        [4025] = {7604,"Gia Cát Lượng","Trương Phi","Quân tiên phong không cần thắng, chúa công tự dẫn quân chi viện. Tất cả làm theo kế hoạch, không được sai sót.","Quân tiên phong không cần thắng, chúa công tự dẫn quân chi viện. Tất cả làm theo kế hoạch, không được sai sót.",},
        [4026] = {7604,"Quan Vũ","Gia Cát Lượng","Khi ta đánh địch thì quân sư làm gì?","Khi ta đánh địch thì quân sư làm gì?",},
        [4027] = {7604,"Gia Cát Lượng","Quan Vũ","Ta chỉ ngồi yên ở trong thành.","Ta chỉ ngồi yên ở trong thành.",},
        [4028] = {7604,"Trương Phi","Gia Cát Lượng","Chúng ta phải chiến đấu, ngươi lại ngồi trong nhà tự tại!","Chúng ta phải chiến đấu, ngươi lại ngồi trong nhà tự tại!",},
        [4029] = {7604,"Gia Cát Lượng","Trương Phi","Kiếm ấn ở đây, trái lệnh xử chém!","Kiếm ấn ở đây, trái lệnh xử chém!",},
        [4030] = {7604,"Lưu Bị","Trương Phi","Nhị đệ không thể trái lệnh.","Nhị đệ không thể trái lệnh.",},
        [4031] = {7604,"Trương Phi","Lưu Bị","Hừ!","Hừ!",},
        [4032] = {7604,"Quan Vũ","Lưu Bị","Ta chưa cần xem kế của hắn vội, lúc đó hỏi hắn cũng không muộn.","Ta chưa cần xem kế của hắn vội, lúc đó hỏi hắn cũng không muộn.",},
        [4033] = {7604,"Gia Cát Lượng","Quan Vũ","Chúa công có thể đóng quân ở dưới núi Bác Vọng. Địch sẽ tới lúc hoàng hôn, ngài hãy rời trại, khi thấy lửa thì đến đánh úp.","Chúa công có thể đóng quân ở dưới núi Bác Vọng. Địch sẽ tới lúc hoàng hôn, ngài hãy rời trại, khi thấy lửa thì đến đánh úp.",},
        [4034] = {7604,"Gia Cát Lượng","Quan Vũ","Ta và My Trúc, Mi Phương dẫn 500 quân cố thủ.","Ta và My Trúc, Mi Phương dẫn 500 quân cố thủ.",},
        [4035] = {7605,"Hạ Hầu Đôn","","Từ Nguyên Trực khen Gia Cát Lượng trước mặt thừa tướng, nay thấy hắn dẫn quân đối địch với ta, thật như lấy trứng chọi đá!","Từ Nguyên Trực khen Gia Cát Lượng trước mặt thừa tướng, nay thấy hắn dẫn quân đối địch với ta, thật như lấy trứng chọi đá!",},
        [4036] = {7605,"Hạ Hầu Đôn","","Muốn bắt Lưu Bị, Gia Cát Lượng, phải hỏi ta.","Muốn bắt Lưu Bị, Gia Cát Lượng, phải hỏi ta.",},
        [4037] = {7605,"Triệu Vân","Hạ Hầu Đôn","Im lặng! Hãy xem trường thương của ta.","Im lặng! Hãy xem trường thương của ta.",},
        [4038] = {7605,"Hạ Hầu Đôn","Triệu Vân","Các ngươi theo Lưu Bị cũng đúng lắm!","Các ngươi theo Lưu Bị cũng đúng lắm!",},
        [4039] = {7606,"Lý Điển","Hạ Hầu Đôn","Triệu Vân vờ hàng dụ địch, sợ có mai phục.","Triệu Vân vờ hàng dụ địch, sợ có mai phục.",},
        [4040] = {7606,"Hạ Hầu Đôn","Lý Điển","Quân địch thập diện mai phục ta cũng không sợ!","Quân địch thập diện mai phục ta cũng không sợ!",},
        [4041] = {7606,"Hạ Hầu Đôn","Lý Điển","Thẳng tiến gò Bác Vọng!","Thẳng tiến gò Bác Vọng!",},
        [4042] = {7607,"Lý Điển","Vu Cấm","Đường phía nam hiểm trở, địch tất bại.","Đường phía nam hiểm trở, địch tất bại.",},
        [4043] = {7607,"Lý Điển","Vu Cấm","Rừng rậm rạp, nếu chúng dùng hỏa công thì sao?","Rừng rậm rạp, nếu chúng dùng hỏa công thì sao?",},
        [4044] = {7607,"Vu Cấm","Lý Điển","Là lời chủ tướng. Ta tiến lên trước, chủ tướng dẫn hậu quân.","Là lời chủ tướng. Ta tiến lên trước, chủ tướng dẫn hậu quân.",},
        [4045] = {7607,"Vu Cấm","Lý Điển","Hậu quân chậm lại! Tiền quân sắp dừng!","Hậu quân chậm lại! Tiền quân sắp dừng!",},
        [4046] = {7607,"Vu Cấm","Lý Điển","Đường phía nam hiểm trở, có thể cản hỏa công.","Đường phía nam hiểm trở, có thể cản hỏa công.",},
        [4047] = {7607,"Hạ Hầu Đôn","Vu Cấm","Đứng lại!","Đứng lại!",},
        [4048] = {7608,"Hạ Hầu Đôn","","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!",},
        [4049] = {7608,"Hạ Hầu Đôn","","Nay nếm mùi thất bại, chạy về Hứa Xương.","Nay nếm mùi thất bại, chạy về Hứa Xương.",},
        [4050] = {7609,"Trương Phi","Gia Cát Lượng","Khổng Minh thật anh kiệt!","Khổng Minh thật anh kiệt!",},
        [4051] = {7609,"Quan Vũ","Gia Cát Lượng","Ta rất ngưỡng mộ.","Ta rất ngưỡng mộ.",},
        [4052] = {7609,"Gia Cát Lượng","Quan Vũ","Hạ Hầu Đôn bỏ chạy, Tào Tháo sẽ tự dẫn quân đến.","Hạ Hầu Đôn bỏ chạy, Tào Tháo sẽ tự dẫn quân đến.",},
        [4053] = {7609,"Lưu Bị","Gia Cát Lượng","Phải làm sao đây?","Phải làm sao đây?",},
        [4054] = {7609,"Gia Cát Lượng","Lưu Bị","Ta có kế này.","Ta có kế này.",},
        [4055] = {7609,"Lưu Bị","Gia Cát Lượng","Khổng Minh tiên sinh hãy nói rõ.","Khổng Minh tiên sinh hãy nói rõ.",},
        [4056] = {7609,"Gia Cát Lượng","Lưu Bị","Tân Dã là một huyện nhỏ, không ở lâu được. Mới đây, tôi nghe Lưu Biểu bị bệnh nguy cấp lắm, chúa công phải nhân dịp này chiếm lấy Kinh Châu làm căn cứ đã, rồi hãy chống cự Tào Tháo sau","Tân Dã là một huyện nhỏ, không ở lâu được. Mới đây, tôi nghe Lưu Biểu bị bệnh nguy cấp lắm, chúa công phải nhân dịp này chiếm lấy Kinh Châu làm căn cứ đã, rồi hãy chống cự Tào Tháo sau",},
        [4057] = {7609,"Lưu Bị","Gia Cát Lượng","Kế của tiên sinh hay lắm nhưng tôi đã chịu ơn Cảnh Thăng, không nỡ làm thế!","Kế của tiên sinh hay lắm nhưng tôi đã chịu ơn Cảnh Thăng, không nỡ làm thế!",},
        [4058] = {7609,"Gia Cát Lượng","Lưu Bị","Nếu chúa công không lấy bây giờ, sau sẽ hối không kịp.","Nếu chúa công không lấy bây giờ, sau sẽ hối không kịp.",},
        [4059] = {7609,"Lưu Bị","Gia Cát Lượng","Ta thà chết, chứ không làm chuyện bất nghĩa","Ta thà chết, chứ không làm chuyện bất nghĩa",},
        [4060] = {7609,"Gia Cát Lượng","Lưu Bị","Vậy để liệu kế khác.","Vậy để liệu kế khác.",},
        [4061] = {7701,"Hạ Hầu Đôn","Tào Tháo","Mưu kế Gia Cát Lượng, dùng hỏa công tiêu diệt quân ta","Mưu kế Gia Cát Lượng, dùng hỏa công tiêu diệt quân ta",},
        [4062] = {7701,"Tào Tháo","Hạ Hầu Đôn","Ngươi dùng binh từ thuở bé, chẳng lẽ không biết rằng ở chốn hiểm phải phòng hoả công à?","Ngươi dùng binh từ thuở bé, chẳng lẽ không biết rằng ở chốn hiểm phải phòng hoả công à?",},
        [4063] = {7701,"Hạ Hầu Đôn","Tào Tháo","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!",},
        [4064] = {7701,"Tào Tháo","Hạ Hầu Đôn","Thắng thua là chuyện thường, đừng lo lắng nữa","Thắng thua là chuyện thường, đừng lo lắng nữa",},
        [4065] = {7701,"Hạ Hầu Đôn","Tào Tháo","Lưu Bị hung hăng như thế, thật là cái hoạ lớn không thể không trừ ngay đi.","Lưu Bị hung hăng như thế, thật là cái hoạ lớn không thể không trừ ngay đi.",},
        [4066] = {7701,"Tào Tháo","Hạ Hầu Đôn","Ta cũng chỉ lo có Lưu Bị và Tôn Quyền thôi, còn những bọn khác có ngại gì. Nay nên nhân dịp này bình định Giang Nam mới được.","Ta cũng chỉ lo có Lưu Bị và Tôn Quyền thôi, còn những bọn khác có ngại gì. Nay nên nhân dịp này bình định Giang Nam mới được.",},
        [4067] = {7701,"Tào Tháo","Hạ Hầu Đôn","Huy động 50 vạn quân, sai Tào Nhân và Tào Hồng làm đội thứ nhất","Huy động 50 vạn quân, sai Tào Nhân và Tào Hồng làm đội thứ nhất",},
        [4068] = {7701,"Tào Tháo","Hạ Hầu Đôn","Trương Liêu và Trương Cáp làm đội thứ nhì; Hạ Hầu Đôn và Hạ Hầu Uyên làm đội thứ ba; Vu Cấm và Lý Điển làm đội thứ tư. Tháo tự lĩnh các tướng làm đội thứ năm. Mỗi đội dẫn mười vạn quân","Trương Liêu và Trương Cáp làm đội thứ nhì; Hạ Hầu Đôn và Hạ Hầu Uyên làm đội thứ ba; Vu Cấm và Lý Điển làm đội thứ tư. Tháo tự lĩnh các tướng làm đội thứ năm. Mỗi đội dẫn mười vạn quân",},
        [4069] = {7701,"Tào Tháo","Hạ Hầu Đôn","Hứa Chử làm Chiết Xung Tướng, dẫn ba nghìn quân đi tiên phong, lại chọn ngày Bính Ngọ, tháng bảy, năm Kiến An thứ 13 xuất phát.","Hứa Chử làm Chiết Xung Tướng, dẫn ba nghìn quân đi tiên phong, lại chọn ngày Bính Ngọ, tháng bảy, năm Kiến An thứ 13 xuất phát.",},
        [4070] = {7702,"Khổng Dung","Tào Tháo","Lưu Bị, Lưu Biểu dòng dõi hoàng tộc. Tôn Quyền nắm sáu quận, lại được sông Trường Giang hiểm trở, cũng không dễ lấy. Nay thừa tướng huy động đạo quân vô đạo ấy, e trái với nguyện vọng của thiên hạ.","Lưu Bị, Lưu Biểu dòng dõi hoàng tộc. Tôn Quyền nắm sáu quận, lại được sông Trường Giang hiểm trở, cũng không dễ lấy. Nay thừa tướng huy động đạo quân vô đạo ấy, e trái với nguyện vọng của thiên hạ.",},
        [4071] = {7702,"Tào Tháo","Khổng Dung","Lưu Bị, Lưu Biểu, Tôn Quyền đều là thần tử phản nghịch, phải thảo phạt!","Lưu Bị, Lưu Biểu, Tôn Quyền đều là thần tử phản nghịch, phải thảo phạt!",},
        [4072] = {7702,"Tào Tháo","Khổng Dung","Lui xuống! Nếu còn tái phạm, trảm ngay lập tức","Lui xuống! Nếu còn tái phạm, trảm ngay lập tức",},
        [4073] = {7703,"Khổng Dung","","Hừ!","Hừ!",},
        [4074] = {7703,"Khổng Dung","","Kẻ bất nhân thảo phạt nhân nghĩa, ắt thất bại!","Kẻ bất nhân thảo phạt nhân nghĩa, ắt thất bại!",},
        [4075] = {7704,"Si Lự","Tào Tháo","Khổng Dung ngày thường vẫn khinh thừa tướng, lại chơi thân với Nễ Hành, Nễ Hành tán dương Khổng Dung là Trọng Mỗ bất tử. Khổng Dung lại gọi Nễ Hành là Nhan Hồi tái sinh.","Khổng Dung ngày thường vẫn khinh thừa tướng, lại chơi thân với Nễ Hành, Nễ Hành tán dương Khổng Dung là Trọng Mỗ bất tử. Khổng Dung lại gọi Nễ Hành là Nhan Hồi tái sinh.",},
        [4076] = {7704,"Si Lự","Tào Tháo","Lần trước Nễ Hành nói xấu thừa tướng, cũng là tự Khổng Dung xui đó.","Lần trước Nễ Hành nói xấu thừa tướng, cũng là tự Khổng Dung xui đó.",},
        [4077] = {7704,"Si Lự","Tào Tháo","Hôm qua khi thừa tướng ra khỏi phủ, có nói: Dĩ Chí Bất Nhân Phạt Chí Nhân","Hôm qua khi thừa tướng ra khỏi phủ, có nói: Dĩ Chí Bất Nhân Phạt Chí Nhân",},
        [4078] = {7704,"Tào Tháo","Si Lự","Khổng Dung hay lắm!","Khổng Dung hay lắm!",},
        [4079] = {7704,"Tào Tháo","Si Lự","Đem Khổng Dung cùng 2 con, xử trảm hết","Đem Khổng Dung cùng 2 con, xử trảm hết",},
        [4080] = {7801,"Lưu Biểu","Lưu Bị","Bệnh ta đã nguy kịch, không biết ra đi lúc nào, hiền đệ hãy nuôi nấng con ta. Nhưng con ta bất tài, sợ không nối được nghiệp bố. Sau khi ta đi, xin hiền đệ hãy cai quản Kinh Châu","Bệnh ta đã nguy kịch, không biết ra đi lúc nào, hiền đệ hãy nuôi nấng con ta. Nhưng con ta bất tài, sợ không nối được nghiệp bố. Sau khi ta đi, xin hiền đệ hãy cai quản Kinh Châu",},
        [4081] = {7801,"Lưu Bị","Lưu Biểu","Ta sẽ giúp đỡ hết sức, đâu dám có ý gì khác.","Ta sẽ giúp đỡ hết sức, đâu dám có ý gì khác.",},
        [4082] = {7801,"Lưu Bị","Lưu Biểu","Tào Tháo tự thống lĩnh đại binh đến đánh. Huyền Đức vội vàng về lại Tân Dã.","Tào Tháo tự thống lĩnh đại binh đến đánh. Huyền Đức vội vàng về lại Tân Dã.",},
        [4083] = {7802,"Lưu Kỳ","Thái Mạo","Ta ở tại Giang Hạ, biết tin phụ thân ốm, liền đến thăm","Ta ở tại Giang Hạ, biết tin phụ thân ốm, liền đến thăm",},
        [4084] = {7802,"Thái Mạo","Lưu Kỳ","Công tử phụng mệnh trấn giữ Giang Hạ, trách nhiệm rất nặng, nay dám tự tiện bỏ về, Đông Ngô kéo đến, thì làm thế nào?","Công tử phụng mệnh trấn giữ Giang Hạ, trách nhiệm rất nặng, nay dám tự tiện bỏ về, Đông Ngô kéo đến, thì làm thế nào?",},
        [4085] = {7802,"Thái Mạo","Lưu Kỳ","Nếu vào gặp chúa công, chúa công nổi giận, bệnh lại nặng thêm, đó là bất hiếu. Công tử nên cấp tốc về ngay đi!","Nếu vào gặp chúa công, chúa công nổi giận, bệnh lại nặng thêm, đó là bất hiếu. Công tử nên cấp tốc về ngay đi!",},
        [4086] = {7802,"Lưu Kỳ","Thái Mạo","Cái này...","Cái này...",},
        [4087] = {7802,"Lưu Kỳ","Thái Mạo","Xin hãy chăm sóc chúa công, nếu có biến hãy báo tin cho ta biết!","Xin hãy chăm sóc chúa công, nếu có biến hãy báo tin cho ta biết!",},
        [4088] = {7803,"Thái Phu Nhân","Thái Mạo","Lưu Biểu đã mất, hiện giờ phải làm gì?","Lưu Biểu đã mất, hiện giờ phải làm gì?",},
        [4089] = {7803,"Thái Mạo","Thái Phu Nhân","Làm giả di chúc, để con trai thứ 2 là Lưu Tông làm chủ Kinh Châu.","Làm giả di chúc, để con trai thứ 2 là Lưu Tông làm chủ Kinh Châu.",},
        [4090] = {7803,"Thái Phu Nhân","Thái Mạo","Được!","Được!",},
        [4091] = {7804,"Lưu Tông","","Cha ta tạ thế, anh ta hiện ở Giang Hạ, chú ta là Huyền Đức ở Tân Dã. Các ngươi lập ta làm chủ, nếu chú ta và anh ta về hỏi tội, thì ăn nói làm sao?","Cha ta tạ thế, anh ta hiện ở Giang Hạ, chú ta là Huyền Đức ở Tân Dã. Các ngươi lập ta làm chủ, nếu chú ta và anh ta về hỏi tội, thì ăn nói làm sao?",},
        [4092] = {7804,"Lý Khuê","Lưu Tông","Công tử nói rất phải. Nay nên đưa thư cáo tang đến Giang Hạ, mời đại công tử về làm chủ Kinh Châu rồi cử Huyền Đức cùng coi việc lớn, đó là kế sách hoàn hảo","Công tử nói rất phải. Nay nên đưa thư cáo tang đến Giang Hạ, mời đại công tử về làm chủ Kinh Châu rồi cử Huyền Đức cùng coi việc lớn, đó là kế sách hoàn hảo",},
        [4093] = {7804,"Lý Khuê","Lưu Tông","Bắc chống Tào Tháo, Nam chống Tôn Quyền. Đây là kế hoạch hay nhất.","Bắc chống Tào Tháo, Nam chống Tôn Quyền. Đây là kế hoạch hay nhất.",},
        [4094] = {7804,"Thái Mạo","Lý Khuê","Ngươi là ai, sao dám nói điều trái lời chúa công!","Ngươi là ai, sao dám nói điều trái lời chúa công!",},
        [4095] = {7804,"Lý Khuê","Thái Mạo","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!",},
        [4096] = {7804,"Thái Mạo","Lý Khuê","Lôi ra ngoài xử trảm!","Lôi ra ngoài xử trảm!",},
        [4097] = {7805,"Lưu Tông","Vương Sán","Tào Tháo dẫn quân đến rồi, phải làm sao đây","Tào Tháo dẫn quân đến rồi, phải làm sao đây",},
        [4098] = {7805,"Vương Sán","Lưu Tông","Tướng quân chuẩn bị so với Tào Công như thế nào?","Tướng quân chuẩn bị so với Tào Công như thế nào?",},
        [4099] = {7805,"Lưu Tông","Vương Sán","Không bằng","Không bằng",},
        [4100] = {7805,"Vương Sán","Lưu Tông","Tào công binh cường tướng khoẻ, đa mưu túc trí, bắt sống Lữ Bố ở Hạ Phì, phá vỡ Quan Thiệu ở Quan Độ, đuổi Lưu Bị ở Lũng Hữu, trừ Ô Hoàn ở Bạch Lang, chiến công hiển hách","Tào công binh cường tướng khoẻ, đa mưu túc trí, bắt sống Lữ Bố ở Hạ Phì, phá vỡ Quan Thiệu ở Quan Độ, đuổi Lưu Bị ở Lũng Hữu, trừ Ô Hoàn ở Bạch Lang, chiến công hiển hách",},
        [4101] = {7805,"Vương Sán","Lưu Tông","Nay lại kéo đại quân xuống Nam Hạ, thế Kinh Tương thực khó lòng địch nổi. Kế của hai ông Phó, Khoái rất hay. Tướng quân nên quyết ngay đi, kẻo về sau lại hối hân.","Nay lại kéo đại quân xuống Nam Hạ, thế Kinh Tương thực khó lòng địch nổi. Kế của hai ông Phó, Khoái rất hay. Tướng quân nên quyết ngay đi, kẻo về sau lại hối hân.",},
        [4102] = {7805,"Lưu Tông","Vương Sán","Ngài dạy rất phải, vậy mẫu thân thấy thế nào?","Ngài dạy rất phải, vậy mẫu thân thấy thế nào?",},
        [4103] = {7805,"Thái Phu Nhân","Lưu Tông","Ta đồng ý đầu hàng","Ta đồng ý đầu hàng",},
        [4104] = {7805,"Lưu Tông","Thái Phu Nhân","Vậy ta sẽ dâng thư xin hàng","Vậy ta sẽ dâng thư xin hàng",},
        [4105] = {7806,"Lưu Bị","","Lưu Kỳ trấn thủ Giang Hạ, Thái Phu Nhân cùng Thái Mạo viết di chúc giả cho Lưu Tôn làm chủ Kinh Châu, xong rồi mới cho báo tang.","Lưu Kỳ trấn thủ Giang Hạ, Thái Phu Nhân cùng Thái Mạo viết di chúc giả cho Lưu Tôn làm chủ Kinh Châu, xong rồi mới cho báo tang.",},
        [4106] = {7806,"Lưu Bị","","Ngài chưa biết Lưu Tôn đã đem cả chín quận Kinh Tương dâng lên Tào Tháo rồi!","Ngài chưa biết Lưu Tôn đã đem cả chín quận Kinh Tương dâng lên Tào Tháo rồi!",},
        [4107] = {7806,"Lưu Bị","","Lúc anh ta sắp mất đã gửi con cho ta. Nếu ta bắt lấy con, cướp lấy đất, thì sau này xuống chín suối còn mặt mũi nào trông thấy anh ta nữa!","Lúc anh ta sắp mất đã gửi con cho ta. Nếu ta bắt lấy con, cướp lấy đất, thì sau này xuống chín suối còn mặt mũi nào trông thấy anh ta nữa!",},
        [4108] = {7806,"Nhân vật","Lưu Bị","Đáng ghét, ta sẽ đi giáo huấn họ","Đáng ghét, ta sẽ đi giáo huấn họ",},
        [4109] = {7807,"Lưu Bị","Gia Cát Lượng","Quân Tào đã đến Uyển Thành, làm cách nào chống đỡ?","Quân Tào đã đến Uyển Thành, làm cách nào chống đỡ?",},
        [4110] = {7807,"Gia Cát Lượng","Lưu Bị","Không chiến, mau đến Phàn Thành tránh địch","Không chiến, mau đến Phàn Thành tránh địch",},
        [4111] = {7807,"Lưu Bị","Gia Cát Lượng","Dựa vào kế của Khổng Minh","Dựa vào kế của Khổng Minh",},
        [4112] = {7901,"Lưu Bị","Gia Cát Lượng","Quân Tào đã đến Bác Vọng, Khổng Minh mau cùng ta bàn kế chống địch","Quân Tào đã đến Bác Vọng, Khổng Minh mau cùng ta bàn kế chống địch",},
        [4113] = {7901,"Gia Cát Lượng","Lưu Bị","Chúa công bình tĩnh. Lần trước ta đốt hơn nửa số quân của Hạ Hầu Đôn. Lần này, quân Tào lại đến, phải làm bọn chúng trúng phải kế trước","Chúa công bình tĩnh. Lần trước ta đốt hơn nửa số quân của Hạ Hầu Đôn. Lần này, quân Tào lại đến, phải làm bọn chúng trúng phải kế trước",},
        [4114] = {7901,"Lưu Bị","Gia Cát Lượng","Kế gì?","Kế gì?",},
        [4115] = {7901,"Gia Cát Lượng","Lưu Bị","Vẫn còn việc phải làm.","Vẫn còn việc phải làm.",},
        [4116] = {7901,"Gia Cát Lượng","Lưu Bị","Ta phải báo tin cho người dân ở Tân Dã, đi cùng ta đến Phàn Thành tránh địch.","Ta phải báo tin cho người dân ở Tân Dã, đi cùng ta đến Phàn Thành tránh địch.",},
        [4117] = {7902,"Lưu Bị","Gia Cát Lượng","Chống địch bằng cách nào?","Chống địch bằng cách nào?",},
        [4118] = {7902,"Gia Cát Lượng","Lưu Bị","Tôn Càn cho thuyền giải cứu bá tánh, My Trúc hộ tống các quan đến Phàn Thành. Vân Trường đem 1000 quân đến thượng lưu sông Bạch Hà mai phục, mang theo nhiều bao tải đựng đầy cát để lấp khúc sông","Tôn Càn cho thuyền giải cứu bá tánh, My Trúc hộ tống các quan đến Phàn Thành. Vân Trường đem 1000 quân đến thượng lưu sông Bạch Hà mai phục, mang theo nhiều bao tải đựng đầy cát để lấp khúc sông",},
        [4119] = {7902,"Gia Cát Lượng","Lưu Bị","Cuối canh ba hôm sau, hễ nghe tiếng người ngựa ở hạ lưu thì vớt những túi cát lên cho nước tràn xuống, thuận theo dòng nước đến tiếp ứng.","Cuối canh ba hôm sau, hễ nghe tiếng người ngựa ở hạ lưu thì vớt những túi cát lên cho nước tràn xuống, thuận theo dòng nước đến tiếp ứng.",},
        [4120] = {7902,"Gia Cát Lượng","Lưu Bị","Trương Phi dẫn 1000 quân mai phục ở bến đò Bác Lăng. Khúc sông này nước chảy chậm, quân Tào bị ngập sẽ trốn qua lối đó, bấy giờ thừa thế tiếp ứng.","Trương Phi dẫn 1000 quân mai phục ở bến đò Bác Lăng. Khúc sông này nước chảy chậm, quân Tào bị ngập sẽ trốn qua lối đó, bấy giờ thừa thế tiếp ứng.",},
        [4121] = {7902,"Gia Cát Lượng","Lưu Bị","Triệu Vân dẫn 3000 quân, chia làm 4 đội. Vân tự lĩnh 1 đội quân mai phục phía ngoài cổng phía đông. 3 đội còn lại phân chia mai phục các hướng. Những ngôi nhà trong thành, gài những vật bắt lửa","Triệu Vân dẫn 3000 quân, chia làm 4 đội. Vân tự lĩnh 1 đội quân mai phục phía ngoài cổng phía đông. 3 đội còn lại phân chia mai phục các hướng. Những ngôi nhà trong thành, gài những vật bắt lửa",},
        [4122] = {7902,"Gia Cát Lượng","Lưu Bị","Quân Tào vào thành tất phải nghỉ ở nhà dân. Chiều tối hôm sau thế nào cũng có gió lớn. Hễ nổi gió,quân phục ở 3 cửa tây, nam, bắc bắn tên lửa vào thành","Quân Tào vào thành tất phải nghỉ ở nhà dân. Chiều tối hôm sau thế nào cũng có gió lớn. Hễ nổi gió,quân phục ở 3 cửa tây, nam, bắc bắn tên lửa vào thành",},
        [4123] = {7902,"Gia Cát Lượng","Lưu Bị","Lúc lửa bốc lên, bên ngoài hò reo trợ oai. Cửa đông bỏ ngỏ cho giặc chạy. Khi giặc chạy ra, thừa thế đuổi đánh","Lúc lửa bốc lên, bên ngoài hò reo trợ oai. Cửa đông bỏ ngỏ cho giặc chạy. Khi giặc chạy ra, thừa thế đuổi đánh",},
        [4124] = {7902,"Gia Cát Lượng","Lưu Bị","Trời sáng, Quan Vũ Trương Phi thu quân về Phàn Thành. Mi Phương, Lưu Phong dẫn dắt 2000 quân, một nửa cờ đỏ, một nửa cờ xanh đi đến ngoại thành Tân Dã lập đồn trú.","Trời sáng, Quan Vũ Trương Phi thu quân về Phàn Thành. Mi Phương, Lưu Phong dẫn dắt 2000 quân, một nửa cờ đỏ, một nửa cờ xanh đi đến ngoại thành Tân Dã lập đồn trú.",},
        [4125] = {7902,"Gia Cát Lượng","Lưu Bị","Nhìn thấy quân Tào đến, quân cờ đỏ bên trái, quân cờ xanh bên phải, hắn hoài nghi ắt sẽ không dám đuổi theo. Hai người họ sẽ đến mai phục.","Nhìn thấy quân Tào đến, quân cờ đỏ bên trái, quân cờ xanh bên phải, hắn hoài nghi ắt sẽ không dám đuổi theo. Hai người họ sẽ đến mai phục.",},
        [4126] = {7902,"Gia Cát Lượng","Lưu Bị","Đợi trong thành nổi lửa, sẽ đuổi theo bại binh, đi lên thượng lưu Bạch Hà để tiếp ứng.","Đợi trong thành nổi lửa, sẽ đuổi theo bại binh, đi lên thượng lưu Bạch Hà để tiếp ứng.",},
        [4127] = {7902,"Lưu Bị","Gia Cát Lượng","Các tướng lĩnh hãy theo kế mà hành động","Các tướng lĩnh hãy theo kế mà hành động",},
        [4128] = {7903,"Hứa Chử","Tào Nhân","Gò Thước Vĩ, một toán quân mã cầm cờ hiệu xanh đỏ.","Gò Thước Vĩ, một toán quân mã cầm cờ hiệu xanh đỏ.",},
        [4129] = {7903,"Hứa Chử","Tào Nhân","Phía trước có mai phục. Quân ta chỉ có thể đi xuống từ đây.","Phía trước có mai phục. Quân ta chỉ có thể đi xuống từ đây.",},
        [4130] = {7903,"Tào Nhân","Hứa Chử","Dừng lại, ở đây chắc chắn có mai phục, quân ta đóng ở đây thôi.","Dừng lại, ở đây chắc chắn có mai phục, quân ta đóng ở đây thôi.",},
        [4131] = {7904,"Tào Nhân","","Trong thành không một bóng người","Trong thành không một bóng người",},
        [4132] = {7904,"Tào Nhân","","Không có mai phục, bá tánh ở đây chắc trốn cả rồi, ta hãy mau vào thành nghỉ ngơi, ngày mai tiếp tục tiến binh","Không có mai phục, bá tánh ở đây chắc trốn cả rồi, ta hãy mau vào thành nghỉ ngơi, ngày mai tiếp tục tiến binh",},
        [4133] = {7904,"Tào Nhân","","Lúc này quân sĩ đều mệt và đói, giành nhau ăn cơm","Lúc này quân sĩ đều mệt và đói, giành nhau ăn cơm",},
        [4134] = {7905,"Hứa Chử","Tào Nhân","Lửa bốc lên ở cửa tây, nam, bắc","Lửa bốc lên ở cửa tây, nam, bắc",},
        [4135] = {7905,"Tào Nhân","Hứa Chử","Cửa đông không cháy, chạy về cửa đông","Cửa đông không cháy, chạy về cửa đông",},
        [4136] = {7905,"Triệu Vân","Tào Nhân","Tào Nhân, chạy đi đâu!","Tào Nhân, chạy đi đâu!",},
        [4137] = {7905,"My Phương","Tào Nhân","Đám giặc kia, chạy đâu cho thoát!","Đám giặc kia, chạy đâu cho thoát!",},
        [4138] = {7905,"Trương Phi","Tào Nhân","Tào tặc hãy nạp mạng!","Tào tặc hãy nạp mạng!",},
        [4139] = {7906,"Tào Nhân","Tào Tháo","Thừa tướng, lần này thất bại rồi!","Thừa tướng, lần này thất bại rồi!",},
        [4140] = {7906,"Tào Tháo","Tào Nhân","Thắng thua là chuyện thường, đừng lo lắng nữa","Thắng thua là chuyện thường, đừng lo lắng nữa",},
        [4141] = {7906,"Tào Tháo","Tào Nhân","Gia Cát thôn phu, to gan thật!","Gia Cát thôn phu, to gan thật!",},
        [4142] = {7906,"Tào Tháo","Tào Nhân","Lập tức đem quân đông như kiến cỏ, kéo đến Tân Dã đóng trại. Sai quân sĩ, một mặt kiểm soát trên núi, một mặt lấp sông Bạch Hà","Lập tức đem quân đông như kiến cỏ, kéo đến Tân Dã đóng trại. Sai quân sĩ, một mặt kiểm soát trên núi, một mặt lấp sông Bạch Hà",},
        [4143] = {7906,"Tào Tháo","Tào Nhân","Chia đại quân làm tám đường, đến lấy Phàn Thành","Chia đại quân làm tám đường, đến lấy Phàn Thành",},
        [4144] = {8001,"Lưu Diệp","Tào Tháo","Thừa tướng mới đến Tương Dương, hãy lấy lòng dân đã. Nay Lưu Bị dời dân Tân Dã vào Phàn Thành, nếu ta đến ngay thì dân hai huyện không vui. Chi bằng phái người chiêu hàng Lưu Bị","Thừa tướng mới đến Tương Dương, hãy lấy lòng dân đã. Nay Lưu Bị dời dân Tân Dã vào Phàn Thành, nếu ta đến ngay thì dân hai huyện không vui. Chi bằng phái người chiêu hàng Lưu Bị",},
        [4145] = {8001,"Lưu Diệp","Tào Tháo","Nếu Lưu Bị không hàng, mình cũng được mang tiếng là thương dân. Nếu Lưu Bị đầu hàng, đất Kinh Châu không đánh cũng được không?","Nếu Lưu Bị không hàng, mình cũng được mang tiếng là thương dân. Nếu Lưu Bị đầu hàng, đất Kinh Châu không đánh cũng được không?",},
        [4146] = {8001,"Tào Tháo","Lưu Diệp","Ai đi sứ được?","Ai đi sứ được?",},
        [4147] = {8001,"Lưu Diệp","Tào Tháo","Từ Thứ và Lưu Bị có tình cảm sâu đậm, sao không bảo hắn đến đây?","Từ Thứ và Lưu Bị có tình cảm sâu đậm, sao không bảo hắn đến đây?",},
        [4148] = {8001,"Tào Tháo","Lưu Diệp","E hắn đi không trở về","E hắn đi không trở về",},
        [4149] = {8001,"Lưu Diệp","Tào Tháo","Nếu hắn đi không trở về, thiên hạ sẽ chê cười, thừa tướng không phải lo","Nếu hắn đi không trở về, thiên hạ sẽ chê cười, thừa tướng không phải lo",},
        [4150] = {8002,"Tào Tháo","Từ Thứ","Ta muốn san bằng Phàn Thành, nhưng lại thương dân. Ông hãy đến dụ Lưu Bị, nếu Lưu Bị đầu hàng, ta sẽ tha tội và phong tước. Nếu hắn ngoan cố thì quân dân đều chết.","Ta muốn san bằng Phàn Thành, nhưng lại thương dân. Ông hãy đến dụ Lưu Bị, nếu Lưu Bị đầu hàng, ta sẽ tha tội và phong tước. Nếu hắn ngoan cố thì quân dân đều chết.",},
        [4151] = {8002,"Tào Tháo","Từ Thứ","Ta biết ông là người trung nghĩa, nên mới nhờ làm việc này, xin đừng phụ ta","Ta biết ông là người trung nghĩa, nên mới nhờ làm việc này, xin đừng phụ ta",},
        [4152] = {8002,"Từ Thứ","Tào Tháo","Tại hạ sẵn lòng","Tại hạ sẵn lòng",},
        [4153] = {8003,"Từ Thứ","Lưu Bị","Tào Tháo sai tôi đến đây dụ sứ quân, giả danh mua chuộc lòng người. Nay Tháo chia quân làm 8 ngã, lấp sông Bạch Hà mà tiến, tôi e Phàn Thành không giữ được, ông nên đi chỗ khác.","Tào Tháo sai tôi đến đây dụ sứ quân, giả danh mua chuộc lòng người. Nay Tháo chia quân làm 8 ngã, lấp sông Bạch Hà mà tiến, tôi e Phàn Thành không giữ được, ông nên đi chỗ khác.",},
        [4154] = {8003,"Lưu Bị","Từ Thứ","Ông đã đến đây, hà tất quay về","Ông đã đến đây, hà tất quay về",},
        [4155] = {8003,"Từ Thứ","Lưu Bị","Nếu không trở về, thiên hạ sẽ chê cười. Mẫu thân đã mất, đành ôm hận suốt đời. Tôi ở bên đó nhưng thề chết không bày kế. Ông có Ngoạ Long giúp đỡ, lo gì nghiệp lớn chẳng thành.","Nếu không trở về, thiên hạ sẽ chê cười. Mẫu thân đã mất, đành ôm hận suốt đời. Tôi ở bên đó nhưng thề chết không bày kế. Ông có Ngoạ Long giúp đỡ, lo gì nghiệp lớn chẳng thành.",},
        [4156] = {8003,"Lưu Bị","Từ Thứ","Tôi không ép ông, cáo từ","Tôi không ép ông, cáo từ",},
        [4157] = {8004,"Từ Thứ","Tào Tháo","Bẩm thừa tướng, Lưu Bị không chịu hàng.","Bẩm thừa tướng, Lưu Bị không chịu hàng.",},
        [4158] = {8004,"Tào Tháo","Từ Thứ","Ngay trong ngày hạ lệnh tiến quân.","Ngay trong ngày hạ lệnh tiến quân.",},
        [4159] = {8005,"Tôn Càn","Lưu Bị","Trăm dân ai muốn đi theo thì cùng sang sông!","Trăm dân ai muốn đi theo thì cùng sang sông!",},
        [4160] = {8005,"Lưu Bị","Tôn Càn","Vì một mình ta mà khiến bá tánh chịu nạn, ta không đành lòng!","Vì một mình ta mà khiến bá tánh chịu nạn, ta không đành lòng!",},
        [4161] = {8005,"Tôn Càn","Lưu Bị","Huyền Đức không nên tự trách!","Huyền Đức không nên tự trách!",},
        [4162] = {8005,"Tôn Càn","Lưu Bị","Quân Tào tấn công, mau qua sông!","Quân Tào tấn công, mau qua sông!",},
        [4163] = {8005,"Lưu Bị","Tôn Càn","Bảo vệ trăm dân, đến bờ phía nam, tiến thẳng Tương Dương","Bảo vệ trăm dân, đến bờ phía nam, tiến thẳng Tương Dương",},
        [4164] = {8006,"Lưu Bị","Lưu Tông","Hiền điệt Lưu Tôn, ta vì cứu trăm dân mà đến đây, không có ý gì khác, mau mở cửa ra","Hiền điệt Lưu Tôn, ta vì cứu trăm dân mà đến đây, không có ý gì khác, mau mở cửa ra",},
        [4165] = {8006,"Lưu Tông","Lưu Bị","Mở cửa ắt sẽ có nguy hiểm!","Mở cửa ắt sẽ có nguy hiểm!",},
        [4166] = {8006,"Ngụy Diên","Lưu Bị","Thái Mạo, Trương Doãn là giặc bán nước! Lưu sứ quân vốn nhân từ, nay vì cứu dân đến đây, sao dám chống cự?","Thái Mạo, Trương Doãn là giặc bán nước! Lưu sứ quân vốn nhân từ, nay vì cứu dân đến đây, sao dám chống cự?",},
        [4167] = {8006,"Ngụy Diên","Lưu Bị","Tôi bỏ cầu treo xuống, Lưu hoàng thúc mau mang quân vào thành tiêu diệt hết bọn cấu kết bán nước","Tôi bỏ cầu treo xuống, Lưu hoàng thúc mau mang quân vào thành tiêu diệt hết bọn cấu kết bán nước",},
        [4168] = {8006,"Lưu Bị","Ngụy Diên","Không được làm trăm dân sợ hãi!","Không được làm trăm dân sợ hãi!",},
        [4169] = {8007,"Lưu Bị","","Ai ngờ muốn cứu dân lại hoá ra hại dân! Ta không muốn vào Tương Dương nữa.","Ai ngờ muốn cứu dân lại hoá ra hại dân! Ta không muốn vào Tương Dương nữa.",},
        [4170] = {8007,"Gia Cát Lượng","Lưu Bị","Giang Lăng là một nơi hiểm yếu ở Kinh Châu, chi bằng ta hãy ra lấy trước để lập căn cứ!","Giang Lăng là một nơi hiểm yếu ở Kinh Châu, chi bằng ta hãy ra lấy trước để lập căn cứ!",},
        [4171] = {8007,"Lưu Bị","Gia Cát Lượng","Rất hợp ý ta!","Rất hợp ý ta!",},
        [4172] = {8007,"Lưu Bị","Gia Cát Lượng","Lập tức đem dân dời Tương Dương, chạy đến Giang Lăng.","Lập tức đem dân dời Tương Dương, chạy đến Giang Lăng.",},
        [4173] = {8101,"Tào Tháo","","Khoái Việt là Giang Lăng Thái Thú Phàn Thành Hầu, Phó Tốn, Vương Xán là quan nội hầu, Lưu Tông là Thanh Châu Thứ Sử, ngày mai nhậm chức.","Khoái Việt là Giang Lăng Thái Thú Phàn Thành Hầu, Phó Tốn, Vương Xán là quan nội hầu, Lưu Tông là Thanh Châu Thứ Sử, ngày mai nhậm chức.",},
        [4174] = {8101,"Lưu Tông","Tào Tháo","Ta không muốn làm quan, chỉ muốn bảo vệ quê hương cha mẹ.","Ta không muốn làm quan, chỉ muốn bảo vệ quê hương cha mẹ.",},
        [4175] = {8101,"Tào Tháo","Lưu Tông","Thanh Châu gần Đế Đô, muốn ngươi làm quan, tránh bị hãm hại ở Kinh Tương.","Thanh Châu gần Đế Đô, muốn ngươi làm quan, tránh bị hãm hại ở Kinh Tương.",},
        [4176] = {8101,"Lưu Tông","Tào Tháo","Mời thừa tướng thu hồi bổ nhiệm.","Mời thừa tướng thu hồi bổ nhiệm.",},
        [4177] = {8101,"Tào Tháo","Lưu Tông","Sắp nhậm chức! Lui xuống.","Sắp nhậm chức! Lui xuống.",},
        [4178] = {8101,"Lưu Tông","Tào Tháo","Dạ...","Dạ...",},
        [4179] = {8101,"Tào Tháo","","Vu Cấm ở đâu?","Vu Cấm ở đâu?",},
        [4180] = {8101,"Vu Cấm","Tào Tháo","Có thần.","Có thần.",},
        [4181] = {8101,"Tào Tháo","Vu Cấm","Ngươi diệt khẩu mẹ con Lưu Tông để trừ hậu hoạn.","Ngươi diệt khẩu mẹ con Lưu Tông để trừ hậu hoạn.",},
        [4182] = {8101,"Vu Cấm","Tào Tháo","Thần nhận lệnh.","Thần nhận lệnh.",},
        [4183] = {8102,"Vu Cấm","Thái Phu Nhân","Ta phụng lệnh hành sự, đến lấy mạng mẹ con ngươi! Mau nộp mạng!","Ta phụng lệnh hành sự, đến lấy mạng mẹ con ngươi! Mau nộp mạng!",},
        [4184] = {8102,"Thái Phu Nhân","Vu Cấm","Xin tướng quân tha cho mẹ con ta!","Xin tướng quân tha cho mẹ con ta!",},
        [4185] = {8102,"Vu Cấm","Thái Phu Nhân","Tướng sĩ hành động thôi!","Tướng sĩ hành động thôi!",},
        [4186] = {8103,"Vu Cấm","Tào Tháo","Lưu Bị dẫn dắt bá tánh ngày đi 10 dặm, tổng có 300 dặm.","Lưu Bị dẫn dắt bá tánh ngày đi 10 dặm, tổng có 300 dặm.",},
        [4187] = {8103,"Tào Tháo","Vu Cấm","Chọn 500 thiết kỵ, liên tục lên đường đuổi kịp Lưu Bị.","Chọn 500 thiết kỵ, liên tục lên đường đuổi kịp Lưu Bị.",},
        [4188] = {8103,"Tào Tháo","Vu Cấm","Đại quân liên tục ùa vào.","Đại quân liên tục ùa vào.",},
        [4189] = {8104,"Lưu Bị","","Gió lớn, bụi che lắp mặt trời, điềm gì?","Gió lớn, bụi che lắp mặt trời, điềm gì?",},
        [4190] = {8104,"Giản Ung","Lưu Bị","Điềm xấu. Vào đêm nay. Chúa công mau bỏ bá tánh lại.","Điềm xấu. Vào đêm nay. Chúa công mau bỏ bá tánh lại.",},
        [4191] = {8104,"Lưu Bị","Giản Ung","Bá tánh theo từ Tân Dã đến đây, sao ta có thể bỏ rơi họ?","Bá tánh theo từ Tân Dã đến đây, sao ta có thể bỏ rơi họ?",},
        [4192] = {8104,"Giản Ung","Lưu Bị","Chúa công nếu không bỏ, tai họa sẽ ập đến.","Chúa công nếu không bỏ, tai họa sẽ ập đến.",},
        [4193] = {8104,"Lưu Bị","Giản Ung","Phía trước là nơi nào?","Phía trước là nơi nào?",},
        [4194] = {8104,"Giản Ung","Lưu Bị","Là huyện Đương Dương. Có ngọn núi Cảnh Sơn.","Là huyện Đương Dương. Có ngọn núi Cảnh Sơn.",},
        [4195] = {8104,"Lưu Bị","Giản Ung","Dừng chân ở đây.","Dừng chân ở đây.",},
        [4196] = {8105,"My Phương","Lưu Bị","Triệu Tử Long đã đầu quân Tào Tháo rồi!","Triệu Tử Long đã đầu quân Tào Tháo rồi!",},
        [4197] = {8105,"Lưu Bị","My Phương","Tử Long là bạn cũ của ta, sao lại nuốt lời?","Tử Long là bạn cũ của ta, sao lại nuốt lời?",},
        [4198] = {8105,"Trương Phi","Lưu Bị","Nay hắn thấy ta thế cùng lực tận, hoặc đầu quân Tào Tháo, mưu cầu phú quý!","Nay hắn thấy ta thế cùng lực tận, hoặc đầu quân Tào Tháo, mưu cầu phú quý!",},
        [4199] = {8105,"Lưu Bị","Trương Phi","Tử Long cùng ta qua hoạn nạn, không thể lung lay vì phú quý.","Tử Long cùng ta qua hoạn nạn, không thể lung lay vì phú quý.",},
        [4200] = {8105,"My Phương","Lưu Bị","Ta đã thấy hắn đi về hướng Tây Bắc.","Ta đã thấy hắn đi về hướng Tây Bắc.",},
        [4201] = {8105,"Trương Phi","Lưu Bị","Ta sẽ đi tìm. Nếu gặp sẽ diệt ngay!","Ta sẽ đi tìm. Nếu gặp sẽ diệt ngay!",},
        [4202] = {8105,"Lưu Bị","Trương Phi","Nếu không gặp nhị huynh Chu Nhan Lương, Văn Xú phải làm sao? Ta nghĩ Tử Long sẽ không bỏ ta.","Nếu không gặp nhị huynh Chu Nhan Lương, Văn Xú phải làm sao? Ta nghĩ Tử Long sẽ không bỏ ta.",},
        [4203] = {8105,"Trương Phi","Lưu Bị","Ta dẫn binh chờ trên cầu Trường Bản, xem Tử Long có về không.","Ta dẫn binh chờ trên cầu Trường Bản, xem Tử Long có về không.",},
        [4204] = {8106,"Triệu Vân","","Chúa công Tương Cam, Mi Nhị phu nhân và A Đẩu đều trông cậy vào ta.","Chúa công Tương Cam, Mi Nhị phu nhân và A Đẩu đều trông cậy vào ta.",},
        [4205] = {8106,"Triệu Vân","","Hôm nay thất lạc, còn mặt mũi nào gặp chủ nhân? Chi bằng tử chiến, ít ra tìm được tung tích chủ mẫu và chủ nhân!","Hôm nay thất lạc, còn mặt mũi nào gặp chủ nhân? Chi bằng tử chiến, ít ra tìm được tung tích chủ mẫu và chủ nhân!",},
        [4206] = {8107,"Triệu Vân","Giản Ung","Từng thấy hai vị chủ mẫu không?","Từng thấy hai vị chủ mẫu không?",},
        [4207] = {8107,"Giản Ung","Triệu Vân","Nhị phu nhân bỏ quân trượng, ôm A Đẩu chạy.","Nhị phu nhân bỏ quân trượng, ôm A Đẩu chạy.",},
        [4208] = {8107,"Giản Ung","Triệu Vân","Ta phi ngựa vượt núi bị tấn công ngã ngựa, ngựa bị lấy mất.","Ta phi ngựa vượt núi bị tấn công ngã ngựa, ngựa bị lấy mất.",},
        [4209] = {8107,"Giản Ung","Triệu Vân","Ta thấy Cam Phu Nhân và một cô nương đi về hướng nam.","Ta thấy Cam Phu Nhân và một cô nương đi về hướng nam.",},
        [4210] = {8107,"Triệu Vân","Giản Ung","Ngươi cưỡi ngựa hồi báo Huyền Đức, ta lên trời xuống biển cũng phải tìm ra chủ mẫu và chủ nhân.","Ngươi cưỡi ngựa hồi báo Huyền Đức, ta lên trời xuống biển cũng phải tìm ra chủ mẫu và chủ nhân.",},
        [4211] = {8107,"Triệu Vân","Giản Ung","Nếu không tìm thấy, sẽ bỏ mạng trên chiến trường!","Nếu không tìm thấy, sẽ bỏ mạng trên chiến trường!",},
        [4212] = {8108,"Giản Ung","Lưu Bị","Được Tử Long cứu, nhờ ta về báo chúa công.","Được Tử Long cứu, nhờ ta về báo chúa công.",},
        [4213] = {8108,"Giản Ung","Lưu Bị","Tử Long lên trời xuống biển cũng phải tìm ra chủ mẫu và chủ nhân!","Tử Long lên trời xuống biển cũng phải tìm ra chủ mẫu và chủ nhân!",},
        [4214] = {8108,"Lưu Bị","Giản Ung","Tử Long lần này nguy hiểm, phải làm sao đây?","Tử Long lần này nguy hiểm, phải làm sao đây?",},
        [4215] = {8108,"Nhân vật","Lưu Bị","Huyền Đức đừng sợ, ta cứu Tử Long, tìm nhị chủ mẫu và A Đẩu.","Huyền Đức đừng sợ, ta cứu Tử Long, tìm nhị chủ mẫu và A Đẩu.",},
        [4216] = {8108,"Lưu Bị","Nhân vật","Được, tứ đệ phải cẩn thận.","Tứ muội hãy cẩn thận.",},
        [4217] = {8109,"Triệu Vân","","Trong bá tánh có Cam Phu Nhân không?","Trong bá tánh có Cam Phu Nhân không?",},
        [4218] = {8109,"Cam Phu Nhân","Triệu Vân","Tử Long!","Tử Long!",},
        [4219] = {8109,"Triệu Vân","Cam Phu Nhân","Làm chủ mẫu thất lạc, tội lớn! My Phu Nhân và chủ nhân ở đâu?","Làm chủ mẫu thất lạc, tội lớn! My Phu Nhân và chủ nhân ở đâu?",},
        [4220] = {8109,"Cam Phu Nhân","Triệu Vân","Ta và My Phu Nhân bị đuổi, trà trộn vào bá tánh đi chung, gặp phải đàn ngựa phi nên đi lạc.","Ta và My Phu Nhân bị đuổi, trà trộn vào bá tánh đi chung, gặp phải đàn ngựa phi nên đi lạc.",},
        [4221] = {8109,"Cam Phu Nhân","Triệu Vân","My Phu Nhân và A Đẩu không biết đã đi đâu. Ta một mình chạy đến đây.","My Phu Nhân và A Đẩu không biết đã đi đâu. Ta một mình chạy đến đây.",},
        [4222] = {8109,"Triệu Vân","Cam Phu Nhân","Cam Phu Nhân lên ngựa, ta sẽ mở đường, đưa người về Trường Bản.","Cam Phu Nhân lên ngựa, ta sẽ mở đường, đưa người về Trường Bản.",},
        [4223] = {8110,"Trương Phi","Triệu Vân","Tử Long! Ngươi phản đại ca ta như thế nào?","Tử Long! Ngươi phản đại ca ta như thế nào?",},
        [4224] = {8110,"Triệu Vân","Trương Phi","Ta không tìm được chủ mẫu và chủ nhân, ăn nói sao đây?","Ta không tìm được chủ mẫu và chủ nhân, ăn nói sao đây?",},
        [4225] = {8110,"Trương Phi","Triệu Vân","Nếu không phải Giản Ung có thư báo, nay gặp ngươi sao chịu dừng tay!","Nếu không phải Giản Ung có thư báo, nay gặp ngươi sao chịu dừng tay!",},
        [4226] = {8110,"Triệu Vân","Trương Phi","Chúa công đang ở đâu?","Chúa công đang ở đâu?",},
        [4227] = {8110,"Trương Phi","Triệu Vân","Ở phía trước không xa.","Ở phía trước không xa.",},
        [4228] = {8110,"Triệu Vân","Trương Phi","Bảo vệ Cam Phu Nhân đi trước, ta đi tìm My Phu Nhân và chủ nhân.","Bảo vệ Cam Phu Nhân đi trước, ta đi tìm My Phu Nhân và chủ nhân.",},
        [4229] = {8110,"Trương Phi","Triệu Vân","Tử Long cẩn thận, Dương Uy Tướng vừa tấn công trận địch, cũng tìm nhị chủ mẫu và A Đẩu.","Tử Long cẩn thận, Dương Uy Tướng vừa tấn công trận địch, cũng tìm nhị chủ mẫu và A Đẩu.",},
        [4230] = {8110,"Triệu Vân","Trương Phi","Được, ta sẽ tìm Dương Uy Tướng cùng cứu nhị chủ mẫu và A Đẩu.","Được, ta sẽ tìm Dương Uy Tướng cùng cứu nhị chủ mẫu và A Đẩu.",},
        [4231] = {8111,"Triệu Vân","My Phu Nhân","Tử Long đến trễ.","Tử Long đến trễ.",},
        [4232] = {8111,"My Phu Nhân","Triệu Vân","A Đẩu gặp tướng quân, thật may mắn. Mong tướng quân thương cho cha nó chỉ có cốt nhục này.","A Đẩu gặp tướng quân, thật may mắn. Mong tướng quân thương cho cha nó chỉ có cốt nhục này.",},
        [4233] = {8111,"My Phu Nhân","Triệu Vân","Tướng quân có thể dạy bảo nó gặp cha, ta không hối tiếc!","Tướng quân có thể dạy bảo nó gặp cha, ta không hối tiếc!",},
        [4234] = {8111,"Triệu Vân","My Phu Nhân","Mời phu nhân lên ngựa, bảo vệ phu nhân thoát khỏi vòng vây.","Mời phu nhân lên ngựa, bảo vệ phu nhân thoát khỏi vòng vây.",},
        [4235] = {8111,"My Phu Nhân","Triệu Vân","Không! Đứa trẻ này phải nhờ tướng quân bảo vệ. Ta bị trọng thương, không sống được lâu nữa!","Không! Đứa trẻ này phải nhờ tướng quân bảo vệ. Ta bị trọng thương, không sống được lâu nữa!",},
        [4236] = {8111,"My Phu Nhân","Triệu Vân","Tướng quân mau đưa đứa trẻ này đi, đừng để ta liên lụy nó.","Tướng quân mau đưa đứa trẻ này đi, đừng để ta liên lụy nó.",},
        [4237] = {8111,"Triệu Vân","My Phu Nhân","Truy binh sắp đến, mời phu nhân mau lên ngựa.","Truy binh sắp đến, mời phu nhân mau lên ngựa.",},
        [4238] = {8111,"My Phu Nhân","Triệu Vân","Ta không thể đi. Đừng mất cả hai.","Ta không thể đi. Đừng mất cả hai.",},
        [4239] = {8111,"My Phu Nhân","Triệu Vân","Tính mạng của nó trong tay tướng quân! (My Phu Nhân đưa A Đẩu cho Triệu Vân.)","Tính mạng của nó trong tay tướng quân! (My Phu Nhân đưa A Đẩu cho Triệu Vân.)",},
        [4240] = {8111,"Triệu Vân","My Phu Nhân","Phu nhân không nghe lời ta, truy binh đuổi đến phải làm sao?","Phu nhân không nghe lời ta, truy binh đuổi đến phải làm sao?",},
        [4241] = {8111,"My Phu Nhân","Triệu Vân","A Đẩu giao cho ngươi rồi. (My Phu Nhân đặt A Đẩu xuống, nhảy giếng tự vẫn.)","A Đẩu giao cho ngươi rồi. (My Phu Nhân đặt A Đẩu xuống, nhảy giếng tự vẫn.)",},
        [4242] = {8111,"Triệu Vân","My Phu Nhân","My Phu Nhân! (Triệu Vân đẩy ngã tường để che miệng giếng. Ôm A Đẩu và phi ngựa rời khỏi).","My Phu Nhân! (Triệu Vân đẩy ngã tường để che miệng giếng. Ôm A Đẩu và phi ngựa rời khỏi).",},
        [4243] = {8112,"Nhân vật","Triệu Vân","Tử Long! Ngươi tìm được nhị chủ mẫu và A Đẩu?","Tử Long! Ngươi tìm được nhị chủ mẫu và A Đẩu?",},
        [4244] = {8112,"Triệu Vân","Nhân vật","Cam Phu Nhân đã an toàn vượt cầu Trường Bản.","Cam Phu Nhân đã an toàn vượt cầu Trường Bản.",},
        [4245] = {8112,"Triệu Vân","Nhân vật","A Đẩu trong tay ta, My Phu Nhân...","A Đẩu trong tay ta, My Phu Nhân...",},
        [4246] = {8112,"Nhân vật","Triệu Vân","My Phu Nhân sao?","My Phu Nhân sao?",},
        [4247] = {8112,"Triệu Vân","Nhân vật","My Phu Nhân bị thương, nên đã nhảy giếng...","My Phu Nhân bị thương, nên đã nhảy giếng...",},
        [4248] = {8112,"Nhân vật","Triệu Vân","Hy sinh giữ con cháu Lưu Bị, quả nhiên là nữ trượng phu.","Hy sinh giữ con cháu Lưu Bị, quả nhiên là nữ trượng phu.",},
        [4249] = {8112,"Nhân vật","Triệu Vân","Tử Long, mau! Cùng diệt Tào trận, đừng uổng nhờ vả của My Phu Nhân.","Tử Long, mau! Cùng diệt Tào trận, đừng uổng nhờ vả của My Phu Nhân.",},
        [4250] = {8201,"Triệu Vân","Trương Phi","Sau có truy binh, Dực Đức giúp ta!","Sau có truy binh, Dực Đức giúp ta!",},
        [4251] = {8201,"Trương Phi","Triệu Vân","Tử Long đi mau, ta sẽ đối phó truy binh.","Tử Long đi mau, ta sẽ đối phó truy binh.",},
        [4252] = {8202,"Triệu Vân","Lưu Bị","My Phu Nhân trọng thương, nhảy giếng tự vẫn, ta chỉ còn cách đẩy tường che lại.","My Phu Nhân trọng thương, nhảy giếng tự vẫn, ta chỉ còn cách đẩy tường che lại.",},
        [4253] = {8202,"Triệu Vân","Lưu Bị","Ta ôm công tử phá vòng vây, được Dương Uy Tướng tương trợ nên thoát được.","Ta ôm công tử phá vòng vây, được Dương Uy Tướng tương trợ nên thoát được.",},
        [4254] = {8202,"Triệu Vân","Lưu Bị","May công tử bình an! (Triệu Vân đưa A Đẩu cho Huyền Đức.)","May công tử bình an! (Triệu Vân đưa A Đẩu cho Huyền Đức.)",},
        [4255] = {8202,"Lưu Bị","Triệu Vân","Đứa trẻ làm phiền Dương Uy Tướng và Tử Long rồi! (Huyền Đức ném A Đẩu xuống đất.)","Đứa trẻ làm phiền Dương Uy Tướng và Tử Long rồi! (Huyền Đức ném A Đẩu xuống đất.)",},
        [4256] = {8202,"Triệu Vân","Lưu Bị","Ta tuy bất chấp thủ đoạn, không thể không báo đáp! (Triệu Vân liền ôm lấy A Đẩu.)","Ta tuy bất chấp thủ đoạn, không thể không báo đáp! (Triệu Vân liền ôm lấy A Đẩu.)",},
        [4257] = {8203,"Nhạc Tiến","Tào Tháo","Tướng địch Trương Phi hung hãn, tay cầm trường thương, phi ngựa trên cầu Trưởng Bản!","Tướng địch Trương Phi hung hãn, tay cầm trường thương, phi ngựa trên cầu Trưởng Bản!",},
        [4258] = {8203,"Nhạc Tiến","Tào Tháo","Rừng cây phía đông khói bụi mịt mù, e là mưu kế của Chư Cát Khổng Minh.","Rừng cây phía đông khói bụi mịt mù, e là mưu kế của Chư Cát Khổng Minh.",},
        [4259] = {8203,"Tào Tháo","Nhạc Tiến","Từng nghe Vân Trường nói: Dực Đức lấy thủ cấp thượng tướng dễ như trở bàn tay.","Từng nghe Vân Trường nói: Dực Đức lấy thủ cấp thượng tướng dễ như trở bàn tay.",},
        [4260] = {8203,"Tào Tháo","Nhạc Tiến","Hôm nay gặp lại, dù có phải kế của Khổng Minh hay không cũng không nên khinh địch.","Hôm nay gặp lại, dù có phải kế của Khổng Minh hay không cũng không nên khinh địch.",},
        [4261] = {8204,"Trương Phi","Tào Tháo","Trương Dực Đức ở đây! Ai dám đến tử chiến?","Trương Dực Đức ở đây! Ai dám đến tử chiến?",},
        [4262] = {8204,"Trương Phi","Tào Tháo","Không chiến lại không lui, là vì sao!","Không chiến lại không lui, là vì sao!",},
        [4263] = {8204,"Tào Tháo","Trương Phi","Các tướng cùng chạy về hướng Tây, vòng qua cầu Trường Bản.","Các tướng cùng chạy về hướng Tây, vòng qua cầu Trường Bản.",},
        [4264] = {8205,"Trương Liêu","Tào Tháo","Thừa tướng đừng sợ Trương Phi! Nay dẫn quân tấn công, có thể bắt được Lưu Bị.","Thừa tướng đừng sợ Trương Phi! Nay dẫn quân tấn công, có thể bắt được Lưu Bị.",},
        [4265] = {8205,"Tào Tháo","Trương Liêu","Ngươi mau cùng Hứa Chử đến cầu Trường Bản thăm dò tin tức.","Ngươi mau cùng Hứa Chử đến cầu Trường Bản thăm dò tin tức.",},
        [4266] = {8206,"Trương Liêu","Tào Tháo","Trương Phi đã hủy cầu nối.","Trương Phi đã hủy cầu nối.",},
        [4267] = {8206,"Tào Tháo","Trương Liêu","Cầu đã bị hủy.","Cầu đã bị hủy.",},
        [4268] = {8206,"Tào Tháo","Trương Liêu","Sai người xây 3 cầu tạm, đêm nay phải qua sông.","Sai người xây 3 cầu tạm, đêm nay phải qua sông.",},
        [4269] = {8206,"Lý Điển","Tào Tháo","E là mưu kế của Gia Cát Lượng, không được khinh suất.","E là mưu kế của Gia Cát Lượng, không được khinh suất.",},
        [4270] = {8206,"Tào Tháo","Lý Điển","Trương Phi anh dũng cũng dùng mưu mẹo!","Trương Phi anh dũng cũng dùng mưu mẹo!",},
        [4271] = {8207,"Lưu Bị","Nhân vật","Trước là sông lớn, sau có truy binh, phải làm sao?","Trước là sông lớn, sau có truy binh, phải làm sao?",},
        [4272] = {8207,"Nhân vật","Lưu Bị","Đừng lo, có Tử Long ta đây!","Đừng lo, có Tử Long ta đây!",},
        [4273] = {8208,"Tào Tháo","","Lưu Bị như hổ trong bẫy, nếu không bắt hắn lúc này sẽ thả hổ về rừng.","Lưu Bị như hổ trong bẫy, nếu không bắt hắn lúc này sẽ thả hổ về rừng.",},
        [4274] = {8208,"Tào Tháo","","Các tướng cố gắng tiến lên.","Các tướng cố gắng tiến lên.",},
        [4275] = {8208,"Quan Vũ","Tào Tháo","Ta đợi ở đây đã lâu! (Quan Vũ đến Giang Hạ mượn quân mã, biết tin Đương Dương Trường Bản Chiến, nên chặn đường ở đây.)","Ta đợi ở đây đã lâu! (Quan Vũ đến Giang Hạ mượn quân mã, biết tin Đương Dương Trường Bản Chiến, nên chặn đường ở đây.)",},
        [4276] = {8208,"Tào Tháo","Quan Vũ","Lại trúng kế của Gia Cát Lượng!","Lại trúng kế của Gia Cát Lượng!",},
        [4277] = {8208,"Tào Tháo","Quan Vũ","Truyền lệnh đại quân rút lui.","Truyền lệnh đại quân rút lui.",},
        [4278] = {8301,"Tào Tháo","","Lưu Bị đã theo Giang Hạ, e sẽ cấu kết Đông Ngô, dùng cách nào đối phó?","Lưu Bị đã theo Giang Hạ, e sẽ cấu kết Đông Ngô, dùng cách nào đối phó?",},
        [4279] = {8301,"Tuân Du","Tào Tháo","Ta sẽ cử binh đến Giang Đông, mời Tôn Quyền săn Giang Hạ, bắt Lưu Bị.","Ta sẽ cử binh đến Giang Đông, mời Tôn Quyền săn Giang Hạ, bắt Lưu Bị.",},
        [4280] = {8301,"Tuân Du","Tào Tháo","Tôn Quyền chắc sẽ lo sợ mà đầu hàng, ta sẽ hoàn thành đại nghiệp.","Tôn Quyền chắc sẽ lo sợ mà đầu hàng, ta sẽ hoàn thành đại nghiệp.",},
        [4281] = {8301,"Tào Tháo","Tuân Du","Được, làm theo lời ngươi, mau cử sứ giả đến Đông Ngô.","Được, làm theo lời ngươi, mau cử sứ giả đến Đông Ngô.",},
        [4282] = {8302,"Lỗ Túc","Tôn Quyền","Kinh Châu và xung quanh thịnh vượng. Nếu chiếm được sẽ bổ sung tài nguyên cho Đế Vương.","Kinh Châu và xung quanh thịnh vượng. Nếu chiếm được sẽ bổ sung tài nguyên cho Đế Vương.",},
        [4283] = {8302,"Lỗ Túc","Tôn Quyền","Túc phụng mệnh đến Giang Hạ phúng điếu, vì nói Lưu Bị sai khiến tướng của Lưu Biểu, cùng diệt Tào Tháo.","Túc phụng mệnh đến Giang Hạ phúng điếu, vì nói Lưu Bị sai khiến tướng của Lưu Biểu, cùng diệt Tào Tháo.",},
        [4284] = {8302,"Lỗ Túc","Tôn Quyền","Nếu Bị nghe lệnh, đại sự đã định.","Nếu Bị nghe lệnh, đại sự đã định.",},
        [4285] = {8302,"Tôn Quyền","Lỗ Túc","Được, ngươi mau đến Giang Hạ.","Được, ngươi mau đến Giang Hạ.",},
        [4286] = {8303,"Lưu Bị","Gia Cát Lượng","Nay Tào Quân chiếm Giang Lăng, sau này đối phó thế nào?","Nay Tào Quân chiếm Giang Lăng, sau này đối phó thế nào?",},
        [4287] = {8303,"Gia Cát Lượng","Lưu Bị","Tào Tháo thế mạnh, chi bằng theo Đông Ngô Tôn Quyền để tiếp ứng. Ta sẽ thừa cơ trục lợi, sao lại không được?","Tào Tháo thế mạnh, chi bằng theo Đông Ngô Tôn Quyền để tiếp ứng. Ta sẽ thừa cơ trục lợi, sao lại không được?",},
        [4288] = {8303,"Lưu Bị","Gia Cát Lượng","Giang Đông nhiều nhân tài, đều có mưu kế, sao chịu hợp tác?","Giang Đông nhiều nhân tài, đều có mưu kế, sao chịu hợp tác?",},
        [4289] = {8303,"Gia Cát Lượng","Lưu Bị","Tào dẫn trăm vạn người, Giang Hán hiểm trở, Giang Đông không cử người đến dò la?","Tào dẫn trăm vạn người, Giang Hán hiểm trở, Giang Đông không cử người đến dò la?",},
        [4290] = {8303,"Gia Cát Lượng","Lưu Bị","Nếu có người đến đây, dựa vào vài lời dẻo ngọt, nói hai quân Nam Bắc thôn tính lẫn nhau.","Nếu có người đến đây, dựa vào vài lời dẻo ngọt, nói hai quân Nam Bắc thôn tính lẫn nhau.",},
        [4291] = {8303,"Gia Cát Lượng","Lưu Bị","Nếu quân nam thắng, cùng diệt Tào Tháo chiếm Kinh Châu, nếu quân bắc thắng, ta thừa thế chiếm Giang Nam.","Nếu quân nam thắng, cùng diệt Tào Tháo chiếm Kinh Châu, nếu quân bắc thắng, ta thừa thế chiếm Giang Nam.",},
        [4292] = {8303,"Lưu Bị","Gia Cát Lượng","Ý hay nhưng làm sao để người Giang Đông đến đây?","Ý hay nhưng làm sao để người Giang Đông đến đây?",},
        [4293] = {8303,"Gia Cát Lượng","Lưu Bị","Người Giang Đông không lâu sau sẽ tự đến.","Người Giang Đông không lâu sau sẽ tự đến.",},
        [4294] = {8304,"Lưu Bị","Gia Cát Lượng","Giang Đông Tôn Quyền cử Lỗ Túc đến phúng điếu, thuyền đã cập bến.","Giang Đông Tôn Quyền cử Lỗ Túc đến phúng điếu, thuyền đã cập bến.",},
        [4295] = {8304,"Gia Cát Lượng","Lưu Bị","Chuyện lớn!","Chuyện lớn!",},
        [4296] = {8304,"Gia Cát Lượng","Lưu Bị","Năm xưa Tôn Sách mất, Tương Dương có cử người đến phúng điếu?","Năm xưa Tôn Sách mất, Tương Dương có cử người đến phúng điếu?",},
        [4297] = {8304,"Lưu Kỳ","Gia Cát Lượng","Giang Đông và gia đình ta có mối thù diệt cha!","Giang Đông và gia đình ta có mối thù diệt cha!",},
        [4298] = {8304,"Gia Cát Lượng","Lưu Kỳ","Lỗ Túc đến là để thăm dò quân tình.","Lỗ Túc đến là để thăm dò quân tình.",},
        [4299] = {8304,"Gia Cát Lượng","Lưu Kỳ","Lỗ Túc hỏi về Tào Tháo, chúa công cứ nói không biết, hoặc đến hỏi Gia Cát Lượng.","Lỗ Túc hỏi về Tào Tháo, chúa công cứ nói không biết, hoặc đến hỏi Gia Cát Lượng.",},
        [4300] = {8304,"Lưu Bị","Gia Cát Lượng","Được.","Được.",},
        [4301] = {8305,"Lỗ Túc","Lưu Bị","Nghe danh hoàng thúc đã lâu, hôm nay được gặp, quả thật hân hạnh.","Nghe danh hoàng thúc đã lâu, hôm nay được gặp, quả thật hân hạnh.",},
        [4302] = {8305,"Lỗ Túc","Lưu Bị","Nghe nói hoàng thúc giao chiến với Tào Tháo, dám hỏi Tào Quân khoảng bao nhiêu?","Nghe nói hoàng thúc giao chiến với Tào Tháo, dám hỏi Tào Quân khoảng bao nhiêu?",},
        [4303] = {8305,"Lưu Bị","Lỗ Túc","Quân Lưu Bị nghe tin Tào Tháo đã chạy mất, không rõ thật hư.","Quân Lưu Bị nghe tin Tào Tháo đã chạy mất, không rõ thật hư.",},
        [4304] = {8305,"Lỗ Túc","Lưu Bị","Nghe nói hoàng thúc dùng kế Khổng Minh, hai trận lửa khiến Tào Tháo hỏang loạn, sao lại không rõ?","Nghe nói hoàng thúc dùng kế Khổng Minh, hai trận lửa khiến Tào Tháo hỏang loạn, sao lại không rõ?",},
        [4305] = {8305,"Lưu Bị","Lỗ Túc","Từ Phi hỏi Khổng Minh sẽ rõ.","Từ Phi hỏi Khổng Minh sẽ rõ.",},
        [4306] = {8305,"Lỗ Túc","Lưu Bị","Khổng Minh ở đâu? Mong diện kiến.","Khổng Minh ở đâu? Mong diện kiến.",},
