        [4308] = {8306,"Lỗ Túc","Gia Cát Lượng","Hướng Mộ Tiên Sinh tài đức, nay được gặp nguyện nghe chuyện an nguy.","Hướng Mộ Tiên Sinh tài đức, nay được gặp nguyện nghe chuyện an nguy.",},
        [4309] = {8306,"Gia Cát Lượng","Lỗ Túc","Gian kế Tào Tháo, Lượng đã biết rõ, nhưng không đủ sức ngăn cản.","Gian kế Tào Tháo, Lượng đã biết rõ, nhưng không đủ sức ngăn cản.",},
        [4310] = {8306,"Lỗ Túc","Gia Cát Lượng","Hoàng thúc định ngăn việc này lại?","Hoàng thúc định ngăn việc này lại?",},
        [4311] = {8306,"Gia Cát Lượng","Lỗ Túc","Sứ quân và Thương Ngô Thái Thú Ngô Thần có giao tình, nguyện theo Ngô Thần.","Sứ quân và Thương Ngô Thái Thú Ngô Thần có giao tình, nguyện theo Ngô Thần.",},
        [4312] = {8306,"Lỗ Túc","Gia Cát Lượng","Ngô Thần không thể tự bảo vệ mình, sao có thể nhận thêm người?","Ngô Thần không thể tự bảo vệ mình, sao có thể nhận thêm người?",},
        [4313] = {8306,"Gia Cát Lượng","Lỗ Túc","Ngô Thần tuy không nên ở lâu, nay tạm nương nhờ rồi tính kế khác.","Ngô Thần tuy không nên ở lâu, nay tạm nương nhờ rồi tính kế khác.",},
        [4314] = {8306,"Lỗ Túc","Gia Cát Lượng","Tôn Tướng Quân chiếm lục quận, binh lính tinh nhuệ và lương thực đầy đủ.","Tôn Tướng Quân chiếm lục quận, binh lính tinh nhuệ và lương thực đầy đủ.",},
        [4315] = {8306,"Lỗ Túc","Gia Cát Lượng","Nay xin hiến kế. Chi bằng sai người tâm phúc sang liên kết Đông Ngô cùng mưu toan việc lớn.","Nay xin hiến kế. Chi bằng sai người tâm phúc sang liên kết Đông Ngô cùng mưu toan việc lớn.",},
        [4316] = {8306,"Gia Cát Lượng","Lỗ Túc","Lưu sứ quân cùng với Tôn tướng quân không có giao tình, sợ uổng mất lời. Vả lại không có tâm phúc để sai khiến.","Lưu sứ quân cùng với Tôn tướng quân không có giao tình, sợ uổng mất lời. Vả lại không có tâm phúc để sai khiến.",},
        [4317] = {8306,"Lỗ Túc","Gia Cát Lượng","Lệnh huynh Gia Cát Cẩn hiện đang làm tham mưu ở Giang Đông. Ta tuy bất tài, xin cùng tiên sinh vào ra mắt Tôn tướng quân để bàn việc lớn.","Lệnh huynh Gia Cát Cẩn hiện đang làm tham mưu ở Giang Đông. Ta tuy bất tài, xin cùng tiên sinh vào ra mắt Tôn tướng quân để bàn việc lớn.",},
        [4318] = {8306,"Lưu Bị","Lỗ Túc","Khổng Minh là thầy ta, rời ta một lúc không xong, đi thế nào được?","Khổng Minh là thầy ta, rời ta một lúc không xong, đi thế nào được?",},
        [4319] = {8306,"Lỗ Túc","Lưu Bị","Sao hoàng thúc không đi cùng Khổng Minh.","Sao hoàng thúc không đi cùng Khổng Minh.",},
        [4320] = {8306,"Lưu Bị","Lỗ Túc","Cái này...","Cái này...",},
        [4321] = {8306,"Nhân vật","Lưu Bị","Chuyến đi Đông Ngô, sao có thể làm phiền đại ca, ta và Khổng Minh đi là được rồi.","Chuyến đi Đông Ngô, sao có thể làm phiền đại ca, ta và Khổng Minh đi là được rồi.",},
        [4322] = {8306,"Lưu Bị","Nhân vật","Vậy làm phiền tứ đệ rồi.","Vậy phiền đến tứ muội rồi.",},
        [4323] = {8307,"Tôn Quyền","Lỗ Túc","Tử Kính sang Giang Hạ thấy tình thế nào?","Tử Kính sang Giang Hạ thấy tình thế nào?",},
        [4324] = {8307,"Lỗ Túc","Tôn Quyền","Cũng đã biết được ít nhiều, xin để thư thả sẽ thưa.","Cũng đã biết được ít nhiều, xin để thư thả sẽ thưa.",},
        [4325] = {8307,"Tôn Quyền","Lỗ Túc","Hôm nọ Tào Tháo sai sứ đưa hịch đến đây, ta đã cho sứ quay về. Nay đang bàn việc ấy với các quan chưa xong.","Hôm nọ Tào Tháo sai sứ đưa hịch đến đây, ta đã cho sứ quay về. Nay đang bàn việc ấy với các quan chưa xong.",},
        [4326] = {8307,"Lỗ Túc","Tôn Quyền","Ý Tào Tháo thế nào?","Ý Tào Tháo thế nào?",},
        [4327] = {8307,"Tôn Quyền","Lỗ Túc","Muốn cùng tướng quân săn Giang Hạ, tiến đánh Lưu Bị, phân chia đất đai, giao hảo với nhau mãi mãi.","Muốn cùng tướng quân săn Giang Hạ, tiến đánh Lưu Bị, phân chia đất đai, giao hảo với nhau mãi mãi.",},
        [4328] = {8307,"Lỗ Túc","Tôn Quyền","Ý chúa công thế nào?","Ý chúa công thế nào?",},
        [4329] = {8307,"Tôn Quyền","Lỗ Túc","Ta cũng chưa quyết định.","Ta cũng chưa quyết định.",},
        [4330] = {8307,"Trương Chiêu","Tôn Quyền","Tào Tháo thống lĩnh quân trăm vạn, mượn tiếng thiên tử, chinh chiến bốn phương, mình chống cự lại thì là nghịch.","Tào Tháo thống lĩnh quân trăm vạn, mượn tiếng thiên tử, chinh chiến bốn phương, mình chống cự lại thì là nghịch.",},
        [4331] = {8307,"Trương Chiêu","Tôn Quyền","Nay Tháo chiếm được Kinh Châu, ta khó địch được nữa. Vậy cứ ý ta, đầu hàng là kế vẹn toàn.","Nay Tháo chiếm được Kinh Châu, ta khó địch được nữa. Vậy cứ ý ta, đầu hàng là kế vẹn toàn.",},
        [4332] = {8307,"Tôn Quyền","Trương Chiêu","Cái này...","Cái này...",},
        [4333] = {8307,"Trương Chiêu","Tôn Quyền","Chúa công không nên nghi ngại. Nếu hàng Tào thì dân Đông Ngô được yên, mà sáu quận Giang Nam cũng giữ được.","Chúa công không nên nghi ngại. Nếu hàng Tào thì dân Đông Ngô được yên, mà sáu quận Giang Nam cũng giữ được.",},
        [4334] = {8307,"Tôn Quyền","Trương Chiêu","Cái này...","Cái này...",},
        [4335] = {8308,"Lỗ Túc","Tôn Quyền","Ai cũng có thể hàng Tào Tháo được, duy có tướng quân thì không hàng được.","Ai cũng có thể hàng Tào Tháo được, duy có tướng quân thì không hàng được.",},
        [4336] = {8308,"Tôn Quyền","Lỗ Túc","Vì sao?","Vì sao?",},
        [4337] = {8308,"Lỗ Túc","Tôn Quyền","Như Lỗ Túc này mà hàng, áo gấm về làng, mà cũng không phải mất đất đai gì cả.","Như Lỗ Túc này mà hàng, áo gấm về làng, mà cũng không phải mất đất đai gì cả.",},
        [4338] = {8308,"Lỗ Túc","Tôn Quyền","Tướng quân mà hàng Tào thì về đâu? Chức tước bất quá phong hầu là cùng, xe một cỗ, ngựa một con, đầy tớ vài ba người.","Tướng quân mà hàng Tào thì về đâu? Chức tước bất quá phong hầu là cùng, xe một cỗ, ngựa một con, đầy tớ vài ba người.",},
        [4339] = {8308,"Lỗ Túc","Tôn Quyền","Họ ai cũng chỉ biết thân người nấy mà thôi, xin tướng quân chớ nghe, nên sớm định kế lớn.","Họ ai cũng chỉ biết thân người nấy mà thôi, xin tướng quân chớ nghe, nên sớm định kế lớn.",},
        [4340] = {8308,"Tôn Quyền","Lỗ Túc","Ta nghe họ nghị luận cảm thấy thất vọng. Tử Kính mới ngỏ kế lớn, chính hợp ý với ta.","Ta nghe họ nghị luận cảm thấy thất vọng. Tử Kính mới ngỏ kế lớn, chính hợp ý với ta.",},
        [4341] = {8308,"Tôn Quyền","Lỗ Túc","Quả thật trời ban Tử Kính cho ta! Nhưng Tháo mới được quân Viên Thiệu và Kinh Châu, thế mạnh khó mà địch nổi.","Quả thật trời ban Tử Kính cho ta! Nhưng Tháo mới được quân Viên Thiệu và Kinh Châu, thế mạnh khó mà địch nổi.",},
        [4342] = {8308,"Lỗ Túc","Tôn Quyền","Túc vừa sang Giang Hạ, đưa Gia Cát Lượng về đây, chúa công gọi vào hỏi sẽ biết tình hình.","Túc vừa sang Giang Hạ, đưa Gia Cát Lượng về đây, chúa công gọi vào hỏi sẽ biết tình hình.",},
        [4343] = {8308,"Tôn Quyền","Lỗ Túc","Ngọa Long tiên sinh có ở đây?","Ngọa Long tiên sinh có ở đây?",},
        [4344] = {8308,"Lỗ Túc","Tôn Quyền","Hiện đang nghỉ ở ngoài nhà khách.","Hiện đang nghỉ ở ngoài nhà khách.",},
        [4345] = {8308,"Tôn Quyền","Lỗ Túc","Nay trời đã tối, không gặp vội. Ngày mai cho gặp các tay anh tuấn Giang Đông đã, rồi sẽ mời lên triều đường bàn việc.","Nay trời đã tối, không gặp vội. Ngày mai cho gặp các tay anh tuấn Giang Đông đã, rồi sẽ mời lên triều đường bàn việc.",},
        [4346] = {8309,"Trương Chiêu","Gia Cát Lượng","Chiêu ta nghe danh tiên sinh ví mình với Quản Trọng, Nhạc Nghị, lời ấy chẳng biết thực hư?","Chiêu ta nghe danh tiên sinh ví mình với Quản Trọng, Nhạc Nghị, lời ấy chẳng biết thực hư?",},
        [4347] = {8309,"Gia Cát Lượng","Trương Chiêu","Lượng ta cũng ví mình như thế","Lượng ta cũng ví mình như thế",},
        [4348] = {8309,"Trương Chiêu","Gia Cát Lượng","Mới đây nghe Lưu Dự Châu ba lần cầu đến tiên sinh, càn quét Kinh Tương, nay chỉ có một buổi sáng mà về tay Tào Tháo hết, chẳng hay ông thế nào?","Mới đây nghe Lưu Dự Châu ba lần cầu đến tiên sinh, càn quét Kinh Tương, nay chỉ có một buổi sáng mà về tay Tào Tháo hết, chẳng hay ông thế nào?",},
        [4349] = {8309,"Gia Cát Lượng","Trương Chiêu","Chủ ta là Lưu Dự Châu, vì không nỡ cướp cơ nghiệp của người đồng tông nên nhất định không lấy.","Chủ ta là Lưu Dự Châu, vì không nỡ cướp cơ nghiệp của người đồng tông nên nhất định không lấy.",},
        [4350] = {8309,"Gia Cát Lượng","Trương Chiêu","Chủ ta đóng quân ở Giang Hạ, sẽ có kế khác, những kẻ tầm thường có hiểu sao được?","Chủ ta đóng quân ở Giang Hạ, sẽ có kế khác, những kẻ tầm thường có hiểu sao được?",},
        [4351] = {8309,"Trương Chiêu","Gia Cát Lượng","Nếu thế thì tiên sinh lời nói không đi đôi với việc làm rồi. ","Nếu thế thì tiên sinh lời nói không đi đôi với việc làm rồi. ",},
        [4352] = {8309,"Trương Chiêu","Gia Cát Lượng","Ai ngờ khi tiên sinh về với Dự Châu, quân Tào mới đến, bỏ giáp quẳng gươm.","Ai ngờ khi tiên sinh về với Dự Châu, quân Tào mới đến, bỏ giáp quẳng gươm.",},
        [4353] = {8309,"Trương Chiêu","Gia Cát Lượng","Bỏ Tân Dã, chạy khỏi Phàn Thành, chẳng có lấy một chỗ dung thân. Hóa ra Dự Châu từ khi được tiên sinh lại không bằng trước?","Bỏ Tân Dã, chạy khỏi Phàn Thành, chẳng có lấy một chỗ dung thân. Hóa ra Dự Châu từ khi được tiên sinh lại không bằng trước?",},
        [4354] = {8309,"Gia Cát Lượng","Trương Chiêu","Chủ ta, trước thua ở Nhữ Ân, đến nhờ Lưu Biểu, quân không đầy một nghìn, Dự Châu chỉ là chốn  tạm, có phải muốn khư khư ngồi giữ cái xó ấy đâu?","Chủ ta, trước thua ở Nhữ Ân, đến nhờ Lưu Biểu, quân không đầy một nghìn, Dự Châu chỉ là chốn  tạm, có phải muốn khư khư ngồi giữ cái xó ấy đâu?",},
        [4355] = {8309,"Gia Cát Lượng","Trương Chiêu","Trong hoàn cảnh quân sĩ không sẵn, thành quách không bền, khiến Hạ Hầu Đôn, Tào Nhân phải lòng run mật vỡ.","Trong hoàn cảnh quân sĩ không sẵn, thành quách không bền, khiến Hạ Hầu Đôn, Tào Nhân phải lòng run mật vỡ.",},
        [4356] = {8309,"Gia Cát Lượng","Trương Chiêu","Lưu Tôn hàng Tào, Dự Châu hoàn toàn không biết. Vả lại, không nỡ nhân lúc loạn mà cướp lấy cơ nghiệp người đồng tông.","Lưu Tôn hàng Tào, Dự Châu hoàn toàn không biết. Vả lại, không nỡ nhân lúc loạn mà cướp lấy cơ nghiệp người đồng tông.",},
        [4357] = {8309,"Gia Cát Lượng","Trương Chiêu","Trận thua ở Đương Dương, vì có vài vạn dân như gồm già trẻ, nên mỗi ngày chỉ đi được mười dặm.","Trận thua ở Đương Dương, vì có vài vạn dân như gồm già trẻ, nên mỗi ngày chỉ đi được mười dặm.",},
        [4358] = {8309,"Trương Chiêu","Gia Cát Lượng","Cái này...","Cái này...",},
        [4359] = {8310,"Tôn Quyền","Gia Cát Lượng","Tiên sinh ở Tân Dã, giúp Lưu Dự Châu giao chiến với Tào Tháo, tất biết tình hình bên Tào thế nào?","Tiên sinh ở Tân Dã, giúp Lưu Dự Châu giao chiến với Tào Tháo, tất biết tình hình bên Tào thế nào?",},
        [4360] = {8310,"Gia Cát Lượng","Tôn Quyền","Cả thiết kỵ, bộ binh và thủy quân ước được hơn trăm vạn.","Cả thiết kỵ, bộ binh và thủy quân ước được hơn trăm vạn.",},
        [4361] = {8310,"Tôn Quyền","Gia Cát Lượng","Nay Tào Tháo đã bình được Kinh Sở, còn tham vọng gì nữa không?","Nay Tào Tháo đã bình được Kinh Sở, còn tham vọng gì nữa không?",},
        [4362] = {8310,"Gia Cát Lượng","Tôn Quyền","Tào Tháo xây trại kín ven sông, sắm sửa thuyền bè, không lấy Giang Đông thì lấy đâu?","Tào Tháo xây trại kín ven sông, sắm sửa thuyền bè, không lấy Giang Đông thì lấy đâu?",},
        [4363] = {8310,"Tôn Quyền","Gia Cát Lượng","Đối với chuyện này, nên đánh hay không, xin giúp ta quyết định.","Đối với chuyện này, nên đánh hay không, xin giúp ta quyết định.",},
        [4364] = {8310,"Gia Cát Lượng","Tôn Quyền","Trước kia thiên hạ đại loạn, nên tướng quân dấy binh ở Giang Đông, Lưu Dự Châu tụ quân ở Hán Nam, cùng giành với Tào Tháo.","Trước kia thiên hạ đại loạn, nên tướng quân dấy binh ở Giang Đông, Lưu Dự Châu tụ quân ở Hán Nam, cùng giành với Tào Tháo.",},
        [4365] = {8310,"Gia Cát Lượng","Tôn Quyền","Nay Tháo đã dẹp được nạn lớn, phá được Kinh Châu, uy lừng bốn bể.","Nay Tháo đã dẹp được nạn lớn, phá được Kinh Châu, uy lừng bốn bể.",},
        [4366] = {8310,"Gia Cát Lượng","Tôn Quyền","Nếu tướng quân có thể lấy quân Ngô chống lại Tào Tháo, thì nên sớm cự tuyệt. Nếu không muốn thế, sao không theo lời các mưu sĩ, ngoảnh về phương bắc mà hàng?","Nếu tướng quân có thể lấy quân Ngô chống lại Tào Tháo, thì nên sớm cự tuyệt. Nếu không muốn thế, sao không theo lời các mưu sĩ, ngoảnh về phương bắc mà hàng?",},
        [4367] = {8310,"Gia Cát Lượng","Tôn Quyền","Tướng quân trong lòng vẫn còn ngờ vực. Việc gấp mà không quyết đoán, thì vạ đến nơi!","Tướng quân trong lòng vẫn còn ngờ vực. Việc gấp mà không quyết đoán, thì vạ đến nơi!",},
        [4368] = {8310,"Tôn Quyền","Gia Cát Lượng","Nếu như vậy thật, thì sao Lưu Dự Châu không hàng Tào?","Nếu như vậy thật, thì sao Lưu Dự Châu không hàng Tào?",},
        [4369] = {8310,"Gia Cát Lượng","Tôn Quyền","Lưu Dự Châu là tôn thân nhà Hán, anh tài cái thế. Việc không xong là bởi trời, có đâu lại chịu luồn cúi người ta?","Lưu Dự Châu là tôn thân nhà Hán, anh tài cái thế. Việc không xong là bởi trời, có đâu lại chịu luồn cúi người ta?",},
        [4370] = {8310,"Lỗ Túc","Gia Cát Lượng","To gan, những lời tiên sinh vừa nói là khinh miệt chủ ta. Sau này bàn tiếp.","To gan, những lời tiên sinh vừa nói là khinh miệt chủ ta. Sau này bàn tiếp.",},
        [4371] = {8311,"Tôn Quyền","Gia Cát Lượng","Tào Tháo vốn chỉ ghét Lữ Bố, Lưu Biểu... Nay đã trừ được cả, duy chỉ còn Dự Châu và ta. Ta không thể đem cả nước Ngô dâng cho người ta được","Tào Tháo vốn chỉ ghét Lữ Bố, Lưu Biểu... Nay đã trừ được cả, duy chỉ còn Dự Châu và ta. Ta không thể đem cả nước Ngô dâng cho người ta được",},
        [4372] = {8311,"Tôn Quyền","Gia Cát Lượng","Kế của ta đã quyết. Dự Châu vừa mới thua trận, làm thế nào mà chống được nạn này.","Kế của ta đã quyết. Dự Châu vừa mới thua trận, làm thế nào mà chống được nạn này.",},
        [4373] = {8311,"Gia Cát Lượng","Tôn Quyền","Dự Châu tuy mới thua, nhưng Quan Vân Trường còn thống lĩnh hàng vạn tinh binh,.","Dự Châu tuy mới thua, nhưng Quan Vân Trường còn thống lĩnh hàng vạn tinh binh,.",},
        [4374] = {8311,"Gia Cát Lượng","Tôn Quyền","Quân Tào Tháo từ xa đến, tất cũng mỏi mệt, vả lại người phương bắc không quen chiến đấu trên nước.","Quân Tào Tháo từ xa đến, tất cũng mỏi mệt, vả lại người phương bắc không quen chiến đấu trên nước.",},
        [4375] = {8311,"Gia Cát Lượng","Tôn Quyền","Quân dân ở Kinh Châu gặp thế bí phải phục Tào, chứ không phải tự nguyện.","Quân dân ở Kinh Châu gặp thế bí phải phục Tào, chứ không phải tự nguyện.",},
        [4376] = {8311,"Gia Cát Lượng","Tôn Quyền","Nay tướng quân muốn đoàn kết với Dự Châu, chắc chắn phá được Tào Tháo.","Nay tướng quân muốn đoàn kết với Dự Châu, chắc chắn phá được Tào Tháo.",},
        [4377] = {8311,"Gia Cát Lượng","Tôn Quyền","Thắng thua chỉ trong lúc này, tướng quân nghĩ kỹ rồi quyết định!","Thắng thua chỉ trong lúc này, tướng quân nghĩ kỹ rồi quyết định!",},
        [4378] = {8311,"Tôn Quyền","Gia Cát Lượng","Ta đã quyết định, hôm sau khởi binh cùng diệt Tào Tháo!","Ta đã quyết định, hôm sau khởi binh cùng diệt Tào Tháo!",},
        [4379] = {8312,"Trương Chiêu","Tôn Quyền","Trúng kế của Khổng Minh rồi!","Trúng kế của Khổng Minh rồi!",},
        [4380] = {8312,"Trương Chiêu","Tôn Quyền","Tào Tháo khi trước binh mọn, tướng ít, còn đánh được Viên Thiệu; huống bây giờ cầm hơn trăm vạn quân, há nên khinh địch?","Tào Tháo khi trước binh mọn, tướng ít, còn đánh được Viên Thiệu; huống bây giờ cầm hơn trăm vạn quân, há nên khinh địch?",},
        [4381] = {8312,"Trương Chiêu","Tôn Quyền","Nếu nghe lời Gia Cát Lượng, hấp tấp dấy binh, là đem củi khô đi chữa cháy.","Nếu nghe lời Gia Cát Lượng, hấp tấp dấy binh, là đem củi khô đi chữa cháy.",},
        [4382] = {8312,"Tôn Quyền","Trương Chiêu","Cái này...","Cái này...",},
        [4383] = {8312,"Cố Ung","Tôn Quyền","Lưu Bị thua vì Tào Tháo, nên muốn mượn quân ta để cự lại Tháo. Hãy nghe lời Tử Bố.","Lưu Bị thua vì Tào Tháo, nên muốn mượn quân ta để cự lại Tháo. Hãy nghe lời Tử Bố.",},
        [4384] = {8312,"Tôn Quyền","Cố Ung","Cái này...","Cái này...",},
        [4385] = {8312,"Lỗ Túc","Tôn Quyền","Bọn Trương Tử Bố khuyên tướng quân hàng Tào Tháo chỉ biết giữ mình và vợ con, chúa công đừng nghe.","Bọn Trương Tử Bố khuyên tướng quân hàng Tào Tháo chỉ biết giữ mình và vợ con, chúa công đừng nghe.",},
        [4386] = {8312,"Lỗ Túc","Tôn Quyền","Nếu chúa công hoài nghi, tất bị chúng làm lỡ việc.","Nếu chúa công hoài nghi, tất bị chúng làm lỡ việc.",},
        [4387] = {8312,"Tôn Quyền","Lỗ Túc","Ngươi lui ra đã, để ta suy nghĩ.","Ngươi lui ra đã, để ta suy nghĩ.",},
        [4388] = {8313,"Ngô Quốc Thái","Tôn Quyền","Suy nghĩ gì mà bỏ ăn bỏ ngủ?","Suy nghĩ gì mà bỏ ăn bỏ ngủ?",},
        [4389] = {8313,"Tôn Quyền","Ngô Quốc Thái","Tào Tháo đóng quân ở Giang Hán. Nếu đánh thì sợ mình ít không địch nổi nhiều, muốn hàng thì sợ Tào Tháo không dung.","Tào Tháo đóng quân ở Giang Hán. Nếu đánh thì sợ mình ít không địch nổi nhiều, muốn hàng thì sợ Tào Tháo không dung.",},
        [4390] = {8313,"Ngô Quốc Thái","Tôn Quyền","Ngươi không nhớ lời Võ Liệt Hoàng Hậu lúc lâm chung dặn?","Ngươi không nhớ lời Võ Liệt Hoàng Hậu lúc lâm chung dặn?",},
        [4391] = {8313,"Ngô Quốc Thái","Tôn Quyền","Chuyện trong hỏi Trương Chiêu, chuyện ngoài hỏi Chu Du.","Chuyện trong hỏi Trương Chiêu, chuyện ngoài hỏi Chu Du.",},
        [4392] = {8313,"Ngô Quốc Thái","Tôn Quyền","Sao con mời Chu Du về hỏi?","Sao con mời Chu Du về hỏi?",},
        [4393] = {8401,"Trương Chiêu","Chu Du","Đô đốc có biết việc mất còn của Giang Đông không?","Đô đốc có biết việc mất còn của Giang Đông không?",},
        [4394] = {8401,"Chu Du","Trương Chiêu","Chưa biết.","Chưa biết.",},
        [4395] = {8401,"Trương Chiêu","Chu Du","Tào Tháo cầm trăm vạn quân, đóng ở Hán Thượng. Tuy hắn định thôn tính ta, nhưng chưa nói rõ ra thôi.","Tào Tháo cầm trăm vạn quân, đóng ở Hán Thượng. Tuy hắn định thôn tính ta, nhưng chưa nói rõ ra thôi.",},
        [4396] = {8401,"Trương Chiêu","Chu Du","Bọn Chiêu đã khuyên chúa công nên hàng, họa may Giang Đông mới tránh được vạ. Không ngờ Gia Cát Lượng vì muốn trả thù Tào Tháo, nên nói khích chúa công.","Bọn Chiêu đã khuyên chúa công nên hàng, họa may Giang Đông mới tránh được vạ. Không ngờ Gia Cát Lượng vì muốn trả thù Tào Tháo, nên nói khích chúa công.",},
        [4397] = {8401,"Trương Chiêu","Chu Du","Tử Kính cố chấp, chỉ đợi đô đốc về quyết định dứt khoát.","Tử Kính cố chấp, chỉ đợi đô đốc về quyết định dứt khoát.",},
        [4398] = {8401,"Chu Du","Trương Chiêu","Người có nghĩ như vậy không?","Người có nghĩ như vậy không?",},
        [4399] = {8401,"Cố Ung","Chu Du","Ý kiến giống nhau.","Ý kiến giống nhau.",},
        [4400] = {8401,"Chu Du","Cố Ung","Ta cũng muốn hàng đã lâu. Sáng mai gặp chúa công, ắt sẽ có quyết định.","Ta cũng muốn hàng đã lâu. Sáng mai gặp chúa công, ắt sẽ có quyết định.",},
        [4401] = {8402,"Trình Phổ","Chu Du","Đô đốc có biết Giang Đông nay mai về tay người khác không?","Đô đốc có biết Giang Đông nay mai về tay người khác không?",},
        [4402] = {8402,"Chu Du","Trình Phổ","Chưa biết.","Chưa biết.",},
        [4403] = {8402,"Trình Phổ","Chu Du","Bọn ta theo Tôn tướng quân mở mang cơ nghiệp, hơn trăm trận đánh mới có được sáu quận. Nay chúa công muốn hàng Tào Tháo, quả thật hổ thẹn! ","Bọn ta theo Tôn tướng quân mở mang cơ nghiệp, hơn trăm trận đánh mới có được sáu quận. Nay chúa công muốn hàng Tào Tháo, quả thật hổ thẹn! ",},
        [4404] = {8402,"Trình Phổ","Chu Du","Xin đô đốc khuyên chúa công quyết kế khởi binh, bọn ta nguyện chiến đấu đến cùng.","Xin đô đốc khuyên chúa công quyết kế khởi binh, bọn ta nguyện chiến đấu đến cùng.",},
        [4405] = {8402,"Chu Du","Trình Phổ","Ý kiến các tướng quân giống nhau không?","Ý kiến các tướng quân giống nhau không?",},
        [4406] = {8402,"Hoàng Cái","Chu Du","Ta thà hy sinh, chứ không hàng Tào!","Ta thà hy sinh, chứ không hàng Tào!",},
        [4407] = {8402,"Chu Du","Hoàng Cái","Xin các tướng hãy về, ta vào gặp chúa công sẽ có quyết định.","Xin các tướng hãy về, ta vào gặp chúa công sẽ có quyết định.",},
        [4408] = {8403,"Gia Cát Cẩn","Chu Du","Gia Cát Lượng nói Lưu Dự Châu muốn kết với Đông Ngô để đánh Tào Tháo. Các quan văn võ bàn định chưa xong, chỉ đợi đô đốc về quyết định.","Gia Cát Lượng nói Lưu Dự Châu muốn kết với Đông Ngô để đánh Tào Tháo. Các quan văn võ bàn định chưa xong, chỉ đợi đô đốc về quyết định.",},
        [4409] = {8403,"Chu Du","Gia Cát Cẩn","Ý ông thế nào?","Ý ông thế nào?",},
        [4410] = {8403,"Gia Cát Cẩn","Chu Du","Hàng thì dễ yên, đánh thì khó giữ.","Hàng thì dễ yên, đánh thì khó giữ.",},
        [4411] = {8403,"Chu Du","Gia Cát Cẩn","Ta đã có chủ trương, ngày mai đến phủ sẽ quyết định.","Ta đã có chủ trương, ngày mai đến phủ sẽ quyết định.",},
        [4412] = {8404,"Lỗ Túc","Chu Du","Nay Tào Tháo đánh chiếm phía Nam, hoà với đánh, chỉ có hai đường. Ý kiến tướng quân thế nào?","Nay Tào Tháo đánh chiếm phía Nam, hoà với đánh, chỉ có hai đường. Ý kiến tướng quân thế nào?",},
        [4413] = {8404,"Chu Du","Lỗ Túc","Tào Tháo mượn tiếng thiên tử, thì không nên kháng cự. Đánh thì tất thua, hàng thì dễ yên. Ngày mai ta vào hầu chúa công xin sai sứ đi hàng Tào.","Tào Tháo mượn tiếng thiên tử, thì không nên kháng cự. Đánh thì tất thua, hàng thì dễ yên. Ngày mai ta vào hầu chúa công xin sai sứ đi hàng Tào.",},
        [4414] = {8404,"Lỗ Túc","Chu Du","Ông nói lầm rồi! Cơ nghiệp Giang Đông đã trải ba đời, sao một chốc mà để vào tay người khác?","Ông nói lầm rồi! Cơ nghiệp Giang Đông đã trải ba đời, sao một chốc mà để vào tay người khác?",},
        [4415] = {8404,"Lỗ Túc","Chu Du","Đang là lúc trông cậy tướng quân giữ cơ nghiệp, không ngờ ông lại theo lời những kẻ hèn nhát đó?","Đang là lúc trông cậy tướng quân giữ cơ nghiệp, không ngờ ông lại theo lời những kẻ hèn nhát đó?",},
        [4416] = {8404,"Chu Du","Lỗ Túc","Sáu quận Giang Đông nếu bị nạn binh đao tất quy oán cho ta, nên ta xin hàng.","Sáu quận Giang Đông nếu bị nạn binh đao tất quy oán cho ta, nên ta xin hàng.",},
        [4417] = {8404,"Lỗ Túc","Chu Du","Không thể. Đông Ngô là nơi hiểm trở, Tào Tháo chưa chắc làm gì được.","Không thể. Đông Ngô là nơi hiểm trở, Tào Tháo chưa chắc làm gì được.",},
        [4418] = {8404,"Gia Cát Lượng","Chu Du","Ha ha ha.","Ha ha ha.",},
        [4419] = {8404,"Chu Du","Gia Cát Lượng","Tiên sinh có việc gì mà cười?","Tiên sinh có việc gì mà cười?",},
        [4420] = {8404,"Gia Cát Lượng","Chu Du","Ta có cười gì đâu, chỉ cười Tử Kính không thức thời.","Ta có cười gì đâu, chỉ cười Tử Kính không thức thời.",},
        [4421] = {8405,"Lỗ Túc","Gia Cát Lượng","Sao tiên sinh bảo ta không thức thời?","Sao tiên sinh bảo ta không thức thời?",},
        [4422] = {8405,"Gia Cát Lượng","Lỗ Túc","Công Cẩn muốn hàng Tào, rất là hợp lẽ.","Công Cẩn muốn hàng Tào, rất là hợp lẽ.",},
        [4423] = {8405,"Chu Du","Gia Cát Lượng","Khổng Minh là người thức thời, tất một lòng như ta.","Khổng Minh là người thức thời, tất một lòng như ta.",},
        [4424] = {8405,"Lỗ Túc","Gia Cát Lượng","Khổng Minh! Sao ông lại nói thế?","Khổng Minh! Sao ông lại nói thế?",},
        [4425] = {8405,"Gia Cát Lượng","Lỗ Túc","Tháo rất giỏi việc dùng binh, những kẻ dám chống lại chỉ có Lữ Bố, Viên Thiệu... Mấy người đó đều bị Tháo diệt cả rồi!","Tháo rất giỏi việc dùng binh, những kẻ dám chống lại chỉ có Lữ Bố, Viên Thiệu... Mấy người đó đều bị Tháo diệt cả rồi!",},
        [4426] = {8405,"Gia Cát Lượng","Lỗ Túc","Chỉ có Lưu Dự Châu là dám chống lại, nay một mình ở đất Giang Hạ, sống còn chưa biết ra sao?","Chỉ có Lưu Dự Châu là dám chống lại, nay một mình ở đất Giang Hạ, sống còn chưa biết ra sao?",},
        [4427] = {8405,"Lỗ Túc","Gia Cát Lượng","Ngươi bảo chúa công ta chịu nhục thuần phục quốc tặc sao!","Ngươi bảo chúa công ta chịu nhục thuần phục quốc tặc sao!",},
        [4428] = {8405,"Gia Cát Lượng","Lỗ Túc","Ta có một kế, sai một người sứ đưa hai người sang sông. Tào Tháo được hai người ấy sẽ cuốn cờ rút lui ngay.","Ta có một kế, sai một người sứ đưa hai người sang sông. Tào Tháo được hai người ấy sẽ cuốn cờ rút lui ngay.",},
        [4429] = {8405,"Chu Du","Gia Cát Lượng","Dùng hai người nào mà lui được quân Tào?","Dùng hai người nào mà lui được quân Tào?",},
        [4430] = {8405,"Gia Cát Lượng","Chu Du","Hai cô con gái của Giang Đông Kiều Công!","Hai cô con gái của Giang Đông Kiều Công!",},
        [4431] = {8406,"Chu Du","Gia Cát Lượng","Vì sao dùng hai cô con gái Giang Đông Kiều Công?","Vì sao dùng hai cô con gái Giang Đông Kiều Công?",},
        [4432] = {8406,"Gia Cát Lượng","Chu Du","Khi ở Long Trung, nghe tin Tháo mới dựng đài Đồng Tước trên sông Chương Hà, chọn cô nương đẹp nhốt vào đó.","Khi ở Long Trung, nghe tin Tháo mới dựng đài Đồng Tước trên sông Chương Hà, chọn cô nương đẹp nhốt vào đó.",},
        [4433] = {8406,"Gia Cát Lượng","Chu Du","Tháo vốn háo sắc, biết bên Giang Đông có Đại Kiều và Tiểu Kiều. Hai người đều có nhan sắc chim sa cá lặn.","Tháo vốn háo sắc, biết bên Giang Đông có Đại Kiều và Tiểu Kiều. Hai người đều có nhan sắc chim sa cá lặn.",},
        [4434] = {8406,"Gia Cát Lượng","Chu Du","Tháo từng thề sẽ lấy được hai chị em nàng Kiều ở Giang Đông, đem về đài Đồng Tước để vui tuổi già!","Tháo từng thề sẽ lấy được hai chị em nàng Kiều ở Giang Đông, đem về đài Đồng Tước để vui tuổi già!",},
        [4435] = {8406,"Gia Cát Lượng","Chu Du","Nay Tháo chực chiếm Giang Nam, cũng chỉ vì hai cô nương ấy. Tướng quân sao không dâng hai người cho Tào Tháo","Nay Tháo chực chiếm Giang Nam, cũng chỉ vì hai cô nương ấy. Tướng quân sao không dâng hai người cho Tào Tháo",},
        [4436] = {8406,"Gia Cát Lượng","Chu Du","Đó cũng là kế Phạm Lãi dâng Tây Thi cho Ngô Vương.","Đó cũng là kế Phạm Lãi dâng Tây Thi cho Ngô Vương.",},
        [4437] = {8406,"Chu Du","Gia Cát Lượng","Có gì làm chứng về việc Tào Tháo muốn được hai nàng Kiều?","Có gì làm chứng về việc Tào Tháo muốn được hai nàng Kiều?",},
        [4438] = {8406,"Nhân vật","Chu Du","Tháo sai Tào Thực viết một bài phú, ý chính trong bài phú nói về nhà Tào nếu làm thiên tử thì thề sẽ lấy cho kỳ được hai nàng Kiều.","Tháo sai Tào Thực viết một bài phú, ý chính trong bài phú nói về nhà Tào nếu làm thiên tử thì thề sẽ lấy cho kỳ được hai nàng Kiều.",},
        [4439] = {8406,"Nhân vật","Chu Du","Ta có mang bài phú đến, mời xem qua.","Ta có mang bài phú đến, mời xem qua.",},
        [4440] = {8406,"Chu Du","Nhân vật","Lão tặc khinh người quá đáng!","Lão tặc khinh người quá đáng!",},
        [4441] = {8406,"Gia Cát Lượng","Chu Du","Ngày xưa Hung Nô xâm nhập, thiên tử còn đem công chúa gả cho hắn, sao tướng quân lại tiếc?","Ngày xưa Hung Nô xâm nhập, thiên tử còn đem công chúa gả cho hắn, sao tướng quân lại tiếc?",},
        [4442] = {8406,"Chu Du","Gia Cát Lượng","Ông chưa rõ Đại Kiều là thê tử Tôn Bá Phù, Tiểu Kiều là thê tử Du đó.","Ông chưa rõ Đại Kiều là thê tử Tôn Bá Phù, Tiểu Kiều là thê tử Du đó.",},
        [4443] = {8406,"Gia Cát Lượng","Chu Du","Ta thật vô tình, nói năng lỗ mỗ, đáng phạt!","Ta thật vô tình, nói năng lỗ mỗ, đáng phạt!",},
        [4444] = {8406,"Chu Du","Gia Cát Lượng","Ta không đội trời chung với lão tặc. Sau này gặp chúa công sẽ khởi binh.","Ta không đội trời chung với lão tặc. Sau này gặp chúa công sẽ khởi binh.",},
        [4445] = {8407,"Tôn Quyền","Chu Du","Mấy nay bàn luận, người khuyên ta nên hàng, kẻ lại bảo nên đánh, xin Công Cẩn quyết định cho.","Mấy nay bàn luận, người khuyên ta nên hàng, kẻ lại bảo nên đánh, xin Công Cẩn quyết định cho.",},
        [4446] = {8407,"Chu Du","Tôn Quyền","Ai khuyên chúa công hàng?","Ai khuyên chúa công hàng?",},
        [4447] = {8407,"Tôn Quyền","Chu Du","Bọn Trương Tử Bố đều muốn hàng.","Bọn Trương Tử Bố đều muốn hàng.",},
        [4448] = {8407,"Chu Du","Tôn Quyền","Muốn nghe ý kiến xin hàng.","Muốn nghe ý kiến xin hàng.",},
        [4449] = {8407,"Trương Chiêu","Chu Du","Tào Tháo mượn danh triều đình dẹp đánh bốn phương, uy thế lớn mạnh.","Tào Tháo mượn danh triều đình dẹp đánh bốn phương, uy thế lớn mạnh.",},
        [4450] = {8407,"Trương Chiêu","Chu Du","Tào Tháo có nhiều thuyền, ta lấy gì chống được. Chi bằng hãy hàng, sau tính kế khác.","Tào Tháo có nhiều thuyền, ta lấy gì chống được. Chi bằng hãy hàng, sau tính kế khác.",},
        [4451] = {8407,"Chu Du","Trương Chiêu","Giang Đông từ khi lập nước đến giờ, đã trải ba đời, sao nỡ chốc lát bỏ mất?","Giang Đông từ khi lập nước đến giờ, đã trải ba đời, sao nỡ chốc lát bỏ mất?",},
        [4452] = {8407,"Tôn Quyền","Chu Du","Thế đánh thì có kế gì?","Thế đánh thì có kế gì?",},
        [4453] = {8407,"Chu Du","Tôn Quyền","Tướng quân là bậc thần võ hùng tài, đáng lẽ tung hoành cả trong thiên hạ, sao lại phải hàng giặc?","Tướng quân là bậc thần võ hùng tài, đáng lẽ tung hoành cả trong thiên hạ, sao lại phải hàng giặc?",},
        [4454] = {8407,"Chu Du","Tôn Quyền","Tháo đi chuyến này, đất bắc chưa yên, mà đánh phương Nam, là một điều kỵ. Quân Bắc không quen đánh dưới nước là điều kỵ thứ hai.","Tháo đi chuyến này, đất bắc chưa yên, mà đánh phương Nam, là một điều kỵ. Quân Bắc không quen đánh dưới nước là điều kỵ thứ hai.",},
        [4455] = {8407,"Chu Du","Tôn Quyền","Mùa đông ngựa không có cỏ ăn là ba điều kỵ, đưa quân bộ tiến vào vùng sông nước, nhiều người đau ốm là bốn điều kỵ.","Mùa đông ngựa không có cỏ ăn là ba điều kỵ, đưa quân bộ tiến vào vùng sông nước, nhiều người đau ốm là bốn điều kỵ.",},
        [4456] = {8407,"Chu Du","Tôn Quyền","Quân Tháo phạm bốn điều kỵ ấy. Du xin vài vạn tinh binh đến đóng ở Hạ Khẩu, đủ phá được Tào Tháo.","Quân Tháo phạm bốn điều kỵ ấy. Du xin vài vạn tinh binh đến đóng ở Hạ Khẩu, đủ phá được Tào Tháo.",},
        [4457] = {8407,"Tôn Quyền","Chu Du","Phong ngươi làm đại đô đốc. Trình Phổ làm phó đô đốc khởi binh diệt Tào!","Phong ngươi làm đại đô đốc. Trình Phổ làm phó đô đốc khởi binh diệt Tào!",},
        [4458] = {8501,"Chu Du","Gia Cát Lượng","Hôm nay trong phủ bàn định đã xong, xin tiên sinh hiến kế hay để phá Tào Tháo.","Hôm nay trong phủ bàn định đã xong, xin tiên sinh hiến kế hay để phá Tào Tháo.",},
        [4459] = {8501,"Gia Cát Lượng","Chu Du","Lòng Tôn tướng quân chưa ổn, không thể định kế được vội.","Lòng Tôn tướng quân chưa ổn, không thể định kế được vội.",},
        [4460] = {8501,"Chu Du","Gia Cát Lượng","Thế nào là lòng chưa ổn định?","Thế nào là lòng chưa ổn định?",},
        [4461] = {8501,"Gia Cát Lượng","Chu Du","Vẫn còn sợ không địch nổi quân Tào. Tướng quân nên nói rõ quân số để Tôn tướng quân vững dạ thì việc lớn ắt xong.","Vẫn còn sợ không địch nổi quân Tào. Tướng quân nên nói rõ quân số để Tôn tướng quân vững dạ thì việc lớn ắt xong.",},
        [4462] = {8501,"Chu Du","Gia Cát Lượng","Tiên sinh nói phải lắm!","Tiên sinh nói phải lắm!",},
        [4463] = {8502,"Tôn Quyền","Chu Du","Đêm khuya Công Cẩn còn đến đây, chắc hẳn có việc gì?","Đêm khuya Công Cẩn còn đến đây, chắc hẳn có việc gì?",},
        [4464] = {8502,"Chu Du","Tôn Quyền","Ngày mai cất quân, chúa công còn nghi hoặc chút nào không?","Ngày mai cất quân, chúa công còn nghi hoặc chút nào không?",},
        [4465] = {8502,"Tôn Quyền","Chu Du","Ta chỉ còn lo quân Tào nhiều lắm, sợ không địch nổi.","Ta chỉ còn lo quân Tào nhiều lắm, sợ không địch nổi.",},
        [4466] = {8502,"Chu Du","Tôn Quyền","Ta đến cũng vì việc đó. Chúa công thấy hịch Tào Tháo nói dối có trăm vạn quân, nên sinh lòng nghi sợ, không xét rõ hư thực.","Ta đến cũng vì việc đó. Chúa công thấy hịch Tào Tháo nói dối có trăm vạn quân, nên sinh lòng nghi sợ, không xét rõ hư thực.",},
        [4467] = {8502,"Chu Du","Tôn Quyền","Nay xét ra hắn chỉ huy động được chục vạn quân, nhưng đa số vẫn còn nghi ngờ chưa phục.","Nay xét ra hắn chỉ huy động được chục vạn quân, nhưng đa số vẫn còn nghi ngờ chưa phục.",},
        [4468] = {8502,"Tôn Quyền","Chu Du","Công Cẩn quả thật đã gỡ được mối hoài nghi cho ta. Chỉ có ngươi và Tử Kính là hợp bụng ta.","Công Cẩn quả thật đã gỡ được mối hoài nghi cho ta. Chỉ có ngươi và Tử Kính là hợp bụng ta.",},
        [4469] = {8503,"Chu Du","Lỗ Túc","Khổng Minh đoán được tâm tư Ngô hầu. Nếu để lâu sẽ gây vạ cho Đông Ngô ta, chi bằng tiêu diệt.","Khổng Minh đoán được tâm tư Ngô hầu. Nếu để lâu sẽ gây vạ cho Đông Ngô ta, chi bằng tiêu diệt.",},
        [4470] = {8503,"Lỗ Túc","Chu Du","Không nên! Giặc Tào chưa phá được, mà đã mất người hiền sĩ, thế là tự mình hại mình.","Không nên! Giặc Tào chưa phá được, mà đã mất người hiền sĩ, thế là tự mình hại mình.",},
        [4471] = {8503,"Chu Du","Lỗ Túc","Người này giúp Lưu Bị, tất gây vạ cho Đông Ngô về sau.","Người này giúp Lưu Bị, tất gây vạ cho Đông Ngô về sau.",},
        [4472] = {8503,"Lỗ Túc","Chu Du","Nên sai anh ruột hắn là Gia Cát Cẩn sang dụ hắn về với Đông Ngô chẳng hay hơn ư?","Nên sai anh ruột hắn là Gia Cát Cẩn sang dụ hắn về với Đông Ngô chẳng hay hơn ư?",},
        [4473] = {8503,"Chu Du","Lỗ Túc","Nói phải.","Nói phải.",},
        [4474] = {8504,"Chu Du","Gia Cát Cẩn","Lệnh đệ là Khổng Minh có tài vương tá, sao lại hạ mình đi thờ Lưu Bị?","Lệnh đệ là Khổng Minh có tài vương tá, sao lại hạ mình đi thờ Lưu Bị?",},
        [4475] = {8504,"Chu Du","Gia Cát Cẩn","Nay mai y đến Giang Đông, đến dụ y về với Đông Ngô, chúa công được thêm một tay giỏi, chẳng phải việc tốt sao?","Nay mai y đến Giang Đông, đến dụ y về với Đông Ngô, chúa công được thêm một tay giỏi, chẳng phải việc tốt sao?",},
        [4476] = {8504,"Gia Cát Cẩn","Chu Du","Từ khi ta đến Giang Đông, chưa lập được chiến công nào, nay đô đốc đã sai, ta xin cố gắng.","Từ khi ta đến Giang Đông, chưa lập được chiến công nào, nay đô đốc đã sai, ta xin cố gắng.",},
        [4477] = {8505,"Gia Cát Cẩn","Gia Cát Lượng","Ngươi đồng ý bỏ Lưu Bị theo Đông Ngô?","Ngươi đồng ý bỏ Lưu Bị theo Đông Ngô?",},
        [4478] = {8505,"Gia Cát Lượng","Gia Cát Cẩn","Ta vẫn khinh Chu lang nhu nhược, không xứng đáng làm tướng","Ta vẫn khinh Chu lang nhu nhược, không xứng đáng làm tướng",},
        [4479] = {8505,"Gia Cát Cẩn","Gia Cát Lượng","Có biết Bá Di, Thúc Tề ngày xưa không?","Có biết Bá Di, Thúc Tề ngày xưa không?",},
        [4480] = {8505,"Gia Cát Cẩn","Gia Cát Lượng","Hai ông ấy dù qua đời ở núi Thủ Dương cũng còn ở với nhau. Hai ta ruột thịt một nhà, mà mỗi người thờ một chủ, chẳng đáng thẹn với Di Tề sao?","Hai ông ấy dù qua đời ở núi Thủ Dương cũng còn ở với nhau. Hai ta ruột thịt một nhà, mà mỗi người thờ một chủ, chẳng đáng thẹn với Di Tề sao?",},
        [4481] = {8505,"Gia Cát Lượng","Gia Cát Cẩn","Huynh nói là tình đệ giữ là nghĩa. Hai ta cùng là dòng dõi nhà Hán.","Huynh nói là tình đệ giữ là nghĩa. Hai ta cùng là dòng dõi nhà Hán.",},
        [4482] = {8505,"Gia Cát Lượng","Gia Cát Cẩn","Nếu huynh bỏ Đông Ngô về thờ hoàng thúc với đệ, huynh đệ sẽ được sum vầy, tình nghĩa hai đường vẹn cả.","Nếu huynh bỏ Đông Ngô về thờ hoàng thúc với đệ, huynh đệ sẽ được sum vầy, tình nghĩa hai đường vẹn cả.",},
        [4483] = {8505,"Gia Cát Lượng","Gia Cát Cẩn","Ý huynh thế nào?","Ý huynh thế nào?",},
        [4484] = {8505,"Gia Cát Cẩn","Gia Cát Lượng","Ta đã hiểu, ta cáo từ.","Ta đã hiểu, ta cáo từ.",},
        [4485] = {8506,"Chu Du","Gia Cát Lượng","Ngày trước quân Tào Tháo ít, mà Tháo đánh được Thiệu, bởi Tháo dùng mưu Hứa Du, triệt lương thực ở Ô Sào.","Ngày trước quân Tào Tháo ít, mà Tháo đánh được Thiệu, bởi Tháo dùng mưu Hứa Du, triệt lương thực ở Ô Sào.",},
        [4486] = {8506,"Chu Du","Gia Cát Lượng","Nay quân Tào tám mươi ba vạn, tất cũng phải đốt lương của Tháo trước. Ta đã dò biết được lương thảo Tào Tháo chứa cả ở núi Tụ Thiết.","Nay quân Tào tám mươi ba vạn, tất cũng phải đốt lương của Tháo trước. Ta đã dò biết được lương thảo Tào Tháo chứa cả ở núi Tụ Thiết.",},
        [4487] = {8506,"Chu Du","Gia Cát Lượng","Tiên sinh ở Hán Thượng đã lâu, am hiểu đường đất, xin phiền tiên sinh cùng cùng đến đó triệt lương Tào Tháo.","Tiên sinh ở Hán Thượng đã lâu, am hiểu đường đất, xin phiền tiên sinh cùng cùng đến đó triệt lương Tào Tháo.",},
        [4488] = {8506,"Gia Cát Lượng","Chu Du","Được.","Được.",},
        [4489] = {8507,"Nhân vật","Gia Cát Lượng","Vì Chu Du không thuyết phục được tiên sinh, mượn tay Tào Tháo để trừ hậu hoạn.","Vì Chu Du không thuyết phục được tiên sinh, mượn tay Tào Tháo để trừ hậu hoạn.",},
        [4490] = {8507,"Gia Cát Lượng","Nhân vật","Nếu ta từ chối, họ hẳn chê cười, chi bằng cứ vâng lời rồi sẽ liệu kế khác.","Nếu ta từ chối, họ hẳn chê cười, chi bằng cứ vâng lời rồi sẽ liệu kế khác.",},
        [4491] = {8507,"Nhân vật","Gia Cát Lượng","Tiên sinh lợi hại.","Tiên sinh lợi hại.",},
        [4492] = {8508,"Lỗ Túc","Gia Cát Lượng","Tiên sinh cướp lương có thành công được không?","Tiên sinh cướp lương có thành công được không?",},
        [4493] = {8508,"Gia Cát Lượng","Lỗ Túc","Ta đánh giỏi nhiều đường, không như ông và Chu Lang chỉ biết có một nghề mà thôi đâu!","Ta đánh giỏi nhiều đường, không như ông và Chu Lang chỉ biết có một nghề mà thôi đâu!",},
        [4494] = {8508,"Lỗ Túc","Gia Cát Lượng","Ta và Công Cẩn sao chỉ biết có một nghề?","Ta và Công Cẩn sao chỉ biết có một nghề?",},
        [4495] = {8508,"Gia Cát Lượng","Lỗ Túc","Giang Nam hay hát mặt bộ cầm quân tài Tử Kính, ra sông đánh thủy có Chu Du.","Giang Nam hay hát mặt bộ cầm quân tài Tử Kính, ra sông đánh thủy có Chu Du.",},
        [4496] = {8508,"Gia Cát Lượng","Lỗ Túc","Ông chỉ có tài trên bộ, chặn đường giữ ải, Chu Công Cẩn thì chỉ biết đánh thủy.","Ông chỉ có tài trên bộ, chặn đường giữ ải, Chu Công Cẩn thì chỉ biết đánh thủy.",},
        [4497] = {8509,"Lỗ Túc","Chu Du","Ta đi thăm dò, thấy Khổng Minh nói ông chỉ giỏi đánh thủy, không giỏi đánh bộ.","Ta đi thăm dò, thấy Khổng Minh nói ông chỉ giỏi đánh thủy, không giỏi đánh bộ.",},
        [4498] = {8509,"Chu Du","Lỗ Túc","Sao dám bảo ta không thể đánh bộ? Ta tự mang quân đến cướp lương Tào ở núi Tụ Thiết cho mà xem.","Sao dám bảo ta không thể đánh bộ? Ta tự mang quân đến cướp lương Tào ở núi Tụ Thiết cho mà xem.",},
        [4499] = {8510,"Lưu Bị","","Khổng Minh đến Đông Ngô, không có tin gì, không biết như thế nào?","Khổng Minh đến Đông Ngô, không có tin gì, không biết như thế nào?",},
        [4500] = {8510,"My Trúc","Lưu Bị","Thám thính thực hư, Đông Ngô nói Khổng Minh và y cùng phá Tào, chưa thể rời khỏi.","Thám thính thực hư, Đông Ngô nói Khổng Minh và y cùng phá Tào, chưa thể rời khỏi.",},
        [4501] = {8510,"My Trúc","Lưu Bị","Chu Lang muốn gặp ngươi cùng bàn kế khác.","Chu Lang muốn gặp ngươi cùng bàn kế khác.",},
        [4502] = {8510,"Quan Vũ","Lưu Bị","Chu Du đa mưu, lại không có thư của Khổng Minh, hoặc có mưu lừa gì chăng, không nên khinh thường.","Chu Du đa mưu, lại không có thư của Khổng Minh, hoặc có mưu lừa gì chăng, không nên khinh thường.",},
        [4503] = {8510,"Lưu Bị","Quan Vũ","Ta nay cùng Đông Ngô, cùng phá Tào Tháo. Nay Chu Du muốn gặp, nếu ta không đi thì không phải là tình đồng minh với nhau.","Ta nay cùng Đông Ngô, cùng phá Tào Tháo. Nay Chu Du muốn gặp, nếu ta không đi thì không phải là tình đồng minh với nhau.",},
        [4504] = {8510,"Quan Vũ","Lưu Bị","Nếu huynh trưởng muốn qua đó, đệ xin đi theo.","Nếu huynh trưởng muốn qua đó, đệ xin đi theo.",},
        [4505] = {8510,"Trương Phi","Lưu Bị","Ta cũng đi.","Ta cũng đi.",},
        [4506] = {8510,"Lưu Bị","Trương Phi","Chỉ Vân Trường đi là đủ, hơn nữa Khổng Minh còn có tứ đệ bên cạnh.","Chỉ cần Vân Trường theo ta là được, hơn nữa bên cạnh Khổng Minh còn có tứ muội.",},
        [4507] = {8511,"Lỗ Túc","Chu Du","Lưu Dự Châu đến rồi.","Lưu Dự Châu đến rồi.",},
        [4508] = {8511,"Chu Du","Lỗ Túc","Đem bao nhiêu thuyền đến.","Đem bao nhiêu thuyền đến.",},
        [4509] = {8511,"Lỗ Túc","Chu Du","Một chiếc thuyền và hơn hai mươi tên tuỳ tùng.","Một chiếc thuyền và hơn hai mươi tên tuỳ tùng.",},
        [4510] = {8511,"Chu Du","Lỗ Túc","Ra lệnh Đao Phủ Thủ mai phục trước.","Ra lệnh Đao Phủ Thủ mai phục trước.",},
        [4511] = {8512,"Chu Du","Lưu Bị","Huyền Đức mời ngồi.","Huyền Đức mời ngồi.",},
        [4512] = {8512,"Lưu Bị","Chu Du","Tiếng tướng quân lẫy lừng cả thiên hạ, dám đâu phiền đến tướng quân phải trọng vọng?","Tiếng tướng quân lẫy lừng cả thiên hạ, dám đâu phiền đến tướng quân phải trọng vọng?",},
        [4513] = {8512,"Chu Du","Lưu Bị","Nặng lời rồi.","Nặng lời rồi.",},
        [4514] = {8512,"Chu Du","Lưu Bị","Người đứng sau Huyền Đức là ai.","Người đứng sau Huyền Đức là ai.",},
        [4515] = {8512,"Lưu Bị","Chu Du","Đệ đệ Quan Vân Trường của ta.","Đệ đệ Quan Vân Trường của ta.",},
        [4516] = {8512,"Chu Du","Lưu Bị","Có phải là người trảm Nhan Lương, Văn Xú khi xưa khi?","Có phải là người trảm Nhan Lương, Văn Xú khi xưa khi?",},
