        [4551] = {8605,"Tào Tháo","","Hôm qua thua trận, mất cả nhuệ khí, nay hắn lại sang xem trộm trại ta, nên dùng kế gì để phá?","Hôm qua thua trận, mất cả nhuệ khí, nay hắn lại sang xem trộm trại ta, nên dùng kế gì để phá?",},
