        [4609] = {8702,"Chu Du","Gia Cát Lượng","Tiên sinh liệu độ mấy hôm thì xong?","Tiên sinh liệu độ mấy hôm thì xong?",},
        [4610] = {8702,"Gia Cát Lượng","Chu Du","Trong nội ba ngày sẽ nộp đủ mười vạn tên.","Trong nội ba ngày sẽ nộp đủ mười vạn tên.",},
        [4611] = {8702,"Chu Du","Gia Cát Lượng","Việc quân không phải trò đùa.","Việc quân không phải trò đùa.",},
        [4612] = {8702,"Gia Cát Lượng","Chu Du","Đâu có dám đùa với đô đốc, nếu ba ngày không xong, cam chịu trọng tội.","Đâu có dám đùa với đô đốc, nếu ba ngày không xong, cam chịu trọng tội.",},
        [4613] = {8702,"Chu Du","Gia Cát Lượng","Khi nào xong việc, sẽ có hậu thưởng.","Khi nào xong việc, sẽ có hậu thưởng.",},
        [4614] = {8702,"Gia Cát Lượng","Chu Du","Ngày mai bắt đầu làm, đến ngày thứ ba, xin đô đốc sai năm trăm quân đến bờ sông nhận tên đem về.","Ngày mai bắt đầu làm, đến ngày thứ ba, xin đô đốc sai năm trăm quân đến bờ sông nhận tên đem về.",},
        [4615] = {8703,"Lỗ Túc","Chu Du","Người này nói khoác chăng?","Người này nói khoác chăng?",},
        [4616] = {8703,"Chu Du","Lỗ Túc","Hắn tự nộp mạng, ta không ép. Hắn đã cam đoan, có cánh cũng không thoát được.","Hắn tự nộp mạng, ta không ép. Hắn đã cam đoan, có cánh cũng không thoát được.",},
        [4617] = {8703,"Chu Du","Lỗ Túc","Ta dặn bọn thợ cố làm dây dưa và không cấp đủ cho đồ dùng.","Ta dặn bọn thợ cố làm dây dưa và không cấp đủ cho đồ dùng.",},
        [4618] = {8703,"Chu Du","Lỗ Túc","Tất nhiên hắn lỡ hẹn. Khi ấy ta trị tội, xem có kêu ca được nữa không?","Tất nhiên hắn lỡ hẹn. Khi ấy ta trị tội, xem có kêu ca được nữa không?",},
        [4619] = {8704,"Gia Cát Lượng","Lỗ Túc","Ta đã bảo Tử Kính đừng nói chuyện gì với Công Cẩn, không ngờ Tử Kính không chịu giấu giếm hộ, hôm nay quả nhiên có chuyện.","Ta đã bảo Tử Kính đừng nói chuyện gì với Công Cẩn, không ngờ Tử Kính không chịu giấu giếm hộ, hôm nay quả nhiên có chuyện.",},
        [4620] = {8704,"Gia Cát Lượng","Lỗ Túc","Trong ba ngày, làm sao làm nổi mười vạn tên, Tử Kính phải cứu ta mới được.","Trong ba ngày, làm sao làm nổi mười vạn tên, Tử Kính phải cứu ta mới được.",},
        [4621] = {8704,"Lỗ Túc","Gia Cát Lượng","Ông tự rước lấy tội, ta biết cứu làm sao bây giờ?","Ông tự rước lấy tội, ta biết cứu làm sao bây giờ?",},
        [4622] = {8704,"Gia Cát Lượng","Lỗ Túc","Tử Kính cho ta mượn hai chục chiếc thuyền, trên thuyền căng vải xanh che chung quanh, xếp hơn nghìn bó cỏ.","Tử Kính cho ta mượn hai chục chiếc thuyền, trên thuyền căng vải xanh che chung quanh, xếp hơn nghìn bó cỏ.",},
        [4623] = {8704,"Gia Cát Lượng","Lỗ Túc","Ta sẽ có kế. Đến ngày thứ ba bảo đảm có đủ mười vạn tên.","Ta sẽ có kế. Đến ngày thứ ba bảo đảm có đủ mười vạn tên.",},
        [4624] = {8704,"Lỗ Túc","Gia Cát Lượng","Tuy không hiểu ý ông, nhưng ta cho phép.","Tuy không hiểu ý ông, nhưng ta cho phép.",},
        [4625] = {8705,"Lỗ Túc","Gia Cát Lượng","Ông gọi ta đến có việc gì?","Ông gọi ta đến có việc gì?",},
        [4626] = {8705,"Gia Cát Lượng","Lỗ Túc","Mời ông cùng đi lấy tên một thể.","Mời ông cùng đi lấy tên một thể.",},
        [4627] = {8705,"Lỗ Túc","Gia Cát Lượng","Lấy tên ở đâu?","Lấy tên ở đâu?",},
        [4628] = {8705,"Gia Cát Lượng","Lỗ Túc","Tử Kính không phải hỏi, cứ đi sẽ biết.","Tử Kính không phải hỏi, cứ đi sẽ biết.",},
        [4629] = {8706,"Lỗ Túc","Gia Cát Lượng","Canh năm, thuyền sao lại hướng về thủy trại Tào Tháo.","Canh năm, thuyền sao lại hướng về thủy trại Tào Tháo.",},
        [4630] = {8706,"Gia Cát Lượng","Lỗ Túc","Đừng vội, dàn thuyền thành hàng chữ nhất, đánh trống hò reo.","Đừng vội, dàn thuyền thành hàng chữ nhất, đánh trống hò reo.",},
        [4631] = {8706,"Lỗ Túc","Gia Cát Lượng","Quân Tào ùa ra thì làm thế nào?","Quân Tào ùa ra thì làm thế nào?",},
        [4632] = {8706,"Gia Cát Lượng","Lỗ Túc","Ta chắc Tào Tháo thấy sương mù sẽ không dám xuất quân, đợi khi nào sương tan thì về.","Ta chắc Tào Tháo thấy sương mù sẽ không dám xuất quân, đợi khi nào sương tan thì về.",},
        [4633] = {8707,"Vu Cấm","Tào Tháo","Thừa tướng , Đông Ngô dùng thuyền reo hò giữa sông.","Thừa tướng , Đông Ngô dùng thuyền reo hò giữa sông.",},
        [4634] = {8707,"Tào Tháo","Vu Cấm","Sương mù dày đặc, quân giặc ắt có mai phục, có thể cử thủy quân bắn tên.","Sương mù dày đặc, quân giặc ắt có mai phục, có thể cử thủy quân bắn tên.",},
        [4635] = {8707,"Tào Tháo","Vu Cấm","Trương Liêu, Từ Hoảng dẫn ba ngàn quân nỏ, bắt tên ra hướng sông.","Trương Liêu, Từ Hoảng dẫn ba ngàn quân nỏ, bắt tên ra hướng sông.",},
        [4636] = {8707,"Trương Liêu","Tào Tháo","Vâng!","Vâng!",},
        [4637] = {8708,"Gia Cát Lượng","","Tạ tên của thừa tướng!","Tạ tên của thừa tướng!",},
        [4638] = {8708,"Tào Tháo","Gia Cát Lượng","Sao! Mau cho thuyền đuổi theo.","Sao! Mau cho thuyền đuổi theo.",},
        [4639] = {8708,"Vu Cấm","Tào Tháo","Thuyền đã đi xa 20 dặm, không đuổi kịp.","Thuyền đã đi xa 20 dặm, không đuổi kịp.",},
        [4640] = {8708,"Tào Tháo","Vu Cấm","Đáng ghét, Khổng Minh mưu mẹo!","Đáng ghét, Khổng Minh mưu mẹo!",},
        [4641] = {8709,"Gia Cát Lượng","Lỗ Túc","Được mười vạn tên mà không tốn chút công sức nào. Nay mai lại đem tên bắn quân Tào, chẳng tiện lắm ư?","Được mười vạn tên mà không tốn chút công sức nào. Nay mai lại đem tên bắn quân Tào, chẳng tiện lắm ư?",},
        [4642] = {8709,"Lỗ Túc","Gia Cát Lượng","Tiên sinh làm sao biết đêm nay có sương mù lớn?","Tiên sinh làm sao biết đêm nay có sương mù lớn?",},
        [4643] = {8709,"Gia Cát Lượng","Lỗ Túc","Làm tướng mà không biết thiên văn địa lý, không giỏi binh thế, đó là tướng xoàng.","Làm tướng mà không biết thiên văn địa lý, không giỏi binh thế, đó là tướng xoàng.",},
        [4644] = {8709,"Gia Cát Lượng","Lỗ Túc","Từ ba hôm trước, ta đã tính biết hôm nay có sương mù lớn, nên mới dám nhận thời hạn ba ngày.","Từ ba hôm trước, ta đã tính biết hôm nay có sương mù lớn, nên mới dám nhận thời hạn ba ngày.",},
        [4645] = {8709,"Gia Cát Lượng","Lỗ Túc","Công Cẩn hạn cho ta mười ngày, chực đem một tội lỗi nhỏ để hại ta; Nhưng số mệnh ta đã có trời, hại làm sao nổi!","Công Cẩn hạn cho ta mười ngày, chực đem một tội lỗi nhỏ để hại ta; Nhưng số mệnh ta đã có trời, hại làm sao nổi!",},
        [4646] = {8709,"Lỗ Túc","Gia Cát Lượng","Tử Kính bái phục.","Tử Kính bái phục.",},
        [4647] = {8710,"Chu Du","Gia Cát Lượng","Mưu kế tiên sinh tài tình, kính phục.","Mưu kế tiên sinh tài tình, kính phục.",},
        [4648] = {8710,"Gia Cát Lượng","Chu Du","Chỉ là kế mọn, không đáng bàn.","Chỉ là kế mọn, không đáng bàn.",},
        [4649] = {8710,"Chu Du","Gia Cát Lượng","Hôm qua, chúa công ta cho người đến thúc giục tiến quân, xin tiên sinh chỉ mẹo.","Hôm qua, chúa công ta cho người đến thúc giục tiến quân, xin tiên sinh chỉ mẹo.",},
        [4650] = {8710,"Gia Cát Lượng","Chu Du","Tài của ta thường lắm, nghĩ sao được diệu kế?","Tài của ta thường lắm, nghĩ sao được diệu kế?",},
        [4651] = {8710,"Chu Du","Gia Cát Lượng","Ta thấy thủy trại Tào Tháo rất hợp binh pháp, không dễ phá được. Ta nghĩ ra một mẹo, xin tiên sinh quyết định giúp.","Ta thấy thủy trại Tào Tháo rất hợp binh pháp, không dễ phá được. Ta nghĩ ra một mẹo, xin tiên sinh quyết định giúp.",},
        [4652] = {8710,"Gia Cát Lượng","Chu Du","Đô đốc đừng nói vội, cùng viết vào lòng bàn tay, xem có khớp nhau không đã.","Đô đốc đừng nói vội, cùng viết vào lòng bàn tay, xem có khớp nhau không đã.",},
        [4653] = {8710,"Chu Du","Gia Cát Lượng","Ta một chữ hỏa.","Ta một chữ hỏa.",},
        [4654] = {8710,"Gia Cát Lượng","Chu Du","Ta cũng một chữ hỏa.","Ta cũng một chữ hỏa.",},
        [4655] = {8710,"Chu Du","Gia Cát Lượng","Hai chúng ta đã hợp ý nhau, xin đừng tiết lộ cho ai biết.","Hai chúng ta đã hợp ý nhau, xin đừng tiết lộ cho ai biết.",},
        [4656] = {8710,"Gia Cát Lượng","Chu Du","Tào Tháo đã hai phen mắc mưu của ta, nhưng chắc lần này hắn cũng không phòng bị.","Tào Tháo đã hai phen mắc mưu của ta, nhưng chắc lần này hắn cũng không phòng bị.",},
        [4657] = {8801,"Tuân Du","Tào Tháo","Ta hãy sai người đến Giang Đông làm gián điệp, dò xét đầy đủ tình hình mới có thể phá được.","Ta hãy sai người đến Giang Đông làm gián điệp, dò xét đầy đủ tình hình mới có thể phá được.",},
        [4658] = {8801,"Tào Tháo","Tuân Du","Ngươi nói chính hợp ý ta. Vậy ngươi thử xem ai đi làm được việc ấy?","Ngươi nói chính hợp ý ta. Vậy ngươi thử xem ai đi làm được việc ấy?",},
        [4659] = {8801,"Tuân Du","Tào Tháo","Thừa tướng sai Thái Trung, Thái Hoà sang trá hàng, tất Đông Ngô không nghi kỵ.","Thừa tướng sai Thái Trung, Thái Hoà sang trá hàng, tất Đông Ngô không nghi kỵ.",},
        [4660] = {8801,"Tào Tháo","Tuân Du","Kế hay.","Kế hay.",},
        [4661] = {8802,"Tào Tháo","Thái Hòa","Hai người dẫn quân sang Đông Ngô trá hàng, có động tĩnh gì sai người về báo ngay. Khi nào thành công, ta sẽ ban thưởng!","Hai người dẫn quân sang Đông Ngô trá hàng, có động tĩnh gì sai người về báo ngay. Khi nào thành công, ta sẽ ban thưởng!",},
        [4662] = {8802,"Thái Hòa","Tào Tháo","Hai chúng ta quyết lấy đầu Chu Du, Gia Cát Lượng về dâng dưới trướng.","Hai chúng ta quyết lấy đầu Chu Du, Gia Cát Lượng về dâng dưới trướng.",},
        [4663] = {8802,"Tào Tháo","Thái Hòa","Được, ta sẽ ban thưởng ngay cho hai ngươi.","Được, ta sẽ ban thưởng ngay cho hai ngươi.",},
        [4664] = {8802,"Tào Tháo","Thái Hòa","Hôm sau, hai người đem năm trăm quân sĩ lái thuyền xuống bờ phía nam.","Hôm sau, hai người đem năm trăm quân sĩ lái thuyền xuống bờ phía nam.",},
        [4665] = {8802,"Thái Hòa","Tào Tháo","Vâng!","Vâng!",},
        [4666] = {8803,"Thái Hòa","Chu Du","Huynh của ta là Thái Mạo, tự dưng bị Tào Tháo sát hại. Bọn ta muốn báo thù nên đến đầu hàng. Mong đô đốc thu dụng, cho làm tiền bộ.","Huynh của ta là Thái Mạo, tự dưng bị Tào Tháo sát hại. Bọn ta muốn báo thù nên đến đầu hàng. Mong đô đốc thu dụng, cho làm tiền bộ.",},
        [4667] = {8803,"Chu Du","Thái Hòa","Chào mừng gia nhập Đông Ngô! Cùng Cam Ninh dẫn quân làm tiền bộ.","Chào mừng gia nhập Đông Ngô! Cùng Cam Ninh dẫn quân làm tiền bộ.",},
        [4668] = {8803,"Thái Hòa","Chu Du","Tạ Chu Lang!","Tạ Chu Lang!",},
        [4669] = {8804,"Chu Du","Cam Ninh","Hai tên này là gian tế Tào Tháo sai đến. Ta muốn tương kế tựu kế, để chúng báo tin cho Tào Tháo. ","Hai tên này là gian tế Tào Tháo sai đến. Ta muốn tương kế tựu kế, để chúng báo tin cho Tào Tháo. ",},
        [4670] = {8804,"Chu Du","Cam Ninh","Ngươi phải đề phòng. Đợi khi ta cất quân sẽ diệt chúng để tế cờ. Ngươi phải cẩn thận, không được để lỡ việc.","Ngươi phải đề phòng. Đợi khi ta cất quân sẽ diệt chúng để tế cờ. Ngươi phải cẩn thận, không được để lỡ việc.",},
        [4671] = {8804,"Cam Ninh","Chu Du","Vâng!","Vâng!",},
        [4672] = {8805,"Hoàng Cái","Chu Du","Bái kiến Chu Lang.","Bái kiến Chu Lang.",},
        [4673] = {8805,"Chu Du","Hoàng Cái","Công Phúc đang đêm đến đây, tất có mưu hay bàn bạc?","Công Phúc đang đêm đến đây, tất có mưu hay bàn bạc?",},
        [4674] = {8805,"Hoàng Cái","Chu Du","Quân địch nhiều, không nên cầm cự lâu. Sao không dùng kế hỏa công?","Quân địch nhiều, không nên cầm cự lâu. Sao không dùng kế hỏa công?",},
        [4675] = {8805,"Chu Du","Hoàng Cái","Ai xui ông hiến kế ấy?","Ai xui ông hiến kế ấy?",},
        [4676] = {8805,"Hoàng Cái","Chu Du","Ta tự nghĩ ra, không có ai bày vẽ cho cả.","Ta tự nghĩ ra, không có ai bày vẽ cho cả.",},
        [4677] = {8805,"Chu Du","Hoàng Cái","Ta vẫn muốn dùng kế ấy,  nhưng không có ai thi hành kế trá hàng cho ta cả.","Ta vẫn muốn dùng kế ấy,  nhưng không có ai thi hành kế trá hàng cho ta cả.",},
        [4678] = {8805,"Hoàng Cái","Chu Du","Ta xin đảm nhận.","Ta xin đảm nhận.",},
        [4679] = {8805,"Chu Du","Hoàng Cái","Nếu không chịu khổ sở thì sao Tào Tháo chịu tin?","Nếu không chịu khổ sở thì sao Tào Tháo chịu tin?",},
        [4680] = {8805,"Hoàng Cái","Chu Du","Ta đội hậu ân của họ Tôn, dẫu gan óc lầy đất cũng cam.","Ta đội hậu ân của họ Tôn, dẫu gan óc lầy đất cũng cam.",},
        [4681] = {8805,"Chu Du","Hoàng Cái","Ông chịu thực hiện khổ nhục kế này, thật là may mắn cho Giang Đông.","Ông chịu thực hiện khổ nhục kế này, thật là may mắn cho Giang Đông.",},
        [4682] = {8805,"Hoàng Cái","Chu Du","Không oán hận.","Không oán hận.",},
        [4683] = {8806,"Chu Du","","Tháo dẫn vạn quân, doanh trại không phải một ngày mà phá xong được. Nay cấp lương thảo ba tháng, chuẩn bị chống địch.","Tháo dẫn vạn quân, doanh trại không phải một ngày mà phá xong được. Nay cấp lương thảo ba tháng, chuẩn bị chống địch.",},
        [4684] = {8806,"Hoàng Cái","Chu Du","Dẫu cấp ba mươi tháng lương thảo cũng chẳng làm trò gì? Nội trong cả tháng này, có thể phá được thì phá.","Dẫu cấp ba mươi tháng lương thảo cũng chẳng làm trò gì? Nội trong cả tháng này, có thể phá được thì phá.",},
        [4685] = {8806,"Hoàng Cái","Chu Du","Nếu không phá nổi, thì theo lời Tử Bố, bỏ giáp quẳng gươm, ngoảnh mặt về bắc mà hàng!","Nếu không phá nổi, thì theo lời Tử Bố, bỏ giáp quẳng gươm, ngoảnh mặt về bắc mà hàng!",},
        [4686] = {8806,"Chu Du","Hoàng Cái","Ta đây phụng mệnh chúa công, ai dám nói đến hàng là diệt. Ngươi nói thế làm nản lòng quân, không diệt ngươi thì ai phục!","Ta đây phụng mệnh chúa công, ai dám nói đến hàng là diệt. Ngươi nói thế làm nản lòng quân, không diệt ngươi thì ai phục!",},
        [4687] = {8806,"Hoàng Cái","Chu Du","Lúc theo Phá Lỗ tướng quân tung hoành, khi ấy ngươi ở đâu?","Lúc theo Phá Lỗ tướng quân tung hoành, khi ấy ngươi ở đâu?",},
        [4688] = {8806,"Chu Du","Hoàng Cái","Mau lôi ra trảm!","Mau lôi ra trảm!",},
        [4689] = {8806,"Cam Ninh","Chu Du","Công Phúc là cựu thần của Đông Ngô, xin hãy khoan thứ cho.","Công Phúc là cựu thần của Đông Ngô, xin hãy khoan thứ cho.",},
        [4690] = {8806,"Chu Du","Cam Ninh","Ngươi dám nói nhiều! Lôi Cam Ninh ra đánh trượng.","Ngươi dám nói nhiều! Lôi Cam Ninh ra đánh trượng.",},
        [4691] = {8806,"Lỗ Túc","Chu Du","Hoàng Cái đáng tội nhưng bất lợi cho quân ta. Xin đô đốc hãy khoan thứ cho, xử phạt sau.","Hoàng Cái đáng tội nhưng bất lợi cho quân ta. Xin đô đốc hãy khoan thứ cho, xử phạt sau.",},
        [4692] = {8806,"Chu Du","Lỗ Túc","Nếu không nể mặt các quan, ta đã quyết lấy mạng ngươi! Nay tạm tha cho ngươi!","Nếu không nể mặt các quan, ta đã quyết lấy mạng ngươi! Nay tạm tha cho ngươi!",},
        [4693] = {8807,"Lỗ Túc","Gia Cát Lượng","Hôm nay Công Cẩn giận đánh Hoàng Cái như thế, tiên sinh là khách sao không nói giúp một câu gì?","Hôm nay Công Cẩn giận đánh Hoàng Cái như thế, tiên sinh là khách sao không nói giúp một câu gì?",},
        [4694] = {8807,"Gia Cát Lượng","Lỗ Túc","Tử Kính còn dối ta.","Tử Kính còn dối ta.",},
        [4695] = {8807,"Lỗ Túc","Gia Cát Lượng","Túc chưa hề có câu gì dối nhau, sao tiên sinh lại nói thế?","Túc chưa hề có câu gì dối nhau, sao tiên sinh lại nói thế?",},
        [4696] = {8807,"Gia Cát Lượng","Lỗ Túc","Tử Kính không biết Công Cẩn đánh Hoàng Cái là mưu kế? Tại sao còn cần đến ta khuyên can?","Tử Kính không biết Công Cẩn đánh Hoàng Cái là mưu kế? Tại sao còn cần đến ta khuyên can?",},
        [4697] = {8807,"Lỗ Túc","Gia Cát Lượng","Thực sự không biết! Giờ mới biết!","Thực sự không biết! Giờ mới biết!",},
        [4698] = {8807,"Gia Cát Lượng","Lỗ Túc","Không dùng khổ nhục kế, sao lừa được Tào Tháo? Đô đốc sai Hoàng Cái sang trá hàng, mượn bọn Thái Trung báo tin cho Tào Tháo.","Không dùng khổ nhục kế, sao lừa được Tào Tháo? Đô đốc sai Hoàng Cái sang trá hàng, mượn bọn Thái Trung báo tin cho Tào Tháo.",},
        [4699] = {8808,"Khám Trạch","Hoàng Cái","Tướng quân có thù với đô đốc?","Tướng quân có thù với đô đốc?",},
        [4700] = {8808,"Hoàng Cái","Khám Trạch","Không đúng.","Không đúng.",},
        [4701] = {8808,"Khám Trạch","Hoàng Cái","Thế thì vừa rồi ông bị đòn có phải là khổ nhục kế không?","Thế thì vừa rồi ông bị đòn có phải là khổ nhục kế không?",},
        [4702] = {8808,"Hoàng Cái","Khám Trạch","Sao ông biết?","Sao ông biết?",},
        [4703] = {8808,"Khám Trạch","Hoàng Cái","Ta xem bộ dạng Công Cẩn, mười phần đã đoán được chín.","Ta xem bộ dạng Công Cẩn, mười phần đã đoán được chín.",},
        [4704] = {8808,"Hoàng Cái","Khám Trạch","Ta chịu hậu ân họ Tôn đã ba đời, không lấy gì báo được, nên hiến kế ấy để phá Tào Tháo.","Ta chịu hậu ân họ Tôn đã ba đời, không lấy gì báo được, nên hiến kế ấy để phá Tào Tháo.",},
        [4705] = {8808,"Hoàng Cái","Khám Trạch","Ta xem trong đám tướng sĩ, không ai là tâm phúc của, có ông là người trung nghĩa, nên mới dám thổ lộ can trường.","Ta xem trong đám tướng sĩ, không ai là tâm phúc của, có ông là người trung nghĩa, nên mới dám thổ lộ can trường.",},
        [4706] = {8808,"Khám Trạch","Hoàng Cái","Phải chăng muốn dùng ta dâng thư trá hàng.","Phải chăng muốn dùng ta dâng thư trá hàng.",},
        [4707] = {8808,"Hoàng Cái","Khám Trạch","Quả thật có ý đó, ông có chịu giúp không?","Quả thật có ý đó, ông có chịu giúp không?",},
        [4708] = {8808,"Khám Trạch","Hoàng Cái","Đại trượng phu không lập công danh, chẳng hoá ra bằng cỏ cây mục nát? Ông đã quên mình báo chúa, đây ta dám há tiếc thân!!","Đại trượng phu không lập công danh, chẳng hoá ra bằng cỏ cây mục nát? Ông đã quên mình báo chúa, đây ta dám há tiếc thân!!",},
        [4709] = {8808,"Hoàng Cái","Khám Trạch","Không biết đa tạ thế nào, chỉ còn cách lạy tạ.","Không biết đa tạ thế nào, chỉ còn cách lạy tạ.",},
        [4710] = {8808,"Khám Trạch","Hoàng Cái","Việc không nên để lâu, xin đi ngay hôm nay!","Việc không nên để lâu, xin đi ngay hôm nay!",},
        [4711] = {8808,"Hoàng Cái","Khám Trạch","Thư đã viết sẵn rồi.","Thư đã viết sẵn rồi.",},
        [4712] = {8808,"Khám Trạch","Hoàng Cái","Ta giả làm ông lão đánh cá, bơi thuyền con sang thẳng bờ phía bắc.","Ta giả làm ông lão đánh cá, bơi thuyền con sang thẳng bờ phía bắc.",},
        [4713] = {8809,"Tào Tháo","Khám Trạch","Ngươi là tham mưu Đông Ngô, đến đây làm gì?","Ngươi là tham mưu Đông Ngô, đến đây làm gì?",},
        [4714] = {8809,"Khám Trạch","Tào Tháo","Hoàng Công Phúc là cựu thần ba đời bên Đông Ngô, nay vô cớ bị Chu Du dập đánh khổ sở trước mặt các tướng. Không biết thừa tướng chịu thu nạp không?","Hoàng Công Phúc là cựu thần ba đời bên Đông Ngô, nay vô cớ bị Chu Du dập đánh khổ sở trước mặt các tướng. Không biết thừa tướng chịu thu nạp không?",},
        [4715] = {8809,"Tào Tháo","Khám Trạch","Hoàng Cái dùng khổ nhục kế, sai người đến dâng thư trá hàng! Lôi ra trảm.","Hoàng Cái dùng khổ nhục kế, sai người đến dâng thư trá hàng! Lôi ra trảm.",},
        [4716] = {8809,"Khám Trạch","Tào Tháo","Ha ha ha.","Ha ha ha.",},
        [4717] = {8809,"Tào Tháo","Khám Trạch","Ta đã biết rõ gian kế, ngươi còn cười gì nữa?","Ta đã biết rõ gian kế, ngươi còn cười gì nữa?",},
        [4718] = {8809,"Khám Trạch","Tào Tháo","Ta đâu dám cười! Ta cười Hoàng Công Phúc không biết người đó thôi!","Ta đâu dám cười! Ta cười Hoàng Công Phúc không biết người đó thôi!",},
        [4719] = {8809,"Tào Tháo","Khám Trạch","Ta đây học binh thư từ nhỏ, còn lạ gì những mẹo ấy, ngươi lừa ai thì được, chớ lừa ta sao nổi.","Ta đây học binh thư từ nhỏ, còn lạ gì những mẹo ấy, ngươi lừa ai thì được, chớ lừa ta sao nổi.",},
        [4720] = {8809,"Khám Trạch","Tào Tháo","Thôi, biết điều thì mau thu quân về, đừng có đánh chác gì nữa mà bị Chu Du tóm được. Đồ vô học!","Thôi, biết điều thì mau thu quân về, đừng có đánh chác gì nữa mà bị Chu Du tóm được. Đồ vô học!",},
        [4721] = {8809,"Tào Tháo","Khám Trạch","Sao lại bảo ta là vô học?","Sao lại bảo ta là vô học?",},
        [4722] = {8809,"Khám Trạch","Tào Tháo","Trốn chúa đi lén, không thể hẹn giờ? Nếu hẹn trước mà lỡ ra việc trong chưa xong, ngoài đã có binh tiếp ứng, chẳng hoá ra lộ chuyện mất?","Trốn chúa đi lén, không thể hẹn giờ? Nếu hẹn trước mà lỡ ra việc trong chưa xong, ngoài đã có binh tiếp ứng, chẳng hoá ra lộ chuyện mất?",},
        [4723] = {8809,"Khám Trạch","Tào Tháo","Do đó gặp lúc thuận tiện thì làm, chứ hẹn trước làm sao được. Ngươi không hiểu lẽ đó, quả là đồ vô học.","Do đó gặp lúc thuận tiện thì làm, chứ hẹn trước làm sao được. Ngươi không hiểu lẽ đó, quả là đồ vô học.",},
        [4724] = {8809,"Tào Tháo","Khám Trạch","Ta xét việc không tinh, lỡ phạm tôn nghiêm, xin tha thứ cho.","Ta xét việc không tinh, lỡ phạm tôn nghiêm, xin tha thứ cho.",},
        [4725] = {8809,"Khám Trạch","Tào Tháo","Ta với Hoàng Công Phúc đã đến hàng, còn có điều gì là dối trá nữa.","Ta với Hoàng Công Phúc đã đến hàng, còn có điều gì là dối trá nữa.",},
        [4726] = {8809,"Tào Tháo","Khám Trạch","Nếu hai người lập được công to, ngày sau sẽ được phong tước cao hơn những người khác.","Nếu hai người lập được công to, ngày sau sẽ được phong tước cao hơn những người khác.",},
        [4727] = {8809,"Khám Trạch","Tào Tháo","Bọn ta có phải vì tước lộc mà lại đây đâu, chính vì thuận lẽ trời, hợp lòng người đó thôi.","Bọn ta có phải vì tước lộc mà lại đây đâu, chính vì thuận lẽ trời, hợp lòng người đó thôi.",},
        [4728] = {8810,"Khám Trạch","Cam Ninh","Hôm qua tướng quân cứu Hoàng Công Phúc mà cũng bị đánh, ta rất căm tức.","Hôm qua tướng quân cứu Hoàng Công Phúc mà cũng bị đánh, ta rất căm tức.",},
        [4729] = {8810,"Cam Ninh","Khám Trạch","Công Cẩn chẳng coi chúng ta ra gì. Ta bị làm nhục thật xấu hổ với mọi người bên sông Trường Giang.","Công Cẩn chẳng coi chúng ta ra gì. Ta bị làm nhục thật xấu hổ với mọi người bên sông Trường Giang.",},
        [4730] = {8810,"Thái Hòa","Cam Ninh","Chẳng hay tướng quân và tiên sinh sao buồn bực làm vậy?","Chẳng hay tướng quân và tiên sinh sao buồn bực làm vậy?",},
        [4731] = {8810,"Cam Ninh","Thái Hòa","Các ngươi biết thế nào được những điều đau khổ trong lòng bọn ta!","Các ngươi biết thế nào được những điều đau khổ trong lòng bọn ta!",},
        [4732] = {8810,"Thái Hòa","Cam Ninh","Không lẽ muốn phản Đông Ngô hàng Tào Tháo?","Không lẽ muốn phản Đông Ngô hàng Tào Tháo?",},
        [4733] = {8810,"Cam Ninh","Thái Hòa","Việc của ta bị lộ rồi, phải diệt khẩu!","Việc của ta bị lộ rồi, phải diệt khẩu!",},
        [4734] = {8810,"Thái Hòa","Cam Ninh","Hai ông chớ lo, ta cũng xin đem việc tâm phúc bày tỏ với các ông","Hai ông chớ lo, ta cũng xin đem việc tâm phúc bày tỏ với các ông",},
        [4735] = {8810,"Cam Ninh","Thái Hòa","Nói mau lên!","Nói mau lên!",},
        [4736] = {8810,"Thái Hòa","Cam Ninh","Tào công sai hai bọn ta đến trá hàng, nếu hai ông có ý quy thuận Tào công, bọn ta xin dẫn tiến.","Tào công sai hai bọn ta đến trá hàng, nếu hai ông có ý quy thuận Tào công, bọn ta xin dẫn tiến.",},
        [4737] = {8810,"Cam Ninh","Thái Hòa","Ngươi nói thật?","Ngươi nói thật?",},
        [4738] = {8810,"Thái Hòa","Cam Ninh","Đâu dám nói dối!","Đâu dám nói dối!",},
        [4739] = {8810,"Cam Ninh","Thái Hòa","Nếu được như thế thì trời cũng giúp ta!","Nếu được như thế thì trời cũng giúp ta!",},
        [4740] = {8810,"Thái Hòa","Cam Ninh","Việc Hoàng Công Phúc và tướng quân bị nhục, ta đã báo tin về cho thừa tướng rồi.","Việc Hoàng Công Phúc và tướng quân bị nhục, ta đã báo tin về cho thừa tướng rồi.",},
        [4741] = {8810,"Khám Trạch","Thái Hòa","Ta đã dâng thư hàng của Công Phúc cho thừa tướng, nay đến gọi Hưng Bá cùng đến đầu hàng.","Ta đã dâng thư hàng của Công Phúc cho thừa tướng, nay đến gọi Hưng Bá cùng đến đầu hàng.",},
        [4742] = {8810,"Cam Ninh","Thái Hòa","Đại trượng phu gặp minh chủ chắc chắn sẽ theo.","Đại trượng phu gặp minh chủ chắc chắn sẽ theo.",},
        [4743] = {8811,"Tào Tháo","","Bên Trường Giang, Cam Ninh bị Chu Du làm nhục, xin làm nội ứng, Hoàng Cái bị phạt, sai Khám Trạch sang xin hàng, đều chưa đáng tin lắm.","Bên Trường Giang, Cam Ninh bị Chu Du làm nhục, xin làm nội ứng, Hoàng Cái bị phạt, sai Khám Trạch sang xin hàng, đều chưa đáng tin lắm.",},
        [4744] = {8811,"Tào Tháo","","Ai dám sang trại Chu Du dò xem hư thực ra sao?","Ai dám sang trại Chu Du dò xem hư thực ra sao?",},
        [4745] = {8811,"Tưởng Can","Tào Tháo","Ta bữa trước sang Đông Ngô, chưa làm được việc gì, vẫn còn áy náy. Nay xin liều đi chuyến nữa, quyết dò được sự thật về báo với thừa tướng.","Ta bữa trước sang Đông Ngô, chưa làm được việc gì, vẫn còn áy náy. Nay xin liều đi chuyến nữa, quyết dò được sự thật về báo với thừa tướng.",},
        [4746] = {8812,"Chu Du","Tưởng Can","Tử Dực sao dám khinh ta thế?","Tử Dực sao dám khinh ta thế?",},
        [4747] = {8812,"Tưởng Can","Chu Du","Ta nghĩ ông là chỗ tình nghĩa huynh đệ cũ, muốn đến bày tỏ tâm sự, sao lại bảo ta lừa lọc?","Ta nghĩ ông là chỗ tình nghĩa huynh đệ cũ, muốn đến bày tỏ tâm sự, sao lại bảo ta lừa lọc?",},
        [4748] = {8812,"Chu Du","Tưởng Can","Ngươi muốn dụ ta hàng Tào, trừ phi sông cạn đá mòn! Lần trước ta nể tình bạn cũ, mời uống rượu và ngủ chung giường.","Ngươi muốn dụ ta hàng Tào, trừ phi sông cạn đá mòn! Lần trước ta nể tình bạn cũ, mời uống rượu và ngủ chung giường.",},
        [4749] = {8812,"Chu Du","Tưởng Can","Không ngờ ngươi ăn cắp thư của ta, lẻn về báo với Tào Tháo, để Trương Doãn, Thái Mạo bị diệt.","Không ngờ ngươi ăn cắp thư của ta, lẻn về báo với Tào Tháo, để Trương Doãn, Thái Mạo bị diệt.",},
        [4750] = {8812,"Chu Du","Tưởng Can","Nếu không nghĩ đến tình xưa, ta đã tống ngươi đi rồi, nhưng sắp đánh Tào Tháo nên giữ ngươi lại, kẻo lộ quân cơ!","Nếu không nghĩ đến tình xưa, ta đã tống ngươi đi rồi, nhưng sắp đánh Tào Tháo nên giữ ngươi lại, kẻo lộ quân cơ!",},
        [4751] = {8812,"Chu Du","Tưởng Can","Đợi khi nào phá xong Tào Tháo, ta sẽ tiễn ngươi sang sông.","Đợi khi nào phá xong Tào Tháo, ta sẽ tiễn ngươi sang sông.",},
        [4752] = {8812,"Tưởng Can","Chu Du","Cái này...","Cái này...",},
        [4753] = {8813,"Chu Du","Lỗ Túc","Bàng Sĩ Nguyên đã chờ ở Tây Sơn.","Bàng Sĩ Nguyên đã chờ ở Tây Sơn.",},
        [4754] = {8813,"Lỗ Túc","Chu Du","Đã đến Tây Sơn.","Đã đến Tây Sơn.",},
        [4755] = {8813,"Chu Du","Lỗ Túc","Vậy chỉ chờ Tưởng Can trúng kế.","Vậy chỉ chờ Tưởng Can trúng kế.",},
        [4756] = {8813,"Nhân vật","Chu Du","Bàng Sĩ Nguyên quyết vào Tào Doanh, vì an toàn, ta giả làm tùy tùng đi cùng.","Bàng Sĩ Nguyên quyết vào Tào Doanh, vì an toàn, ta giả làm tùy tùng đi cùng.",},
        [4757] = {8813,"Chu Du","Nhân vật","Làm phiền Dương Uy Tướng.","Làm phiền Dương Uy Tướng.",},
        [4758] = {8901,"Tưởng Can","Bàng Thống","Ngươi là ai ở trong Tây Sơn, đọc sách dưới trời sao.","Ngươi là ai ở trong Tây Sơn, đọc sách dưới trời sao.",},
        [4759] = {8901,"Bàng Thống","Tưởng Can","Họ Bàng, tên Thống, tự Sĩ Nguyên.","Họ Bàng, tên Thống, tự Sĩ Nguyên.",},
        [4760] = {8901,"Tưởng Can","Bàng Thống","Có phải là Phượng Sồ tiên sinh đó không?","Có phải là Phượng Sồ tiên sinh đó không?",},
        [4761] = {8901,"Bàng Thống","Tưởng Can","Đúng vậy.","Đúng vậy.",},
        [4762] = {8901,"Tưởng Can","Bàng Thống","Nghe danh đã lâu, sao nay lại ẩn dật ở đây?","Nghe danh đã lâu, sao nay lại ẩn dật ở đây?",},
        [4763] = {8901,"Bàng Thống","Tưởng Can","Chu Du cậy tài khinh người, nên ta phải đến ở chỗ này. Xin hỏi ông là ai?","Chu Du cậy tài khinh người, nên ta phải đến ở chỗ này. Xin hỏi ông là ai?",},
        [4764] = {8901,"Tưởng Can","Bàng Thống","Ta là Tưởng Cán. Nếu tiên sinh chịu đi theo Tào công, ta xin tiến dẫn.","Ta là Tưởng Cán. Nếu tiên sinh chịu đi theo Tào công, ta xin tiến dẫn.",},
        [4765] = {8901,"Bàng Thống","Tưởng Can","Ta muốn bỏ đất Giang Đông này đã lâu. Ông đã có lòng tiến dẫn, thì phải đi ngay.","Ta muốn bỏ đất Giang Đông này đã lâu. Ông đã có lòng tiến dẫn, thì phải đi ngay.",},
        [4766] = {8901,"Tưởng Can","Bàng Thống","Hai ta sẽ xuống núi tìm thuyền, trở về Giang Bắc.","Hai ta sẽ xuống núi tìm thuyền, trở về Giang Bắc.",},
        [4767] = {8902,"Tào Tháo","Bàng Thống","Chu Du còn trẻ mà đã khinh người. Nghe danh tiên sinh đã lâu, xin tiên sinh dạy bảo cho.","Chu Du còn trẻ mà đã khinh người. Nghe danh tiên sinh đã lâu, xin tiên sinh dạy bảo cho.",},
        [4768] = {8902,"Bàng Thống","Tào Tháo","Ta vẫn được nghe thừa tướng dùng binh rất giỏi, nay xin cho được xem việc bố trí quân sĩ thế nào.","Ta vẫn được nghe thừa tướng dùng binh rất giỏi, nay xin cho được xem việc bố trí quân sĩ thế nào.",},
        [4769] = {8902,"Tào Tháo","Bàng Thống","Tiên sinh chớ có quá khen.","Tiên sinh chớ có quá khen.",},
        [4770] = {8902,"Bàng Thống","Tào Tháo","Còn mong dạy bảo giúp cho.","Còn mong dạy bảo giúp cho.",},
        [4771] = {8902,"Tào Tháo","Bàng Thống","Tại sao lại hỏi thế?","Tại sao lại hỏi thế?",},
        [4772] = {8902,"Bàng Thống","Tào Tháo","Thủy quân ốm nhiều, cần có thầy lang giỏi mới chữa được.","Thủy quân ốm nhiều, cần có thầy lang giỏi mới chữa được.",},
        [4773] = {8902,"Tào Tháo","Bàng Thống","Vì không quen thủy thổ, bệnh thổ tả bùng phát, nhiều người tử vong.","Vì không quen thủy thổ, bệnh thổ tả bùng phát, nhiều người tử vong.",},
        [4774] = {8902,"Bàng Thống","Tào Tháo","Phương pháp huấn luyện thủy quân của thừa tướng thật tuyệt diệu, nhưng tiếc chưa được toàn diện.","Phương pháp huấn luyện thủy quân của thừa tướng thật tuyệt diệu, nhưng tiếc chưa được toàn diện.",},
        [4775] = {8902,"Tào Tháo","Bàng Thống","Phương pháp nào?","Phương pháp nào?",},
        [4776] = {8902,"Bàng Thống","Tào Tháo","Ở sông lớn, sóng gió liên miên. Người phương bắc không quen ngồi thuyền bị sóng đánh nghiêng ngả mới sinh ra bệnh.","Ở sông lớn, sóng gió liên miên. Người phương bắc không quen ngồi thuyền bị sóng đánh nghiêng ngả mới sinh ra bệnh.",},
        [4777] = {8902,"Bàng Thống","Tào Tháo","Nếu đem thuyền lớn nhỏ, ghép lại thành từng cụm, đầu đuôi dùng xích sắt ghép liền với nhau, trên thuyền lát ván phẳng phiu.","Nếu đem thuyền lớn nhỏ, ghép lại thành từng cụm, đầu đuôi dùng xích sắt ghép liền với nhau, trên thuyền lát ván phẳng phiu.",},
        [4778] = {8902,"Bàng Thống","Tào Tháo","Như thế chẳng những người đi lại dễ dàng, mà đến ngựa chạy cũng được, không còn lo chi thủy triều và sóng gió nữa.","Như thế chẳng những người đi lại dễ dàng, mà đến ngựa chạy cũng được, không còn lo chi thủy triều và sóng gió nữa.",},
        [4779] = {8902,"Tào Tháo","Bàng Thống","Nhờ kế hay của tiên sinh mới có thể phá được Đông Ngô!","Nhờ kế hay của tiên sinh mới có thể phá được Đông Ngô!",},
        [4780] = {8902,"Bàng Thống","Tào Tháo","Ta cũng mới nghĩ nông cạn, xin thừa tướng xem thử có nên không.","Ta cũng mới nghĩ nông cạn, xin thừa tướng xem thử có nên không.",},
        [4781] = {8902,"Tào Tháo","Bàng Thống","Lập tức truyền thợ rèn, ngày đêm đúc dây xích và ván lớn ghép chặt các thuyền lại.","Lập tức truyền thợ rèn, ngày đêm đúc dây xích và ván lớn ghép chặt các thuyền lại.",},
        [4782] = {8903,"Từ Thứ","Bàng Thống","Hoàng Cái dùng khổ nhục kế, ngươi lại đến bày liên hoàn kế, còn không phải định dùng hỏa công hay sao!?","Hoàng Cái dùng khổ nhục kế, ngươi lại đến bày liên hoàn kế, còn không phải định dùng hỏa công hay sao!?",},
        [4783] = {8903,"Từ Thứ","Bàng Thống","Bọn ngươi mưu mô ác độc, chỉ lừa được Tào Tháo, sao che nổi mắt ta!","Bọn ngươi mưu mô ác độc, chỉ lừa được Tào Tháo, sao che nổi mắt ta!",},
        [4784] = {8903,"Bàng Thống","Từ Thứ","Nếu ngươi làm lộ kế của ta, thì nhân dân tám mươi mốt châu bên Giang Nam đều do anh hại cả!","Nếu ngươi làm lộ kế của ta, thì nhân dân tám mươi mốt châu bên Giang Nam đều do anh hại cả!",},
        [4785] = {8903,"Từ Thứ","Bàng Thống","Tính mạng tám mươi ba vạn quân mã ở đây sẽ ra sao?","Tính mạng tám mươi ba vạn quân mã ở đây sẽ ra sao?",},
        [4786] = {8903,"Nhân vật","Từ Thứ","Nguyên Trực định phá kế của ta thật?","Nguyên Trực định phá kế của ta thật?",},
        [4787] = {8903,"Từ Thứ","Nhân vật","Tào Tháo làm hại mẹ ta, ta thề suốt đời không bày một mưu kế gì cho y cả, lẽ đâu lại phá kế của ngươi?","Tào Tháo làm hại mẹ ta, ta thề suốt đời không bày một mưu kế gì cho y cả, lẽ đâu lại phá kế của ngươi?",},
        [4788] = {8903,"Từ Thứ","Nhân vật","Vì ta cũng ở trong đám quân này, hãy bày ta một kế thoát thân, ta sẽ ngăm miệng lại đi ngay chỗ khác.","Vì ta cũng ở trong đám quân này, hãy bày ta một kế thoát thân, ta sẽ ngăm miệng lại đi ngay chỗ khác.",},
        [4789] = {8903,"Nhân vật","Từ Thứ","Nguyên Trực quả có cao kiến, có thì khó đâu!","Nguyên Trực quả có cao kiến, có thì khó đâu!",},
        [4790] = {8903,"Từ Thứ","Nhân vật","Mong tướng quân chỉ giáo.","Mong tướng quân chỉ giáo.",},
        [4791] = {8903,"Nhân vật","Từ Thứ","Nguyên Trực kề tai nghe.","Nguyên Trực kề tai nghe.",},
        [4792] = {8903,"Từ Thứ","Nhân vật","Kế hay, đa tạ Dương Uy Tướng.","Kế hay, đa tạ Dương Uy Tướng.",},
        [4793] = {8904,"Tào Tháo","","Ta dẫn quân sang đánh, lúc nào cũng lo lắng về bọn Mã Đằng, Hàn Toại. Tin đồn chưa biết hư thực, nhưng cũng nên đề phòng.","Ta dẫn quân sang đánh, lúc nào cũng lo lắng về bọn Mã Đằng, Hàn Toại. Tin đồn chưa biết hư thực, nhưng cũng nên đề phòng.",},
        [4794] = {8904,"Từ Thứ","Tào Tháo","Từ khi được thừa tướng thu dùng, chưa lập được công lao nào. Ta xin ra giữ ải Tản Quan, nếu có khẩn cấp sẽ báo cho thừa tướng.","Từ khi được thừa tướng thu dùng, chưa lập được công lao nào. Ta xin ra giữ ải Tản Quan, nếu có khẩn cấp sẽ báo cho thừa tướng.",},
        [4795] = {8904,"Tào Tháo","Từ Thứ","Nếu Nguyên Trực chịu khó đi cho, ta cấp ba nghìn quân mã bộ, cùng Tang Bá làm tiên phong, lập tức đi ngay.","Nếu Nguyên Trực chịu khó đi cho, ta cấp ba nghìn quân mã bộ, cùng Tang Bá làm tiên phong, lập tức đi ngay.",},
        [4796] = {8904,"Từ Thứ","Tào Tháo","Từ Thứ cùng Tang Bá xuất phát.","Từ Thứ cùng Tang Bá xuất phát.",},
        [4797] = {9001,"Tào Tháo","","Chu Du, Lỗ Túc không biết lòng trời. Nay có người đến hàng ta làm nội ứng, trời cũng giúp ta.","Chu Du, Lỗ Túc không biết lòng trời. Nay có người đến hàng ta làm nội ứng, trời cũng giúp ta.",},
        [4798] = {9001,"Tuân Du","Tào Tháo","Thừa tướng không nên nói, sợ lộ thông tin.","Thừa tướng không nên nói, sợ lộ thông tin.",},
        [4799] = {9001,"Tào Tháo","Tuân Du","Mọi người có mặt ở đây đều là tâm phúc của ta cả, nói ra có ngại gì!","Mọi người có mặt ở đây đều là tâm phúc của ta cả, nói ra có ngại gì!",},
        [4800] = {9001,"Tào Tháo","Tuân Du","Lưu Bị, Gia Cát Lượng, không tự lượng sức, hòng đẩy núi Thái Sơn, thật ngốc nghếch!","Lưu Bị, Gia Cát Lượng, không tự lượng sức, hòng đẩy núi Thái Sơn, thật ngốc nghếch!",},
        [4801] = {9002,"Tào Tháo","","Quạ ấy làm sao mà đang đêm lại kêu?","Quạ ấy làm sao mà đang đêm lại kêu?",},
        [4802] = {9002,"Trình Dục","Tào Tháo","Quạ thấy sáng trăng, tưởng trời đã sáng, cho nên bay ra kêu.","Quạ thấy sáng trăng, tưởng trời đã sáng, cho nên bay ra kêu.",},
        [4803] = {9002,"Tào Tháo","Trình Dục","Từ khi cầm ngọn giáo này, ta phá Hoàng Cân, bắt Lữ Bố, diệt Viên Thuật... Không phụ ý chí của kẻ đại trượng phu! ","Từ khi cầm ngọn giáo này, ta phá Hoàng Cân, bắt Lữ Bố, diệt Viên Thuật... Không phụ ý chí của kẻ đại trượng phu! ",},
        [4804] = {9002,"Tào Tháo","Trình Dục","Cảnh sắc hôm nay, cảm xúc dâng trào.","Cảnh sắc hôm nay, cảm xúc dâng trào.",},
        [4805] = {9002,"Tào Tháo","Trình Dục","Uống rượu hát vang, đời có mấy khi","Uống rượu hát vang, đời có mấy khi",},
        [4806] = {9002,"Tào Tháo","Trình Dục","Cảm xúc trong lòng, nỗi sầu khó quên","Cảm xúc trong lòng, nỗi sầu khó quên",},
        [4807] = {9002,"Tào Tháo","Trình Dục","Tà áo xanh xanh, an ủi hồn ta","Tà áo xanh xanh, an ủi hồn ta",},
        [4808] = {9003,"Tào Tháo","","Nhờ có diệu kế Phượng Sồ dùng xích sắt buộc thuyền, đi trên biển như đất liền.","Nhờ có diệu kế Phượng Sồ dùng xích sắt buộc thuyền, đi trên biển như đất liền.",},
        [4809] = {9003,"Trình Dục","Tào Tháo","Thuyền ghép lại làm một tuy vững chắc thật, nhưng nếu bị đánh hỏa công thì khó bề xoay xở.","Thuyền ghép lại làm một tuy vững chắc thật, nhưng nếu bị đánh hỏa công thì khó bề xoay xở.",},
        [4810] = {9003,"Tào Tháo","Trình Dục","Trình Trọng Đức tuy đã lo xa, nhưng chưa nhìn thấu đáo.","Trình Trọng Đức tuy đã lo xa, nhưng chưa nhìn thấu đáo.",},
        [4811] = {9003,"Tuân Du","Tào Tháo","Trọng Đức nói đúng, sao thừa tướng còn cười?","Trọng Đức nói đúng, sao thừa tướng còn cười?",},
        [4812] = {9003,"Tào Tháo","Tuân Du","Dùng hỏa công phải có gió đông. Nay chỉ có gió tây, gió đông ở đâu ra?","Dùng hỏa công phải có gió đông. Nay chỉ có gió tây, gió đông ở đâu ra?",},
        [4813] = {9003,"Tào Tháo","Tuân Du","Phép đánh hỏa công phải nhờ sức gió. Mùa này làm gì có gió đông nam. Nếu dùng hỏa công tức là tự đốt quân chúng, ta có sợ gì?","Phép đánh hỏa công phải nhờ sức gió. Mùa này làm gì có gió đông nam. Nếu dùng hỏa công tức là tự đốt quân chúng, ta có sợ gì?",},
        [4814] = {9003,"Trình Dục","Tào Tháo","Thừa tướng cao kiến, bọn ta còn kém xa!","Thừa tướng cao kiến, bọn ta còn kém xa!",},
        [4815] = {9004,"Nhân vật","Bàng Thống","Sĩ Nguyên, mưu kế đã thành, Tào Tháo xuất chinh ngày mai, mau rút lui.","Sĩ Nguyên, mưu kế đã thành, Tào Tháo xuất chinh ngày mai, mau rút lui.",},
        [4816] = {9004,"Bàng Thống","Nhân vật","Được, chúng ta tìm thuyền đi đêm.","Được, chúng ta tìm thuyền đi đêm.",},
        [4817] = {9101,"Chu Du","Lỗ Túc","Gió lớn, cờ vàng phấp phới giữa hồ. Điềm xấu!","Gió lớn, cờ vàng phấp phới giữa hồ. Điềm xấu!",},
        [4818] = {9101,"Chu Du","Lỗ Túc","Dùng hỏa kế thế nào! Kế sách trước đó đều đổ sông đổ biển.","Dùng hỏa kế thế nào! Kế sách trước đó đều đổ sông đổ biển.",},
        [4819] = {9101,"Lỗ Túc","Chu Du","Cái này...","Cái này...",},
        [4820] = {9101,"Chu Du","Lỗ Túc","A!","A!",},
        [4821] = {9101,"Lỗ Túc","Chu Du","Người đâu, đô đốc bị thương, mau truyền quân y.","Người đâu, đô đốc bị thương, mau truyền quân y.",},
        [4822] = {9102,"Lỗ Túc","Gia Cát Lượng","Gió lớn không thể dùng hỏa kế, Chu Lang ngã bệnh.","Gió lớn không thể dùng hỏa kế, Chu Lang ngã bệnh.",},
        [4823] = {9102,"Gia Cát Lượng","Lỗ Túc","Theo ý ông thì ra làm sao?","Theo ý ông thì ra làm sao?",},
        [4824] = {9102,"Lỗ Túc","Gia Cát Lượng","Đây là phúc của Tào Tháo, họa của Giang Đông.","Đây là phúc của Tào Tháo, họa của Giang Đông.",},
        [4825] = {9102,"Gia Cát Lượng","Lỗ Túc","Bệnh Công Cẩn, ta có thể chữa được.","Bệnh Công Cẩn, ta có thể chữa được.",},
        [4826] = {9102,"Lỗ Túc","Gia Cát Lượng","Nếu được như thế, thì may cho Đông Ngô quá! Ta sẽ đi cùng.","Nếu được như thế, thì may cho Đông Ngô quá! Ta sẽ đi cùng.",},
        [4827] = {9103,"Lỗ Túc","Chu Du","Bệnh tình đô đốc ra sao?","Bệnh tình đô đốc ra sao?",},
        [4828] = {9103,"Chu Du","Lỗ Túc","Ruột gan đau quặn, thỉnh thoảng lại mê mẩn.","Ruột gan đau quặn, thỉnh thoảng lại mê mẩn.",},
        [4829] = {9103,"Lỗ Túc","Chu Du","Đô đốc đã dùng thuốc gì?","Đô đốc đã dùng thuốc gì?",},
        [4830] = {9103,"Chu Du","Lỗ Túc","Uống vào lại nôn ra, không thuốc nào chịu.","Uống vào lại nôn ra, không thuốc nào chịu.",},
        [4831] = {9103,"Lỗ Túc","Chu Du","Khổng Minh nói có thể chữa được bệnh đô đốc. Ông ta đang ở ngoài, cho mời vào khám chữa!","Khổng Minh nói có thể chữa được bệnh đô đốc. Ông ta đang ở ngoài, cho mời vào khám chữa!",},
        [4832] = {9103,"Chu Du","Lỗ Túc","Được.","Được.",},
        [4833] = {9104,"Gia Cát Lượng","Chu Du","Mấy bữa nay không đến hầu, ngờ đâu ngọc thể bất an!","Mấy bữa nay không đến hầu, ngờ đâu ngọc thể bất an!",},
        [4834] = {9104,"Chu Du","Gia Cát Lượng","Người ta hoạ phúc sớm tối khôn lường, biết đâu mà giữ cho xuể!","Người ta hoạ phúc sớm tối khôn lường, biết đâu mà giữ cho xuể!",},
        [4835] = {9104,"Gia Cát Lượng","Chu Du","Trời kia gió mưa bất thường, người ta có liệu trước được không?","Trời kia gió mưa bất thường, người ta có liệu trước được không?",},
        [4836] = {9104,"Chu Du","Gia Cát Lượng","Được.","Được.",},
        [4837] = {9104,"Gia Cát Lượng","Chu Du","Ta có một phương thuốc, làm cho đô đốc thuận được khí.","Ta có một phương thuốc, làm cho đô đốc thuận được khí.",},
        [4838] = {9104,"Chu Du","Gia Cát Lượng","Mong tiên sinh chỉ bảo.","Mong tiên sinh chỉ bảo.",},
        [4839] = {9104,"Gia Cát Lượng","Chu Du","Muốn đánh Tào Công, phải dùng hỏa công, muôn việc đủ cả, chỉ thiếu gió đông.","Muốn đánh Tào Công, phải dùng hỏa công, muôn việc đủ cả, chỉ thiếu gió đông.",},
        [4840] = {9104,"Chu Du","Gia Cát Lượng","Tiên sinh đã biết gốc bệnh của ta, vậy phải dùng thuốc gì trị được?","Tiên sinh đã biết gốc bệnh của ta, vậy phải dùng thuốc gì trị được?",},
        [4841] = {9104,"Gia Cát Lượng","Chu Du","Ta tuy rằng bất tài, nhưng có thể gọi gió hô mưa.","Ta tuy rằng bất tài, nhưng có thể gọi gió hô mưa.",},
        [4842] = {9104,"Gia Cát Lượng","Chu Du","Đô đốc muốn gió đông nam, phải lập đàn tại núi Nam Bình, dùng một trăm hai chục người cầm cờ đứng xung quanh.","Đô đốc muốn gió đông nam, phải lập đàn tại núi Nam Bình, dùng một trăm hai chục người cầm cờ đứng xung quanh.",},
        [4843] = {9104,"Gia Cát Lượng","Chu Du","Ta xin lên đàn mượn gió đông nam thật to ba ngày ba đêm để đô đốc dùng binh. Đô đốc nghĩ thế nào?","Ta xin lên đàn mượn gió đông nam thật to ba ngày ba đêm để đô đốc dùng binh. Đô đốc nghĩ thế nào?",},
        [4844] = {9104,"Chu Du","Gia Cát Lượng","Chỉ cần một đêm thôi. Nhưng xin tiên sinh phải làm ngay cho, chớ để chậm trễ.","Chỉ cần một đêm thôi. Nhưng xin tiên sinh phải làm ngay cho, chớ để chậm trễ.",},
        [4845] = {9104,"Gia Cát Lượng","Chu Du","Ngày 20 tháng 11 bắt đầu tế gió, đến ngày 22 thì ngưng, có được không?","Ngày 20 tháng 11 bắt đầu tế gió, đến ngày 22 thì ngưng, có được không?",},
        [4846] = {9104,"Chu Du","Gia Cát Lượng","Cử năm trăm tinh binh, đến núi Nam Bình lập đàn, cầm cờ nghe lệnh.","Cử năm trăm tinh binh, đến núi Nam Bình lập đàn, cầm cờ nghe lệnh.",},
        [4847] = {9105,"Chu Du","Lỗ Túc","Hôm ấy, trời gần tối không có gió nào. Khổng Minh nói sai rồi, trời đông thế này làm gì có được gió đông nam?","Hôm ấy, trời gần tối không có gió nào. Khổng Minh nói sai rồi, trời đông thế này làm gì có được gió đông nam?",},
        [4848] = {9105,"Lỗ Túc","Chu Du","Ta chắc Khổng Minh không phải là người nói bậy.","Ta chắc Khổng Minh không phải là người nói bậy.",},
        [4849] = {9105,"Lỗ Túc","Chu Du","Đô đốc, cờ quạt tung bay, đuôi cờ bay về phía tây bắc. Đột nhiên nổi gió Đông Nam.","Đô đốc, cờ quạt tung bay, đuôi cờ bay về phía tây bắc. Đột nhiên nổi gió Đông Nam.",},
        [4850] = {9105,"Chu Du","Lỗ Túc","Người này có phép đoạt được trời đất. Nếu để sống sẽ gây vạ lớn cho Đông Ngô, chi bằng diệt trừ hậu hoạn.","Người này có phép đoạt được trời đất. Nếu để sống sẽ gây vạ lớn cho Đông Ngô, chi bằng diệt trừ hậu hoạn.",},
        [4851] = {9105,"Chu Du","Lỗ Túc","Đinh Phụng, Từ Thịnh, hai người dẫn một trăm quân, Từ Thịnh đi đường thủy, Đinh Phụng đi đường bộ,","Đinh Phụng, Từ Thịnh, hai người dẫn một trăm quân, Từ Thịnh đi đường thủy, Đinh Phụng đi đường bộ,",},
        [4852] = {9105,"Chu Du","Lỗ Túc","Cùng đến núi Nam Bình, xử lý Gia Cát Lượng, đem thủ cấp về đây nhận thưởng.","Cùng đến núi Nam Bình, xử lý Gia Cát Lượng, đem thủ cấp về đây nhận thưởng.",},
        [4853] = {9105,"Từ Thịnh","Chu Du","Vâng, đô đốc.","Vâng, đô đốc.",},
        [4854] = {9106,"Nhân vật","Gia Cát Lượng","Chu Lang hẹp hòi, gió đông nam đã thổi, hắn sẽ cử người đến bắt ngươi.","Chu Lang hẹp hòi, gió đông nam đã thổi, hắn sẽ cử người đến bắt ngươi.",},
        [4855] = {9106,"Gia Cát Lượng","Nhân vật","Ta đã biết trước, nhờ Tử Long tiếp ứng trên sông.","Ta đã biết trước, nhờ Tử Long tiếp ứng trên sông.",},
        [4856] = {9106,"Nhân vật","Gia Cát Lượng","Đêm qua đã đậu thuyền ở bờ sông, mau đến gặp Tử Long.","Đêm qua đã đậu thuyền ở bờ sông, mau đến gặp Tử Long.",},
        [4857] = {9107,"Từ Thịnh","Gia Cát Lượng","Quân sư đừng đi! Đô đốc cho mời!","Quân sư đừng đi! Đô đốc cho mời!",},
        [4858] = {9107,"Gia Cát Lượng","Từ Thịnh","Ông hãy về bẩm với đô đốc dùng binh cho khéo, Lượng ta tạm về Hạ Khẩu, khi khác sẽ xin đến hầu.","Ông hãy về bẩm với đô đốc dùng binh cho khéo, Lượng ta tạm về Hạ Khẩu, khi khác sẽ xin đến hầu.",},
        [4859] = {9107,"Từ Thịnh","Gia Cát Lượng","Dừng lại, có chuyện muốn nói.","Dừng lại, có chuyện muốn nói.",},
        [4860] = {9107,"Gia Cát Lượng","Từ Thịnh","Ta đã biết đô đốc sẽ diệt ta, nên đã dặn trước Tử Long đến đón, tướng quân không cần phải đuổi theo!","Ta đã biết đô đốc sẽ diệt ta, nên đã dặn trước Tử Long đến đón, tướng quân không cần phải đuổi theo!",},
        [4861] = {9107,"Triệu Vân","Từ Thịnh","Ta là Triệu Tử Long ở Thường Sơn! Ta phụng mệnh đến đón quân sư, sao ngươi dám đuổi theo?","Ta là Triệu Tử Long ở Thường Sơn! Ta phụng mệnh đến đón quân sư, sao ngươi dám đuổi theo?",},
        [4862] = {9107,"Đinh Phụng","Từ Thịnh","Gia Cát Lượng mưu mẹo, Triệu Vân sức địch muôn người. Ngươi có biết trận đánh ở Đương Dương, Trường Bản không?","Gia Cát Lượng mưu mẹo, Triệu Vân sức địch muôn người. Ngươi có biết trận đánh ở Đương Dương, Trường Bản không?",},
        [4863] = {9107,"Đinh Phụng","Từ Thịnh","Chúng ta chỉ việc quay về báo là hơn cả.","Chúng ta chỉ việc quay về báo là hơn cả.",},
        [4864] = {9107,"Từ Thịnh","Đinh Phụng","Được.","Được.",},
        [4865] = {9108,"Từ Thịnh","Chu Du","Khổng Minh đã hẹn Triệu Vân đến đón, các ngươi đuổi không kịp đâu.","Khổng Minh đã hẹn Triệu Vân đến đón, các ngươi đuổi không kịp đâu.",},
        [4866] = {9108,"Chu Du","Từ Thịnh","Người này đa mưu quá, làm cho ta chẳng yên tâm chút nào.","Người này đa mưu quá, làm cho ta chẳng yên tâm chút nào.",},
        [4867] = {9108,"Lỗ Túc","Chu Du","Hãy để phá xong Tào Tháo, rồi ta sẽ liệu.","Hãy để phá xong Tào Tháo, rồi ta sẽ liệu.",},
        [4868] = {9108,"Chu Du","Lỗ Túc","Được.","Được.",},
        [4869] = {9201,"Lỗ Túc","Tào Tháo","Giang Nam có cánh buồm xuôi gió tiến lại, giữa có một lá cờ to đề Tiên phong Hoàng Cái.","Giang Nam có cánh buồm xuôi gió tiến lại, giữa có một lá cờ to đề Tiên phong Hoàng Cái.",},
        [4870] = {9201,"Tào Tháo","Lỗ Túc","Công Phúc đến hàng, trời cũng giúp ta!","Công Phúc đến hàng, trời cũng giúp ta!",},
        [4871] = {9201,"Trình Dục","Tào Tháo","Thuyền này khả nghi, không nên cho vào gần trại.","Thuyền này khả nghi, không nên cho vào gần trại.",},
        [4872] = {9201,"Tào Tháo","Trình Dục","Sao ngươi biết!","Sao ngươi biết!",},
        [4873] = {9201,"Trình Dục","Tào Tháo","Thuyền chở lương mà lại nổi bồng bềnh. Đêm nay gió đông nam to, nếu có âm mưu gì, thì làm thế nào?","Thuyền chở lương mà lại nổi bồng bềnh. Đêm nay gió đông nam to, nếu có âm mưu gì, thì làm thế nào?",},
        [4874] = {9201,"Tào Tháo","Trình Dục","Ai ra cản thuyền ấy?","Ai ra cản thuyền ấy?",},
        [4875] = {9201,"Văn Sính","Tào Tháo","Ta đã quen ở trên mặt nước, hoan nghênh đến.","Ta đã quen ở trên mặt nước, hoan nghênh đến.",},
        [4876] = {9201,"Tào Tháo","Văn Sính","Được.","Được.",},
        [4877] = {9202,"Văn Sính","Hoàng Cái","Thừa tướng lệnh các thuyền không vào gần trại, thả neo đậu lại giữa sông!","Thừa tướng lệnh các thuyền không vào gần trại, thả neo đậu lại giữa sông!",},
        [4878] = {9202,"Văn Sính","Hoàng Cái","Hạ buồm xuống!","Hạ buồm xuống!",},
        [4879] = {9202,"Hoàng Cái","Văn Sính","Bắn hỏa tiễn!","Bắn hỏa tiễn!",},
        [4880] = {9202,"Hoàng Cái","Văn Sính","Thuyền hỏa, xông vào thủy trại!","Thuyền hỏa, xông vào thủy trại!",},
        [4881] = {9202,"Văn Sính","Hoàng Cái","Không được để chúng đưa thuyền vào.","Không được để chúng đưa thuyền vào.",},
        [4882] = {9203,"Hoàng Cái","Tào Tháo","Hoàng Cái đến đây! Tào Tặc đừng hòng chạy!","Hoàng Cái đến đây! Tào Tặc đừng hòng chạy!",},
        [4883] = {9203,"Tào Tháo","Hoàng Cái","Mau bảo vệ ta.","Mau bảo vệ ta.",},
        [4884] = {9203,"Trương Liêu","Tào Tháo","Thuyền bị dây xích khoát chặt, gió bay theo gió sông.","Thuyền bị dây xích khoát chặt, gió bay theo gió sông.",},
        [4885] = {9203,"Trương Liêu","Tào Tháo","Thừa tướng mau cho thuyền vào bờ.","Thừa tướng mau cho thuyền vào bờ.",},
        [4886] = {9203,"Hoàng Cái","Trương Liêu","Chạy đâu cho thoát.","Chạy đâu cho thoát.",},
        [4887] = {9203,"Trương Liêu","Hoàng Cái","Hoàng Cái đỡ tên!","Hoàng Cái đỡ tên!",},
        [4888] = {9301,"Gia Cát Lượng","Triệu Vân","Tử Long dẫn quân đi vào Ô Lâm, mai phục trong rừng. Sau canh tư, Tào Tháo sẽ bỏ chạy theo đường này.","Tử Long dẫn quân đi vào Ô Lâm, mai phục trong rừng. Sau canh tư, Tào Tháo sẽ bỏ chạy theo đường này.",},
        [4889] = {9301,"Gia Cát Lượng","Triệu Vân","Khi hắn đi ngang sẽ đốt lửa. Nếu không diệt tận cũng diệt được một nửa.","Khi hắn đi ngang sẽ đốt lửa. Nếu không diệt tận cũng diệt được một nửa.",},
        [4890] = {9301,"Triệu Vân","Gia Cát Lượng","Ô Lâm có hai đường: Nam Quận và Kinh Châu. Không biết đón đường nào?","Ô Lâm có hai đường: Nam Quận và Kinh Châu. Không biết đón đường nào?",},
        [4891] = {9301,"Gia Cát Lượng","Triệu Vân","Nam Quận địa thế hiểm trở, Tào Tháo không dám qua, tất phải đến Kinh Châu, để kéo về Hứa Xương.","Nam Quận địa thế hiểm trở, Tào Tháo không dám qua, tất phải đến Kinh Châu, để kéo về Hứa Xương.",},
        [4892] = {9301,"Triệu Vân","Gia Cát Lượng","Tử Long xuất phát ngay.","Tử Long xuất phát ngay.",},
        [4893] = {9301,"Gia Cát Lượng","Triệu Vân","Dực Đức dẫn quân mai phục trong hang Hồ Lô, Tào Tháo không dám đến nam Di Lăng, tất chạy qua bắc Di Lăng.","Dực Đức dẫn quân mai phục trong hang Hồ Lô, Tào Tháo không dám đến nam Di Lăng, tất chạy qua bắc Di Lăng.",},
        [4894] = {9301,"Gia Cát Lượng","Triệu Vân","Ngày mai lúc tạnh mưa, quân Tào đến đó thổi cơm. Hễ thấy có khói thì đốt lửa ở sườn núi rồi đổ ra mà đánh.","Ngày mai lúc tạnh mưa, quân Tào đến đó thổi cơm. Hễ thấy có khói thì đốt lửa ở sườn núi rồi đổ ra mà đánh.",},
        [4895] = {9301,"Trương Phi","Gia Cát Lượng","Dực Đức xuất phát ngay.","Dực Đức xuất phát ngay.",},
        [4896] = {9301,"Gia Cát Lượng","Trương Phi","My Trúc, Mi Phương, Lưu Phong mang thuyền đi quanh sông vây bắt bại quân, tước lấy khí giới.","My Trúc, Mi Phương, Lưu Phong mang thuyền đi quanh sông vây bắt bại quân, tước lấy khí giới.",},
        [4897] = {9301,"My Trúc","Gia Cát Lượng","Được.","Được.",},
        [4898] = {9301,"Gia Cát Lượng","My Trúc","Võ Sương vô cùng hiểm yếu, xin công tử dẫn quân về ngay, bố trí ở các cửa bến. Quân Tào thua chạy đến đó, xông ra mà bắt.","Võ Sương vô cùng hiểm yếu, xin công tử dẫn quân về ngay, bố trí ở các cửa bến. Quân Tào thua chạy đến đó, xông ra mà bắt.",},
        [4899] = {9301,"Lưu Kỳ","Gia Cát Lượng","Ta sẽ hành động ngay.","Ta sẽ hành động ngay.",},
        [4900] = {9301,"Gia Cát Lượng","Lưu Kỳ","Chúa công nên đóng quân ở cửa Phàn Khẩu, lên núi cao xem Chu Du đêm nay thành công lớn.","Chúa công nên đóng quân ở cửa Phàn Khẩu, lên núi cao xem Chu Du đêm nay thành công lớn.",},
        [4901] = {9301,"Lưu Bị","Gia Cát Lượng","Tiên sinh anh minh.","Tiên sinh anh minh.",},
        [4902] = {9302,"Quan Vũ","Gia Cát Lượng","Quan Mỗ theo huynh trưởng chinh chiến nhiều năm. Hôm nay gặp địch mạnh, quân sư không dùng là có ý gì?","Quan Mỗ theo huynh trưởng chinh chiến nhiều năm. Hôm nay gặp địch mạnh, quân sư không dùng là có ý gì?",},
        [4903] = {9302,"Gia Cát Lượng","Quan Vũ","Vân Trường đừng trách! Ta vốn định nhờ túc hạ giữ một cửa ải quan trọng nhất, nhưng có chút trở ngại không dám điều đi.","Vân Trường đừng trách! Ta vốn định nhờ túc hạ giữ một cửa ải quan trọng nhất, nhưng có chút trở ngại không dám điều đi.",},
        [4904] = {9302,"Quan Vũ","Gia Cát Lượng","Có trở ngại gì? Đồng ý gặp Du.","Có trở ngại gì? Đồng ý gặp Du.",},
        [4905] = {9302,"Gia Cát Lượng","Quan Vũ","Trước đây Tào Tháo đối xử tốt với túc hạ, túc hạ đương nhiên báo đáp. Nay Tháo dẫn binh thua, chắc chắn đi Hoa Dung Đạo, nếu lệnh túc hạ đi, chắc chắn cho hắn đi qua.","Trước đây Tào Tháo đối xử tốt với túc hạ, túc hạ đương nhiên báo đáp. Nay Tháo dẫn binh thua, chắc chắn đi Hoa Dung Đạo, nếu lệnh túc hạ đi, chắc chắn cho hắn đi qua.",},
        [4906] = {9302,"Quan Vũ","Gia Cát Lượng","Quân sư suy nghĩ nhiều rồi! Ngày trước Tào Tháo đúng là trọng đãi ta, ta đã trảm Nhan Lương, diệt Văn Xú, giải Bạch Mã Chi Vi báo đáp hắn. Hôm nay gặp, tại sao lại phải tha!","Quân sư suy nghĩ nhiều rồi! Ngày trước Tào Tháo đúng là trọng đãi ta, ta đã trảm Nhan Lương, diệt Văn Xú, giải Bạch Mã Chi Vi báo đáp hắn. Hôm nay gặp, tại sao lại phải tha!",},
        [4907] = {9302,"Gia Cát Lượng","Quan Vũ","Nếu tha thì sao?","Nếu tha thì sao?",},
        [4908] = {9302,"Quan Vũ","Gia Cát Lượng","Nguyện theo quân pháp!","Nguyện theo quân pháp!",},
        [4909] = {9302,"Gia Cát Lượng","Quan Vũ","Vậy thì, lập văn thư.","Vậy thì, lập văn thư.",},
        [4910] = {9302,"Quan Vũ","Gia Cát Lượng","Nếu Tào Tháo không đi đường đó lên thì sao?","Nếu Tào Tháo không đi đường đó lên thì sao?",},
        [4911] = {9302,"Gia Cát Lượng","Quan Vũ","Ta và ngươi cũng dùng quân Lệnh trạng.","Ta và ngươi cũng dùng quân Lệnh trạng.",},
        [4912] = {9302,"Gia Cát Lượng","Quan Vũ","Vân Trường ở trên núi cao đường nhỏ Hoa Dung, đốt củi tạo khói, dụ Tào Tháo đến.","Vân Trường ở trên núi cao đường nhỏ Hoa Dung, đốt củi tạo khói, dụ Tào Tháo đến.",},
        [4913] = {9302,"Quan Vũ","Gia Cát Lượng","Tào Tháo thấy khói, biết có mai phục, làm sao chịu đến?","Tào Tháo thấy khói, biết có mai phục, làm sao chịu đến?",},
        [4914] = {9302,"Gia Cát Lượng","Quan Vũ","Chưa nghe qua luận hư hư thực thực trong binh pháp sao? Tháo tuy biết dụng binh, nhưng lần này có thể giấu được hắn. Hắn thấy khói bốc lên, sẽ bảo phô trương thanh thế, chắc chắn đi đường này đến. Tướng quân đừng dung tình.","Chưa nghe qua luận hư hư thực thực trong binh pháp sao? Tháo tuy biết dụng binh, nhưng lần này có thể giấu được hắn. Hắn thấy khói bốc lên, sẽ bảo phô trương thanh thế, chắc chắn đi đường này đến. Tướng quân đừng dung tình.",},
        [4915] = {9303,"Lưu Bị","Gia Cát Lượng","Đệ ta nghĩa khí sâu nặng, nếu Tào Tháo đúng là đi Hoa Dung Đạo, sợ là sẽ tha cho hắn.","Đệ ta nghĩa khí sâu nặng, nếu Tào Tháo đúng là đi Hoa Dung Đạo, sợ là sẽ tha cho hắn.",},
        [4916] = {9303,"Gia Cát Lượng","Lưu Bị","Đêm tối quan sát càn tượng, Tháo tặc chưa tử vong. Vân Trường giữ ân tình này, cũng là việc tốt.","Đêm tối quan sát càn tượng, Tháo tặc chưa tử vong. Vân Trường giữ ân tình này, cũng là việc tốt.",},
        [4917] = {9303,"Lưu Bị","Gia Cát Lượng","Tiên sinh thần toán, thế gian hiếm có!","Tiên sinh thần toán, thế gian hiếm có!",},
        [4918] = {9401,"Tào Tháo","Hứa Chử","Phía trước là đâu?","Phía trước là đâu?",},
        [4919] = {9401,"Hứa Chử","Tào Tháo","Một bên là Nam Di Lăng Đại Lộ, một bên là Bắc Di Lăng Đại Lộ","Một bên là Nam Di Lăng Đại Lộ, một bên là Bắc Di Lăng Đại Lộ",},
        [4920] = {9401,"Tào Tháo","Hứa Chử","Nơi nào đi Nam Quận Giang Lăng gần?","Nơi nào đi Nam Quận Giang Lăng gần?",},
        [4921] = {9401,"Hứa Chử","Tào Tháo","Đi Nam Di Lăng qua Hồ Lô Khẩu là tiện nhất.","Đi Nam Di Lăng qua Hồ Lô Khẩu là tiện nhất.",},
        [4922] = {9401,"Tào Tháo","Hứa Chử","Đi Nam Di Lăng.","Đi Nam Di Lăng.",},
        [4923] = {9402,"Tào Tháo","","Ha ha ha.","Ha ha ha.",},
        [4924] = {9402,"Trương Liêu","Tào Tháo","Sao thừa tướng lại cười?","Sao thừa tướng lại cười?",},
        [4925] = {9402,"Tào Tháo","Trương Liêu","Ta cười Gia Cát Lượng, Chu Du trí mưu không đủ. Nếu ta dụng binh, thì ngay nơi này, cũng mai phục binh mã, đợi quân địch mệt rồi tấn công.","Ta cười Gia Cát Lượng, Chu Du trí mưu không đủ. Nếu ta dụng binh, thì ngay nơi này, cũng mai phục binh mã, đợi quân địch mệt rồi tấn công.",},
        [4926] = {9402,"Tào Tháo","Trương Liêu","Chúng ta cho dù thoát hiểm cũng khó tránh trọng thương. Ta cười là vì vậy.","Chúng ta cho dù thoát hiểm cũng khó tránh trọng thương. Ta cười là vì vậy.",},
        [4927] = {9402,"Trương Phi","Tào Tháo","Ai nói Khổng Minh không đủ mưu trí, Dực Đức ở đây chờ đã lâu!","Ai nói Khổng Minh không đủ mưu trí, Dực Đức ở đây chờ đã lâu!",},
        [4928] = {9402,"Trương Liêu","Trương Phi","Thừa tướng đi trước, ta đến yểm hộ.","Thừa tướng đi trước, ta đến yểm hộ.",},
        [4929] = {9403,"Trương Liêu","Tào Tháo","Phía trước có hai ngã, xin hỏi đi ngã nào?","Phía trước có hai ngã, xin hỏi đi ngã nào?",},
        [4930] = {9403,"Tào Tháo","Trương Liêu","Con đường nào gần?","Con đường nào gần?",},
        [4931] = {9403,"Trương Liêu","Tào Tháo","Đường lớn bằng phẳng, nhưng xa hơn 50 dặm. Đường nhỏ vào Hoa Dung Đạo, nhưng gần hơn 50 dặm, chỉ là đường hẹp hiểm trở khó đi.","Đường lớn bằng phẳng, nhưng xa hơn 50 dặm. Đường nhỏ vào Hoa Dung Đạo, nhưng gần hơn 50 dặm, chỉ là đường hẹp hiểm trở khó đi.",},
        [4932] = {9403,"Trương Liêu","Tào Tháo","Hơn nữa bên vách núi đường nhỏ có vài nơi có khói, đường lớn thì không có động tĩnh gì.","Hơn nữa bên vách núi đường nhỏ có vài nơi có khói, đường lớn thì không có động tĩnh gì.",},
        [4933] = {9403,"Tào Tháo","Trương Liêu","Đi đường nhỏ Hoa Dung Đạo.","Đi đường nhỏ Hoa Dung Đạo.",},
        [4934] = {9403,"Trương Liêu","Tào Tháo","Có khói hiệu, tất có binh mã, sao phải quay về đường này?","Có khói hiệu, tất có binh mã, sao phải quay về đường này?",},
        [4935] = {9403,"Tào Tháo","Trương Liêu","Không nghe binh thư nói sao: Hư tắc thực chi, thực tắc hư chi.","Không nghe binh thư nói sao: Hư tắc thực chi, thực tắc hư chi.",},
        [4936] = {9403,"Tào Tháo","Trương Liêu","Gia Cát Lượng đa mưu, sai người ở vách núi tạo khói, khiến quân ta không dám đi con đường núi này, hắn phục binh chờ ở đường lớn. Ta đã quyết định, không để trúng kế hắn!","Gia Cát Lượng đa mưu, sai người ở vách núi tạo khói, khiến quân ta không dám đi con đường núi này, hắn phục binh chờ ở đường lớn. Ta đã quyết định, không để trúng kế hắn!",},
        [4937] = {9403,"Trương Liêu","Tào Tháo","Thừa tướng diệu toán, không ai sánh bằng.","Thừa tướng diệu toán, không ai sánh bằng.",},
        [4938] = {9404,"Trương Liêu","Tào Tháo","Không được, hai bên có 500 Đao Thủ, thủ Đại Tướng Quan Vân Trường, cầm Thanh Long Đao, cưỡi ngựa Xích Thố, cản trở đường đi.","Không được, hai bên có 500 Đao Thủ, thủ Đại Tướng Quan Vân Trường, cầm Thanh Long Đao, cưỡi ngựa Xích Thố, cản trở đường đi.",},
        [4939] = {9404,"Tào Tháo","Trương Liêu","Đã đến đây, phải quyết một trận tử chiến!","Đã đến đây, phải quyết một trận tử chiến!",},
        [4940] = {9404,"Trình Dục","Tào Tháo","Ta biết Vân Trường ngạo thượng mà không nhẫn hạ, bắt nạt kẻ mạnh chứ không ức hiếp kẻ yếu, ân oán phân minh, là người tín nghĩa.","Ta biết Vân Trường ngạo thượng mà không nhẫn hạ, bắt nạt kẻ mạnh chứ không ức hiếp kẻ yếu, ân oán phân minh, là người tín nghĩa.",},
        [4941] = {9404,"Trình Dục","Tào Tháo","Thừa tướng ngày trước có ơn với người, nay đích thân nói làm sao để thoát nạn.","Thừa tướng ngày trước có ơn với người, nay đích thân nói làm sao để thoát nạn.",},
        [4942] = {9405,"Tào Tháo","Quan Vũ","Tướng quân khỏe chứ!","Tướng quân khỏe chứ!",},
        [4943] = {9405,"Quan Vũ","Tào Tháo","Quan mỗ phụng lệnh, đợi thừa tướng đã lâu.","Quan mỗ phụng lệnh, đợi thừa tướng đã lâu.",},
        [4944] = {9405,"Tào Tháo","Quan Vũ","Tào Tháo binh bại thế nguy, đến đây hết đường, mong tướng quân nể tình ngày xưa.","Tào Tháo binh bại thế nguy, đến đây hết đường, mong tướng quân nể tình ngày xưa.",},
        [4945] = {9405,"Quan Vũ","Tào Tháo","Quan mỗ tuy nhận hậu ân từ thừa tướng, đã trảm Nhan Lương, diệt Văn Xú, giải vây Bạch Mã báo đáp. Việc hôm nay, sao dám lấy tư phế công?","Quan mỗ tuy nhận hậu ân từ thừa tướng, đã trảm Nhan Lương, diệt Văn Xú, giải vây Bạch Mã báo đáp. Việc hôm nay, sao dám lấy tư phế công?",},
        [4946] = {9405,"Tào Tháo","Quan Vũ","Lúc ngũ quan trảm tướng, có còn nhớ không? Đại trượng phu lấy tín nghĩa làm trọng. Tướng quân hiểu rõ Xuân Thu chẳng lẽ không biết chuyện Dữu Công Chi Tư truy Tử Trạc Nhũ Tử?","Lúc ngũ quan trảm tướng, có còn nhớ không? Đại trượng phu lấy tín nghĩa làm trọng. Tướng quân hiểu rõ Xuân Thu chẳng lẽ không biết chuyện Dữu Công Chi Tư truy Tử Trạc Nhũ Tử?",},
        [4947] = {9405,"Quan Vũ","Tào Tháo","Tướng sĩ giải tán.","Tướng sĩ giải tán.",},
        [4948] = {9405,"Tào Tháo","Quan Vũ","Đa tạ tướng quân.","Đa tạ tướng quân.",},
        [4949] = {9501,"Tào Tháo","","Phía Nam dâng một con gà rừng, gà rừng rất thích lông của mình, nhìn thấy ảnh phản chiếu dưới nước sẽ nhảy múa.","Phía Nam dâng một con gà rừng, gà rừng rất thích lông của mình, nhìn thấy ảnh phản chiếu dưới nước sẽ nhảy múa.",},
        [4950] = {9501,"Tào Tháo","","Ta muốn nó nhảy múa nhưng không có cách nào.","Ta muốn nó nhảy múa nhưng không có cách nào.",},
        [4951] = {9501,"Tào Xung","Tào Tháo","Hay để gà rừng trước gương thử.","Hay để gà rừng trước gương thử.",},
        [4952] = {9501,"Tào Tháo","Tào Xung","Ừ, cũng là một cách.","Ừ, cũng là một cách.",},
        [4953] = {9502,"Tào Tháo","Tào Xung","Ta để một cái gương lớn trước mặt gà rừng, gà thấy gương liền bắt đầu nhảy múa.","Ta để một cái gương lớn trước mặt gà rừng, gà thấy gương liền bắt đầu nhảy múa.",},
        [4954] = {9502,"Tào Tháo","Tào Xung","Nhưng gà rừng nhảy mãi không ngưng, cuối cùng kiệt sức.","Nhưng gà rừng nhảy mãi không ngưng, cuối cùng kiệt sức.",},
        [4955] = {9502,"Tào Xung","Tào Tháo","Có lẽ đó chính là nhìn thân thương phận.","Có lẽ đó chính là nhìn thân thương phận.",},
        [4956] = {9503,"Hoàn Phu Nhân","Tào Tháo","Mạnh Đức, Tào Xung đột nhiên trọng bệnh, ngươi mau đi xem","Mạnh Đức, Tào Xung đột nhiên trọng bệnh, ngươi mau đi xem",},
        [4957] = {9503,"Tào Tháo","Hoàn Phu Nhân","Sao? Ta về Hứa Đô ngay.","Sao? Ta về Hứa Đô ngay.",},
        [4958] = {9504,"Tào Tháo","Hoàn Phu Nhân","Có mời đại phu xem chưa?","Có mời đại phu xem chưa?",},
        [4959] = {9504,"Hoàn Phu Nhân","Tào Tháo","Đã nhờ đại phu xem, không biết là bệnh gì.","Đã nhờ đại phu xem, không biết là bệnh gì.",},
        [4960] = {9504,"Tào Tháo","Hoàn Phu Nhân","Yên tâm, ta tìm hết danh y khắp thiên hạ cũng phải chữa bệnh cho Tào Xung.","Yên tâm, ta tìm hết danh y khắp thiên hạ cũng phải chữa bệnh cho Tào Xung.",},
        [4961] = {9504,"Tào Tháo","Hoàn Phu Nhân","Mau, đi tìm danh y cho ta! Nhanh!","Mau, đi tìm danh y cho ta! Nhanh!",},
        [4962] = {9504,"Tào Nhân","Tào Tháo","Vâng!","Vâng!",},
        [4963] = {9505,"Tào Phi","Tào Tháo","Tào Xung Qua Đời, là do không may, đừng quá đau buồn.","Tào Xung Qua Đời, là do không may, đừng quá đau buồn.",},
        [4964] = {9505,"Tào Tháo","Tào Phi","Kiến An năm thứ 2, Tào Ngang theo ta xuất chinh Trương Tú, phụ trách đoạn hậu mà qua đời.","Kiến An năm thứ 2, Tào Ngang theo ta xuất chinh Trương Tú, phụ trách đoạn hậu mà qua đời.",},
        [4965] = {9505,"Tào Tháo","Tào Phi","Tào Xung vô cùng thông minh, mới 13 tuổi đã qua đời, sao ta có thể không đau lòng!","Tào Xung vô cùng thông minh, mới 13 tuổi đã qua đời, sao ta có thể không đau lòng!",},
        [4966] = {9505,"Tào Tháo","Tào Phi","Đó là bất hạnh của ta, nhưng là may mắn của Nhữ Tào.","Đó là bất hạnh của ta, nhưng là may mắn của Nhữ Tào.",},
        [4967] = {9601,"Chu Du","Lưu Bị","Dự Châu chuyển binh đến đây, lẽ nào có ý chiếm Nam Quận?","Dự Châu chuyển binh đến đây, lẽ nào có ý chiếm Nam Quận?",},
        [4968] = {9601,"Lưu Bị","Chu Du","Nghe tin Đô Đốc muốn chiếm Nam Quận, cố tình đến tương trợ. Nếu Đô Đốc không dám, Bị sẽ chiếm.","Nghe tin Đô Đốc muốn chiếm Nam Quận, cố tình đến tương trợ. Nếu Đô Đốc không dám, Bị sẽ chiếm.",},
        [4969] = {9601,"Chu Du","Lưu Bị","Đông Ngô ta muốn thôn tính Hán Giang, nay Nam Quận đã nằm trong tay, sao lại không dám?","Đông Ngô ta muốn thôn tính Hán Giang, nay Nam Quận đã nằm trong tay, sao lại không dám?",},
        [4970] = {9601,"Lưu Bị","Chu Du","Thắng thua không thể dự đoán. Tào Tháo trước khi về, ra lệnh Tào Nhân thủ Nam Quận, chắc chắn có mưu kế, cộng thêm Tào Nhân dũng mãnh, e là Đô Đốc không thể chiếm.","Thắng thua không thể dự đoán. Tào Tháo trước khi về, ra lệnh Tào Nhân thủ Nam Quận, chắc chắn có mưu kế, cộng thêm Tào Nhân dũng mãnh, e là Đô Đốc không thể chiếm.",},
        [4971] = {9601,"Chu Du","Lưu Bị","Nếu ta không chiếm được, thì mặc cho công chiếm.","Nếu ta không chiếm được, thì mặc cho công chiếm.",},
        [4972] = {9601,"Lưu Bị","Chu Du","Tử Kính, Khổng Minh làm chứng tại đây, Đô Đốc đừng nuốt lời.","Tử Kính, Khổng Minh làm chứng tại đây, Đô Đốc đừng nuốt lời.",},
        [4973] = {9601,"Chu Du","Lưu Bị","Đại trượng phu đã nói ra thì sẽ không hối hận!","Đại trượng phu đã nói ra thì sẽ không hối hận!",},
        [4974] = {9601,"Lưu Bị","Chu Du","Đô Đốc nói vậy, là công luận. Để Đông Ngô chiếm trước, nếu không được, chúa công sẽ chiếm, có gì không được!","Đô Đốc nói vậy, là công luận. Để Đông Ngô chiếm trước, nếu không được, chúa công sẽ chiếm, có gì không được!",},
        [4975] = {9602,"Lưu Bị","Gia Cát Lượng","Tiên sinh dạy Bị trả lời như vậy, tuy đã nói theo, nhưng suy đi nghĩ lại vẫn chưa hiểu. Nay ta một thân một mình, không nơi dừng chân, muốn được Nam Quận làm chốn dung thân.","Tiên sinh dạy Bị trả lời như vậy, tuy đã nói theo, nhưng suy đi nghĩ lại vẫn chưa hiểu. Nay ta một thân một mình, không nơi dừng chân, muốn được Nam Quận làm chốn dung thân.",},
        [4976] = {9602,"Lưu Bị","Gia Cát Lượng","Nếu dạy Chu Du chiếm, thành trì đã thuộc về Đông Ngô, thì làm sao ở?","Nếu dạy Chu Du chiếm, thành trì đã thuộc về Đông Ngô, thì làm sao ở?",},
        [4977] = {9602,"Gia Cát Lượng","Lưu Bị","Trước đó Lượng khuyên chúa công chiếm Kinh Châu, chúa công không nghe, nay lại muốn chiếm?","Trước đó Lượng khuyên chúa công chiếm Kinh Châu, chúa công không nghe, nay lại muốn chiếm?",},
        [4978] = {9602,"Lưu Bị","Gia Cát Lượng","Trước đó là nơi của Cảnh Thăng, ta không nỡ chiếm, nay đã là của Tào Tháo, chiếm là hợp lý.","Trước đó là nơi của Cảnh Thăng, ta không nỡ chiếm, nay đã là của Tào Tháo, chiếm là hợp lý.",},
        [4979] = {9602,"Gia Cát Lượng","Lưu Bị","Không cần chúa công lo lắng. Cứ để Chu Du đi tàn sát, sớm muộn chúa công sẽ ngồi vị trí cao trong Nam Quận Thành.","Không cần chúa công lo lắng. Cứ để Chu Du đi tàn sát, sớm muộn chúa công sẽ ngồi vị trí cao trong Nam Quận Thành.",},
        [4980] = {9602,"Lưu Bị","Gia Cát Lượng","Ngươi có kế gì?","Ngươi có kế gì?",},
        [4981] = {9602,"Gia Cát Lượng","Lưu Bị","Hãy ghé tai nghe.","Hãy ghé tai nghe.",},
        [4982] = {9603,"Lỗ Túc","Chu Du","Đô Đốc làm thế nào để Huyền Đức chiếm Nam Quận?","Đô Đốc làm thế nào để Huyền Đức chiếm Nam Quận?",},
        [4983] = {9603,"Chu Du","Lỗ Túc","Ta búng tay là có được Nam Quận, uổng công làm ơn.","Ta búng tay là có được Nam Quận, uổng công làm ơn.",},
        [4984] = {9603,"Chu Du","Lỗ Túc","Ai dám chiếm Nam Quận trước?","Ai dám chiếm Nam Quận trước?",},
        [4985] = {9603,"Tưởng Khâm","Chu Du","Ta!","Ta!",},
        [4986] = {9603,"Chu Du","Tưởng Khâm","Ngươi là Tiên Phong, Từ Thịnh, Đinh Phụng là Phó Tướng, dẫn 5000 lính ngựa tinh nhuệ, qua sông trước. Ta dẫn binh theo tiếp ứng sau.","Ngươi là Tiên Phong, Từ Thịnh, Đinh Phụng là Phó Tướng, dẫn 5000 lính ngựa tinh nhuệ, qua sông trước. Ta dẫn binh theo tiếp ứng sau.",},
        [4987] = {9604,"Ngưu Kim","Tào Nhân","Binh Ngô đã vượt Hán Giang.","Binh Ngô đã vượt Hán Giang.",},
        [4988] = {9604,"Tào Nhân","Ngưu Kim","Giữ vững không giao chiến.","Giữ vững không giao chiến.",},
        [4989] = {9604,"Ngưu Kim","Tào Nhân","Binh đến thành mà không chiến, là sợ hãi. Huống chi Ngô binh vừa bại, nên gầy dựng lại nhuệ khí. Ta nguyện mượn năm trăm tinh binh, quyết một trận tử chiến.","Binh đến thành mà không chiến, là sợ hãi. Huống chi Ngô binh vừa bại, nên gầy dựng lại nhuệ khí. Ta nguyện mượn năm trăm tinh binh, quyết một trận tử chiến.",},
        [4990] = {9604,"Tào Nhân","Ngưu Kim","Được, Ngưu Kim ngươi dẫn năm trăm quân xuất chiến.","Được, Ngưu Kim ngươi dẫn năm trăm quân xuất chiến.",},
        [4991] = {9605,"Chu Du","Cam Ninh","Ta dẫn binh, phải đích thân quyết chiến Tào Nhân.","Ta dẫn binh, phải đích thân quyết chiến Tào Nhân.",},
        [4992] = {9605,"Cam Ninh","Chu Du","Đô Đốc đừng vội. Nay Tào Nhân lệnh Tào Hồng trấn thủ Di Lăng, tạo thế bao vây, ta nguyện dẫn ba ngàn tinh binh chiếm Di Lăng, sau đó Đô Đốc có thể chiếm Nam Quận.","Đô Đốc đừng vội. Nay Tào Nhân lệnh Tào Hồng trấn thủ Di Lăng, tạo thế bao vây, ta nguyện dẫn ba ngàn tinh binh chiếm Di Lăng, sau đó Đô Đốc có thể chiếm Nam Quận.",},
        [4993] = {9605,"Chu Du","Cam Ninh","Sau đó, ngươi dẫn ba ngàn binh tấn công Di Lăng.","Sau đó, ngươi dẫn ba ngàn binh tấn công Di Lăng.",},
        [4994] = {9606,"Tào Nhân","Ngưu Kim","Mật thám báo tin, Đông Ngô Cam Ninh dẫn ba ngàn tinh binh tấn công Di Lăng.","Mật thám báo tin, Đông Ngô Cam Ninh dẫn ba ngàn tinh binh tấn công Di Lăng.",},
        [4995] = {9606,"Tào Nhân","Ngưu Kim","Di Lăng mà mất, Nam Quận cũng không giữ được. Mau đi cứu.","Di Lăng mà mất, Nam Quận cũng không giữ được. Mau đi cứu.",},
        [4996] = {9606,"Tào Nhân","Ngưu Kim","Sai người gấp rút báo cho Tào Hồng, lệnh Hồng ra thành dụ địch.","Sai người gấp rút báo cho Tào Hồng, lệnh Hồng ra thành dụ địch.",},
        [4997] = {9606,"Tào Nhân","Ngưu Kim","Ngươi và Tào Thuần âm thầm dẫn binh cứu Tào Hồng.","Ngươi và Tào Thuần âm thầm dẫn binh cứu Tào Hồng.",},
        [4998] = {9606,"Ngưu Kim","Tào Nhân","Được.","Được.",},
        [4999] = {9607,"Cam Ninh","Tào Hồng","Hưng Bá ở đây, mau bỏ Di Lăng đầu hàng.","Hưng Bá ở đây, mau bỏ Di Lăng đầu hàng.",},
        [5000] = {9607,"Tào Hồng","Cam Ninh","Tào Hồng ta sao lại sợ tên Cẩm Phàm tặc ngươi! Giờ sẽ quyết chiến với ngươi.","Tào Hồng ta sao lại sợ tên Cẩm Phàm tặc ngươi! Giờ sẽ quyết chiến với ngươi.",},
        [5001] = {9608,"Cam Ninh","","Không ngờ Tào Hồng ngươi kém cỏi vậy, chỉ 20 hiệp đã thua bỏ chạy.","Không ngờ Tào Hồng ngươi kém cỏi vậy, chỉ 20 hiệp đã thua bỏ chạy.",},
        [5002] = {9608,"Cam Ninh","","Di Lăng này đã thuộc về ta.","Di Lăng này đã thuộc về ta.",},
        [5003] = {9609,"Tào Hồng","Cam Ninh","Cẩm Phàm Tặc, ngươi tưởng Di Lăng này dễ đoạt vậy sao?","Cẩm Phàm Tặc, ngươi tưởng Di Lăng này dễ đoạt vậy sao?",},
        [5004] = {9609,"Ngưu Kim","Cam Ninh","Ngươi đã bị bao vây trong thành.","Ngươi đã bị bao vây trong thành.",},
        [5005] = {9609,"Tào Nhân","Cam Ninh","Mau đầu hàng!","Mau đầu hàng!",},
        [5006] = {9609,"Cam Ninh","Tào Nhân","Đáng ghét, trúng kế rồi!","Đáng ghét, trúng kế rồi!",},
        [5007] = {9701,"Chu Du","","Cam Ninh trúng kế, bị nhốt trong Di Lăng Thành!","Cam Ninh trúng kế, bị nhốt trong Di Lăng Thành!",},
        [5008] = {9701,"Trình Phổ","Chu Du","Vội điều binh đến cứu.","Vội điều binh đến cứu.",},
        [5009] = {9701,"Chu Du","Trình Phổ","Đây là nơi quan trọng, nếu chia binh đi cứu, Tào Nhân dẫn binh đến tấn công thì sao?","Đây là nơi quan trọng, nếu chia binh đi cứu, Tào Nhân dẫn binh đến tấn công thì sao?",},
        [5010] = {9701,"Lữ Mông","Chu Du","Cam Hưng Bá là Giang Đông Đại Tướng, sao không cứu được?","Cam Hưng Bá là Giang Đông Đại Tướng, sao không cứu được?",},
        [5011] = {9701,"Chu Du","Lữ Mông","Ta định tự đi cứu hắn, nhưng để ai ở lại đây thay ta?","Ta định tự đi cứu hắn, nhưng để ai ở lại đây thay ta?",},
        [5012] = {9701,"Lữ Mông","Chu Du","Để Lăng Công Tích lại. Mông làm tiền khu, Đô Đốc đoạn hậu, không đến 10 ngày, chắc chắn chiến thắng.","Để Lăng Công Tích lại. Mông làm tiền khu, Đô Đốc đoạn hậu, không đến 10 ngày, chắc chắn chiến thắng.",},
        [5013] = {9701,"Chu Du","Lữ Mông","Không biết Lăng Công Tích có đồng ý đảm nhiệm chức trách thay ta?","Không biết Lăng Công Tích có đồng ý đảm nhiệm chức trách thay ta?",},
        [5014] = {9701,"Lăng Thống","Chu Du","Nếu là thời hạn 10 ngày thì có thể, trên 10 ngày thì không dám đảm nhiệm.","Nếu là thời hạn 10 ngày thì có thể, trên 10 ngày thì không dám đảm nhiệm.",},
        [5015] = {9701,"Chu Du","Lăng Thống","Để lại hơn một vạn binh, giao cho Lăng Thống, ngay trong ngày đại binh đến Di Lăng.","Để lại hơn một vạn binh, giao cho Lăng Thống, ngay trong ngày đại binh đến Di Lăng.",},
        [5016] = {9701,"Lữ Mông","Chu Du","Di Lăng đường nhỏ, chiếm Nam Quận rất tiện. Có thể sai 500 quân đi chặt cây để cản đường hắn.","Di Lăng đường nhỏ, chiếm Nam Quận rất tiện. Có thể sai 500 quân đi chặt cây để cản đường hắn.",},
        [5017] = {9701,"Lữ Mông","Chu Du","Quân họ thất bại, chắc chắn đi đường này, ngựa không đi được, chắc chắn sẽ bỏ ngựa, ta có thể lấy ngựa.","Quân họ thất bại, chắc chắn đi đường này, ngựa không đi được, chắc chắn sẽ bỏ ngựa, ta có thể lấy ngựa.",},
        [5018] = {9701,"Chu Du","Lữ Mông","Được!","Được!",},
        [5019] = {9702,"Chu Du","","Binh sắp đến Di Lăng, ai có thể phá vòng vây vào cứu Cam Ninh?","Binh sắp đến Di Lăng, ai có thể phá vòng vây vào cứu Cam Ninh?",},
        [5020] = {9702,"Chu Thái","Chu Du","Ta nguyện đi!","Ta nguyện đi!",},
        [5021] = {9702,"Chu Du","Chu Thái","Được!","Được!",},
        [5022] = {9703,"Chu Thái","Cam Ninh","Đô Đốc tự dẫn binh đến.","Đô Đốc tự dẫn binh đến.",},
        [5023] = {9703,"Cam Ninh","Chu Thái","Ta truyền lệnh quân sĩ ăn no chuẩn bị sẵn sàng nội ứng.","Ta truyền lệnh quân sĩ ăn no chuẩn bị sẵn sàng nội ứng.",},
        [5024] = {9704,"Tào Hồng","Tào Nhân","Nay mất Di Lăng, tình thế nguy cấp, sao không tháo di kế của thừa tướng để giải nguy?","Nay mất Di Lăng, tình thế nguy cấp, sao không tháo di kế của thừa tướng để giải nguy?",},
        [5025] = {9704,"Tào Nhân","Tào Hồng","Lời ngươi vừa đúng ý ta.","Lời ngươi vừa đúng ý ta.",},
        [5026] = {9704,"Tào Nhân","Tào Hồng","Ha ha ha.","Ha ha ha.",},
        [5027] = {9704,"Tào Nhân","Tào Hồng","Thừa tướng lệnh năm canh nấu cơm, bình minh, quân mã lớn nhỏ, đều rời thành, trong thành cắm tinh kỳ, phô trương thanh thế.","Thừa tướng lệnh năm canh nấu cơm, bình minh, quân mã lớn nhỏ, đều rời thành, trong thành cắm tinh kỳ, phô trương thanh thế.",},
        [5028] = {9704,"Tào Hồng","Tào Nhân","Kế hay kế hay.","Kế hay kế hay.",},
        [5029] = {9705,"Tào Nhân","","Thừa tướng diệu sách như thần! Chu Du Cam Ninh trúng kế.","Thừa tướng diệu sách như thần! Chu Du Cam Ninh trúng kế.",},
        [5030] = {9705,"Tào Nhân","","Ngô Binh giành vào thành trống trước đều rơi vào bẫy, hai bên bắn cung nỏ, thế như vũ bão, tử thương vô số.","Ngô Binh giành vào thành trống trước đều rơi vào bẫy, hai bên bắn cung nỏ, thế như vũ bão, tử thương vô số.",},
        [5031] = {9705,"Tào Nhân","","Chu Du vội cưỡi ngựa về, bị một cung tên bắn trúng, lật người ngã ngựa.","Chu Du vội cưỡi ngựa về, bị một cung tên bắn trúng, lật người ngã ngựa.",},
        [5032] = {9706,"Chu Du","","Nơi nào đánh trống reo hò?","Nơi nào đánh trống reo hò?",},
        [5033] = {9706,"Cam Ninh","Chu Du","Trong quân đang giáo diễn binh sĩ.","Trong quân đang giáo diễn binh sĩ.",},
        [5034] = {9706,"Chu Du","Cam Ninh","Sao lại bắt nạt ta! Ta biết Tào binh thường đến trước trại chửi bới. Trình Đức Mưu cùng nắm binh quyền, sao lại ngồi nhìn?","Sao lại bắt nạt ta! Ta biết Tào binh thường đến trước trại chửi bới. Trình Đức Mưu cùng nắm binh quyền, sao lại ngồi nhìn?",},
        [5035] = {9706,"Trình Phổ","Chu Du","Ta thấy Công Cẩn mắc bệnh, y giả nên cẩn trọng trong lời nói, nên Tào binh khiêu chiến, không dám thông báo.","Ta thấy Công Cẩn mắc bệnh, y giả nên cẩn trọng trong lời nói, nên Tào binh khiêu chiến, không dám thông báo.",},
        [5036] = {9706,"Chu Du","Trình Phổ","Ngài không xuất chiến, chủ ý thế nào?","Ngài không xuất chiến, chủ ý thế nào?",},
        [5037] = {9706,"Trình Phổ","Chu Du","Chúng tướng đều muốn thu binh tạm về Giang Đông. Chờ vết thương của ngài bình phục, sẽ an bài sau.","Chúng tướng đều muốn thu binh tạm về Giang Đông. Chờ vết thương của ngài bình phục, sẽ an bài sau.",},
        [5038] = {9706,"Chu Du","Trình Phổ","Đại trượng phu ăn quân lộc, hy sinh nơi chiến trường, da ngựa bọc thây là điều đương nhiên! Sao có thể vì mình ta mà chậm trễ quốc gia đại sự?","Đại trượng phu ăn quân lộc, hy sinh nơi chiến trường, da ngựa bọc thây là điều đương nhiên! Sao có thể vì mình ta mà chậm trễ quốc gia đại sự?",},
        [5039] = {9707,"Tào Nhân","Chu Du","Chu Du chắc chắn mất mạng, không dám nhìn thẳng quân ta nữa!","Chu Du chắc chắn mất mạng, không dám nhìn thẳng quân ta nữa!",},
        [5040] = {9707,"Chu Du","Tào Nhân","Tào Nhân thất phu! Gặp Chu Lang không!","Tào Nhân thất phu! Gặp Chu Lang không!",},
        [5041] = {9707,"Tào Nhân","Chu Du","Có dám quyết chiến không!","Có dám quyết chiến không!",},
        [5042] = {9707,"Chu Du","Tào Nhân","Phan Chương xuất chiến.","Phan Chương xuất chiến.",},
        [5043] = {9708,"Chu Du","","A! (Chu Du la lớn một tiếng, ngã xuống ngựa.)","A! (Chu Du la lớn một tiếng, ngã xuống ngựa.)",},
        [5044] = {9708,"Trình Phổ","Chu Du","Đô Đốc cơ thể thế nào?","Đô Đốc cơ thể thế nào?",},
        [5045] = {9708,"Chu Du","Trình Phổ","Đó là kế của ta.","Đó là kế của ta.",},
        [5046] = {9708,"Trình Phổ","Chu Du","Ngươi có kế gì?","Ngươi có kế gì?",},
        [5047] = {9708,"Chu Du","Trình Phổ","Ta vốn không đau đớn, ta làm như vậy là để Tào binh biết ta bệnh nguy để lừa địch. ","Ta vốn không đau đớn, ta làm như vậy là để Tào binh biết ta bệnh nguy để lừa địch. ",},
        [5048] = {9708,"Chu Du","Trình Phổ","Có thể sai quân sĩ vào thành giả vờ đầu hàng, nói ta đã qua đời. Tối nay Tào Nhân chắc chắn đến cướp trại. Ta mai phục ở xung quanh, có thể bắt được Tào Nhân.","Có thể sai quân sĩ vào thành giả vờ đầu hàng, nói ta đã qua đời. Tối nay Tào Nhân chắc chắn đến cướp trại. Ta mai phục ở xung quanh, có thể bắt được Tào Nhân.",},
        [5049] = {9708,"Trình Phổ","Chu Du","Quả là diệu kế!","Quả là diệu kế!",},
        [5050] = {9709,"Tào Nhân","","Nghe nói Chu Du bị hở vết thương, vừa về trại đã qua đời, chúng tướng để tang.","Nghe nói Chu Du bị hở vết thương, vừa về trại đã qua đời, chúng tướng để tang.",},
        [5051] = {9709,"Tào Nhân","","Tối nay đi cướp trại, đoạt thi hài Chu Du, trảm thủ đem về Hứa Đô.","Tối nay đi cướp trại, đoạt thi hài Chu Du, trảm thủ đem về Hứa Đô.",},
        [5052] = {9709,"Tào Nhân","","Ngưu Kim làm Tiên Phong, tự làm trung quân, Tào Hồng, Tào Thuần làm hợp hậu, chỉ để Trần Kiểu dẫn ít quân thủ thành, quân binh còn lại đều tiến lên.","Ngưu Kim làm Tiên Phong, tự làm trung quân, Tào Hồng, Tào Thuần làm hợp hậu, chỉ để Trần Kiểu dẫn ít quân thủ thành, quân binh còn lại đều tiến lên.",},
        [5053] = {9710,"Tào Hồng","Tào Nhân","Trong trại không thấy một ai, chỉ là giả vờ cắm cờ.","Trong trại không thấy một ai, chỉ là giả vờ cắm cờ.",},
        [5054] = {9710,"Tào Nhân","Tào Hồng","Không được, trúng kế rồi, mau lui quân về Tương Dương.","Không được, trúng kế rồi, mau lui quân về Tương Dương.",},
        [5055] = {9801,"Trình Phổ","Chu Du","Nam Quận Thành cắm đầy tinh kỳ, Triệu Tử Long thừa cơ đoạt thành trì.","Nam Quận Thành cắm đầy tinh kỳ, Triệu Tử Long thừa cơ đoạt thành trì.",},
        [5056] = {9801,"Chu Du","Trình Phổ","Cái gì! Chỉnh đốn binh mã tấn công thành.","Cái gì! Chỉnh đốn binh mã tấn công thành.",},
        [5057] = {9801,"Trình Phổ","Chu Du","Trên thành loạn tiễn bắn xuống, tạm không thể công thành.","Trên thành loạn tiễn bắn xuống, tạm không thể công thành.",},
        [5058] = {9801,"Chu Du","Trình Phổ","Cam Ninh dẫn hàng ngàn quân mã, chiếm Kinh Châu.","Cam Ninh dẫn hàng ngàn quân mã, chiếm Kinh Châu.",},
        [5059] = {9801,"Chu Du","Trình Phổ","Lăng Thống dẫn hàng ngàn quân mã, chiếm Tương Dương, sau đó chiếm Nam Quận cũng chưa muộn.","Lăng Thống dẫn hàng ngàn quân mã, chiếm Tương Dương, sau đó chiếm Nam Quận cũng chưa muộn.",},
        [5060] = {9802,"Trình Phổ","Chu Du","Báo! Gia Cát Lượng chiếm được Nam Quận, dùng binh phù, đêm tối điều quân mã thủ thành Kinh Châu đến cứu.","Gia Cát Lượng đoạt được Nam Quận, dùng Binh Phù điều động binh mã thủ thành Kinh Châu đến cứu viện.",},
        [5061] = {9802,"Trình Phổ","Chu Du","Trương Phi tập kích Kinh Châu.","Trương Phi tập kích Kinh Châu.",},
        [5062] = {9802,"Chu Du","Trình Phổ","Gì!","Gì!",},
        [5063] = {9803,"Trình Phổ","Chu Du","Báo! Hạ Hầu Đôn ở Tương Dương, bị Gia Cát Lượng sai người đưa binh phù, giả vờ là Tào Nhân cầu cứu, dụ Đôn dẫn quân ra ngoài, rồi dạy Vân Trường chiếm Tương Dương.","Hạ Hầu Đôn ở Tương Dương, Gia Cát Lượng cho người nói dối rằng Tào Nhân cầu cứu, dụ y ra khỏi thành, rồi sai Vân Trường đánh úp Tương Dương.",},
        [5064] = {9803,"Trình Phổ","Chu Du","Hai thành trì không cần tốn sức đã thuộc về Lưu Huyền Đức.","Hai thành trì không cần tốn sức đã thuộc về Lưu Huyền Đức.",},
        [5065] = {9803,"Chu Du","Trình Phổ","Gia Cát Lượng sao có được binh phù?","Gia Cát Lượng sao có được binh phù?",},
        [5066] = {9803,"Trình Phổ","Chu Du","Hắn bắt Trần Kiểu, binh phù đương nhiên thuộc về hắn.","Hắn bắt Trần Kiểu, binh phù đương nhiên thuộc về hắn.",},
        [5067] = {9803,"Chu Du","Trình Phổ","A! (Chu Du kêu to, vết thương bị hở.)","A! (Chu Du kêu to, vết thương bị hở.)",},
        [5068] = {9803,"Trình Phổ","Chu Du","Người đâu, mau đỡ Đô Đốc về doanh.","Người đâu, mau đỡ Đô Đốc về doanh.",},
        [5069] = {9804,"Chu Du","Lỗ Túc","Nếu không diệt Gia Cát thôn phu, sao dập tắt được nộ khí trong lòng ta!","Nếu không diệt Gia Cát thôn phu, sao dập tắt được nộ khí trong lòng ta!",},
        [5070] = {9804,"Chu Du","Lỗ Túc","Ta muốn khởi binh quyết chiến cùng Lưu Bị, Gia Cát Lượng, đoạt lại thành trì. Tử Kính hãy giúp ta.","Ta muốn khởi binh quyết chiến cùng Lưu Bị, Gia Cát Lượng, đoạt lại thành trì. Tử Kính hãy giúp ta.",},
        [5071] = {9804,"Lỗ Túc","Chu Du","Không được. Nay đang giằng co với Tào Tháo, chưa phân thắng bại, Chúa công nay công Hợp Phì không được. Nội bộ tranh giành nhau, Tào binh sẽ thừa cơ tiến đến, tình thế sẽ rất nguy.","Không được. Nay đang giằng co với Tào Tháo, chưa phân thắng bại, Chúa công nay công Hợp Phì không được. Nội bộ tranh giành nhau, Tào binh sẽ thừa cơ tiến đến, tình thế sẽ rất nguy.",},
        [5072] = {9804,"Lỗ Túc","Chu Du","Lưu Huyền Đức từng có quan hệ tốt với Tào Tháo, nếu dồn ép quá, hiến thành trì, cùng công đánh Đông Ngô, thế nào?","Lưu Huyền Đức từng có quan hệ tốt với Tào Tháo, nếu dồn ép quá, hiến thành trì, cùng công đánh Đông Ngô, thế nào?",},
        [5073] = {9804,"Chu Du","Lỗ Túc","Chúng ta dùng kế sách, tổn binh mã, phí lương tiền, sẵn hết cho hắn, chẳng phải đáng ghét sao!","Chúng ta dùng kế sách, tổn binh mã, phí lương tiền, sẵn hết cho hắn, chẳng phải đáng ghét sao!",},
        [5074] = {9804,"Lỗ Túc","Chu Du","Công Cẩn hãy kiên nhẫn. Dung mỗ gặp Huyền Đức, nói lí với hắn. Nếu nói không được, lúc đó động binh cũng chưa muộn.","Công Cẩn hãy kiên nhẫn. Dung mỗ gặp Huyền Đức, nói lí với hắn. Nếu nói không được, lúc đó động binh cũng chưa muộn.",},
        [5075] = {9901,"Lỗ Túc","Gia Cát Lượng","Chủ ta Ngô Hầu, và Đô Đốc Công Cẩn, Giáo mỗ nhiều lần tỏ ý với hoàng thúc, dẫn trăm vạn người lấy danh xuống Giang Nam, thực chất là thực hiện mưu đồ với hoàng thúc.","Chủ ta Ngô Hầu, và Đô Đốc Công Cẩn, Giáo mỗ nhiều lần tỏ ý với hoàng thúc, dẫn trăm vạn người lấy danh xuống Giang Nam, thực chất là thực hiện mưu đồ với hoàng thúc.",},
        [5076] = {9901,"Lỗ Túc","Gia Cát Lượng","May có Đông Ngô diệt lùi Tào binh, cứu hoàng thúc. Tất cả Kinh Châu Cửu Quận, đều quy Đông Ngô.","May có Đông Ngô diệt lùi Tào binh, cứu hoàng thúc. Tất cả Kinh Châu Cửu Quận, đều quy Đông Ngô.",},
        [5077] = {9901,"Lỗ Túc","Gia Cát Lượng","Nay hoàng thúc dùng ngụy kế, đoạt chiếm Kinh Tương, khiến Giang Đông tốn lương tiền quân mã, còn hoàng thúc thu lợi, e là không thuận với lý.","Nay hoàng thúc dùng ngụy kế, đoạt chiếm Kinh Tương, khiến Giang Đông tốn lương tiền quân mã, còn hoàng thúc thu lợi, e là không thuận với lý.",},
        [5078] = {9901,"Gia Cát Lượng","Lỗ Túc","Tử Kính là người cao minh, sao lại nói thế? Thường nói là: Vật ắt quy chủ. Kinh Tương Cửu Quận, không phải đất của Đông Ngọ, là cơ nghiệp của Lưu Cảnh Thăng.","Tử Kính là người cao minh, sao lại nói thế? Thường nói là: Vật ắt quy chủ. Kinh Tương Cửu Quận, không phải đất của Đông Ngọ, là cơ nghiệp của Lưu Cảnh Thăng.",},
        [5079] = {9901,"Gia Cát Lượng","Lỗ Túc","Chủ nhân ta là đệ đệ của Cảnh Thăng. Cảnh Thăng tuy qua đời, nhưng nhi tử người vẫn còn, thúc thúc phụ trợ cháu chiếm Kinh Châu, có gì không được?","Chủ nhân ta là đệ đệ của Cảnh Thăng. Cảnh Thăng tuy qua đời, nhưng nhi tử người vẫn còn, thúc thúc phụ trợ cháu chiếm Kinh Châu, có gì không được?",},
        [5080] = {9901,"Lỗ Túc","Gia Cát Lượng","Nếu là công tử Lưu Kỳ chiếm cứ, có thể hiểu được, nay công tử ở Giang Hạ, không ở nơi này!","Nếu là công tử Lưu Kỳ chiếm cứ, có thể hiểu được, nay công tử ở Giang Hạ, không ở nơi này!",},
        [5081] = {9901,"Gia Cát Lượng","Lỗ Túc","Tử Kính muốn gặp công tử sao?","Tử Kính muốn gặp công tử sao?",},
        [5082] = {9901,"Lỗ Túc","Gia Cát Lượng","Được!","Được!",},
        [5083] = {9901,"Gia Cát Lượng","Lỗ Túc","Khổng Minh sẽ dẫn ngươi gặp ngài.","Khổng Minh sẽ dẫn ngươi gặp ngài.",},
        [5084] = {9902,"Lưu Kỳ","Lỗ Túc","Bệnh hoạn không thể hành lễ, Tử Kính đừng trách. (Lưu Kỳ nằm trên giường.)","Bệnh hoạn không thể hành lễ, Tử Kính đừng trách. (Lưu Kỳ nằm trên giường.)",},
        [5085] = {9902,"Lỗ Túc","Lưu Kỳ","Nếu công tử không còn thì làm sao?","Nếu công tử không còn thì làm sao?",},
        [5086] = {9902,"Gia Cát Lượng","Lỗ Túc","Công tử còn một ngày, thủ một ngày, nếu không còn, sẽ bàn bạc lại.","Công tử còn một ngày, thủ một ngày, nếu không còn, sẽ bàn bạc lại.",},
        [5087] = {9902,"Lỗ Túc","Gia Cát Lượng","Nếu công tử không ở đây, phải trả thành cho Đông Ngô.","Nếu công tử không ở đây, phải trả thành cho Đông Ngô.",},
        [5088] = {9902,"Gia Cát Lượng","Lỗ Túc","Tử Kính nói phải.","Tử Kính nói phải.",},
        [5089] = {9903,"Lỗ Túc","Chu Du","Sau khi Lưu Kỳ mất, thành trì sẽ trả cho Đông Ngô ta.","Sau khi Lưu Kỳ mất, thành trì sẽ trả cho Đông Ngô ta.",},
        [5090] = {9903,"Chu Du","Lỗ Túc","Lưu Kỳ còn trẻ, sao có thể bỏ mạng? Khi nào mới có lại Kinh Châu?","Lưu Kỳ còn trẻ, sao có thể bỏ mạng? Khi nào mới có lại Kinh Châu?",},
        [5091] = {9903,"Lỗ Túc","Chu Du","Đô Đốc yên tâm. Giao cho Lỗ Túc, chắc chắn lấy Kinh Tương trả lại Đông Ngô.","Đô Đốc yên tâm. Giao cho Lỗ Túc, chắc chắn lấy Kinh Tương trả lại Đông Ngô.",},
        [5092] = {9903,"Chu Du","Lỗ Túc","Tử Kính có cao kiến gì?","Tử Kính có cao kiến gì?",},
        [5093] = {9903,"Lỗ Túc","Chu Du","Ta thấy Lưu Kỳ tửu sắc quá độ, bệnh nặng nguy kịch, nay sắc mặt xanh xao, khó thở, không quá nửa năm chắc chắn qua đời.","Ta thấy Lưu Kỳ tửu sắc quá độ, bệnh nặng nguy kịch, nay sắc mặt xanh xao, khó thở, không quá nửa năm chắc chắn qua đời.",},
        [5094] = {9903,"Lỗ Túc","Chu Du","Lúc đó đi chiếm Kinh Châu, Lưu Bị sẽ không có lí do từ chối.","Lúc đó đi chiếm Kinh Châu, Lưu Bị sẽ không có lí do từ chối.",},
        [5095] = {9903,"Chu Du","Lỗ Túc","Được.","Được.",},
        [5096] = {9904,"Y Tịch","Lưu Bị","Phải biết kế lâu dài của Kinh Châu, sao không hỏi hiền sĩ?","Phải biết kế lâu dài của Kinh Châu, sao không hỏi hiền sĩ?",},
        [5097] = {9904,"Lưu Bị","Y Tịch","Hiền sĩ ở đâu?","Hiền sĩ ở đâu?",},
        [5098] = {9904,"Y Tịch","Lưu Bị","Kinh Tương Mã Thị, năm huynh đệ đều có tài danh: người nhỏ tên Tắc, tự Ấu Thường, người hiền tài nhất, giữa chân mày có lông trắng, tên Lương, tự Quý Thường.","Kinh Tương Mã Thị, năm huynh đệ đều có tài danh: người nhỏ tên Tắc, tự Ấu Thường, người hiền tài nhất, giữa chân mày có lông trắng, tên Lương, tự Quý Thường.",},
        [5099] = {9904,"Y Tịch","Lưu Bị","Có câu: Mã Thị ngũ thường, bạch mi tối lương. Sao ngài không nhờ người này hiến kế?","Có câu: Mã Thị ngũ thường, bạch mi tối lương. Sao ngài không nhờ người này hiến kế?",},
        [5100] = {9904,"Lưu Bị","Y Tịch","Mau mời người này.","Mau mời người này.",},
        [5101] = {9905,"Lưu Bị","Mã Lương","Tiên sinh có kế sách giữ Kinh Tương không.","Tiên sinh có kế sách giữ Kinh Tương không.",},
        [5102] = {9905,"Mã Lương","Lưu Bị","Kinh Tương bị địch bao vây, e là không thể giữ lâu, nhưng lệnh công tử Lưu Kỳ ở đây dưỡng bệnh, chiêu dụ cựu nhân trấn thủ, Biểu Tấu công tử làm Kinh Châu Thứ Sử để dân an lòng.","Kinh Tương bị địch bao vây, e là không thể giữ lâu, nhưng lệnh công tử Lưu Kỳ ở đây dưỡng bệnh, chiêu dụ cựu nhân trấn thủ, Biểu Tấu công tử làm Kinh Châu Thứ Sử để dân an lòng.",},
        [5103] = {9905,"Mã Lương","Lưu Bị","Sau đó nam chinh Vũ Lân, Phượng Tường, Quế Dương, Linh Lăng, tích thu lương tiền làm vốn. Đây là kế lâu dài.","Sau đó nam chinh Vũ Lân, Phượng Tường, Quế Dương, Linh Lăng, tích thu lương tiền làm vốn. Đây là kế lâu dài.",},
        [5104] = {9905,"Lưu Bị","Mã Lương","Bốn quận nên chiếm quận nào trước?","Bốn quận nên chiếm quận nào trước?",},
        [5105] = {9905,"Mã Lương","Lưu Bị","Phía tây Tương Giang, Linh Lăng gần nhất, nên chiếm trước, kế đến là Vũ Lân. Sau đó phía đông Tương Giang chiếm Quế Dương, Phượng Tường cuối cùng.","Phía tây Tương Giang, Linh Lăng gần nhất, nên chiếm trước, kế đến là Vũ Lân. Sau đó phía đông Tương Giang chiếm Quế Dương, Phượng Tường cuối cùng.",},
        [5106] = {9905,"Lưu Bị","Mã Lương","Được! Tiễn Lưu Kỳ về Tương Dương, thay Vân Trường về Kinh Châu.","Được! Tiễn Lưu Kỳ về Tương Dương, thay Vân Trường về Kinh Châu.",},
        [5107] = {9905,"Lưu Bị","Mã Lương","Điều binh chiếm Linh Lăng, sai Trương Phi làm Tiên Phong, Triệu Vân hợp hậu, Khổng Minh và ta là trung quân, nhân mã một vạn năm ngàn, Vân Trường thủ Kinh Châu, My Trúc, Lưu Phong thủ Giang Lăng.","Điều binh chiếm Linh Lăng, sai Trương Phi làm Tiên Phong, Triệu Vân hợp hậu, Khổng Minh và ta là trung quân, nhân mã một vạn năm ngàn, Vân Trường thủ Kinh Châu, My Trúc, Lưu Phong thủ Giang Lăng.",},
        [5108] = {10001,"Gia Cát Lượng","Hình Đạo Vinh","Ta là Hoa Dương Gia Cát Khổng Minh. Tào Tháo dẫn trăm vạn người, trúng kế của ta bị diệt tan tác.","Ta là Hoa Dương Gia Cát Khổng Minh. Tào Tháo dẫn trăm vạn người, trúng kế của ta bị diệt tan tác.",},
        [5109] = {10001,"Gia Cát Lượng","Hình Đạo Vinh","Các ngươi sao có thể đối địch ta? Ta đến chiêu hàng các ngươi, còn không sớm đầu hàng?","Các ngươi sao có thể đối địch ta? Ta đến chiêu hàng các ngươi, còn không sớm đầu hàng?",},
        [5110] = {10001,"Hình Đạo Vinh","Gia Cát Lượng","Xích Bích ác chiến, là mưu kế của Chu Lang, liên quan gì đến ngươi, dám nói lời ngông cuồng!","Xích Bích ác chiến, là mưu kế của Chu Lang, liên quan gì đến ngươi, dám nói lời ngông cuồng!",},
        [5111] = {10001,"Hình Đạo Vinh","Gia Cát Lượng","Hỏi binh khí trong tay ta đi!","Hỏi binh khí trong tay ta đi!",},
        [5112] = {10002,"Triệu Vân","Hình Đạo Vinh","Nhận ra Triệu Tử Long không!","Nhận ra Triệu Tử Long không!",},
        [5113] = {10002,"Triệu Vân","Hình Đạo Vinh","Chạy đi đâu!","Chạy đi đâu!",},
        [5114] = {10002,"Hình Đạo Vinh","Triệu Vân","Tử Long tha mạng!","Tử Long tha mạng!",},
        [5115] = {10003,"Gia Cát Lượng","Hình Đạo Vinh","Nếu ngươi cùng ta bắt được Lưu Hiền, sẽ cho ngươi đầu hàng.","Nếu ngươi cùng ta bắt được Lưu Hiền, sẽ cho ngươi đầu hàng.",},
        [5116] = {10003,"Hình Đạo Vinh","Gia Cát Lượng","Ta nguyện đi.","Ta nguyện đi.",},
        [5117] = {10003,"Gia Cát Lượng","Hình Đạo Vinh","Ngươi dùng cách gì bắt hắn?","Ngươi dùng cách gì bắt hắn?",},
        [5118] = {10003,"Hình Đạo Vinh","Gia Cát Lượng","Quân sư nếu đồng ý thả ta về, ta tự có cách. Tối nay quân sư điều quân cướp trại, ta làm nội ứng, bắt sống Lưu Hiền giao cho quân sư. Lưu Hiền bị bắt, Lưu Độ sẽ tự đầu hàng.","Quân sư nếu đồng ý thả ta về, ta tự có cách. Tối nay quân sư điều quân cướp trại, ta làm nội ứng, bắt sống Lưu Hiền giao cho quân sư. Lưu Hiền bị bắt, Lưu Độ sẽ tự đầu hàng.",},
        [5119] = {10003,"Lưu Bị","Hình Đạo Vinh","Sao ta tin ngươi được?","Sao ta tin ngươi được?",},
        [5120] = {10003,"Hình Đạo Vinh","Lưu Bị","Ta tuyệt đối không nói dối!","Ta tuyệt đối không nói dối!",},
        [5121] = {10003,"Gia Cát Lượng","Hình Đạo Vinh","Ta tin Hình Tướng Quân ngươi không nói dối, ngươi về đi.","Ta tin Hình Tướng Quân ngươi không nói dối, ngươi về đi.",},
        [5122] = {10003,"Hình Đạo Vinh","Gia Cát Lượng","Đa tạ Khổng Minh.","Đa tạ Khổng Minh.",},
        [5123] = {10004,"Hình Đạo Vinh","Lưu Hiền","Lưu Bị thả ta về làm nội ứng, phải bắt sống ngươi, để Lưu Độ tự hàng.","Lưu Bị thả ta về làm nội ứng, phải bắt sống ngươi, để Lưu Độ tự hàng.",},
        [5124] = {10004,"Lưu Hiền","Hình Đạo Vinh","Vậy phải làm sao?","Vậy phải làm sao?",},
        [5125] = {10004,"Hình Đạo Vinh","Lưu Hiền","Tương kế tựu kế. Tối nay cho quân mai phục ngoài trại, trong trại giả vờ cắm cờ, chờ Khổng Minh đến cướp trại thì bắt hắn.","Tương kế tựu kế. Tối nay cho quân mai phục ngoài trại, trong trại giả vờ cắm cờ, chờ Khổng Minh đến cướp trại thì bắt hắn.",},
        [5126] = {10004,"Lưu Hiền","Hình Đạo Vinh","Được.","Được.",},
        [5127] = {10005,"Lưu Hiền","Hình Đạo Vinh","Một nhóm quân đến cửa trại, tay cầm đuốc định đốt trại.","Một nhóm quân đến cửa trại, tay cầm đuốc định đốt trại.",},
        [5128] = {10005,"Hình Đạo Vinh","Lưu Hiền","Mau tóm lấy Khổng Minh.","Mau tóm lấy Khổng Minh.",},
        [5129] = {10005,"Lưu Hiền","Hình Đạo Vinh","Được!","Được!",},
        [5130] = {10006,"Hình Đạo Vinh","Lưu Hiền","Đuổi kịp 10 dặm, quân không thấy đâu.","Đuổi kịp 10 dặm, quân không thấy đâu.",},
        [5131] = {10006,"Hình Đạo Vinh","Lưu Hiền","Trúng kế rồi, mau chuồn thôi!","Trúng kế rồi, mau chuồn thôi!",},
        [5132] = {10006,"Lưu Hiền","Hình Đạo Vinh","Không thể vào trại, có thể trại đã bị chiếm","Không thể vào trại, có thể trại đã bị chiếm",},
        [5133] = {10006,"Lưu Hiền","Hình Đạo Vinh","Đến cướp Trại Khổng Minh là được.","Đến cướp Trại Khổng Minh là được.",},
        [5134] = {10007,"Triệu Vân","Lưu Hiền","Lưu Hiền trốn đâu cho thoát!","Lưu Hiền trốn đâu cho thoát!",},
        [5135] = {10007,"Lưu Hiền","Triệu Vân","Ta đến đầu hàng!","Ta đến đầu hàng!",},
        [5136] = {10007,"Lưu Hiền","Triệu Vân","Hình Đạo Vinh xui ta như thế chứ không phải chủ ý của ta.","Hình Đạo Vinh xui ta như thế chứ không phải chủ ý của ta.",},
        [5137] = {10008,"Triệu Vân","Lưu Độ","Lưu Hiền bị bắt, nếu Lưu Độ Nhữ không hàng thì chiếm thành diệt môn","Lưu Hiền bị bắt, nếu Lưu Độ Nhữ không hàng thì chiếm thành diệt môn",},
        [5138] = {10008,"Lưu Hiền","Lưu Độ","Khổng Minh không hại ta mà còn mời rượu.","Khổng Minh không hại ta mà còn mời rượu.",},
        [5139] = {10008,"Lưu Hiền","Lưu Độ","Mong Lưu Độ giao ấn đầu hàng.","Mong Lưu Độ giao ấn đầu hàng.",},
        [5140] = {10008,"Lưu Độ","Lưu Hiền","Ta chấp nhận đầu hàng Huyền Đức.","Ta chấp nhận đầu hàng Huyền Đức.",},
        [5141] = {10008,"Gia Cát Lượng","Lưu Độ","Huyền Đức để Lưu Độ Nhữ giữ chức thái thú quận, Lưu Hiền cho về Kinh Châu theo giúp việc quân.","Huyền Đức để Lưu Độ Nhữ giữ chức thái thú quận, Lưu Hiền cho về Kinh Châu theo giúp việc quân.",},
        [5142] = {10008,"Lưu Độ","Gia Cát Lượng","Đa tạ đại ân Khổng Minh và Lưu Huyền Đức","Đa tạ đại ân Khổng Minh và Lưu Huyền Đức",},
        [5143] = {10101,"Triệu Vân","Trần Ứng","Chủ ta Lưu Huyền Đức, là em trai Lưu Biểu, nay hỗ trợ công tử Lưu Kỳ thống lĩnh Kinh Châu, rất được lòng dân, sao ngươi dám nghênh địch?","Chủ ta Lưu Huyền Đức, là em trai Lưu Biểu, nay hỗ trợ công tử Lưu Kỳ thống lĩnh Kinh Châu, rất được lòng dân, sao ngươi dám nghênh địch?",},
        [5144] = {10101,"Trần Ứng","Triệu Vân","Ta đây chỉ phục Tào thừa tướng, há theo Lưu Bị!","Ta đây chỉ phục Tào thừa tướng, há theo Lưu Bị!",},
        [5145] = {10101,"Triệu Vân","Trần Ứng","Lẽ nào lại như vậy! Xem thương!","Lẽ nào lại như vậy! Xem thương!",},
        [5146] = {10102,"Triệu Vân","Trần Ứng","Chắc ngươi cũng không đánh lại ta đâu, ta sẽ cho ngươi về, mau đi nói với Triệu Phạm, bảo hắn mau tới đầu hàng.","Chắc ngươi cũng không đánh lại ta đâu, ta sẽ cho ngươi về, mau đi nói với Triệu Phạm, bảo hắn mau tới đầu hàng.",},
        [5147] = {10102,"Trần Ứng","Triệu Vân","Tạ ơn tướng quân.","Tạ ơn tướng quân.",},
        [5148] = {10103,"Triệu Phạm","Trần Ứng","Ta nói muốn đầu hàng, ngươi lại muốn chiến, vậy thì thôi.","Ta nói muốn đầu hàng, ngươi lại muốn chiến, vậy thì thôi.",},
        [5149] = {10103,"Triệu Phạm","Trần Ứng","Ngươi về đi, ta sẽ cầm ấn tín này đi tìm Triệu Vân đầu hàng","Ngươi về đi, ta sẽ cầm ấn tín này đi tìm Triệu Vân đầu hàng",},
        [5150] = {10104,"Triệu Phạm","Triệu Vân","Triệu tướng quân và ta cùng họ, 500 năm trước chung một nhà. Hai ta lại là đồng hương.","Triệu tướng quân và ta cùng họ, 500 năm trước chung một nhà. Hai ta lại là đồng hương.",},
        [5151] = {10104,"Triệu Phạm","Triệu Vân","Nếu có được Kiên Trung, kết làm huynh đệ thì thật may mắn.","Nếu có được Kiên Trung, kết làm huynh đệ thì thật may mắn.",},
        [5152] = {10104,"Triệu Phạm","Triệu Vân","Tướng quân lớn hơn tiểu đệ 4 tháng tuổi, tiểu đệ mong được gọi tướng quân là huynh trưởng!","Tướng quân lớn hơn tiểu đệ 4 tháng tuổi, tiểu đệ mong được gọi tướng quân là huynh trưởng!",},
        [5153] = {10104,"Triệu Vân","Triệu Phạm","Hiền đệ không cần đa lễ!","Hiền đệ không cần đa lễ!",},
        [5154] = {10105,"Triệu Phạm","Triệu Vân","Tiểu đệ có việc này, nhân huynh đừng từ chối. Gia tẩu Phàn thị, góa bụa 3 năm, đệ thường khuyên tẩu ấy tái giá.","Tiểu đệ có việc này, nhân huynh đừng từ chối. Gia tẩu Phàn thị, góa bụa 3 năm, đệ thường khuyên tẩu ấy tái giá.",},
        [5155] = {10105,"Triệu Phạm","Triệu Vân","Gia tẩu đưa ra 3 điều kiện: Một là văn võ song toàn, nổi tiếng thiên hạ; hai là tướng mạo khôi ngô, uy nghi xuất chúng, ba là phải cùng họ với gia huynh.","Gia tẩu đưa ra 3 điều kiện: Một là văn võ song toàn, nổi tiếng thiên hạ; hai là tướng mạo khôi ngô, uy nghi xuất chúng, ba là phải cùng họ với gia huynh.",},
        [5156] = {10105,"Triệu Phạm","Triệu Vân","Thật trùng hợp thay, nay tôn huynh lại hợp với lời gia tẩu nói. Nếu tôn huynh không chê gia tẩu, mong được đem theo sính lễ, theo về làm vợ tướng quân, thế nào?","Thật trùng hợp thay, nay tôn huynh lại hợp với lời gia tẩu nói. Nếu tôn huynh không chê gia tẩu, mong được đem theo sính lễ, theo về làm vợ tướng quân, thế nào?",},
        [5157] = {10105,"Triệu Vân","Triệu Phạm","Khốn kiếp! Ta vừa kết làm huynh đệ với ngươi, tẩu ngươi cũng là tẩu của ta, há có thể làm chuyện loạn luân như vậy được!","Khốn kiếp! Ta vừa kết làm huynh đệ với ngươi, tẩu ngươi cũng là tẩu của ta, há có thể làm chuyện loạn luân như vậy được!",},
        [5158] = {10105,"Triệu Phạm","Triệu Vân","Ta chỉ có ý tốt, sao lại vô lễ như vậy chứ!","Ta chỉ có ý tốt, sao lại vô lễ như vậy chứ!",},
        [5159] = {10106,"Trần Ứng","Triệu Phạm","Triệu Vân tức giận đi ra khỏi thành, chúng ta liều mạng với hắn nào!","Triệu Vân tức giận đi ra khỏi thành, chúng ta liều mạng với hắn nào!",},
        [5160] = {10106,"Triệu Phạm","Trần Ứng","Chỉ sợ đánh không lại hắn.","Chỉ sợ đánh không lại hắn.",},
        [5161] = {10106,"Trần Ứng","Triệu Phạm","Ta và Bào Long giả hàng vào trong quân của hắn, ngươi đem quân tới, chúng ta trong ngoài phối hợp tóm gọn hắn.","Ta và Bào Long giả hàng vào trong quân của hắn, ngươi đem quân tới, chúng ta trong ngoài phối hợp tóm gọn hắn.",},
        [5162] = {10107,"Trần Ứng","Triệu Vân","Triệu Phạm định dùng Mỹ nhân kế lừa tướng quân, chỉ chờ tướng quân uống say, sẽ dìu vào bên trong mưu sát, đem đầu tới chỗ Tào thừa tướng, thật bất nhân.","Triệu Phạm định dùng Mỹ nhân kế lừa tướng quân, chỉ chờ tướng quân uống say, sẽ dìu vào bên trong mưu sát, đem đầu tới chỗ Tào thừa tướng, thật bất nhân.",},
        [5163] = {10107,"Trần Ứng","Triệu Vân","Hai người bọn ta thấy tướng quân tức giận, ắt sẽ liên lụy đến bọn ta, nên mới đầu hàng.","Hai người bọn ta thấy tướng quân tức giận, ắt sẽ liên lụy đến bọn ta, nên mới đầu hàng.",},
        [5164] = {10107,"Triệu Vân","","Ha ha ha... Được, người đâu! Bắt 2 người này lại! Đừng tưởng ta không biết các ngươi giả hàng, đừng hòng lừa ta!","Ha ha ha... Được, người đâu! Bắt 2 người này lại! Đừng tưởng ta không biết các ngươi giả hàng, đừng hòng lừa ta!",},
        [5165] = {10107,"Triệu Vân","","Truyền lệnh xuống: Kẻ muốn hại ta, Trần Ứng, Bào Long; các ngươi làm theo lời ta, sẽ có trọng thưởng.","Truyền lệnh xuống: Kẻ muốn hại ta, Trần Ứng, Bào Long; các ngươi làm theo lời ta, sẽ có trọng thưởng.",},
        [5166] = {10107,"Triệu Vân","","Đi, chúng ta đến Quế Dương Thành tìm Triệu Phạm tính sổ!","Đi, chúng ta đến Quế Dương Thành tìm Triệu Phạm tính sổ!",},
        [5167] = {10108,"Lưu Bị","Triệu Vân","Tử Long làm tốt lắm, có công lấy Quế Dương!","Tử Long làm tốt lắm, có công lấy Quế Dương!",},
        [5168] = {10108,"Nhân vật","Triệu Vân","Triệu Phạm muốn gả tẩu tẩu cho ngươi, vốn dĩ là một chuyện tốt, Tử Long sao lại như vậy?","Triệu Phạm muốn gả tẩu tẩu cho ngươi, vốn dĩ là một chuyện tốt, Tử Long sao lại như vậy?",},
        [5169] = {10108,"Triệu Vân","Lưu Bị","Triệu Phạm vừa kết làm huynh đệ với ta, nếu ta cưới tẩu tẩu hắn, sẽ bị cười chê, huống chi tẩu hắn tái giá, sẽ mất khí tiết.","Triệu Phạm vừa kết làm huynh đệ với ta, nếu ta cưới tẩu tẩu hắn, sẽ bị cười chê, huống chi tẩu hắn tái giá, sẽ mất khí tiết.",},
        [5170] = {10108,"Triệu Vân","Lưu Bị","Triệu Phạm mới hàng, lòng dạ khó đoán, chúa công mới định giang sơn, có trăm mối lo, Vân nào dám vì một người phụ nữ mà làm hỏng đại sự của Chúa công?","Triệu Phạm mới hàng, lòng dạ khó đoán, chúa công mới định giang sơn, có trăm mối lo, Vân nào dám vì một người phụ nữ mà làm hỏng đại sự của Chúa công?",},
        [5171] = {10108,"Lưu Bị","Triệu Vân","Nay đại sự đã định, ta sẽ cưới nàng, thế nào?","Nay đại sự đã định, ta sẽ cưới nàng, thế nào?",},
        [5172] = {10108,"Triệu Vân","","Nữ nhân thiên hạ vô số, chưa thành danh, sao dám nghĩ lập thê?","Nữ nhân thiên hạ vô số, chưa thành danh, sao dám nghĩ lập thê?",},
        [5173] = {10108,"Nhân vật","","Tử Long thật đúng là trượng phu!","Tử Long thật đúng là trượng phu!",},
        [5174] = {10201,"Trương Phi","Gia Cát Lượng","Tử Long và Tứ Đệ đều đoạt được một quận, ta vẫn chưa làm được gì! Hãy cho ta 3000 binh mã, ta sẽ chiếm quận Vũ Lăn, bắt sống Thái Thú Kim Toàn!","Tử Long và Tứ Muội đều đoạt được một quận, ta vẫn chưa làm được gì! Hãy cho ta 3000 binh mã, ta sẽ chiếm quận Vũ Lăn, bắt sống Thái Thú Kim Toàn!",},
        [5175] = {10201,"Gia Cát Lượng","Trương Phi","Trước kia khi Tử Long lấy Quế Dương Quận, phải cần có quân lệnh. Nay Dực Đức muốn lấy Vũ Lân, thì cũng phải có quân lệnh thì mới được dẫn quân đi.","Trước kia khi Tử Long lấy Quế Dương Quận, phải cần có quân lệnh. Nay Dực Đức muốn lấy Vũ Lân, thì cũng phải có quân lệnh thì mới được dẫn quân đi.",},
        [5176] = {10201,"Tinh Thái","Nhân vật","Chúa công! Cha xưa nay nôn nóng, nay lập quân lệnh muốn lấy Vũ Lân Quận, ta lo sẽ xảy ra loạn.","Chúa công! Cha xưa nay nôn nóng, nay lập quân lệnh muốn lấy Vũ Lân Quận, ta lo sẽ xảy ra loạn.",},
        [5177] = {10201,"Nhân vật","Tinh Thái","Tinh Thái yên tâm, chúng ta ngầm quan sát, tùy cơ ứng biến, giúp tam ca một tay.","Tinh Thái yên tâm, chúng ta ngầm quan sát, tùy cơ ứng biến, giúp tam ca một tay.",},
        [5178] = {10202,"Kim Toàn","Củng Chí","Trương Phi dẫn quân đến đây, muốn chiếm quận ta, chờ ta chỉnh đốn quân lính sẽ xuất thành nghênh địch!","Trương Phi dẫn quân đến đây, muốn chiếm quận ta, chờ ta chỉnh đốn quân lính sẽ xuất thành nghênh địch!",},
        [5179] = {10202,"Củng Chí","Kim Toàn","Lưu Huyền Đức là hoàng thúc Đại Hán, nhân nghĩa bao trùm thiên hạ; cộng thêm Trương Dực Đức kiêu dũng phi thường. Không thể nghênh địch, chi bằng đầu hàng.","Lưu Huyền Đức là hoàng thúc Đại Hán, nhân nghĩa bao trùm thiên hạ; cộng thêm Trương Dực Đức kiêu dũng phi thường. Không thể nghênh địch, chi bằng đầu hàng.",},
        [5180] = {10202,"Kim Toàn","Củng Chí","Lẽ nào lại như vậy! Ngươi muốn cấu kết với tặc nhân hay sao? Người đâu! Lôi Củng Chí ra ngoài trảm!","Lẽ nào lại như vậy! Ngươi muốn cấu kết với tặc nhân hay sao? Người đâu! Lôi Củng Chí ra ngoài trảm!",},
        [5181] = {10202,"Kim Toàn Tùy Tùng","Kim Toàn","Không được không được! Trảm người của mình, sẽ bất lợi cho quân ta, ảnh hưởng tới sĩ khí!","Không được không được! Trảm người của mình, sẽ bất lợi cho quân ta, ảnh hưởng tới sĩ khí!",},
        [5182] = {10202,"Kim Toàn","Củng Chí","Hừ! Tạm tha cho ngươi đó, ta sẽ dẫn quân xuất chiến, đấu với Trương Dực Đức kia!","Hừ! Tạm tha cho ngươi đó, ta sẽ dẫn quân xuất chiến, đấu với Trương Dực Đức kia!",},
        [5183] = {10203,"Trương Phi","","Ông nội ngươi hôm nay muốn lấy Vũ Lân Quận! Kim Toàn mau ra nộp mạng!","Ông nội ngươi hôm nay muốn lấy Vũ Lân Quận! Kim Toàn mau ra nộp mạng!",},
        [5184] = {10203,"Kim Toàn","","Ai dám xuất chiến?... Ai dám xuất chiến?... Có ai không! Ai dám xuất chiến!... Thôi! Để ta!","Ai dám xuất chiến?... Ai dám xuất chiến?... Có ai không! Ai dám xuất chiến!... Thôi! Để ta!",},
        [5185] = {10203,"Trương Phi","","Hừ! Chạy đâu cho thoát.","Hừ! Chạy đâu cho thoát.",},
        [5186] = {10204,"Kim Toàn","Củng Chí","Củng Chí! Ngươi định làm gì? Bắn tên vào ta làm gì! Mau mở cổng thành, cho ta về!","Củng Chí! Ngươi định làm gì? Bắn tên vào ta làm gì! Mau mở cổng thành, cho ta về!",},
        [5187] = {10204,"Củng Chí","Kim Toàn","Là ngươi tự chuốc lấy, ta và bách tính tự hàng Lưu Hĩ.","Là ngươi tự chuốc lấy, ta và bách tính tự hàng Lưu Hĩ.",},
        [5188] = {10204,"Kim Toàn","Củng Chí","Ngươi! A... (bị trúng một tên)","Ngươi! A... (bị trúng một tên)",},
        [5189] = {10205,"Tinh Thái","","May nhờ kế sách của Chúa công, thuyết phục Củng Chí, để hắn ngoan ngoãn đầu hàng.","May nhờ kế sách của Chúa công, thuyết phục Củng Chí, để hắn ngoan ngoãn đầu hàng.",},
        [5190] = {10205,"Nhân vật","","Tinh Thái muội muội quá khen, nhờ đại ca nhân đức vang danh thiên hạ, dân chúng vui vẻ kính phục.","Tinh Thái muội muội quá khen, nhờ đại ca nhân đức vang danh thiên hạ, dân chúng vui vẻ kính phục.",},
        [5191] = {10206,"Lưu Bị","Trương Phi","Dực Đức lần này lấy được Vũ Lân Quận, cũng là công lớn, tốt lắm!","Dực Đức lần này lấy được Vũ Lân Quận, cũng là công lớn, tốt lắm!",},
        [5192] = {10206,"Trương Phi","Lưu Bị","Không ngờ lần này lấy Vũ Lân Quận lại dễ dàng như vậy!","Không ngờ lần này lấy Vũ Lân Quận lại dễ dàng như vậy!",},
        [5193] = {10301,"Quan Vũ","Gia Cát Lượng","Nghe nói Tam Đệ, Tứ Đệ và Tử Long mỗi người đoạt được một quận. Nay Quan mỗ cũng xin ra trận đoạt một quận về!","Nghe nói Tam Đệ, Tứ Muội và Tử Long mỗi người đoạt được một quận. Nay Quan mỗ cũng xin ra trận đoạt một quận về!",},
        [5194] = {10301,"Gia Cát Lượng","Quan Vũ","Thái Thú Hàn Huyền chẳng có tài cán gì, nhưng dưới trướng có một viên tướng tên là Hoàng Trung, tuy đã gần 60 tuổi, nhưng dũng mãnh phi thường, không nên khinh địch!","Thái Thú Hàn Huyền chẳng có tài cán gì, nhưng dưới trướng có một viên tướng tên là Hoàng Trung, tuy đã gần 60 tuổi, nhưng dũng mãnh phi thường, không nên khinh địch!",},
        [5195] = {10301,"Quan Vũ","Gia Cát Lượng","Quân sư sao lại nâng người khác lên, hạ mình xuống như vậy?","Quân sư sao lại nâng người khác lên, hạ mình xuống như vậy?",},
        [5196] = {10301,"Quan Vũ","Gia Cát Lượng","Quan mỗ không không cần dùng tới 3000 quân, chỉ cần 500 Đao Thủ, sẽ lấy được thủ cấp Hoàng Trung, Hàn Huyền đem về đây.","Quan mỗ không không cần dùng tới 3000 quân, chỉ cần 500 Đao Thủ, sẽ lấy được thủ cấp Hoàng Trung, Hàn Huyền đem về đây.",},
        [5197] = {10301,"Gia Cát Lượng","Quan Vũ","Vân Trường khinh thường Hoàng Trung, chỉ e lỡ việc.","Vân Trường khinh thường Hoàng Trung, chỉ e lỡ việc.",},
        [5198] = {10301,"Nhân vật","Gia Cát Lượng","Ta muốn đi cùng nhị ca để tiếp ứng.","Ta muốn đi cùng nhị ca để tiếp ứng.",},
        [5199] = {10301,"Gia Cát Lượng","Nhân vật","Vậy thì tốt.","Vậy thì tốt.",},
        [5200] = {10302,"Hàn Huyền","","Quan Vũ muốn tới lấy Phượng Tường chúng ta, phải làm sao đây?","Quan Vũ muốn tới lấy Phượng Tường chúng ta, phải làm sao đây?",},
        [5201] = {10302,"Hoàng Trung","Hàn Huyền","Chúa công không cần lo, với ngọn đao này, cây cung này, bao nhiêu người tới cũng sẽ tiêu đời!","Chúa công không cần lo, với ngọn đao này, cây cung này, bao nhiêu người tới cũng sẽ tiêu đời!",},
        [5202] = {10302,"Dương Linh","Hàn Huyền","Không muốn lão tướng quân xuất trận, để ta định đoạt.","Không muốn lão tướng quân xuất trận, để ta định đoạt.",},
        [5203] = {10302,"Hàn Huyền","Dương Linh","Được, cho ngươi 1000 binh mã, xuất thành nghênh chiến!","Được, cho ngươi 1000 binh mã, xuất thành nghênh chiến!",},
        [5204] = {10303,"Quan Vũ","Hoàng Trung","Lúc trước Dương Linh kia quá yếu, một đao là xong chuyện. Ngươi có phải là Hoàng Trung?","Lúc trước Dương Linh kia quá yếu, một đao là xong chuyện. Ngươi có phải là Hoàng Trung?",},
        [5205] = {10303,"Hoàng Trung","Quan Vũ","Đã nghe danh ta, còn dám xâm phạm đất ta!","Đã nghe danh ta, còn dám xâm phạm đất ta!",},
        [5206] = {10303,"Quan Vũ","Hoàng Trung","Ta tới lấy thủ cấp ngươi!","Ta tới lấy thủ cấp ngươi!",},
        [5207] = {10304,"Hàn Huyền","","Đã đánh hơn 100 lượt rồi mà không phân thắng bại, Hoàng tướng quân mau quay về, đừng để có sơ sót gì.","Đã đánh hơn 100 lượt rồi mà không phân thắng bại, Hoàng tướng quân mau quay về, đừng để có sơ sót gì.",},
        [5208] = {10304,"Quan Vũ","","Lão tướng Hoàng Trung quả là danh bất hư truyền, lần sau có lẽ phải dùng kế mới mong thắng nổi.","Lão tướng Hoàng Trung quả là danh bất hư truyền, lần sau có lẽ phải dùng kế mới mong thắng nổi.",},
        [5209] = {10305,"Quan Vũ","Hoàng Trung","Lần này ta sẽ không nhường ngươi đâu, nhất định phải phân cao thấp.","Lần này ta sẽ không nhường ngươi đâu, nhất định phải phân cao thấp.",},
        [5210] = {10305,"Hoàng Trung","Quan Vũ","Hừ, mạnh miệng lắm, ai thua ai thắng còn chưa chắc.","Hừ, mạnh miệng lắm, ai thua ai thắng còn chưa chắc.",},
        [5211] = {10305,"Quan Vũ","Hoàng Trung","Xem đao!","Xem đao!",},
        [5212] = {10305,"Hoàng Trung","Quan Vũ","Hừ!... A! (Chiến mã mất móng trước hất Hoàng Trung ngã xuống đất)","Hừ!... A! (Chiến mã mất móng trước hất Hoàng Trung ngã xuống đất)",},
        [5213] = {10305,"Quan Vũ","Hoàng Trung","(Lên ngựa thu đao) Ta sẽ tạm tha cho ngươi, mau đổi ngựa rồi đánh tiếp!","(Lên ngựa thu đao) Ta sẽ tạm tha cho ngươi, mau đổi ngựa rồi đánh tiếp!",},
        [5214] = {10306,"Hàn Huyền","Hoàng Trung","Có chuyện gì vậy? Sao Hoàng tướng quân lại bị ngựa hất xuống đất?","Có chuyện gì vậy? Sao Hoàng tướng quân lại bị ngựa hất xuống đất?",},
        [5215] = {10306,"Hoàng Trung","Hàn Huyền","Ngựa này lâu rồi không ra trận, nên có sơ suất này. Sau này tái đấu, ta sẽ dùng cung tên bắn.","Ngựa này lâu rồi không ra trận, nên có sơ suất này. Sau này tái đấu, ta sẽ dùng cung tên bắn.",},
        [5216] = {10307,"Hoàng Trung","","Vân Trường nghĩa khí tha cho ta, ta cũng không nỡ làm hại! Nhưng nếu không bắn, lại là trái quân lệnh.","Vân Trường nghĩa khí tha cho ta, ta cũng không nỡ làm hại! Nhưng nếu không bắn, lại là trái quân lệnh.",},
        [5217] = {10307,"Hoàng Trung","","Ta chỉ hù dọa thôi mà hắn đã sợ bỏ chạy.","Ta chỉ hù dọa thôi mà hắn đã sợ bỏ chạy.",},
        [5218] = {10307,"Quan Vũ","","Nghe nói Hoàng Trung bách phát bách trúng, sao lại chỉ nghe tiếng dây cung mà không thấy tên? Lẽ nào lời đồn là giả?","Nghe nói Hoàng Trung bách phát bách trúng, sao lại chỉ nghe tiếng dây cung mà không thấy tên? Lẽ nào lời đồn là giả?",},
        [5219] = {10307,"Hoàng Trung","","Xem tên của ta đây! (Cầm cung lên, giương dây cung, nhắm vào tua vải trên nón Vân Trường)","Xem tên của ta đây! (Cầm cung lên, giương dây cung, nhắm vào tua vải trên nón Vân Trường)",},
        [5220] = {10307,"Quan Vũ","","Thì ra Hoàng Trung đúng là có tài thiện xạ, nay chỉ bắn tua vải, chính là để báo ơn hôm qua.","Thì ra Hoàng Trung đúng là có tài thiện xạ, nay chỉ bắn tua vải, chính là để báo ơn hôm qua.",},
        [5221] = {10401,"Hàn Huyền","Hoàng Trung","Ta đã quan sát ba ngày! Hôm trước ngươi xuất chiến không dốc toàn lực, đã là không đúng! Hôm qua ngươi ngã ngựa, hắn không lấy mạng ngươi, ắt là có sự thông đồng!","Ta đã quan sát ba ngày! Hôm trước ngươi xuất chiến không dốc toàn lực, đã là không đúng! Hôm qua ngươi ngã ngựa, hắn không lấy mạng ngươi, ắt là có sự thông đồng!",},
        [5222] = {10401,"Hàn Huyền","Hoàng Trung","Hai bắn hai phát vẫn hụt, phát thứ ba chỉ trúng đỉnh nón, còn nói là không phải thông đồng?","Hai bắn hai phát vẫn hụt, phát thứ ba chỉ trúng đỉnh nón, còn nói là không phải thông đồng?",},
        [5223] = {10401,"Hoàng Trung","Hàn Huyền","Ta vô tội!","Ta vô tội!",},
        [5224] = {10401,"Hàn Huyền","","Ta không nghe! Người đâu, lôi Hoàng Trung ra ngoài trảm! Nếu kẻ nào xin tha, sẽ trảm luôn!","Ta không nghe! Người đâu, lôi Hoàng Trung ra ngoài trảm! Nếu kẻ nào xin tha, sẽ trảm luôn!",},
        [5225] = {10401,"Ngụy Diên","","Hoàng Trung tướng quân là người bảo hộ của Phượng Tường, nay nếu trảm Hoàng tướng quân nghĩa là sẽ gây hại cho bá tánh Phượng Tường!","Hoàng Trung tướng quân là người bảo hộ của Phượng Tường, nay nếu trảm Hoàng tướng quân nghĩa là sẽ gây hại cho bá tánh Phượng Tường!",},
        [5226] = {10401,"Ngụy Diên","","Hàn Huyền tàn bạo bất nhân, khinh thường hiền sĩ, mọi người cùng diệt hắn! Ai muốn thì theo ta!","Hàn Huyền tàn bạo bất nhân, khinh thường hiền sĩ, mọi người cùng diệt hắn! Ai muốn thì theo ta!",},
        [5227] = {10402,"Quan Vũ","Lưu Bị","Đại ca, đệ đã chiếm được Phượng Tường.","Đại ca, đệ đã chiếm được Phượng Tường.",},
        [5228] = {10402,"Lưu Bị","Quan Vũ","Tốt lắm! Nhị đệ có công! Ta muốn đi gặp Hoàng Trung tướng quân.","Tốt lắm! Nhị đệ có công! Ta muốn đi gặp Hoàng Trung tướng quân.",},
        [5229] = {10402,"Hoàng Trung","Lưu Bị","Ta muốn quy thuận Huyền Đức Công, chỉ là xin hãy hậu táng cho chúa công cũ là Hàn Huyền.","Ta muốn quy thuận Huyền Đức Công, chỉ là xin hãy hậu táng cho chúa công cũ là Hàn Huyền.",},
        [5230] = {10402,"Lưu Bị","Hoàng Trung","Tướng quân thật trung nghĩa!","Tướng quân thật trung nghĩa!",},
        [5231] = {10403,"Quan Vũ","Lưu Bị","Xin giới thiệu với đại ca, đây là Ngụy Diên.","Xin giới thiệu với đại ca, đây là Ngụy Diên.",},
        [5232] = {10403,"Gia Cát Lượng","","Người đâu, đem Ngụy Diên ra ngoài trảm!","Người đâu, đem Ngụy Diên ra ngoài trảm!",},
        [5233] = {10403,"Lưu Bị","Gia Cát Lượng","Sao quân sư lại muốn diệt Ngụy Diên?","Sao quân sư lại muốn diệt Ngụy Diên?",},
        [5234] = {10403,"Gia Cát Lượng","Lưu Bị","Ăn lộc của chủ mà hại chủ, ấy là bất trung; ở đất của người mà phản bội, ấy là bất nghĩa.","Ăn lộc của chủ mà hại chủ, ấy là bất trung; ở đất của người mà phản bội, ấy là bất nghĩa.",},
        [5235] = {10403,"Gia Cát Lượng","Lưu Bị","Ta thấy Ngụy Diên tướng mạo phản phúc, không diệt ắt mang hậu họa.","Ta thấy Ngụy Diên tướng mạo phản phúc, không diệt ắt mang hậu họa.",},
        [5236] = {10403,"Lưu Bị","Gia Cát Lượng","Nếu trảm người này, e là người đầu hàng sẽ lo lắng không yên. Mong quân sư tha cho.","Nếu trảm người này, e là người đầu hàng sẽ lo lắng không yên. Mong quân sư tha cho.",},
        [5237] = {10403,"Gia Cát Lượng","Ngụy Diên","Thôi được, ta sẽ tha mạng cho ngươi. Ngươi phải tận trung với chủ, không có dụng tâm khác, không thì ta sẽ lấy thủ cấp ngươi.","Thôi được, ta sẽ tha mạng cho ngươi. Ngươi phải tận trung với chủ, không có dụng tâm khác, không thì ta sẽ lấy thủ cấp ngươi.",},
        [5238] = {10501,"Chu Du","Lỗ Túc","Ta muốn khởi binh quyết chiến với Lưu Bị và Gia Cát Lượng, đoạt lại thành trì. May nhờ có Tử Kính và Trình Đức Mưu giúp đỡ, ta quyết đoạt lại Nam Quận về cho Đông Ngô.","Ta muốn khởi binh quyết chiến với Lưu Bị và Gia Cát Lượng, đoạt lại thành trì. May nhờ có Tử Kính và Trình Đức Mưu giúp đỡ, ta quyết đoạt lại Nam Quận về cho Đông Ngô.",},
        [5239] = {10501,"Lỗ Túc","Chu Du","Không được. Nay đang giằng co với Tào Tháo, chưa phân thắng bại. Lưu Huyền Đức trước có giao tình với Tào Tháo, nếu ta thúc ép quá, nhỡ họ dâng nộp thành trì, cùng đánh Đông Ngô, biết làm thế nào?","Không được. Nay đang giằng co với Tào Tháo, chưa phân thắng bại. Lưu Huyền Đức trước có giao tình với Tào Tháo, nếu ta thúc ép quá, nhỡ họ dâng nộp thành trì, cùng đánh Đông Ngô, biết làm thế nào?",},
        [5240] = {10501,"Chu Du","Lỗ Túc","Chúng ta dùng kế sách, tổn binh mã, phí lương tiền, sẵn hết cho hắn, chẳng phải đáng ghét sao!","Chúng ta dùng kế sách, tổn binh mã, phí lương tiền, sẵn hết cho hắn, chẳng phải đáng ghét sao!",},
        [5241] = {10501,"Lỗ Túc","Chu Du","Công Cẩn hãy kiên nhẫn. Dung mỗ gặp Huyền Đức, nói lí với hắn. Nếu nói không được, lúc đó động binh cũng chưa muộn.","Công Cẩn hãy kiên nhẫn. Dung mỗ gặp Huyền Đức, nói lí với hắn. Nếu nói không được, lúc đó động binh cũng chưa muộn.",},
        [5242] = {10501,"Chu Du Tùy Tùng","Chu Du","Tử Kính nói rất đúng.","Tử Kính nói rất đúng.",},
        [5243] = {10502,"Lỗ Túc","Triệu Vân","Ta muốn gặp Lưu Huyền Đức.","Ta muốn gặp Lưu Huyền Đức.",},
        [5244] = {10502,"Triệu Vân","Lỗ Túc","Chúa công và quân sư đang ở Kinh Châu.","Chúa công và quân sư đang ở Kinh Châu.",},
        [5245] = {10502,"Nhân vật","Triệu Vân","Lỗ Túc đến tìm đại ca chắc chắn muốn lấy lại Nam Quận, ta phải đi theo nói giúp đại ca.","Lỗ Túc đến tìm đại ca chắc chắn muốn lấy lại Nam Quận, ta phải đi theo nói giúp đại ca.",},
        [5246] = {10503,"Lỗ Túc","Gia Cát Lượng","Ta đại diện cho Ngô Hầu và Đô Đốc Công Cẩn đến chuyển lời cho hoàng thúc. Ngày trước Tào Tháo dẫn quân tiến đánh, chính Đông Ngô đã đánh lui quân Tào, giải nguy cho ngài.","Ta đại diện cho Ngô Hầu và Đô Đốc Công Cẩn đến chuyển lời cho hoàng thúc. Ngày trước Tào Tháo dẫn quân tiến đánh, chính Đông Ngô đã đánh lui quân Tào, giải nguy cho ngài.",},
        [5247] = {10503,"Lỗ Túc","Gia Cát Lượng","Tất cả 9 quận Kinh Châu, hợp lại đều thuộc Đông Ngô. Nay hoàng thúc dùng kế chiếm đoạt Kinh Tương, khiến quân lương quân mã Giang Đông trống không, còn hoàng thúc chỉ ngồi hưởng lợi, e rằng không hợp tình lý.","Tất cả 9 quận Kinh Châu, hợp lại đều thuộc Đông Ngô. Nay hoàng thúc dùng kế chiếm đoạt Kinh Tương, khiến quân lương quân mã Giang Đông trống không, còn hoàng thúc chỉ ngồi hưởng lợi, e rằng không hợp tình lý.",},
        [5248] = {10503,"Gia Cát Lượng","Lỗ Túc","Tử Kính sao lại nói thế? Kinh Tương 9 quận không phải đất Đông Ngô, mà là cơ nghiệp Lưu Cảnh Thăng. chúa công ta là đệ đệ của Cảnh Thăng.","Tử Kính sao lại nói thế? Kinh Tương 9 quận không phải đất Đông Ngô, mà là cơ nghiệp Lưu Cảnh Thăng. chúa công ta là đệ đệ của Cảnh Thăng.",},
        [5249] = {10503,"Gia Cát Lượng","Lỗ Túc","Chủ nhân ta là tộc đệ của Cảnh Thăng. Cảnh Thăng tuy qua đời, nhưng vẫn còn con trai nối dõi! Nay chú giúp cháu cai trị Kinh Châu, có gì không được?","Chủ nhân ta là tộc đệ của Cảnh Thăng. Cảnh Thăng tuy qua đời, nhưng vẫn còn con trai nối dõi! Nay chú giúp cháu cai trị Kinh Châu, có gì không được?",},
        [5250] = {10504,"Lỗ Túc","Nhân vật","Nếu đây là nơi công tử Lưu Kỳ chiếm cứ, thì có thể hiểu được, nhưng nay công tử ở Giang Hạ, không có ở đây!","Nếu đây là nơi công tử Lưu Kỳ chiếm cứ, thì có thể hiểu được, nhưng nay công tử ở Giang Hạ, không có ở đây!",},
        [5251] = {10504,"Nhân vật","Lỗ Túc","Tử Kính muốn gặp công tử Lưu Kỳ? Người đâu, mời công tử ra đây.","Tử Kính muốn gặp công tử Lưu Kỳ? Người đâu, mời công tử ra đây.",},
        [5252] = {10504,"Lưu Kỳ","Lỗ Túc","Thân bệnh không thể hành lễ, thứ lỗi.","Thân bệnh không thể hành lễ, thứ lỗi.",},
        [5253] = {10504,"Lỗ Túc","Nhân vật","Nếu công tử không còn thì làm sao?","Nếu công tử không còn thì làm sao?",},
        [5254] = {10504,"Nhân vật","Lỗ Túc","Công tử còn một ngày, thủ một ngày, nếu không còn, sẽ bàn bạc lại.","Công tử còn một ngày, thủ một ngày, nếu không còn, sẽ bàn bạc lại.",},
        [5255] = {10504,"Lỗ Túc","Nhân vật","Nếu công tử không ở đây, phải trả thành cho Đông Ngô.","Nếu công tử không ở đây, phải trả thành cho Đông Ngô.",},
        [5256] = {10504,"Nhân vật","Lỗ Túc","Tử Kính nói phải.","Tử Kính nói phải.",},
        [5257] = {10505,"Lỗ Túc","Chu Du","Bẩm Đô Đốc, Gia Cát Lượng nói, nếu Lưu Kỳ không còn, sẽ trả thành trì cho Đông Ngô.","Bẩm Đô Đốc, Gia Cát Lượng nói, nếu Lưu Kỳ không còn, sẽ trả thành trì cho Đông Ngô.",},
        [5258] = {10505,"Chu Du","Lỗ Túc","Lưu Kỳ còn trẻ, sao có thể bỏ mạng? Khi nào mới có lại Kinh Châu?","Lưu Kỳ còn trẻ, sao có thể bỏ mạng? Khi nào mới có lại Kinh Châu?",},
        [5259] = {10505,"Lỗ Túc","Chu Du","Đô Đốc yên tâm. Giao cho Lỗ Túc, chắc chắn lấy Kinh Tương trả lại Đông Ngô.","Đô Đốc yên tâm. Giao cho Lỗ Túc, chắc chắn lấy Kinh Tương trả lại Đông Ngô.",},
        [5260] = {10505,"Chu Du","Lỗ Túc","Tử Kính có cao kiến gì?","Tử Kính có cao kiến gì?",},
        [5261] = {10505,"Lỗ Túc","Chu Du","Ta thấy Lưu Kỳ tửu sắc quá độ, bệnh nặng nguy kịch, nay sắc mặt xanh xao, khó thở, không quá nửa năm chắc chắn qua đời. Chừng đó đòi lại Kinh Châu, Lưu Bị cũng không còn cớ gì nữa.","Ta thấy Lưu Kỳ tửu sắc quá độ, bệnh nặng nguy kịch, nay sắc mặt xanh xao, khó thở, không quá nửa năm chắc chắn qua đời. Chừng đó đòi lại Kinh Châu, Lưu Bị cũng không còn cớ gì nữa.",},
        [5262] = {10505,"Tôn Quyền Tín Sứ","Chu Du","Trận chiến Hợp Phì diễn ra đã lâu, hãy để Đô Đốc rút quân về tiếp viện cho Hợp Phì.","Trận chiến Hợp Phì diễn ra đã lâu, hãy để Đô Đốc rút quân về tiếp viện cho Hợp Phì.",},
        [5263] = {10601,"Nhân vật","Tinh Thái","Nghe nói Tôn Quyền từ sau trận chiến Xích Bích, ở lại Hợp Phì, cùng Tào binh giao tranh hơn mười trận mà vẫn bất phân thắng bại, chúng ta đến đó xem sao.","Nghe nói Tôn Quyền từ sau trận chiến Xích Bích, ở lại Hợp Phì, cùng Tào binh giao tranh hơn mười trận mà vẫn bất phân thắng bại, chúng ta đến đó xem sao.",},
        [5264] = {10601,"Tinh Thái","Nhân vật","Tinh Thái nguyện đi theo chúa công, chúa công đi đâu xin đi theo đó!","Tinh Thái nguyện đi theo chúa công, chúa công đi đâu xin đi theo đó!",},
        [5265] = {10602,"Tôn Quyền","","Chúng ta và quân Tào đánh nhau đã nhiều trận vẫn bất phân thắng bại. Nghe nói quân Tào đang đợi viện quân, không biết là thật hay giả.","Chúng ta và quân Tào đánh nhau đã nhiều trận vẫn bất phân thắng bại. Nghe nói quân Tào đang đợi viện quân, không biết là thật hay giả.",},
        [5266] = {10602,"Tôn Quyền Tùy Tùng","Tôn Quyền","Chi bằng bắt vài tên tín sứ đến hỏi xem thực hư thế nào.","Chi bằng bắt vài tên tín sứ đến hỏi xem thực hư thế nào.",},
        [5267] = {10603,"Tôn Quyền Tùy Tùng","Tôn Quyền","Bẩm chúa công, đã bắt được tín sứ quân Tào, đây là mật báo của bọn chúng.","Bẩm chúa công, đã bắt được tín sứ quân Tào, đây là mật báo của bọn chúng.",},
        [5268] = {10603,"Tôn Quyền","","Sao? Phe địch có bốn vạn viện quân sắp đến? Việc này... để ta suy nghĩ...","Sao? Phe địch có bốn vạn viện quân sắp đến? Việc này... để ta suy nghĩ...",},
        [5269] = {10603,"Tôn Quyền","","Đã đánh biết bao nhiêu trận, quân sĩ đều đã mệt mỏi, nay đại quân phe địch đang kéo đến, tiếp tục thế này sẽ bất lợi cho phe ta...","Đã đánh biết bao nhiêu trận, quân sĩ đều đã mệt mỏi, nay đại quân phe địch đang kéo đến, tiếp tục thế này sẽ bất lợi cho phe ta...",},
        [5270] = {10603,"Tôn Quyền","","Chúng ta hãy rút quân đi, Trở về nghỉ ngơi rồi sau này tái chiến.","Chúng ta hãy rút quân đi, Trở về nghỉ ngơi rồi sau này tái chiến.",},
        [5271] = {10603,"Nhân vật","Tôn Quyền","Trọng Mưu khoan hãy vội, ta nghĩ bốn vạn đại quân của kẻ địch không chừng chỉ là tin giả.","Trọng Mưu khoan hãy vội, ta nghĩ bốn vạn đại quân của kẻ địch không chừng chỉ là tin giả.",},
        [5272] = {10603,"Nhân vật","Tôn Quyền","Nghe nói trong quân Tào có một tướng tên Trương Liêu, ta đang muốn đi gặp cho biết.","Nghe nói trong quân Tào có một tướng tên Trương Liêu, ta đang muốn đi gặp cho biết.",},
        [5273] = {10603,"Tôn Quyền","Nhân vật","Tướng quân hãy bảo trọng, nhớ phải cẩn thận.","Tướng quân hãy bảo trọng, nhớ phải cẩn thận.",},
        [5274] = {10604,"Tinh Thái","Nhân vật","Chúa công, nghe nói đại tướng quân Tào Trương Liêu dũng mãnh vô song, chúa công nhớ cẩn thận.","Chúa công, nghe nói đại tướng quân Tào Trương Liêu dũng mãnh vô song, chúa công nhớ cẩn thận.",},
        [5275] = {10604,"Nhân vật","Trương Liêu","Mắt tựa sao sáng, mặt như ngọc tím, Trương tướng quân quả nhiên anh dũng.","Mắt tựa sao sáng, mặt như ngọc tím, Trương tướng quân quả nhiên anh dũng.",},
        [5276] = {10604,"Nhân vật","Trương Liêu","Hôm nay nguyện tỉ thí cùng tướng quân, dừng tay đúng lúc!","Hôm nay nguyện tỉ thí cùng tướng quân, dừng tay đúng lúc!",},
        [5277] = {10604,"Trương Liêu","Nhân vật","Đa tạ chỉ giáo!","Đa tạ chỉ giáo!",},
        [5278] = {10701,"Tôn Quyền","Lỗ Túc","Hôm nay đại quân Trình Phổ kéo đến, chúng đến rất đúng lúc, ta phải đích thân ra doanh thăm hỏi quân sĩ.","Hôm nay đại quân Trình Phổ kéo đến, chúng đến rất đúng lúc, ta phải đích thân ra doanh thăm hỏi quân sĩ.",},
        [5279] = {10701,"Lỗ Túc","Tôn Quyền","Chúa công đích thân xuống ngựa nghênh đón, ta sao dám nhận.","Chúa công đích thân xuống ngựa nghênh đón, ta sao dám nhận.",},
        [5280] = {10701,"Tôn Quyền","Lỗ Túc","Ta xuống ngựa nghênh đón ngươi sẽ khiến địa vị của ngươi được tăng cao sao?","Ta xuống ngựa nghênh đón ngươi sẽ khiến địa vị của ngươi được tăng cao sao?",},
        [5281] = {10701,"Lỗ Túc","Tôn Quyền","Mong rằng chúa công uy đức thiên hạ, thành tựu đế nghiệp, giúp ta lưu danh sử xanh, vậy mới có thể chứng tỏ địa vị của ta.","Mong rằng chúa công uy đức thiên hạ, thành tựu đế nghiệp, giúp ta lưu danh sử xanh, vậy mới có thể chứng tỏ địa vị của ta.",},
        [5282] = {10701,"Tôn Quyền","Lỗ Túc","Ha ha ha, được!","Ha ha ha, được!",},
        [5283] = {10702,"Tôn Quyền Tùy Tùng","Tôn Quyền","Bẩm chúa công, Trương Liêu phái ngươi đưa chiến thư đến.","Bẩm chúa công, Trương Liêu phái ngươi đưa chiến thư đến.",},
        [5284] = {10702,"Tôn Quyền","","Trương Liêu ức hiếp người quá đáng, nghe nói đại quân Trình Phổ đến thì ra chiến, chẳng lẽ là xem thường ta?","Trương Liêu ức hiếp người quá đáng, nghe nói đại quân Trình Phổ đến thì ra chiến, chẳng lẽ là xem thường ta?",},
        [5285] = {10702,"Tôn Quyền","","Ngày mai hãy xem ta đại chiến với kẻ địch!","Ngày mai hãy xem ta đại chiến với kẻ địch!",},
        [5286] = {10703,"Lý Điển","Nhạc Tiến","Người đối diện là Tôn Quyền! Nếu bắt được hắn, đủ báo thù cho đại quân ta!","Người đối diện là Tôn Quyền! Nếu bắt được hắn, đủ báo thù cho đại quân ta!",},
        [5287] = {10703,"Nhạc Tiến","Lý Điển","Được, ta giải quyết Tống Khiêm và Thái Sử Từ trước, chúng ta cùng nhau đi bắt Tôn Quyền.","Được, ta giải quyết Tống Khiêm và Thái Sử Từ trước, chúng ta cùng nhau đi bắt Tôn Quyền.",},
        [5288] = {10704,"Tống Khiêm","","Chúa công đi mau!","Chúa công đi mau!",},
        [5289] = {10704,"Thái Sử Từ","","Không xong rồi, Tống Khiêm đã trúng tên, bảo vệ chúa công!","Không xong rồi, Tống Khiêm đã trúng tên, bảo vệ chúa công!",},
        [5290] = {10704,"Trình Phổ","","Chúa công đừng sợ, có Trình Phổ ở đây.","Chúa công đừng sợ, có Trình Phổ ở đây.",},
        [5291] = {10704,"Tôn Quyền","","Về doanh!","Về doanh!",},
        [5292] = {10705,"Lỗ Túc","Tôn Quyền","Chúa công khí phách hiên ngang, khinh đại địch.","Chúa công khí phách hiên ngang, khinh đại địch.",},
        [5293] = {10705,"Lỗ Túc","Tôn Quyền","Hôm nay Tống Khiêm tử von, là do chúa công khinh địch, sau này nhớ phải bảo trọng.","Hôm nay Tống Khiêm tử von, là do chúa công khinh địch, sau này nhớ phải bảo trọng.",},
        [5294] = {10705,"Tôn Quyền","Lỗ Túc","Là lỗi của ta, từ nay xin sửa đổi.","Là lỗi của ta, từ nay xin sửa đổi.",},
        [5295] = {10705,"Thái Sử Từ","Tôn Quyền","Thủ hạ của ta có người tên Qua Định, là huynh đệ với kẻ chăm ngựa của Trương Liêu.","Thủ hạ của ta có người tên Qua Định, là huynh đệ với kẻ chăm ngựa của Trương Liêu.",},
        [5296] = {10705,"Thái Sử Từ","Tôn Quyền","Hắn bị trách phạt oán trách trong lòng, đêm nay cho người đến báo, lấy đuốc ra hiệu, thích sát Trương Liêu, báo thù cho Tống Khiêm, xin hãy dẫn binh ngoại ứng.","Hắn bị trách phạt oán trách trong lòng, đêm nay cho người đến báo, lấy đuốc ra hiệu, thích sát Trương Liêu, báo thù cho Tống Khiêm, xin hãy dẫn binh ngoại ứng.",},
        [5297] = {10705,"Gia Cát Cẩn","Thái Sử Từ","Trương Liêu đa mưu, e rằng có chuẩn bị, hãy nhớ cẩn thận.","Trương Liêu đa mưu, e rằng có chuẩn bị, hãy nhớ cẩn thận.",},
        [5298] = {10706,"Qua Định","Kẻ Chăm Ngựa","Ta đã phái người báo tướng quân Thái Sử Từ, hôm nay chắc chắn sẽ đến tiếp ứng. Chúng ta phải làm sao?","Ta đã phái người báo tướng quân Thái Sử Từ, hôm nay chắc chắn sẽ đến tiếp ứng. Chúng ta phải làm sao?",},
        [5299] = {10706,"Kẻ Chăm Ngựa","Qua Định","Ở đây cách trung quân còn xa, đêm vội không kịp vào, chỉ còn cách đốt lửa trong bãi cỏ, ngươi đến phía trước kêu gọi, binh lính trong thành ắt loạn, lúc đó sẽ thích sát Trương Liêu, số quân còn lại sẽ tự bỏ đi.","Ở đây cách trung quân còn xa, đêm vội không kịp vào, chỉ còn cách đốt lửa trong bãi cỏ, ngươi đến phía trước kêu gọi, binh lính trong thành ắt loạn, lúc đó sẽ thích sát Trương Liêu, số quân còn lại sẽ tự bỏ đi.",},
        [5300] = {10706,"Trương Liêu","","Truyền lệnh xuống, đêm nay canh phòng cẩn mật, không được sơ suất.","Truyền lệnh xuống, đêm nay canh phòng cẩn mật, không được sơ suất.",},
        [5301] = {10706,"Tùy Tùng","Trương Liêu","Hôm nay toàn thắng, quân Ngô đã lui xa, sao tướng quân không nghỉ ngơi?","Hôm nay toàn thắng, quân Ngô đã lui xa, sao tướng quân không nghỉ ngơi?",},
        [5302] = {10706,"Trương Liêu","Tùy Tùng","Không được. Lỡ quân Ngô nhân lúc ta không phòng bị, thừa cơ tấn công thì phải làm sao? Phòng bị phải nghiêm ngặt hơn những hôm trước.","Không được. Lỡ quân Ngô nhân lúc ta không phòng bị, thừa cơ tấn công thì phải làm sao? Phòng bị phải nghiêm ngặt hơn những hôm trước.",},
        [5303] = {10706,"Tùy Tùng","Trương Liêu","Báo cáo! Phía sau đốt lửa, có tiếng người la hét tạo phản! Tướng quân hãy đi xem thế nào!","Báo cáo! Phía sau đốt lửa, có tiếng người la hét tạo phản! Tướng quân hãy đi xem thế nào!",},
        [5304] = {10706,"Trương Liêu","Tùy Tùng","Đâu thể nào người trong cả thành đều tạo phản? Chắc chắn có kẻ cố tình gây rối lòng quân, kẻ nào làm loạn trảm ngay!","Đâu thể nào người trong cả thành đều tạo phản? Chắc chắn có kẻ cố tình gây rối lòng quân, kẻ nào làm loạn trảm ngay!",},
        [5305] = {10706,"Tùy Tùng","Trương Liêu","Báo cáo! Ngoài thành có tiếng la hét, kẻ địch nhiều người kéo đến!","Báo cáo! Ngoài thành có tiếng la hét, kẻ địch nhiều người kéo đến!",},
        [5306] = {10706,"Trương Liêu","Tùy Tùng","Đây là ngoại ứng của quân Ngô, phải dùng kế để phá.","Đây là ngoại ứng của quân Ngô, phải dùng kế để phá.",},
        [5307] = {10707,"Thái Sử Từ","","Trong thành nổi lửa có tiếng tạo phản chắc chắn là Qua Định bọn họ đã thành công rồi. Chúng ta hãy đi bắt Trương Liêu, báo thù cho Tống Khiêm!","Trong thành nổi lửa có tiếng tạo phản chắc chắn là Qua Định bọn họ đã thành công rồi. Chúng ta hãy đi bắt Trương Liêu, báo thù cho Tống Khiêm!",},
        [5308] = {10707,"Trương Liêu","","Hừ, chúng ta mắc bẫy rồi, bắn tên!","Hừ, chúng ta mắc bẫy rồi, bắn tên!",},
        [5309] = {10707,"Lục Tốn","Thái Sử Từ","Thái Sử Từ tướng quân! Có mai phục! Mau rút lui!","Thái Sử Từ tướng quân! Có mai phục! Mau rút lui!",},
        [5310] = {10707,"Thái Sử Từ","","Đại trượng phu ở thời loạn, phải cầm kiếm lập công lớn! Nay chí chưa thành, số trời khó cãi! (Dứt lời tạ thế, thọ 41 tuổi)","Đại trượng phu ở thời loạn, phải cầm kiếm lập công lớn! Nay chí chưa thành, số trời khó cãi! (Dứt lời tạ thế, thọ 41 tuổi)",},
        [5311] = {10801,"Gia Cát Lượng","Lưu Bị","Tối ngắm tinh tượng, tây bắt có sao rơi, chắc là thành viên hoàng tộc nào đó đã mất.","Tối ngắm tinh tượng, tây bắt có sao rơi, chắc là thành viên hoàng tộc nào đó đã mất.",},
        [5312] = {10801,"Lưu Bị Tùy Tùng","Lưu Bị","Báo cáo chúa công, công tử Lưu Kỳ bệnh nặng qua đời.","Báo cáo chúa công, công tử Lưu Kỳ bệnh nặng qua đời.",},
        [5313] = {10801,"Gia Cát Lượng","Lưu Bị","Sinh tử có số, chúa công đừng buồn, bảo trọng sức khỏe.","Sinh tử có số, chúa công đừng buồn, bảo trọng sức khỏe.",},
        [5314] = {10801,"Lưu Bị","Gia Cát Lượng","Nay Lưu Kỳ đã mất, Đông Ngô đến đòi Kinh Châu, phải nói sao đây?","Nay Lưu Kỳ đã mất, Đông Ngô đến đòi Kinh Châu, phải nói sao đây?",},
        [5315] = {10801,"Gia Cát Lượng","Lưu Bị","Nếu có người đến, Lượng tự có cách trả lời bọn chúng.","Nếu có người đến, Lượng tự có cách trả lời bọn chúng.",},
        [5316] = {10802,"Lỗ Túc","Lưu Bị","Chúa công nghe nói Lưu Kỳ qua đời, mang chút lễ vật đến bái tế. Chu Đô Đốc xin gửi thành ý đến Lưu Hoàng Thúc, Gia Cát tiên sinh.","Chúa công nghe nói Lưu Kỳ qua đời, mang chút lễ vật đến bái tế. Chu Đô Đốc xin gửi thành ý đến Lưu Hoàng Thúc, Gia Cát tiên sinh.",},
        [5317] = {10802,"Lỗ Túc","Lưu Bị","Ngày trước Hoàng Thúc từng hứa, một khi công tử không còn, sẽ trả Kinh Châu. Nay công tử đã qua đời, không biết ngày nào mới trao trả?","Ngày trước Hoàng Thúc từng hứa, một khi công tử không còn, sẽ trả Kinh Châu. Nay công tử đã qua đời, không biết ngày nào mới trao trả?",},
        [5318] = {10802,"Lưu Bị","Lỗ Túc","Không vội, hãy uống rượu trước rồi chúng ta từ từ trò chuyện.","Không vội, hãy uống rượu trước rồi chúng ta từ từ trò chuyện.",},
        [5319] = {10802,"Lỗ Túc","Lưu Bị","Giải quyết xong việc chính rồi uống rượu vẫn chưa muộn.","Giải quyết xong việc chính rồi uống rượu vẫn chưa muộn.",},
        [5320] = {10802,"Gia Cát Lượng","Lỗ Túc","Tử Kính thật không biết điều, cứ chờ người khác phải nói! Chúa công nhà ta là hoàng thúc, tộc đệ của Lưu Cảnh Thăng. Thiên hạ của họ Lưu, chúa công ta không có phần, họ Tôn lại muốn tranh giành hay sao?","Tử Kính thật không biết điều, cứ chờ người khác phải nói! Chúa công nhà ta là hoàng thúc, tộc đệ của Lưu Cảnh Thăng. Thiên hạ của họ Lưu, chúa công ta không có phần, họ Tôn lại muốn tranh giành hay sao?",},
        [5321] = {10802,"Gia Cát Lượng","Lỗ Túc","Trong Xích Bích Chiến, chúa công ta đã bỏ nhiều công sức, các tướng sĩ đều liều mình, đâu chỉ mỗi mình Đông Ngô? Nếu không nhờ ta mượn gió đông nam, Chu Lang liệu có thể lập công không?","Trong Xích Bích Chiến, chúa công ta đã bỏ nhiều công sức, các tướng sĩ đều liều mình, đâu chỉ mỗi mình Đông Ngô? Nếu không nhờ ta mượn gió đông nam, Chu Lang liệu có thể lập công không?",},
        [5322] = {10802,"Gia Cát Lượng","Lỗ Túc","Khi nãy chúa công không trả lời là vì biết Tử Kính là người thông minh, không cần nói rõ, chắc bản thân cũng đã biết được?","Khi nãy chúa công không trả lời là vì biết Tử Kính là người thông minh, không cần nói rõ, chắc bản thân cũng đã biết được?",},
        [5323] = {10803,"Lỗ Túc","Gia Cát Lượng","Lời của Khổng Minh không hợp lý lẽ; chuyện này với Lỗ Túc ta mà nói e rằng không được tiện.","Lời của Khổng Minh không hợp lý lẽ; chuyện này với Lỗ Túc ta mà nói e rằng không được tiện.",},
        [5324] = {10803,"Gia Cát Lượng","Lỗ Túc","Có gì không tiện?","Có gì không tiện?",},
        [5325] = {10803,"Lỗ Túc","Gia Cát Lượng","Trước Lưu Hoàng Thúc gặp nạn, là ta dẫn hai người đến gặp chúa công; sau Chu Công Cẩn đánh Kinh Châu, cũng là ta ngăn cản. Các ngươi từng nói công tử mất sẽ trả Kinh Châu, ta cũng đã đồng ý.","Trước Lưu Hoàng Thúc gặp nạn, là ta dẫn hai người đến gặp chúa công; sau Chu Công Cẩn đánh Kinh Châu, cũng là ta ngăn cản. Các ngươi từng nói công tử mất sẽ trả Kinh Châu, ta cũng đã đồng ý.",},
        [5326] = {10803,"Lỗ Túc","Gia Cát Lượng","Nay các ngươi không làm đúng lời hứa, ta làm sao về phục mệnh? chúa công và Chu Công Cẩn chắc chắn sẽ trị tội ta.","Nay các ngươi không làm đúng lời hứa, ta làm sao về phục mệnh? chúa công và Chu Công Cẩn chắc chắn sẽ trị tội ta.",},
        [5327] = {10803,"Gia Cát Lượng","Lỗ Túc","Vậy để chúa công ta lập văn thư, tạm thời mượn Kinh Châu, đợi chúa công lấy được thành trị khác sẽ giao trả lại cho Đông Ngô, thế nào?","Vậy để chúa công ta lập văn thư, tạm thời mượn Kinh Châu, đợi chúa công lấy được thành trị khác sẽ giao trả lại cho Đông Ngô, thế nào?",},
        [5328] = {10803,"Lỗ Túc","Gia Cát Lượng","Khổng Minh muốn đoạt được nơi nào mới trả Kinh Châu?","Khổng Minh muốn đoạt được nơi nào mới trả Kinh Châu?",},
        [5329] = {10803,"Gia Cát Lượng","Lỗ Túc","Đợi chúa công đoạt được Tây Xuyên thì sẽ hoàn trả.","Đợi chúa công đoạt được Tây Xuyên thì sẽ hoàn trả.",},
        [5330] = {10804,"Gia Cát Lượng","Lỗ Túc","Văn thư ta đã viết xong, cũng đã đóng dấu. Tử Kính tiên sinh cũng hãy đóng dấu, về để Ngô Hầu xem qua.","Văn thư ta đã viết xong, cũng đã đóng dấu. Tử Kính tiên sinh cũng hãy đóng dấu, về để Ngô Hầu xem qua.",},
        [5331] = {10804,"Lỗ Túc","Gia Cát Lượng","Ta biết hoàng thúc là người nhân nghĩa, chắc chắn sẽ không nuốt lời.","Ta biết hoàng thúc là người nhân nghĩa, chắc chắn sẽ không nuốt lời.",},
        [5332] = {10804,"Gia Cát Lượng","Lỗ Túc","Tử Kính về gặp Ngô Hầu, nói rõ thiện ý, nếu không chấp thuận văn thư này, ta trở mặt đoạt cả 81 châu.","Tử Kính về gặp Ngô Hầu, nói rõ thiện ý, nếu không chấp thuận văn thư này, ta trở mặt đoạt cả 81 châu.",},
        [5333] = {10804,"Gia Cát Lượng","Lỗ Túc","Nay chỉ cần hai nhà hòa thuận, Tào tặc không thể cười nhạo chúng ta.","Nay chỉ cần hai nhà hòa thuận, Tào tặc không thể cười nhạo chúng ta.",},
        [5334] = {10805,"Chu Du","Lỗ Túc","Tử Kính đòi Kinh Châu thế nào rồi?","Tử Kính đòi Kinh Châu thế nào rồi?",},
        [5335] = {10805,"Lỗ Túc","Chu Du","Văn thư ở đây.","Văn thư ở đây.",},
        [5336] = {10805,"Chu Du","Lỗ Túc","Tử Kính trúng kế của Gia Cát rồi! Tiếng là mượn đất, thực ra là chiếm đoạt luôn. Cứ nói chiếm được Tây Xuyên sẽ trả, nhưng khi nào mới hành động?","Tử Kính trúng kế của Gia Cát rồi! Tiếng là mượn đất, thực ra là chiếm đoạt luôn. Cứ nói chiếm được Tây Xuyên sẽ trả, nhưng khi nào mới hành động?",},
        [5337] = {10805,"Chu Du","Lỗ Túc","Văn thư này sao đáng tin, ngươi lại tin hắn sao? Nếu hắn không trả, chúa công trách tội thì sao?","Văn thư này sao đáng tin, ngươi lại tin hắn sao? Nếu hắn không trả, chúa công trách tội thì sao?",},
        [5338] = {10805,"Lỗ Túc","Chu Du","Huyền Đức chắc sẽ không gạt ta.","Huyền Đức chắc sẽ không gạt ta.",},
        [5339] = {10805,"Chu Du","Lỗ Túc","Tử Kính là người thành thật, Lưu Bị là kẻ kiêu hùng, Gia Cát Lượng gian xảo, e rằng không nghĩ như ngài.","Tử Kính là người thành thật, Lưu Bị là kẻ kiêu hùng, Gia Cát Lượng gian xảo, e rằng không nghĩ như ngài.",},
        [5340] = {10901,"Gian Tế","Chu Du","Trong Kinh Châu giương cờ vải, ngoài thành xây mộ mới, quân sĩ đều đeo tang.","Trong Kinh Châu giương cờ vải, ngoài thành xây mộ mới, quân sĩ đều đeo tang.",},
        [5341] = {10901,"Chu Du","Gian Tế","Ai mất?","Ai mất?",},
        [5342] = {10901,"Gian Tế","Chu Du","Lưu Huyền Đức mất Cam Phu Nhân, nay sắp xếp đem chôn.","Lưu Huyền Đức mất Cam Phu Nhân, nay sắp xếp đem chôn.",},
        [5343] = {10901,"Chu Du","Gian Tế","Kế ta đã thành: Khiến Lưu Bị giơ tay chịu trói, dễ dàng đoạt lấy Kinh Châu!","Kế ta đã thành: Khiến Lưu Bị giơ tay chịu trói, dễ dàng đoạt lấy Kinh Châu!",},
        [5344] = {10901,"Chu Du","Gian Tế","Nay ta dâng tấu cho chúa công, bảo người đến Kinh Châu làm mai, bảo Lưu Bị đến ở rể rồi bắt nhốt trong ngục, cho người đi lấy Kinh Châu đổi Lưu Bị.","Nay ta dâng tấu cho chúa công, bảo người đến Kinh Châu làm mai, bảo Lưu Bị đến ở rể rồi bắt nhốt trong ngục, cho người đi lấy Kinh Châu đổi Lưu Bị.",},
        [5345] = {10901,"Chu Du","Gian Tế","Đợi hắn giao thành Kinh Châu, ta tự có chủ ý khác.","Đợi hắn giao thành Kinh Châu, ta tự có chủ ý khác.",},
        [5346] = {10902,"Lữ Phạm","Lưu Bị","Gần đây nghe nói hoàng thúc góa vợ, nay có một nơi xứng đáng lắm, nên mạnh dạn sang đây làm mối. Không biết ý hoàng thúc thế nào?","Gần đây nghe nói hoàng thúc góa vợ, nay có một nơi xứng đáng lắm, nên mạnh dạn sang đây làm mối. Không biết ý hoàng thúc thế nào?",},
        [5347] = {10902,"Lưu Bị","Lữ Phạm","Tuổi trung niên mất thê tử, đây là chuyện bất hạnh. Sao có thể vội vàng bàn chuyện hôn nhân?","Tuổi trung niên mất thê tử, đây là chuyện bất hạnh. Sao có thể vội vàng bàn chuyện hôn nhân?",},
        [5348] = {10902,"Lữ Phạm","Lưu Bị","Con người không có thê tử, như nhà không có nóc. chúa công Ngô Hầu có một người em gái, xinh đẹp hiền thục, rất xứng đôi với người.","Con người không có thê tử, như nhà không có nóc. chúa công Ngô Hầu có một người em gái, xinh đẹp hiền thục, rất xứng đôi với người.",},
        [5349] = {10902,"Lữ Phạm","Lưu Bị","Nếu hai nhà kết thân, Tào tặc sẽ không dám dòm ngó đông nạm. Nhưng Thái Ngô Phu Nhân rất yêu thương con gái, không chịu gả đi xa, xin hoàng thúc đến Đông Ngô một chuyến.","Nếu hai nhà kết thân, Tào tặc sẽ không dám dòm ngó đông nạm. Nhưng Thái Ngô Phu Nhân rất yêu thương con gái, không chịu gả đi xa, xin hoàng thúc đến Đông Ngô một chuyến.",},
        [5350] = {10902,"Lưu Bị","Lữ Phạm","Ta đã hơn nửa đời người, tóc cũng đã bạc, em gái Ngô Hầu đang tuổi thanh xuân, e là ta không xứng với người.","Ta đã hơn nửa đời người, tóc cũng đã bạc, em gái Ngô Hầu đang tuổi thanh xuân, e là ta không xứng với người.",},
        [5351] = {10902,"Lữ Phạm","Lưu Bị","Em gái Ngô Hầu tuy là thân gái nhưng tính tình tựa nam nhi. Nay hoàng thúc vang danh khắp nơi, thục nữ xứng với quân tử, tuổi tác đâu quan trọng!","Em gái Ngô Hầu tuy là thân gái nhưng tính tình tựa nam nhi. Nay hoàng thúc vang danh khắp nơi, thục nữ xứng với quân tử, tuổi tác đâu quan trọng!",},
        [5352] = {10902,"Lưu Bị","Lữ Phạm","Xin người hãy ở lại vài ngày rồi về bẩm báo sau.","Xin người hãy ở lại vài ngày rồi về bẩm báo sau.",},
        [5353] = {10903,"Gia Cát Lượng","Lưu Bị","Mục đích họ đến đây ta đã biết. Ta vừa bói một quẻ, đại cát đại lợi. chúa công cứ đồng ý hôn sự này, chọn ngày đi thành hôn.","Mục đích họ đến đây ta đã biết. Ta vừa bói một quẻ, đại cát đại lợi. chúa công cứ đồng ý hôn sự này, chọn ngày đi thành hôn.",},
        [5354] = {10903,"Lưu Bị","Gia Cát Lượng","Chu Du âm mưu hãm hại Lưu Bị, ta sao có thể dấn thân vào nơi nguy hiểm?","Chu Du âm mưu hãm hại Lưu Bị, ta sao có thể dấn thân vào nơi nguy hiểm?",},
        [5355] = {10903,"Gia Cát Lượng","Lưu Bị","Chu Du tuy có kế, nhưng sao qua mặt được Gia Cát Lượng ta!","Chu Du tuy có kế, nhưng sao qua mặt được Gia Cát Lượng ta!",},
        [5356] = {10904,"Tôn Càn","Lưu Bị","Ngô Hầu chờ đợi chúa công đến kết thân.","Ngô Hầu chờ đợi chúa công đến kết thân.",},
        [5357] = {10904,"Lưu Bị","Tôn Càn","E rằng có cạm bẫy, không dám mạo hiểm.","E rằng có cạm bẫy, không dám mạo hiểm.",},
        [5358] = {10904,"Gia Cát Lượng","Lưu Bị","Ta đã có ba kế sách, phải là Tử Long mới có thể làm được.","Ta đã có ba kế sách, phải là Tử Long mới có thể làm được.",},
        [5359] = {10904,"Gia Cát Lượng","Lưu Bị","Ta đưa chúa công đến đất Ngô, được quân sư đưa cho 3 cẩm nang này. Quân sư có dặn, phải lần lượt làm theo từng kế trong đó.","Ta đưa chúa công đến đất Ngô, được quân sư đưa cho 3 cẩm nang này. Quân sư có dặn, phải lần lượt làm theo từng kế trong đó.",},
        [5360] = {10905,"Triệu Vân","","Quân sư dặn 3 diệu kế, theo thứ tự mà làm. Nay đã đến nơi, hẵng mở cẩm nang đầu tiên.","Quân sư dặn 3 diệu kế, theo thứ tự mà làm. Nay đã đến nơi, hẵng mở cẩm nang đầu tiên.",},
        [5361] = {10905,"Triệu Vân","","Nếu vậy, ta sẽ làm ngay.","Nếu vậy, ta sẽ làm ngay.",},
        [5362] = {10906,"Kiều Quốc Lão","Ngô Quốc Thái","Lệnh ái đã gả cho Lưu Huyền Đức làm phu nhân, nay Huyền Đức đã đến, có gì giấu giếm?","Lệnh ái đã gả cho Lưu Huyền Đức làm phu nhân, nay Huyền Đức đã đến, có gì giấu giếm?",},
        [5363] = {10906,"Ngô Quốc Thái","Kiều Quốc Lão","Lão thân không biết việc này.","Lão thân không biết việc này.",},
        [5364] = {10906,"Kiều Quốc Lão","Ngô Quốc Thái","Quả nhiên có việc này. Con rể đã nghỉ ngơi ở dịch trạm, năm trăm quân sĩ đi theo đang mua sắm lễ vật, chuẩn bị thành thân.","Quả nhiên có việc này. Con rể đã nghỉ ngơi ở dịch trạm, năm trăm quân sĩ đi theo đang mua sắm lễ vật, chuẩn bị thành thân.",},
        [5365] = {10906,"Kiều Quốc Lão","Ngô Quốc Thái","Mai mối bên nhà gái là Lữ Phạm, nhà trai là Tôn Càn, cũng ở dịch trạm chờ đợi.","Mai mối bên nhà gái là Lữ Phạm, nhà trai là Tôn Càn, cũng ở dịch trạm chờ đợi.",},
        [5366] = {10906,"Ngô Quốc Thái","Kiều Quốc Lão","Họ dám không nói với ta?","Họ dám không nói với ta?",},
        [5367] = {10907,"Tôn Quyền","Ngô Quốc Thái","Mẫu thân tại sao lại buồn phiền?","Mẫu thân tại sao lại buồn phiền?",},
        [5368] = {10907,"Ngô Quốc Thái","Tôn Quyền","Ngươi dám coi ta không ra gì, tỷ tỷ ta khi lâm nguy đã dặn dò ngươi những gì?","Ngươi dám coi ta không ra gì, tỷ tỷ ta khi lâm nguy đã dặn dò ngươi những gì?",},
        [5369] = {10907,"Tôn Quyền","Ngô Quốc Thái","Mẫu thân có gì xin cứ nói rõ!","Mẫu thân có gì xin cứ nói rõ!",},
        [5370] = {10907,"Ngô Quốc Thái","Tôn Quyền","Trai lớn lấy vợ, gái lớn gả chồng là chuyện bình thường xưa nay. Phàm là con cái phải nghe theo lời mẫu thân. Nay định gả con ta cho Huyền Đức, sao lại giấu ta?","Trai lớn lấy vợ, gái lớn gả chồng là chuyện bình thường xưa nay. Phàm là con cái phải nghe theo lời mẫu thân. Nay định gả con ta cho Huyền Đức, sao lại giấu ta?",},
        [5371] = {10907,"Tôn Quyền","Ngô Quốc Thái","Không phải vậy đâu. Đây là kế của Chu Du, vì muốn lấy Kinh Châu nên mới sắp đặt chuyện này. Chỉ cần dụ và bắt Lưu Bị thì nhất định Lưu Bị sẽ dùng Kinh Châu để đổi, lúc đó Kinh Châu sẽ thuộc về ta.","Không phải vậy đâu. Đây là kế của Chu Du, vì muốn lấy Kinh Châu nên mới sắp đặt chuyện này. Chỉ cần dụ và bắt Lưu Bị thì nhất định Lưu Bị sẽ dùng Kinh Châu để đổi, lúc đó Kinh Châu sẽ thuộc về ta.",},
        [5372] = {10907,"Ngô Quốc Thái","Tôn Quyền","Chu Du là đô đốc của sáu quận tám mươi mốt châu, bản thân không nghĩ ra kế chiếm lấy Kinh Châu, lại muốn dùng con gái ta để lấy Kinh Châu sao!","Chu Du là đô đốc của sáu quận tám mươi mốt châu, bản thân không nghĩ ra kế chiếm lấy Kinh Châu, lại muốn dùng con gái ta để lấy Kinh Châu sao!",},
        [5373] = {10907,"Ngô Quốc Thái","Tôn Quyền","Nếu diệt Lưu Bị, con gái ta lại trở thành góa phụ, sau này còn gả cho ai khác được nữa? Như vậy chẳng phải là lỡ duyên con ta sao!","Nếu diệt Lưu Bị, con gái ta lại trở thành góa phụ, sau này còn gả cho ai khác được nữa? Như vậy chẳng phải là lỡ duyên con ta sao!",},
        [5374] = {10907,"Kiều Quốc Lão","Ngô Quốc Thái","Nếu dùng kế này mà có được Kinh Châu, cũng sẽ bị thiên hạ cười chê. Chuyện này phải làm sao đây!","Nếu dùng kế này mà có được Kinh Châu, cũng sẽ bị thiên hạ cười chê. Chuyện này phải làm sao đây!",},
        [5375] = {10908,"Kiều Quốc Lão","Tôn Quyền","Việc đã như vậy, Lưu hoàng thúc dù sao cũng là người trong Hán Thất, chi bằng nạp làm rể luôn, tránh để mất mặt.","Việc đã như vậy, Lưu hoàng thúc dù sao cũng là người trong Hán Thất, chi bằng nạp làm rể luôn, tránh để mất mặt.",},
        [5376] = {10908,"Tôn Quyền","Kiều Quốc Lão","Tuổi tác e không tương xứng.","Tuổi tác e không tương xứng.",},
        [5377] = {10908,"Kiều Quốc Lão","Tôn Quyền","Lưu hoàng thúc là hào kiệt đương thế, nếu trở thành rể nhà ta, lệnh muội cũng không thiệt thòi gì.","Lưu hoàng thúc là hào kiệt đương thế, nếu trở thành rể nhà ta, lệnh muội cũng không thiệt thòi gì.",},
        [5378] = {10908,"Ngô Quốc Thái","Tôn Quyền","Ta chưa từng gặp Lưu hoàng thúc. Hôm qua hẹn ở Cam Lộ Tự gặp mặt: Nếu không vừa ý ta, tùy các ngươi xử lý, nếu trúng ý ta, ta sẽ gả con gái cho Lưu hoàng thúc!","Ta chưa từng gặp Lưu hoàng thúc. Hôm qua hẹn ở Cam Lộ Tự gặp mặt: Nếu không vừa ý ta, tùy các ngươi xử lý, nếu trúng ý ta, ta sẽ gả con gái cho Lưu hoàng thúc!",},
        [5379] = {10909,"Tôn Càn","Triệu Vân","Sau này Ngô Hầu, Quốc Thái đích thân đến gặp!","Sau này Ngô Hầu, Quốc Thái đích thân đến gặp!",},
        [5380] = {10909,"Triệu Vân","Tôn Càn","Ngày gặp gỡ e rằng lành ít dữ nhiều, tự mang theo theo năm trăm quân lính bảo vệ.","Ngày gặp gỡ e rằng lành ít dữ nhiều, tự mang theo theo năm trăm quân lính bảo vệ.",},
        [5381] = {10910,"Lưu Bị","Ngô Quốc Thái","Lưu Bị bái kiến Ngô Quốc Thái.","Lưu Bị bái kiến Ngô Quốc Thái.",},
        [5382] = {10910,"Ngô Quốc Thái","Lưu Bị","Con rể ta đây sao?","Con rể ta đây sao?",},
        [5383] = {10910,"Kiều Quốc Lão","Lưu Bị","Huyền Đức đúng là một bậc anh tài; lại nhân đức hơn người: Quốc Thái được rể tốt như vậy, đúng là đáng mừng!","Huyền Đức đúng là một bậc anh tài; lại nhân đức hơn người: Quốc Thái được rể tốt như vậy, đúng là đáng mừng!",},
        [5384] = {10910,"Triệu Vân","Lưu Bị","(Lén nói với Lưu Bị) Khi nãy ta tuần tra nhìn thấy trong phòng có Đao Phủ Thủ mai phục, chắc chắn không có ý tốt. Mau nói cho Quốc Thái biết.","(Lén nói với Lưu Bị) Khi nãy ta tuần tra nhìn thấy trong phòng có Đao Phủ Thủ mai phục, chắc chắn không có ý tốt. Mau nói cho Quốc Thái biết.",},
        [5385] = {10910,"Lưu Bị","Ngô Quốc Thái","Nếu muốn diệt Lưu Bị thì cứ ra tay. Cho Đao Phủ Thủ mai phục là có ý gì?","Nếu muốn diệt Lưu Bị thì cứ ra tay. Cho Đao Phủ Thủ mai phục là có ý gì?",},
        [5386] = {10910,"Ngô Quốc Thái","Lưu Bị","(Tức giận) Hỗn láo! Hôm nay Huyền Đức là con rể ta, cũng như con cháu trong nhà. Tại sao lại cho Đao Phủ Thủ mai phục?","(Tức giận) Hỗn láo! Hôm nay Huyền Đức là con rể ta, cũng như con cháu trong nhà. Tại sao lại cho Đao Phủ Thủ mai phục?",},
        [5387] = {10910,"Tôn Quyền","Lữ Phạm","Sao? Ta cũng không rõ... Lữ Phạm, là chủ ý của ngươi sao?","Sao? Ta cũng không rõ... Lữ Phạm, là chủ ý của ngươi sao?",},
        [5388] = {10910,"Lữ Phạm","Giả Hoa","Sao? Ta cũng không biết... Giả Hoa, chuyện này là sao?","Sao? Ta cũng không biết... Giả Hoa, chuyện này là sao?",},
        [5389] = {10910,"Giả Hoa","","……","……",},
        [5390] = {11001,"Lưu Bị","","Nếu Lưu Bị có thể có được Kinh Châu, hoàn thành bá nghiệp, sau nhát kiếm này đá sẽ bổ thành hai. Nếu tử vong nơi đây, kiếm gãy mà đá chẳng hề chi.","Nếu Lưu Bị có thể có được Kinh Châu, hoàn thành bá nghiệp, sau nhát kiếm này đá sẽ bổ thành hai. Nếu tử vong nơi đây, kiếm gãy mà đá chẳng hề chi.",},
        [5391] = {11001,"Tôn Quyền","Lưu Bị","Huyền Đức Công sao lại hận đá này đến thế?","Huyền Đức Công sao lại hận đá này đến thế?",},
        [5392] = {11001,"Lưu Bị","Tôn Quyền","Ta đã năm mươi tuổi vẫn chưa thể vì nước diệt giặc, trong lòng rất phiền muộn. Nay được Quốc Thái chọn làm rể, quả thật là một kỳ ngộ.","Ta đã năm mươi tuổi vẫn chưa thể vì nước diệt giặc, trong lòng rất phiền muộn. Nay được Quốc Thái chọn làm rể, quả thật là một kỳ ngộ.",},
        [5393] = {11001,"Lưu Bị","Tôn Quyền","Vừa nãy khấn trời bói quẻ, nếu diệt được Tào thì chém vỡ hòn đá, may là được như nguyện.","Vừa nãy khấn trời bói quẻ, nếu diệt được Tào thì chém vỡ hòn đá, may là được như nguyện.",},
        [5394] = {11001,"Tôn Quyền","","Ta cũng khấn trời bói quẻ, nếu diệt được tào thì chém vỡ hòn đá này.","Ta cũng khấn trời bói quẻ, nếu diệt được tào thì chém vỡ hòn đá này.",},
        [5395] = {11001,"Tôn Quyền","","(Trong lòng cầu nguyện) Nếu lấy lại được Kinh Châu, hưng vượng Đông Ngô thì chém hòn đá này làm hai mảnh!","(Trong lòng cầu nguyện) Nếu lấy lại được Kinh Châu, hưng vượng Đông Ngô thì chém hòn đá này làm hai mảnh!",},
        [5396] = {11002,"Lưu Bị","Tôn Quyền","Phía nam bơi thuyền, phía bắc cưỡi ngựa, quả nhiên là thật.","Phía nam bơi thuyền, phía bắc cưỡi ngựa, quả nhiên là thật.",},
        [5397] = {11002,"Tôn Quyền","Lưu Bị","Lời này của Lưu Bị là khinh ta không giỏi cưỡi ngựa ư?","Lời này của Lưu Bị là khinh ta không giỏi cưỡi ngựa ư?",},
        [5398] = {11002,"Tôn Quyền","","Ngươi đâu, dắt ngựa ra! Để Lưu Hoàng Thúc xem thử người phía nam có biết cưỡi ngựa khônh?","Ngươi đâu, dắt ngựa ra! Để Lưu Hoàng Thúc xem thử người phía nam có biết cưỡi ngựa khônh?",},
        [5399] = {11002,"Lưu Bị","Tôn Quyền","Ngô Hầu muốn so tài cưỡi ngựa với ta ư! Mời!","Ngô Hầu muốn so tài cưỡi ngựa với ta ư! Mời!",},
        [5400] = {11003,"Nhân vật","Triệu Vân","Trông đại ca và Tôn Quyền khá hòa hợp, không biết khi nào mới có thể dùng cẩm nang mà quân sư đưa cho trước đó.","Trông đại ca và Tôn Quyền khá hòa hợp, không biết khi nào mới có thể dùng cẩm nang mà quân sư đưa cho trước đó.",},
        [5401] = {11003,"Triệu Vân","Nhân vật","Quân sư liệu việc như thần, có thể hóa giải phiền phức lần này đều nhờ vào diệu kế trong cẩm nang.","Quân sư liệu việc như thần, có thể hóa giải phiền phức lần này đều nhờ vào diệu kế trong cẩm nang.",},
        [5402] = {11003,"Triệu Vân","Nhân vật","Quân sư dặn mỗi cẩm nang đều có thời cơ dùng, chúng ta không thể lơ là.","Quân sư dặn mỗi cẩm nang đều có thời cơ dùng, chúng ta không thể lơ là.",},
        [5403] = {11003,"Nhân vật","Triệu Vân","Tử Long nói rất đúng.","Tử Long nói rất đúng.",},
        [5404] = {11101,"Lưu Bị","Kiều Quốc Lão","Nhiều người bên Giang Tả muốn hại Lưu Bị, e là không ở lại đây lâu được.","Nhiều người bên Giang Tả muốn hại Lưu Bị, e là không ở lại đây lâu được.",},
        [5405] = {11101,"Kiều Quốc Lão","Lưu Bị","Huyền Đức yên tâm, ta sẽ báo với Quốc Thái bảo vệ chu toàn.","Huyền Đức yên tâm, ta sẽ báo với Quốc Thái bảo vệ chu toàn.",},
        [5406] = {11101,"Ngô Quốc Thái","Lưu Bị","Con rể của ta, ai dám làm hại! Ngày mai cho mời đến thư viện ở tạm, chọn ngày thành hôn!","Con rể của ta, ai dám làm hại! Ngày mai cho mời đến thư viện ở tạm, chọn ngày thành hôn!",},
        [5407] = {11101,"Lưu Bị","Ngô Quốc Thái","E là Triệu Vân ở ngoài không tiện, quân sĩ sẽ không có ai đôn đốc.","E là Triệu Vân ở ngoài không tiện, quân sĩ sẽ không có ai đôn đốc.",},
        [5408] = {11101,"Ngô Quốc Thái","Lưu Bị","Để bọn họ vào tròng phủ nghỉ ngơi cả đi, tránh ở ngoài trạm dịch lại sinh chuyện.","Để bọn họ vào tròng phủ nghỉ ngơi cả đi, tránh ở ngoài trạm dịch lại sinh chuyện.",},
        [5409] = {11102,"Tiểu Kiều","Lưu Bị","Nghe nói Lưu Hoàng Thúc sắp lấy Tôn Thượng Hương?","Nghe nói Lưu Hoàng Thúc sắp lấy Tôn Thượng Hương?",},
        [5410] = {11102,"Lưu Bị","Tiểu Kiều","Lưu Bị bất tài, may mắn được Ngô Quốc Thái thưởng thức đem gả con gái cho ta.","Lưu Bị bất tài, may mắn được Ngô Quốc Thái thưởng thức đem gả con gái cho ta.",},
        [5411] = {11102,"Tiểu Kiều","Lưu Bị","Thượng Hương tuy là nữ tử nhưng chí hơn nam nhi, muốn lấy cô ấy không phải dễ. Để ta thử sức các ngươi xem!","Thượng Hương tuy là nữ tử nhưng chí hơn nam nhi, muốn lấy cô ấy không phải dễ. Để ta thử sức các ngươi xem!",},
        [5412] = {11102,"Nhân vật","Tiểu Kiều","Được! Lâu rồi không hoạt động chân tay, Tiểu Kiều muội muội phải cẩn thận đấy!","Được! Lâu rồi không hoạt động chân tay, Tiểu Kiều muội muội phải cẩn thận đấy!",},
        [5413] = {11103,"Đại Kiều","Nhân vật","Sao Tiểu Kiều lại để các ngươi qua ải dễ vậy? Nhưng gặp ta thì phải cẩn thận đấy!","Sao Tiểu Kiều lại để các ngươi qua ải dễ vậy? Nhưng gặp ta thì phải cẩn thận đấy!",},
        [5414] = {11103,"Nhân vật","Đại Kiều","Đại Kiều muội muội là tốt nhất, Ngô Quốc Thái cũng đã nhận định đại ca ta làm con rể, muội cứ tác thành cho họ là được!","Đại Kiều muội muội là tốt nhất, Ngô Quốc Thái cũng đã nhận định đại ca ta làm con rể, muội cứ tác thành cho họ là được!",},
        [5415] = {11103,"Đại Kiều","Nhân vật","Thượng Hương muội muội là em gái phu quân ta, phải là anh hùng mới xứng đáng lấy muội ấy.","Thượng Hương muội muội là em gái phu quân ta, phải là anh hùng mới xứng đáng lấy muội ấy.",},
        [5416] = {11103,"Nhân vật","Đại Kiều","Vậy xin Đại Kiều muội muội chỉ giáo!","Vậy xin Đại Kiều muội muội chỉ giáo!",},
        [5417] = {11104,"Nhân vật","Lưu Bị","Chúc mừng đại ca tân hôn!","Chúc mừng đại ca tân hôn!",},
        [5418] = {11104,"Lưu Bị","Thượng Hương","A... Sao trong phòng tân hôn lại có nhiều đao kiếm như thế? Nô tỳ thì toàn đeo kiếm đứng hầu, không lẽ là mai phục?","A... Sao trong phòng tân hôn lại có nhiều đao kiếm như thế? Nô tỳ thì toàn đeo kiếm đứng hầu, không lẽ là mai phục?",},
        [5419] = {11104,"Thị Nữ","Lưu Bị","Quý nhân đừng sợ, phu nhân từ nhỏ thích xem võ, thường lệnh nô tỳ đấu kiếm mua vui nên mới như thế.","Quý nhân đừng sợ, phu nhân từ nhỏ thích xem võ, thường lệnh nô tỳ đấu kiếm mua vui nên mới như thế.",},
        [5420] = {11104,"Lưu Bị","Thượng Hương","Đó không phải là việc phu nhân nên xem, ta có chút sợ hãi, hãy dẹp bỏ hết đi.","Đó không phải là việc phu nhân nên xem, ta có chút sợ hãi, hãy dẹp bỏ hết đi.",},
        [5421] = {11104,"Thượng Hương","Lưu Bị","Chinh chiến nửa đời người mà còn sợ đao kiếm ư!","Chinh chiến nửa đời người mà còn sợ đao kiếm ư!",},
        [5422] = {11104,"Thượng Hương","Lưu Bị","Hãy dẹp bỏ hết vũ khí trong phòng đi.","Hãy dẹp bỏ hết vũ khí trong phòng đi.",},
        [5423] = {11105,"Tôn Quyền","Chu Du","Mẫu thân ta muốn gả muội muội ta cho Lưu Bị. Không ngờ biến giả thành thật. Chuyện này làm sao đây?","Mẫu thân ta muốn gả muội muội ta cho Lưu Bị. Không ngờ biến giả thành thật. Chuyện này làm sao đây?",},
        [5424] = {11105,"Chu Du","Tôn Quyền","Ta thấy chi bằng bắt y trước, sau đó giam lỏng ở trong cung. Ngày ngày cho y uống rượu và vui chơi để làm nhụt chí y.","Ta thấy chi bằng bắt y trước, sau đó giam lỏng ở trong cung. Ngày ngày cho y uống rượu và vui chơi để làm nhụt chí y.",},
        [5425] = {11105,"Chu Du","Tôn Quyền","Cũng có thể dùng kế ly gián y với Quan Vũ, Trương Phi và Gia Cát Lượng, sau đó cho binh tiến đánh từng tên một, như vậy đại sự ắt thành.","Cũng có thể dùng kế ly gián y với Quan Vũ, Trương Phi và Gia Cát Lượng, sau đó cho binh tiến đánh từng tên một, như vậy đại sự ắt thành.",},
        [5426] = {11105,"Trương Chiêu","Tôn Quyền","Kế của Công Cẩn rất hợp ý ta. Lưu Bị tuy là dòng dõi Hán Thất, nhưng chưa từng được hưởng vinh hoa phú quý. Nay ta cho hắn được hưởng thụ, ắt sẽ dần xa rời thuộc hạ, Kinh Châu rồi sẽ về tay ta!","Kế của Công Cẩn rất hợp ý ta. Lưu Bị tuy là dòng dõi Hán Thất, nhưng chưa từng được hưởng vinh hoa phú quý. Nay ta cho hắn được hưởng thụ, ắt sẽ dần xa rời thuộc hạ, Kinh Châu rồi sẽ về tay ta!",},
        [5427] = {11105,"Tôn Quyền","Chu Du","Truyền lệnh xuống sắp xếp Đông Phủ, mời Huyền Đức và muội muội đến ở. Ban cho các cô nương ca múa và vàng bạc châu báu.","Truyền lệnh xuống sắp xếp Đông Phủ, mời Huyền Đức và muội muội đến ở. Ban cho các cô nương ca múa và vàng bạc châu báu.",},
        [5428] = {11201,"Triệu Vân","","Khổng Minh đưa ba cẩm nang cho ta, dặn đến Nam Từ thì mở cái thứ nhất, ở đến cuối năm thì mở cái thứ hai, lúc gặp nguy cấp thì mở cái thứ ba.","Khổng Minh đưa ba cẩm nang cho ta, dặn đến Nam Từ thì mở cái thứ nhất, ở đến cuối năm thì mở cái thứ hai, lúc gặp nguy cấp thì mở cái thứ ba.",},
        [5429] = {11201,"Triệu Vân","","Nay sắp hết năm, chúa công vẫn ham mê nữ sắc chưa từng ra gặp mặt, sao không mở cẩm nang thứ hai xem thế nào?","Nay sắp hết năm, chúa công vẫn ham mê nữ sắc chưa từng ra gặp mặt, sao không mở cẩm nang thứ hai xem thế nào?",},
        [5430] = {11202,"Triệu Vân","Lưu Bị","Hôm nay Khổng Minh cho người đến báo, nói Tào Tháo muốn báo thù trận Xích Bích nên thống lĩnh năm mươi vạn quân tiến đánh Kinh Châu, muốn mời chúa công trở về.","Hôm nay Khổng Minh cho người đến báo, nói Tào Tháo muốn báo thù trận Xích Bích nên thống lĩnh năm mươi vạn quân tiến đánh Kinh Châu, muốn mời chúa công trở về.",},
        [5431] = {11202,"Lưu Bị","Triệu Vân","Phải bàn bạc với phu nhân.","Phải bàn bạc với phu nhân.",},
        [5432] = {11202,"Triệu Vân","Lưu Bị","Nếu bàn bạc với phu nhân thì người sẽ không để chúa công trở về. Thà rằng đừng nói, tối nay lên đường ngay, để chậm trễ sẽ lỡ việc!","Nếu bàn bạc với phu nhân thì người sẽ không để chúa công trở về. Thà rằng đừng nói, tối nay lên đường ngay, để chậm trễ sẽ lỡ việc!",},
        [5433] = {11202,"Lưu Bị","Triệu Vân","Hãy lui ra trước đi, ta làm vậy có lý của ta!","Hãy lui ra trước đi, ta làm vậy có lý của ta!",},
        [5434] = {11202,"Triệu Vân","Lưu Bị","Chúa công phải ra quyết định sớm, đừng chậm trễ.","Chúa công phải ra quyết định sớm, đừng chậm trễ.",},
        [5435] = {11203,"Thượng Hương","Lưu Bị","Nam tử hán đại trượng phu có gì mà buồn phiền vậy?","Nam tử hán đại trượng phu có gì mà buồn phiền vậy?",},
        [5436] = {11203,"Lưu Bị","Thượng Hương","Ta tha hương nơi xứ sở quê người, không thể chăm sóc song thân, bái tế tổ tiên, quả thật là đại nghịch bất hiếu. Nay thấy đã sắp sang năm, trong lòng thương cảm.","Ta tha hương nơi xứ sở quê người, không thể chăm sóc song thân, bái tế tổ tiên, quả thật là đại nghịch bất hiếu. Nay thấy đã sắp sang năm, trong lòng thương cảm.",},
        [5437] = {11203,"Thượng Hương","Lưu Bị","Người đừng giấu ta! Lúc nãy nghe Triệu Tử Long nói là Kinh Châu lâm nguy nên người muốn quay về chứ gì.","Người đừng giấu ta! Lúc nãy nghe Triệu Tử Long nói là Kinh Châu lâm nguy nên người muốn quay về chứ gì.",},
        [5438] = {11203,"Lưu Bị","Thượng Hương","Nếu phu nhân đã biết thì ta cũng không giấu gì. Nếu ta không đi, lỡ như Kinh Châu có gì thì ta sẽ bị thiên hạ cười chê. Nếu đi, ta lại không đành lòng.","Nếu phu nhân đã biết thì ta cũng không giấu gì. Nếu ta không đi, lỡ như Kinh Châu có gì thì ta sẽ bị thiên hạ cười chê. Nếu đi, ta lại không đành lòng.",},
        [5439] = {11203,"Thượng Hương","Lưu Bị","Thiếp đã gả cho người, người đi đâu thiếp theo đấy.","Thiếp đã gả cho người, người đi đâu thiếp theo đấy.",},
        [5440] = {11203,"Lưu Bị","Thượng Hương","Tâm ý phu nhân tuy là như vậy, nhưng Quốc Thái và Ngô Hầu an sao chịu để phu nhân đi? Nếu phu nhân thương xót Lưu Bị thì tạm thời từ biệt đi.","Tâm ý phu nhân tuy là như vậy, nhưng Quốc Thái và Ngô Hầu an sao chịu để phu nhân đi? Nếu phu nhân thương xót Lưu Bị thì tạm thời từ biệt đi.",},
        [5441] = {11203,"Thượng Hương","Lưu Bị","Hay là chúng ta mượn cớ đi bái tế tổ tiên sau đó âm thầm trốn đi, người thấy sao?","Hay là chúng ta mượn cớ đi bái tế tổ tiên sau đó âm thầm trốn đi, người thấy sao?",},
        [5442] = {11203,"Lưu Bị","Thượng Hương","Vậy phải hết sức cẩn thận, đừng cho ai biết.","Vậy phải hết sức cẩn thận, đừng cho ai biết.",},
        [5443] = {11204,"Tôn Quyền","Trương Chiêu","Sao? Lưu Bị dẫn muội muội ta đi rồi?","Sao? Lưu Bị dẫn muội muội ta đi rồi?",},
        [5444] = {11204,"Trương Chiêu","Tôn Quyền","Hôm nay để người này thoát đi, sớm muộn gì cũng thành họa lớn, phải đuổi theo.","Hôm nay để người này thoát đi, sớm muộn gì cũng thành họa lớn, phải đuổi theo.",},
        [5445] = {11204,"Trình Phổ","Tôn Quyền","Chúa công dù có giận dữ cũng vô dụng, ta đoán chắc Trần Võ, Phan Chương sẽ không bắt được bọn họ.","Chúa công dù có giận dữ cũng vô dụng, ta đoán chắc Trần Võ, Phan Chương sẽ không bắt được bọn họ.",},
        [5446] = {11204,"Trình Phổ","Tôn Quyền","Quận chúa thích võ, tính cách nghiêm nghị, các tướng đều sợ hãi. Nay một lòng theo Lưu Bị, tướng đuổi theo nếu gặp quận chúa thì sao xuống tay được?","Quận chúa thích võ, tính cách nghiêm nghị, các tướng đều sợ hãi. Nay một lòng theo Lưu Bị, tướng đuổi theo nếu gặp quận chúa thì sao xuống tay được?",},
        [5447] = {11204,"Tôn Quyền","Trình Phổ","Đây là bội kiếm của ta, hai ngươi hãy đem kiếm này lấy thủ cấp của muội muội và Lưu Bị về đây! Kẻ trái lệnh sẽ bị phạt!","Đây là bội kiếm của ta, hai ngươi hãy đem kiếm này lấy thủ cấp của muội muội và Lưu Bị về đây! Kẻ trái lệnh sẽ bị phạt!",},
        [5448] = {11205,"Từ Thịnh","","Lưu Bị hãy mau xuống ngựa chịu trói! Ta phụng lệnh Chu Đô Đốc đứng đợi ở đây đã lâu!","Lưu Bị hãy mau xuống ngựa chịu trói! Ta phụng lệnh Chu Đô Đốc đứng đợi ở đây đã lâu!",},
        [5449] = {11205,"Lưu Bị","Triệu Vân","Phía trước có lính chặn đường, sau có truy binh, phải làm sao đây?","Phía trước có lính chặn đường, sau có truy binh, phải làm sao đây?",},
        [5450] = {11205,"Triệu Vân","Lưu Bị","Chúa công đừng sợ, quân sư có ba kế hay đều nằm trong ba cẩm nang.","Chúa công đừng sợ, quân sư có ba kế hay đều nằm trong ba cẩm nang.",},
        [5451] = {11205,"Triệu Vân","Lưu Bị","Nay còn cái thứ ba, được dặn dò dùng trong lúc nguy cấp.","Nay còn cái thứ ba, được dặn dò dùng trong lúc nguy cấp.",},
        [5452] = {11206,"Lưu Bị","Thượng Hương","Trước đó Ngô Hầu và Chu Du lập mưu dùng phu nhân làm mồi nhử Lưu Bị ta, muốn cầm tù ta và đoạt Kinh Châu. Nhưng ta không sợ mà còn dám đến là vì biết chắc phu nhân sẽ thương yêu ta.","Trước đó Ngô Hầu và Chu Du lập mưu dùng phu nhân làm mồi nhử Lưu Bị ta, muốn cầm tù ta và đoạt Kinh Châu. Nhưng ta không sợ mà còn dám đến là vì biết chắc phu nhân sẽ thương yêu ta.",},
        [5453] = {11206,"Lưu Bị","Thượng Hương","Hôm qua nghe nói Ngô Hầu muốn hại ta, nên lấy cớ Kinh Châu gặp nạn để trở về. May là phu nhân không từ bỏ mà chọn đồng hành cùng ta.","Hôm qua nghe nói Ngô Hầu muốn hại ta, nên lấy cớ Kinh Châu gặp nạn để trở về. May là phu nhân không từ bỏ mà chọn đồng hành cùng ta.",},
        [5454] = {11206,"Lưu Bị","Thượng Hương","Nay Ngô Hầu lại sai người đuổi theo, Chu Du cũng sai người cản đường, tai họa này chỉ mình phu nhân hóa giải được.","Nay Ngô Hầu lại sai người đuổi theo, Chu Du cũng sai người cản đường, tai họa này chỉ mình phu nhân hóa giải được.",},
        [5455] = {11206,"Thượng Hương","Lưu Bị","Nếu huynh ấy đã không nhận muội muội này thì cũng không cần nhìn mặt nhau nữa! Nguy nan hôm nay ta sẽ tự giải quyết.","Nếu huynh ấy đã không nhận muội muội này thì cũng không cần nhìn mặt nhau nữa! Nguy nan hôm nay ta sẽ tự giải quyết.",},
        [5456] = {11207,"Thượng Hương","Từ Thịnh","Hai người các ngươi muốn tạo phản ư?","Hai người các ngươi muốn tạo phản ư?",},
        [5457] = {11207,"Từ Thịnh","Thượng Hương","Không dám! Phụng lệnh Chu đô đốc, đóng quân ở đây chờ Lưu Bị.","Không dám! Phụng lệnh Chu đô đốc, đóng quân ở đây chờ Lưu Bị.",},
        [5458] = {11207,"Thượng Hương","Từ Thịnh","Nghịch tặc Chu Du! Đông Ngô ta chưa từng tệ bạc ngươi! Huyền Đức là hoàng thúc Đại Hán, là trượng phu của ta. Ta đã nói với mẫu thân, ca ca là trở về Kinh Châu rồi.","Nghịch tặc Chu Du! Đông Ngô ta chưa từng tệ bạc ngươi! Huyền Đức là hoàng thúc Đại Hán, là trượng phu của ta. Ta đã nói với mẫu thân, ca ca là trở về Kinh Châu rồi.",},
        [5459] = {11207,"Thượng Hương","Từ Thịnh","Nay các ngươi ở chân núi chặn đường là muốn cướp đoạt tài sản của phu thê ta ư?","Nay các ngươi ở chân núi chặn đường là muốn cướp đoạt tài sản của phu thê ta ư?",},
        [5460] = {11207,"Từ Thịnh","Thượng Hương","Phu nhân bớt giận, chuyện này không liên quan đến ta, là lệnh của Chu đô đốc.","Phu nhân bớt giận, chuyện này không liên quan đến ta, là lệnh của Chu đô đốc.",},
        [5461] = {11207,"Thượng Hương","Từ Thịnh","Ngươi chỉ sợ Chu Du, nhưng không sợ ta ư? Chu Du diệt được ngươi, ta không diệt được Chu Du ư?","Ngươi chỉ sợ Chu Du, nhưng không sợ ta ư? Chu Du diệt được ngươi, ta không diệt được Chu Du ư?",},
        [5462] = {11208,"Thượng Hương","Trần Võ","Đám thất phu các ngươi dám ly gián huynh muội ta! Ta đã gả đi, nay trở về theo phu quân thì có gì không đúng.","Đám thất phu các ngươi dám ly gián huynh muội ta! Ta đã gả đi, nay trở về theo phu quân thì có gì không đúng.",},
        [5463] = {11208,"Thượng Hương","Trần Võ","Mẫu thân lệnh phu thê ta trở về Kinh Châu. Dù là ca ca đến cũng phải làm theo lý. Hai người các ngươi ỷ vào binh nhiều muốn sát hại ta ư?","Mẫu thân lệnh phu thê ta trở về Kinh Châu. Dù là ca ca đến cũng phải làm theo lý. Hai người các ngươi ỷ vào binh nhiều muốn sát hại ta ư?",},
        [5464] = {11208,"Trần Võ","Thượng Hương","Họ dù sao cũng là huynh muội, lại có Quốc Thái nói giúp, Ngô Hầu là bậc chí hiếu, sao nỡ cãi lời mẫu thân? Mai này nghĩ lại, lại thành ra lỗi do chúng ta, chi bằng hãy nương tay.","Họ dù sao cũng là huynh muội, lại có Quốc Thái nói giúp, Ngô Hầu là bậc chí hiếu, sao nỡ cãi lời mẫu thân? Mai này nghĩ lại, lại thành ra lỗi do chúng ta, chi bằng hãy nương tay.",},
        [5465] = {11209,"Chu Thái","Từ Thịnh","Các ngươi có gặp Lưu Bị chưa? Sao không bắt lấy?","Các ngươi có gặp Lưu Bị chưa? Sao không bắt lấy?",},
        [5466] = {11209,"Từ Thịnh","Chu Thái","Có Tôn Phu Nhân ở đó, hơn nữa Tôn Phu Nhân lại phụng lệnh của Quốc Thái, chúng ta không dám làm càn.","Có Tôn Phu Nhân ở đó, hơn nữa Tôn Phu Nhân lại phụng lệnh của Quốc Thái, chúng ta không dám làm càn.",},
        [5467] = {11209,"Chu Thái","Từ Thịnh","Chính vì vậy nên Ngô Hầu ban cho thanh kiếm này, ra lệnh diệt em gái người trước, rồi diệt Lưu Bị!","Chính vì vậy nên Ngô Hầu ban cho thanh kiếm này, ra lệnh diệt em gái người trước, rồi diệt Lưu Bị!",},
        [5468] = {11209,"Từ Thịnh","Chu Thái","Vậy chúng ta mau đuổi theo! Họ có bộ binh, không đi nhanh được. Bốn người chúng ta truy đuổi trên bờ, nếu đuổi kịp thì diệt ngay không cần nhiều lời.","Vậy chúng ta mau đuổi theo! Họ có bộ binh, không đi nhanh được. Bốn người chúng ta truy đuổi trên bờ, nếu đuổi kịp thì diệt ngay không cần nhiều lời.",},
        [5469] = {11209,"Lưu Bị","Nhân vật","Nguy rồi, lại có người đuổi đến. Phải làm sao đây?","Nguy rồi, lại có người đuổi đến. Phải làm sao đây?",},
        [5470] = {11209,"Nhân vật","Lưu Bị","Đại ca mau đi trước, nơi này cứ giao cho ta!","Đại ca mau đi trước, nơi này cứ giao cho ta!",},
        [5471] = {11209,"Lưu Bị","Nhân vật","Tứ đệ cẩn thận!","Tứ muội cẩn thận!",},
        [5472] = {11301,"Lưu Bị","","Chạy vội cả ngày, người ngựa đều mệt, nay truy binh lại đuổi đến, thật sự cùng đường rồi!","Chạy vội cả ngày, người ngựa đều mệt, nay truy binh lại đuổi đến, thật sự cùng đường rồi!",},
        [5473] = {11301,"Nhân vật","Lưu Bị","Đại ca đừng lo, ta sẽ đối phó đám truy binh đó, đại ca cứ yên tâm về Kinh Châu.","Đại ca đừng lo, ta sẽ đối phó đám truy binh đó, đại ca cứ yên tâm về Kinh Châu.",},
        [5474] = {11301,"Triệu Vân","","May quá có thuyền rồi! Xin chúa công xuống thuyền ngay!","May quá có thuyền rồi! Xin chúa công xuống thuyền ngay!",},
        [5475] = {11302,"Gia Cát Lượng","","Khổng Minh chờ ở đây đã lâu. Chúa công đến rồi, cho thuyền chạy đi!","Khổng Minh chờ ở đây đã lâu. Chúa công đến rồi, cho thuyền chạy đi!",},
        [5476] = {11302,"Chu Thái","","Lưu Bị đứng lại!","Lưu Bị đứng lại!",},
        [5477] = {11302,"Nhân vật","Chu Thái","Đừng đuổi nữa! Thuyền đã đi xa rồi, đuổi không kịp nữa!","Đừng đuổi nữa! Thuyền đã đi xa rồi, đuổi không kịp nữa!",},
        [5478] = {11302,"Gia Cát Lượng","Chu Thái","Ta đã đoán trước rồi, các ngươi hãy trở về nói với Chu lang sau này đừng dùng mỹ nhân kế nữa.","Ta đã đoán trước rồi, các ngươi hãy trở về nói với Chu lang sau này đừng dùng mỹ nhân kế nữa.",},
        [5479] = {11303,"Chu Du","","Hừ! Cho rằng chúng ta không có thuyền ư? Chiến thuyền Đông Ngô ở đây, các chơi chạy đi đâu?","Hừ! Cho rằng chúng ta không có thuyền ư? Chiến thuyền Đông Ngô ở đây, các chơi chạy đi đâu?",},
        [5480] = {11303,"Gia Cát Lượng","","Chúng ta cập vào bờ bắc, bỏ thuyền lên bờ.","Chúng ta cập vào bờ bắc, bỏ thuyền lên bờ.",},
        [5481] = {11303,"Chu Du","","Chúng ta cũng lên bờ, Lưu Bị đang ở phía trước, mau đuổi theo!","Chúng ta cũng lên bờ, Lưu Bị đang ở phía trước, mau đuổi theo!",},
        [5482] = {11303,"Quan Vũ","Chu Du","Quan Vân Trường ở đây! Chu Lang, có dám đánh không?","Quan Vân Trường ở đây! Chu Lang, có dám đánh không?",},
        [5483] = {11303,"Chu Du","","Hừ, xem như các ngươi giỏi! Rút lui!","Hừ, xem như các ngươi giỏi! Rút lui!",},
        [5484] = {11304,"Nhân vật","","Chu Du tính toán đủ đường, nhưng Gia Cát Lượng vẫn cao tau hơn! Đúng là, Chu Lang diệu kế yên thiên hạ, đã mất phu nhân lại thiệt quân!","Chu Du tính toán đủ đường, nhưng Gia Cát Lượng vẫn cao tau hơn! Đúng là, Chu Lang diệu kế yên thiên hạ, đã mất phu nhân lại thiệt quân!",},
        [5485] = {11304,"Chu Du","","Các ngươi đừng có quá đáng! Chúng ta lên bờ quyết một trận sinh tử đi!","Các ngươi đừng có quá đáng! Chúng ta lên bờ quyết một trận sinh tử đi!",},
        [5486] = {11304,"Hoàng Cái","Chu Du","Đô Đốc bớt giận, cẩn thận vết thương...","Đô Đốc bớt giận, cẩn thận vết thương...",},
        [5487] = {11304,"Chu Du","Hoàng Cái","Kế của ta thất bại, còn mặt mũi gì đi gặp Ngô Hầu! Oa... (Thổ huyết ngất đi)","Kế của ta thất bại, còn mặt mũi gì đi gặp Ngô Hầu! Oa... (Thổ huyết ngất đi)",},
        [5488] = {11401,"Tinh Thái","Nhân vật","Lưu Hoàng Thúc từ Đông Ngô trở về, còn dẫn theo Tôn Phu Nhân, thật vất vả cho chúa công.","Lưu Hoàng Thúc từ Đông Ngô trở về, còn dẫn theo Tôn Phu Nhân, thật vất vả cho chúa công.",},
        [5489] = {11401,"Nhân vật","Tinh Thái","Chuyện nhỏ thôi. Quan trọng là nhân cách của đại ca đã chinh phục được Tôn Phu Nhân.","Chuyện nhỏ thôi. Quan trọng là nhân cách của đại ca đã chinh phục được Tôn Phu Nhân.",},
        [5490] = {11401,"Tinh Thái","Nhân vật","Tiếp theo Đông Ngô hẳn sẽ an phận chút, trận chiến với quân Tào lúc trước đã bị tổn thất không ít.","Tiếp theo Đông Ngô hẳn sẽ an phận chút, trận chiến với quân Tào lúc trước đã bị tổn thất không ít.",},
        [5491] = {11401,"Nhân vật","Tinh Thái","Không biết bên phía Tào Tháo như thế, chúng ta qua đó xem thử đi.","Không biết bên phía Tào Tháo như thế, chúng ta qua đó xem thử đi.",},
        [5492] = {11401,"Tinh Thái","Nhân vật","Hừ, tuy không muốn gặp Tào Tháo lắm, nhưng chúa công đi đâu thì ta sẽ theo đó.","Hừ, tuy không muốn gặp Tào Tháo lắm, nhưng chúa công đi đâu thì ta sẽ theo đó.",},
        [5493] = {11402,"Tào Tháo","Nhân vật","Trận chiến Xích Bích quả thật khiến quân ta tổn thất rất nhiều... Thật sự không cam tâm.","Trận chiến Xích Bích quả thật khiến quân ta tổn thất rất nhiều... Thật sự không cam tâm.",},
        [5494] = {11402,"Nhân vật","Tào Tháo","Mạnh Đức huynh có từng nghĩ qua vì sao Xích Bích Chiến lại thua hai nhà Tôn Lưu không?","Mạnh Đức huynh có từng nghĩ qua vì sao Xích Bích Chiến lại thua hai nhà Tôn Lưu không?",},
        [5495] = {11402,"Tào Tháo","Nhân vật","Tiểu anh hùng có gì chỉ giáo?","Tiểu anh hùng có gì chỉ giáo?",},
        [5496] = {11402,"Nhân vật","Tào Tháo","Tàu thuyền là sở trường của Tôn Ngô, còn quân của Tào huynh không quen thủy chiến, Thiết Liên Hoàn lại khiến việc điều động tàu thuyền gặp khó khăn, đây là lý do thứ nhất.","Tàu thuyền là sở trường của Tôn Ngô, còn quân của Tào huynh không quen thủy chiến, Thiết Liên Hoàn lại khiến việc điều động tàu thuyền gặp khó khăn, đây là lý do thứ nhất.",},
        [5497] = {11402,"Nhân vật","Tào Tháo","Các binh sĩ hành quân từ phía bắc cho đến Trường Giang, vốn đã rất mệt mỏi, lại không quen khí hậu phía nam nên sức chiến đấu bị giảm nhiều, đây là lý do thứ hai.","Các binh sĩ hành quân từ phía bắc cho đến Trường Giang, vốn đã rất mệt mỏi, lại không quen khí hậu phía nam nên sức chiến đấu bị giảm nhiều, đây là lý do thứ hai.",},
        [5498] = {11402,"Tào Tháo","Nhân vật","Những việc này ta đều biết, ta cũng đã ra lệnh tăng cường rèn luyện để chuẩn bị cho trận chiến sau này.","Những việc này ta đều biết, ta cũng đã ra lệnh tăng cường rèn luyện để chuẩn bị cho trận chiến sau này.",},
        [5499] = {11402,"Nhân vật","Tào Tháo","Không phải, vẫn có một lý do rất quan trọng.","Không phải, vẫn có một lý do rất quan trọng.",},
        [5500] = {11402,"Tào Tháo","Nhân vật","Xin lắng nghe.","Xin lắng nghe.",},
        [5501] = {11403,"Nhân vật","Tào Tháo","Binh lực Tào huynh hùng hậu, thống nhất phía bắc, đánh xuống phía nam lại liên tục giành chiến thắng.","Binh lực Tào huynh hùng hậu, thống nhất phía bắc, đánh xuống phía nam lại liên tục giành chiến thắng.",},
        [5502] = {11403,"Nhân vật","Tào Tháo","Cho nên có thể các binh sĩ của Tào huynh đã có chút kiêu căng tự mãn? Cảm thấy bản thân đã vô địch thiên hạ?","Cho nên có thể các binh sĩ của Tào huynh đã có chút kiêu căng tự mãn? Cảm thấy bản thân đã vô địch thiên hạ?",},
        [5503] = {11403,"Tào Tháo","Nhân vật","Cái này...","Cái này...",},
        [5504] = {11403,"Nhân vật","Tào Tháo","Còn Tôn Lưu lại ở trong hoàn cảnh sống còn, nên hành sự cẩn thận, tướng lĩnh đoàn kết, binh sĩ liều mạng.","Còn Tôn Lưu lại ở trong hoàn cảnh sống còn, nên hành sự cẩn thận, tướng lĩnh đoàn kết, binh sĩ liều mạng.",},
        [5505] = {11403,"Tào Tháo","Nhân vật","Tiểu anh hùng nói rất có lý.","Tiểu anh hùng nói rất có lý.",},
        [5506] = {11404,"Tào Tháo","Nhân vật","Vậy đối với tình hình hiện tại, tiểu anh hùng có cao kiến gì không?","Vậy đối với tình hình hiện tại, tiểu anh hùng có cao kiến gì không?",},
        [5507] = {11404,"Nhân vật","Tào Tháo","Hiện giờ thế cuộc thiên hạ đã định, Tào huynh không thể mạo hiểm tiến đánh, mà nên ổn định nội bộ, phát triển mở rộng.","Hiện giờ thế cuộc thiên hạ đã định, Tào huynh không thể mạo hiểm tiến đánh, mà nên ổn định nội bộ, phát triển mở rộng.",},
        [5508] = {11404,"Tào Tháo","Nhân vật","Nói rất đúng, vậy làm thế nào để phát triển mở rộng đây?","Nói rất đúng, vậy làm thế nào để phát triển mở rộng đây?",},
        [5509] = {11404,"Nhân vật","Tào Tháo","Tào huynh nên chiêu nạp hiền sĩ, trọng dụng người tài, như thế mới thật sự tìm được người cùng mưu đồ đại nghiệp.","Tào huynh nên chiêu nạp hiền sĩ, trọng dụng người tài, như thế mới thật sự tìm được người cùng mưu đồ đại nghiệp.",},
        [5510] = {11404,"Tào Tháo","Nhân vật","Ta sẽ thông báo lệnh cầu hiền tài, chiêu nạp nhân tài trong thiên hạ về dưới trướng ta.","Ta sẽ thông báo lệnh cầu hiền tài, chiêu nạp nhân tài trong thiên hạ về dưới trướng ta.",},
        [5511] = {11501,"Tào Tháo","Nhân vật","Đồng Tước Đài đã xây xong, ta sắp đãi tiệc mời quần thần, tiểu anh hùng cũng đến tham gia đi.","Đồng Tước Đài đã xây xong, ta sắp đãi tiệc mời quần thần, tiểu anh hùng cũng đến tham gia đi.",},
        [5512] = {11501,"Nhân vật","Tào Tháo","Được, ta thích nhất là náo nhiệt.","Được, ta thích nhất là náo nhiệt.",},
        [5513] = {11502,"Tào Tháo","Nhân vật","Đồng Tước Đài giáp mặt Chương Hà, trái phải là hai đài cao long phượng, mỗi đài cao mười trượng, phía trên có hai cầu nối liền.","Đồng Tước Đài giáp mặt Chương Hà, trái phải là hai đài cao long phượng, mỗi đài cao mười trượng, phía trên có hai cầu nối liền.",},
        [5514] = {11502,"Nhân vật","Tào Tháo","Quả thật là tuyệt diệu.","Quả thật là tuyệt diệu.",},
        [5515] = {11502,"Nhân vật","Tào Tháo","Món ăn tuy ngon, điệu múa cũng đẹp, nhưng luôn thấy thiếu gì đó, thật muốn hoạt động chân tay một chút.","Món ăn tuy ngon, điệu múa cũng đẹp, nhưng luôn thấy thiếu gì đó, thật muốn hoạt động chân tay một chút.",},
        [5516] = {11502,"Tào Tháo","Nhân vật","Ở đây đều là văn thần võ tướng của ta, tiểu anh hùng muốn so tài với ai đây?","Ở đây đều là văn thần võ tướng của ta, tiểu anh hùng muốn so tài với ai đây?",},
        [5517] = {11502,"Văn Sính","Tào Tháo","Văn Sính sớm đã nghe danh Dương Uy Tướng, xin được chỉ giáo!","Văn Sính sớm đã nghe danh Dương Uy Tướng, xin được chỉ giáo!",},
        [5518] = {11503,"Trương Hợp","Nhân vật","Dương Uy Tướng thân thủ tốt! Trương Hợp cũng đến thỉnh giáo!","Dương Uy Tướng thân thủ tốt! Trương Hợp cũng đến thỉnh giáo!",},
        [5519] = {11503,"Nhân vật","Trương Hợp","Mời!","Mời!",},
        [5520] = {11504,"Hạ Hầu Uyên","Nhân vật","Họ bị Dương Uy Tướng mai phục, ta cũng muốn đến so tài!","Họ bị Dương Uy Tướng mai phục, ta cũng muốn đến so tài!",},
        [5521] = {11504,"Nhân vật","Hạ Hầu Uyên","Đừng khách sáo, cứ xuất chiêu.","Đừng khách sáo, cứ xuất chiêu.",},
        [5522] = {11504,"Hạ Hầu Uyên","Nhân vật","Tướng quân nói đúng, Hạ Hầu Uyên cũng đến!","Tướng quân nói đúng, Hạ Hầu Uyên cũng đến!",},
        [5523] = {11505,"Tuân Úc","Nhân vật","Dương Uy Tướng võ nghệ cao cường, liên thắng nhiều vị đại tướng.","Dương Uy Tướng võ nghệ cao cường, liên thắng nhiều vị đại tướng.",},
        [5524] = {11505,"Tào Tháo","Nhân vật","Ha ha, được! Ta mời tiểu anh hùng một ly!","Ha ha, được! Ta mời tiểu anh hùng một ly!",},
        [5525] = {11505,"Nhân vật","Tào Tháo","Cạn!","Cạn!",},
        [5526] = {11601,"Tào Tháo","","Ở đây có áo gấm, treo trên cành liễu, dưới có bia bắn, ai có thể bắn trúng hồng tâm sẽ được ban áo gấm, bắn hụt sẽ phải phạt rượu.","Ở đây có áo gấm, treo trên cành liễu, dưới có bia bắn, ai có thể bắn trúng hồng tâm sẽ được ban áo gấm, bắn hụt sẽ phải phạt rượu.",},
        [5527] = {11601,"Tào Hưu","","Bắn! (Bắn trúng hồng tâm)","Bắn! (Bắn trúng hồng tâm)",},
        [5528] = {11601,"Tào Tháo","","Được! Tiễn pháp tốt!","Được! Tiễn pháp tốt!",},
        [5529] = {11601,"Tào Hưu","","Cẩm bào là của ta rồi!","Cẩm bào là của ta rồi!",},
        [5530] = {11601,"Văn Sính","","Cẩm bào thừa tướng, phải ưu tiên người ngoài. Xem ra đây! (Bắn trúng hồng tâm)","Cẩm bào thừa tướng, phải ưu tiên người ngoài. Xem ra đây! (Bắn trúng hồng tâm)",},
        [5531] = {11602,"Văn Sính","","Mau đưa áo bào cho ta!","Mau đưa áo bào cho ta!",},
        [5532] = {11602,"Tào Hồng","Văn Sính","Văn Liệt bắn trúng, sao người giành được? Xem ta đây! (Bắn trúng hồng tâm)","Văn Liệt bắn trúng, sao người giành được? Xem ta đây! (Bắn trúng hồng tâm)",},
        [5533] = {11602,"Tào Hồng","","Ta là người xứng đáng.","Ta là người xứng đáng.",},
        [5534] = {11602,"Trương Hợp","Tào Hồng","Khoan đã! Các ngươi bắn chưa tài! Xem ta đây! (Xoay người, bắn trúng hồng tâm)","Khoan đã! Các ngươi bắn chưa tài! Xem ta đây! (Xoay người, bắn trúng hồng tâm)",},
        [5535] = {11602,"Trương Hợp","","Cẩm bào sẽ thuộc về ta!","Cẩm bào sẽ thuộc về ta!",},
        [5536] = {11603,"Hạ Hầu Uyên","Trương Hợp","Ngươi xoay người bắn tên có gì đâu mà lạ! Xem ta đây! (Một phát trúng ngay giữa 4 mũi tên)","Ngươi xoay người bắn tên có gì đâu mà lạ! Xem ta đây! (Một phát trúng ngay giữa 4 mũi tên)",},
        [5537] = {11603,"Hạ Hầu Uyên","","Mũi tên này có được áo gấm không?","Mũi tên này có được áo gấm không?",},
        [5538] = {11603,"Từ Hoảng","Hạ Hầu Uyên","Hãy để áo gấm ấy cho Từ Hoảng!","Hãy để áo gấm ấy cho Từ Hoảng!",},
        [5539] = {11603,"Hạ Hầu Uyên","","Ngươi có cách bắn gì mà giành áo bào?","Ngươi có cách bắn gì mà giành áo bào?",},
        [5540] = {11603,"Từ Hoảng","","Ta không bắn cung, ta chỉ lấy áo thôi. (Bắn rơi áo bào)","Ta không bắn cung, ta chỉ lấy áo thôi. (Bắn rơi áo bào)",},
        [5541] = {11604,"Tào Tháo","","(Cười lớn) Được rồi, các ngươi tranh giành đến đỏ mặt tía tai. Ta ban mỗi người một thước gấm Thục nhé?","(Cười lớn) Được rồi, các ngươi tranh giành đến đỏ mặt tía tai. Ta ban mỗi người một thước gấm Thục nhé?",},
        [5542] = {11604,"Nhân vật","Tào Tháo","Tướng thì lấy ngựa bắn cung, uy dũng như thế đủ rồi. Các ngươi đều là những người hay chữ, sao không ngâm thơ để ghi nhận thắng cảnh này?","Tướng thì lấy ngựa bắn cung, uy dũng như thế đủ rồi. Các ngươi đều là những người hay chữ, sao không ngâm thơ để ghi nhận thắng cảnh này?",},
        [5543] = {11604,"Tào Thực","","Khoan thai theo nhịp bước, lên đài ngắm cảnh vui. Thấy trời cao muôn dặm, thấy đất nặng ân tình.","Khoan thai theo nhịp bước, lên đài ngắm cảnh vui. Thấy trời cao muôn dặm, thấy đất nặng ân tình.",},
        [5544] = {11604,"Tào Thực","","...Phò tá hoàng đế, bình định tứ phương. Thiên địa thuận hòa, tỏa sáng huy hoàng.","...Phò tá hoàng đế, bình định tứ phương. Thiên địa thuận hòa, tỏa sáng huy hoàng.",},
        [5545] = {11604,"Tào Tháo","","Được! Tử Kiến quả nhiên là con ta, chắc sẽ làm nên chuyện lớn!","Được! Tử Kiến quả nhiên là con ta, chắc sẽ làm nên chuyện lớn!",},
        [5546] = {11701,"Hoa Hâm","Tào Tháo","Đông Ngô cử ta đến báo với Tào thừa tướng, Lưu Bị chiếm Kinh Châu, Tôn Quyền gả em gái cho Lưu Bị.","Đông Ngô cử ta đến báo với Tào thừa tướng, Lưu Bị chiếm Kinh Châu, Tôn Quyền gả em gái cho Lưu Bị.",},
        [5547] = {11701,"Tào Tháo","Hoa Hâm","Sao? Lại đế Lưu Bị chiếm được Kinh Châu, còn liên hôn với Tôn Quyền? Định chống lại ta?","Sao? Lại đế Lưu Bị chiếm được Kinh Châu, còn liên hôn với Tôn Quyền? Định chống lại ta?",},
        [5548] = {11701,"Trình Dục","Tào Tháo","Thừa tướng trong vạn quân, nay nghe Lưu Bị chiếm Kinh Châu, sao lại bất ngờ như thế?","Thừa tướng trong vạn quân, nay nghe Lưu Bị chiếm Kinh Châu, sao lại bất ngờ như thế?",},
        [5549] = {11701,"Tào Tháo","Trình Dục","Lưu Bị là bậc tài năng, nay được Kinh Châu, khác gì rồng mắc cạn được vào biển lớn!","Lưu Bị là bậc tài năng, nay được Kinh Châu, khác gì rồng mắc cạn được vào biển lớn!",},
        [5550] = {11702,"Trình Dục","Tào Tháo","Thừa tướng có biết mục đích của Hoa Hâm?","Thừa tướng có biết mục đích của Hoa Hâm?",},
        [5551] = {11702,"Tào Tháo","Trình Dục","Không lẽ là cái bẫy?","Không lẽ là cái bẫy?",},
        [5552] = {11702,"Trình Dục","Tào Tháo","Tôn Quyền vốn ghét Lưu Bị, muốn cất quân đến đánh, lại sợ thừa tướng thừa cơ đến báo thù, nên dâng biểu tiến cử Lưu Bị để Bị yên lòng và để thừa tướng đừng mong mỏi gì nữa.","Tôn Quyền vốn ghét Lưu Bị, muốn cất quân đến đánh, lại sợ thừa tướng thừa cơ đến báo thù, nên dâng biểu tiến cử Lưu Bị để Bị yên lòng và để thừa tướng đừng mong mỏi gì nữa.",},
        [5553] = {11702,"Tào Tháo","Trình Dục","Nói chí phải.","Nói chí phải.",},
        [5554] = {11702,"Trình Dục","Tào Tháo","Ta có một kế, khiến cho Tôn, Lưu xâu xé lẫn nhau, thừa tướng sẽ thừa cơ đánh phá, chỉ một trận là diệt được cả hai tên.","Ta có một kế, khiến cho Tôn, Lưu xâu xé lẫn nhau, thừa tướng sẽ thừa cơ đánh phá, chỉ một trận là diệt được cả hai tên.",},
        [5555] = {11703,"Tào Tháo","Trình Dục","Trọng Đức có cách nào không?","Trọng Đức có cách nào không?",},
        [5556] = {11703,"Trình Dục","Tào Tháo","Thừa tướng nên tâu phong Chu Du làm Nam Quận Thái Thú, Trình Phổ là Giang Hạ Thái Thú, giữ Hoa Hâm làm quan triều đình;","Thừa tướng nên tâu phong Chu Du làm Nam Quận Thái Thú, Trình Phổ là Giang Hạ Thái Thú, giữ Hoa Hâm làm quan triều đình;",},
        [5557] = {11703,"Trình Dục","Tào Tháo","Du có thù với Lưu Bị. Ta thừa cơ hai bên xâu xé mà đánh thì có hay hơn không?","Du có thù với Lưu Bị. Ta thừa cơ hai bên xâu xé mà đánh thì có hay hơn không?",},
        [5558] = {11703,"Tào Tháo","Trình Dục","Lời Trọng Đức hợp ý ta lắm.","Lời Trọng Đức hợp ý ta lắm.",},
        [5559] = {11704,"Tinh Thái","Nhân vật","Chúa công! Nghe nói Trình Dục đề nghị với Tào Tháo, ly gián quan hệ Lưu Hoàng Thúc và Tôn Quyền.","Chúa công! Nghe nói Trình Dục đề nghị với Tào Tháo, ly gián quan hệ Lưu Hoàng Thúc và Tôn Quyền.",},
        [5560] = {11704,"Nhân vật","Tinh Thái","Ta cũng nghe nói, mau về Kinh Châu báo với đại ca.","Ta cũng nghe nói, mau về Kinh Châu báo với đại ca.",},
        [5561] = {11704,"Tinh Thái","Nhân vật","Tốt quá, cuối cùng có thể về nhà rồi.","Tốt quá, cuối cùng có thể về nhà rồi.",},
        [5562] = {11801,"Tôn Quyền","Lỗ Túc","Ngươi cho Lưu Bị mượn Kinh Châu, nay hắn dây dưa không trả, đợi đến bao giờ?","Ngươi cho Lưu Bị mượn Kinh Châu, nay hắn dây dưa không trả, đợi đến bao giờ?",},
        [5563] = {11801,"Lỗ Túc","Tôn Quyền","Văn thư viết rõ, có được Tây Xuyên sẽ trả!","Văn thư viết rõ, có được Tây Xuyên sẽ trả!",},
        [5564] = {11801,"Tôn Quyền","Lỗ Túc","Cứ nói chiếm Tây Xuyên, mãi chưa dấy binh, đợi đến già à?","Cứ nói chiếm Tây Xuyên, mãi chưa dấy binh, đợi đến già à?",},
        [5565] = {11801,"Lỗ Túc","Tôn Quyền","Vậy ta xin sang đòi.","Vậy ta xin sang đòi.",},
        [5566] = {11802,"Lỗ Túc","Lưu Bị","Ta nghe lệnh Ngô Hầu đến vì chuyện của Kinh Châu. Hoàng thúc mượn đã lâu, đã đến lúc trả lại rồi.","Ta nghe lệnh Ngô Hầu đến vì chuyện của Kinh Châu. Hoàng thúc mượn đã lâu, đã đến lúc trả lại rồi.",},
        [5567] = {11802,"Lưu Bị","","Cái này... Hu hu...","Cái này... Hu hu...",},
        [5568] = {11802,"Lỗ Túc","Lưu Bị","Hoàng thúc sao lại khóc?","Hoàng thúc sao lại khóc?",},
        [5569] = {11802,"Gia Cát Lượng","Lỗ Túc","Ban đầu nói chiếm được Tây Xuyên sẽ trả. Giờ nghĩ lại Ích Châu Lưu Chương là đệ của chủ nhân, nếu chiếm chắc sẽ bị mắng;","Ban đầu nói chiếm được Tây Xuyên sẽ trả. Giờ nghĩ lại Ích Châu Lưu Chương là đệ của chủ nhân, nếu chiếm chắc sẽ bị mắng;",},
        [5570] = {11802,"Gia Cát Lượng","Lỗ Túc","Ích Châu đã không lấy được mà lại phải trả Kinh Châu thì còn chỗ nào mà ở? Việc thực khó nghĩ, bởi thế mới đau lòng mà khóc.","Ích Châu đã không lấy được mà lại phải trả Kinh Châu thì còn chỗ nào mà ở? Việc thực khó nghĩ, bởi thế mới đau lòng mà khóc.",},
        [5571] = {11802,"Lỗ Túc","Gia Cát Lượng","Hoàng thúc chớ nên phiền não, hãy bàn với Khổng Minh thế nào cho phải thì thôi!","Hoàng thúc chớ nên phiền não, hãy bàn với Khổng Minh thế nào cho phải thì thôi!",},
        [5572] = {11802,"Gia Cát Lượng","Lỗ Túc","Nhờ Tử Kính về bày tỏ với Ngô hầu, xin cho khoan hạn ít lâu nữa.","Nhờ Tử Kính về bày tỏ với Ngô hầu, xin cho khoan hạn ít lâu nữa.",},
        [5573] = {11803,"Chu Du","Lỗ Túc","Tử Kính lại mắc lừa Gia Cát Lượng rồi! Khi xưa Lưu Bị ở nhờ Lưu Biểu còn muốn cướp Kinh Châu, huống chi đối với Lưu Chương ở Tây Xuyên?","Tử Kính lại mắc lừa Gia Cát Lượng rồi! Khi xưa Lưu Bị ở nhờ Lưu Biểu còn muốn cướp Kinh Châu, huống chi đối với Lưu Chương ở Tây Xuyên?",},
        [5574] = {11803,"Chu Du","Lỗ Túc","Tử Kính cứ về Kinh Châu, bảo với Lưu Bị: Tôn, Lưu hai nhà đã kết thân với nhau, thì cũng như một nhà. ","Tử Kính cứ về Kinh Châu, bảo với Lưu Bị: Tôn, Lưu hai nhà đã kết thân với nhau, thì cũng như một nhà. ",},
        [5575] = {11803,"Chu Du","Lỗ Túc","Hãy để Đông Ngô khởi quân lấy hộ Tây Xuyên. Sau khi lấy được, hoàng thúc phải trả Kinh Châu cho Đông Ngô","Hãy để Đông Ngô khởi quân lấy hộ Tây Xuyên. Sau khi lấy được, hoàng thúc phải trả Kinh Châu cho Đông Ngô",},
        [5576] = {11803,"Lỗ Túc","Chu Du","Tây Xuyên xa xôi, chưa dễ lấy được, kế ấy của đô đốc, có lẽ không thành?","Tây Xuyên xa xôi, chưa dễ lấy được, kế ấy của đô đốc, có lẽ không thành?",},
        [5577] = {11803,"Chu Du","Lỗ Túc","Ta mượn tiếng lấy Tây Xuyên, kỳ thực là sang lấy Kinh Châu, để hắn không đề phòng trước.","Ta mượn tiếng lấy Tây Xuyên, kỳ thực là sang lấy Kinh Châu, để hắn không đề phòng trước.",},
        [5578] = {11804,"Lỗ Túc","Chu Du","Ta đến Kinh Châu nói với họ, Lưu Bị và Gia Cát Lượng mừng rỡ chuẩn bị xuất thành.","Ta đến Kinh Châu nói với họ, Lưu Bị và Gia Cát Lượng mừng rỡ chuẩn bị xuất thành.",},
        [5579] = {11804,"Chu Du","Lỗ Túc","Hóa ra Khổng Minh cũng có ngày trúng kế! Kinh Châu có người tiếp ứng?","Hóa ra Khổng Minh cũng có ngày trúng kế! Kinh Châu có người tiếp ứng?",},
        [5580] = {11804,"My Trúc","Chu Du","Lưu Hoàng Thúc sai My Trúc đến gặp đô đốc. chúa công đợi ở ngoài thành Kinh Châu.","Lưu Hoàng Thúc sai My Trúc đến gặp đô đốc. chúa công đợi ở ngoài thành Kinh Châu.",},
        [5581] = {11804,"Chu Du","My Trúc","Việc nhà của ngươi, xuất binh viễn chinh, khao quân không dễ dàng.","Việc nhà của ngươi, xuất binh viễn chinh, khao quân không dễ dàng.",},
        [5582] = {11805,"Chu Du","Triệu Vân","Quân ta đã đến Kinh Châu, sao không thấy tiếp ứng?","Quân ta đã đến Kinh Châu, sao không thấy tiếp ứng?",},
        [5583] = {11805,"Triệu Vân","Chu Du","Các tướng nghe lệnh, phòng thủ!","Các tướng nghe lệnh, phòng thủ!",},
        [5584] = {11805,"Chu Du","Triệu Vân","Ta giúp ngươi chiếm Tây Xuyên, các ngươi đang làm gì đây?","Ta giúp ngươi chiếm Tây Xuyên, các ngươi đang làm gì đây?",},
        [5585] = {11805,"Triệu Vân","Chu Du","Khổng Minh quân sư đã biết kế của đô đốc, nên giữ Triệu Vân ở lại.","Khổng Minh quân sư đã biết kế của đô đốc, nên giữ Triệu Vân ở lại.",},
        [5586] = {11805,"Triệu Vân","Chu Du","Chúa công nói người và Lưu Chương đều là tôn thần nhà Hán cả, sao nỡ bội nghĩa mà cướp lấy Tây Xuyên?","Chúa công nói người và Lưu Chương đều là tôn thần nhà Hán cả, sao nỡ bội nghĩa mà cướp lấy Tây Xuyên?",},
        [5587] = {11805,"Chu Du","","Hừ! Rút!","Hừ! Rút!",},
        [5588] = {11806,"Chu Du","","Lưu Bị, Gia Cát Lượng, các ngươi đúng là quá đáng! Ngươi cho là ta không đoạt nổi Tây Xuyên? Ta quyết đoạt bằng được!","Lưu Bị, Gia Cát Lượng, các ngươi đúng là quá đáng! Ngươi cho là ta không đoạt nổi Tây Xuyên? Ta quyết đoạt bằng được!",},
        [5589] = {11806,"Chu Du Tùy Tùng","Chu Du","Báo đô đốc, Gia Cát Lượng có thư.","Báo đô đốc, Gia Cát Lượng có thư.",},
        [5590] = {11806,"Chu Du","","Xem thử hắn nói gì. ...Tây Xuyên thiết nghĩ không nên. Ích Châu, dân mạnh đất hiểm...","Xem thử hắn nói gì. ...Tây Xuyên thiết nghĩ không nên. Ích Châu, dân mạnh đất hiểm...",},
        [5591] = {11806,"Chu Du","","Nay ngài cất quân phương xa, nếu Tào Tháo thừa cơ đến đánh, Giang Nam ắt sẽ ra tro! Ta không nỡ ngồi nhìn mà không báo trước, xin ngài soi xét. Thật quá đáng!","Nay ngài cất quân phương xa, nếu Tào Tháo thừa cơ đến đánh, Giang Nam ắt sẽ ra tro! Ta không nỡ ngồi nhìn mà không báo trước, xin ngài soi xét. Thật quá đáng!",},
        [5592] = {11806,"Chu Du","","Phụt... (Tức giận) Ta không phải không muốn báo nước, nhưng số mệnh ta đến đây thôi.","Phụt... (Tức giận) Ta không phải không muốn báo nước, nhưng số mệnh ta đến đây thôi.",},
        [5593] = {11806,"Chu Du","","Trời sinh Du sao còn sinh Lượng! (Than liền mấy hơi rồi mất)","Trời sinh Du sao còn sinh Lượng! (Than liền mấy hơi rồi mất)",},
        [5594] = {11901,"Tôn Quyền","","Công Cẩn có tài vương tá, chẳng may mất sớm thế này, ta còn trông cậy vào ai? (Khóc)","Công Cẩn có tài vương tá, chẳng may mất sớm thế này, ta còn trông cậy vào ai? (Khóc)",},
        [5595] = {11901,"Lỗ Túc","Tôn Quyền","Chúa công đừng buồn. Ta sẽ hết lòng phò tá chúa công.","Chúa công đừng buồn. Ta sẽ hết lòng phò tá chúa công.",},
        [5596] = {11901,"Tôn Quyền","Lỗ Túc","Nay phong Lỗ Túc làm đại đô đốc, thống lĩnh cả binh mã.","Nay phong Lỗ Túc làm đại đô đốc, thống lĩnh cả binh mã.",},
        [5597] = {11902,"Gia Cát Lượng","Lưu Bị","Ta xem thiên văn, sao rơi xuống đất, Chu Du đã mất.","Ta xem thiên văn, sao rơi xuống đất, Chu Du đã mất.",},
        [5598] = {11902,"Lưu Bị","Gia Cát Lượng","Chu Du mất rồi, giờ phải làm sao?","Chu Du mất rồi, giờ phải làm sao?",},
        [5599] = {11902,"Gia Cát Lượng","Lưu Bị","Lỗ Túc sẽ thay Chu Du thống lĩnh binh quyền. Ta sang Giang Nam một chuyến mượn cớ viếng tang để tìm hiền sĩ về giúp chúa công.","Lỗ Túc sẽ thay Chu Du thống lĩnh binh quyền. Ta sang Giang Nam một chuyến mượn cớ viếng tang để tìm hiền sĩ về giúp chúa công.",},
        [5600] = {11902,"Lưu Bị","Gia Cát Lượng","Chỉ ngại các tướng sĩ Đông Ngô muốn hại tiên sinh!","Chỉ ngại các tướng sĩ Đông Ngô muốn hại tiên sinh!",},
        [5601] = {11902,"Gia Cát Lượng","Lưu Bị","Khi Chu Du còn sống, ta còn chẳng ngại, giờ hắn đã mất, còn gì lo nữa?","Khi Chu Du còn sống, ta còn chẳng ngại, giờ hắn đã mất, còn gì lo nữa?",},
        [5602] = {11903,"Chu Du Bộ Tướng","","Gia Cát Lượng còn dám đến chia buồn? Ta sẽ báo thù cho đô đốc!","Gia Cát Lượng còn dám đến chia buồn? Ta sẽ báo thù cho đô đốc!",},
        [5603] = {11903,"Lỗ Túc","","Gia Cát Lượng đến Đông Ngô sẽ có Triệu Vân đi cùng, đừng manh động.","Gia Cát Lượng đến Đông Ngô sẽ có Triệu Vân đi cùng, đừng manh động.",},
        [5604] = {11903,"Gia Cát Lượng","","(Khóc nấc) Than ôi Công Cẩn, chẳng may trời hại! Thọ yểu số trời, ai không ái ngại?","(Khóc nấc) Than ôi Công Cẩn, chẳng may trời hại! Thọ yểu số trời, ai không ái ngại?",},
        [5605] = {11903,"Gia Cát Lượng","","Lòng thì trung nghĩa, hồn thì anh linh, mới ba chục tuổi, nghìn thu nêu danh!","Lòng thì trung nghĩa, hồn thì anh linh, mới ba chục tuổi, nghìn thu nêu danh!",},
        [5606] = {11903,"Gia Cát Lượng","","Hồn có khôn thiêng, soi thấy can tâm. Từ nay thiên hạ, ai kẻ tri âm?","Hồn có khôn thiêng, soi thấy can tâm. Từ nay thiên hạ, ai kẻ tri âm?",},
        [5607] = {11903,"Lỗ Túc","","Khổng Minh vẫn là người tử tế, chỉ là Công Cẩn hẹp hòi, tự rước họa mà thôi.","Khổng Minh vẫn là người tử tế, chỉ là Công Cẩn hẹp hòi, tự rước họa mà thôi.",},
        [5608] = {11904,"Bàng Thống","Gia Cát Lượng","Ngươi còn dám đến viếng tang, rõ ràng coi thường Đông Ngô?","Ngươi còn dám đến viếng tang, rõ ràng coi thường Đông Ngô?",},
        [5609] = {11904,"Gia Cát Lượng","Bàng Thống","Ha ha... Đừng nói như thế.","Ha ha... Đừng nói như thế.",},
        [5610] = {11904,"Bàng Thống","Gia Cát Lượng","Lâu quá không gặp, chúng ta tâm sự nào.","Lâu quá không gặp, chúng ta tâm sự nào.",},
        [5611] = {11904,"Gia Cát Lượng","Bàng Thống","Ta nghĩ Tôn Quyền sẽ không trọng dụng các hạ đâu. Nếu có điều gì không như ý, hãy sang Kinh Châu, cùng ta phò trợ Huyền Đức.","Ta nghĩ Tôn Quyền sẽ không trọng dụng các hạ đâu. Nếu có điều gì không như ý, hãy sang Kinh Châu, cùng ta phò trợ Huyền Đức.",},
        [5612] = {12001,"Lỗ Túc","Tôn Quyền","Ta tài nhỏ trí mọn, Công Cẩn tiến cử nhầm, ta xin cử một người giúp chúa công;","Ta tài nhỏ trí mọn, Công Cẩn tiến cử nhầm, ta xin cử một người giúp chúa công;",},
        [5613] = {12001,"Lỗ Túc","Tôn Quyền","Mưu lược hơn người. Công Cẩn từng nhiều lần dùng kế của người này, mà Khổng Minh cũng phục tài hắn, hiện đang ở Giang Nam, sao không trọng dụng!","Mưu lược hơn người. Công Cẩn từng nhiều lần dùng kế của người này, mà Khổng Minh cũng phục tài hắn, hiện đang ở Giang Nam, sao không trọng dụng!",},
        [5614] = {12001,"Tôn Quyền","Lỗ Túc","Người này tên gì?","Người này tên gì?",},
        [5615] = {12001,"Lỗ Túc","Tôn Quyền","Người này họ Bàng tên Thống, đạo hiệu Phượng Sồ tiên sinh.","Người này họ Bàng tên Thống, đạo hiệu Phượng Sồ tiên sinh.",},
        [5616] = {12001,"Tôn Quyền","Lỗ Túc","Ta nghe danh người này đã lâu, nay có ở đây, nên mời đến ngay.","Ta nghe danh người này đã lâu, nay có ở đây, nên mời đến ngay.",},
        [5617] = {12002,"Lỗ Túc","Tôn Quyền","Chúa công, đây chính là Phượng Sồ tiên sinh Bàng Thống.","Chúa công, đây chính là Phượng Sồ tiên sinh Bàng Thống.",},
        [5618] = {12002,"Tôn Quyền","Bàng Thống","(Kẻ này tướng mạo kì lạ) Bình sinh ông học môn gì là chính?","(Kẻ này tướng mạo kì lạ) Bình sinh ông học môn gì là chính?",},
        [5619] = {12002,"Bàng Thống","Tôn Quyền","Không cần câu nệ, tuỳ cơ ứng biến.","Không cần câu nệ, tuỳ cơ ứng biến.",},
        [5620] = {12002,"Tôn Quyền","Bàng Thống","Tài học của ông so với Công Cẩn thì thế nào?","Tài học của ông so với Công Cẩn thì thế nào?",},
        [5621] = {12002,"Bàng Thống","Tôn Quyền","Những điều ta biết, khác Công Cẩn nhiều.","Những điều ta biết, khác Công Cẩn nhiều.",},
        [5622] = {12002,"Tôn Quyền","Bàng Thống","Ông hãy về, khi nào cần đến, sẽ cho người mời.","Ông hãy về, khi nào cần đến, sẽ cho người mời.",},
        [5623] = {12002,"Lỗ Túc","Tôn Quyền","Chúa công sao không dùng Bàng Sĩ Nguyên?","Chúa công sao không dùng Bàng Sĩ Nguyên?",},
        [5624] = {12002,"Tôn Quyền","Lỗ Túc","Hắn là một cuồng sĩ, không thể dùng!","Hắn là một cuồng sĩ, không thể dùng!",},
        [5625] = {12003,"Lỗ Túc","Bàng Thống","Không phải là ta không chịu tiến cử, mà là chúa công vẫn chưa đồng ý, các hạ hãy chờ thêm đã.","Không phải là ta không chịu tiến cử, mà là chúa công vẫn chưa đồng ý, các hạ hãy chờ thêm đã.",},
        [5626] = {12003,"Bàng Thống","Lỗ Túc","Ơ...","Ơ...",},
        [5627] = {12003,"Lỗ Túc","Bàng Thống","Các hạ có tài trị nước yên dân, đi đâu mà chẳng được trọng dụng? Nếu không có ý ở lại Đông Ngô, muốn đi xứ nào, cứ nói thật cho ta biết?","Các hạ có tài trị nước yên dân, đi đâu mà chẳng được trọng dụng? Nếu không có ý ở lại Đông Ngô, muốn đi xứ nào, cứ nói thật cho ta biết?",},
        [5628] = {12003,"Bàng Thống","Lỗ Túc","Ta muốn sang với Kinh Châu Lưu Hoàng Thúc.","Ta muốn sang với Kinh Châu Lưu Hoàng Thúc.",},
        [5629] = {12003,"Lỗ Túc","Bàng Thống","Ta tiến cử ông với Huyền Đức, ông sẽ giúp Tôn, Lưu hai nhà hòa thuận, chung sức phá Tào.","Ta tiến cử ông với Huyền Đức, ông sẽ giúp Tôn, Lưu hai nhà hòa thuận, chung sức phá Tào.",},
        [5630] = {12003,"Bàng Thống","Lỗ Túc","Đó là ý chí cả đời của ta!","Đó là ý chí cả đời của ta!",},
        [5631] = {12004,"Lưu Bị","Bàng Thống","Ông từ xa đến đây, khí vất vả lắm nhỉ?","Ông từ xa đến đây, khí vất vả lắm nhỉ?",},
        [5632] = {12004,"Bàng Thống","Lưu Bị","Nghe hoàn thúc chiêu nạp hiền sĩ, ta liền đến đây.","Nghe hoàn thúc chiêu nạp hiền sĩ, ta liền đến đây.",},
        [5633] = {12004,"Lưu Bị","Bàng Thống","Huyện Lỗi Dương thiếu một chức tri huyện, ngươi hãy làm tạm, sau này cần đến sẽ xin trọng dụng.","Huyện Lỗi Dương thiếu một chức tri huyện, ngươi hãy làm tạm, sau này cần đến sẽ xin trọng dụng.",},
        [5634] = {12004,"Bàng Thống","Lưu Bị","Huyền Đức đối xử tệ với ta! Khổng Minh không ở đây, sau này nói tiếp.","Huyền Đức đối xử tệ với ta! Khổng Minh không ở đây, sau này nói tiếp.",},
        [5635] = {12005,"Trương Phi","","Thống đến huyện Lỗi Dương, không màn chính sự, thật quá đáng!","Thống đến huyện Lỗi Dương, không màn chính sự, thật quá đáng!",},
        [5636] = {12005,"Bàng Thống","Trương Phi","Cái thứ huyện nhỏ này được mấy nỗi công việc, có gì mà phải coi xét? Tướng quân hãy ngồi xem ta giải quyết!","Cái thứ huyện nhỏ này được mấy nỗi công việc, có gì mà phải coi xét? Tướng quân hãy ngồi xem ta giải quyết!",},
        [5637] = {12006,"Bàng Thống","Trương Phi","(Trăm việc giải quyết nhanh gọn) Tào Tháo, Tôn Quyền ta còn chẳng coi vào đâu, huống chi một cái huyện ranh này, việc gì mà phải bận lòng!","(Trăm việc giải quyết nhanh gọn) Tào Tháo, Tôn Quyền ta còn chẳng coi vào đâu, huống chi một cái huyện ranh này, việc gì mà phải bận lòng!",},
        [5638] = {12006,"Trương Phi","Bàng Thống","Tiên sinh thật là bậc cao tài, tiểu tử có mắt không tròng.","Tiên sinh thật là bậc cao tài, tiểu tử có mắt không tròng.",},
        [5639] = {12006,"Bàng Thống","Trương Phi","Ta có thư tiến cử của Lỗ Túc.","Ta có thư tiến cử của Lỗ Túc.",},
        [5640] = {12006,"Trương Phi","Bàng Thống","Sao lúc mới gặp, tiên sinh không đưa ra?","Sao lúc mới gặp, tiên sinh không đưa ra?",},
        [5641] = {12006,"Bàng Thống","Trương Phi","Nếu đưa ngay thì chẳng khác nào ta nhờ lá thư tiến cử này để cầu cạnh!","Nếu đưa ngay thì chẳng khác nào ta nhờ lá thư tiến cử này để cầu cạnh!",},
        [5642] = {12006,"Lưu Bị","Bàng Thống","Giá không có ông, suýt nữa bỏ mất một vị đại hiền!","Giá không có ông, suýt nữa bỏ mất một vị đại hiền!",},
        [5643] = {12006,"Lưu Bị","Bàng Thống","Tư Mã Đức Tháo nói: Có một trong hai người Phục Long và Phượng Sồ, thì sẽ có được thiên hạ.","Tư Mã Đức Tháo nói: Có một trong hai người Phục Long và Phượng Sồ, thì sẽ có được thiên hạ.",},
        [5644] = {12101,"Mã Đằng","","Từ khi cùng Đổng Thừa vâng mật chiếu, hẹn với Huyền Đức đánh giặc. Đổng Thừa tử trận, Huyền Đức thì thua mãi.","Từ khi cùng Đổng Thừa vâng mật chiếu, hẹn với Huyền Đức đánh giặc. Đổng Thừa tử trận, Huyền Đức thì thua mãi.",},
        [5645] = {12101,"Mã Đằng","","Tào Tháo phong ta thành tướng quân, thảo phạt Tôn Quyền, ta phải làm sao?","Tào Tháo phong ta thành tướng quân, thảo phạt Tôn Quyền, ta phải làm sao?",},
        [5646] = {12101,"Mã Đại","Mã Đằng","Tào Tháo đang có âm mưu, thúc phụ nếu đến sẽ gặp nguy hiểm.","Tào Tháo đang có âm mưu, thúc phụ nếu đến sẽ gặp nguy hiểm.",},
        [5647] = {12101,"Mã Siêu","Mã Đằng","Con nguyện khởi binh Tây Lương, theo cha tấn công Hứa Xương, trừ hại cho thiện hạ.","Con nguyện khởi binh Tây Lương, theo cha tấn công Hứa Xương, trừ hại cho thiện hạ.",},
        [5648] = {12101,"Mã Đằng","Mã Siêu","Ngươi ở lại Tây Lương, nói Mã Hưu và Mã Đại đi với ta. Tào Tháo biết ngươi đang ở Tây Lương, sẽ không dám hại ta.","Ngươi ở lại Tây Lương, nói Mã Hưu và Mã Đại đi với ta. Tào Tháo biết ngươi đang ở Tây Lương, sẽ không dám hại ta.",},
        [5649] = {12101,"Mã Siêu","Mã Đằng","Phụ thân muốn đi, nhớ đừng tùy tiện vào Kinh Sư. Nên tùy cơ ứng biến, quan sát động tĩnh.","Phụ thân muốn đi, nhớ đừng tùy tiện vào Kinh Sư. Nên tùy cơ ứng biến, quan sát động tĩnh.",},
        [5650] = {12102,"Hoàng Khuê","Mã Đằng","Tào Tháo phái ta đến dẫn Tướng Quân vào thành, chắc là có ý xấu. Sau đó chờ Tào Tháo ra thành điểm quân, sẽ diệt hắn ở Điểm Quân Xứ, đại sự cáo thành.","Tào Tháo phái ta đến dẫn Tướng Quân vào thành, chắc là có ý xấu. Sau đó chờ Tào Tháo ra thành điểm quân, sẽ diệt hắn ở Điểm Quân Xứ, đại sự cáo thành.",},
        [5651] = {12102,"Mã Đằng","Hoàng Khuê","Tạ Tướng Quân nói đúng sự thật.","Tạ Tướng Quân nói đúng sự thật.",},
        [5652] = {12103,"Lý Xuân Hương","Hoàng Khuê","Người đời đều nói Lưu Hoàng Thúc nhân đức, Tào Tháo là gian hùng, đúng là vậy sao?","Người đời đều nói Lưu Hoàng Thúc nhân đức, Tào Tháo là gian hùng, đúng là vậy sao?",},
        [5653] = {12103,"Hoàng Khuê","Lý Xuân Hương","Ngươi là một nữ nhi mà còn biết chính tà, huống chi là ta? Ta chỉ muốn tiêu diệt Tào tặc!","Ngươi là một nữ nhi mà còn biết chính tà, huống chi là ta? Ta chỉ muốn tiêu diệt Tào tặc!",},
        [5654] = {12103,"Lý Xuân Hương","Hoàng Khuê","Ngươi muốn diệt hắn? Ngươi ra tay bằng cách nào?","Ngươi muốn diệt hắn? Ngươi ra tay bằng cách nào?",},
        [5655] = {12103,"Hoàng Khuê","Lý Xuân Hương","Ta đã hẹn sẵn Mã Tướng Quân, ngày mai sẽ diệt hắn lúc điểm binh ở ngoài thành.","Ta đã hẹn sẵn Mã Tướng Quân, ngày mai sẽ diệt hắn lúc điểm binh ở ngoài thành.",},
        [5656] = {12103,"Lý Xuân Hương","Hoàng Khuê","À... thì ra là vậy... (mau nói với Thừa Tướng)","À... thì ra là vậy... (mau nói với Thừa Tướng)",},
        [5657] = {12104,"Tào Tháo","","Ta đã biết mật báo, có người muốn nhân lúc ta ra thành điểm quân lấy mạng ta, thì ra là Mã Tướng Quân to gan như vậy.","Ta đã biết mật báo, có người muốn nhân lúc ta ra thành điểm quân lấy mạng ta, thì ra là Mã Tướng Quân to gan như vậy.",},
        [5658] = {12104,"Tào Tháo","","Người đâu, bắn tên tiêu diệt chúng!","Người đâu, bắn tên tiêu diệt chúng!",},
        [5659] = {12104,"Mã Đằng","","Ta không thể vì nước diệt tặc, là ý trời! (ôm hận qua đời)","Ta không thể vì nước diệt tặc, là ý trời! (ôm hận qua đời)",},
        [5660] = {12201,"Tào Tháo","","Nghe nói gần đây Lưu Bị huấn luyện quân mã, chuẩn bị vũ khí, nếu hắn chiếm Xuyên thành công, sẽ như mọc thêm cánh, ta phải làm thế nào?","Nghe nói gần đây Lưu Bị huấn luyện quân mã, chuẩn bị vũ khí, nếu hắn chiếm Xuyên thành công, sẽ như mọc thêm cánh, ta phải làm thế nào?",},
        [5661] = {12201,"Trần Quần","Tào Tháo","Nay Lưu Bị Tôn Quyền hợp tác, Thừa Tướng dẫn binh đi lấy Giang Đông, Lưu Bị ở Tây Xuyên chắc chắn không muốn cứu viện, Giang Đông chắc chắn thuộc về Thừa Tướng.","Nay Lưu Bị Tôn Quyền hợp tác, Thừa Tướng dẫn binh đi lấy Giang Đông, Lưu Bị ở Tây Xuyên chắc chắn không muốn cứu viện, Giang Đông chắc chắn thuộc về Thừa Tướng.",},
        [5662] = {12201,"Tào Tháo","Trần Quần","Quả đúng ý ta, lập tức điều động 30 vạn đại quân, tiến đánh Giang Đông!","Quả đúng ý ta, lập tức điều động 30 vạn đại quân, tiến đánh Giang Đông!",},
        [5663] = {12202,"Tôn Quyền","","Có Gian Tế báo tin, Tào Tháo muốn dẫn 30 vạn đại quân tấn công chúng ta.","Có Gian Tế báo tin, Tào Tháo muốn dẫn 30 vạn đại quân tấn công chúng ta.",},
        [5664] = {12202,"Trương Chiêu","Tôn Quyền","Có thể nói Lỗ Túc viết thư cho Huyền Đức, Lỗ Tử Kính có ơn với Huyền Đức, chắc chắn sẽ nghe theo. Huống chi Huyền Đức là con rể của Đông Ngô, càng không dám cãi.","Có thể nói Lỗ Túc viết thư cho Huyền Đức, Lỗ Tử Kính có ơn với Huyền Đức, chắc chắn sẽ nghe theo. Huống chi Huyền Đức là con rể của Đông Ngô, càng không dám cãi.",},
        [5665] = {12203,"Gia Cát Lượng","","Không cần huy động binh lính Giang Nam, cũng không cần huy động binh lính Kinh Châu, ta có cách khiến Tào Tháo không dám dòm ngó Đông Nam.","Không cần huy động binh lính Giang Nam, cũng không cần huy động binh lính Kinh Châu, ta có cách khiến Tào Tháo không dám dòm ngó Đông Nam.",},
        [5666] = {12203,"Gia Cát Lượng","","Nói Lỗ Túc cứ ăn ngon ngủ kĩ, nếu có Bắc Binh xâm phạm, Hoàng Thúc tự có kế lui binh.","Nói Lỗ Túc cứ ăn ngon ngủ kĩ, nếu có Bắc Binh xâm phạm, Hoàng Thúc tự có kế lui binh.",},
        [5667] = {12203,"Lưu Bị","Gia Cát Lượng","Tiên sinh có diệu kế gì, có thể khiến Tào Tháo thoái lui?","Tiên sinh có diệu kế gì, có thể khiến Tào Tháo thoái lui?",},
        [5668] = {12203,"Gia Cát Lượng","Lưu Bị","Điều mà Tháo thường lo, là binh của Tây Lương. Nay Tháo diệt Mã Đằng, con trai hắn Mã Siêu thống lĩnh Tây Lương, chắc chắn căm hận Tháo tặc.","Điều mà Tháo thường lo, là binh của Tây Lương. Nay Tháo diệt Mã Đằng, con trai hắn Mã Siêu thống lĩnh Tây Lương, chắc chắn căm hận Tháo tặc.",},
        [5669] = {12203,"Gia Cát Lượng","Lưu Bị","Chúa công có thể viết thư cho Mã Siêu, nói hắn dẫn quân vào ải, Tào Tháo còn tâm trí đâu mà nghĩ về Giang Đông nữa?","Chúa công có thể viết thư cho Mã Siêu, nói hắn dẫn quân vào ải, Tào Tháo còn tâm trí đâu mà nghĩ về Giang Đông nữa?",},
        [5670] = {12301,"Mã Siêu","Bàng Đức","Tối qua mơ thấy thân ở núi tuyết, đàn hổ đến cắn, giật mình tỉnh dậy, giờ cảm thấy rất hoang mang.","Tối qua mơ thấy thân ở núi tuyết, đàn hổ đến cắn, giật mình tỉnh dậy, giờ cảm thấy rất hoang mang.",},
        [5671] = {12301,"Bàng Đức","Mã Siêu","Mơ gặp hổ ở vùng tuyết là điềm không may, chẳng lẽ lão tướng quân xảy ra chuyện ở Hứa Xương?","Mơ gặp hổ ở vùng tuyết là điềm không may, chẳng lẽ lão tướng quân xảy ra chuyện ở Hứa Xương?",},
        [5672] = {12301,"Mã Đại","Mã Siêu","(Thê thảm bước vào) Hu hu hu... thúc phụ và đệ đệ mất mạng dưới tay Tào Tháo rồi!","(Thê thảm bước vào) Hu hu hu... thúc phụ và đệ đệ mất mạng dưới tay Tào Tháo rồi!",},
        [5673] = {12301,"Mã Siêu","Bàng Đức","Gì! Lẽ nào lại như vậy! Ta và Tào Tháo không đội trời chung!","Gì! Lẽ nào lại như vậy! Ta và Tào Tháo không đội trời chung!",},
        [5674] = {12301,"Bàng Đức","Mã Siêu","Lưu Hoàng Thúc phái người gửi thư đến, muốn hợp tác với tướng quân cùng diệt Tào tặc.","Lưu Hoàng Thúc phái người gửi thư đến, muốn hợp tác với tướng quân cùng diệt Tào tặc.",},
        [5675] = {12301,"Mã Siêu","Bàng Đức","Thù diệt cha ắt phải báo! Ta sẽ dẫn đại quan, tập hợp với Lưu Hoàng Thúc.","Thù diệt cha ắt phải báo! Ta sẽ dẫn đại quan, tập hợp với Lưu Hoàng Thúc.",},
        [5676] = {12302,"Hàn Toại","Mã Siêu","Tào Tháo viết thư cho ta, nói nếu bắt ngươi đến Hứa Đô, sẽ phong ta làm Tây Lương Hầu.","Tào Tháo viết thư cho ta, nói nếu bắt ngươi đến Hứa Đô, sẽ phong ta làm Tây Lương Hầu.",},
        [5677] = {12302,"Mã Siêu","Hàn Toại","Vậy thúc phụ hãy trói con và Mã Đại huynh đệ áp đến Hứa Xương, thúc phụ không cần mất công sức.","Vậy thúc phụ hãy trói con và Mã Đại huynh đệ áp đến Hứa Xương, thúc phụ không cần mất công sức.",},
        [5678] = {12302,"Hàn Toại","Mã Siêu","Ta và phụ thân ngươi kết thành huynh đệ, sao có thể hại ngươi? Nếu ngươi dấy binh, ta nhất định hỗ trợ.","Ta và phụ thân ngươi kết thành huynh đệ, sao có thể hại ngươi? Nếu ngươi dấy binh, ta nhất định hỗ trợ.",},
        [5679] = {12303,"Mã Siêu","","Trường An Thành là nơi Tây Hán xây dựng thủ đô, thành quách kiên cố. Đại quân ta đã bao vây 10 ngày, tấn công mãi không được, phải làm sao đây?","Trường An Thành là nơi Tây Hán xây dựng thủ đô, thành quách kiên cố. Đại quân ta đã bao vây 10 ngày, tấn công mãi không được, phải làm sao đây?",},
        [5680] = {12303,"Nhân vật","Mã Siêu","Trường An Thành đất cứng nước kiềm, nay bao vây 10 ngày, chắc là quân dân đều khốn khó. Hay là tạm rút quân, chỉ cần làm như vậy như vậy, sẽ dễ dàng có được Trường An.","Trường An Thành đất cứng nước kiềm, nay bao vây 10 ngày, chắc là quân dân đều khốn khó. Hay là tạm rút quân, chỉ cần làm như vậy như vậy, sẽ dễ dàng có được Trường An.",},
        [5681] = {12303,"Mã Siêu","Nhân vật","Kế này tuyệt diệu.","Kế này tuyệt diệu.",},
        [5682] = {12304,"Bàng Đức","Mã Siêu","5 ngày đã qua, lúc quân dân ra thành đốn củi lấy nước, quân ta phái người thừa cơ hội đó tập kích trong ngoài, đánh cho họ không kịp trở tay!","5 ngày đã qua, lúc quân dân ra thành đốn củi lấy nước, quân ta phái người thừa cơ hội đó tập kích trong ngoài, đánh cho họ không kịp trở tay!",},
        [5683] = {12304,"Mã Siêu","Bàng Đức","Lần này công chiếm thành trì, mọi người đều có công lao, người đâu truyền lệnh xuống, thưởng cho ba quân!","Lần này công chiếm thành trì, mọi người đều có công lao, người đâu truyền lệnh xuống, thưởng cho ba quân!",},
        [5684] = {12401,"Tào Tháo","","Sao có thể để mất Trường An! Từ Hoảng, Tào Hồng đi gác Đồng Quan, ta sẽ dẫn đại quân theo sau, nếu để mất cửa ải sẽ trảm các ngươi!","Sao có thể để mất Trường An! Từ Hoảng, Tào Hồng đi gác Đồng Quan, ta sẽ dẫn đại quân theo sau, nếu để mất cửa ải sẽ trảm các ngươi!",},
        [5685] = {12401,"Tào Nhân","Tào Tháo","Tào Hồng tính tình nóng nảy, ta lo hắn sẽ làm hỏng chuyện.","Tào Hồng tính tình nóng nảy, ta lo hắn sẽ làm hỏng chuyện.",},
        [5686] = {12401,"Tào Tháo","Tào Nhân","Ngươi và ta áp vận Lương Thảo, rồi sau đó tiếp ứng.","Ngươi và ta áp vận Lương Thảo, rồi sau đó tiếp ứng.",},
        [5687] = {12402,"Mã Siêu","","Tào Tháo! Cả nhà các ngươi đều nhát như thỏ đế! Có bản lĩnh thì xuống đây đánh với ta! Đừng ở trên thành làm rùa rụt cổ!","Tào Tháo! Cả nhà các ngươi đều nhát như thỏ đế! Có bản lĩnh thì xuống đây đánh với ta! Đừng ở trên thành làm rùa rụt cổ!",},
        [5688] = {12402,"Tào Hồng","Từ Hoảng","Lẽ nào lại như vậy! Ta phải xuống giáo huấn tên không biết trời cao đất dày này!","Lẽ nào lại như vậy! Ta phải xuống giáo huấn tên không biết trời cao đất dày này!",},
        [5689] = {12402,"Từ Hoảng","Tào Hồng","Là Mã Siêu cố tình chọc tức Tướng Quân, đừng để mắc bẫy hắn. Đợi đại quân của Thừa Tướng đến, sẽ có dự tính.","Là Mã Siêu cố tình chọc tức Tướng Quân, đừng để mắc bẫy hắn. Đợi đại quân của Thừa Tướng đến, sẽ có dự tính.",},
        [5690] = {12403,"Tào Hồng","","Cơ hội đến rồi! Nhân lúc họ đang trong cảnh khốn đốn, ta phải xuống dưới quyết đấu một trận với Mã Siêu! Bị hắn mắng bấy lâu nay, ta tức quá mà.","Cơ hội đến rồi! Nhân lúc họ đang trong cảnh khốn đốn, ta phải xuống dưới quyết đấu một trận với Mã Siêu! Bị hắn mắng bấy lâu nay, ta tức quá mà.",},
        [5691] = {12403,"Từ Hoảng","","Tào Hồng Tướng Quân đừng đi, mau trở về!","Tào Hồng Tướng Quân đừng đi, mau trở về!",},
        [5692] = {12403,"Mã Siêu","","Trễ rồi! Ăn một thương của ta!","Trễ rồi! Ăn một thương của ta!",},
        [5693] = {12403,"Từ Hoảng","","Nguy rồi, trúng kế gian tặc rồi! Tào Hồng Tướng Quân mau chạy, ta yểm hộ người!","Nguy rồi, trúng kế gian tặc rồi! Tào Hồng Tướng Quân mau chạy, ta yểm hộ người!",},
        [5694] = {12501,"Tào Tháo","Tào Hồng","Đã nói thời hạn 10 ngày, sao 9 ngày ngươi đã mất Đồng Quan?","Đã nói thời hạn 10 ngày, sao 9 ngày ngươi đã mất Đồng Quan?",},
        [5695] = {12501,"Tào Hồng","Tào Tháo","Tên Mã Siêu của Tây Lương Quân, lần này đến lần khác nhục mạ gia đình chúng ta, ta nhân lúc bọn chúng lơ là sĩ khí định đến giáo huấn một phen, không ngờ bị trúng gian kế.","Tên Mã Siêu của Tây Lương Quân, lần này đến lần khác nhục mạ gia đình chúng ta, ta nhân lúc bọn chúng lơ là sĩ khí định đến giáo huấn một phen, không ngờ bị trúng gian kế.",},
        [5696] = {12501,"Tào Tháo","Từ Hoảng","Tào Hồng tuổi trẻ bồng bột, Từ Hoảng sao ngươi cũng không hiểu chuyện vậy chứ?","Tào Hồng tuổi trẻ bồng bột, Từ Hoảng sao ngươi cũng không hiểu chuyện vậy chứ?",},
        [5697] = {12501,"Từ Hoảng","Tào Tháo","Ta đã khuyên nhiều lần, Tào Hồng Tướng Quân không nghe. Hôm ấy ta ở cửa ải chọn Xe Lương, lúc phát hiện thì tiểu tướng quân đã xuống ải trúng kế rồi.","Ta đã khuyên nhiều lần, Tào Hồng Tướng Quân không nghe. Hôm ấy ta ở cửa ải chọn Xe Lương, lúc phát hiện thì tiểu tướng quân đã xuống ải trúng kế rồi.",},
        [5698] = {12501,"Tào Tháo","Tào Hồng","Tào Hồng! Lẽ ra nên lấy mạng ngươi, nể tình chúng quan cầu xin nên tạm tha mạng cho ngươi. Ngày mai ta sẽ đi đoạt lại Đồng Quan.","Tào Hồng! Lẽ ra nên lấy mạng ngươi, nể tình chúng quan cầu xin nên tạm tha mạng cho ngươi. Ngày mai ta sẽ đi đoạt lại Đồng Quan.",},
        [5699] = {12502,"Mã Siêu","","Tào tặc khi quân phạm thượng, tội không thể tha!","Tào tặc khi quân phạm thượng, tội không thể tha!",},
        [5700] = {12502,"Tào Tháo","","Nguy rồi, Mã Siêu này thật dũng mãnh, ta không phải đối thủ của hắn. Đành trốn trước vậy.","Nguy rồi, Mã Siêu này thật dũng mãnh, ta không phải đối thủ của hắn. Đành trốn trước vậy.",},
        [5701] = {12502,"Tây Lương Quân","","Mau đuổi theo Tào Tặc! Đừng để hắn chạy! Tên mặc bào đỏ chính là Tào Tháo!","Mau đuổi theo Tào Tặc! Đừng để hắn chạy! Tên mặc bào đỏ chính là Tào Tháo!",},
        [5702] = {12502,"Tào Tháo","","Không cần áo bào nữa, cởi ra bỏ đi.","Không cần áo bào nữa, cởi ra bỏ đi.",},
        [5703] = {12502,"Tây Lương Quân","","Tên râu dài chính là Tào Tháo!","Tên râu dài chính là Tào Tháo!",},
        [5704] = {12502,"Tào Tháo","","Cắt râu đi, chạy mau chạy mau!","Cắt râu đi, chạy mau chạy mau!",},
        [5705] = {12601,"Hứa Chử","","Nghe nói Mã Siêu đó rất lợi hại, thề tử chiến với hắn!","Nghe nói Mã Siêu đó rất lợi hại, thề tử chiến với hắn!",},
        [5706] = {12601,"Mã Siêu","","Hứa Chử gửi chiến thư cho ta, thật khinh người!","Hứa Chử gửi chiến thư cho ta, thật khinh người!",},
        [5707] = {12601,"Hứa Chử","Mã Siêu","Đừng nhiều lời, đến chiến đấu đi!","Đừng nhiều lời, đến chiến đấu đi!",},
        [5708] = {12601,"Mã Siêu","Hứa Chử","Kẻ địch ta từng gặp không ai bằng Hứa Chử, không hổ là Hổ Si!","Kẻ địch ta từng gặp không ai bằng Hứa Chử, không hổ là Hổ Si!",},
        [5709] = {12701,"Tào Tháo","Giả Hủ","Hàn Toại phái người mang thư đến, thương luyện chuyện giảng hòa cắt đất. Văn Hòa cảm thấy thế nào?","Hàn Toại phái người mang thư đến, thương luyện chuyện giảng hòa cắt đất. Văn Hòa cảm thấy thế nào?",},
        [5710] = {12701,"Giả Hủ","Tào Tháo","Việc quân cơ không nề dối trá, có thể giả vờ đồng ý, sau đó dùng kế Phản Gián, để Hàn Toại và Mã Siêu nghi ngờ nhau là được.","Việc quân cơ không nề dối trá, có thể giả vờ đồng ý, sau đó dùng kế Phản Gián, để Hàn Toại và Mã Siêu nghi ngờ nhau là được.",},
        [5711] = {12701,"Tào Tháo","Giả Hủ","Cao kiến trên thiên hạ thường tương đồng nhau. Mưu kế của Văn Hòa, chính là suy nghĩ trong lòng ta.","Cao kiến trên thiên hạ thường tương đồng nhau. Mưu kế của Văn Hòa, chính là suy nghĩ trong lòng ta.",},
        [5712] = {12701,"Tào Tháo","","Truyền thư của ta cho Mã Siêu, đợi ta lui binh, trả đất Hà Tây cho hắn.","Truyền thư của ta cho Mã Siêu, đợi ta lui binh, trả đất Hà Tây cho hắn.",},
        [5713] = {12702,"Mã Siêu","Hàn Toại","Nghe nói Tào Tháo gửi một lá thư đến, thúc phụ có thể cho con mượn xem?","Nghe nói Tào Tháo gửi một lá thư đến, thúc phụ có thể cho con mượn xem?",},
        [5714] = {12702,"Hàn Toại","Mã Siêu","Đây, không hiểu tại sao trên thư bị sửa rất nhiều chỗ, chẳng lẽ gửi nhầm thư nháp.","Đây, không hiểu tại sao trên thư bị sửa rất nhiều chỗ, chẳng lẽ gửi nhầm thư nháp.",},
        [5715] = {12702,"Mã Siêu","Hàn Toại","Sao lại có ai gửi thư nháp? Chẳng lẽ thúc phụ sợ con biết quá nhiều nên sửa đi.","Sao lại có ai gửi thư nháp? Chẳng lẽ thúc phụ sợ con biết quá nhiều nên sửa đi.",},
        [5716] = {12702,"Hàn Toại","Mã Siêu","Cháu hiền đừng nghi, ta không làm thế.","Cháu hiền đừng nghi, ta không làm thế.",},
        [5717] = {12703,"Dương Thu","Hàn Toại","Mã Siêu ỷ bản thân dũng mãnh, thường cố ý bắt nạt chúa công, theo mỗ thấy, hay là theo phe Tào công, sau này có thể được phong hầu.","Mã Siêu ỷ bản thân dũng mãnh, thường cố ý bắt nạt chúa công, theo mỗ thấy, hay là theo phe Tào công, sau này có thể được phong hầu.",},
        [5718] = {12703,"Hàn Toại","Dương Thu","Ta và Mã Đằng kết bái huynh đệ, sao có thể phản bội?","Ta và Mã Đằng kết bái huynh đệ, sao có thể phản bội?",},
        [5719] = {12703,"Dương Thu","Hàn Toại","Việc đến nước này, không thể làm khác. Ta phái người báo tin cho Tào Tháo, hẹn tối nay phóng hỏa, trong ngoài phối hợp, cùng đối phó Mã Siêu.","Việc đến nước này, không thể làm khác. Ta phái người báo tin cho Tào Tháo, hẹn tối nay phóng hỏa, trong ngoài phối hợp, cùng đối phó Mã Siêu.",},
        [5720] = {12704,"Mã Siêu","","Ai dám mưu hại ta! Nhìn đây! (Vung kiếm tấn công Hàn Toại)","Ai dám mưu hại ta! Nhìn đây! (Vung kiếm tấn công Hàn Toại)",},
        [5721] = {12704,"Vu Cấm","","Tào Thừa Tướng phái ta đến chi viện Hàn Tướng Quân!","Tào Thừa Tướng phái ta đến chi viện Hàn Tướng Quân!",},
        [5722] = {12704,"Nhân vật","Mã Siêu","Hảo hán không ham lợi trước mắt, Mã Tướng Quân mau theo ta rời khỏi, ngày tháng còn dài!","Hảo hán không ham lợi trước mắt, Mã Tướng Quân mau theo ta rời khỏi, ngày tháng còn dài!",},
        [5723] = {12801,"Trương Lỗ","Diêm Phố","Tây Lương Mã Đằng đã bị Tào Tháo diệt, Mã Siêu vừa mới thua, Tào Tháo chắc chắn xâm nhập vào Hán Trung ta. Ta muốn tự xưng là Hán Ninh Vương, đốc binh chống Tào Tháo, thế nào?","Tây Lương Mã Đằng đã bị Tào Tháo diệt, Mã Siêu vừa mới thua, Tào Tháo chắc chắn xâm nhập vào Hán Trung ta. Ta muốn tự xưng là Hán Ninh Vương, đốc binh chống Tào Tháo, thế nào?",},
        [5724] = {12801,"Diêm Phố","Trương Lỗ","Mã Siêu chiến bại, người dân Tây Lương chạy vào Hán Trung, không dưới vài vạn.","Mã Siêu chiến bại, người dân Tây Lương chạy vào Hán Trung, không dưới vài vạn.",},
        [5725] = {12801,"Diêm Phố","Trương Lỗ","Lưu Chương là kẻ tầm thường, chi bằng chúng ta chiếm cứ Tây Xuyên trước, rồi mới xưng vương!","Lưu Chương là kẻ tầm thường, chi bằng chúng ta chiếm cứ Tây Xuyên trước, rồi mới xưng vương!",},
        [5726] = {12802,"Lưu Chương","Trương Tùng","Nghe nói Trương Lỗ muốn chiếm Tây Xuyên, phải làm sao đây?","Nghe nói Trương Lỗ muốn chiếm Tây Xuyên, phải làm sao đây?",},
        [5727] = {12802,"Trương Tùng","Lưu Chương","Chúa công yên tâm, mỗ tuy bất tài, nhưng sẽ dùng tài khéo ăn nói, khiến cho Trương Lỗ không dám nhìn Tây Xuyên bằng con mắt chính diện.","Chúa công yên tâm, mỗ tuy bất tài, nhưng sẽ dùng tài khéo ăn nói, khiến cho Trương Lỗ không dám nhìn Tây Xuyên bằng con mắt chính diện.",},
        [5728] = {12802,"Lưu Chương","Trương Tùng","Xin lắng nghe.","Xin lắng nghe.",},
        [5729] = {12802,"Trương Tùng","Lưu Chương","Nghe nói Tào Tháo đã diệt Lữ Bố, lại còn phá Mã Siêu, thiên hạ vô địch. chúa công có thể chuẩn bị vật hiến tặng, khuyên Tào Tháo dấy binh Hán Trung để đối phó Trương Lỗ.","Nghe nói Tào Tháo đã diệt Lữ Bố, lại còn phá Mã Siêu, thiên hạ vô địch. chúa công có thể chuẩn bị vật hiến tặng, khuyên Tào Tháo dấy binh Hán Trung để đối phó Trương Lỗ.",},
        [5730] = {12802,"Trương Tùng","Lưu Chương","Trương Lỗ tự lo còn không xong, làm gì còn dám lăm le Tây Xuyên?","Trương Lỗ tự lo còn không xong, làm gì còn dám lăm le Tây Xuyên?",},
        [5731] = {12802,"Lưu Chương","Trương Tùng","Rất tốt, vậy làm như ngươi nói.","Rất tốt, vậy làm như ngươi nói.",},
        [5732] = {12901,"Tào Tháo","Trương Tùng","Chủ ngươi Lưu Chương sao nhiều năm không tiến cống?","Chủ ngươi Lưu Chương sao nhiều năm không tiến cống?",},
        [5733] = {12901,"Trương Tùng","Tào Tháo","Dọc đường vất vả, tặc khấu rục rịch, không thể cho vào.","Dọc đường vất vả, tặc khấu rục rịch, không thể cho vào.",},
        [5734] = {12901,"Tào Tháo","Trương Tùng","Ta bình định thiên hạ, sao lại có đạo tặc?","Ta bình định thiên hạ, sao lại có đạo tặc?",},
        [5735] = {12901,"Trương Tùng","Tào Tháo","Nam có Tôn Quyền, Bắc có Trương Lỗ, Tây có Lưu Bị, làm sao có thể nói là thái bình?","Nam có Tôn Quyền, Bắc có Trương Lỗ, Tây có Lưu Bị, làm sao có thể nói là thái bình?",},
        [5736] = {12901,"Tào Tháo","Trương Tùng","Hừ, dung mạo hèn hạ, lời nói mạo phạm, vô lễ!","Hừ, dung mạo hèn hạ, lời nói mạo phạm, vô lễ!",},
        [5737] = {12902,"Trương Tùng","","Tào Thừa Tướng cũng chỉ có thế, văn bất Minh Khổng, Mạnh chi đạo, võ bất đạt Tôn, Ngô chi cơ, chuyên gây ác bá để lên vị trí lớn. ","Tào Thừa Tướng cũng chỉ có thế, văn bất Minh Khổng, Mạnh chi đạo, võ bất đạt Tôn, Ngô chi cơ, chuyên gây ác bá để lên vị trí lớn. ",},
        [5738] = {12902,"Dương Tu","Trương Tùng","Ngươi ở vùng sâu vùng xa, sao lại biết đại tài của Thừa Tướng? Để ta cho ngươi xem sự lợi hại, có nghe quaMạnh Đức Thư chưa?","Ngươi ở vùng sâu vùng xa, sao lại biết đại tài của Thừa Tướng? Để ta cho ngươi xem sự lợi hại, có nghe quaMạnh Đức Thư chưa?",},
        [5739] = {12902,"Trương Tùng","Dương Tu","(Xem hết từ đầu đến cuối)... Ta còn tưởng là sách gì, sách này trẻ con còn thuộc, có gì mới không!","(Xem hết từ đầu đến cuối)... Ta còn tưởng là sách gì, sách này trẻ con còn thuộc, có gì mới không!",},
        [5740] = {12902,"Dương Tu","Trương Tùng","Đây là sách bí tàng của Thừa Tướng, chưa truyền ra ngoài, sao ngay cả trẻ con cũng biết?","Đây là sách bí tàng của Thừa Tướng, chưa truyền ra ngoài, sao ngay cả trẻ con cũng biết?",},
        [5741] = {12902,"Trương Tùng","Dương Tu","Nếu ngươi không tin, ta đọc cho nghe. (Đọc từ đầu đến đuôi không sai chữ nào)","Nếu ngươi không tin, ta đọc cho nghe. (Đọc từ đầu đến đuôi không sai chữ nào)",},
        [5742] = {12902,"Dương Tu","Trương Tùng","Người nhìn một lần là nhớ, quả là kỳ tài!","Người nhìn một lần là nhớ, quả là kỳ tài!",},
        [5743] = {12903,"Tào Tháo","Trương Tùng","Ta xem tiểu nhân thiên hạ như cỏ rác, thuận ta sống, nghịch ta tử, ngươi hiểu không?","Ta xem tiểu nhân thiên hạ như cỏ rác, thuận ta sống, nghịch ta tử, ngươi hiểu không?",},
        [5744] = {12903,"Trương Tùng","Tào Tháo","Thừa Tướng dẫn binh khắp nơi, bách chiến bách thắng, Bộc Dương đánh Lữ Bố, Uyển Thành chiến Trương Tú, Xích Bích gặp Chu Lang, Hoa Dung gặp Quan Vũ;","Thừa Tướng dẫn binh khắp nơi, bách chiến bách thắng, Bộc Dương đánh Lữ Bố, Uyển Thành chiến Trương Tú, Xích Bích gặp Chu Lang, Hoa Dung gặp Quan Vũ;",},
        [5745] = {12903,"Trương Tùng","Tào Tháo","Tháo Chạy ở Đồng Quan, đoạt thuyền tránh tiễn ở Vị Thủy, có thể nói là vô địch trên thiên hạ!","Tháo Chạy ở Đồng Quan, đoạt thuyền tránh tiễn ở Vị Thủy, có thể nói là vô địch trên thiên hạ!",},
        [5746] = {12903,"Tào Tháo","Trương Tùng","Sao ngươi dám nói xấu ta! Đánh hắn ra ngoài!","Sao ngươi dám nói xấu ta! Đánh hắn ra ngoài!",},
        [5747] = {13001,"Trương Tùng","","Vốn định hiến bản đồ Tây Xuyên cho Tào Tháo, nào ngờ hắn lại không xem ai ra gì như vậy! Hay là vòng đường đến Kinh Châu xem thử Lưu Bị thế nào.","Vốn định hiến bản đồ Tây Xuyên cho Tào Tháo, nào ngờ hắn lại không xem ai ra gì như vậy! Hay là vòng đường đến Kinh Châu xem thử Lưu Bị thế nào.",},
        [5748] = {13001,"Nhân vật","Trương Tùng","Có phải là Trương Tùng Trương Biệt Giá? Ta và huynh trưởng chờ đã lâu.","Có phải là Trương Tùng Trương Biệt Giá? Ta và huynh trưởng chờ đã lâu.",},
        [5749] = {13001,"Lưu Bị","Trương Tùng","Nghe danh đại phu đã lâu, như sấm bên tai. Cố tình đến tiếp đón, nếu không chê bai, hãy đến chỗ ta nghỉ ngơi để ta thể hiện lòng ngưỡng mộ.","Nghe danh đại phu đã lâu, như sấm bên tai. Cố tình đến tiếp đón, nếu không chê bai, hãy đến chỗ ta nghỉ ngơi để ta thể hiện lòng ngưỡng mộ.",},
        [5750] = {13001,"Trương Tùng","","Nghe đồn Lưu Huyền Đức rộng lượng hiếu khách, quả đúng vậy.","Nghe đồn Lưu Huyền Đức rộng lượng hiếu khách, quả đúng vậy.",},
        [5751] = {13002,"Trương Tùng","Lưu Bị","Mấy ngày nay đa tạ Huyền Đức hậu đãi, ta phải về đây, cáo từ tại đây.","Mấy ngày nay đa tạ Huyền Đức hậu đãi, ta phải về đây, cáo từ tại đây.",},
        [5752] = {13002,"Lưu Bị","Trương Tùng","Được Mông Đại Phu không chê, ở lại ba ngày, nay tạm biệt không biết khi nào lại được nghe người chỉ dạy.","Được Mông Đại Phu không chê, ở lại ba ngày, nay tạm biệt không biết khi nào lại được nghe người chỉ dạy.",},
        [5753] = {13002,"Trương Tùng","Lưu Bị","Ta không phải bán chủ cầu vinh, nay gặp Minh Công, mong Minh Công hãy lấy Tây Xuyên làm căn cứ, sau này nhắm vào Hán Trung, đoạt thiên hạ, khuông phù Hán Thất.","Ta không phải bán chủ cầu vinh, nay gặp Minh Công, mong Minh Công hãy lấy Tây Xuyên làm căn cứ, sau này nhắm vào Hán Trung, đoạt thiên hạ, khuông phù Hán Thất.",},
        [5754] = {13002,"Trương Tùng","Lưu Bị","Nếu Minh Công có ý chiếm Tây Xuyên, Tùng nguyện giúp đỡ, làm nội ứng, thế nào?","Nếu Minh Công có ý chiếm Tây Xuyên, Tùng nguyện giúp đỡ, làm nội ứng, thế nào?",},
        [5755] = {13002,"Lưu Bị","Trương Tùng","Lưu Quý Ngọc là huynh đệ tông thất với ta, nếu tấn công hắn, ắt bị người đời dè bỉu!","Lưu Quý Ngọc là huynh đệ tông thất với ta, nếu tấn công hắn, ắt bị người đời dè bỉu!",},
        [5756] = {13003,"Nhân vật","","Đại trượng phu phải nỗ lực tạo dựng sự nghiệp cho mình. Không cố gắng tranh thủ cơ hội, vuột mất chớ than van.","Đại trượng phu phải nỗ lực tạo dựng sự nghiệp cho mình. Không cố gắng tranh thủ cơ hội, vuột mất chớ than van.",},
        [5757] = {13003,"Trương Tùng","Nhân vật","Tướng Quân nói phải, cảm kích Minh Công, tặng tranh này cho Tướng Quân. Nhưng nhìn tranh này là biết đường đi ở Thục Trung.","Tướng Quân nói phải, cảm kích Minh Công, tặng tranh này cho Tướng Quân. Nhưng nhìn tranh này là biết đường đi ở Thục Trung.",},
        [5758] = {13003,"Trương Tùng","Nhân vật","Ta có hai người bằng hữu tốt là Pháp Chính và Mạnh Đạt, hai người này chắc chắn giúp được ngài.","Ta có hai người bằng hữu tốt là Pháp Chính và Mạnh Đạt, hai người này chắc chắn giúp được ngài.",},
        [5759] = {13003,"Nhân vật","Trương Tùng","Non xanh nước biếc mãi trường tồn. Sau này nên chuyện, ắt sẽ báo đáp!","Non xanh nước biếc mãi trường tồn. Sau này nên chuyện, ắt sẽ báo đáp!",},
        [5760] = {13004,"Trương Tùng","Pháp Chính","Lần này ta đã gặp Tào Tháo, khinh hiền ngạo sĩ, có thể cùng ưu, nhưng không thể cùng lạc. Ta đã trao Ích Châu cho Lưu Hoàng Thúc.","Lần này ta đã gặp Tào Tháo, khinh hiền ngạo sĩ, có thể cùng ưu, nhưng không thể cùng lạc. Ta đã trao Ích Châu cho Lưu Hoàng Thúc.",},
        [5761] = {13004,"Pháp Chính","Trương Tùng","Từ lâu ta đã thấy Lưu Chương bất tài, muốn gặp Lưu Hoàng Thúc đã lâu. Nếu chúng ta suy nghĩ giống nhau vậy thì còn do dự gì nữa?","Từ lâu ta đã thấy Lưu Chương bất tài, muốn gặp Lưu Hoàng Thúc đã lâu. Nếu chúng ta suy nghĩ giống nhau vậy thì còn do dự gì nữa?",},
        [5762] = {13004,"Trương Tùng","Pháp Chính","Ngày mai ta đi gặp Lưu Chương, để hắn phái hai vị ca ca làm sứ, đến Kinh Châu. Cùng Lưu Hoàng Thúc thương lượng đại kế.","Ngày mai ta đi gặp Lưu Chương, để hắn phái hai vị ca ca làm sứ, đến Kinh Châu. Cùng Lưu Hoàng Thúc thương lượng đại kế.",},
        [5763] = {13101,"Pháp Chính","Lưu Bị","Ích Châu là nơi hiểm trở, không đủ tài cán, khó mà trị an được. Nay Lưu Quý Ngọc không biết dùng người, cơ nghiệp này sớm muộn cũng thuộc về kẻ khác!","Ích Châu là nơi hiểm trở, không đủ tài cán, khó mà trị an được. Nay Lưu Quý Ngọc không biết dùng người, cơ nghiệp này sớm muộn cũng thuộc về kẻ khác!",},
        [5764] = {13101,"Pháp Chính","Lưu Bị","Nay ta nguyện theo về với ngài!","Nay ta nguyện theo về với ngài!",},
        [5765] = {13101,"Bàng Thống","Lưu Bị","Ở trên Yến Tiệc chúng ta sẽ diệt Lưu Chương, mai phục 100 Đao Phủ Thủ, chúa công ném ly chính là hiệu lệnh.","Ở trên Yến Tiệc chúng ta sẽ diệt Lưu Chương, mai phục 100 Đao Phủ Thủ, chúa công ném ly chính là hiệu lệnh.",},
        [5766] = {13101,"Lưu Bị","","Lưu Quý Ngọc là huynh đệ tông thất với ta, sao nỡ giành cơ đồ của hắn! Các ngươi đừng nói nữa!","Lưu Quý Ngọc là huynh đệ tông thất với ta, sao nỡ giành cơ đồ của hắn! Các ngươi đừng nói nữa!",},
        [5767] = {13102,"Thuộc Hạ Lưu Chương","Lưu Chương","Chúa công gọi Lưu Huyền Đức đến Ích Châu, e là dẫn sói vào nhà, hay là cho hắn đi về sớm để tránh hậu họa.","Chúa công gọi Lưu Huyền Đức đến Ích Châu, e là dẫn sói vào nhà, hay là cho hắn đi về sớm để tránh hậu họa.",},
        [5768] = {13102,"Lưu Chương","Thuộc Hạ Lưu Chương","Huynh trưởng ta Lưu Huyền Đức, hơn hẳn người khác.","Huynh trưởng ta Lưu Huyền Đức, hơn hẳn người khác.",},
        [5769] = {13102,"Thuộc Hạ Lưu Chương","Lưu Chương","Tuy Huyền Đức không có ý này, nhưng thủ hạ của hắn đều muốn chiếm Tây Xuyên để mưu cầu phú quý.","Tuy Huyền Đức không có ý này, nhưng thủ hạ của hắn đều muốn chiếm Tây Xuyên để mưu cầu phú quý.",},
        [5770] = {13102,"Lưu Chương","Thuộc Hạ Lưu Chương","Các ngươi đừng nói nữa, không được ly gián tình huynh đệ của bọn ta.","Các ngươi đừng nói nữa, không được ly gián tình huynh đệ của bọn ta.",},
        [5771] = {13103,"Lưu Chương","Lưu Bị","Ta nhận được báo cáo, Trương Lỗ bỗng nhiên chỉnh đốn binh mã, sẽ tấn công Hà Manh Quan. Huyền Đức có kế sách hay gì?","Ta nhận được báo cáo, Trương Lỗ bỗng nhiên chỉnh đốn binh mã, sẽ tấn công Hà Manh Quan. Huyền Đức có kế sách hay gì?",},
        [5772] = {13103,"Lưu Bị","Lưu Chương","Ta nguyện dẫn binh canh giữ Hà Manh Quan, đối phó Trương Lỗ.","Ta nguyện dẫn binh canh giữ Hà Manh Quan, đối phó Trương Lỗ.",},
        [5773] = {13103,"Lưu Chương","Lưu Bị","Vậy thì vất vả rồi.","Vậy thì vất vả rồi.",},
        [5774] = {13201,"Cố Ung","Tôn Quyền","Nghe nói Lưu Bị dẫn quân đến vùng núi hiểm trở chưa thể về sớm. Sao không sai quân chặn cửa Xuyên, bịt đường về của hắn, sau đó khởi binh Kinh Châu?","Nghe nói Lưu Bị dẫn quân đến vùng núi hiểm trở chưa thể về sớm. Sao không sai quân chặn cửa Xuyên, bịt đường về của hắn, sau đó khởi binh Kinh Châu?",},
        [5775] = {13201,"Tôn Quyền","Cố Ung","Quả là diệu kế!","Quả là diệu kế!",},
        [5776] = {13201,"Ngô Quốc Thái","Tôn Quyền","Kẻ hiến kế này thật quá lắm! Ta chỉ có mỗi đứa con gái, đã gả cho Lưu Bị, nay lại xuất binh, định hại con gái ta sao?","Kẻ hiến kế này thật quá lắm! Ta chỉ có mỗi đứa con gái, đã gả cho Lưu Bị, nay lại xuất binh, định hại con gái ta sao?",},
        [5777] = {13201,"Ngô Quốc Thái","Tôn Quyền","Ngươi có được cơ nghiệp của phụ thân huynh trưởng, sở hữu 81 châu còn chưa đủ, giờ còn ham lợi nhỏ mà không niệm tình cốt nhục!","Ngươi có được cơ nghiệp của phụ thân huynh trưởng, sở hữu 81 châu còn chưa đủ, giờ còn ham lợi nhỏ mà không niệm tình cốt nhục!",},
        [5778] = {13201,"Tôn Quyền","Ngô Quốc Thái","Mẫu thân nói phải, không dám cãi lời.","Mẫu thân nói phải, không dám cãi lời.",},
        [5779] = {13201,"Trương Chiêu","Tôn Quyền","Chúa công, ta có kế này, hay là như vậy, như vậy...","Chúa công, ta có kế này, hay là như vậy, như vậy...",},
        [5780] = {13202,"Chu Thiện","Thượng Hương","Đại nhân! Quốc Thái lâm trọng bệnh, sớm chiều chỉ tưởng niệm phu nhân. Nói phu nhân dẫn A Đẩu đến gặp mặt. Nếu đến trễ, e là không thể gặp nữa.","Đại nhân! Quốc Thái lâm trọng bệnh, sớm chiều chỉ tưởng niệm phu nhân. Nói phu nhân dẫn A Đẩu đến gặp mặt. Nếu đến trễ, e là không thể gặp nữa.",},
        [5781] = {13202,"Thượng Hương","Chu Thiện","Hoàng Thúc dẫn binh đi xa, giờ ta muốn về, phải thông báo quân sư trước.","Hoàng Thúc dẫn binh đi xa, giờ ta muốn về, phải thông báo quân sư trước.",},
        [5782] = {13202,"Chu Thiện","Thượng Hương","Nếu quân sư nói phải chờ Hoàng Thúc về thì sao? Thời gian không còn nhiều, chúng ta đã chuẩn bị thuyền chỉ chờ phu nhân lên xe ra khỏi thành thôi.","Nếu quân sư nói phải chờ Hoàng Thúc về thì sao? Thời gian không còn nhiều, chúng ta đã chuẩn bị thuyền chỉ chờ phu nhân lên xe ra khỏi thành thôi.",},
        [5783] = {13202,"Thượng Hương","Chu Thiện","Được, chúng ta đi.","Được, chúng ta đi.",},
        [5784] = {13203,"Triệu Vân","","Không được chèo thuyền! Hãy cho ta tiễn phu nhân! Nếu phu nhân muốn đi, chỉ có một lời này, chủ mẫu muốn đi đâu? Tại sao không thông báo với quân sư?","Không được chèo thuyền! Hãy cho ta tiễn phu nhân! Nếu phu nhân muốn đi, chỉ có một lời này, chủ mẫu muốn đi đâu? Tại sao không thông báo với quân sư?",},
        [5785] = {13203,"Thượng Hương","Triệu Vân","Mẫu thân ta bệnh nặng nguy cấp, không có thời gian thông báo.","Mẫu thân ta bệnh nặng nguy cấp, không có thời gian thông báo.",},
        [5786] = {13203,"Triệu Vân","Thượng Hương","Chủ mẫu có thể đi thăm bệnh, nhưng để tiểu chủ nhân lại. (Kéo lấy A Đẩu)","Chủ mẫu có thể đi thăm bệnh, nhưng để tiểu chủ nhân lại. (Kéo lấy A Đẩu)",},
        [5787] = {13203,"Trương Phi","Thượng Hương","Tẩu tẩu để cháu lại! (Trảm Chu Thiện)","Tẩu tẩu để cháu lại! (Trảm Chu Thiện)",},
        [5788] = {13203,"Thượng Hương","Trương Phi","Các ngươi sao dám vô lễ như vậy!","Các ngươi sao dám vô lễ như vậy!",},
        [5789] = {13203,"Trương Phi","Thượng Hương","Ca ca ta là Hoàng Thúc Đại Hán, sẽ không làm bẽ mặt tẩu tẩu nữa, hôm nay tạm biệt, nếu nhớ ân nghĩa ca ca thì hãy về sớm.","Ca ca ta là Hoàng Thúc Đại Hán, sẽ không làm bẽ mặt tẩu tẩu nữa, hôm nay tạm biệt, nếu nhớ ân nghĩa ca ca thì hãy về sớm.",},
        [5790] = {13301,"Đổng Chiêu","","Công lao của thừa tướng đứng đầu thiên hạ, đâu lẽ nào chỉ được bằng vai với bá quan trong triều?","Công lao của thừa tướng đứng đầu thiên hạ, đâu lẽ nào chỉ được bằng vai với bá quan trong triều?",},
        [5791] = {13301,"Đổng Chiêu","","Theo ý vi thần, ngài nên được nhận tước Ngụy Công, ban Cửu Tích để ghi nhận công đức.","Theo ý vi thần, ngài nên được nhận tước Ngụy Công, ban Cửu Tích để ghi nhận công đức.",},
        [5792] = {13301,"Tuân Úc","Đổng Chiêu","Không được! Thừa tướng vốn dấy binh vì nghĩa, khuông phò Hán thất, ấy là cái chí trung trinh, cái nghĩa khiêm nhường! Quân tử lấy đức cảm hóa lòng người, không nên làm vậy!","Không được! Thừa tướng vốn dấy binh vì nghĩa, khuông phò Hán thất, ấy là cái chí trung trinh, cái nghĩa khiêm nhường! Quân tử lấy đức cảm hóa lòng người, không nên làm vậy!",},
        [5793] = {13301,"Đổng Chiêu","Tuân Úc","Làm sao để một người cản trở ý muốn của nhiều người?","Làm sao để một người cản trở ý muốn của nhiều người?",},
        [5794] = {13301,"Tuân Úc","Đổng Chiêu","Hôm nay ta không muốn thấy chuyện này!","Hôm nay ta không muốn thấy chuyện này!",},
        [5795] = {13302,"Tào Tháo","","Ta muốn dấy binh xuống Giang Nam, muốn Tuân Lệnh Quân đồng hành với ta.","Ta muốn dấy binh xuống Giang Nam, muốn Tuân Lệnh Quân đồng hành với ta.",},
        [5796] = {13302,"Thị Vệ","Tào Tháo","Tuân Lệnh Quân không khỏe, nói không thể đi xa, chỉ có thể ở Thọ Xuyến tịnh dưỡng.","Tuân Lệnh Quân không khỏe, nói không thể đi xa, chỉ có thể ở Thọ Xuyến tịnh dưỡng.",},
        [5797] = {13302,"Tào Tháo","Thị Vệ","Được, phái người đem hộp thức ăn đến cho hắn.","Được, phái người đem hộp thức ăn đến cho hắn.",},
        [5798] = {13303,"Thị Vệ","Tuân Úc","Tuân Lệnh Quân, Thừa Tướng gửi cho ngài hộp thức ăn này.","Tuân Lệnh Quân, Thừa Tướng gửi cho ngài hộp thức ăn này.",},
        [5799] = {13303,"Tuân Úc","","Sao lại... trống không! Ta hiểu rồi, Thừa Tướng muốn nói ta không có bổng lộc để ăn! (Uống độc tự vẫn)","Sao lại... trống không! Ta hiểu rồi, Thừa Tướng muốn nói ta không có bổng lộc để ăn! (Uống độc tự vẫn)",},
        [5800] = {13401,"Thị Vệ","","Nhìn dọc theo dòng sông, vô số lá cờ, không biết binh lính tụ tập ở đâu.","Nhìn dọc theo dòng sông, vô số lá cờ, không biết binh lính tụ tập ở đâu.",},
        [5801] = {13401,"Tào Tháo","","Theo ta lên núi xem. Nhìn chiến thuyền của đối phương, chia thành các đội, xếp theo thứ tự. Cờ chia làm 6 màu, vũ khí nổi bật.","Theo ta lên núi xem. Nhìn chiến thuyền của đối phương, chia thành các đội, xếp theo thứ tự. Cờ chia làm 6 màu, vũ khí nổi bật.",},
        [5802] = {13401,"Tào Tháo","","Quả là oai phong tuấn tú! Sinh con nên sinh đứa như Tôn Trọng Mưu! Con của Lưu Cảnh Thăng thật chẳng ra sao!","Quả là oai phong tuấn tú! Sinh con nên sinh đứa như Tôn Trọng Mưu! Con của Lưu Cảnh Thăng thật chẳng ra sao!",},
        [5803] = {13402,"Tôn Quyền","Tào Tháo","Trung Nguyên giàu có, vì sao thừa tướng lại đến xâm phạm Giang Đông?","Trung Nguyên giàu có, vì sao thừa tướng lại đến xâm phạm Giang Đông?",},
        [5804] = {13402,"Tào Tháo","Tôn Quyền","Ngươi là hạ thần, không tôn kính vương thất. Ta phụng mệnh thiên tử đến thảo phạt ngươi.","Ngươi là hạ thần, không tôn kính vương thất. Ta phụng mệnh thiên tử đến thảo phạt ngươi.",},
        [5805] = {13402,"Tôn Quyền","Tào Tháo","Thật là nực cười. Thiên hạ ai không biết ngươi bắt thiên tử uy hiếp chư hầu nghe lệnh? Ta phải thảo phạt ngươi để cứu lấy đất nước mới đúng.","Thật là nực cười. Thiên hạ ai không biết ngươi bắt thiên tử uy hiếp chư hầu nghe lệnh? Ta phải thảo phạt ngươi để cứu lấy đất nước mới đúng.",},
        [5806] = {13403,"Tào Tháo","","Tối qua ta nằm mơ thấy trên không trung có hai mặt trời. Tôn Quyền không phải nhân vật tầm thường, theo như dấu hiệu này, sau này ắt là đế vương.","Tối qua ta nằm mơ thấy trên không trung có hai mặt trời. Tôn Quyền không phải nhân vật tầm thường, theo như dấu hiệu này, sau này ắt là đế vương.",},
        [5807] = {13403,"Tào Tháo","","Chiến đấu vài trận, thắng có bại có, nay lại mưa triền miên, quân sĩ phải lặn lội trong sình lầy, vô cùng khốn khổ, hay là rút binh?","Chiến đấu vài trận, thắng có bại có, nay lại mưa triền miên, quân sĩ phải lặn lội trong sình lầy, vô cùng khốn khổ, hay là rút binh?",},
        [5808] = {13403,"Tùy Tùng","Tào Tháo","Báo cáo, Đông Ngô có sứ mang thư đến.","Báo cáo, Đông Ngô có sứ mang thư đến.",},
        [5809] = {13403,"Tào Tháo","","Tôn Quyền nói trong thư: Ta và Thừa Tướng, đều là thần tể của triều Hán... ngày hôm nay khi nước sông vừa dâng lên, ngài hãy mau rời đi. Nếu không thì sẽ có đại họa Xích Bích.","Tôn Quyền nói trong thư: Ta và Thừa Tướng, đều là thần tể của triều Hán... ngày hôm nay khi nước sông vừa dâng lên, ngài hãy mau rời đi. Nếu không thì sẽ có đại họa Xích Bích.",},
        [5810] = {13403,"Tào Tháo","","Công nghi tự tư yên. Túc hạ bất tử, cô bất đắc an. Ha ha ha! Tôn Trọng Mưu không lừa ta! Lui binh!","Công nghi tự tư yên. Túc hạ bất tử, cô bất đắc an. Ha ha ha! Tôn Trọng Mưu không lừa ta! Lui binh!",},
        [5811] = {13501,"Lưu Bị","","Tào Tháo chiến Tôn Quyền, cho dù ai thắng thì chắc chắn có được Kinh Châu, ta nên làm sao?","Tào Tháo chiến Tôn Quyền, cho dù ai thắng thì chắc chắn có được Kinh Châu, ta nên làm sao?",},
        [5812] = {13501,"Nhân vật","Lưu Bị","Đại ca yên tâm, có Không Minh quân sư ở Kinh Châu, e là Đông Ngô không dám đến mạo phạm. Đại ca có thể đến hỏi Lưu Chương lấy binh mã lương tiền để chống đỡ Tào Tháo.","Đại ca yên tâm, có Không Minh quân sư ở Kinh Châu, e là Đông Ngô không dám đến mạo phạm. Đại ca có thể đến hỏi Lưu Chương lấy binh mã lương tiền để chống đỡ Tào Tháo.",},
        [5813] = {13501,"Lưu Bị","Nhân vật","Được, ta sẽ viết thư, nhờ Tứ Đệ gửi cho Lưu Chương.","Được, ta sẽ viết thư, nhờ Tứ Muội gửi cho Lưu Chương.",},
        [5814] = {13502,"Dương Hoài","Lưu Chương","Từ khi Lưu Bị đến đây, tìm đủ cách thu phục lòng dân. Nay lại yêu cầu chúng ta cung cấp quân lương, không nên đồng ý!","Từ khi Lưu Bị đến đây, tìm đủ cách thu phục lòng dân. Nay lại yêu cầu chúng ta cung cấp quân lương, không nên đồng ý!",},
        [5815] = {13502,"Lưu Chương","Dương Hoài","Ta và Huyền Đức có tình huynh đệ, sao có thể không cho hắn?","Ta và Huyền Đức có tình huynh đệ, sao có thể không cho hắn?",},
        [5816] = {13502,"Dương Hoài","Lưu Chương","Lưu Bị là người có dã tâm, để hắn ở lại đất Thục lâu khác gì dẫn hổ vào nhà. Nay còn phải cho hắn binh lính và lương tiền, chẳng phải hắn như hổ thêm cánh sao?","Lưu Bị là người có dã tâm, để hắn ở lại đất Thục lâu khác gì dẫn hổ vào nhà. Nay còn phải cho hắn binh lính và lương tiền, chẳng phải hắn như hổ thêm cánh sao?",},
        [5817] = {13502,"Hoàng Quyền","Dương Hoài","Chúa công có thể cho Lưu Bị những binh lính già yếu, vừa không tổn hại tình huynh đệ, cũng không thể như hổ thêm cánh.","Chúa công có thể cho Lưu Bị những binh lính già yếu, vừa không tổn hại tình huynh đệ, cũng không thể như hổ thêm cánh.",},
        [5818] = {13503,"Lưu Bị","","Lẽ nào lại như vậy! Ta giúp Lưu Chương chống địch, phí tâm lao lực! Nay hắn keo kiệt chỉ cho ta binh lính già yếu, làm sao ta có thể tận tâm làm việc cho hắn?","Lẽ nào lại như vậy! Ta giúp Lưu Chương chống địch, phí tâm lao lực! Nay hắn keo kiệt chỉ cho ta binh lính già yếu, làm sao ta có thể tận tâm làm việc cho hắn?",},
        [5819] = {13503,"Bàng Thống","Lưu Bị","Chúa công xưa nay trọng nhân nghĩa, hôm nay mà tức giận, thì công sức xưa nay mất hết.","Chúa công xưa nay trọng nhân nghĩa, hôm nay mà tức giận, thì công sức xưa nay mất hết.",},
        [5820] = {13503,"Lưu Bị","Bàng Thống","Vậy giờ nên làm sao?","Vậy giờ nên làm sao?",},
        [5821] = {13503,"Bàng Thống","Lưu Bị","Ta có ba cách, giờ nhân lúc đêm khuya đi đường tắt xâm nhập Thành Đô, là thượng kế; giả vờ về Kinh Châu, Dương Hoài, Cao Bái nghe tin chắc chắn đến tiễn, ","Ta có ba cách, giờ nhân lúc đêm khuya đi đường tắt xâm nhập Thành Đô, là thượng kế; giả vờ về Kinh Châu, Dương Hoài, Cao Bái nghe tin chắc chắn đến tiễn, ",},
        [5822] = {13503,"Bàng Thống","Lưu Bị","nhân cơ hội diệt họ, đoạt cửa ải, chiếm Bồi Thành sau đó là Thành Đô, đây là trung kế; trả Bạch Đế, trong đêm về Kinh Châu, từ từ nghĩ cách, đây là hạ kế.","nhân cơ hội diệt họ, đoạt cửa ải, chiếm Bồi Thành sau đó là Thành Đô, đây là trung kế; trả Bạch Đế, trong đêm về Kinh Châu, từ từ nghĩ cách, đây là hạ kế.",},
        [5823] = {13503,"Nhân vật","Lưu Bị","Ba kế này, thượng kế quá gấp gáp, hạ kế quá chậm chạp; trung kế vừa phải, nên làm theo kế này.","Ba kế này, thượng kế quá gấp gáp, hạ kế quá chậm chạp; trung kế vừa phải, nên làm theo kế này.",},
        [5824] = {13601,"Trương Tùng","","Tại sao Lưu Hoàng Thúc không nghe theo ta, muốn về Kinh Châu chứ! Buồn lòng quá!","Tại sao Lưu Hoàng Thúc không nghe theo ta, muốn về Kinh Châu chứ! Buồn lòng quá!",},
        [5825] = {13601,"Trương Túc","","Nhặt được thư Trương Tùng viết cho Lưu Bị:... Tùng làm nội ứng, nhớ đừng nhầm lẫn! Cái gì! Trương Tùng muốn tạo phản! Ta phải báo với chúa công!","Nhặt được thư Trương Tùng viết cho Lưu Bị:... Tùng làm nội ứng, nhớ đừng nhầm lẫn! Cái gì! Trương Tùng muốn tạo phản! Ta phải báo với chúa công!",},
        [5826] = {13601,"Lưu Chương","","Gì! Ta đối với Trương Tùng không tệ, hắn cấu kết người ngoài tạo phản ư! Người đâu, diệt cả nhà Trương Tùng!","Gì! Ta đối với Trương Tùng không tệ, hắn cấu kết người ngoài tạo phản ư! Người đâu, diệt cả nhà Trương Tùng!",},
        [5827] = {13601,"Dương Hoài","","Ta và Cao Bái định giấu dao trong người, mượn cớ tiễn biệt lấy mạng Lưu Bị để trừ hậu hoạn.","Ta và Cao Bái định giấu dao trong người, mượn cớ tiễn biệt lấy mạng Lưu Bị để trừ hậu hoạn.",},
        [5828] = {13602,"Bàng Thống","Lưu Bị","Dương Hoài, Cao Bái đến thì phải đề phòng, nếu không đến thì chúng ta trực tiếp dậy binh đi chiếm ải, không được chậm trễ.","Dương Hoài, Cao Bái đến thì phải đề phòng, nếu không đến thì chúng ta trực tiếp dậy binh đi chiếm ải, không được chậm trễ.",},
        [5829] = {13602,"Lưu Bị","Bàng Thống","Gió thổi sập cờ có chữ Soái, tại sao?","Gió thổi sập cờ có chữ Soái, tại sao?",},
        [5830] = {13602,"Bàng Thống","Lưu Bị","Là cảnh báo, Dương Hoài, Cao Bái chắc chắn có ý hành thích, phải đề phòng.","Là cảnh báo, Dương Hoài, Cao Bái chắc chắn có ý hành thích, phải đề phòng.",},
        [5831] = {13602,"Dương Hoài","Lưu Bị","Nghe nói Lưu Hoàng Thúc sắp về Kinh Châu, huynh đệ hai ta đến tiễn.","Nghe nói Lưu Hoàng Thúc sắp về Kinh Châu, huynh đệ hai ta đến tiễn.",},
        [5832] = {13602,"Nhân vật","Dương Hoài","Nếu là tiễn biệt thì sao phải cầm theo dao? Đại ca ta một lòng tận tụy cho Lưu Chương, các ngươi đến ly gián lại còn hành thích, tội không thể tha!","Nếu là tiễn biệt thì sao phải cầm theo dao? Đại ca ta một lòng tận tụy cho Lưu Chương, các ngươi đến ly gián lại còn hành thích, tội không thể tha!",},
        [5833] = {13603,"Nhân vật","","Các Tướng Sĩ còn lại nghe rõ! Hai người này ly gián huynh đệ Hoàng Thúc, lại giấu dao hành thích nên sẽ bị diệt. Các ngươi vô tội không cần hoảng sợ.","Các Tướng Sĩ còn lại nghe rõ! Hai người này ly gián huynh đệ Hoàng Thúc, lại giấu dao hành thích nên sẽ bị diệt. Các ngươi vô tội không cần hoảng sợ.",},
        [5834] = {13603,"Bàng Thống","","Giờ còn phải nhờ mọi người dẫn đường đưa đại quân ta vào ải. Mọi người đều được trọng thưởng.","Giờ còn phải nhờ mọi người dẫn đường đưa đại quân ta vào ải. Mọi người đều được trọng thưởng.",},
        [5835] = {13701,"Lưu Bị","","Giờ chúng ta có Bồi Thủy Quan, tiếp đến phải chiếm Lạc Thành. Linh Bao, Đặng Hiền dẫn 2 vạn quân đóng hai trại lớn, ai muốn dẫn trước lập công?","Giờ chúng ta có Bồi Thủy Quan, tiếp đến phải chiếm Lạc Thành. Linh Bao, Đặng Hiền dẫn 2 vạn quân đóng hai trại lớn, ai muốn dẫn trước lập công?",},
        [5836] = {13701,"Hoàng Trung","Lưu Bị","Lão phu tình nguyện đi.","Lão phu tình nguyện đi.",},
        [5837] = {13701,"Ngụy Diên","Lưu Bị","Lão tướng quân tuổi đã cao, sao đi được? Tiểu tướng bất tài tình nguyện đi.","Lão tướng quân tuổi đã cao, sao đi được? Tiểu tướng bất tài tình nguyện đi.",},
        [5838] = {13701,"Bàng Thống","Ngụy Diên","Hai vị không cần tranh, cả hai đều có thể dẫn một đội binh mã, mỗi người một trại, ai tranh được một trại trước sẽ thắng.","Hai vị không cần tranh, cả hai đều có thể dẫn một đội binh mã, mỗi người một trại, ai tranh được một trại trước sẽ thắng.",},
        [5839] = {13702,"Ngụy Diên","","Hoàng Trung Tướng Quân còn chưa xuất phát, hay là ta xuất phát trước, giành công đầu.","Hoàng Trung Tướng Quân còn chưa xuất phát, hay là ta xuất phát trước, giành công đầu.",},
        [5840] = {13702,"Ngụy Diên","","Nếu đã xuất phát rồi, tại sao không lấy cả hai trại, có thể lập hai công lao.","Nếu đã xuất phát rồi, tại sao không lấy cả hai trại, có thể lập hai công lao.",},
        [5841] = {13702,"Đặng Hiền","Ngụy Diên","Ngụy Diên nhanh đầu hàng!","Ngụy Diên nhanh đầu hàng!",},
        [5842] = {13702,"Ngụy Diên","","Không hay rồi, rút mau!","Không hay rồi, rút mau!",},
        [5843] = {13703,"Hoàng Trung","","Hoàng Trung ở đây! Đặng Hiền mau ra nạp mạng!","Hoàng Trung ở đây! Đặng Hiền mau ra nạp mạng!",},
        [5844] = {13703,"Nhân vật","","Ta đã đoạt trại, ngươi còn trốn đi đâu?","Ta đã đoạt trại, ngươi còn trốn đi đâu?",},
        [5845] = {13801,"Lưu Bị","","Quân Sư từ Kinh Châu gửi thư về, nói quan sát tinh tượng có chuyện không hay xảy ra, Chủ Tướng Soái lành ít dữ nhiều, phải cẩn thận.","Quân Sư từ Kinh Châu gửi thư về, nói quan sát tinh tượng có chuyện không hay xảy ra, Chủ Tướng Soái lành ít dữ nhiều, phải cẩn thận.",},
        [5846] = {13801,"Lưu Bị","","Đã vậy thì hay là chúng ta đi về trước đi.","Đã vậy thì hay là chúng ta đi về trước đi.",},
        [5847] = {13801,"Bàng Thống","Lưu Bị","Chiêm Tinh Thuật ta cũng biết đôi chút. Đại hung chi tướng đã ứng nghiệm trên Linh Bao rồi, chúa công không cần lo, có thể tiến binh.","Chiêm Tinh Thuật ta cũng biết đôi chút. Đại hung chi tướng đã ứng nghiệm trên Linh Bao rồi, chúa công không cần lo, có thể tiến binh.",},
        [5848] = {13801,"Lưu Bị","Bàng Thống","Được, vậy thì chúng ta chia hai phe đi chiếm Lạc Thành.","Được, vậy thì chúng ta chia hai phe đi chiếm Lạc Thành.",},
        [5849] = {13802,"Lưu Bị","Bàng Thống","Con ngựa thối, dám hất Quân Sư xuống! Sao Quân Sư lại cưỡi con ngựa kém cỏi này!","Con ngựa thối, dám hất Quân Sư xuống! Sao Quân Sư lại cưỡi con ngựa kém cỏi này!",},
        [5850] = {13802,"Bàng Thống","Lưu Bị","Cưỡi con ngựa này đã lâu, chưa từng như thế.","Cưỡi con ngựa này đã lâu, chưa từng như thế.",},
        [5851] = {13802,"Lưu Bị","Bàng Thống","Nếu cưỡi ngựa không quen, ra trận rất nguy hiểm. Quân sư hãy dùng ngựa trắng của ta.","Nếu cưỡi ngựa không quen, ra trận rất nguy hiểm. Quân sư hãy dùng ngựa trắng của ta.",},
        [5852] = {13802,"Bàng Thống","Lưu Bị","Cảm tạ hậu ơn của chúa công, không biết lấy gì báo đáp.","Cảm tạ hậu ơn của chúa công, không biết lấy gì báo đáp.",},
        [5853] = {13803,"Bàng Thống","","Đây là nơi đâu?","Đây là nơi đâu?",},
        [5854] = {13803,"Bàng Thống","","Địa danh này là Lạc Phụng Pha, ta hiệu là Phượng Sồ, nơi này bất lợi cho ta, mau rút.","Địa danh này là Lạc Phụng Pha, ta hiệu là Phượng Sồ, nơi này bất lợi cho ta, mau rút.",},
        [5855] = {13803,"Trương Nhiệm","","Kẻ cưỡi ngựa trắng ắt là Lưu Bị, hắn đến rồi, Cung Tiễn Thủ! Bắn!","Kẻ cưỡi ngựa trắng ắt là Lưu Bị, hắn đến rồi, Cung Tiễn Thủ! Bắn!",},
        [5856] = {13803,"Bàng Thống","","Không hay rồi, có mai phục! Mau rút!","Không hay rồi, có mai phục! Mau rút!",},
        [5857] = {13901,"Gia Cát Lượng","","Bi thay! Đau thay! Sao phía Tây rơi, Bàng Sĩ Nguyên chắc chắn mất mạng!","Bi thay! Đau thay! Sao phía Tây rơi, Bàng Sĩ Nguyên chắc chắn mất mạng!",},
        [5858] = {13901,"Gia Cát Lượng","","Ta chiêm tinh thấy năm nay Canh Tinh ở phía Tây, bất lợi cho Quân Sư; Thiên Cẩu phạm Quân Sư, Thái Bạch đến Lạc Thành, đã gửi thư cho chúa công dặn người cẩn trọng.","Ta chiêm tinh thấy năm nay Canh Tinh ở phía Tây, bất lợi cho Quân Sư; Thiên Cẩu phạm Quân Sư, Thái Bạch đến Lạc Thành, đã gửi thư cho chúa công dặn người cẩn trọng.",},
        [5859] = {13901,"Gia Cát Lượng","","Nay chủ ta mất một cánh tay! Sẽ nhận được tin tức ngay.","Nay chủ ta mất một cánh tay! Sẽ nhận được tin tức ngay.",},
        [5860] = {13901,"Quan Bình","Gia Cát Lượng","Báo cáo Gia Cát Quân Sư, Bàng Quân Sư bị Trương Nhiệm bắn tên qua đời ở Lạc Phụng Pha","Báo cáo Gia Cát Quân Sư, Bàng Quân Sư bị Trương Nhiệm bắn tên qua đời ở Lạc Phụng Pha",},
        [5861] = {13902,"Gia Cát Lượng","","Giờ chúa công ở Bồi Quan tiến thoái lưỡng nan, Lượng không thể không đi.","Giờ chúa công ở Bồi Quan tiến thoái lưỡng nan, Lượng không thể không đi.",},
        [5862] = {13902,"Quan Vũ","Gia Cát Lượng","Nếu quân sư đi, ai sẽ bảo vệ Kinh Châu? Đây là nơi quan trọng, không được xem nhẹ.","Nếu quân sư đi, ai sẽ bảo vệ Kinh Châu? Đây là nơi quan trọng, không được xem nhẹ.",},
        [5863] = {13902,"Gia Cát Lượng","Quan Vũ","Thư của chúa công tuy không nêu tên người này nhưng ta đã hiểu ý. chúa công dặn ta tự xem xét dùng nhân tài, Vân Trường Công Phương có thể đảm trọng trách này.","Thư của chúa công tuy không nêu tên người này nhưng ta đã hiểu ý. chúa công dặn ta tự xem xét dùng nhân tài, Vân Trường Công Phương có thể đảm trọng trách này.",},
        [5864] = {13902,"Quan Vũ","Gia Cát Lượng","Đại trượng phu đã nhận trọng trách, thề không bỏ cuộc.","Đại trượng phu đã nhận trọng trách, thề không bỏ cuộc.",},
        [5865] = {13903,"Gia Cát Lượng","Quan Vũ","Nếu Tào Tháo dẫn binh đến, phải đối phó thế nào?","Nếu Tào Tháo dẫn binh đến, phải đối phó thế nào?",},
        [5866] = {13903,"Quan Vũ","Gia Cát Lượng","Dùng sức chống đỡ.","Dùng sức chống đỡ.",},
        [5867] = {13903,"Gia Cát Lượng","Quan Vũ","Nếu Tào Tháo, Tôn Quyền cùng dậy binh, vậy phải làm sao?","Nếu Tào Tháo, Tôn Quyền cùng dậy binh, vậy phải làm sao?",},
        [5868] = {13903,"Quan Vũ","Gia Cát Lượng","Chia ra chống đỡ.","Chia ra chống đỡ.",},
        [5869] = {13903,"Gia Cát Lượng","Quan Vũ","Nếu vậy thì Kinh Châu sẽ lâm nguy. Ta có 8 chữ, Tướng Quân hãy nhớ, giữ được Kinh Châu.","Nếu vậy thì Kinh Châu sẽ lâm nguy. Ta có 8 chữ, Tướng Quân hãy nhớ, giữ được Kinh Châu.",},
        [5870] = {13903,"Quan Vũ","Gia Cát Lượng","Quân Sư hãy nói, lời của Quân Sư, khắc ghi trong lòng.","Quân Sư hãy nói, lời của Quân Sư, khắc ghi trong lòng.",},
        [5871] = {13903,"Gia Cát Lượng","Quan Vũ","Bắc cự Tào Tháo, đông hòa Tôn Quyền.","Bắc cự Tào Tháo, đông hòa Tôn Quyền.",},
        [5872] = {14001,"Gia Cát Lượng","Trương Phi","Tây Xuyên nhiều hào kiệt, không thể khinh địch. Đến bất cứ đâu, nên tử tế, không nên đắc ý đánh đập sĩ tốt. Mong Tướng Quân sớm về Lạc Thành, đừng nên chậm trễ.","Tây Xuyên nhiều hào kiệt, không thể khinh địch. Đến bất cứ đâu, nên tử tế, không nên đắc ý đánh đập sĩ tốt. Mong Tướng Quân sớm về Lạc Thành, đừng nên chậm trễ.",},
        [5873] = {14001,"Trương Phi","Gia Cát Lượng","Nghe theo Quân Sư, không mảy may động vào những người đã hàng.","Nghe theo Quân Sư, không mảy may động vào những người đã hàng.",},
        [5874] = {14002,"Nghiêm Nhan Quân Sĩ","","Trương Phi tại Đương Dương Trường Bản, một tiếng hét làm cho trăm vạn Tào Binh rút lui, không thể khinh địch. Giờ chúng ta kiên quyết không ra, họ không có lương thực sẽ tự rút lui.","Trương Phi tại Đương Dương Trường Bản, một tiếng hét làm cho trăm vạn Tào Binh rút lui, không thể khinh địch. Giờ chúng ta kiên quyết không ra, họ không có lương thực sẽ tự rút lui.",},
        [5875] = {14002,"Tùy Tùng","Nghiêm Nhan","Tướng Quân ta đã nói, ngươi đến đầu hàng sớm, tha cho tính mạng bá tánh trong thành, nếu không quy thuận sẽ san bằng tất cả, già trẻ không tha!","Tướng Quân ta đã nói, ngươi đến đầu hàng sớm, tha cho tính mạng bá tánh trong thành, nếu không quy thuận sẽ san bằng tất cả, già trẻ không tha!",},
        [5876] = {14002,"Nghiêm Nhan","","Lẽ nào lại như vậy! Thất phu sao dám vô lễ như vậy! Người đâu hãy cho hắn biết tay!","Lẽ nào lại như vậy! Thất phu sao dám vô lễ như vậy! Người đâu hãy cho hắn biết tay!",},
        [5877] = {14003,"Trương Phi","","Tên lão thất phu Nghiêm Nhan làm ta tức quá đi!","Tên lão thất phu Nghiêm Nhan làm ta tức quá đi!",},
        [5878] = {14003,"Tùy Tùng","Trương Phi","Tướng Quân không cần lo lắng, gần đây Quân Sĩ đốn củi tìm được một con đường nhỏ, dẫn đến Ba Quận.","Tướng Quân không cần lo lắng, gần đây Quân Sĩ đốn củi tìm được một con đường nhỏ, dẫn đến Ba Quận.",},
        [5879] = {14003,"Trương Phi","","Không được chậm trễ, canh hai tạo phản, nhân canh ba trăng sáng, lặng lẽ lên đường. Ta đi trước dẫn đường, các ngươi lần lượt theo sau.","Không được chậm trễ, canh hai tạo phản, nhân canh ba trăng sáng, lặng lẽ lên đường. Ta đi trước dẫn đường, các ngươi lần lượt theo sau.",},
        [5880] = {14003,"Nghiêm Nhan","","Quả nhiên Trương Phi không thể nhẫn nại đã trúng kế. Người phía trước chính là Trương Phi! Chúng ta hãy qua đó bắt hắn!","Quả nhiên Trương Phi không thể nhẫn nại đã trúng kế. Người phía trước chính là Trương Phi! Chúng ta hãy qua đó bắt hắn!",},
        [5881] = {14003,"Trương Phi","","Lão tặc đừng chạy! Ta ở đây này! Phía trước là giả thôi!","Lão tặc đừng chạy! Ta ở đây này! Phía trước là giả thôi!",},
        [5882] = {14003,"Nghiêm Nhan","","Nguy rồi! Trúng kế!","Nguy rồi! Trúng kế!",},
        [5883] = {14004,"Trương Phi","Nghiêm Nhan","Đại Tướng đến đây, tại sao không hàng, mà dám chống địch?","Đại Tướng đến đây, tại sao không hàng, mà dám chống địch?",},
        [5884] = {14004,"Nghiêm Nhan","Trương Phi","Các ngươi bất nghĩa! Xâm nhập Châu Quận của ta! Chỉ có tướng quân hy sinh, không có tướng quân đầu hàng!","Các ngươi bất nghĩa! Xâm nhập Châu Quận của ta! Chỉ có tướng quân hy sinh, không có tướng quân đầu hàng!",},
        [5885] = {14004,"Nghiêm Nhan","Trương Phi","Tên tặc thối, muốn diệt thì ngươi cứ việc!","Tên tặc thối, muốn diệt thì ngươi cứ việc!",},
        [5886] = {14004,"Trương Phi","Nghiêm Nhan","(Tự tay cởi trói, mặc quần áo cho Nghiêm Nhan) Lúc nãy nói lời mạo phạm, xin đừng trách. Ta biết lão tướng quân là một hào kiệt.","(Tự tay cởi trói, mặc quần áo cho Nghiêm Nhan) Lúc nãy nói lời mạo phạm, xin đừng trách. Ta biết lão tướng quân là một hào kiệt.",},
        [5887] = {14004,"Nghiêm Nhan","Trương Phi","(Bị ơn nghĩa của Trương Phi cảm động, đầu hàng) Tướng bại trận, nhận hậu ơn, không gì báo đáp, nguyện làm trâu làm ngựa.","(Bị ơn nghĩa của Trương Phi cảm động, đầu hàng) Tướng bại trận, nhận hậu ơn, không gì báo đáp, nguyện làm trâu làm ngựa.",},
