-- metatable
local mt = { 
    __index = function(t, k) 
        assert(__key_map[k], "cannot find " .. k .. " in hero_rank")
        if _lang ~= "cn" and _isExist  and t._lang_key_map[k] then
            return t._lang[t._lang_key_map[k]]
        end
        return t._raw[__key_map[k]]
    end
}

-- 
function hero_rank.length()
    return #hero_rank._data
end

-- 
function hero_rank.hasKey(k)
    if __key_map[k] == nil then
        return false
    else
        return true
    end
end

--
function hero_rank.indexOf(index)
    if index == nil or not hero_rank._data[index] then
        return nil
    end
    if Lang.lang ~= "cn" and Lang.lang ~= _lang then 
        _lang = Lang.lang 
        if Lang.isFileExist("app/i18n/".. _lang .."/config/hero_rank.lua") then _isExist =  true end
    end
    if _lang ~= "cn" and _isExist then
        local table = require( "app.i18n.".. _lang ..".config." .. "hero_rank" )
        return setmetatable({_raw = hero_rank._data[index], _lang=table._data[index], _lang_key_map=table.__key_map}, mt)
    end
    return setmetatable({_raw = hero_rank._data[index]}, mt)
end

--
function hero_rank.get(id,rank,limit)
    
    local k = id .. '_' .. rank .. '_' .. limit
    return hero_rank.indexOf(__index_id_rank_limit[k])
        
end

--
function hero_rank.set(id,rank,limit, key, value)
    local record = hero_rank.get(id,rank,limit)
    if record then
        local keyIndex = __key_map[key]
        if keyIndex then
            record._raw[keyIndex] = value
        end
    end
end

--
function hero_rank.index()
    return __index_id_rank_limit
end

return hero_rank