        [3] = {3,"가장 잘생긴 꽃미남이 가장 빠른 걸음을 걷는 걸 보여주지.","나에게는 무적 방패가 있다. 달리기할 때 옷자락 잡고 늘어질 생각하지 마.","지형을 보는데 능하지. 이 경기장의 지형은 이미 다 꿰뚫고 있어.","이 경기를 위해, 내가 새로운 스타일을 준비했어.",},
        [4] = {4,"여자 몸으로 경기에 참여했어요, 양보해 주세요.","나보다 빨리 뛰면, 채찍으로 용서치 않겠어.","나는 허리 아래가 다 다리인데, 달리기가 느리겠어?","남자라면 저에게 2장 거리를 양보해주세요.",},
        [5] = {5,"내가 작아 보인다고 무시하지 마, 달리기는 절대 뒤지지 않아.","오자양장 중에 내가 제일 빨라.","변장하고 시장을 왔다 갔다 했어, 다리 힘이 매우 좋다고.","작게 말해줄게, 매번 우금이 나에게 져.",},
        [6] = {6,"무겁고 가벼운 건 재보면 알지. 누가 빠르고 느린지는 겨뤄보면 알고.","달리기는 건강에 도움이 돼, 쉽게 병이 안 생기지.","아버지가 이렇게나 나를 사랑해주시는데, 아버지의 체면을 살려드려야지.","어제 형 조비와 달리기 연습을 했어. 형보다 느리지 않아.",},
        [7] = {7,"사마의에게 비키라고 해! 오늘 이 여신님이 일등을 가져가겠다!","이 경기에서 이겨서, 사마의가 날 새롭게 보게 할 거야!","백 부인이 젊긴 하지만, 나 같은 우아함은 없지!","후회하지 않는 자는 원망할 것도 없지.",},
        [8] = {8,"질풍 행자, 이 이름 어때? 이번에 이기면 이 이름을 쓸 거야.","악진의 말 듣지 마, 오자양장 중에 내가 제일 빠르다고.","잠시 후에 내 질풍 같은 속도를 보여주지.","악진이 또 내가 그 보다 빠르지 않다고 하면, 없애 버리겠어.",},
