        [19] = {19,"호랑이 밑에 호랑이가 난다더니, 아가씨도 뒤지지 않는구려.","저 관은병은 예쁘고, 똑똑한 데다, 무예도 출중해요. 아버님께 꼭 일등을 가져오겠습니다.","언젠가는 아버지를 뛰어넘을 거에요. 오늘 일등을 가져오는 것부터 시작이에요!","오늘 꼭 일등으로 뛰어서 아버지의 분을 풀어드릴 거에요.",},
        [20] = {20,"저는 어릴 적부터 말을 타고, 활을 쏘는 데 능숙했습니다. 달리기라면 저를 이기기 어려울 거예요!","꼭 우승을 가져다드리겠습니다!","아직 카운트 다운이야? 빨리 시작할 수 없나!","일등을 가져와서 저를 증명하겠어요!",},
        [21] = {21,"나는 강동소패왕 손백부다!","강동의 젊은이여, 어찌 천하를 두려워하는가!","나 손백부가 오늘 일등을 차지하겠다!","강동의 젊은이들, 잘 보게나!",},
        [22] = {22,"태양이 이렇게나 이글거리는데, 까맣게 타면 어쩌지요? 다행히도 연화유산이 저를 지켜줄 거에요!","백부님은요? 저에게 응원해 주신다고 하시고는.","이 경기에서 이기면 인생은 망친 거다, 너희가 감히 나를 이길 수 있겠나?","잠시만 저를 도와주세요. 저 뛰는 게 예쁜가요?",},
        [23] = {23,"흥, 여자라고 쉽게 보지 마세요!","공근이 말하길, 이기고 지는 건 중요하지 않다고 했어요. 즐거운 게 제일 중요하다고요!","히히, 내 성화쌍선이 분명 내가 우승을하게 도와줄 거야!","이기고 지는 건 중요하지 않습니다. 즐거운 게 제일 중요하지요!",},
