        [13] = {13,"이름이 바꿔도 제가 누군지 알 수 있겠어요?","심국 시대에도 달리기 대회가 있었나요?","봄날 초당에서 늘어지게 잤건만 창밖은 아직도 한낮이구나.","자신이 원하는 대로 하지 않으면 운명과 맞서 싸울 수 없습니다.",},
        [14] = {14,"이기고 싶어요!","방통이 10위에 올랐어요. 모르셨어요?","소패왕 손책, 후열열 공격이 대단하네요!","두 사람이 한 동이 탁주에도 기쁘게 만났는데, 예부터 지금까지 크고 작은 일 끊이지 않았지만 모두가 한낱 웃으며 하는 이야기일 뿐.",},
        [15] = {15,"빨리 이 경기를 이기고 싶어요!","서서를 육성하여 메인으로 썼더니 정말 좋았어요!","이기고 싶으신가요? 비결은 바로 저를 선택하시는 겁니다!","강자 중에 자연히 강한 자가 있고, 사기꾼이 사기꾼을 만나다.",},
        [16] = {16,"얼마나 기다렸는데 이제야 시작하다니. 재밌는 얘기를 해드릴 테니 함께 놀아봐요.","운이 좋은 사람이 되고 싶어요! 적생장수, 적색장비야 내게로 오라!","욕하고 허세를 부리는 것도 게임의 재미 중 하나죠.","자기 한 몸의 수양만을 힘쓴다면 어떻게 오랜 세월 이름이 남겠는가!",},
        [17] = {17,"2배 보너스 주실 분 없나요?","적색영혼이 몇 개 더 있으며 저놈을 이길 수 있을 거야!","오랫동안 수정을 모았는데 드디어 적색영혼으로 교환할 수 있게 됐어요!","어리고 무지한 사람이 어떻게 천둥소리를 들을 수 있으며, 병든 나무꾼은 호표의 울부짖음을 듣기 어렵네.",},
        [18] = {18,"여자의 거짓말을 용서할 수 있어야 진정한 남자입니다.","왜 이렇게 말이 많아. 조용히 경기에만 집중할 수 있게 해줄래?","쉿! 잠시 그녀를 생각하게 조용히 해줘.","뭐 하고 있어? 하면 너랑 얘기하고 있다고 말해야 해요.",},
