        [20] = {20,"나를 제외한 누구도 날 무시할 수 없어!","나는 사람이고 너는 내가 아니야. 그러니까 너는 사람이 아니야.","또 경기에 참가했어? 입장료 많이 벌었겠네!","몸이 더러우면 고금을 이해하지 못하고, 배가 더러우면 제후를 받아들이지 못하며, 마음이 탁하면 반역을 꿈꾸네!",},
        [21] = {21,"어떻게 하면 멋지게 경기에서 이길 수 있지?","대장부로 태어나 어찌 남 밑에서만 살 수 있겠습니까!","나를 이길 수 있는 것은 순진함이 아니라 뜨거운 날씨야!","아직 시작 전이라 조금 지루한데 기획자 욕 좀 해볼까?",},
