        [28] = {28,"1등이 되기로 결심했어!","천대에 들어갈 수 없다면, 마음속에 들어가세요.","보물상자를 열어 방송에 나가면 누군가가 몰래 훔쳐보고 있는 듯한 느낌이 들어요.","적토마에 올라 천릿길을 달리는데 청룡언월도의 서슬로 다섯 관문 지났도다.",},
        [29] = {29,"속으로 슬퍼하느니 차라리 행동을 하세요.","찾을 수 없네! 과거의 잘록했던 허리를. 원망하네! 몸속의 지방을.","남자가 우는우는우는 것은 죄가 아니며, 아무리 강한 사람도 지칠 때가 있습니다.","대장부는 업적을 세우기 위해 항상 채찍질해야 한다.",},
        [30] = {30,"사마의의 풍화륜이 아무리 빨라도 저보다는 못해요!","천년에 한번 볼 수 있다고 약속했는데. 1시간 만에 위장에 도착했네.","대교, 보연사, 초선 모두 노래를 할 수 있다는 거 아시나요?","금수를 제거하려면 우선 금수에 몸을 바쳐야 한다.",},
        [31] = {31,"나에게는 발밑에 바람을 일으키는 능력이 있다!","고양이는 신비한 동물로 가난한 사람이나 부자나 모두 무시해요.","대도독은 너무 멋진데 안타깝게도 경기에 자주 출전을 안 해요!","삶과 죽음 앞에 두 뜻이 없으니 얼마나 장한가.",},
        [32] = {32,"방탕한 생활을 하는 사람은 달리기에서 분명 질 거에요!","내가 점프를 해서 너의 무릎뼈를 부술 수 있을 것 같아?","왜 이렇게 소교변신이 널려 있는 거야?","큰 꿈을 누가 깨울 것인가. 일평생 나는 스스로 알고 있었네! ",},
        [33] = {33,"곽가에게는 고상만리의 매가 있지만 경기장에서는 저보다 빠르지 못할 거에요!","모두 웃긴 얘기만 하는데 내가 달달한 얘기를 해줄게!","태사자가 스킬을 발동하면 발로 활을 당긴다는 걸 발견했어.","계책을 쓰다가 또 강한 적수 만나고, 지혜를 다투는데 뜻밖의 사람 만나네.",},
        [34] = {34,"하후돈의 강렬 피해 반사가 대단하다고 들었어요? 그래도 달리기는 제가 1등이에요!","일반적으로 헤어 스타일에 만족하지 않는 사람은 영원히 얼굴 문제라는 것을 인정하지 않아요.","손권 손책 형제, 하나는 뒷열을 하나는 전열을 공격합니다!","예주에서 고독과 가난을 탄식하니 남양에 와룡이 있다고 행복할까!",},
        [35] = {35,"조인은 문짝같 아서 뛸 때는 제가 제일 멋져요!","하루 쉬기로 하였는데 밤낮으로 계속 이어질지 몰랐어요!","여몽은 운이 좋을 때 6명을 기절시킬 수 있어요!","프래그래머 친구에게 돈을 빌렸는데, 1024원을 보내면서 정수로 채우라고 말했어요.",},
        [36] = {36,"어서 가세요. 이러다 제가 1위 하겠어요!","물만 마셔도 살이 찌는데 콜라도 마셔야겠어.","감녕이 칼을 메고 달리는 모습이 너무 웃겨요!","대장부로서 업적을 세우지 못하면 초목과 더불어 썩어 갈 일만 남지 않겠소?",},
        [37] = {37,"허저의 유성추 무게가 팔십 근이라 빨리 달릴 수 없을 거예요!","지갑이 차용증으로 가득 찼어요.","손견은 첫 턴에 무적 보호를 해요!","청산을 늙지 않고, 푸른 물은 영원히 흐른다.",},
