        [62] = {203,"장각","창천은 이미 죽고 황천을 세워야 한다. 때는 갑자년이니 천하가 크게 길하다! 천하가 내 손안에 있다!","창천은 이미 죽고 황천을 세워야 한다. 때는 갑자년이니 천하가 크게 길하다! 천하가 내 손안에 있다!","","story_zhangj_8","story_zhangj_8",},
        [63] = {203,"관은병","사악한 사교 무리로군!","사악한 사교 무리로군!","","story_gyp_17","story_gyp_17",},
        [64] = {203,"장각","우레의 신과 번개의 여신은 나의 명령을 들어라. 하늘의 기를 모으고, 하늘의 도를 행한다! 난세를 바꾸는 힘을 보아라!","우레의 신과 번개의 여신은 나의 명령을 들어라. 하늘의 기를 모으고, 하늘의 도를 행한다! 난세를 바꾸는 힘을 보아라!","","story_zhangj_9","story_zhangj_9",},
        [65] = {203,"관은병","쓸데 없는 말이 많구나, 맛 좀 봐라!","쓸데 없는 말이 많구나, 맛 좀 봐라!","","story_gyp_18","story_gyp_18",},
        [66] = {204,"우길","장각, 나 우길이 도와주겠소!","장각, 나 우길이 도와주겠소!","","story_yj_1","story_yj_1",},
        [67] = {204,"장성채","엉터리 도사가 뭐 이리 많지! 장각을 물리치니 우길이 왔네!","엉터리 도사가 뭐 이리 많지! 장각을 물리치니 우길이 왔네!","","story_zxc_24","story_zxc_24",},
        [68] = {204,"조조","황건적들 난동을 부리다니, 이 조조가 도와주겠소!","황건적들 난동을 부리다니, 이 조조가 도와주겠소!","","story_cc_2","story_cc_2",},
        [69] = {204,"우길","조조, 황건적은 그저 가난한 백성일 뿐인데, 왜 없애버리려고 하는가?","조조, 황건적은 그저 가난한 백성일 뿐인데, 왜 없애버리려고 하는가?","","story_yj_2","story_yj_2",},
        [70] = {204,"우길","조조, 내 말 좀 들어보시오.","조조, 내 말 좀 들어보시오.","","story_yj_3","story_yj_3",},
        [71] = {204,"우길","훗날 재상이 된다면 기억하시오. 과부와 고아를 속이지 않으면 사십 년 후 윤회를 잘할 것이오! 잊지 마시오!","훗날 재상이 된다면 기억하시오. 과부와 고아를 속이지 않으면 사십 년 후 윤회를 잘할 것이오! 잊지 마시오!","","story_yj_4","story_yj_4",},
        [72] = {204,"조조","내가 미래의 승상이라는 거군! 하하! 그렇다면 이 조조와 함께 하는것이 어떻소!","내가 미래의 승상이라는 거군! 하하! 그렇다면 이 조조와 함께 하는것이 어떻소!","","story_cc_3","story_cc_3",},
        [73] = {204,"조조","우길, 왜 황건적 잔당을 돕는가, 이 조조와 함께 하는 게 낫지 않은가!","우길, 왜 황건적 잔당을 돕는가, 이 조조와 함께 하는 게 낫지 않은가!","","story_cc_4","story_cc_4",},
        [74] = {15,"장성채","#장비# 획득을 축하해요!","#장비# 획득을 축하해요!","","story_zxc_25","story_zxc_25",},
        [75] = {15,"관은병","이건 고정도입니다! 위력이 세서 장수들이 많이 사용합니다!","이건 고정도입니다! 위력이 세서 장수들이 많이 사용합니다!","","story_gyp_19","story_gyp_19",},
        [76] = {15,"장성채","#장비#로 이동 해보세요!","#장비#로 이동 해보세요!","","story_zxc_26","story_zxc_26",},
        [77] = {16,"장성채","또 골치 아픈 적들이 나타났어요. 장비를 강화하고 전투력을 상승시켜야 해요!","또 골치 아픈 적들이 나타났어요. 장비를 강화하고 전투력을 상승시켜야 해요!","","story_zxc_27","story_zxc_27",},
        [78] = {17,"장성채","#강화# 후에 장비가 강력해졌어요! 공격력도 전의 두배예요!","#강화# 후에 장비가 강력해졌어요! 공격력도 전의 두배예요!","","story_zxc_28","story_zxc_28",},
        [79] = {17,"장성채","앞으로 장비를 잘 #강화# 해주세요!","앞으로 장비를 잘 #강화# 해주세요!","","story_zxc_29","story_zxc_29",},
        [80] = {17,"관은병","저보다 더 강해지셨어요. 적을 처치해 보세요!","저보다 더 강해지셨어요. 적을 처치해 보세요!","","story_gyp_21","story_gyp_21",},
        [81] = {18,"장성채","실력이 급상승하고 있으니, 새로운 동료가 있으면 더 좋겠어요.","실력이 급상승하고 있으니, 새로운 동료가 있으면 더 좋겠어요.","","story_zxc_30","story_zxc_30",},
        [82] = {18,"관은병","자색 협공장수 한쌍이 생겼어요. 함께하면 천하무적이 될 거예요!","자색 협공장수 한쌍이 생겼어요. 함께하면 천하무적이 될 거예요!","","story_gyp_22","story_gyp_22",},
        [83] = {19,"장성채","와, 잘 됐어요. 새로운 협공장수예요! 굉장해요!","와, 잘 됐어요. 새로운 협공장수예요! 굉장해요!","","story_zxc_32","story_zxc_32",},
        [84] = {19,"장성채","#9레벨#과 #17레벨#이 되시면, 새로운 출전위치가 해제될거예요.","#9레벨#과 #17레벨#이 되시면, 새로운 출전위치가 해제될거예요.","","story_zxc_33","story_zxc_33",},
        [85] = {19,"관은병","곧 #9레벨#이 됩니다! 저와 같이 싸우러 가요! 돌진!","곧 #9레벨#이 됩니다! 저와 같이 싸우러 가요! 돌진!","","story_gyp_23","story_gyp_23",},
        [86] = {20,"장성채","실력이 크게 향상되어 경기장에 가서 도전할 수 있어요!","실력이 크게 향상되어 경기장에 가서 도전할 수 있어요!","","story_zxc_34","story_zxc_34",},
        [87] = {20,"장성채","함께 #경기장#에 가봐요!","함께 #경기장#에 가봐요!","","story_zxc_35","story_zxc_35",},
        [88] = {21,"장성채","#경기장#엔 뛰어난 사람이 많아요, 다른 유저를 계속 이기면 순위가 상승해요.","#경기장#엔 뛰어난 사람이 많아요, 다른 유저를 계속 이기면 순위가 상승해요.","","story_zxc_36","story_zxc_36",},
        [89] = {21,"관은병","#순위#가 상승할 때마다, 많은 보상을 획득할 수 있어요! ","#순위#가 상승할 때마다, 많은 보상을 획득할 수 있어요! ","","story_gyp_24","story_gyp_24",},
        [90] = {21,"장성채","경기상점으로 가봐요!","경기상점으로 가봐요!","","story_zxc_38","story_zxc_38",},
        [91] = {22,"장성채","경기장에서 적을 이기면 명성과 순위를 얻어요. 일정 순위를 달성하면, 추가 보상도 있어요!","경기장에서 적을 이기면 명성과 순위를 얻어요. 일정 순위를 달성하면, 추가 보상도 있어요!","","story_zxc_39","story_zxc_39",},
        [92] = {22,"장성채","순위가 상승하면 금화 보상을 받을 수 있어요! 순위가 높을수록, 매일 받는 명성도 많아져요.","순위가 상승하면 금화 보상을 받을 수 있어요! 순위가 높을수록, 매일 받는 명성도 많아져요.","","story_zxc_37","story_zxc_37",},
        [93] = {22,"관은병","경기장에서 계속 순위를 올려요! 1위가 되도록 도울게요!","경기장에서 계속 순위를 올려요! 1위가 되도록 도울게요!","","story_gyp_25","story_gyp_25",},
        [94] = {23,"장성채","#유람#이 해제 됐어요.! 실력이 급격하게 향상되어 이제 유람할 수 있어요!","#유람#이 해제 됐어요.! 실력이 급격하게 향상되어 이제 유람할 수 있어요!","관은병","story_zxc_40","story_zxc_40",},
        [95] = {23,"관은병","전국에 많은 병서와 병부를 남겨졌다는데, 유람에서 이런 #보물#을 얻을수 있어요!","전국에 많은 병서와 병부를 남겨졌다는데, 유람에서 이런 #보물#을 얻을수 있어요!","","story_gyp_26","story_gyp_26",},
        [96] = {23,"장성채","유람에서는 보물외에 각종 행운도 만날수 있어요. 어서 레벨을 올려보세요.","유람에서는 보물외에 각종 행운도 만날수 있어요. 어서 레벨을 올려보세요.","","story_zxc_41","story_zxc_41",},
        [97] = {23,"장성채","함께 #유람#하러 가요!","함께 #유람#하러 가요!","","story_zxc_42","story_zxc_42",},
        [98] = {24,"장성채","행운이예요, #수경학당#이네요! 정말 하늘이 내린 천자인가 봐요!","행운이예요, #수경학당#이네요! 정말 하늘이 내린 천자인가 봐요!","","story_zxc_43","story_zxc_43",},
        [99] = {24,"관은병","수경선생이 내는 문제를 맞추면 금화를 얻을 수 있어요!","수경선생이 내는 문제를 맞추면 금화를 얻을 수 있어요!","","story_gyp_27","story_gyp_27",},
        [100] = {25,"장성채","보물 획득을 축하해요! 보물은 병서와 병부 두 종류로 병서는 공격을 강화시키고, 병부는 생명을 연장시켜줘요.","보물 획득을 축하해요! 보물은 병서와 병부 두 종류로 병서는 공격을 강화시키고, 병부는 생명을 연장시켜줘요.","","story_zxc_44","story_zxc_44",},
        [101] = {25,"관은병","이 <오자>라는 책은 전국시기 명장 오기가 쓴 병서인데, 전투력을 상승하는 데 큰 도움이 될 거예요!","이 <오자>라는 책은 전국시기 명장 오기가 쓴 병서인데, 전투력을 상승하는 데 큰 도움이 될 거예요!","","story_gyp_29","story_gyp_29",},
        [102] = {25,"장성채","함께 진형으로 가서 보물을 장착 해봐요.","함께 진형으로 가서 보물을 장착 해봐요.","","story_zxc_45","story_zxc_45",},
        [103] = {26,"장성채","보물을 강화해보세요","보물을 강화해보세요","","story_zxc_46","story_zxc_46",},
        [104] = {26,"장성채","벼루로 보물을 강화 할 수 있어요. 강화 후 보물은 더 위력이 강해져요.","벼루로 보물을 강화 할 수 있어요. 강화 후 보물은 더 위력이 강해져요.","","story_zxc_47","story_zxc_47",},
        [105] = {27,"관은병","보세요! 보물 강화 후에 전투력이 많이 상승했어요.","보세요! 보물 강화 후에 전투력이 많이 상승했어요.","","story_gyp_30","story_gyp_30",},
        [106] = {27,"관은병","계속 유람하면, 더 많은 보물을 얻을 수 있어요!유람으로 더 많은 보물을 얻을 수 있어요!","계속 유람하면, 더 많은 보물을 얻을 수 있어요!유람으로 더 많은 보물을 얻을 수 있어요!","","story_gyp_31","story_gyp_31",},
        [107] = {28,"장성채","축하해요. #관직#이 해제됐어요!","축하해요. #관직#이 해제됐어요!","","story_zxc_48","story_zxc_48",},
        [108] = {28,"장성채","실력이 널리 알려졌어요. 관직을 올리러 가볼가요!","실력이 널리 알려졌어요. 관직을 올리러 가볼가요!","","story_zxc_49","story_zxc_49",},
        [109] = {29,"장성채","승급을 축하드려요! 관직을 올리면 전체장수의 전투력을 상승되고, 진정한 강자가 될 수 있어요!","승급을 축하드려요! 관직을 올리면 전체장수의 전투력을 상승되고, 진정한 강자가 될 수 있어요!","","story_zxc_50","story_zxc_50",},
        [110] = {29,"관은병","와, 전투력이 급등했어요! 점점 강해지네요!","와, 전투력이 급등했어요! 점점 강해지네요!","","story_gyp_32","story_gyp_32",},
        [111] = {29,"관은병","메인던전으로 가서 적을 혼내주세요","메인던전으로 가서 적을 혼내주세요","","story_gyp_33","story_gyp_33",},
        [112] = {30,"장성채","과관참장에 도전하고 #고품질 장비#를 가질 수 있어요!","과관참장에 도전하고 #고품질 장비#를 가질 수 있어요!","","story_zxc_51","story_zxc_51",},
        [113] = {31,"장성채","과관참장 관문마다 세 개의 난이도가 있어요. 난이도가 높을수록 보상도 많아져요!","과관참장 관문마다 세 개의 난이도가 있어요. 난이도가 높을수록 보상도 많아져요!","","story_zxc_52","story_zxc_52",},
        [114] = {31,"관은병","정철은 장비상점에서 각종 장비와 바꿀 수 있고, 성수가 많아지면 추가보상을 얻을 수 있어요.","정철은 장비상점에서 각종 장비와 바꿀 수 있고, 성수가 많아지면 추가보상을 얻을 수 있어요.","","story_gyp_34","story_gyp_34",},
        [115] = {31,"장성채","방금 많은 정철을 획득했어요.  장비상점으로 가봐요.","방금 많은 정철을 획득했어요.  장비상점으로 가봐요.","","story_zxc_53","story_zxc_53",},
        [116] = {32,"장성채","한빙검을 얻으셨네요! 정말 멋져요!","한빙검을 얻으셨네요! 정말 멋져요!","","story_zxc_54","story_zxc_54",},
        [117] = {32,"장성채","고품질 장비를 얻으려면, 매일 과관참장에 도전해야 해요!","고품질 장비를 얻으려면, 매일 과관참장에 도전해야 해요!","","story_zxc_55","story_zxc_55",},
        [118] = {32,"관은병","좋은 장비를 더 많이 갖고 싶어요! 과관참장으로 가요!","좋은 장비를 더 많이 갖고 싶어요! 과관참장으로 가요!","","story_gyp_35","story_gyp_35",},
        [119] = {33,"장성채","#요일던전#이 해제됐어요! 정말 좋은 곳이에요!","#요일던전#이 해제됐어요! 정말 좋은 곳이에요!","","story_zxc_56","story_zxc_56",},
        [120] = {33,"관은병","요일던전에서 많은 아이템을 얻을 수 있어요! 은화, 돌파단, 제련석 등등이요. 전투력을 계속 올릴 수 있어요!","요일던전에서 많은 아이템을 얻을 수 있어요! 은화, 돌파단, 제련석 등등이요. 전투력을 계속 올릴 수 있어요!","","story_gyp_36","story_gyp_36",},
        [121] = {33,"장성채","와, 요일던전은 정말 굉장해요, 빨리 가봐요!","와, 요일던전은 정말 굉장해요, 빨리 가봐요!","","story_zxc_57","story_zxc_57",},
        [122] = {34,"장성채","혼자는 외로워요! #군단#이 해제 됐어요, 같이 가봐요!","혼자는 외로워요! #군단#이 해제 됐어요, 같이 가봐요!","","story_zxc_58","story_zxc_58",},
        [123] = {34,"관은병","군단에 가입하면 더 많은 컨텐츠를 즐길 수 있어요! 군단에 가입해 보세요!","군단에 가입하면 더 많은 컨텐츠를 즐길 수 있어요! 군단에 가입해 보세요!","","story_gyp_37","story_gyp_37",},
        [124] = {205,"주인공","장각은 죽였는데, 우길이 도망쳤어요!","장각은 죽였는데, 우길이 도망쳤어요!","","story_zj_13","story_nzj_13",},
        [125] = {205,"조조","그대는 인재가 틀림없소. 나중에 낙양에 오면 날 찾으시오! 봉효, 우리는 가지","그대는 인재가 틀림없소. 나중에 낙양에 오면 날 찾으시오! 봉효, 우리는 가지","","story_cc_5","story_cc_5",},
        [126] = {205,"장성채","우길의 말에 무슨 뜻이 있는 것 같은데, 조조는 신경을 안 쓰네요.","우길의 말에 무슨 뜻이 있는 것 같은데, 조조는 신경을 안 쓰네요.","주인공","story_zxc_59","story_zxc_59",},
        [127] = {205,"장성채","조조는 영웅일까요, 간웅일까요?","조조는 영웅일까요, 간웅일까요?","","story_zxc_60","story_zxc_60",},
        [128] = {205,"주인공","조조가 오늘 날 도와줬으니, 다음에 기회가 되면 나도 힘써 돕겠어요.","조조가 오늘 날 도와줬으니, 다음에 기회가 되면 나도 힘써 돕겠어요.","","story_zj_14","story_nzj_14",},
        [129] = {205,"장성채","우길이 도망갈 때 떨어뜨린 명장령이에요!","우길이 도망갈 때 떨어뜨린 명장령이에요!","","story_zxc_61","story_zxc_61",},
        [130] = {205,"주인공","#명장령#이 또 생겼네요! 잘 됐어요!","#명장령#이 또 생겼네요! 잘 됐어요!","","story_zj_15","story_nzj_15",},
        [131] = {205,"장성채","대장군 하진에게 #명장령#이 있대요. 낙양에 가서 하진을 찾아봐요.","대장군 하진에게 #명장령#이 있대요. 낙양에 가서 하진을 찾아봐요.","","story_zxc_62","story_zxc_62",},
        [132] = {205,"주인공","좋아요, 마침 낙양 유람을 해보고 싶었어요.","좋아요, 마침 낙양 유람을 해보고 싶었어요.","","story_zj_16","story_nzj_16",},
        [133] = {301,"장성채","쉿! 비밀이 있는 것 같아요.","쉿! 비밀이 있는 것 같아요.","주인공","story_zxc_63","story_zxc_63",},
        [134] = {301,"장양","감히 하진이 날 모해하려고 하다니, 내가 먼저 손을 써주지!","감히 하진이 날 모해하려고 하다니, 내가 먼저 손을 써주지!","","story_zr_1","story_zr_1",},
        [135] = {301,"장양","여봐라! 태후의 조령을 거짓으로 꾸며, 하진을 입궁시켜 죽이거라!","여봐라! 태후의 조령을 거짓으로 꾸며, 하진을 입궁시켜 죽이거라!","","story_zr_2","story_zr_2",},
        [136] = {301,"주인공","모습을 보니, 설마 태감인가요?","모습을 보니, 설마 태감인가요?","장성채","story_zj_17","story_nzj_17",},
        [137] = {301,"장성채","맞아요! 태감 장양이에요. 십상시의 우두머리예요.","맞아요! 태감 장양이에요. 십상시의 우두머리예요.","","story_zxc_64","story_zxc_64",},
        [138] = {301,"장성채","하진은 하태후의 오라버니죠. 조조와 원소가 환관을 죽여 민심을 얻으라고 하진을 설득하고 있어요.","하진은 하태후의 오라버니죠. 조조와 원소가 환관을 죽여 민심을 얻으라고 하진을 설득하고 있어요.","","story_zxc_65","story_zxc_65",},
        [139] = {301,"장성채","무슨 소문이든 장양이 모두 알고 있어요. 먼저 손을 쓰고 있는 거죠.","무슨 소문이든 장양이 모두 알고 있어요. 먼저 손을 쓰고 있는 거죠.","주인공","story_zxc_66","story_zxc_66",},
        [140] = {301,"주인공","태감과 황제친족의 싸움이군요","태감과 황제친족의 싸움이군요","","story_zj_18","story_nzj_18",},
        [141] = {301,"장성채","하진을 어서 찾아봐요. 장양이 명장령을 손에 넣으면 안 되니까요.","하진을 어서 찾아봐요. 장양이 명장령을 손에 넣으면 안 되니까요.","주인공","story_zxc_67","story_zxc_67",},
        [142] = {302,"하태후","감히 내 침전에 함부로 들어오다니! 어서 저들을 쫓아내거라!","감히 내 침전에 함부로 들어오다니! 어서 저들을 쫓아내거라!","","story_hth_1","story_hth_1",},
        [143] = {302,"장성채","하태후예요. 정말 젊네요! 생긴 건 뭐 별로네요. 저처럼 예쁘지도 않고.","하태후예요. 정말 젊네요! 생긴 건 뭐 별로네요. 저처럼 예쁘지도 않고.","","story_zxc_68","story_zxc_68",},
        [144] = {302,"관은병","나 관은병이 몇만 배는 예쁘겠어.","나 관은병이 몇만 배는 예쁘겠어.","","story_gyp_38","story_gyp_38",},
        [145] = {303,"장성채","하진, 여기 있었군요. 어서 #명장령#을 내놓으세요.","하진, 여기 있었군요. 어서 #명장령#을 내놓으세요.","","story_zxc_69","story_zxc_69",},
        [146] = {303,"하진","누가 함부로 황궁에 들어왔느냐!","누가 함부로 황궁에 들어왔느냐!","","story_hj_1","story_hj_1",},
        [147] = {303,"관은병","와아, 또 전투다!","와아, 또 전투다!","","story_gyp_39","story_gyp_39",},
        [148] = {304,"장성채","하진, 장양이 당신을 죽이려고 매복하고 있어요. 황궁에 들어가지 마세요!","하진, 장양이 당신을 죽이려고 매복하고 있어요. 황궁에 들어가지 마세요!","","story_zxc_70","story_zxc_70",},
        [149] = {304,"하진","환관이 권력을 장악하다니, 누이가 황궁에 있는데 내가 어찌 가만히 있겠느냐.","환관이 권력을 장악하다니, 누이가 황궁에 있는데 내가 어찌 가만히 있겠느냐.","","story_hj_2","story_hj_2",},
        [150] = {304,"장성채","하진이 위험한 것을 알면서 궁에 들어가다니, 누이에 대한 정이 각별하군요!","하진이 위험한 것을 알면서 궁에 들어가다니, 누이에 대한 정이 각별하군요!","관은병","story_zxc_71","story_zxc_71",},
        [151] = {304,"관은병","어서 장양을 찾아 반란을 제압해주세요!","어서 장양을 찾아 반란을 제압해주세요!","","story_gyp_40","story_gyp_40",},
        [152] = {305,"장양","하하하, 하진, 잘 봐라, 이제 누가 힘이 있느냐? 우리 둘 다 아니다!","하하하, 하진, 잘 봐라, 이제 누가 힘이 있느냐? 우리 둘 다 아니다!","","story_zr_3","story_zr_3",},
        [153] = {305,"장양","후환을 생각하지 않고 동탁을 수도로 부르다니, 동탁이 어떤 자인지 아느냐?","후환을 생각하지 않고 동탁을 수도로 부르다니, 동탁이 어떤 자인지 아느냐?","","story_zr_4","story_zr_4",},
        [154] = {305,"장양","허, 동탁이 오다니, 난세가 곧 시작되겠군!","허, 동탁이 오다니, 난세가 곧 시작되겠군!","","story_zr_5","story_zr_5",},
        [155] = {305,"장성채","우리가 한발 늦었네요! 장양이 하진을 죽이고, 어린황제를 붙잡았어요!","우리가 한발 늦었네요! 장양이 하진을 죽이고, 어린황제를 붙잡았어요!","장양","story_zxc_72","story_zxc_72",},
        [156] = {305,"관은병","장양, 왜 어린황제를 위협하는 거죠? 살고 싶다면 황제와 명장령을 내놓으세요!","장양, 왜 어린황제를 위협하는 거죠? 살고 싶다면 황제와 명장령을 내놓으세요!","","story_gyp_41","story_gyp_41",},
        [157] = {305,"장양","먼저 내 손에 있는 가위에 물어보시지! 너희들도 대가 끊기게 해주마!","먼저 내 손에 있는 가위에 물어보시지! 너희들도 대가 끊기게 해주마!","","story_zr_6","story_zr_6",},
        [158] = {306,"장양","너희들을 이기지 못하다니! 십상시도 알고 보면 불쌍한 사람들이고, 이제 힘도 없다! 이렇게 된 이상 너희들이 소제를 잘 돌봐주길 바란다!","너희들을 이기지 못하다니! 십상시도 알고 보면 불쌍한 사람들이고, 이제 힘도 없다! 이렇게 된 이상 너희들이 소제를 잘 돌봐주길 바란다!","소제","story_zr_7","story_zr_7",},
        [159] = {306,"소제","장양! 안 되오!","장양! 안 되오!","장양","story_hxd_1","story_hxd_1",},
        [160] = {306,"장양","신은 천명을 따릅니다. 옥체 보존하소서. (장양이 물에 뛰어들어 자살한다.)","신은 천명을 따릅니다. 옥체 보존하소서. (장양이 물에 뛰어들어 자살한다.)","","story_zr_8","story_zr_8",},
        [161] = {306,"소제","어찌 됐든, 이 반란을 평정해 주어 고맙구나. 이 #명장령#으로 사례하겠다.","어찌 됐든, 이 반란을 평정해 주어 고맙구나. 이 #명장령#으로 사례하겠다.","","story_hxd_3","story_hxd_3",},
        [162] = {306,"장성채","십상시의 반란은 평정했지만, 동탁이 이미 수도에 들어와서 골치 아프게 생겼어요!","십상시의 반란은 평정했지만, 동탁이 이미 수도에 들어와서 골치 아프게 생겼어요!","","story_zxc_73","story_zxc_73",},
        [163] = {306,"관은병","지난번에는 동탁이 황건적을 토벌한다는 이유로 잇속을 챙기고, 서량병을 이끌고 우리 명장령을 뺏어갔어요. 좋은 사람 같지는 않아요.","지난번에는 동탁이 황건적을 토벌한다는 이유로 잇속을 챙기고, 서량병을 이끌고 우리 명장령을 뺏어갔어요. 좋은 사람 같지는 않아요.","장성채","story_gyp_42","story_gyp_42",},
        [164] = {306,"장성채","낙양에서 조조를 찾아봐요. 권력 있는 집안 출신이라 인맥이 넓으니, 동탁을 토벌할 방법이 있는지 알아봐요.","낙양에서 조조를 찾아봐요. 권력 있는 집안 출신이라 인맥이 넓으니, 동탁을 토벌할 방법이 있는지 알아봐요.","","story_zxc_74","story_zxc_74",},
        [165] = {306,"관은병","조조가 다른 #명장령#의 실마리를 가지고 있을지 몰라요.","조조가 다른 #명장령#의 실마리를 가지고 있을지 몰라요.","","story_gyp_43","story_gyp_43",},
        [166] = {306,"주인공","그게 좋겠네요! 낙양에 가서 조조를 찾아요!","그게 좋겠네요! 낙양에 가서 조조를 찾아요!","관은병","story_zj_19","story_nzj_19",},
        [167] = {401,"조조","마침 잘 왔소! 동탁이 군대를 거느리고 조정을 혼란에 빠뜨리고, 폭정을 하고 있소.","마침 잘 왔소! 동탁이 군대를 거느리고 조정을 혼란에 빠뜨리고, 폭정을 하고 있소.","","story_cc_6","story_cc_6",},
        [168] = {401,"조조","동탁은 원소를 죽이려고 했는데, 원소는 기주로 도망쳤다오. 원소의 부탁으로 마침 동탁 토벌에 대해 상의하려던 참이었소.","동탁은 원소를 죽이려고 했는데, 원소는 기주로 도망쳤다오. 원소의 부탁으로 마침 동탁 토벌에 대해 상의하려던 참이었소.","주인공","story_cc_7","story_cc_7",},
        [169] = {401,"주인공","같이 가요! 저도 그 일 때문에 왔어요.","같이 가요! 저도 그 일 때문에 왔어요.","","story_zj_20","story_nzj_20",},
        [170] = {401,"왕윤","(왕윤이 편지를 읽고 흐느끼며 말한다) 동탁이 권력을 휘두르니 종묘사직을 지키기 힘들겠구나.","(왕윤이 편지를 읽고 흐느끼며 말한다) 동탁이 권력을 휘두르니 종묘사직을 지키기 힘들겠구나.","","story_xy_1","story_xy_1",},
        [171] = {401,"조조","울지 마시오. 조조가 재주는 없지만 동탁을 꼭 죽이겠소.","울지 마시오. 조조가 재주는 없지만 동탁을 꼭 죽이겠소.","","story_cc_8","story_cc_8",},
        [172] = {401,"주인공","동탁이 무고한 사람을 함부로 죽이니, 제가 동탁을 없애겠어요.","동탁이 무고한 사람을 함부로 죽이니, 제가 동탁을 없애겠어요.","","story_zj_21","story_nzj_21",},
        [173] = {401,"왕윤","그 열정을 보니 희망이 보이는군요! 하지만 동탁은 서량에서 온 흉악한 자라서 실력이 안 된다면 포기하는 게 낫지요.","그 열정을 보니 희망이 보이는군요! 하지만 동탁은 서량에서 온 흉악한 자라서 실력이 안 된다면 포기하는 게 낫지요.","조조","story_xy_2","story_xy_2",},
        [174] = {401,"왕윤","우리 집안의 고수와 싸워 이긴다면, 칠성보도를 드릴테니 그것으로 동탁을 죽이세요!","우리 집안의 고수와 싸워 이긴다면, 칠성보도를 드릴테니 그것으로 동탁을 죽이세요!","","story_xy_3","story_xy_3",},
        [175] = {401,"주인공","곳곳에 호위병이 깔렸지만, 뭐 별거 아니죠! 제가 하겠어요!","곳곳에 호위병이 깔렸지만, 뭐 별거 아니죠! 제가 하겠어요!","","story_zj_22","story_nzj_22",},
        [176] = {402,"왕윤","두 분 정말 대단하군요! 칠성보도는 여기 있어요. 꼭 이기세요!","두 분 정말 대단하군요! 칠성보도는 여기 있어요. 꼭 이기세요!","조조","story_xy_4","story_xy_4",},
        [177] = {402,"조조","잘 될 테니 걱정 마시오.!","잘 될 테니 걱정 마시오.!","왕윤","story_cc_9","story_cc_9",},
        [178] = {402,"조조","동탁의 저택으로 들어갈 때 내 하인으로 가장하고 있다가, 내가 기침을 두 번 하면 손을 쓰시오!","동탁의 저택으로 들어갈 때 내 하인으로 가장하고 있다가, 내가 기침을 두 번 하면 손을 쓰시오!","주인공","story_cc_10","story_cc_10",},
        [179] = {402,"주인공","좋아요, 걱정 마세요!","좋아요, 걱정 마세요!","조조","story_zj_23","story_nzj_23",},
        [180] = {403,"동탁","맹덕, 여기는 어쩐 일이오?","맹덕, 여기는 어쩐 일이오?","조조","story_dz_4","story_dz_4",},
        [181] = {403,"조조","내게 좋은 검이 있어 드리려고 왔소. 흠흠!","내게 좋은 검이 있어 드리려고 왔소. 흠흠!","동탁","story_cc_11","story_cc_11",},
        [182] = {403,"동탁","어디 한 번 봅시다! 예리한 것이 정말 좋은 검이군!","어디 한 번 봅시다! 예리한 것이 정말 좋은 검이군!","조조","story_dz_5","story_dz_5",},
        [183] = {403,"주인공","이때다! 동탁 네 이놈, 넌 죽은 목숨이다!","이때다! 동탁 네 이놈, 넌 죽은 목숨이다!","동탁","story_zj_24","story_nzj_24",},
        [184] = {404,"동탁","여기 자객이 있다! 내 아들 봉선은 어디 있느냐? ","여기 자객이 있다! 내 아들 봉선은 어디 있느냐? ","여포","story_dz_6","story_dz_6",},
        [185] = {404,"여포","누가 내 아버지를 해하려 하느냐!","누가 내 아버지를 해하려 하느냐!","주인공","story_lb_5","story_lb_5",},
        [186] = {404,"주인공","아깝네요, 여포가 아니었다면 동탁은 오늘 죽었을 텐데!","아깝네요, 여포가 아니었다면 동탁은 오늘 죽었을 텐데!","동탁","story_zj_25","story_nzj_25",},
        [187] = {404,"동탁","또 너냐! 지난번 #명장령#을 뺏어갔었지. 마침 잘 왔다! 봉선, 저들을 죽여라!","또 너냐! 지난번 #명장령#을 뺏어갔었지. 마침 잘 왔다! 봉선, 저들을 죽여라!","주인공","story_dz_7","story_dz_7",},
        [188] = {404,"조조","그만 싸우고 떠나는 게 좋겠소!! 여포는 매우 강한 데다가 동탁에게는 실력 있는 자가 많으니, 일단 피합시다! ","그만 싸우고 떠나는 게 좋겠소!! 여포는 매우 강한 데다가 동탁에게는 실력 있는 자가 많으니, 일단 피합시다! ","주인공","story_cc_12","story_cc_12",},
        [189] = {405,"진궁","나는 중모현 현령 진궁이오. 당신은 누구시오?","나는 중모현 현령 진궁이오. 당신은 누구시오?","조조","story_cg_1","story_cg_1",},
        [190] = {405,"조조","지나가는 행상인으로 성은 황보라 하오.","지나가는 행상인으로 성은 황보라 하오.","진궁","story_cc_13","story_cc_13",},
        [191] = {405,"진궁","내가 관직에 있을 때 당신이 조조라는 것을 알았소! 동탁이 박하게 대하지 않았는데, 왜 죽이려는 것이오?","내가 관직에 있을 때 당신이 조조라는 것을 알았소! 동탁이 박하게 대하지 않았는데, 왜 죽이려는 것이오?","조조","story_cg_2","story_cg_2",},
        [192] = {405,"진궁","둘을 감옥에 가두거라. 내일 낙양으로 가서 상을 구해야겠다.","둘을 감옥에 가두거라. 내일 낙양으로 가서 상을 구해야겠다.","조조","story_cg_3","story_cg_3",},
        [193] = {405,"조조","네가 어찌 영웅의 뜻을 알겠느냐! 이미 눈치챘으니, 결전을 벌이자!","네가 어찌 영웅의 뜻을 알겠느냐! 이미 눈치챘으니, 결전을 벌이자!","진궁","story_cc_14","story_cc_14",},
        [194] = {406,"진궁","동탁이 나라를 어지럽히고, 한실의 녹봉을 받는 나로서, 나라 걱정을 하지 않는다면 짐승과 뭐가 다르겠소! 그냥 한 번 시험해 본 것뿐이오.","동탁이 나라를 어지럽히고, 한실의 녹봉을 받는 나로서, 나라 걱정을 하지 않는다면 짐승과 뭐가 다르겠소! 그냥 한 번 시험해 본 것뿐이오.","조조","story_cg_4","story_cg_4",},
        [195] = {406,"진궁","두 분 천하의 충신이니, 진궁은 관직을 버리고 두 분을 따르겠소.","두 분 천하의 충신이니, 진궁은 관직을 버리고 두 분을 따르겠소.","조조","story_cg_5","story_cg_5",},
        [196] = {406,"조조","그대같은 인재를 먼저 만나고, 진궁도 얻었으니, 조조가 복이 많소! 술을 마시며 회포를 풀어봅시다!","그대같은 인재를 먼저 만나고, 진궁도 얻었으니, 조조가 복이 많소! 술을 마시며 회포를 풀어봅시다!","진궁","story_cc_15","story_cc_15",},
        [197] = {406,"조조","지기를 만나면 천 잔의 술도 부족하지요. 마음껏 즐깁시다!","지기를 만나면 천 잔의 술도 부족하지요. 마음껏 즐깁시다!","진궁","story_cc_16","story_cc_16",},
        [198] = {406,"주인공","조조, 이렇게 흥겨운 분위기 속에서 우리 한 번 겨뤄보는 건 어떨까요!","조조, 이렇게 흥겨운 분위기 속에서 우리 한 번 겨뤄보는 건 어떨까요!","조조","story_zj_26","story_nzj_26",},
        [199] = {406,"조조","좋소, 한 번 싸워봅시다!","좋소, 한 번 싸워봅시다!","주인공","story_cc_17","story_cc_17",},
        [200] = {407,"조조","하하하, 그대는 정말 대단하시오. 조조는 상대가 안 되는군요!","하하하, 그대는 정말 대단하시오. 조조는 상대가 안 되는군요!","주인공","story_cc_18","story_cc_18",},
        [201] = {407,"주인공","과찬이세요! 저는 삼국 최고장수가 되고 싶어요!","과찬이세요! 저는 삼국 최고장수가 되고 싶어요!","","story_zj_27","story_nzj_27",},
        [202] = {407,"조조","이렇게 큰 뜻을 품고 있다니! #명장령#을  드리리다. 어서 꿈을 이루기 바라오!","이렇게 큰 뜻을 품고 있다니! #명장령#을  드리리다. 어서 꿈을 이루기 바라오!","","story_cc_19","story_cc_19",},
        [203] = {407,"주인공","감사합니다! 이제 어떻게 할 건가요?","감사합니다! 이제 어떻게 할 건가요?","","story_zj_28","story_nzj_28",},
        [204] = {407,"조조","군대를 정비하고, 천하의 제후에게 출병하라고 하여 동탁을 토벌할 것이오!","군대를 정비하고, 천하의 제후에게 출병하라고 하여 동탁을 토벌할 것이오!","","story_cc_20","story_cc_20",},
        [205] = {407,"주인공","저도 넣어주세요. 동탁은 꼭 제가 처치하겠어요!","저도 넣어주세요. 동탁은 꼭 제가 처치하겠어요!","","story_zj_29","story_nzj_29",},
        [206] = {407,"조조","그대가 있으니 모든 일이 잘될 것이오!","그대가 있으니 모든 일이 잘될 것이오!","","story_cc_21","story_cc_21",},
        [207] = {501,"장성채","조조와 함께 격문을 보내 동탁 토벌을 계획하자 18로 제후들이 호응하고 있어요. 정말 대단해요!","조조와 함께 격문을 보내 동탁 토벌을 계획하자 18로 제후들이 호응하고 있어요. 정말 대단해요!","주인공","","",},
        [208] = {501,"주인공","유주의 공손찬도 왔군요. 지난번 의형제들이 공손찬 휘하에 들어갔는데, 이번에 올 수 있을지 모르겠네요.","유주의 공손찬도 왔군요. 지난번 의형제들이 공손찬 휘하에 들어갔는데, 이번에 올 수 있을지 모르겠네요.","","","",},
        [209] = {501,"유비","넷째 아우, 조조와 격문을 보내 동탁을 토벌한다는 소식을 듣고 널 도우러 왔다!","넷째 아우, 조조와 격문을 보내 동탁을 토벌한다는 소식을 듣고 널 도우러 왔다!","","","",},
        [210] = {501,"주인공","정말 오셨군요!","정말 오셨군요!","","","",},
        [211] = {501,"원술","흥, 보잘것없는 주제에 이곳에서 위세를 떨다니!","흥, 보잘것없는 주제에 이곳에서 위세를 떨다니!","","","",},
        [212] = {501,"조조","그러지 마시오! 이 분은 걸출한 인물이오! 각 로 제후가 모두 모였는데, 협력하지 않으면 큰일을 그르치게 되잖소!","그러지 마시오! 이 분은 걸출한 인물이오! 각 로 제후가 모두 모였는데, 협력하지 않으면 큰일을 그르치게 되잖소!","","","",},
        [213] = {501,"주인공","서로 굴복하지 않으니, 겨뤄보는 게 좋겠군요!","서로 굴복하지 않으니, 겨뤄보는 게 좋겠군요!","","","",},
        [214] = {501,"원소","좋습니다! 이기는 사람이 제후 맹주가 되기로 하지요! 나 원소가 가지고 있는 #명장령#을 주겠소!","좋습니다! 이기는 사람이 제후 맹주가 되기로 하지요! 나 원소가 가지고 있는 #명장령#을 주겠소!","","","",},
        [215] = {501,"주인공","그럼 시작하시죠. #명장령#은 분명 제가 갖게 될 겁니다!","그럼 시작하시죠. #명장령#은 분명 제가 갖게 될 겁니다!","","","",},
        [216] = {502,"유비","아우, 이분이 북평태수 공손찬이시다. 옛 동창이지.","아우, 이분이 북평태수 공손찬이시다. 옛 동창이지.","주인공","","",},
        [217] = {502,"주인공","백마장군 공손찬이시군요.","백마장군 공손찬이시군요.","","","",},
        [218] = {502,"공손찬","좋습니다. 현덕의 동생이니 언제 토론이나 한 번 하죠.","좋습니다. 현덕의 동생이니 언제 토론이나 한 번 하죠.","","","",},
        [219] = {503,"원술","너는 누구냐, 나는 사세삼공 원가의 적자 원술이다!","너는 누구냐, 나는 사세삼공 원가의 적자 원술이다!","유비","","",},
        [220] = {503,"유비","나는 한실종친으로 중산정왕의 후예이며, 성은 유, 이름은 비, 자는 현덕이오.","나는 한실종친으로 중산정왕의 후예이며, 성은 유, 이름은 비, 자는 현덕이오.","","","",},
        [221] = {503,"원술","짚신이나 파는 천민이 무슨 한실종친이라고!","짚신이나 파는 천민이 무슨 한실종친이라고!","","","",},
        [222] = {503,"주인공","원술, 너무 무례하게 구는군요!","원술, 너무 무례하게 구는군요!","","","",},
        [223] = {503,"관은병","원술, 당신은 상대도 안 돼!","원술, 당신은 상대도 안 돼!","","","",},
        [224] = {503,"장성채","원술을 굴복시키고 나서 다시 얘기해요!","원술을 굴복시키고 나서 다시 얘기해요!","","","",},
        [225] = {504,"원소","대단하시오! 그대를 맹주로 천거하겠소! #명장령#도 받으시오.","대단하시오! 그대를 맹주로 천거하겠소! #명장령#도 받으시오.","주인공","","",},
        [226] = {504,"주인공","#명장령#은 받겠어요! 하지만 맹주의 지위는 당신에게 양보할게요!","#명장령#은 받겠어요! 하지만 맹주의 지위는 당신에게 양보할게요!","","","",},
        [227] = {504,"유비","아우, 함부로 말하지 말거라!","아우, 함부로 말하지 말거라!","","","",},
        [228] = {504,"원술","하하, 그럼 맹주 자리는 우리 가문이 차지해야겠군!","하하, 그럼 맹주 자리는 우리 가문이 차지해야겠군!","","","",},
        [229] = {504,"관은병","원술 당신의 자리도 아닌데, 왜 그렇게 좋아해요!","원술 당신의 자리도 아닌데, 왜 그렇게 좋아해요!","","","",},
        [230] = {504,"원술","네가 감히... (원술이 화가 나 말문이 막힌다.)","네가 감히... (원술이 화가 나 말문이 막힌다.)","","","",},
        [231] = {504,"장성채","화웅 그 패장이 또 싸움을 걸고 있어요! 여포가 관문을 지키고 있대요!","화웅 그 패장이 또 싸움을 걸고 있어요! 여포가 관문을 지키고 있대요!","주인공","","",},
        [232] = {504,"주인공","일이 더 커지겠군! 우리 삼국 최고의 전신 여포를 만나러 호뢰관으로 가요!","일이 더 커지겠군! 우리 삼국 최고의 전신 여포를 만나러 호뢰관으로 가요!","","","",},
        [233] = {601,"여포","나에게는 적토마와 방천화극이 있고, #명장령#도 있으니, 천하에 누가 나를 상대하겠느냐!","나에게는 적토마와 방천화극이 있고, #명장령#도 있으니, 천하에 누가 나를 상대하겠느냐!","주인공","","",},
        [234] = {601,"주인공","그건 아니죠, 내가 당신은 무찌를 테니까요!","그건 아니죠, 내가 당신은 무찌를 테니까요!","","","",},
        [235] = {601,"여포","너구나! 지난번에 내 #명장령#을 가져갔었지!","너구나! 지난번에 내 #명장령#을 가져갔었지!","","","",},
        [236] = {601,"주인공","맞아요, 바로 저예요! 무섭죠! 어서 #명장령#이나 내놓으시죠!","맞아요, 바로 저예요! 무섭죠! 어서 #명장령#이나 내놓으시죠!","","","",},
        [237] = {601,"여포","날 먼저 해치운 뒤에 다시 얘기하거라!","날 먼저 해치운 뒤에 다시 얘기하거라!","","","",},
        [238] = {602,"이각","어린 장군이 위풍당당하고 무예가 뛰어나군. 동탁에게 딸이 있는데 짝지어 주면 어떨까요?","어린 장군이 위풍당당하고 무예가 뛰어나군. 동탁에게 딸이 있는데 짝지어 주면 어떨까요?","관은병","","",},
        [239] = {602,"관은병","꿈 깨요! 반란을 일으킨 동탁 집안과 결혼을 할 것 같아요?","꿈 깨요! 반란을 일으킨 동탁 집안과 결혼을 할 것 같아요?","","","",},
        [240] = {602,"장성채","동탁의 딸은 얼굴도 못생겼다던데, 어떻게 어울리겠어요?","동탁의 딸은 얼굴도 못생겼다던데, 어떻게 어울리겠어요?","","","",},
        [241] = {603,"화웅","반봉은 이미 죽였다. 다음은 누구 차례냐!","반봉은 이미 죽였다. 다음은 누구 차례냐!","관우","","",},
        [242] = {603,"관우","화웅, 날뛰지 마라!","화웅, 날뛰지 마라!","","","",},
        [243] = {603,"주인공","화웅은 내 아래 있는 패장인데 어찌 직접 나서려고 하세요! 제가 하겠습니다.","화웅은 내 아래 있는 패장인데 어찌 직접 나서려고 하세요! 제가 하겠습니다.","","","",},
        [244] = {604,"관우","역시 영리하고 용맹스럽군!","역시 영리하고 용맹스럽군!","유비","","",},
        [245] = {604,"관우","아우가 화웅을 죽였으니, 지체하지 말고 지금 관문으로 들어가 동탁을 사로잡으시죠!","아우가 화웅을 죽였으니, 지체하지 말고 지금 관문으로 들어가 동탁을 사로잡으시죠!","","","",},
        [246] = {604,"유비","너무 서두르지 말거라. 먼저 용맹한 여포를 사로잡아야 동탁을 벨 수 있을 거다.","너무 서두르지 말거라. 먼저 용맹한 여포를 사로잡아야 동탁을 벨 수 있을 거다.","","","",},
        [247] = {604,"유비","넷째야, 우리가 도울 테니 함께 여포와 싸우자!","넷째야, 우리가 도울 테니 함께 여포와 싸우자!","","","",},
        [248] = {604,"여포","여포가 여기 있다! 누가 감히 내 아버지를 해하려 하느냐!","여포가 여기 있다! 누가 감히 내 아버지를 해하려 하느냐!","","","",},
        [249] = {604,"장비","연인 장비가 왔다! 여포는 각오해라!","연인 장비가 왔다! 여포는 각오해라!","","","",},
        [250] = {604,"여포","나는 전신 여포다. 함께 덤벼도 이기지 못할 것이다! 너희들에게 지면 #명장령#을 주겠다!","나는 전신 여포다. 함께 덤벼도 이기지 못할 것이다! 너희들에게 지면 #명장령#을 주겠다!","","","",},
        [251] = {604,"주인공","#명장령#은 제 몫이죠! 어서 싸워요!","#명장령#은 제 몫이죠! 어서 싸워요!","","","",},
        [252] = {605,"여포","난 초선 생각에 싸움에는 관심 없다! #명장령#이나 가져가라! (여포가 말을 달려 멀리 사라진다.)","난 초선 생각에 싸움에는 관심 없다! #명장령#이나 가져가라! (여포가 말을 달려 멀리 사라진다.)","장비","","",},
        [253] = {605,"장비","여포가 달아나요!","여포가 달아나요!","","","",},
        [254] = {605,"유비","아우, 그냥 놔두거라!","아우, 그냥 놔두거라!","","","",},
        [255] = {605,"유비","넷째야, 호뢰관은 이미 뚫렸으니, 우리는 공손찬과 유주로 돌아갈 것이다. 다음에 또 만나자!","넷째야, 호뢰관은 이미 뚫렸으니, 우리는 공손찬과 유주로 돌아갈 것이다. 다음에 또 만나자!","주인공","","",},
        [256] = {605,"주인공","다음에 만나요!","다음에 만나요!","","","",},
        [257] = {605,"장성채","#명장령#을 또 얻었어요! 이제 어디로 가죠?","#명장령#을 또 얻었어요! 이제 어디로 가죠?","","","",},
        [258] = {605,"주인공","도적을 잡으려면 두목을 잡아야죠. 낙양으로 가서 동탁을 잡아 화근을 없애야겠어요!","도적을 잡으려면 두목을 잡아야죠. 낙양으로 가서 동탁을 잡아 화근을 없애야겠어요!","","","",},
        [259] = {701,"조조","정탐꾼의 소식에 의하면 동탁이 각 로 제후에게 겁을 먹고, 낙양을 불태우고 천자를 위협하여 장안으로 간다고 하오. 제공들은 어떻게 생각하시오?","정탐꾼의 소식에 의하면 동탁이 각 로 제후에게 겁을 먹고, 낙양을 불태우고 천자를 위협하여 장안으로 간다고 하오. 제공들은 어떻게 생각하시오?","주인공","","",},
        [260] = {701,"주인공","동탁의 죄는 용서할 수가 없군요. 지금 추격한다면 천하를 평정할 수 있을 거예요! 낙양으로 가서 동탁을 사로잡아요!","동탁의 죄는 용서할 수가 없군요. 지금 추격한다면 천하를 평정할 수 있을 거예요! 낙양으로 가서 동탁을 사로잡아요!","","","",},
        [261] = {701,"원소","군사들은 피로하고, 동탁의 세력은 무시할 수 없으니 잠시 정비를 합시다. 강행하면 반드시 패할 것이오.","군사들은 피로하고, 동탁의 세력은 무시할 수 없으니 잠시 정비를 합시다. 강행하면 반드시 패할 것이오.","","","",},
        [262] = {701,"조조","정말 생각이 없군!","정말 생각이 없군!","","","",},
        [263] = {701,"주인공","원소는 큰일을 하기엔 부족하군요!","원소는 큰일을 하기엔 부족하군요!","","","",},
        [264] = {701,"조조","현재 18로 제후의 원소 부류에서 그대만 참으로 걸출하오. 당신과 만난 것은 정말 조조에게 큰 행운이라오.","현재 18로 제후의 원소 부류에서 그대만 참으로 걸출하오. 당신과 만난 것은 정말 조조에게 큰 행운이라오.","","","",},
        [265] = {701,"조조","강동의 손견은 이미 군대를 일으켜 낙양으로 갔으니, 그대와 우리 두 갈래로 나누어, 나는 동탁을 쫓고, 당신은 손견을 돕는 것이 어떻소?","강동의 손견은 이미 군대를 일으켜 낙양으로 갔으니, 그대와 우리 두 갈래로 나누어, 나는 동탁을 쫓고, 당신은 손견을 돕는 것이 어떻소?","","","",},
        [266] = {701,"주인공","좋아요, 저는 손견을 도우러 낙양으로 갈게요!","좋아요, 저는 손견을 도우러 낙양으로 갈게요!","","","",},
        [267] = {702,"황개","너는 누구길래, 낙양성에서 소란을 피우느냐?","너는 누구길래, 낙양성에서 소란을 피우느냐?","장성채","","",},
        [268] = {702,"장성채","우리는 손견을 도와 동탁을 토벌하러 왔어요!","우리는 손견을 도와 동탁을 토벌하러 왔어요!","","","",},
        [269] = {702,"황개","동탁이 첩자로 젊은것들을 보냈구나!","동탁이 첩자로 젊은것들을 보냈구나!","","","",},
        [270] = {702,"장성채","어쩜 저렇게 눈치가 없을까요? 조만간 고생 좀 하겠네요!","어쩜 저렇게 눈치가 없을까요? 조만간 고생 좀 하겠네요!","","","",},
        [271] = {702,"관은병","흥, 먼저 굴복시킨 후에 다시 얘기해야겠어요.","흥, 먼저 굴복시킨 후에 다시 얘기해야겠어요.","","","",},
        [272] = {703,"장성채","손견은 원래 원술의 부하이고, 원술, 원소와 같은 부류이니 먼저 손견과 겨루시죠?","손견은 원래 원술의 부하이고, 원술, 원소와 같은 부류이니 먼저 손견과 겨루시죠?","관은병","","",},
        [273] = {703,"관은병","성채가 또 짖굳게 구네요!","성채가 또 짖굳게 구네요!","","","",},
        [274] = {703,"장성채","히히!","히히!","","","",},
        [275] = {703,"장성채","손견, 승상의 딸이 매우 예쁘다고 들었어요. 장군도 아들이 있으니 결혼으로 맺어주면 어떨까요?","손견, 승상의 딸이 매우 예쁘다고 들었어요. 장군도 아들이 있으니 결혼으로 맺어주면 어떨까요?","손견","","",},
        [276] = {703,"손견","하늘의 뜻을 거스르고, 왕실을 혼란에 빠뜨린 동탁을 소탕해야 하는 판에 어떻게 역적과 사돈을 맺는단 말이냐!","하늘의 뜻을 거스르고, 왕실을 혼란에 빠뜨린 동탁을 소탕해야 하는 판에 어떻게 역적과 사돈을 맺는단 말이냐!","","","",},
        [277] = {704,"주인공","성채, 함부로 말하지 마요! 손 장군은 진정한 영웅이고, 황건적과 동탁을 토벌할 때 선봉에 섰고, 한실에 대한 충성심이 지극히 높다고요.","성채, 함부로 말하지 마요! 손 장군은 진정한 영웅이고, 황건적과 동탁을 토벌할 때 선봉에 섰고, 한실에 대한 충성심이 지극히 높다고요.","손견","","",},
        [278] = {704,"손견","과찬이시오! 그대가 혁혁한 공을 세워 이름을 널리 알린 것에 비하면 아무것도 아니오. 낙양성에서 얻은 #명장령#을 줄 테니 받으시오.","과찬이시오! 그대가 혁혁한 공을 세워 이름을 널리 알린 것에 비하면 아무것도 아니오. 낙양성에서 얻은 #명장령#을 줄 테니 받으시오.","","","",},
        [279] = {704,"주인공","감사합니다. 손 장군! 그럼 잘 받겠습니다!","감사합니다. 손 장군! 그럼 잘 받겠습니다!","","","",},
        [280] = {704,"주인공","손 장군은 앞으로 어떻게 하실 거죠?","손 장군은 앞으로 어떻게 하실 거죠?","","","",},
        [281] = {704,"손견","18로 제후들과 갈등이 많아, 강동으로 돌아가려고 하오.","18로 제후들과 갈등이 많아, 강동으로 돌아가려고 하오.","","","",},
        [282] = {704,"주인공","장군과 말이 잘 통하니, 장군을 따라 강동 유람이나 해야겠어요.","장군과 말이 잘 통하니, 장군을 따라 강동 유람이나 해야겠어요.","","","",},
        [283] = {704,"손견","지금 원소가 18로 제후의 맹주이니, 가서 작별인사를 해야겠소.","지금 원소가 18로 제후의 맹주이니, 가서 작별인사를 해야겠소.","","","",},
        [284] = {801,"손견","몸이 좋지 않아, 장사로 돌아가려고 인사하러 왔소.","몸이 좋지 않아, 장사로 돌아가려고 인사하러 왔소.","원소","","",},
        [285] = {801,"원소","옥새는 조정의 보물인데 왜 가지고 가려고 하시오?","옥새는 조정의 보물인데 왜 가지고 가려고 하시오?","","","",},
        [286] = {801,"손견","옥새는 내게 없소! 내가 옥새를 숨겼다면 맹세하건대 천수를 다하지 못하고 칼과 화살에 죽을 것이오!","옥새는 내게 없소! 내가 옥새를 숨겼다면 맹세하건대 천수를 다하지 못하고 칼과 화살에 죽을 것이오!","","","",},
        [287] = {801,"원소","공이 그렇게까지 말하는데 보내주지 않으면 내가 속 좁은 사람이 되겠지요! (원소가 보내주자, 손견은 작별인사를 한다.)","공이 그렇게까지 말하는데 보내주지 않으면 내가 속 좁은 사람이 되겠지요! (원소가 보내주자, 손견은 작별인사를 한다.)","손견","","",},
        [288] = {801,"원소","여봐라! 형주목 유표에게 편지를 보내 손견이 가는 길을 막고 옥새를 뺏으라고 하여라.","여봐라! 형주목 유표에게 편지를 보내 손견이 가는 길을 막고 옥새를 뺏으라고 하여라.","주인공","","",},
        [289] = {801,"장성채","원소는 정말 겉 다르고 속 다른 소인배네요. 손견이 정말 옥새를 숨긴 것 같나요?","원소는 정말 겉 다르고 속 다른 소인배네요. 손견이 정말 옥새를 숨긴 것 같나요?","주인공","","",},
        [290] = {801,"주인공","정말 옥새가 손견 손에 있다면 그것도 그의 능력이죠! 우리는 형주로 유람을 떠날 텐데 누구 손에 있든 뭐가 중요하겠어요!","정말 옥새가 손견 손에 있다면 그것도 그의 능력이죠! 우리는 형주로 유람을 떠날 텐데 누구 손에 있든 뭐가 중요하겠어요!","","","",},
        [291] = {801,"장성채","유표도 #명장령#을 가지고 있다던데, 기회를 봐서 조언을 구하면 좋겠어요.","유표도 #명장령#을 가지고 있다던데, 기회를 봐서 조언을 구하면 좋겠어요.","","","",},
        [292] = {801,"주인공","유표가 #명장령#을 가지고 있다면 어서 형주로 떠나요!","유표가 #명장령#을 가지고 있다면 어서 형주로 떠나요!","","","",},
        [293] = {802,"채모","어디서 왔는지 모르지만 두 미녀분은 이 채모와 함께 가는 게 좋겠소.","어디서 왔는지 모르지만 두 미녀분은 이 채모와 함께 가는 게 좋겠소.","장성채","","",},
        [294] = {802,"장성채","누구시죠? 대단하신 분인가요?","누구시죠? 대단하신 분인가요?","","","",},
        [295] = {802,"채모","나는 형주 양양성의 호족으로, 최근에 내 누이가 한실 종친 유표의 아내가 되었고. 채 씨 집안은 말할 것도 없다오!","나는 형주 양양성의 호족으로, 최근에 내 누이가 한실 종친 유표의 아내가 되었고. 채 씨 집안은 말할 것도 없다오!","","","",},
        [296] = {802,"장성채","흥, 누이가 유표의 첩으로 들어간 걸 너무 자랑하는데!","흥, 누이가 유표의 첩으로 들어간 걸 너무 자랑하는데!","","","",},
        [297] = {802,"관은병","채 씨가 쓸데없이 너무 나대는군, 맛 좀 봐라!","채 씨가 쓸데없이 너무 나대는군, 맛 좀 봐라!","","","",},
        [298] = {803,"유표","채모와 황조를 격파하다니 실력이 대단하구나.","채모와 황조를 격파하다니 실력이 대단하구나.","장성채","","",},
        [299] = {803,"장성채","유표가 젊었을 때 혼자서 형주에 들어가 형주 구군을 평정했대요. 정말 대단하죠.","유표가 젊었을 때 혼자서 형주에 들어가 형주 구군을 평정했대요. 정말 대단하죠.","","","",},
        [300] = {803,"관은병","뭐가 대단하다고. 양양성에 쉽게 들어가진 않을 테니, 다들 두들겨 맞을 각오를 해야 할 거야.","뭐가 대단하다고. 양양성에 쉽게 들어가진 않을 테니, 다들 두들겨 맞을 각오를 해야 할 거야.","","","",},
        [301] = {803,"유표","누가 맞을지는 아직 모르오. 내가 얼마나 강한지 한 번 보여주겠소!","누가 맞을지는 아직 모르오. 내가 얼마나 강한지 한 번 보여주겠소!","","","",},
        [302] = {804,"유표","참으로 대단하십니다. 패배를 인정합니다!","참으로 대단하십니다. 패배를 인정합니다!","주인공","","",},
        [303] = {804,"주인공","맞고 난 후 말투가 달라졌네요! 인정한다면, #명장령#을 내놓으세요!","맞고 난 후 말투가 달라졌네요! 인정한다면, #명장령#을 내놓으세요!","","","",},
        [304] = {804,"유표","내게는 #명장령#이 없습니다!","내게는 #명장령#이 없습니다!","","","",},
        [305] = {804,"주인공","뭐라고요? #명장령#이 없다고요? 그럼 괜히 왔잖아요, 덕분에...","뭐라고요? #명장령#이 없다고요? 그럼 괜히 왔잖아요, 덕분에...","","","",},
        [306] = {804,"황개","장군, 큰일 났습니다! 괴량의 간계에 빠져서 황조의 꾐에 성 밖으로 나가 고립되어 위험한 상황입니다!","장군, 큰일 났습니다! 괴량의 간계에 빠져서 황조의 꾐에 성 밖으로 나가 고립되어 위험한 상황입니다!","","","",},
        [307] = {804,"주인공","황개 장군은 걱정 마세요. 우리가 가서 손견을 구할게요!","황개 장군은 걱정 마세요. 우리가 가서 손견을 구할게요!","","","",},
        [308] = {805,"주인공","한 발 늦었구나! 황조는 사로잡았지만 손견 장군은 이미 화살에 맞아 죽었군.","한 발 늦었구나! 황조는 사로잡았지만 손견 장군은 이미 화살에 맞아 죽었군.","장성채","","",},
        [309] = {805,"장성채","그날 손견이 맹세한 대로 된 걸 보면, 옥새가 그의 손에... (말하는 사이 손책이 말을 타고 도착한다.)","그날 손견이 맹세한 대로 된 걸 보면, 옥새가 그의 손에... (말하는 사이 손책이 말을 타고 도착한다.)","","","",},
        [310] = {805,"손책","누가 부친을 죽인 거냐? 너희들이냐?","누가 부친을 죽인 거냐? 너희들이냐?","","","",},
        [311] = {805,"주인공","당신은?","당신은?","","","",},
        [312] = {805,"손책","난 손견의 장자, 동오 소패왕 손책이다! 아버지를 죽였으니 목숨을 내놓아라!","난 손견의 장자, 동오 소패왕 손책이다! 아버지를 죽였으니 목숨을 내놓아라!","","","",},
        [313] = {806,"황개","오해십니다! 이 젊은장수는  구하러 온 것이고, 황조 등이 주공을 죽였습니다.","오해십니다! 이 젊은장수는  구하러 온 것이고, 황조 등이 주공을 죽였습니다.","손책","","",},
        [314] = {806,"손책","아버지가 돌아가셨다고 해서 급한 마음에 무례를 범했습니다. 오해한 것이니 탓하지 마십시오.","아버지가 돌아가셨다고 해서 급한 마음에 무례를 범했습니다. 오해한 것이니 탓하지 마십시오.","","","",},
        [315] = {806,"주인공","이해해요. 황조를 붙잡았으니 손견 장군의 시체와 바꾸는 게 좋겠죠?","이해해요. 황조를 붙잡았으니 손견 장군의 시체와 바꾸는 게 좋겠죠?","","","",},
        [316] = {806,"손책","너무 고맙습니다! 어떻게 이 은헤를 갚아야 할지. 이 #명장령#을 사례로 드리고 싶습니다!","너무 고맙습니다! 어떻게 이 은헤를 갚아야 할지. 이 #명장령#을 사례로 드리고 싶습니다!","","","",},
        [317] = {806,"주인공","좋아요. 정말 호탕하시군요. 앞으로 어떻게 하실 거죠?","좋아요. 정말 호탕하시군요. 앞으로 어떻게 하실 거죠?","","","",},
        [318] = {806,"손책","선친의 옛 부하들은 원술 휘하에 있어, 잠시 원술에게 의탁하려고 합니다. 힘을 키워 다시 복수해야지요.","선친의 옛 부하들은 원술 휘하에 있어, 잠시 원술에게 의탁하려고 합니다. 힘을 키워 다시 복수해야지요.","","","",},
        [319] = {806,"손책","그때가 되면 장군께서 다시 강동으로 와서, 힘을 보태주시실 바랍니다! (손책이 슬퍼하며 손견의 시체를 안고 떠난다.)","그때가 되면 장군께서 다시 강동으로 와서, 힘을 보태주시실 바랍니다! (손책이 슬퍼하며 손견의 시체를 안고 떠난다.)","","","",},
        [320] = {806,"장성채","이제 어디로 가죠?","이제 어디로 가죠?","","","",},
        [321] = {806,"주인공","그날 조조가 동탁을 막으러 갔는데, 형양에 도착했다고 하니, 그곳으로 가보죠!","그날 조조가 동탁을 막으러 갔는데, 형양에 도착했다고 하니, 그곳으로 가보죠!","","","",},
