        [730] = {2018502,"장각","가장 얻기 힘든 것이 민심이다. 민심이 따르고 있으니 이 기회를 놓쳐서는 안 된다. 오늘부터 나는 천공장군이다.","가장 얻기 힘든 것이 민심이다. 민심이 따르고 있으니 이 기회를 놓쳐서는 안 된다. 오늘부터 나는 천공장군이다.","","","",},
        [731] = {2018502,"장보","형님, 그럼 저는요?","형님, 그럼 저는요?","장각","","",},
