        [739] = {2018601,"원술","손견이 황건적을 토벌하고 공을 세웠다던데, 좀 알아보았느냐?","손견이 황건적을 토벌하고 공을 세웠다던데, 좀 알아보았느냐?","원술 부하","","",},
        [740] = {2018601,"원술 부하","손 씨는 대대로 오지에서 관직을 맡았고, 부춘에 집이 있습니다. 손견이 태어나기 전에 조상의 무덤에서 기이한 빛이 나타나, 오색 기운이 하늘까지 이어지고, 멀리까지 보였다고 합니다.","손 씨는 대대로 오지에서 관직을 맡았고, 부춘에 집이 있습니다. 손견이 태어나기 전에 조상의 무덤에서 기이한 빛이 나타나, 오색 기운이 하늘까지 이어지고, 멀리까지 보였다고 합니다.","원술","","",},
        [741] = {2018601,"원술","오, 신기한 일이구나!","오, 신기한 일이구나!","원술 부하","","",},
        [742] = {2018601,"원술 부하","마을 어른들이 이는 신기한 광경으로 앞으로 집안이 크게 번성할 것이다.라고 말했는데, 태어나보니 용모가 범상치 않았다고 합니다.","마을 어른들이 이는 신기한 광경으로 앞으로 집안이 크게 번성할 것이다.라고 말했는데, 태어나보니 용모가 범상치 않았다고 합니다.","원술","","",},
        [743] = {2018601,"원술 부하","어려서 삼군 현승을 맡아 이름을 날리고, 후에 반란군을 무찔러 장사태수가 되었다고 합니다.","어려서 삼군 현승을 맡아 이름을 날리고, 후에 반란군을 무찔러 장사태수가 되었다고 합니다.","원술","","",},
        [744] = {2018601,"원술","손견이 아무리 잘났어도 원가의 사세삼공에는 못 미치지.","손견이 아무리 잘났어도 원가의 사세삼공에는 못 미치지.","원술 부하","","",},
