        [746] = {2018601,"원술","좋다. 손견을 파로장군 겸 예주자사로 상주해야겠다.","좋다. 손견을 파로장군 겸 예주자사로 상주해야겠다.","원술 부하","","",},
        [747] = {2018602,"유표","손견, 어찌 우리 형주를 지나가시오?","손견, 어찌 우리 형주를 지나가시오?","손견","","",},
        [748] = {2018602,"손견","18로 제후들과 갈등이 많아, 강동으로 돌아가려고 하오.","18로 제후들과 갈등이 많아, 강동으로 돌아가려고 하오.","유표","","",},
        [749] = {2018602,"손견","당초 사직을 구하기 위해 원소, 원술과 의병을 일으켰는데, 이제 역적도 섬멸했잖소. 내부에서 분쟁이 있으니 누구와 힘을 합칠 수 있겠소?","당초 사직을 구하기 위해 원소, 원술과 의병을 일으켰는데, 이제 역적도 섬멸했잖소. 내부에서 분쟁이 있으니 누구와 힘을 합칠 수 있겠소?","유표","","",},
        [750] = {2018602,"유표","강동으로 돌아가겠다면 말리지는 않겠소. 하지만 옥새는 조정의 보물인데 왜 가지고 가려고 하시오?","강동으로 돌아가겠다면 말리지는 않겠소. 하지만 옥새는 조정의 보물인데 왜 가지고 가려고 하시오?","손견","","",},
        [751] = {2018602,"손견","(크게 노한다.) 지금 싸우자는 것이오?","(크게 노한다.) 지금 싸우자는 것이오?","유표","","",},
        [752] = {2018603,"한당","장군, 광풍이 불어 깃발이 부러진 것을 보니 불길한데, 철수하시죠.","장군, 광풍이 불어 깃발이 부러진 것을 보니 불길한데, 철수하시죠.","손견","","",},
        [753] = {2018603,"손견","양양을 곧 얻을 수 있는데 깃발 때문에 전쟁을 멈춰서야 되겠느냐!","양양을 곧 얻을 수 있는데 깃발 때문에 전쟁을 멈춰서야 되겠느냐!","한당","","",},
        [754] = {2018603,"괴량","밤에 천문을 보니 별이 떨어져 들판을 반으로 가르고 손견에게 떨어졌습니다.  원소에게 편지를 보내 도와달라고 하십시오.","밤에 천문을 보니 별이 떨어져 들판을 반으로 가르고 손견에게 떨어졌습니다.  원소에게 편지를 보내 도와달라고 하십시오.","손견","","",},
