        [764] = {2018604,"군사","장군! (산에서 돌과 화살이 빗발쳐 손견이 화살을 맞고 죽으니 향년 37세였다.)","장군! (산에서 돌과 화살이 빗발쳐 손견이 화살을 맞고 죽으니 향년 37세였다.)","손견","","",},
        [765] = {2018701,"황현군수","지금 주와 군 사이에 분쟁이 있는데, 낙양의 유사(상과 벌을 담당하는 관리)가 판결한다고 하오.","지금 주와 군 사이에 분쟁이 있는데, 낙양의 유사(상과 벌을 담당하는 관리)가 판결한다고 하오.","시첩","","",},
        [766] = {2018701,"시첩","관리의 손에 먼저 상소문을 올리는 자가 유리하겠네요.","관리의 손에 먼저 상소문을 올리는 자가 유리하겠네요.","황현군수","","",},
