        [768] = {2018701,"시첩","태사자를 추천할게요. 듣기로 스물한 살인데 키가 크고, 원숭이 같은 팔에 활을 잘 쏘는 신궁이라니 그를 보내시죠.","태사자를 추천할게요. 듣기로 스물한 살인데 키가 크고, 원숭이 같은 팔에 활을 잘 쏘는 신궁이라니 그를 보내시죠.","황현군수","","",},
        [769] = {2018701,"황현군수","좋소. 태사자를 빨리 낙양으로 보냅시다! (태사자가 밤낮을 달려 낙양에 도착한다.)","좋소. 태사자를 빨리 낙양으로 보냅시다! (태사자가 밤낮을 달려 낙양에 도착한다.)","시첩","","",},
        [770] = {2018702,"태사자","(공문 앞에서 오랫동안 기다린다.) 당신도 상소를 올리러 왔소?","(공문 앞에서 오랫동안 기다린다.) 당신도 상소를 올리러 왔소?","주리","","",},
