        [773] = {2018702,"주리","왜 내 상소문을 찢는 거요!","왜 내 상소문을 찢는 거요!","태사자","","",},
        [774] = {2018702,"태사자","당신이 상소문을 주지 않았다면 이런 일도 없었을 거요. 서로 벌을 받을 수 있으니, 함께 도망가서 목숨을 보전합시다!","당신이 상소문을 주지 않았다면 이런 일도 없었을 거요. 서로 벌을 받을 수 있으니, 함께 도망가서 목숨을 보전합시다!","주리","","",},
        [775] = {2018702,"주리","좋소.","좋소.","태사자","","",},
        [776] = {2018702,"태사자","그럼 빨리 성을 나가 살길을 찾읍시다. (나중에 낙양으로 돌아가 군 태수의 인장을 전달하고 임무를 완수하여 이름을 널리 알린다.)","그럼 빨리 성을 나가 살길을 찾읍시다. (나중에 낙양으로 돌아가 군 태수의 인장을 전달하고 임무를 완수하여 이름을 널리 알린다.)","주리","","",},
        [777] = {2018703,"태사자","지금이 손책을 잡을 때다! 용기 있는 자들은 나를 따르라!","지금이 손책을 잡을 때다! 용기 있는 자들은 나를 따르라!","손책","","",},
        [778] = {2018703,"손책","누구시오?","누구시오?","태사자","","",},
        [779] = {2018703,"태사자","동래 출신 태사자요. 손책을 잡으러 왔는데 누가 손책이오?","동래 출신 태사자요. 손책을 잡으러 왔는데 누가 손책이오?","손책","","",},
        [780] = {2018703,"손책","바로 나요. 자, 덤비시오. 나 손책은 하나도 두렵지 않소.","바로 나요. 자, 덤비시오. 나 손책은 하나도 두렵지 않소.","태사자","","",},
        [781] = {2018703,"태사자","모두 덤벼도 두렵지 않소! (서로 창을 겨누며 오십 합을 싸우고도 승부가 나지 않는다.)","모두 덤벼도 두렵지 않소! (서로 창을 겨누며 오십 합을 싸우고도 승부가 나지 않는다.)","손책","","",},
        [782] = {2018801,"소교","언니는 나중에 어떤 사람과 결혼하고 싶어요?","언니는 나중에 어떤 사람과 결혼하고 싶어요?","대교","","",},
        [783] = {2018801,"대교","소교, 짓궂게 왜 그래!","소교, 짓궂게 왜 그래!","소교","","",},
        [784] = {2018801,"소교","언니, 얼굴이 빨개졌어요! 좋아하는 사람 있어요?","언니, 얼굴이 빨개졌어요! 좋아하는 사람 있어요?","대교","","",},
        [785] = {2018801,"교공","소교, 이제 그만 놀리거라. 지금 완성도 위험하니 너희들은 집안에만 있고 나가지 말아라.","소교, 이제 그만 놀리거라. 지금 완성도 위험하니 너희들은 집안에만 있고 나가지 말아라.","소교","","",},
        [786] = {2018801,"대교","네, 알겠어요.","네, 알겠어요.","교공","","",},
        [787] = {2018802,"소교","집에만 있으니 답답해. 아버지가 안 계시니 몰래 나가서 놀아야겠어!","집에만 있으니 답답해. 아버지가 안 계시니 몰래 나가서 놀아야겠어!","대교","","",},
        [788] = {2018802,"대교","소교, 빨리 집에 가자. 네가 안 보여서 걱정하고 계셔.","소교, 빨리 집에 가자. 네가 안 보여서 걱정하고 계셔.","소교","","",},
        [789] = {2018802,"손책","낭자, 정말 아름답소. 내가 집까지 데려다주고 싶은데, 괜찮겠소?","낭자, 정말 아름답소. 내가 집까지 데려다주고 싶은데, 괜찮겠소?","대교","","",},
        [790] = {2018802,"대교","(대교는 손책을 보자 얼굴이 빨개진다.) 괜찮아요. 소교, 어서 가자!","(대교는 손책을 보자 얼굴이 빨개진다.) 괜찮아요. 소교, 어서 가자!","손책","","",},
        [791] = {2018802,"주유","손책, 놀라게 하지 마세요!","손책, 놀라게 하지 마세요!","손책","","",},
        [792] = {2018802,"대교","빨리 집으로 가자! (대교가 소교를 데리고 급하게 집으로 간다.)","빨리 집으로 가자! (대교가 소교를 데리고 급하게 집으로 간다.)","손책","","",},
        [793] = {2018803,"대교","완성이 무너져서 아버지가 나를 손책에게 시집보낸다는데...","완성이 무너져서 아버지가 나를 손책에게 시집보낸다는데...","","","",},
        [794] = {2018803,"대교","손책은 어떤 사람일까? 얼굴 한 번 못 보고 시집을 가는구나.","손책은 어떤 사람일까? 얼굴 한 번 못 보고 시집을 가는구나.","","","",},
        [795] = {2018803,"손책","낭자, 날 기억하나요? 그날 만난 후 계속 당신을 생각했다오. 나와 결혼해 백년해로하겠소?","낭자, 날 기억하나요? 그날 만난 후 계속 당신을 생각했다오. 나와 결혼해 백년해로하겠소?","대교","","",},
        [796] = {2018803,"손책","걱정 마시오. 원하지 않는다면 강요하지 않겠소.","걱정 마시오. 원하지 않는다면 강요하지 않겠소.","대교","","",},
        [797] = {2018803,"대교","(매우 수줍어한다.)...","(매우 수줍어한다.)...","손책","","",},
        [798] = {2018803,"손책","말 안 해도 좋소. 원하면 고개를 끄덕이고, 원치 않으면 고개를 가로저으시오.","말 안 해도 좋소. 원하면 고개를 끄덕이고, 원치 않으면 고개를 가로저으시오.","대교","","",},
        [799] = {2018803,"대교","(가볍게 고개를 끄덕이며, 작은 소리로 말한다.) 저를 진심으로 대하시면 저도 변치 않겠어요!","(가볍게 고개를 끄덕이며, 작은 소리로 말한다.) 저를 진심으로 대하시면 저도 변치 않겠어요!","손책","","",},
        [800] = {2018901,"탁군태수","얘야, 오늘 널 위해 좋은 혼사를 정했다!","얘야, 오늘 널 위해 좋은 혼사를 정했다!","공손 부인","","",},
        [801] = {2018901,"공손 부인","제 혼사를 마음대로 정하시면 어떡해요!","제 혼사를 마음대로 정하시면 어떡해요!","탁군태수","","",},
        [802] = {2018901,"탁군태수","너도 분명 맘에 들 거다. 이름은 공손찬이고 요서 귀족 출신이야.","너도 분명 맘에 들 거다. 이름은 공손찬이고 요서 귀족 출신이야.","공손 부인","","",},
        [803] = {2018901,"탁군태수","용모도 준수하고 목소리도 좋고, 전도유망한 지혜로운 젊은이란다.","용모도 준수하고 목소리도 좋고, 전도유망한 지혜로운 젊은이란다.","공손 부인","","",},
        [804] = {2018901,"공손 부인","그럼 아버지 뜻에 따르겠어요!","그럼 아버지 뜻에 따르겠어요!","탁군태수","","",},
        [805] = {2018901,"탁군태수","내 딸이 공손 부인이 되는구나, 하하!","내 딸이 공손 부인이 되는구나, 하하!","공손 부인","","",},
        [806] = {2018902,"유비","형님은 참 복이 많습니다!","형님은 참 복이 많습니다!","공손찬","","",},
        [807] = {2018902,"공손찬","당신은 중산정왕의 후손인데, 내가 당신과 비교가 되겠소!","당신은 중산정왕의 후손인데, 내가 당신과 비교가 되겠소!","유비","","",},
        [808] = {2018902,"유비","이제 탁군태수의 사위가 되었고, 장인이 노식을 스승으로 삼게 해주었으니 앞날이 창창하지요!","이제 탁군태수의 사위가 되었고, 장인이 노식을 스승으로 삼게 해주었으니 앞날이 창창하지요!","공손찬","","",},
        [809] = {2018902,"공손찬","하하, 당신도 노식의 제자가 되지 않소! 우리는 동창이고 또 동향이니, 앞으로 잘해봅시다.","하하, 당신도 노식의 제자가 되지 않소! 우리는 동창이고 또 동향이니, 앞으로 잘해봅시다.","유비","","",},
        [810] = {2018903,"공손찬","나는 변경을 지키는 백마장군 공손찬이다! 오항의 도적들은 어서 나와라!","나는 변경을 지키는 백마장군 공손찬이다! 오항의 도적들은 어서 나와라!","오항왕","","",},
        [811] = {2018903,"오항왕","장군, 우리 둘 다 명령에 복종하는 사람들인데 서로 싸울 필요가 있겠소?","장군, 우리 둘 다 명령에 복종하는 사람들인데 서로 싸울 필요가 있겠소?","공손찬","","",},
        [812] = {2018903,"공손찬","허튼 소리! 네 놈들을 한 명씩 상대해주마! 어서 덤벼라!","허튼 소리! 네 놈들을 한 명씩 상대해주마! 어서 덤벼라!","오항왕","","",},
        [813] = {2018903,"오항왕","이런 용맹스러운 영웅호걸이 있다니?! 알겠소. 모두 철수하라!","이런 용맹스러운 영웅호걸이 있다니?! 알겠소. 모두 철수하라!","공손찬","","",},
        [814] = {2018903,"오항왕","목숨을 부지하는 것이 더 중요하니, 앞으로 백마 탄 무리를 보면 바로 철수하겠소!","목숨을 부지하는 것이 더 중요하니, 앞으로 백마 탄 무리를 보면 바로 철수하겠소!","공손찬","","",},
        [815] = {20181001,"반란을 일으킨 저인","운이 좋아 오늘 한족 십여 명을 잡았군. 다 죽여야겠어! 하하하!","운이 좋아 오늘 한족 십여 명을 잡았군. 다 죽여야겠어! 하하하!","가후","","",},
        [816] = {20181001,"가후","(운이 정말 없군. 고향으로 가는 길에 강도를 만나다니! 이렇게 죽을 순 없으니 방법을 생각해보자!)","(운이 정말 없군. 고향으로 가는 길에 강도를 만나다니! 이렇게 죽을 순 없으니 방법을 생각해보자!)","반란을 일으킨 저인","","",},
        [817] = {20181001,"가후","(왜 우리를 잡은 걸까? 돈이 필요한가 아니면 목숨을 원하는 것인가! 옳거니!) 내가 누군 줄 아느냐?","(왜 우리를 잡은 걸까? 돈이 필요한가 아니면 목숨을 원하는 것인가! 옳거니!) 내가 누군 줄 아느냐?","반란을 일으킨 저인","","",},
        [818] = {20181001,"반란을 일으킨 저인","뭐라고 하는 거냐?","뭐라고 하는 거냐?","가후","","",},
        [819] = {20181001,"가후","그 유명한 단공을 아느냐?","그 유명한 단공을 아느냐?","반란을 일으킨 저인","","",},
        [820] = {20181001,"반란을 일으킨 저인","황보규, 장환과 함께 양주삼명으로 이름을 알린 그 장군말이군.","황보규, 장환과 함께 양주삼명으로 이름을 알린 그 장군말이군.","가후","","",},
        [821] = {20181001,"가후","맞다! 내가 바로 단공의 외손자이다. 나를 해치지 않으면 충분한 돈으로 사례하겠다.","맞다! 내가 바로 단공의 외손자이다. 나를 해치지 않으면 충분한 돈으로 사례하겠다.","반란을 일으킨 저인","","",},
        [822] = {20181001,"반란을 일으킨 저인","한족은 교활하니 맹세를 해야 풀어주겠다! (가후가 맹세하자, 저인들이 풀어주었다. 다른 사람들은 모두 죽었고, 가후는 기지를 발휘하여 목숨을 건졌다.)","한족은 교활하니 맹세를 해야 풀어주겠다! (가후가 맹세하자, 저인들이 풀어주었다. 다른 사람들은 모두 죽었고, 가후는 기지를 발휘하여 목숨을 건졌다.)","가후","","",},
        [823] = {20181002,"이각","왕윤이 그냥 지나치지 않을 텐데, 어서 도망갑시다!","왕윤이 그냥 지나치지 않을 텐데, 어서 도망갑시다!","가후","","",},
        [824] = {20181002,"가후","단독으로 행동하면 분명 사로잡힐 것이니, 군대를 모아서 장안으로 쳐들어가 동탁의 원수를 갚는 것이 좋겠습니다.","단독으로 행동하면 분명 사로잡힐 것이니, 군대를 모아서 장안으로 쳐들어가 동탁의 원수를 갚는 것이 좋겠습니다.","이각","","",},
        [825] = {20181002,"이각","실패하면 어쩌죠?","실패하면 어쩌죠?","가후","","",},
