        [827] = {20181002,"이각","정말 대단하십니다. 장안으로 쳐들어갑시다!","정말 대단하십니다. 장안으로 쳐들어갑시다!","가후","","",},
        [828] = {20181003,"조조","후계자를 세우려고 하는데, 누구로 하면 좋겠느냐?","후계자를 세우려고 하는데, 누구로 하면 좋겠느냐?","가후","","",},
