        [1392] = {20184204,"전위","장수가 반란을 일으킨 것으니 제가 막겠습니다. 먼저 가십시오!","장수가 반란을 일으킨 것으니 제가 막겠습니다. 먼저 가십시오!","조조","","",},
        [1393] = {20184204,"조조","장수가 일부러 항복하고 반란을 일으켰구나!","장수가 일부러 항복하고 반란을 일으켰구나!","전위","","",},
