        [1395] = {20184301,"동탁","이각, 당신은 용맹하고 용병술에 능하고 언변이 좋잖소. 손견이 잘 싸워 아군이 계속 패하는데 무슨 방책이 없소?","이각, 당신은 용맹하고 용병술에 능하고 언변이 좋잖소. 손견이 잘 싸워 아군이 계속 패하는데 무슨 방책이 없소?","이각","","",},
        [1396] = {20184301,"이각","듣기로 손견이 아들이 있는데 매우 영명하다고 합니다. 승딸의 딸과 결혼을 시키면 좋을 것 같습니다.","듣기로 손견이 아들이 있는데 매우 영명하다고 합니다. 승딸의 딸과 결혼을 시키면 좋을 것 같습니다.","동탁","","",},
        [1397] = {20184301,"동탁","좋소, 직접 얘기해 보시오. 일이 성사되면 큰 상을 내리겠소.","좋소, 직접 얘기해 보시오. 일이 성사되면 큰 상을 내리겠소.","이각","","",},
        [1398] = {20184301,"이각","승상은 저만 믿으십시오!","승상은 저만 믿으십시오!","동탁","","",},
        [1399] = {20184302,"이각","동탁은 이미 죽고, 나는 서량으로 달아나려고 하오, 살길이 있을지 모르겠소.","동탁은 이미 죽고, 나는 서량으로 달아나려고 하오, 살길이 있을지 모르겠소.","가후","","",},
        [1400] = {20184302,"가후","내게 계책이 있습니다! 성사되면 조정을 받들어 천하를 평정할 수 있고, 실패하면 그때 달아나도 늦지 않습니다.","내게 계책이 있습니다! 성사되면 조정을 받들어 천하를 평정할 수 있고, 실패하면 그때 달아나도 늦지 않습니다.","이각","","",},
        [1401] = {20184302,"이각","빨리 말해 보시오. 무슨 계책이요?","빨리 말해 보시오. 무슨 계책이요?","가후","","",},
        [1402] = {20184302,"가후","단독으로 행동하면 분명 사로잡힐 것이니, 군대를 모아서 장안으로 쳐들어가 동탁의 원수를 갚는 것이 좋겠습니다.","단독으로 행동하면 분명 사로잡힐 것이니, 군대를 모아서 장안으로 쳐들어가 동탁의 원수를 갚는 것이 좋겠습니다.","이각","","",},
        [1403] = {20184302,"이각","괜찮은 방법이오. 왕윤이 지금 동탁 군대를 소탕하고 있소. 왕윤이 서량인을 모두 죽인다고 소문을 내면 군대를 모아 장안으로 쳐들어갈 수 있을 것이오.","괜찮은 방법이오. 왕윤이 지금 동탁 군대를 소탕하고 있소. 왕윤이 서량인을 모두 죽인다고 소문을 내면 군대를 모아 장안으로 쳐들어갈 수 있을 것이오.","가후","","",},
        [1404] = {20184303,"한헌제","황제인 짐이 어찌 이리 비참해졌는가. 이각과 곽사가 흉악하여 장안을 난장판으로 만들었구나.","황제인 짐이 어찌 이리 비참해졌는가. 이각과 곽사가 흉악하여 장안을 난장판으로 만들었구나.","가후","","",},
        [1405] = {20184303,"가후","폐하, 장안을 떠나 낙양으로 가는 것이 좋겠습니다.","폐하, 장안을 떠나 낙양으로 가는 것이 좋겠습니다.","한헌제","","",},
        [1406] = {20184303,"한헌제","이각과 곽사가 반대할까 걱정이오.","이각과 곽사가 반대할까 걱정이오.","가후","","",},
        [1407] = {20184303,"가후","걱정 마십시오! 제게 묘책이 있습니다. 곽사의 아내는 질투가 많다고 하니 반간계를 이용하면 서로 죽일 것입니다.","걱정 마십시오! 제게 묘책이 있습니다. 곽사의 아내는 질투가 많다고 하니 반간계를 이용하면 서로 죽일 것입니다.","한헌제","","",},
        [1408] = {20184304,"곽사","이각, 나를 연회에 초대해서 술에 독을 타다니 왜 나를 해치려는 것이오?","이각, 나를 연회에 초대해서 술에 독을 타다니 왜 나를 해치려는 것이오?","이각","","",},
        [1409] = {20184304,"곽사","내 아내가 똥을 주어 토하지 않았다면 난 죽었을 거요.","내 아내가 똥을 주어 토하지 않았다면 난 죽었을 거요.","이각","","",},
        [1410] = {20184304,"이각","내 아내를 먼저 유혹한 사람이 누군데 그러시오!","내 아내를 먼저 유혹한 사람이 누군데 그러시오!","곽사","","",},
        [1411] = {20184304,"곽사","너는 무녀를 함부로 믿어 군에서도 북을 치게 하고 주문을 외우게 했지. 내가 언제 못생기고 어리석은 네 아내를 꼬드겼느냐?","너는 무녀를 함부로 믿어 군에서도 북을 치게 하고 주문을 외우게 했지. 내가 언제 못생기고 어리석은 네 아내를 꼬드겼느냐?","이각","","",},
        [1412] = {20184304,"이각","모욕은 참을 수 없다! 병사를 모아 너를 죽이겠다!","모욕은 참을 수 없다! 병사를 모아 너를 죽이겠다!","곽사","","",},
        [1413] = {20184305,"곽사","전에도 네가 나를 죽기살기로 때렸던 것이 기억나는구나.","전에도 네가 나를 죽기살기로 때렸던 것이 기억나는구나.","이각","","",},
        [1414] = {20184305,"이각","영원한 적은 없고 영원한 이익만 있는 법이니, 우리가 힘을 합치지 않으면 위험에 빠질 것이오.","영원한 적은 없고 영원한 이익만 있는 법이니, 우리가 힘을 합치지 않으면 위험에 빠질 것이오.","곽사","","",},
        [1415] = {20184305,"이각","동승이 헌제를 데리고 장안을 떠나 홍농으로 갔소. 산동에 이르러 기반을 다지면 천하 제후가 우리를 토벌하려고 할 것이오.","동승이 헌제를 데리고 장안을 떠나 홍농으로 갔소. 산동에 이르러 기반을 다지면 천하 제후가 우리를 토벌하려고 할 것이오.","곽사","","",},
        [1416] = {20184305,"곽사","장제병이 장안을 점거하여 쉽게 움직일 수 없소. 우리가 같이 홍농에서 한군을 죽이고 천하를 평정해야겠소! 이락과 양봉의 세력이 큰데 무슨 계책이 있소?","장제병이 장안을 점거하여 쉽게 움직일 수 없소. 우리가 같이 홍농에서 한군을 죽이고 천하를 평정해야겠소! 이락과 양봉의 세력이 큰데 무슨 계책이 있소?","이각","","",},
        [1417] = {20184305,"이각","약자는 죽이고 강한자로 채운 '감사군'을 만들고, 장병의 옷가지를 길에 내버리면 적군의 마음이 혼란해질 것이오.","약자는 죽이고 강한자로 채운 '감사군'을 만들고, 장병의 옷가지를 길에 내버리면 적군의 마음이 혼란해질 것이오.","곽사","","",},
        [1418] = {20184305,"곽사","역시 용맹하고 계책이 많구려. 대단하오!","역시 용맹하고 계책이 많구려. 대단하오!","이각","","",},
        [1419] = {20184401,"소교","언니, 부탁이에요. 이 토끼를 나한테 주면 안 돼요?","언니, 부탁이에요. 이 토끼를 나한테 주면 안 돼요?","대교","","",},
        [1420] = {20184401,"대교","아버지가 준 선물인데 어떻게 주겠어? 항상 내 물건을 뺏었지만 이번에는 줄 수 없어.","아버지가 준 선물인데 어떻게 주겠어? 항상 내 물건을 뺏었지만 이번에는 줄 수 없어.","소교","","",},
        [1421] = {20184401,"소교","언니, 안 주면 언니랑 말도 안 할 거예요.","언니, 안 주면 언니랑 말도 안 할 거예요.","대교","","",},
        [1422] = {20184401,"대교","그러렴. 며칠은 조용히 지낼 수 있겠구나.","그러렴. 며칠은 조용히 지낼 수 있겠구나.","소교","","",},
        [1423] = {20184401,"소교","흥, 정말 안 주면 울 거예요. 으앙...","흥, 정말 안 주면 울 거예요. 으앙...","대교","","",},
        [1424] = {20184401,"대교","알았어, 줄게. 울었다가 웃었다가, 앞으로 너와 결혼하는 사람은 어떻게 하니?","알았어, 줄게. 울었다가 웃었다가, 앞으로 너와 결혼하는 사람은 어떻게 하니?","소교","","",},
        [1425] = {20184401,"소교","(눈물을 닦으며 웃는다.) 고마워요. 이 세상에서 언니가 제일 좋아요. 앞으로 언니를 잘 보살펴 줄게요!","(눈물을 닦으며 웃는다.) 고마워요. 이 세상에서 언니가 제일 좋아요. 앞으로 언니를 잘 보살펴 줄게요!","대교","","",},
        [1426] = {20184402,"소교","형부가 떠난 후, 언니가 계속 외로워해요.","형부가 떠난 후, 언니가 계속 외로워해요.","주유","","",},
        [1427] = {20184402,"주유","이야기도 많이 나누면서 달래주는 게 좋겠소.","이야기도 많이 나누면서 달래주는 게 좋겠소.","소교","","",},
        [1428] = {20184402,"소교","우리 집에 와서 며칠 쉬라고 하면 어떨까요?","우리 집에 와서 며칠 쉬라고 하면 어떨까요?","주유","","",},
        [1429] = {20184402,"주유","편한대로 하시오.","편한대로 하시오.","소교","","",},
        [1430] = {20184403,"제갈량","...좌우에 쌍대를 세우니 하나는 옥룡이고 하나는 금봉이다. 이교를 동남에서 끌어와 아침 저녁으로 즐기리라.","...좌우에 쌍대를 세우니 하나는 옥룡이고 하나는 금봉이다. 이교를 동남에서 끌어와 아침 저녁으로 즐기리라.","소교","","",},
        [1431] = {20184403,"소교","조식이 지은 <동작대부>를 읊고 계셨군요.","조식이 지은 <동작대부>를 읊고 계셨군요.","제갈량","","",},
        [1432] = {20184403,"제갈량","이것을 알고 있을지 몰랐군요.","이것을 알고 있을지 몰랐군요.","소교","","",},
        [1433] = {20184403,"소교","제 기억으로 원문은 '동서로 이어진 두 다리가 마치 무지개처럼 하늘에 걸려있다'인데, 주유를 자극하려고 바꾸신 거군요?","제 기억으로 원문은 '동서로 이어진 두 다리가 마치 무지개처럼 하늘에 걸려있다'인데, 주유를 자극하려고 바꾸신 거군요?","제갈량","","",},
        [1434] = {20184403,"제갈량","참으로 영특하시군요. 정말 탄복했습니다.","참으로 영특하시군요. 정말 탄복했습니다.","소교","","",},
        [1435] = {20184403,"소교","조조의 대군이 남하하면 형주와 강동은 인접해 있어 연합하지 않으면 조조의 밥이 되겠지요.","조조의 대군이 남하하면 형주와 강동은 인접해 있어 연합하지 않으면 조조의 밥이 되겠지요.","제갈량","","",},
        [1436] = {20184403,"소교","저도 지금의 형세를 알고 있으니 주유를 설득해 당신을 돕도록 하겠어요.","저도 지금의 형세를 알고 있으니 주유를 설득해 당신을 돕도록 하겠어요.","제갈량","","",},
        [1437] = {20184404,"소교","요즘 무슨 근심거리라도 있나요?","요즘 무슨 근심거리라도 있나요?","주유","","",},
        [1438] = {20184404,"주유","그냥 군에 일이 많아서 그렇소.","그냥 군에 일이 많아서 그렇소.","소교","","",},
        [1439] = {20184404,"소교","그럼, 제가 잠시 쉬시라고 춤을 한 번 출게요.","그럼, 제가 잠시 쉬시라고 춤을 한 번 출게요.","주유","","",},
        [1440] = {20184404,"주유","평소에 춤을 아름답고 부드럽고 다채롭게 추었는데, 오늘은 왜 <대풍가>를 추었소?","평소에 춤을 아름답고 부드럽고 다채롭게 추었는데, 오늘은 왜 <대풍가>를 추었소?","소교","","",},
        [1441] = {20184404,"소교","<대풍가>는 한고조 유방이 지은 것이죠. 당시 유방은 항우에게 번번이 졌지만 항우의 반대세력과 연합하여 결국 천하를 통일하고 한나라를 세웠어요.","<대풍가>는 한고조 유방이 지은 것이죠. 당시 유방은 항우에게 번번이 졌지만 항우의 반대세력과 연합하여 결국 천하를 통일하고 한나라를 세웠어요.","주유","","",},
        [1442] = {20184404,"주유","유비와 연합하여 조조에 대항하라는 뜻이군요. 걱정 마시오. 이미 아뢰어 공명과 대책을 세웠소.","유비와 연합하여 조조에 대항하라는 뜻이군요. 걱정 마시오. 이미 아뢰어 공명과 대책을 세웠소.","소교","","",},
        [1443] = {20184404,"소교","눈치가 이처럼 빠르시니 정말 대단하세요!","눈치가 이처럼 빠르시니 정말 대단하세요!","주유","","",},
        [1444] = {20184501,"손책","병사를 일으켜 절강을 안정시키려고 왔는데, 너는 왜 도적을 도왔느냐?","병사를 일으켜 절강을 안정시키려고 왔는데, 너는 왜 도적을 도왔느냐?","왕랑","","",},
        [1445] = {20184501,"왕랑","탐욕이 가득 찼구나! 오군을 얻고 내 땅을 노리다니! 오늘 엄 씨의 원한을 갚아주마!","탐욕이 가득 찼구나! 오군을 얻고 내 땅을 노리다니! 오늘 엄 씨의 원한을 갚아주마!","손책","","",},
        [1446] = {20184501,"손책","어디서 헛소리를 하느냐!","어디서 헛소리를 하느냐!","왕랑","","",},
        [1447] = {20184501,"손책","태사자가 나가 저 놈의 머리를 가져오너라.","태사자가 나가 저 놈의 머리를 가져오너라.","왕랑","","",},
        [1448] = {20184502,"조조","당시 회계에서 밥그릇을 잃어버린 일은 잊으시오.","당시 회계에서 밥그릇을 잃어버린 일은 잊으시오.","왕랑","","",},
        [1449] = {20184502,"왕랑","행동과 말을 적절하게 표현하기가 어렵습니다.","행동과 말을 적절하게 표현하기가 어렵습니다.","조조","","",},
        [1450] = {20184502,"조조","왜 그렇게 말하시오?","왜 그렇게 말하시오?","왕랑","","",},
        [1451] = {20184502,"왕랑","당시 한나라의 관리였는데, 백성을 보호하지 못하고 막아내지 못했지요.","당시 한나라의 관리였는데, 백성을 보호하지 못하고 막아내지 못했지요.","조조","","",},
        [1452] = {20184502,"왕랑","그런데 손님을 공경하고, 인재를 깍듯이 대하니, 새로온 손님을 이렇게 대하실 줄 몰랐습니다.","그런데 손님을 공경하고, 인재를 깍듯이 대하니, 새로온 손님을 이렇게 대하실 줄 몰랐습니다.","조조","","",},
