        [1606] = {20185602,"조인","지난번 공의 말을 듣지 않아 장수 두 명을 잃고 양양을 잃었소. 어찌하면 좋겠소?","지난번 공의 말을 듣지 않아 장수 두 명을 잃고 양양을 잃었소. 어찌하면 좋겠소?","만총","","",},
        [1607] = {20185602,"만총","운장은 용맹스럽고, 지혜가 많아 가볍게 봐서는 안 됩니다.","운장은 용맹스럽고, 지혜가 많아 가볍게 봐서는 안 됩니다.","조인","","",},
        [1608] = {20185602,"조인","어서 사람을 장안으로 보내 편지를 올려야겠다. 운장이 양양을 격파하고 번성이 포위되어 위험한 상황이니, 빨리 도와달라고 하여라.","어서 사람을 장안으로 보내 편지를 올려야겠다. 운장이 양양을 격파하고 번성이 포위되어 위험한 상황이니, 빨리 도와달라고 하여라.","만총","","",},
        [1609] = {20185602,"조인","목숨을 걸고 성을 지켜라!","목숨을 걸고 성을 지켜라!","만총","","",},
        [1610] = {20185603,"조인","번성이 포위되고, 물살이 점점 심해져 남녀 모두 벽돌을 날라 메우지만 소용이 없다.","번성이 포위되고, 물살이 점점 심해져 남녀 모두 벽돌을 날라 메우지만 소용이 없다.","만총","","",},
        [1611] = {20185603,"조인","이제는 어쩔 수가 없구나. 적이 아직 안 왔으니 배를 타고 빠져나가야겠다. 성은 잃어도 목숨은 지킬 수 있을 것이다.","이제는 어쩔 수가 없구나. 적이 아직 안 왔으니 배를 타고 빠져나가야겠다. 성은 잃어도 목숨은 지킬 수 있을 것이다.","만총","","",},
        [1612] = {20185603,"만총","산의 물이 오래 가겠습니까? 며칠 후면 저절로 없어질 것입니다. 관공이 성을 공격하지 않지만 따로 겹하에 장수를 두었습니다.","산의 물이 오래 가겠습니까? 며칠 후면 저절로 없어질 것입니다. 관공이 성을 공격하지 않지만 따로 겹하에 장수를 두었습니다.","조인","","",},
        [1613] = {20185603,"만총","우리 군사가 배후에 있을까 봐 쉽게 진격하지 않을 겁니다. 지금 포기하면 황하 이남은 다시는 얻지 못하게 됩니다.","우리 군사가 배후에 있을까 봐 쉽게 진격하지 않을 겁니다. 지금 포기하면 황하 이남은 다시는 얻지 못하게 됩니다.","조인","","",},
        [1614] = {20185603,"조인","그대의 가르침이 아니었다면, 큰 실수를 할 뻔했소.","그대의 가르침이 아니었다면, 큰 실수를 할 뻔했소.","만총","","",},
