        [1616] = {20185603,"조인","장병은 번성을 사수하고, 성에 수 백개의 활을 준비하여 주야로 지키고 있소.","장병은 번성을 사수하고, 성에 수 백개의 활을 준비하여 주야로 지키고 있소.","만총","","",},
        [1617] = {20185604,"조인","관우가 화살을 맞고 서황에게 패하여 맥성으로 달아났소.","관우가 화살을 맞고 서황에게 패하여 맥성으로 달아났소.","","","",},
        [1618] = {20185604,"조인","아깝다. 내가 맥성으로 가서 관우를 사로잡았어야 했는데!","아깝다. 내가 맥성으로 가서 관우를 사로잡았어야 했는데!","","","",},
        [1619] = {20185604,"조인","동오의 여몽이 힘들이지 않고 얻다니 참으로 애석하구나.","동오의 여몽이 힘들이지 않고 얻다니 참으로 애석하구나.","","","",},
        [1620] = {20185701,"오국태","남녀가 성장하면 결혼을 하는 것이 당연한 이치인데, 네 모친이니 내 허락을 받아야 할 것이다.","남녀가 성장하면 결혼을 하는 것이 당연한 이치인데, 네 모친이니 내 허락을 받아야 할 것이다.","손권","","",},
        [1621] = {20185701,"오국태","유비를 사위로 삼으며서 왜 나를 속이느냐? 분명 내 딸의 일인데.","유비를 사위로 삼으며서 왜 나를 속이느냐? 분명 내 딸의 일인데.","손권","","",},
