        [1623] = {20185701,"오국태","모든 백성이 다 아는데, 어떻게 나를 속이겠느냐!","모든 백성이 다 아는데, 어떻게 나를 속이겠느냐!","손권","","",},
        [1624] = {20185701,"손권","아닙니다. 그것은 주유의 계략입니다. 그것을 명분으로 유비를 이곳에 가두고 그를 형주와 바꿀 것입니다.","아닙니다. 그것은 주유의 계략입니다. 그것을 명분으로 유비를 이곳에 가두고 그를 형주와 바꿀 것입니다.","오국태","","",},
        [1625] = {20185701,"손권","따르지 않는다면, 먼저 유비를 죽일 것입니다. 계략에 불과합니다.","따르지 않는다면, 먼저 유비를 죽일 것입니다. 계략에 불과합니다.","오국태","","",},
        [1626] = {20185701,"오국태","81주의 6군독을 맡고 있으면서, 제대로 된 계획 없이 내 딸을 이용해 미인계를 쓰려고 하다니!","81주의 6군독을 맡고 있으면서, 제대로 된 계획 없이 내 딸을 이용해 미인계를 쓰려고 하다니!","손권","","",},
        [1627] = {20185701,"오국태","유비를 죽이면 과부가 될 텐데, 앞으로 누가 혼담을 꺼내겠느냐? 내 딸의 인생을 망치는 일이다!","유비를 죽이면 과부가 될 텐데, 앞으로 누가 혼담을 꺼내겠느냐? 내 딸의 인생을 망치는 일이다!","손권","","",},
        [1628] = {20185701,"오국태","이렇게 형주를 얻으면 천하의 비웃을 거리가 될 것이다!","이렇게 형주를 얻으면 천하의 비웃을 거리가 될 것이다!","손권","","",},
        [1629] = {20185701,"오국태","내일 감로사에서 만나 보고, 마음에 들지 않으면 너희 뜻에 따르고, 마음에 들면 시집을 보낼 것이다!","내일 감로사에서 만나 보고, 마음에 들지 않으면 너희 뜻에 따르고, 마음에 들면 시집을 보낼 것이다!","손권","","",},
        [1630] = {20185702,"손권","모친이 힘을 써서 누이를 유비에게 시집보냈소. 어찌하면 좋겠소?","모친이 힘을 써서 누이를 유비에게 시집보냈소. 어찌하면 좋겠소?","주유","","",},
        [1631] = {20185702,"주유","제가 계획한 일이 이렇게 될 줄 몰랐습니다. 이것을 이용한 계략을 짜야겠습니다.","제가 계획한 일이 이렇게 될 줄 몰랐습니다. 이것을 이용한 계략을 짜야겠습니다.","손권","","",},
        [1632] = {20185702,"주유","유비는 효웅이고, 관우, 장비, 조운과 같은 장수가 있으며, 제갈량이 있어서 남의 밑에 오래 있지 않을 겁니다.","유비는 효웅이고, 관우, 장비, 조운과 같은 장수가 있으며, 제갈량이 있어서 남의 밑에 오래 있지 않을 겁니다.","손권","","",},
        [1633] = {20185702,"주유","오나라에 자연스럽게 있게 하는 겁니다. 궁을 성대하게 지어, 큰 뜻을 꺾고 미녀를 보내 이목을 즐겁게 하십시오.","오나라에 자연스럽게 있게 하는 겁니다. 궁을 성대하게 지어, 큰 뜻을 꺾고 미녀를 보내 이목을 즐겁게 하십시오.","손권","","",},
        [1634] = {20185702,"주유","관우, 장비와의 정을 잊고, 제갈량과 멀어지게 하십시오. 곳곳에 군대를 배치해 공격하면 일은 반드시 성사될 것입니다.","관우, 장비와의 정을 잊고, 제갈량과 멀어지게 하십시오. 곳곳에 군대를 배치해 공격하면 일은 반드시 성사될 것입니다.","손권","","",},
        [1635] = {20185702,"주유","지금 풀어주면 교룡이 구름과 비를 만나 연못에 있지 않으려고 할 것입니다. 잘 생각해보셔야 합니다.","지금 풀어주면 교룡이 구름과 비를 만나 연못에 있지 않으려고 할 것입니다. 잘 생각해보셔야 합니다.","손권","","",},
        [1636] = {20185702,"손권","내 생각과 같소. 유비는 미천한 출신에 천하를 누비느라 부귀영화를 맛보지 못했을 거요.","내 생각과 같소. 유비는 미천한 출신에 천하를 누비느라 부귀영화를 맛보지 못했을 거요.","주유","","",},
        [1637] = {20185702,"손권","화려한 곳에서 인생을 즐기면 자연히 공명, 관우, 장비 등과 소원해 질 것이오.","화려한 곳에서 인생을 즐기면 자연히 공명, 관우, 장비 등과 소원해 질 것이오.","주유","","",},
