        [1719] = {20186302,"손권","이 당나귀 목에 왜 '제갈자유'라고 쓴 팻말이 걸려있소? 제갈근 자네를 가리키는 것이오?","이 당나귀 목에 왜 '제갈자유'라고 쓴 팻말이 걸려있소? 제갈근 자네를 가리키는 것이오?","제갈각","","",},
        [1720] = {20186302,"제갈각","두 글자가 빠진 것 같은데, 팻말에 더할 수 있도록 허락해 주십시오.","두 글자가 빠진 것 같은데, 팻말에 더할 수 있도록 허락해 주십시오.","손권","","",},
