        [1723] = {20186303,"손권","제갈근 그대를 대장군, 좌도호로 삼고, 예주목을 겸하게 하려고 하오. 어떻소?","제갈근 그대를 대장군, 좌도호로 삼고, 예주목을 겸하게 하려고 하오. 어떻소?","제갈근","","",},
        [1724] = {20186303,"제갈근","예주는 지금 위나라 땅이라 우리 손에 있지 않습니다.","예주는 지금 위나라 땅이라 우리 손에 있지 않습니다.","손권","","",},
        [1725] = {20186303,"손권","그러니 열심히 노력하여 그 땅을 점령하면 그대에게 주겠소!","그러니 열심히 노력하여 그 땅을 점령하면 그대에게 주겠소!","제갈근","","",},
        [1726] = {20186401,"황승언","내 딸 월영의 아명은 아교인데, 꽃처럼 아름답고 매우 영리하니, 어떤 젊은이가 우리 딸과 결혼할는지 모르겠구나.","내 딸 월영의 아명은 아교인데, 꽃처럼 아름답고 매우 영리하니, 어떤 젊은이가 우리 딸과 결혼할는지 모르겠구나.","황월영","","",},
        [1727] = {20186401,"황월영","제갈공명의 학식과 인품이 뛰어나다고 하니, 아버지께서 혼담을 꺼내 보세요.","제갈공명의 학식과 인품이 뛰어나다고 하니, 아버지께서 혼담을 꺼내 보세요.","황승언","","",},
        [1728] = {20186401,"황월영","제가 노란 머리에 검은 피부를 가진 추녀라고 말씀해 보세요. 그의 반응을 보면 인품을 알 수 있을 거예요.","제가 노란 머리에 검은 피부를 가진 추녀라고 말씀해 보세요. 그의 반응을 보면 인품을 알 수 있을 거예요.","황승언","","",},
        [1729] = {20186401,"황승언","좋은 계책이구나. 공명이 미색을 탐하는 자라면 혼담을 꺼내지 않는 것이 좋겠다.","좋은 계책이구나. 공명이 미색을 탐하는 자라면 혼담을 꺼내지 않는 것이 좋겠다.","황월영","","",},
        [1730] = {20186402,"제갈량","월영이 발명한 나무 개, 나무 호랑이, 나무 사람은 기묘하여 탄성이 절로 나는군요.","월영이 발명한 나무 개, 나무 호랑이, 나무 사람은 기묘하여 탄성이 절로 나는군요.","황월영","","",},
        [1731] = {20186402,"황월영","우리가 결혼할 때 제가 가마도, 말도, 배도 타지 않는다면 어떤 방법을 찾으시겠어요?","우리가 결혼할 때 제가 가마도, 말도, 배도 타지 않는다면 어떤 방법을 찾으시겠어요?","제갈량","","",},
        [1732] = {20186402,"제갈량","음...생각 좀 해보겠소.","음...생각 좀 해보겠소.","황월영","","",},
        [1733] = {20186403,"제갈량","월영이 낸 문제 덕분에 군에서 물자를 운송할 수 있는 목우유마를 발명할 수 있었소.","월영이 낸 문제 덕분에 군에서 물자를 운송할 수 있는 목우유마를 발명할 수 있었소.","황월영","","",},
        [1734] = {20186403,"황월영","과찬이세요. 도움이 되었다니 저도 기뻐요.","과찬이세요. 도움이 되었다니 저도 기뻐요.","제갈량","","",},
        [1735] = {20186403,"황월영","깃털 부채를 드리니 땅을 점령하고 나라를 안정케 하는 데 도움이 되길 바랍니다.","깃털 부채를 드리니 땅을 점령하고 나라를 안정케 하는 데 도움이 되길 바랍니다.","제갈량","","",},
        [1736] = {20186501,"반장","전방의 장료가 우리 진영을 급습하니, 용감히 싸우지 않고 도망가기 바쁘구나. 어찌 이럴 수 있는가!","전방의 장료가 우리 진영을 급습하니, 용감히 싸우지 않고 도망가기 바쁘구나. 어찌 이럴 수 있는가!","","","",},
        [1737] = {20186501,"반장","탈주병 두 명은 내가 죽였다. 죽고 싶지 않으면 나를 따르라!","탈주병 두 명은 내가 죽였다. 죽고 싶지 않으면 나를 따르라!","","","",},
        [1738] = {20186501,"손권","반장이 용맹하여 편장군으로 삼으니, 백 명의 교관을 통솔하여 반주에 주둔하시오.","반장이 용맹하여 편장군으로 삼으니, 백 명의 교관을 통솔하여 반주에 주둔하시오.","반장","","",},
        [1739] = {20186502,"관우","너희들은 누구냐? 보잘것없는 병사들이 나를 막다니! 길을 내주면 목숨만은 살려주겠다.","너희들은 누구냐? 보잘것없는 병사들이 나를 막다니! 길을 내주면 목숨만은 살려주겠다.","반장","","",},
        [1740] = {20186502,"반장","역시 관우는 안하무인이구나. 동오의 실력을 얕보지 말아라.","역시 관우는 안하무인이구나. 동오의 실력을 얕보지 말아라.","관우","","",},
        [1741] = {20186502,"관우","내가 싸움으로 지치지 않고 지금의 처지가 아니라면 너 따위와 말하지도 않았을 것이다!","내가 싸움으로 지치지 않고 지금의 처지가 아니라면 너 따위와 말하지도 않았을 것이다!","반장","","",},
        [1742] = {20186502,"반장","긴말은 필요 없고, 실력으로 보여주마!","긴말은 필요 없고, 실력으로 보여주마!","관우","","",},
        [1743] = {20186601,"손책","천하를 새롭게 시작하는 것은 내가 낫지만, 대업을 지키는 것은 네가 나보다 낫다.","천하를 새롭게 시작하는 것은 내가 낫지만, 대업을 지키는 것은 네가 나보다 낫다.","손권","","",},
        [1744] = {20186601,"손권","형님이 계신대 어찌 대업을 이루지 못하겠습니까? 저도 최선을 다해 돕겠습니다.","형님이 계신대 어찌 대업을 이루지 못하겠습니까? 저도 최선을 다해 돕겠습니다.","손책","","",},
        [1745] = {20186601,"손책","어려서부터 재주가 뛰어났으니, 앞으로 관원들을 네가 맡도록 하여라.","어려서부터 재주가 뛰어났으니, 앞으로 관원들을 네가 맡도록 하여라.","손권","","",},
        [1746] = {20186602,"장소","손권과 유비가 연합하여 조조에 대항하자는 선생의 의견에 전적으로 동의합니다. 참으로 감탄했습니다.","손권과 유비가 연합하여 조조에 대항하자는 선생의 의견에 전적으로 동의합니다. 참으로 감탄했습니다.","제갈량","","",},
        [1747] = {20186602,"장소","강동에 머무르면서 대업을 함께 이루면 어떻겠습니까?","강동에 머무르면서 대업을 함께 이루면 어떻겠습니까?","제갈량","","",},
        [1748] = {20186602,"제갈량","손장군은 가히 군주라 할 수 있으나 그 도량을 보면 나를 어질게 대할 수는 있어도 내 기량을 다 발휘할 수는 없으니, 나는 머물 수 없소.","손장군은 가히 군주라 할 수 있으나 그 도량을 보면 나를 어질게 대할 수는 있어도 내 기량을 다 발휘할 수는 없으니, 나는 머물 수 없소.","장소","","",},
        [1749] = {20186603,"제갈량","귀국의 군사력이 조군에 맞설 만큼 강했다면 일찍이 단교하는 것이 맞습니다. 조군에게 대항할 수 없다면 차라리 항복하는 게 낫습니다.","귀국의 군사력이 조군에 맞설 만큼 강했다면 일찍이 단교하는 것이 맞습니다. 조군에게 대항할 수 없다면 차라리 항복하는 게 낫습니다.","손권","","",},
        [1750] = {20186603,"손권","유비는 왜 조조에게 투항하지 않소?","유비는 왜 조조에게 투항하지 않소?","제갈량","","",},
        [1751] = {20186603,"제갈량","유비는 한실의 후예로 그를 흠모하여 의탁한 자들이 셀 수 없이 많은데 어떻게 투항할 수 있습니까?","유비는 한실의 후예로 그를 흠모하여 의탁한 자들이 셀 수 없이 많은데 어떻게 투항할 수 있습니까?","손권","","",},
        [1752] = {20186603,"손권","내게도 강동의 넓은 땅과 십만 정병이 있는데, 어찌 그에게 좌지우지될 수 있겠소? 하면 될 것이오!","내게도 강동의 넓은 땅과 십만 정병이 있는데, 어찌 그에게 좌지우지될 수 있겠소? 하면 될 것이오!","제갈량","","",},
        [1753] = {20186701,"문추","항상 우리 둘의 이름을 언급하는데 우리 둘 중 누가 더 강한지는 알 수가 없소.","항상 우리 둘의 이름을 언급하는데 우리 둘 중 누가 더 강한지는 알 수가 없소.","안량","","",},
