        [1772] = {20186902,"관우","아들아, 잘 듣거라. 장수는 힘과 용기만 있어선 안 된다. 지혜와 모략을 알아야 큰일을 이루고 큰 그릇이 되는 것이야.","아들아, 잘 듣거라. 장수는 힘과 용기만 있어선 안 된다. 지혜와 모략을 알아야 큰일을 이루고 큰 그릇이 되는 것이야.","관평","","",},
        [1773] = {20186902,"관평","아버님 말씀이 맞습니다. 그러니 오나라에서 온 사신 제갈근을 죽이지 마십시오!","아버님 말씀이 맞습니다. 그러니 오나라에서 온 사신 제갈근을 죽이지 마십시오!","관우","","",},
